INSERT INTO [t133357] VALUES (442,'(٤١) - وقوله تعالى: {واعلموا أنما غنمتم من شيء فأن لله خمسه}','1029','435','0');
INSERT INTO [t133357] VALUES (443,'(٤٥) - قوله تعالى: {يا أيها الذين آمنوا إذا لقيتم فئة فاثبتوا واذكروا الله كثيرا}','1045','435','0');
INSERT INTO [t133357] VALUES (444,'(٥٧) - (٦١) - قوله تعالى: {فإما تثقفنهم في الحرب فشرد بهم من خلفهم لعلهم يتذكرون * وإما تخافن من قوم خيانة} إلى قوله تعالى: {وإن جنحوا للسلم فاجنح لها}','1045','435','0');
INSERT INTO [t133357] VALUES (445,'(٦٠) - وقوله تعالى: {وأعدوا لهم ما استطعتم من قوة ومن رباط الخيل}','1047','435','0');
INSERT INTO [t133357] VALUES (446,'(٦٠) - وقوله تعالى: {وآخرين من دونهم لا تعلمونهم الله يعلمهم}','1048','435','0');
INSERT INTO [t133357] VALUES (447,'(٦١) - قوله تعالى: {وإن جنحوا للسلم فاجنح لها}','1048','435','0');
INSERT INTO [t133357] VALUES (448,'(٦٥) - (٦٧) - قوله تعالى: {يا أيها النبي حرض المؤمنين على القتال} إلى قوله تعالى: {ما كان لنبي}','1051','435','0');
INSERT INTO [t133357] VALUES (449,'(٦٧) - قوله تعالى: {ما كان لنبي أن يكون له أسرى حتى يثخن في الأرض تريدون عرض الدنيا والله يريد الآخرة}','1052','435','0');
INSERT INTO [t133357] VALUES (450,'(٦٨) - قوله تعالى: {لولا كتاب من الله}','1053','435','0');
INSERT INTO [t133357] VALUES (451,'(٦٩) - وقوله تعالى: {فكلوا مما غنمتم حلالا طيبا}','1053','435','0');
INSERT INTO [t133357] VALUES (452,'(٧٢) - (٧٥) - قوله تعالى: {إن الذين آمنوا وهاجروا وجاهدوا بأموالهم وأنفسهم في سبيل الله والذين آووا ونصروا أولئك بعضهم أولياء بعض والذين آمنوا ولم يهاجروا ما لكم من ولايتهم من شيء} إلى قوله: {إن الله بكل شيء عليم}','1054','435','0');
INSERT INTO [t133357] VALUES (453,'سورة براءة','1057','0','0');
INSERT INTO [t133357] VALUES (454,'(١) - قوله تعالى: {براءة من الله ورسوله}','1058','453','0');
INSERT INTO [t133357] VALUES (455,'(٤) - وقوله تعالى: {ثم لم ينقصوكم شيئا ولم يظهروا عليكم أحدا}','1058','453','0');
INSERT INTO [t133357] VALUES (456,'(٢) - وقوله تعالى: {فيسحوا}','1059','453','0');
INSERT INTO [t133357] VALUES (457,'(٣) - وقوله تعالى: {وأذان من الله}','1060','453','0');
INSERT INTO [t133357] VALUES (458,'(٤) - وقوله تعالى: {إلا الذين عاهدتم من المشركين ثم لم ينقضوكم شيئا ولا يظهروا عليكم أحدا}','1062','453','0');
INSERT INTO [t133357] VALUES (459,'(٥) - وقوله تعالى: {فإذا انسلخ الأشهر الحرم فاقتلوا المشركين حيث وجدتموهم}','1063','453','0');
INSERT INTO [t133357] VALUES (460,'(٥) - وقوله تعالى: {فإن تابوا وأقاموا الصلاة وآتوا الزكاة}','1067','453','0');
INSERT INTO [t133357] VALUES (461,'(٦) - (٧) - قوله تعالى: {وإن أحد من المشركين استجارك فأجره حتى يسمع كلام الله ثم أبلغه مأمنه} إلى قوله: {فاستقيموا لهم}','1069','453','0');
INSERT INTO [t133357] VALUES (462,'(٦) - وقوله تعالى: {حتى يسمع كلام الله}','1070','453','0');
INSERT INTO [t133357] VALUES (463,'(٧) - وقوله تعالى: {كيف يكون للمشركين عهد عند الله وعند رسوله}','1070','453','0');
INSERT INTO [t133357] VALUES (464,'(٩) - قوله تعالى: {اشتروا بآيات الله ثمنا قليلا}','1071','453','0');
INSERT INTO [t133357] VALUES (465,'(١٢) - وقوله تعلى: {وإن نكثوا أيمانهم من بعد عهدهم وطعنوا في دينكم فقاتلوا أئمة الكفر إنهم لا أيمان لهم لعلهم ينتهون}','1072','453','0');
INSERT INTO [t133357] VALUES (466,'(٢٨) - قوله تعالى: {يا أيها الذين آمنوا إنما المشركون نجس فلا يقربوا المسجد الحرام بعد عامهم هذا}','1073','453','0');
INSERT INTO [t133357] VALUES (467,'(٢٩) - وقوله تعالى: {حتى يعطوا الجزية عن يد وهم صاغرون}','1084','453','0');
INSERT INTO [t133357] VALUES (468,'(٣٤) - قوله تعالى: {والذين يكنزون الذهب والفضة ولا ينفقونها في سبيل الله فبشرهم بعذاب أليم}','1090','453','0');
INSERT INTO [t133357] VALUES (469,'(٣٦)، (٣٧) - قوله تعالى: {إن عدة الشهور عند الله اثنا عشر شهرا في كتاب الله يوم خلق السماوات والأرض منها أربعة حرم ذلك الدين القيم فلا تظلموا فيهن أنفسكم وقاتلوا المشركين كافة كما يقاتلونكم كافة} إلى قوله: {سوء أعمالهم}','1093','453','0');
INSERT INTO [t133357] VALUES (470,'(٣٨)، (٣٩) - قوله تعالى: {يا أيها الذين آمنوا ما لكم إذا قيل لكم انفروا في سبيل الله اثاقلتم إلى الأرض} إلى قوله: {أليما}','1097','453','0');
INSERT INTO [t133357] VALUES (471,'(٤١) - قوله تعالى: {انفروا خفافا وثقالا} الآية','1098','453','0');
INSERT INTO [t133357] VALUES (472,'(٤٣) - (٤٥) - قوله تعالى: {عفا الله عنك لم أذنت لهم} إلى قوله: {يترددون}','1101','453','0');
INSERT INTO [t133357] VALUES (473,'(٥١) - قوله تعالى: {وعلى الله فليتوكل المؤمنون}','1102','453','0');
INSERT INTO [t133357] VALUES (474,'(٥٣) - قوله تعالى: {قل أنفقوا طوعا أو كرها لن يتقبل منكم إنكم كنتم قوما فاسقين}','1103','453','0');
INSERT INTO [t133357] VALUES (475,'(٦٠) - قوله تعالى: {إنما الصدقات للفقراء ... } إلى قوله تعالى: {فريضة من الله}','1104','453','0');
INSERT INTO [t133357] VALUES (476,'(٧٣) - قوله تعالى: {يا أيها النبي جاهد الكفار والمنافقين واغلظ عليهم}','1126','453','0');
INSERT INTO [t133357] VALUES (477,'(٧٥) - (٧٧) - قوله تعالى: {ومنهم من عاهد الله} إلى قوله: {فأعقبهم نفاقا في قلوبهم إلى يوم يلقونه بما أخلفوا الله ما وعدوه وبما كانوا يكذبون}','1128','453','0');
INSERT INTO [t133357] VALUES (478,'(٨٠) - قوله تعالى: {استغفر لهم أو لا تستغفر لهم إن تستغفر لهم سبعين مرة فلن يغفر الله لهم}','1131','453','0');
INSERT INTO [t133357] VALUES (479,'(٨٤) - وقوله تعالى: {لا تصل على أحد منهم مات أبدا ولا تقم على قبره}','1132','453','0');
INSERT INTO [t133357] VALUES (480,'(٩١) - وقوله تعالى بعد هذا: {ما على المحسنين من سبيل}','1134','453','0');
INSERT INTO [t133357] VALUES (481,'(١٠٣) - قوله تعالى: {خذ من أموالهم صدقة تطهرهم وتزكيهم بها وصل عليهم إن صلاتك سكن لهم}','1134','453','0');
INSERT INTO [t133357] VALUES (482,'(١٠٨) - قوله تعالى: {لا تقم فيه أبدا لمسجد أسس على التقوى من أول يوم أحق أن تقوم فيه فيه رجال يحبون أن يتطهروا والله يحب المطهرين}','1138','453','0');
INSERT INTO [t133357] VALUES (483,'(١١٣) - (١١٤) - قوله تعالى: {ما كان للنبي والذين آمنوا أن يستغفروا للمشركين} إلى قوله: {فلما تبين له أنه عدو لله تبرأ منه}','1140','453','0');
INSERT INTO [t133357] VALUES (484,'(١١٩) - قوله تعالى: {يا أيها الذين آمنوا اتقوا الله وكونوا مع الصادقين}','1142','453','0');
INSERT INTO [t133357] VALUES (485,'(١٢٠) - قوله تعالى: {ما كان لأهل المدينة ومن حولهم من الأعراب أن يتخلفوا عن رسول الله ولا يرغبوا بأنفسهم عن نفسه}','1143','453','0');
INSERT INTO [t133357] VALUES (486,'(١٢٢)، (١٢٣) - قوله تعالى: {وما كان المؤمنون لينفروا كافة فلولا نفر من كل فرقة منهم طائفة ليتفقهوا في الدين} إلى قوله: {وليجدوا فيكم غلظة}','1144','453','0');
INSERT INTO [t133357] VALUES (487,'(١٢٣) - قوله تعالى: {يا أيها الذين آمنوا قاتلوا الذين يلونكم من الكفار}','1146','453','0');
INSERT INTO [t133357] VALUES (488,'(٤١) - قوله تعالى: {وإن كذبوك فقل لي عملي ولكم عملكم}','1148','453','0');
INSERT INTO [t133357] VALUES (489,'(١٠٨) - (١٠٩) - قوله تعالى: {وما أنا عليكم بوكيل} وقوله: {واصبر حتى يحكم الله وهو خير الحاكمين}','1149','453','0');
INSERT INTO [t133357] VALUES (490,'(١٥) - قوله تعالى: {قال الذين لا يرجون لقاءنا ائت بقرآن غير هذا أو بدله}','1149','453','0');
INSERT INTO [t133357] VALUES (491,'(٥٩) - وقوله تعالى: {قل أرأيتم ما أنزل الله إليكم من رزق فجعلتم منه حراما وحلالا قل آلله أذن لكم}','1149','453','0');
INSERT INTO [t133357] VALUES (492,'(٨٧) - وقوله تعالى: {واجعلوا بيوتكم قبلة}','1150','453','0');
INSERT INTO [t133357] VALUES (493,'سورة هود عليه السلام','1151','0','0');
INSERT INTO [t133357] VALUES (494,'(١٥) - قوله تعالى: {من كان يريد الحياة الدنيا وزينتها نوف إليهم أعمالهم فيها}','1151','493','0');
INSERT INTO [t133357] VALUES (495,'(٤٥) - وقوله تعالى: {ونادى نوح ربه فقال رب إن ابني من أهلي}','1152','493','0');
INSERT INTO [t133357] VALUES (496,'(٦٥) - قوله تعالى: {فقال تمتعوا في داركم ثلاثة أيام ذلك وعد غير مكذوب}','1153','493','0');
INSERT INTO [t133357] VALUES (497,'(٨٧) - قوله تعالى: {قالوا يا شعيب أصلواتك تأمرك أن نترك ما يعبد آباؤنا أو أن نفعل في أموالنا ما نشاء}','1153','493','0');
INSERT INTO [t133357] VALUES (498,'(١١٣) - (١١٥) - قوله تعالى: {ولا تركنوا إلى الذين ظلموا فتمسكم النار} إلى قوله: {فإن الله لا يضيع أجر المحسنين}','1155','493','0');
INSERT INTO [t133357] VALUES (499,'(١١٤) - قوله تعالى: {وأقم الصلاة}','1155','493','0');
INSERT INTO [t133357] VALUES (500,'(١١٥) - وقوله تعالى: {واصبر فإن الله لا يضيع أجر المحسنين}','1158','493','0');
INSERT INTO [t133357] VALUES (501,'سورة يوسف عليه السلام','1159','0','0');
INSERT INTO [t133357] VALUES (502,'(٤) - قوله تعالى: {والشمس والقمر رأيتهم لي ساجدين}','1159','501','0');
INSERT INTO [t133357] VALUES (503,'(١٨) - قوله تعالى: {وجاءوا على قميصه بدم كذب}','1159','501','0');
INSERT INTO [t133357] VALUES (504,'(١٩) - قوله تعالى: {وجاءت سيارة فأرسلوا واردهم}','1160','501','0');
INSERT INTO [t133357] VALUES (505,'(٢٦) - قوله تعالى: {وشهد شاهد من أهلها إن كان قميصه}','1161','501','0');
INSERT INTO [t133357] VALUES (506,'(٤٢) - قوله تعالى: {فلبث في السجن بضع سنين}','1163','501','0');
INSERT INTO [t133357] VALUES (507,'(٤٤) - وقوله تعالى: {قالوا أضغاث أحلام}','1164','501','0');
INSERT INTO [t133357] VALUES (508,'(٥٤)، (٥٥) - قوله تعالى: {وقال الملك ائتوني به أستخلصه لنفسي فلما كلمه قال إنك اليوم لدينا مكين أمين * قال اجعلني على خزائن الأرض إني حفيظ عليم}','1164','501','0');
INSERT INTO [t133357] VALUES (509,'(٧٢) - قوله تعالى: {ولمن جاء به حمل بعير وأنا به زعيم}','1166','501','0');
INSERT INTO [t133357] VALUES (510,'(٧٨) - قوله تعالى: {فخذ أحدنا مكانه إنا نراك من المحسنين}','1168','501','0');
INSERT INTO [t133357] VALUES (511,'(٨١) - (٨٢) - قوله تعالى: {وما شهدنا إلا بما علمنا وما كنا للغيب حافظين * واسأل القرية التي كنا فيها والعير التي أقبلنا فيها وإنا لصادقون}','1169','501','0');
INSERT INTO [t133357] VALUES (512,'(٨٢) - وقوله تعالى: {واسأل القرية التي كنا فيها والعير التي أقبلنا فيها وإنا لصادقون}','1170','501','0');
INSERT INTO [t133357] VALUES (513,'(٨٤) - (٨٦) - قوله تعالى: {وتولى عنهم وقال يا أسفى وابيضت عيناه من الحزن فهو كظيم * قالوا تالله تفتؤ تذكر يوسف حتى تكون حرضا أو تكون من الهالكين * قال إنما أشكو بثي وحزني إلى الله}','1171','501','0');
INSERT INTO [t133357] VALUES (514,'(٨٨) - قوله تعالى: {قالوا يا أيها العزيز مسنا وأهلنا الضر وجئنا ببضاعة مزجاة فأوف لنا الكيل وتصدق علينا إن الله يجزي المتصدقين}','1173','501','0');
INSERT INTO [t133357] VALUES (515,'(١٠١) - قوله تعالى: {توفني مسلما وألحقني بالصالحين}','1175','501','0');
INSERT INTO [t133357] VALUES (516,'سورة الرعد','1177','0','0');
INSERT INTO [t133357] VALUES (517,'(٨) - قوله تعالى: {الله يعلم ما تحمل كل أنثى وما تغيض الأرحام وما تزداد وكل شيء عنده بمقدار}','1177','516','0');
INSERT INTO [t133357] VALUES (518,'سورة إبراهيم عليه السلام','1180','0','0');
INSERT INTO [t133357] VALUES (519,'(٢٢) - قوله تعالى: {وقال الشيطان لما قضي الأمر إن الله وعدكم وعد الحق وودعتك فأخلفتكم وما كان لي عليكم من سلطان إلا أن دعوتكم فاستجبتم لي فلا تلوموني ولوموا أنفسكم}','1180','518','0');
INSERT INTO [t133357] VALUES (520,'(٢٥) - قوله تعالى: {تؤتي أكلها كل حين}','1181','518','0');
INSERT INTO [t133357] VALUES (521,'(٣١) - قوله تعالى: {قل لعبادي الذين آمنوا يقيموا الصلاة} إلى قوله: {وعلانية}','1182','518','0');
INSERT INTO [t133357] VALUES (522,'سورة الحجر','1183','0','0');
INSERT INTO [t133357] VALUES (523,'(٣) - قوله تعالى: {ذرهم يأكلوا ويتمتعوا ويلههم الأمل فسوف يعلمون}','1183','522','0');
INSERT INTO [t133357] VALUES (524,'(٧٢) - قوله تعالى: {لعمرك إنهم لفي سكرتهم يعمهون}','1183','522','0');
INSERT INTO [t133357] VALUES (525,'(٨٥) - قوله تعالى: {فاصفح الصفح الجميل}','1184','522','0');
INSERT INTO [t133357] VALUES (526,'(٩٤) - قوله تعالى: {وأعرض عن المشركين}','1184','522','0');
INSERT INTO [t133357] VALUES (527,'سورة النحل','1185','0','0');
INSERT INTO [t133357] VALUES (528,'(٥) - (٨) - قوله تعالى: {والأنعام خلقها لكم فيها دفء ومنافع ومنها تأكلون} إلى قوله: {ويخلق ما لا تعلمون}','1185','527','0');
INSERT INTO [t133357] VALUES (529,'(١٤) - قوله تعالى: {وهو الذي سخر البحر لتأكلوا منه لحما طريا} إلى قوله: {ولعلكم تشكرون}','1187','527','0');
INSERT INTO [t133357] VALUES (530,'(١٦) - قوله تعالى: {وعلامات وبالنجم هم يهتدون}','1188','527','0');
INSERT INTO [t133357] VALUES (531,'(٦٧) - (٦٩) - قوله تعالى: {ومن ثمرات النخيل والأعناب تتخذون منه سكرا ورزقا حسنا ... } إلى قوله تعالى: {إن في ذلك لآية لقوم يتفكرون}','1190','527','0');
INSERT INTO [t133357] VALUES (532,'(٦٩) - وقوله تعالى للنحل: {ثم كلي من كل الثمرات فاسلكي سبل ربك ذللا}','1191','527','0');
INSERT INTO [t133357] VALUES (533,'(٧٥) - قوله تعالى: {صرب الله مثلا عبدا مملوكا لا يقدر على شيء}','1192','527','0');
INSERT INTO [t133357] VALUES (534,'(٧٨) - قوله تعالى: {والله أخرجكم من بطون أمهاتكم لا تعلمون شيئا}','1193','527','0');
INSERT INTO [t133357] VALUES (535,'(٨٠) - قوله تعالى: {وجعل لكم من جلود الأنعام} إلى قوله: {ومتاعا إلى حين}','1193','527','0');
INSERT INTO [t133357] VALUES (536,'(٨٢) - قوله تعالى: {فإن تولوا فإنما عليك البلاغ المبين}','1194','527','0');
INSERT INTO [t133357] VALUES (537,'(٩٠) - (٩١) - وقوله تعالى: {إن الله يأمر بالعدل والإحسان وإيتاء ذي القربى وينهى عن الفحشاء والمنكر والبغي ... } إلى قوله تعالى: {وقد جعلتم الله عليكم كفيلا}','1194','527','0');
INSERT INTO [t133357] VALUES (538,'(٩٤)، (٩٥) - قوله تعالى: {ولا تتخذوا أيمانكم دخلا بينكم ... } إلى قوله تعالى: {إنما عند الله هو خير لكم}','1194','527','0');
INSERT INTO [t133357] VALUES (539,'(٩٨) - قوله تعالى: {فإذا قرأت القرآن فاستعذ بالله من الشيطان الرجيم}','1194','527','0');
INSERT INTO [t133357] VALUES (540,'سورة سبحان','1199','0','0');
INSERT INTO [t133357] VALUES (541,'(٢٣)، (٢٤) - قوله تعالى: {وبالوالدين إحسانا إما يبلغن عندك الكبر أحدهما أو كلاهما فلا تقل لها أف ولا تنهرهما وقل لهما قولا كريما * واخفض لها جناح الذل من الرحمة وقل رب ارحمهما كما ربياني صغيرا}','1200','540','0');
