INSERT INTO [t133415] VALUES (1,'كتاب الطهارة','1','0','0');
INSERT INTO [t133415] VALUES (2,'١ - مسألة: الخارج النجس من بدن الآدمي، من أي موضع كان، يوجب انتقاض الطهارة. وعنده من السبيلين.','1','1','0');
INSERT INTO [t133415] VALUES (3,'٢ - مسألة: النية والترتيب في الوضوء ليسا بشرط. وعنده شرط.','5','1','0');
INSERT INTO [t133415] VALUES (4,'٣ - مسألة: إزالة النجاسة الحقيقية بما سوى الماء من المائعات الطاهرات جائزة وعنده لا يجوز.','8','1','0');
INSERT INTO [t133415] VALUES (5,'كتاب الزكاة','10','0','0');
INSERT INTO [t133415] VALUES (6,'٤ - مسألة: الزكاة واجبة في الحلى. ولا تجب عنده.','10','5','0');
INSERT INTO [t133415] VALUES (7,'٥ - مسألة: لا تجب الزكاة في المال الضمار، وعنده تجب.','13','5','0');
INSERT INTO [t133415] VALUES (8,'٦ - مسألة: المستفاد من جنس النصاب يضم إلى ما عنده من النصاب في حكم الحول. وأنه تجب الزكاة فيه عند تمام الحول على الأصل.','15','5','0');
INSERT INTO [t133415] VALUES (9,'٧ - مسألة: أثمان الإبل المزكاة لا تضم إلى ما عنده من النصاب في حكم الحول.','17','5','0');
INSERT INTO [t133415] VALUES (10,'٨ - مسألة: مسألة المديون بقدر الدين لا ينعقد سببا لوجوب الزكاة.','19','5','0');
INSERT INTO [t133415] VALUES (11,'٩ - مسألة: من عليه الزكاة إذا فرط في أداء الزكاة حتى هلك النصاب سقطت عنه الزكاة.','21','5','0');
INSERT INTO [t133415] VALUES (12,'١٠ - مسألة: دفع القيم في باب الزكاة جائز','24','5','0');
INSERT INTO [t133415] VALUES (13,'١١ - مسألة: الزكاة لا تجب على الصبي والمجنون.','25','5','0');
INSERT INTO [t133415] VALUES (14,'كتاب الصوم','28','0','0');
INSERT INTO [t133415] VALUES (15,'١٢ - مسألة: إذا صام رمضان بمطلق النية أو بنية النقل أجزأه- خلافا له.','28','14','0');
INSERT INTO [t133415] VALUES (16,'١٣ - مسألة: صوم رمضان يتأدى بنية من النهار قبل الزوال، خلا له.','29','14','0');
INSERT INTO [t133415] VALUES (17,'١٤ - مسألة: المجنون إذا أفاق في بعض الشهر يلزمه قضاء ما مضى- خلافا له.','31','14','0');
INSERT INTO [t133415] VALUES (18,'١٥ - مسألة: النذر بصوم يوم النحر وأيام التشريق يصح.','33','14','0');
INSERT INTO [t133415] VALUES (19,'١٦ - مسألة: إذا أكل أو شرب في نهار رمضان عامدا، تلزمه الكفارة.','35','14','0');
INSERT INTO [t133415] VALUES (20,'١٧ - مسألة: المنفرد برؤية الهلال إذا شهد عند القاضي','38','14','0');
INSERT INTO [t133415] VALUES (21,'١٨ - مسألة: الكفارتان تتداخلان.','39','14','0');
INSERT INTO [t133415] VALUES (22,'١٩ - مسألة: إذا شرع في الصوم التطوع ثم أفسده، قضاه.','41','14','0');
INSERT INTO [t133415] VALUES (23,'كتاب النكاح','44','0','0');
INSERT INTO [t133415] VALUES (24,'٢٠ - مسألة: الاشتغال بالنكاح أفضل من التخلي لنوافل العبادات.','44','23','0');
INSERT INTO [t133415] VALUES (25,'٢١ - مسألة: البنت المخلوقة من ماء الزاني يحرم على الزاني نكاحها.','49','23','0');
INSERT INTO [t133415] VALUES (26,'٢٢ - مسألة: الزنا يوجب حرمة المصاهرة.','51','23','0');
INSERT INTO [t133415] VALUES (27,'٢٣ - مسألة: الأب إذا تزوج بجارية الابن يجوز.','53','23','0');
INSERT INTO [t133415] VALUES (28,'٢٤ - مسألة: زوج المعتدة إذا قال: أخبرتني المرأة أن عدتها انقضت','56','23','0');
INSERT INTO [t133415] VALUES (29,'٢٥ - مسألة: الأب لا يملك إجبار البكر البالغة على النكاح.','57','23','0');
INSERT INTO [t133415] VALUES (30,'٢٦ - مسألة: الأب والجد يملكان إجبار الثيب الصغيرة.','59','23','0');
INSERT INTO [t133415] VALUES (31,'٢٧ - مسألة: غير الأب والجد مثل الأخ والعم يثبت له إنكاح الصغيرة والصغيرة، ولهما الخيار إذا بلغا.','60','23','0');
INSERT INTO [t133415] VALUES (32,'٢٨ - مسألة: خبار البلوغ يثبت للصغير والصغيرة إذا زوجهما غير الأب والجد','62','23','0');
INSERT INTO [t133415] VALUES (33,'٢٩ - مسألة: النكاح بغير الولى ينعقد نافذا.','64','23','0');
INSERT INTO [t133415] VALUES (34,'٣٠ - مسألة: المصابة بالفجور لا تستنطق.','69','23','0');
INSERT INTO [t133415] VALUES (35,'٣١ - مسألة: أحد الأولياء إذا زوج وليته من غير كفء برضاها، يلزم العقد، ولا يكون للباقين حق الاعتراض.','71','23','0');
INSERT INTO [t133415] VALUES (36,'٣٢ - مسألة: أقرب الأولياء إذا غاب غيبة منقطعة، تثبت للأبعد ولاية التزويج.','73','23','0');
INSERT INTO [t133415] VALUES (37,'٣٣ - مسألة: المولى يملك إجبار عبده على النكاح.','74','23','0');
INSERT INTO [t133415] VALUES (38,'٣٤ - مسألة: العجز عن الإنفاق لا يوجب حق المطالبة بالتفريق والوجه فيه','76','23','0');
INSERT INTO [t133415] VALUES (39,'٣٥ - مسألة: إذا سبي الزوجان معا، لا تقع الفرقة بينهما.','79','23','0');
INSERT INTO [t133415] VALUES (40,'٣٦ - مسألة: المنكوحة لا ترد بالعيوب الخمسة وهي الرتق والقرن والبرص والجذام والجنون.','82','23','0');
INSERT INTO [t133415] VALUES (41,'٣٧ - مسألة: طول الحرة لا يمنع نكاح الأمة، خلافا له.','85','23','0');
INSERT INTO [t133415] VALUES (42,'٣٨ - مسألة: النكاح ينعقد بلفظ البيع والهبة.','87','23','0');
INSERT INTO [t133415] VALUES (43,'٣٩ - مسألة: نكاح الأخت في عدة الأخت عن طلاق بائن لا يجوز','90','23','0');
INSERT INTO [t133415] VALUES (44,'٤٠ - مسألة: إذا تزوج امرأة ولم يسم لها مهرا أو على أن لا مهر لها- يصح النكاح','92','23','0');
INSERT INTO [t133415] VALUES (45,'٤١ - مسألة: الخلوة الصحيحة بحكم النكاح الصحيح توجب كمال المهر، خلافا له.','95','23','0');
INSERT INTO [t133415] VALUES (46,'كتاب الطلاق','98','0','0');
INSERT INTO [t133415] VALUES (47,'٤٢ - مسألة: قال أبو حنيفة وأبو يوسف: الزوج الثاني يهدم الطلقة والطلقتين وهو قول ابن مسعود وابن عباس وابن عمر.','98','46','0');
INSERT INTO [t133415] VALUES (48,'٤٣ - مسألة: إذا قال لها: أنت طالق أو مطلقة أو طلقتك ونوى الثلاث أو الثنتين- لا تصح نيته، وتقع واحدة رجعية.','101','46','0');
INSERT INTO [t133415] VALUES (49,'٤٤ - مسألة: إضافة الطلاق إلى البد أو إلى جزء معين لا يعبر به عن جميع البدن، لا يصح. وعنده يصح.','103','46','0');
INSERT INTO [t133415] VALUES (50,'٤٥ - مسألة: التنجيز يبطل التعليق.','106','46','0');
INSERT INTO [t133415] VALUES (51,'٤٦ - مسألة: إرسال الطلقات الثلاث جملة حرام.','109','46','0');
INSERT INTO [t133415] VALUES (52,'٤٧ - مسألة: عدد الطلاق معتبر برق النساء وحريتهن، لا برق الرجال وحريتهم.','111','46','0');
INSERT INTO [t133415] VALUES (53,'٤٨ - مسألة: تعليق الطلاق والعتاق بالملك جائز، خلافا له.','114','46','0');
INSERT INTO [t133415] VALUES (54,'٤٩ - مسألة: الكنايات كلها بوائن إلا قوله: اعتدي -واستبري رحمك- وأنت واحدة.','117','46','0');
INSERT INTO [t133415] VALUES (55,'٥٠ - مسألة: الطلاق الرجعي لا يحرم الوطء. وهو صريح الطلاق.','119','46','0');
INSERT INTO [t133415] VALUES (56,'٥١ - مسألة: إذا طلق امرأته في مرض موته طلاقا بائنا، فمات وهي في العدة- ترث.','121','46','0');
INSERT INTO [t133415] VALUES (57,'٥٢ - مسألة: إذا قال، لامرأته قبل أن يدخل بها: إن دخلت الدار فأنت طالق وطالق وطالق','124','46','0');
INSERT INTO [t133415] VALUES (58,'٥٣ - مسألة: إذا قال لامرأته: "أن منك طالق" لا يقع الطلاق، وإن نوى، خلافا له.','126','46','0');
INSERT INTO [t133415] VALUES (59,'٥٤ - مسألة: ظهار الدمى لا يصح حتى لا يحرم الوطء أصلا.','128','46','0');
INSERT INTO [t133415] VALUES (60,'٥٥ - مسألة: المختلعة يلحقها صريح الطلاق ما دامت في العدة.','130','46','0');
INSERT INTO [t133415] VALUES (61,'٥٦ - مسألة: إذا قال الرجل لامرأته: "طلقي نفسك واحدة" فطلقت ثلاثا- لا يقع شيء.','132','46','0');
INSERT INTO [t133415] VALUES (62,'٥٧ - مسألة: قال أبو حنيفة وأبو يوسف: المعتدة عن طلاق رجعي إذا كانت أيام حيضها دون العشرة','134','46','0');
INSERT INTO [t133415] VALUES (63,'٥٨ - مسألة: قال أبو حنيفة وأصحابه: العدتان تتداخلان، وهو مذهب معاذ بن جبل وجابر بن عبد الله.','137','46','0');
INSERT INTO [t133415] VALUES (64,'٥٩ - مسألة: الأقراء الحيض دون الأظهار.','139','46','0');
INSERT INTO [t133415] VALUES (65,'كتاب العتاق','142','0','0');
INSERT INTO [t133415] VALUES (66,'٦٠ - [مسألة]: من ملك ذا رحم محرم منه، يعتق عليه.','142','65','0');
INSERT INTO [t133415] VALUES (67,'٦١ - مسألة: إذا قال الرجل لعبده وهو أكبر سنا منه: "هذا ابني" عتق عليه.','144','65','0');
INSERT INTO [t133415] VALUES (68,'٦٢ - مسألة: صريح الطلاق وكنايات لا يقع به العتق، وإن نوى، حتى لو قال لأمته "أنت طالق"، لم تعتق.','146','65','0');
INSERT INTO [t133415] VALUES (69,'٦٣ - مسألة: إذا قال لأمته: "أول ولد تلديه فهو حر" أو قال: "فأنت حرة". فولدت ميتا، ثم حيا يعتق الحي.','148','65','0');
INSERT INTO [t133415] VALUES (70,'٦٤ - مسألة: إذا قال لعبده: "إن أديت إلى ألفا فأنت حر" فجاء العبد بالألف، وخلى بينها وبين المولى، يجبر على القبول، حتى يعتق العبد بالتخلية.','151','65','0');
INSERT INTO [t133415] VALUES (71,'٦٥ - مسألة: الوطء في العتق المبهم لا يكون بيانا/ للعتق في غير الموطوءة. وهي أن يقول لأمتيه: إحداكما حرة، ثم وطئ إحداهما.','153','65','0');
INSERT INTO [t133415] VALUES (72,'٦٦ - مسألة: الإعتاق يتجزأ.','156','65','0');
INSERT INTO [t133415] VALUES (73,'٦٧ - مسألة: العتق لا يتجزأ.','166','65','0');
INSERT INTO [t133415] VALUES (74,'٦٧ - مكررا- مسألة: إذا اشترى أباه ناويا عن كفارة يمينه أجزأه عن الكفارة، خلافا له.','168','65','0');
INSERT INTO [t133415] VALUES (75,'٦٨ - مسألة: الشهادة القائمة على عتق العبد لا تقبل من غير دعوى العبد، خلافا لهما.','170','65','0');
INSERT INTO [t133415] VALUES (76,'٦٩ - مسألة: رجلان اشتريا عبدا أو اتهبا عبدا هو قريب أحدهما حتى عتق نصيبه، لا يضمن لشريكه نصيبه/، علم بذلك أو لم يعلم. وقالا: يضمن.','173','65','0');
INSERT INTO [t133415] VALUES (77,'٧٠ - مسألة: المكاتب إذا مات عن وفاء، لا ينفسخ عقد الكتابة، بل يؤدي بدل الكتابة عنه، ويحكم بحريته وحرية أولاده وسلامة أكسابه.','175','65','0');
INSERT INTO [t133415] VALUES (78,'٧١ - مسألة: بيع المدبر المطلق لا يجوز. وهو أن يقول لعبده: "دبرتك" أو يقول: "أنت حر بعد موتي".','176','65','0');
INSERT INTO [t133415] VALUES (79,'كتاب الأيمان','179','0','0');
INSERT INTO [t133415] VALUES (80,'٧٢ - [مسألة: ] إذا قال الرجل: "لله على أن أذبح ولدي أو أنحره"- يصح نذره ويخرج عن العهدة بذبح شاة.','179','79','0');
INSERT INTO [t133415] VALUES (81,'٧٣ - مسألة: اليمين الغموس لا توجب الكفارة.','182','79','0');
INSERT INTO [t133415] VALUES (82,'٧٤ - مسألة: التكفير قبل الحنث لا يجوز.','185','79','0');
INSERT INTO [t133415] VALUES (83,'٧٥ - مسألة: إذا أعتق رقبة كافرة عن كفارة اليمين أو الظهار يجزئه.','188','79','0');
INSERT INTO [t133415] VALUES (84,'٧٦ - مسألة: إذا أعتق المكاتب عن كفارة يمينه يجزئه.','191','79','0');
INSERT INTO [t133415] VALUES (85,'٧٧ - مسألة: إذا اشترى أباه عن كفارة إفطاره بجزئه.','193','79','0');
INSERT INTO [t133415] VALUES (86,'٧٨ - مسألة: إذا قال الرجل لغيره: "أعتق عبدك عني على ألف درهم" فقال: "أعتقت" يقع العتق عن الأمر، حتى يكون الولاء له وتسقط عنه الكفارة إذا نوى به التكفير، وتلزمه الألف.','195','79','0');
INSERT INTO [t133415] VALUES (87,'كتاب الحدود','197','0','0');
INSERT INTO [t133415] VALUES (88,'٧٩ - [مسألة]: الإسلام من شرائط الإحصان.','197','87','0');
INSERT INTO [t133415] VALUES (89,'٨٠ - مسألة: اللواطة لا توجب الحد','199','87','0');
INSERT INTO [t133415] VALUES (90,'٨١ - مسألة: الجلد مع النفي لا يجتمعان في زنا الأبكار','201','87','0');
INSERT INTO [t133415] VALUES (91,'٨٢ - مسألة: إذا تزوج بمحارمه ودخل بها، قال: علمت أنها علي حرام - لا يجب عليه الحد','203','87','0');
INSERT INTO [t133415] VALUES (92,'٨٣ - مسألة: إذا استأجر امرأة ليزني بها فزنى بها، وقال: علمت أنها حرام، لا يجب الحد','205','87','0');
INSERT INTO [t133415] VALUES (93,'٨٤ - مسألة: العاقلة البالغة إذا مكنت نفسها من صبي أو مجنون يفعل بها، لا يجب الحد عليها','208','87','0');
INSERT INTO [t133415] VALUES (94,'٨٥ - مسألة: المولى لا يملك إقامة الحدود على مملوكه','210','87','0');
INSERT INTO [t133415] VALUES (95,'٨٦ - مسألة: الزنا الموجب للحد لا يظهر إلا بالإقرار أربع مرات في أربعة مجالس. وعنده بالإقرار مرة واحدة','212','87','0');
INSERT INTO [t133415] VALUES (96,'٨٧ - مسألة: الإحصان يثبت بشهادة رجل وامرأتين','215','87','0');
INSERT INTO [t133415] VALUES (97,'٨٨ - مسألة: حد القذف لا يورث، ولا يسقط بإسقاطه، ولا يجري فيه التداخل. وعنده: يورث، ويسقط، ويتداخل','217','87','0');
INSERT INTO [t133415] VALUES (98,'كتاب السرقة','221','0','0');
INSERT INTO [t133415] VALUES (99,'٨٩ - مسألة: القطع مع الضمان لا يجتمعان','221','98','0');
INSERT INTO [t133415] VALUES (100,'٩٠ - مسألة: لا قطع على النباش','222','98','0');
INSERT INTO [t133415] VALUES (101,'٩١ - مسألة: السارق لا يؤتى على أطرافه الأربعة - خلافا له','225','98','0');
INSERT INTO [t133415] VALUES (102,'٩٢ - مسألة: السارق إذا ملك المسروق بعد القضاء، قبل الاستيفاء، بالهبة وغيرها من أسباب الملك، لا يجب القطع استيفاء، خلافا له','227','98','0');
INSERT INTO [t133415] VALUES (103,'٩٣ - مسألة: السارق من المودع والمستعير والمضارب والمرتهن والمستأجر والغاصب- يقطع بخصومة هؤلاء وبخصومة المالك وحده','230','98','0');
INSERT INTO [t133415] VALUES (104,'كتاب السير','232','0','0');
INSERT INTO [t133415] VALUES (105,'٩٤ - مسألة: قسمة الغنائم في دار الحرب لا يجوز','232','104','0');
INSERT INTO [t133415] VALUES (106,'٩٥ - مسألة: قال أبو حنيفة وأصحابه: خمس الغنيمة يقسم على ثلاثة أسهم، سهم لليتامى، وسهم للمساكين، وسهم لأبناء السبيل','234','104','0');
INSERT INTO [t133415] VALUES (107,'٩٦ - مسألة: الكفار إذا استولوا على أموال المسلمين وأحرزوها بدار الحرب، ملكوها','237','104','0');
INSERT INTO [t133415] VALUES (108,'٩٧ - مسألة: الحربي إذا أسلم في دار الحرب ولم يهاجر إلينا، فقتله مسلم أو ذمي، لا يجب عليه الدية','239','104','0');
INSERT INTO [t133415] VALUES (109,'٩٨ - مسألة: العبد الآبق إلى دار الحرب إذا أخذه الكفار لا يملكونه','240','104','0');
INSERT INTO [t133415] VALUES (110,'٩٩ - مسالة: الغازي إذا جاوز الدرب فارسا، فنفقت فرسه وقاتل راجلا، يستحق سهم الفرسان. ولو كان على العكس فعلى العكس','242','104','0');
INSERT INTO [t133415] VALUES (111,'١٠٠ - مسألة: المرتدة لا يباح قتلها','245','104','0');
INSERT INTO [t133415] VALUES (112,'١٠١ - مسألة: الجزية تسقط بالموت والإسلام','247','104','0');
INSERT INTO [t133415] VALUES (113,'١٠٢ - مسألة: أمان العبد المحجور عن القتال لا يصح، خلافا له','249','104','0');
INSERT INTO [t133415] VALUES (114,'١٠٣ - مسألة: إسلام الصبي العاقل يصح','251','104','0');
INSERT INTO [t133415] VALUES (115,'كتاب الغصب','253','0','0');
INSERT INTO [t133415] VALUES (116,'١٠٤ - [مسألة]: زوائد الغصب أمانة: متصلة كانت كالسمن والجمال، أو منفصلة كاللبن والولد','253','115','0');
INSERT INTO [t133415] VALUES (117,'١٠٥ - مسألة: الزوائد المتصلة لا تضمن بالبيع والتسليم','255','115','0');
INSERT INTO [t133415] VALUES (118,'١٠٦ - مسألة: الدور والعقار لا تضمن بالغصب','255','115','0');
INSERT INTO [t133415] VALUES (119,'١٠٧ - مسألة: المنافع لا تضمن بالغضب','257','115','0');
INSERT INTO [t133415] VALUES (120,'١٠٨ - مسألة: المضمونات تملك بالغصب عند أداء الضمان مستندا إلى وقت الغصب','258','115','0');
INSERT INTO [t133415] VALUES (121,'١٠٩ - مسألة: نقصان الولادة ينجبر بالولد إذا كان له وفاء بقيمة النقصان','261','115','0');
INSERT INTO [t133415] VALUES (122,'١١٠ - مسألة: إذا غصب حنطة وطحنها أو زرعها ينقطع حق المالك ويصير ملكا للغاصب بالضمان، خلافا له','262','115','0');
INSERT INTO [t133415] VALUES (123,'١١١ - مسألة: إذا غصب ساجة وأدخلها في بنائه، ينقطع حق المالك إلى الضمان','265','115','0');
INSERT INTO [t133415] VALUES (124,'١١٢ - مسألة: المسلم إذا أتلف خمر الذمي أو خنزيره - يجب عليه الضمان','267','115','0');
INSERT INTO [t133415] VALUES (125,'كتاب الوديعة','269','0','0');
INSERT INTO [t133415] VALUES (126,'١١٣ - مسألة: المودع إذا خالف في الوديعة ثم عاد إلى الوفاق يبرأ عن الضمان','269','125','0');
INSERT INTO [t133415] VALUES (127,'١١٤ - مسألة: إذا أودع عند صبي محجور عليه مالا، أو عند عبد محجور عليه مالا، فاستهلكه - لا ضمان عليه أصلا، وعلى العبد ضمانه بعد العتق','270','125','0');
INSERT INTO [t133415] VALUES (128,'١١٥ - مسألة: المودع إذا سافر بمال الوديعة في طريق آمن، فهلكت الوديعة في يده - لا يضمن. وعنده يضمن','272','125','0');
INSERT INTO [t133415] VALUES (129,'كتاب العارية','275','0','0');
INSERT INTO [t133415] VALUES (130,'١١٦ - [مسألة]: إذا هلك المستعار لا يضمن في أي حال هلك','275','129','0');
INSERT INTO [t133415] VALUES (131,'كتاب الصيد','278','0','0');
INSERT INTO [t133415] VALUES (132,'١١٧ - مسألة: متروك التسمية عامدا عند الذبح لا يحل أكله، خلافا له','278','131','0');
INSERT INTO [t133415] VALUES (133,'١١٨ - مسألة: الجنين لا يتذكى بذكاة الأم','281','131','0');
INSERT INTO [t133415] VALUES (134,'١١٩ - مسألة: لحم الخيل مكروه كراهة تحريم','283','131','0');
INSERT INTO [t133415] VALUES (135,'الأضحية','286','0','0');
INSERT INTO [t133415] VALUES (136,'١٢٠ - مسألة: الأضحية واجبة على الأغنياء البالغين المقيمين','286','135','0');
INSERT INTO [t133415] VALUES (137,'كتاب الهبة','289','0','0');
INSERT INTO [t133415] VALUES (138,'١٢١ - مسألة: هبة المشاع فيما يحتمل القسمة لا تتم ولا تفيد الملك قبل القسمة. وعنده تامة','289','137','0');
INSERT INTO [t133415] VALUES (139,'١٢٢ - مسألة: الهبة من الأجنبي لا تقع لازمة، حتى يملك الواهب الرجوع فيه','292','137','0');
INSERT INTO [t133415] VALUES (140,'كتاب البيوع','296','0','0');
INSERT INTO [t133415] VALUES (141,'١٢٣ - [مسألة]: البيع الفاسد يفيد الملك عند اتصال القبض به','296','140','0');
INSERT INTO [t133415] VALUES (142,'١٢٤ - مسألة: إذا باع قفيز جص بقفيزي جص، لا يجوز','298','140','0');
INSERT INTO [t133415] VALUES (143,'١٢٥ - مسألة: التقابض في بيع الطعام بالطعام ليس بشرط لجواز البيع','300','140','0');
INSERT INTO [t133415] VALUES (144,'١٢٦ - مسألة: إذ باع درهما ودينارا بدرهمين ودينارين أو باع كر حنطة وكر شعير بكرى حنطة وكرى شعير - يصح البيع ويصرف الجنس إلى خلاف الجنس','302','140','0');
INSERT INTO [t133415] VALUES (145,'١٢٧ - مسألة: بيع الرطب بالتمر متساويا، كيلا - يجوز، خلافا لهم','304','140','0');
INSERT INTO [t133415] VALUES (146,'١٢٨ - مسألة: إذا باع فلسا رائجا بعينه، بفلسين رائجين - يجوز','307','140','0');
INSERT INTO [t133415] VALUES (147,'١٢٩ - مسألة: بيع لحم الشاة بالشاة يجوز مطلقا. وقالا: يجوز على سبيل الاعتبار','309','140','0');
INSERT INTO [t133415] VALUES (148,'١٣٠ - مسألة: شراء ما باع بأقل مما باع قبل نقد الثمن، لا يجوز - خلافا له','310','140','0');
INSERT INTO [t133415] VALUES (149,'١٣١ - مسألة: الزوائد المنفصلة من البيع المتولدة بعد القبض، نحو الولد والثمر، تمنع الرد بالعيب. وعنده: لا تمنع','313','140','0');
INSERT INTO [t133415] VALUES (150,'١٣٢ - مسألة: الزيادة في الثمن والمثمن تصح وتلتحق بأصل العقد ويجعل كأن العقد ورد عليهما - خلافا له','315','140','0');
INSERT INTO [t133415] VALUES (151,'١٣٣ - مسألة: إذا اشترى شيئا لم يره، يصح العقد، ويثبت له الخيار إذا رآه - خلافا له','318','140','0');
INSERT INTO [t133415] VALUES (152,'١٣٤ - مسألة: الكافر إذا اشترى عبدا مسلما يصح الشراء ويجبر على البيع في الحال. وعنده لا يصح أصلا','320','140','0');
INSERT INTO [t133415] VALUES (153,'١٣٥ - مسألة: بيع لبن الآدميات الحرائر والإماء لا يجوز','322','140','0');
INSERT INTO [t133415] VALUES (154,'١٣٦ - مسألة. بيع العقار المبيع قبل القبض، يجوز','324','140','0');
INSERT INTO [t133415] VALUES (155,'١٣٧ - مسألة: كل عقد له مجيز حال وقوعه، كالبيع والإجارة ونحوهما، ينعقد من الفضولي، ويتوقف نفاذه على إجازة المالك: إن أجازة المالك ثبت حكمه مستندا إلى وقت العقد، وإلا يبطل التصرف. وعنده: لا ينعقد أصلا','325','140','0');
INSERT INTO [t133415] VALUES (156,'١٣٨ - مسألة: المشتري من الغاصب إذا أعتنق العتد المشترى، يصح ويتوقف على إجازة المالك البيع. فإذا أجاز البيع، يعتق على المشترى، ويكون الولاء له','328','140','0');
INSERT INTO [t133415] VALUES (157,'١٣٩ - مسألة: خيار الشرط لا يورث - خلافا له','330','140','0');
INSERT INTO [t133415] VALUES (158,'١٤٠ - مسألة: رجلان اشتريا شيئا على أنهما بالخيار ثلاثة أيام، لا ينفرد أحدهما بالفسخ. وكذا في خيار الرؤية وخيار العيب - خلافا لهما','332','140','0');
INSERT INTO [t133415] VALUES (159,'١٤١ - مسألة: المشترى إذا أفلس بعد قبض المبيع أو مات مفلسا، لا يثبت للبائع حق الفسخ، بل يباع المبيع ويقسم بين الغرماء بالحصص','334','140','0');
INSERT INTO [t133415] VALUES (160,'١٤٢ - مسألة: وطء الثيب يمنع الرد بالعيب','337','140','0');
INSERT INTO [t133415] VALUES (161,'١٤٣ - مسألة: قال أبو حنيفة: إذا باع نفس العبد منه بجارية أو أعتق عبده على جارية، ثم استحقت الجارية أو ردت بعيب، يرجع المولى على العبد بقيمة العبد لا بقيمة الجارية. وعندهم بقيمة الجارية','339','140','0');
INSERT INTO [t133415] VALUES (162,'١٤٤ - مسألة: سلم الحال بغير الأجل لا يجوز','341','140','0');
INSERT INTO [t133415] VALUES (163,'١٤٥ - مسألة: إذا أسلم في المنقطع في غير حينه وجعل الأجل في حينه، أو أسلم في حينه وجعل الأجل في حينه، وهو منقطع فيما بين ذلك، لا يجوز. خلافا له','344','140','0');
INSERT INTO [t133415] VALUES (164,'١٤٦ - مسألة: السلم في الحيوان لا يجوز، وإن بين أوصافه. وعنده: يجوز إذا بين جنسه ونوعه وسنه ووصفه','345','140','0');
INSERT INTO [t133415] VALUES (165,'١٤٧ - مسألة: إذا اختلف المتبايعان في مقدار الثمن، والسلعة هالكة بعد القبض، لا يتحالفان، والقول قول المشتري مع يمينيه. وعنده يتحالفان ويترادان العقد بالقيمة','347','140','0');
INSERT INTO [t133415] VALUES (166,'١٤٨ - مسألة. الوكيل بالبيع المطلق إذا باع بغبن فاحش، ينفذ تصرفه على الموكل','350','140','0');
INSERT INTO [t133415] VALUES (167,'كتاب الصرف','352','0','0');
INSERT INTO [t133415] VALUES (168,'١٤٩ - [مسألة]: الدراهم والدنانير لا يتعينان في عقود المعاوضات وفسوخها وإن عينت، حتى لا يطالب المشترى بأداء تلك الدراهم، وله أن يمسكها ويسلم غير [ها]، ولا ينفسخ البيع بهلاكها قبل القبض','352','167','0');
INSERT INTO [t133415] VALUES (169,'كتاب الشفعة','356','0','0');
INSERT INTO [t133415] VALUES (170,'١٥٠ - مسألة: حق الشفعة يثبت للجار الملازق والشريك في الطريق. وعنده لا يثبت','356','169','0');
INSERT INTO [t133415] VALUES (171,'١٥١ - مسألة: الشفعة تستحق على عدد الرءوس. وعنده: على قدر الأنصباء','359','169','0');
INSERT INTO [t133415] VALUES (172,'كتاب الإجارات','361','0','0');
INSERT INTO [t133415] VALUES (173,'١٥٢ - مسألة: الأجرة لا تملك بنفس العقد إلا بالتعجيل','361','172','0');
INSERT INTO [t133415] VALUES (174,'١٥٣ - مسألة: إجارة المشاع غير صحيحة، سواء كان محتملا القسمة كالدار ونحوها، أو غير محتمل كالدابة ونحوها - خلافا له','363','172','0');
INSERT INTO [t133415] VALUES (175,'١٥٤ - مسألة: الأجير المشترك يضمن ما جنت يداه، والمالك بالخيار: إن شاء ضمنه الثوب غير مقصور، ولم يعطه الأجر. والقياس أن لا يضمن','365','172','0');
INSERT INTO [t133415] VALUES (176,'كتاب الشهادات','368','0','0');
INSERT INTO [t133415] VALUES (177,'١٥٥ - [مسألة]: النكاح ينعقد بحضرة الفساق','368','176','0');
INSERT INTO [t133415] VALUES (178,'١٥٦ - مسألة: شهادة أهل الذمة بعضهم على بعض مقبولة','371','176','0');
INSERT INTO [t133415] VALUES (179,'١٥٧ - مسألة: المحدود في القذف إذا تاب ثم شهد، لا تقبل شهادته - خلافا له','372','176','0');
INSERT INTO [t133415] VALUES (180,'١٥٨ - مسألة: شهادة أحد الزوجين لصاحبه غير مقبولة','375','176','0');
INSERT INTO [t133415] VALUES (181,'١٥٩ - مسألة: النكاح ينعقد ويظهر بشهادة رجل وامرأتين. وقال الخصم: لا ينعقد ولا يظهر','378','176','0');
INSERT INTO [t133415] VALUES (182,'١٦٠ - مسألة: قضاء القاضي في العقود والفسوخ بشهادة الزور، ينفذ ظاهرا وباطنا','380','176','0');
INSERT INTO [t133415] VALUES (183,'كتاب الدعوى','388','0','0');
INSERT INTO [t133415] VALUES (184,'١٦١ - [مسألة]: النكول حجة يقضي بها في باب الأموال. وعنده لا يقضي بمجرد النكول، بل ينقل اليمين إلى المدعي. فإذا حلف يقضي له','388','183','0');
INSERT INTO [t133415] VALUES (185,'١٦٢ - مسألة: الاستحلاف لا يجري في الأشياء الثلاثة، وهي: النكاح ويندرج تحته الرجعة والفئ في الإيلاء، والرق ويندرج تحته الولاء، والنسب ويندرج تحته أمومية الولد لأنها من آثار النسب','393','183','0');
INSERT INTO [t133415] VALUES (186,'١٦٣ - مسألة: القضاء على الغائب، وللغائب - لا يجوز','395','183','0');
INSERT INTO [t133415] VALUES (187,'١٦٤ - مسألة: الخارجان إذا ادعيا ملكا مطلقا أو ادعيا النتاج، والعين في','398','183','0');
INSERT INTO [t133415] VALUES (188,'١٦٥ - مسألة: الخارج مع ذي اليد إذا تنازعا في الملك المطلق، وأقاما جميعا البينة - يقضى ببينة الخارج. وعنده: يقضى ببينة ذي اليد','400','183','0');
INSERT INTO [t133415] VALUES (189,'١٦٦ - مسألة: الأب إذا استولد جارية ابنة، يتملكها بالقيمة، ولا يلمه العقر. وعنده: يتملكها ويلزمه العقر','403','183','0');
INSERT INTO [t133415] VALUES (190,'كتاب الإقرار','406','0','0');
INSERT INTO [t133415] VALUES (191,'١٦٧ - [مسألة]: دين الصحة مقدم على دين المرض. وعنده: لا يقدم، بل غريم المرض أسوة لغريم الصحة','406','190','0');
INSERT INTO [t133415] VALUES (192,'١٦٨ - مسألة: المريض مرض الموت إذا أقر لبعض ورثته لا يصح، إلا بتصديق الباقين','409','190','0');
INSERT INTO [t133415] VALUES (193,'كتاب الوكالة','411','0','0');
INSERT INTO [t133415] VALUES (194,'١٦٩ - مسألة: الوكيل بالخصومة إذا أقر على موكله في مجلس القضاء، ينفذ إقراره. ولو أقر في غير مجلس القضاء، لا ينفذ','411','193','0');
INSERT INTO [t133415] VALUES (195,'١٧٠ - مسألة: التوكيل بغير رضا الخصم لا يقع لازما','413','193','0');
INSERT INTO [t133415] VALUES (196,'كتاب الكفالة','415','0','0');
INSERT INTO [t133415] VALUES (197,'١٧١ - مسألة: "الكفالة" بنفس من عليه الدين وبالأعيان المضمونة، نحو الغصب والمستعار يصح. وعنده لا يصح بالنفس قولا واحدا، وله في العين قولان','415','196','0');
INSERT INTO [t133415] VALUES (198,'١٧٢ - مسألة: الكفالة بالدين عن ميت مفلس لا تصح، خلافا لهم','418','196','0');
INSERT INTO [t133415] VALUES (199,'كتاب الحوالة','423','0','0');
INSERT INTO [t133415] VALUES (200,'١٧٣ - [مسألة]: المحتال عليه إذا مات مفلسا، يثبت للمحتال له ولاية مطالبة المحيل بقضاء دينه. وعنده: لا يثبت','423','199','0');
INSERT INTO [t133415] VALUES (201,'كتاب الصلح','426','0','0');
INSERT INTO [t133415] VALUES (202,'١٧٤ - [مسألة]: الصلح على الإنكار جائز','426','201','0');
INSERT INTO [t133415] VALUES (203,'كتاب الرهن','429','0','0');
INSERT INTO [t133415] VALUES (204,'١٧٥ - مسألة: الرهن مضمون بالأقل من قيمته ومن الدين. وعنده أمانة','429','203','0');
INSERT INTO [t133415] VALUES (205,'١٧٦ - مسألة: رهن المشاع لا يجوز، سواء كان محتملا للقسمة أو غير محتمل لها- رهن من شريكه أو من أجنبي. وبعضهم قالوا: يبطل والصحيح أنه ينعقد بوصف الفساد','432','203','0');
INSERT INTO [t133415] VALUES (206,'١٧٧ - مسألة: الراهن إذا أعتق العبد المرهون ينفذ إعتاقه، غير أنه: إن كان موسرا يضمن قيمته، ويكون رهنا عند المرتهن. وإن كان معسرا يسعى العبد في قيمته، ويكون رهنا عنده، ويرجع العبد به على المولى','434','203','0');
INSERT INTO [t133415] VALUES (207,'كتاب الأشربة','437','0','0');
INSERT INTO [t133415] VALUES (208,'١٧٨ - مسألة: تخليل الخمر مباح. والخل الحاصل به حلال، سواء كان بعلاج بأن ألقى فيها الملح أو الخل، أو بغير علاج بأن نقلها من الظل إلى الشمس أو من الشمس إلى الظل. وعنده: إن كان بعلاج لا بحل قولا واحدا، وإن كان بغير علاج له فيه قولان','437','207','0');
INSERT INTO [t133415] VALUES (209,'كتاب الإكراه','443','0','0');
INSERT INTO [t133415] VALUES (210,'١٧٩ - مسألة: طلاق المكره وعتاقه ونكاحه ونذره ويمينه، صحيح نافذ - خلافا له','443','209','0');
INSERT INTO [t133415] VALUES (211,'١٨٠ - مسألة: إذا باع مكرها وسلم مكرها، ينفذ البيع ويثبت الملك - خلافا لزفر','446','209','0');
INSERT INTO [t133415] VALUES (212,'١٨١ - مسألة: قال أبو حنيفة ومحمد: إذا أكره إنسانا على قتل غيره بالسيف، فقتله، يجب القصاص على المكره دون المكره. وقال زفر: يجب على المكره دون المكره. وقال الشافعى: يجب عليهما. وقال أبو يوسف: لا يجب على واحد منهما','448','209','0');
INSERT INTO [t133415] VALUES (213,'كتاب الحجر','451','0','0');
INSERT INTO [t133415] VALUES (214,'١٨٢ - مسألة: الحجر على الحر السفيه باطل، حتى تنفذ تصرفاته قبل الحجر وبعده عند أبى حنيفة','451','213','0');
INSERT INTO [t133415] VALUES (215,'كتاب المأذون','454','0','0');
INSERT INTO [t133415] VALUES (216,'١٨٣ - مسألة: المأذون في النوع، يكون مأذونا في الأنواع كلها. وعنده: لا يصير مأذونا إلا في النوع الذي خص المولى الإذن به','454','215','0');
INSERT INTO [t133415] VALUES (217,'١٨٤ - مسألة: المولى إذا رأى عبده يبيع ويشترى فسكت، يصير مأذونا له في التجارة خلافا له','456','215','0');
INSERT INTO [t133415] VALUES (218,'١٨٥ - مسألة: الصبى العاقل المأذون له في التجارة إذا باع أو اشترى أو آجر أو استأجر ينفذ تصرفه. وعنده لا ينفذ تصرفه. ولو تصرف قبل إذن المولى ينعقد عندنا، ولا ينعقد عنده','460','215','0');
INSERT INTO [t133415] VALUES (219,'١٨٦ - مسألة: رقبة العبد المأذون تباع بدين التجارة. وعنده: لا تباع','461','215','0');
INSERT INTO [t133415] VALUES (220,'كتاب الجنايات','464','0','0');
INSERT INTO [t133415] VALUES (221,'١٨٧ - مسألة: موجب العمد هو القصاص عينا. وليس للولى أخذ الدية بغير رضا القاتل. وله قولان: في قول: الواجب أحدهما لا بعينه والمولى خيار التعين. وفي قول: الواجب هو القصاص عينا، كلن للولى، حق إسقاط القصاص وأخذ الدية بغير رضا القاتل','464','220','0');
INSERT INTO [t133415] VALUES (222,'١٨٨ - آلة: شريك الأب لا يجب عليه القصاص. وعنده: يجب. وأما شريك الخاطئ مش واضح السبع وشريك الصبي والمجنون وشريك نفسه - عندنا: لا يجب. وهو مش واضح من مذهب الشافعى. وبعضهم يمنعون','467','220','0');
INSERT INTO [t133415] VALUES (223,'١٨٩ - مسألة: الواحد يقتل بالجماعة قصاصا اكتفاء. وعنده: لا يقتل اكتفاء، غير أنه إن قتلهم على التعاقب يقتل بالأول اكتفاء وتجب الديات للباقيين. وإن قتلهم على المقارنة: له فيه قولان- في قول: يقتل بالواحد غير عين، وتجب الديات للباقيين','469','220','0');
INSERT INTO [t133415] VALUES (224,'١٩٠ - مسألة: إذا ضرب إنسانا بالسوط الصغير ووالى في الضربات، حتى مات، لا يجب عليه القصاص','474','220','0');
INSERT INTO [t133415] VALUES (225,'١٩١ - مسألة: من له القصاص في الطرف إذا استوفى الطرف وسرى إلى النفس ومات - يضمن دية النفس. وعندهم: لا يضمن','477','220','0');
INSERT INTO [t133415] VALUES (226,'١٩٢ - مسألة: من له القصاص في النفس إذا قطع طرف من عليه القصاص، وبرأ، ثم عفا عن النفس، يضمن أرش اليد. وعندهم: لا يضمن','480','220','0');
INSERT INTO [t133415] VALUES (227,'١٩٣ - مسألة: المشجوج رأسه او المقطوعة يده إذا عفا عن الشجة والقطع ثم سرى إلى النفس ومات يضمن والقاطع دية النفس. وقالوا: لا يضمن','482','220','0');
INSERT INTO [t133415] VALUES (228,'١٩٤ - مسألة: القصاص إذا كان بين صغير وكبير يتفرد الكبير بالاستيفاء. وعندهم لا يتفرد','484','220','0');
INSERT INTO [t133415] VALUES (229,'١٩٥ - مسألة: القتل بالمثقل دقا لا يوجب القصاص. وقالوا: يوجب. والقتل بالمثقل جرحا: عن أبى حنيفة روايتان. والأصح أن يوجب','487','220','0');
INSERT INTO [t133415] VALUES (230,'١٩٦ - مسألة: شهود القصاص إذا رجعوا بعد الاستيفاء أو جاء المشهود بقتله حيا والشهود أقروا بالتعمد - لا يجب عليهم القتل','490','220','0');
INSERT INTO [t133415] VALUES (231,'١٩٧ - مسألة: الجمل أو الصبي أو المجنون إذا صايل على إنسان فقتله المصول عليه: يضمن. وعنده: لا يضمن','491','220','0');
INSERT INTO [t133415] VALUES (232,'١٩٨ - مسألة: المسلم يقتل بالذمي قصاصا، خلافا له','494','220','0');
INSERT INTO [t133415] VALUES (233,'١٩٩ - مسألة: الحر يقتل بالعبد قصاصا. وعنده: لا يقتل','498','220','0');
INSERT INTO [t133415] VALUES (234,'٢٠٠ - مسألة: مباح الدم بأي سبب كان إذا التجأ إلى الحرم يصير آمنا عن القتل فيه والإخراج عنه للقتل. لكن يمنع عنه الطعام والشراب حتى يضطر إلى الخروج، فيخرج فيقتل','500','220','0');
INSERT INTO [t133415] VALUES (235,'٢٠١ - مسألة: دية العبد المقتول خطأ لا يزاد على عشرة آلاف درهم، بل ينقص منها عشرة. وتتحملها العاقلة. وعنده: تبلغ إلى تمام قيمته، ولا تتحملها العاقلة، كضمان سائر الأموال','504','220','0');
INSERT INTO [t133415] VALUES (236,'٢٠٢ - مسألة: القتل العمد لا يوجب الكفارة، خلافا له','507','220','0');
