INSERT INTO [t13443] VALUES (1,'المقدمة .. (وما نرسل بالآيات إلا تخويفا).','3','0','0');
INSERT INTO [t13443] VALUES (2,'سبب تأليف الكتاب.','3','0','0');
INSERT INTO [t13443] VALUES (3,'المعنى العام للخوف.','4','0','0');
INSERT INTO [t13443] VALUES (4,'من ثمرات الخوف ومعانيه.','5','0','0');
INSERT INTO [t13443] VALUES (5,'قول الإمام الجنيد في الخوف.','5','0','0');
INSERT INTO [t13443] VALUES (6,'قول أبي حفص النيسابوري عن الخوف وثمراته.','5','0','0');
INSERT INTO [t13443] VALUES (7,'قول أبي سليمان الداراني عما يثمره رحيل الخوف عن القلب.','6','0','0');
INSERT INTO [t13443] VALUES (8,'قول ذي النون المصري عما يثمره رحيل الخوف عن القلب.','6','0','0');
INSERT INTO [t13443] VALUES (9,'قول شيخ الإسلام عن الخوف المحمود؟.','6','0','0');
INSERT INTO [t13443] VALUES (10,'الفرق بين السلف والخلف في الخوف من الله تعالى.','7','0','0');
INSERT INTO [t13443] VALUES (11,'أسباب خوف السلف من الله تعالى واعتبارهم بما يجريه من الآيات.','7','0','0');
INSERT INTO [t13443] VALUES (12,'أسباب عدم خوف الناس اليوم من الله تعالى وعدم اعتبارهم بما يجريه من الآيات.','8','0','0');
INSERT INTO [t13443] VALUES (13,'ماذا قال ابن مسعود حينما زلزلت الكوفة؟!.','8','0','0');
INSERT INTO [t13443] VALUES (14,'ما هو معنى استعتاب الله لعباده؟!.','8','0','0');
INSERT INTO [t13443] VALUES (15,'من أسباب قسوة القلوب اليوم، وفراغها من الخوف من الله عز وجل.','9','0','0');
INSERT INTO [t13443] VALUES (16,'قول ابن الجوزي عن الدنيا، وأنه لا أحد يتصرف فيها سوى الله، والتعليق على ذلك.','9','0','0');
INSERT INTO [t13443] VALUES (17,'الآيات والكوارث سببها الكفر والمعاصي، والأدلة على ذلك.','11','0','0');
INSERT INTO [t13443] VALUES (18,'الأدلة من القرآن الكريم على أن ما يحدثه من الكوارث على الكفار والعصاة عقوبة.','11','0','0');
INSERT INTO [t13443] VALUES (19,'ما حصل في جنوب أمريكا من إعصار مدمر يذكرنا بما أصاب الأمم الكافرة الماضية.','12','0','0');
INSERT INTO [t13443] VALUES (20,'ذكر ما أخبر به تعالى مما دمر به قوم هود بما يشبه الإعصار الذي ضرب أمريكا.','13','0','0');
INSERT INTO [t13443] VALUES (21,'ذكر ما أخبر به تعالى مما أغرق به قوم نوح بما يشبه الغرق الذي حل بأمريكا.','13','0','0');
INSERT INTO [t13443] VALUES (22,'ذكر ما أصاب قبيلة (سبأ) اليمنية لما كفرت بالله وبنعمته عليها.','14','0','0');
INSERT INTO [t13443] VALUES (23,'ذكر ما قاله ابن القيم عن شؤم الذنوب وأنها سبب في حصول الآيات والكوارث.','15','0','0');
INSERT INTO [t13443] VALUES (24,'ذكر ما فعله أبو الدرداء - رضي الله عنه - لما فتحت قبرص وفرق بين أهلها.','17','0','0');
INSERT INTO [t13443] VALUES (25,'ذكر تحذير الله عباده من الذنوب وأن ما حل بالأمم السابقة من الآيات بسبب ذنوبهم.','18','0','0');
INSERT INTO [t13443] VALUES (26,'هل سينفع الناس إحالتهم الكوارث والآيات لـ (الطبيعة) حينما تحل بهم؟!.','19','0','0');
INSERT INTO [t13443] VALUES (27,'هل معرفة الأسباب الطبيعية لحدوث الكوارث ينافي الخوف من الله، وأنها عقابا منه؟.','20','0','0');
INSERT INTO [t13443] VALUES (28,'السلف يعرفون السبب الطبيعي لحدوث الآيات والكوارث ولكنهم يخافون أيضا.','20','0','0');
INSERT INTO [t13443] VALUES (29,'ذكر ما حصل لكثير من أهل الوقت لما علموا بالأسباب الطبيعية للآيات والكوارث!.','20','0','0');
INSERT INTO [t13443] VALUES (30,'القول بأن الفضاء لا حد له إنكار للسموات السبع والرب الذي فوقها.','21','0','0');
INSERT INTO [t13443] VALUES (31,'السبب الطبيعي لحدوث الزلازل.','21','0','0');
INSERT INTO [t13443] VALUES (32,'السبب الكوني للكسوف والخسوف.','21','0','0');
INSERT INTO [t13443] VALUES (33,'هل العلم بوقت حدوث الحوادث الكونية ينافي أن يكون تخويفا من الله؟!.','21','0','0');
INSERT INTO [t13443] VALUES (34,'قول الحسن البصري في تفسير قوله تعالى: (ولقد صدق عليهم إبليس ظنه).','22','0','0');
INSERT INTO [t13443] VALUES (35,'هل العلم بوقت ولادة المخلوق ينافي أن الله تعالى هو الذي خلقه؟!.','23','0','0');
INSERT INTO [t13443] VALUES (36,'هل زرع النبات والعلم بوقت خروجه ينافي أن الله تعالى هو الذي خلقه؟!.','23','0','0');
INSERT INTO [t13443] VALUES (37,'معرفة الأسباب وأوقات انعقادها أجراه الله في الرحمة كما أجراه في العذاب والنقمة.','23','0','0');
INSERT INTO [t13443] VALUES (38,'اقتران الحوادث الكونية بكسوف الشمس أو خسوف القمر.','24','0','0');
INSERT INTO [t13443] VALUES (39,'(حاشية): الإعلام بالكسوف ليس من هدي أهل الإسلام.','24','0','0');
INSERT INTO [t13443] VALUES (40,'قل ما تسلم أطراف الأرض البعيدة عن الإسلام من الحوادث أثناء الكسوف والخسوف.','25','0','0');
INSERT INTO [t13443] VALUES (41,'أحاديث توضح أن حدوث الكسوف والخسوف مدعاة للخوف من الله عز وجل.','25','0','0');
INSERT INTO [t13443] VALUES (42,'ذكر ما فعله النبي - صلى الله عليه وسلم - لما كسفت الشمس في عهده، وما قاله.','26','0','0');
INSERT INTO [t13443] VALUES (43,'ذكر أحاديث صحيحة توضح كيفية صلاة الكسوف والخسوف.','26','0','0');
INSERT INTO [t13443] VALUES (44,'تعليق على قول بعض الحسابين (الفلكيين) عن كسوف قادم بأنه (كسوف جميل).','27','0','0');
INSERT INTO [t13443] VALUES (45,'ذكر فرح الكفار حين حدوث الكسوف والخسوف.','28','0','0');
INSERT INTO [t13443] VALUES (46,'(حاشية): أحاديث تبين أن هذه الأمة ستتبع سنن أهل الكتاب.','28','0','0');
INSERT INTO [t13443] VALUES (47,'كلام جليل القدر لشيخ الإسلام بأن السبب الطبيعي لحدوث الكسوف لا ينافي الخوف.','29','0','0');
INSERT INTO [t13443] VALUES (48,'كيف يكون الخوف من الله، وما الذي يتضمنه التخويف منه سبحانه؟!.','29','0','0');
INSERT INTO [t13443] VALUES (49,'ذكر ما يستحب فعله حين حصول الكسوف والخسوف.','30','0','0');
INSERT INTO [t13443] VALUES (50,'يحصل كسوف الشمس دائما في آخر الشهر، وخسوف القمر في أوسطه.','30','0','0');
INSERT INTO [t13443] VALUES (51,'كلام لابن تيمية بأن الريح التي أرسلها الله على عاد جاءت في موسم الشتاء.','31','0','0');
INSERT INTO [t13443] VALUES (52,'(حاشية): أدلة على أن الريح التي ضربت عاد كانت في الشتاء الذي تشتد فيه الرياح.','31','0','0');
INSERT INTO [t13443] VALUES (53,'هل كون الأعاصير تأتي في موسم معروف تنافي أن تكون عذابا وتخويفا؟!.','32','0','0');
INSERT INTO [t13443] VALUES (54,'ما هي حقيقة (الطبيعة) التي يحال إليها ما يجريه الله من الآيات والكوارث؟!.','33','0','0');
INSERT INTO [t13443] VALUES (55,'إحالة الحوادث الكونية للطبيعة تعطيل وإلحاد.','33','0','0');
INSERT INTO [t13443] VALUES (56,'ما هي الطبيعة عند الطبائعيين والملاحدة وزنادقة الأطباء؟!.','33','0','0');
INSERT INTO [t13443] VALUES (57,'كلام نفيس للإمام الخطابي في تبيان أوضح الدلائل على أن للخلق صانعا ومدبرا.','34','0','0');
INSERT INTO [t13443] VALUES (58,'الكلام حول النطفة، وما قاله الخطابي فيها، وتعلق ذلك بموضوع الكتاب.','34','0','0');
INSERT INTO [t13443] VALUES (59,'الإمام الخطابي يعرض أربعة أمثلة تبين زيف (الطبائعيين) وإلحاد (الداروينيين).','36','0','0');
INSERT INTO [t13443] VALUES (60,'ما هي نظرية (التصميم الذكي) الغربية التي تهدم نظرية (داروين)؟!.','38','0','0');
INSERT INTO [t13443] VALUES (61,'هل نظرية (التصميم الذكي) توافق عقيدة المسلمين؟.','38','0','0');
INSERT INTO [t13443] VALUES (62,'إحالة لكتاب المؤلف (الفرقان في بيان إعجاز القرآن) فيما يتعلق بداروين وأمثاله.','38','0','0');
INSERT INTO [t13443] VALUES (63,'كلام نفيس لشيخ الإسلام يبين غاية ما توصل إليه المتفلسفة وأتباعهم في الكونيات.','39','0','0');
INSERT INTO [t13443] VALUES (64,'الإمام ابن القيم يوضح ما هي الطبيعة التي يعتبرها الجهال وراء الحوادث الكونية.','40','0','0');
INSERT INTO [t13443] VALUES (65,'كلام نفيس جدا لابن القيم في (الطبيعة) وتفاعلات عناصرها وآثارها.','40','0','0');
INSERT INTO [t13443] VALUES (66,'حال المؤمن مع هذه الحوادث هو تعلقه بمسببها ومسبب الأسباب سبحانه.','42','0','0');
INSERT INTO [t13443] VALUES (67,'كلام نفيس للإمام ابن القيم كأنه يصف به أهل هذا الزمان مع هذه الحوادث.','42','0','0');
INSERT INTO [t13443] VALUES (68,'السبب الطبيعي للزلازل وتعلقه بالسبب الشرعي لحدوثها.','43','0','0');
INSERT INTO [t13443] VALUES (69,'الانشغال بمقياس (ريختر) حين حدوث الزلازل والغفلة عن الله علامة خذلان.','44','0','0');
INSERT INTO [t13443] VALUES (70,'أثر عظيم لعائشة - رضي الله عنها - في بيان السبب الشرعي لحدوث الزلازل.','44','0','0');
INSERT INTO [t13443] VALUES (71,'ذكر ما حصل لجبير بن مطعم حينما سمع قوله تعالى: (أم خلقوا من غير شيء).','45','0','0');
INSERT INTO [t13443] VALUES (72,'ذكر تفسير ابن كثير للآية، والتعليق على ذلك.','45','0','0');
INSERT INTO [t13443] VALUES (73,'ماذا سيقول الطبائعيون لو قيل لهم: (إن عمارة بنت نفسيها وسيارة كونت نفسها)!.','45','0','0');
INSERT INTO [t13443] VALUES (74,'بهذا الكنز العظيم تعرف بأن (الطبيعة) ليست مستقلة بذاتها.','46','0','0');
INSERT INTO [t13443] VALUES (75,'(حاشية): نص حديث (لا حول ولا قوة إلا بالله) كنز من كنوز الجنة.','46','0','0');
INSERT INTO [t13443] VALUES (76,'كلام نفيس لا بن القيم عن معنى (لا حول ولا قوة إلا بالله).','46','0','0');
INSERT INTO [t13443] VALUES (77,'كلام نفيس لشيخ الإسلام عن معنى لفظ (الحول) و (القوة).','47','0','0');
INSERT INTO [t13443] VALUES (78,'لماذا صارت (لا حول ولا قوة إلا بالله) كنزا من كنوز الجنة؟!.','47','0','0');
INSERT INTO [t13443] VALUES (79,'ذكر ما يعقده قول (لا حول ولا قوة إلا بالله) و (لا إله إلا الله) من معاقد التوحيد.','48','0','0');
INSERT INTO [t13443] VALUES (80,'ذكر الأشياء التي يحصل بها اليقين، وأنواع التفكر والاعتبار.','49','0','0');
INSERT INTO [t13443] VALUES (81,'يحصل اليقين بتدبر القرآن، وما يحدثه الله في الآفاق، والعمل بموجب ذلك.','49','0','0');
INSERT INTO [t13443] VALUES (82,'ما هي الآيات المشهودة؟.','50','0','0');
INSERT INTO [t13443] VALUES (83,'النوع الأول من أنواع العظة والاعتبار: العظة بالمسموع.','50','0','0');
INSERT INTO [t13443] VALUES (84,'النوع الثاني من أنواع العظة والاعتبار: العظة بالمشهود.','50','0','0');
INSERT INTO [t13443] VALUES (85,'ذكر أكثر من (٤٠) حادثة تاريخية كبرى في القديم والحديث وما فيها من العظة.','52','0','0');
INSERT INTO [t13443] VALUES (86,'أسباب ذكر هذه الحوادث.','52','0','0');
INSERT INTO [t13443] VALUES (87,'من أحداث سنة (٠٠٦٩ هـ): طاعون البصرة الجارف، وموت مئات الآلاف.','53','86','0');
INSERT INTO [t13443] VALUES (88,'من أحداث سنة (٠٢٢٦ هـ): مطر وبرد كالبيض، وصوت مجهول يجأر إلى الله.','53','86','0');
INSERT INTO [t13443] VALUES (89,'من أحداث سنة (٠٢٣٣ هـ): زلزلة مهولة ومدمرة بدمشق.','54','86','0');
INSERT INTO [t13443] VALUES (90,'من أحداث سنة (٠٢٤٠ هـ): صيحة عظيمة من السماء، وخسف بالمغرب.','54','86','0');
INSERT INTO [t13443] VALUES (91,'من أحداث سنة (٠٢٤٢ هـ): ١ - زلازل هائلة في جهة المشرق.','55','86','0');
INSERT INTO [t13443] VALUES (92,'من أحداث سنة (٠٢٤٢ هـ): ٢ - طائر بحلب يناشد الناس بتقوى الله.','55','86','0');
INSERT INTO [t13443] VALUES (93,'من أحداث سنة (٠٣٠٤ هـ): حيوان غريب يتسلط على الناس في بغداد.','56','86','0');
INSERT INTO [t13443] VALUES (94,'من أحداث سنة (٠٣٤٤ هـ): زلزلة قوية تهز مصر، وفزع الناس إلى الله.','56','86','0');
INSERT INTO [t13443] VALUES (95,'من أحداث سنة (٠٣٤٦ هـ): نقص البحر، وزلازل وخسوفات عظيمة في الشرق.','56','86','0');
INSERT INTO [t13443] VALUES (96,'من أحداث سنة (٠٤٢٥ هـ): ١ - زلازل عظيمة في مصر والشام.','57','86','0');
INSERT INTO [t13443] VALUES (97,'من أحداث سنة (٠٤٢٥ هـ): ٢ - إعصار مدمر (ريح سوداء) في العراق.','57','86','0');
INSERT INTO [t13443] VALUES (98,'من أحداث سنة (٠٤٣٤ هـ): زلزلة عظيمة في '' تبريز'' الفارسية.','58','86','0');
INSERT INTO [t13443] VALUES (99,'من أحداث سنة (٠٤٤١ هـ): سحابة مخيفة في العراق تنكشف بعد لجوء الناس لله.','58','86','0');
INSERT INTO [t13443] VALUES (100,'من أحداث سنة (٠٤٤٨ هـ): وباء وغلاء يعم الدنيا كلها.','59','86','0');
INSERT INTO [t13443] VALUES (101,'من أحداث سنة (٠٤٤٩ هـ): استمرار الوباء والغلاء، وقصص مرعبة.','59','86','0');
INSERT INTO [t13443] VALUES (102,'من أحداث سنة (٠٤٥٥ هـ): زلزلة عظيمة بواسط والشام، ووباء بمصر.','62','86','0');
INSERT INTO [t13443] VALUES (103,'من أحداث سنة (٠٤٦٠ هـ): زلزلة عظيمة بمصر وفلسطين.','62','86','0');
INSERT INTO [t13443] VALUES (104,'من أحداث سنة (٠٤٦٢ هـ): وباء شديد بمصر، وقصص مرعبة في ذلك.','63','86','0');
INSERT INTO [t13443] VALUES (105,'من أحداث سنة (٠٤٨٥ هـ): برد عظيم في البصرة، وريح قاصف أهلكت النخيل.','64','86','0');
INSERT INTO [t13443] VALUES (106,'من أحداث سنة (٠٥٢٤ هـ): زلزلة عظيمة بالعراق، وعقارب طيارة ببغداد.','64','86','0');
INSERT INTO [t13443] VALUES (107,'من أحداث سنة (٠٥٣١ هـ): كثرة موت الفجأة بأصبهان، وسحاب أسود بالشام.','65','86','0');
INSERT INTO [t13443] VALUES (108,'من أحداث سنة (٠٥٣٣ هـ): خسف عظيم بجنزة الفارسية، وزلازل بحلب الشامية.','65','86','0');
INSERT INTO [t13443] VALUES (109,'من أحداث سنة (٠٥٤٤ هـ): زلازل تهز الأرض، وخلق يموتون بمرض قاتل.','66','86','0');
INSERT INTO [t13443] VALUES (110,'من أحداث سنة (٠٥٤٩ هـ): ريح عظيمة تبيد قريتي المعلف وسحلة اليمنيتين.','66','86','0');
INSERT INTO [t13443] VALUES (111,'من أحداث سنة (٠٥٥٢ هـ): زلازل هائلة تضرب الشام، والناس يجأرون إلى الله.','67','86','0');
INSERT INTO [t13443] VALUES (112,'- قصيدة مؤثرة في الزلازل الهائلة التي ضربت الشام.','68','111','0');
INSERT INTO [t13443] VALUES (113,'من أحداث سنة (٠٥٦٩ هـ): سيل وبرد عظيم ببغداد، والناس يجأرون إلى الله.','70','86','0');
INSERT INTO [t13443] VALUES (114,'من أحداث سنة (٠٥٧٥ هـ): زلزلة عظيمة تهز الأرض، وتخلف دمارا هائلا.','70','86','0');
INSERT INTO [t13443] VALUES (115,'من أحداث سنة (٠٥٩٣ هـ): ١ - انقضاض كوكب عظيم، والناس يستغيثون بالله.','71','86','0');
INSERT INTO [t13443] VALUES (116,'من أحداث سنة (٠٥٩٣ هـ): ٢ - رياح عاتية بمصر، والناس يعتصمون بالمساجد.','71','86','0');
INSERT INTO [t13443] VALUES (117,'- تعليق على الفرق بين المسلمين في الماضي واليوم حين حدوث الآيات والكوارث.','72','116','0');
INSERT INTO [t13443] VALUES (118,'- كلام نفيس لابن القيم في قوله تعالى: (فما لهم عن التذكرة معرضين).','74','116','0');
INSERT INTO [t13443] VALUES (119,'من أحداث سنة (٠٥٩٦ هـ): توقف نهر النيل وظهور غلاء مفرط وعظيم بمصر.','74','86','0');
INSERT INTO [t13443] VALUES (120,'من أحداث سنة (٠٥٩٧ هـ): ١ - زلزلة عظمى تعم الدنيا.','75','86','0');
INSERT INTO [t13443] VALUES (121,'- خطبة بليغة حول الزلازل وأسبابها الشرعية.','76','120','0');
INSERT INTO [t13443] VALUES (122,'من أحداث سنة (٠٥٩٧ هـ): ٢ - تطاير بعض النجوم، والناس يضجون إلى الله.','77','86','0');
INSERT INTO [t13443] VALUES (123,'- تعليق على إرجاع الملاحدة والطبائعيين لتساقط بعض النجوم لأسباب طبيعية.','77','122','0');
INSERT INTO [t13443] VALUES (124,'- فائدة في بيان التوحيد العلمي والتوحيد العملي.','78','122','0');
INSERT INTO [t13443] VALUES (125,'من أحداث سنة (٠٦١٧ هـ): غزو التتار لبلدان الإسلام وإحداثهم القتل والدمار.','79','86','0');
INSERT INTO [t13443] VALUES (126,'- المؤرخ الشهير '' ابن الأثير '' يصف هول الفاجعة بغزو التتار لديار الإسلام.','79','125','0');
INSERT INTO [t13443] VALUES (127,'من أحداث سنة (٠٦٥٤ هـ): نار الحجاز التي ورد الحديث بذكرها.','82','86','0');
INSERT INTO [t13443] VALUES (128,'- نص الحديث الذي جاء فيه بأن نار الحجاز من أشراط الساعة '' الصغرى ''.','82','127','0');
INSERT INTO [t13443] VALUES (129,'- ذكر ما قاله النووي عن نار الحجاز، وهو من المعاصرين لها.','82','127','0');
INSERT INTO [t13443] VALUES (130,'- ذكر نص رسالة تشرح بالتفصيل حادثة اندلاع نار الحجاز والغرق العظيم ببغداد.','83','127','0');
INSERT INTO [t13443] VALUES (131,'- ذكر ما حصل من الناس تجاه هذه النار الهائلة التي اندلعت في المدينة النبوية.','84','127','0');
INSERT INTO [t13443] VALUES (132,'- (فائدة) في أن العلم بحدوث كارثة ما، لا ينفي الخوف منها حين وقوعها.','85','127','0');
INSERT INTO [t13443] VALUES (133,'- عودة إلى استكمال ذكر وصف الفاشاني لنار الحجاز.','85','127','0');
INSERT INTO [t13443] VALUES (134,'- قصيدة جليلة القدر في وصف حادثة نار الحجاز الهائلة.','86','127','0');
INSERT INTO [t13443] VALUES (135,'- ماذا عسى أهل الوقت أن يقولوا ويفعلوا لو رأوا عقوبات الله على الأمم السابقة؟!.','87','127','0');
INSERT INTO [t13443] VALUES (136,'من أحداث سنة (٠٦٥٦ هـ):غزو التتار لبغداد ووصف ما أحدثوه من القتل والدمار.','88','86','0');
INSERT INTO [t13443] VALUES (137,'- هل أمن الناس عقوبات الله وقد أجراها على من كان قبلهم ممن هم أقل ذنوبا؟!.','88','136','0');
INSERT INTO [t13443] VALUES (138,'- إذا أراد الله إنفاذ قضائه وقدره أذهب من ذوي العقول عقولهم!.','89','136','0');
INSERT INTO [t13443] VALUES (139,'من أحداث سنة (٠٧١٨ هـ): غلاء وفناء شديدين في العراق وبلدان الشرق.','92','86','0');
INSERT INTO [t13443] VALUES (140,'من أحداث سنة (٠٧٢٥ هـ): غرق مهول ببغداد، والناس يستغيثون بالله.','93','86','0');
INSERT INTO [t13443] VALUES (141,'من أحداث سنة (٠٧٤٩ هـ): ١ - وباء وبلاء عظيم في غزة وبلاد الإفرنج.','93','86','0');
INSERT INTO [t13443] VALUES (142,'- انتقال الوباء لدمشق، وأهلها يستغيثون بالله، وما حصل لهم بذلك.','94','141','0');
INSERT INTO [t13443] VALUES (143,'- خروج الناس من مختلف الديانات يوم جمعة وهم يستجيرون بالله.','95','141','0');
INSERT INTO [t13443] VALUES (144,'من أحداث سنة (٠٧٤٩ هـ): ٢ - إعصار مخيف وشديد يضرب دمشق وما حولها.','95','86','0');
INSERT INTO [t13443] VALUES (145,'من أحداث سنة (١١٨٧ هـ): طاعون عظيم في بغداد والبصرة.','96','86','0');
INSERT INTO [t13443] VALUES (146,'من أحداث سنة (١٢٠٤ هـ): سقوط برد عظيم في حريملاء، والناس يجأرون لله.','96','86','0');
INSERT INTO [t13443] VALUES (147,'من أحداث سنة (١٢٤٧ هـ): طاعون عظيم يعم العراق والكويت وما حولهما.','96','86','0');
INSERT INTO [t13443] VALUES (148,'- وقفة حول هذه الأحداث التي جرت في ديار الإسلام قديما وحديثا.','98','147','0');
INSERT INTO [t13443] VALUES (149,'- ما لم يتم ذكره هنا من الآيات والكوارث التي حلت بأهل الإسلام أكثر بكثير مما ذكر.','98','147','0');
INSERT INTO [t13443] VALUES (150,'من أحداث سنة (١٤٢٤ هـ): زلزال مهول يضرب مدينة تابعة لبم الإيرانية.','99','86','0');
INSERT INTO [t13443] VALUES (151,'من أحداث سنة (١٤٢٥ هـ): طوفان سموه '' تسونامي '' يجتاح جنوب شرق آسيا.','99','86','0');
INSERT INTO [t13443] VALUES (152,'- الحال التي كان عليها الناس في الشواطئ قبل أن يجتاحهم البحر بأمواج كالجبال.','99','151','0');
INSERT INTO [t13443] VALUES (153,'- وصف للطوفان البحري '' تسونامي ''، وذكر عظم ما خلفه من القتلى والدمار.','100','151','0');
INSERT INTO [t13443] VALUES (154,'- ذكر ما أسفرت عنه الشواطئ الذي دمرها الطوفان بعد أن رجع البحر لحالته الطبيعية.','100','151','0');
INSERT INTO [t13443] VALUES (155,'- ذكر ما قاله بعض الخبراء الأمريكيين عن شدة هذا الطوفان البحري العظيم.','101','151','0');
INSERT INTO [t13443] VALUES (156,'من أحداث سنة (١٤٢٦ هـ): ١ - أعاصير مدمرة تضرب جنوب شرق أمريكا.','102','86','0');
INSERT INTO [t13443] VALUES (157,'- ذكر ما قاله حاكم أحد الولايات التي ضربها الله بإعصار عظيم أسموه بـ '' كاترينا ''.','102','156','0');
INSERT INTO [t13443] VALUES (158,'- ذكر أن الولاية التي دمرها الله كانت تنظم احتفالا يجمع اللوطية والزناة من كل مكان.','102','156','0');
INSERT INTO [t13443] VALUES (159,'- ذكر انتحار قريب من ٥٠٠ شرطي بعد مشاهدة ما خلفه الإعصار من الدمار بمدينتهم.','103','156','0');
INSERT INTO [t13443] VALUES (160,'- ذكر حجم الخسائر في الأرواح والممتلكات والأموال التي خلفتها الأعاصير بإذن ربها.','104','156','0');
INSERT INTO [t13443] VALUES (161,'(٢٤) زلزالا مدمرا يضرب العالم خلال (١٥) عاما خلت فقط!.','105','0','0');
INSERT INTO [t13443] VALUES (162,'ذكر حديث تكاثر الزلازل في آخر الزمان.','105','0','0');
INSERT INTO [t13443] VALUES (163,'وقوع أعاصير أمريكا وزلازل باكستان قبل وبعد الكسوف يؤكد كلام ابن القيم وشيخه.','105','0','0');
INSERT INTO [t13443] VALUES (164,'ذكر أن (قريشا) كانت تخلص لله في الشدة بينما لأثر لهذا في هذا الزمان!.','106','0','0');
INSERT INTO [t13443] VALUES (165,'تسمية هذه العقوبات بأسماء مثل تسونامي وكاترينا وغيرها تشغل عن مسببها تعالى.','106','0','0');
INSERT INTO [t13443] VALUES (166,'(حاشية): بيان وجه الخطأ من تسمية اللواط والزنا بـ '' الجنس '' والتحذير من ذلك.','106','0','0');
INSERT INTO [t13443] VALUES (167,'الفرق بين عقوبات الله على الكفار وبين عقوباته على المسلمين.','108','0','0');
INSERT INTO [t13443] VALUES (168,'ذكر آيات قرآنية بأن ما أصاب الكفار إنما هو بسبب ذنوبهم.','108','0','0');
INSERT INTO [t13443] VALUES (169,'ليس ما يعاقب الله به المسلمين مماثلا لما يعاقب به الكفار، بل هو أهون.','108','0','0');
INSERT INTO [t13443] VALUES (170,'ما يعاقب الله به المسلمين فيه تكفير لسيئاتهم ورفعة لدرجاتهم.','109','0','0');
INSERT INTO [t13443] VALUES (171,'ما يصيب الله به المسلمين من الكوارث فيه صلاح نفوسهم من الرجوع لله والتوبة إليه.','109','0','0');
INSERT INTO [t13443] VALUES (172,'حديث صحيح يبين أن هذه الأمة أمة مرحومة وعذابها يكون في الدنيا بالزلازل والقتل ..','109','0','0');
INSERT INTO [t13443] VALUES (173,'حديث عظيم لعائشة يبين أن هذه الكوارث عذاب على الكافرين وموعظة للمؤمنين.','109','0','0');
INSERT INTO [t13443] VALUES (174,'ليس للكفار المرة ولنا الحلوة إن سلكنا طريقهم!.','110','0','0');
INSERT INTO [t13443] VALUES (175,'بيان الفرق بين خشية الإنسان لله تعالى وبين خشية الجبال له سبحانه.','111','0','0');
INSERT INTO [t13443] VALUES (176,'من لم تنفع به هذه الكوارث والآيات فأمامه الملين الأعظم (النار) - نعوذ بالله منها -.','111','0','0');
INSERT INTO [t13443] VALUES (177,'أسباب عدم حدوث العقوبات الكونية العامة على الناس اليوم لكثرة معاصيهم!.','112','0','0');
INSERT INTO [t13443] VALUES (178,'الإملاء والاستدراج أعظم أسباب تأخير العقوبات، وآيات قرآنية تبين ذلك.','112','0','0');
INSERT INTO [t13443] VALUES (179,'معنى قوله تعالى: (سنستدرجهم من حيث لا يعلمون).','113','0','0');
INSERT INTO [t13443] VALUES (180,'حديث عظيم يبين استدراج الله للعبد العاصي.','113','0','0');
INSERT INTO [t13443] VALUES (181,'كيف يمكن معرفة سر تأخر العقوبات مع كثرة المعاصي والإعراض عن الله؟!.','114','0','0');
INSERT INTO [t13443] VALUES (182,'أمران يختلف بهما الناس اليوم عن أسلافهم في التأثر بالآيات والعقوبات.','115','0','0');
INSERT INTO [t13443] VALUES (183,'سببان مهمان من أسباب تأخر العقوبات.','116','0','0');
INSERT INTO [t13443] VALUES (184,'معنى قوله تعالى: (كلا بل ران على قلوبهم ما كانوا يكسبون).','116','0','0');
INSERT INTO [t13443] VALUES (185,'السبب الأول: عقوبة الذنب الذنب بعده حتى يموت القلب ولا ملين له سوى النار.','116','0','0');
INSERT INTO [t13443] VALUES (186,'السبب الثاني: قرب يوم القيامة الذي يعاقب فيه المسيء إلا من رحم الله.','117','0','0');
INSERT INTO [t13443] VALUES (187,'رؤيا عظيمة لامرأة صالحة تبين سبب تأخر العقوبات مع كثرة المعاصي.','118','0','0');
INSERT INTO [t13443] VALUES (188,'بشر المحاربين لله ممن أفلت من عقاب الدنيا بأن الساعة أدهى وأمر!.','118','0','0');
INSERT INTO [t13443] VALUES (189,'معنى الأجل المسمى في قوله تعالى: (ولكن يؤخرهم إلى أجل مسمى).','119','0','0');
INSERT INTO [t13443] VALUES (190,'من أفلت من عقاب الدنيا فلن يفلت من الموت المحتوم الذي سيسوقه لربه تعالى.','119','0','0');
INSERT INTO [t13443] VALUES (191,'قصيدة مؤثرة حول الدنيا والموت والدار الآخرة.','119','0','0');
INSERT INTO [t13443] VALUES (192,'جواب من قال بأن الزلازل وغيرها لو كانت عذابا لما حدثت في مواضع خالية من الناس.','121','0','0');
INSERT INTO [t13443] VALUES (193,'أقدار الرب الحكيم سبحانه سابقة في كتابه مقارنة لعلمه.','122','0','0');
INSERT INTO [t13443] VALUES (194,'ما يحدث من الزلازل في الأماكن الخالية من البشر إذن من الله للأرض للتنفس.','122','0','0');
INSERT INTO [t13443] VALUES (195,'خاتمة الكتاب.','123','0','0');
INSERT INTO [t13443] VALUES (196,'رؤيا بليغة لرجل من أصبهان!.','123','0','0');
INSERT INTO [t13443] VALUES (197,'قصة لرجل من بني إسرائيل أخذه الله حينما تمادى في غيه وامتلأ صاعه بالذنوب!.','124','0','0');
INSERT INTO [t13443] VALUES (198,'إن الله ليملي للظالم حتى إذا أخذه لم يفلته.','126','0','0');
INSERT INTO [t13443] VALUES (199,'ذكر حال النبي - صلى الله عليه وسلم - حينما يرى ريحا أو غيما.','126','0','0');
INSERT INTO [t13443] VALUES (200,'من كان بالله اعرف كان منه أخوف.','127','0','0');
