INSERT INTO [t1360] VALUES (132,'حرمت الخمر بعينها قليلها وكثيرها , والسكر من كل شراب','132','0','0');
INSERT INTO [t1360] VALUES (133,'إذا أقيمت الصلاة فأتوها وعليكم السكينة , فصلوا ما أدركتم , واقضوا ما','133','0','0');
INSERT INTO [t1360] VALUES (134,'من نفس عن مؤمن كربة من كرب الدنيا نفس الله عنه كربة من كرب الآخرة , والله في عون العبد ما','134','0','0');
INSERT INTO [t1360] VALUES (135,'فصلى وأقامني عن يمينه وذكر الحديث كذا في الأصل','135','0','0');
INSERT INTO [t1360] VALUES (136,'عليكم بالباه فمن لم يستطع فعليه بالصوم فإنه له وجاء','136','0','0');
INSERT INTO [t1360] VALUES (137,'الذبيح إسحاق','137','0','0');
INSERT INTO [t1360] VALUES (138,'من بنى لله مسجدا ولو كمفحص قطاة بنى الله له بيتا في الجنة','138','0','0');
INSERT INTO [t1360] VALUES (139,'من بنى لله مسجدا ولو مفحص قطاة بنى الله له بيتا في الجنة','139','0','0');
INSERT INTO [t1360] VALUES (140,'تفضل الصلاة التي يستاك لها على الصلاة التي لا يستاك لها سبعين ضعفا , ويفضل الذكر الخفي','140','0','0');
INSERT INTO [t1360] VALUES (141,'إذا سألتم الله فسلوه ببطون أكفكم ولا تسلوه بظهورها','141','0','0');
INSERT INTO [t1360] VALUES (142,'من خير الناس بعد رسول الله صلى الله عليه وسلم؟ قال: أبو بكر ثم عمر','142','0','0');
INSERT INTO [t1360] VALUES (143,'لا يقض القاضي بين اثنين وهو غضبان','143','0','0');
INSERT INTO [t1360] VALUES (144,'ثلاثة من الإيمان: الإنفاق من الإقتار , وإنصاف الناس من نفسك , وبذل السلام للعالم أخبرنا','144','0','0');
INSERT INTO [t1360] VALUES (145,'فقد خرق ستر الله','145','0','0');
INSERT INTO [t1360] VALUES (146,'من أمن رجلا على نفسه فقتله فأنا بريء من القاتل وولي المقتول','146','0','0');
INSERT INTO [t1360] VALUES (147,'ما سابقت أبا بكر إلى خير قط , إلا سبقني إليه','147','0','0');
INSERT INTO [t1360] VALUES (148,'إن كففت عني وإلا قئتها','148','0','0');
INSERT INTO [t1360] VALUES (149,'المال يذهب حله وحرامه ... يوما ويبقى في غد آثامه ليس التقي بمتق لإلهه ... حتى يطيب شرابه','149','0','0');
