INSERT INTO [t1378] VALUES (36,'باب ذكر ما احتج به من رأي وجوب القراءة خلف الإمام فيما خافت الإمام فيه بالقراءة دون ما جهر بها فيه وهو أحد قولي أبي عبد الله محمد بن إدريس الشافعي المطلبي رحمه الله , وفيه إن صح وجه الاحتجاج بما ورد فيه تثبيت القراءة في الظهر والعصر وفي الركعة الآخرة','252','0','0');
INSERT INTO [t1378] VALUES (37,'ذكر الخبر الذي ورد في الأمر بالإنصات لقراءة الإمام وذلك فيما يحتج به من ذهب إلى قول الشافعي رحمه الله في القديم','300','0','0');
INSERT INTO [t1378] VALUES (38,'ذكر خبر آخر يحتج به من قال بقول الشافعي في القديم','317','0','0');
INSERT INTO [t1378] VALUES (39,'باب ذكر أخبار يحتج بها من زعم أن لا قراءة خلف الإمام بحال','332','0','0');
