INSERT INTO [t13922] VALUES (1,'كتاب الاعتكاف','4','0','0');
INSERT INTO [t13922] VALUES (2,'مسألة لا يصح الاعتكاف إلا في مسجد تقام فيه الجماعات','4','1','0');
INSERT INTO [t13922] VALUES (3,'مسألة لا يصح اعتكاف المرأة في بيتها','9','1','0');
INSERT INTO [t13922] VALUES (4,'مسألة يصح الاعتكاف بغير صوم في أصح الروايتين','13','1','0');
INSERT INTO [t13922] VALUES (5,'مسألة إذا نذر اعتكاف يومين لزم اعتكاف يومين وليلة؛ يدخل المسجد قبل طلوع الفجر، ويبقى فيه ذلك اليوم وليلته واليوم الثاني، ويخرج بعد غروب الشمس من اليوم الثاني','23','1','0');
INSERT INTO [t13922] VALUES (6,'مسألة وإذا نذر اعتكاف أيام يتخللها يوم الجمع، فاعتكف في مسجد من المساجد، وخرج لصلاة الجمع، لم يبطل اعتكافه','26','1','0');
INSERT INTO [t13922] VALUES (7,'مسألة إذا قال: (الله على أن أعتكف شهرا) لزمه أن يتابع، وإن فرق لم يجزه','29','1','0');
INSERT INTO [t13922] VALUES (8,'مسألة إذا وطئ المعتكف ناسيا بطل اعتكافه','35','1','0');
INSERT INTO [t13922] VALUES (9,'مسألة إذا وطئ عامدا في حال الاعتكاف، وجبت عليه كفارة الوطء في أصح الروايتين','37','1','0');
INSERT INTO [t13922] VALUES (10,'مسالة إذا قبل، أو لمس، فلم ينزل فقد أساء، وعندي: أنه لا يفسد اعتكافه','39','1','0');
INSERT INTO [t13922] VALUES (11,'مسألة فإن باشرها فيما دون الفرج فأنزل، فسد اعتكافه','41','1','0');
INSERT INTO [t13922] VALUES (12,'مسألة ما كان في فعله قربة، يجوز للمعتكف شرطه؛ مثل عيادة المرضى، وشهود الجنازة، وزيارة أهله، وقصد العلماء','42','1','0');
INSERT INTO [t13922] VALUES (13,'مسألة والمستحب للمعتكف التشاغل بذكر الله تعالى، والصلاة، وقراءة القرآن في خاصته. ولا يستحب له إقراء القرآن، وكتب الحديث، ومجالسة العلماء، والمناظرة لهم في العلم، والتدريس','44','1','0');
INSERT INTO [t13922] VALUES (14,'مسألة فإن نذر اعتكاف شهر، ومات، فإنه يقضى عنه','47','1','0');
INSERT INTO [t13922] VALUES (15,'كتاب الحج','50','0','0');
INSERT INTO [t13922] VALUES (16,'مسألة من شرط وجوب الحج وجود الزاد والراحلة','50','15','0');
INSERT INTO [t13922] VALUES (17,'المعضوب [إذا كان عنده مال] يحج به عن نفسه، لزمه ذلك','56','15','0');
INSERT INTO [t13922] VALUES (18,'مسألة [لو بذل غيره] الطاعة في الحج عنه، لم يلزمه فرض الحج، سواء كان المبذول له صحيحا، أو [زمنا] كان، أو معسرا','59','15','0');
INSERT INTO [t13922] VALUES (19,'مسألة إذا حج المعضوب أو الصحيح عن نفسه حجة تطوع أجزأه','66','15','0');
INSERT INTO [t13922] VALUES (20,'مسألة إذا كان مرضه يرجى زواله، لم يجز له أن يحج عن نفسه','68','15','0');
INSERT INTO [t13922] VALUES (21,'مسألة وإذا حج المعضوب عن نفسه، أو الشيخ الكبير، ثم برئ من غضبه، أو أحدث الله- تعالى- في الشيخ قوة، فإن تصور ذلك، فإنه يجزئه، ولا إعادة عليه','70','15','0');
INSERT INTO [t13922] VALUES (22,'مسألة من كان البحر بينه وبين طريق مكة، والغالب منه السلامة، فعليه الحج','72','15','0');
INSERT INTO [t13922] VALUES (23,'مسألة الأعمى إذا وجد زادا وراحلة وقائدا يقوده، لزمه الخروج بنفسه','73','15','0');
INSERT INTO [t13922] VALUES (24,'مسألة إذا مات وعليه حجة الإسلام، لزم الورثة أن يحجوا عنه من صلب المال من دورية أهله، سواء وصى بذلك، أو لم يوص','77','15','0');
INSERT INTO [t13922] VALUES (25,'مسألة الحج يقع عن المحجوج عنه','86','15','0');
INSERT INTO [t13922] VALUES (26,'مسألة لا يجوز الاستئجار على الحج، ولا على الطاعات، كالإمامة، والأذان، وتعليم القرآن','90','15','0');
INSERT INTO [t13922] VALUES (27,'مسألة فإن أخذ نفقة؛ ليحج بها عن غيره، فصد الآخذ بعذر، أو موت في بعض الطريق، لم يلزمه ضمان ما أنفق','95','15','0');
INSERT INTO [t13922] VALUES (28,'مسألة فإن أخذ مالا؛ ليحج به عن ميت، فقرن، لم يضمن النفقة، وكان دم القران في مال الحاج','97','15','0');
INSERT INTO [t13922] VALUES (29,'مسألة إذا أمره أن يحج عنه، وأمره آخر بمثل ذلك، فأهل بحجة عن أحدهما، لا ينوي واحدا منهما بعينه، وقع إحرامه عن نفسه، وليس له أن يصرفه إلى واحد منهما','98','15','0');
INSERT INTO [t13922] VALUES (30,'مسألة إذا حج عن غيره قبل أن يحج عن نفسه لم يجز عن الغير','100','15','0');
INSERT INTO [t13922] VALUES (31,'مسألة إذا كان عليه حجة الإسلام، فأحرم به ينوي تطوعا، وقع عن حجة الإسلام في أصح الروايتين','111','15','0');
INSERT INTO [t13922] VALUES (32,'مسألة الحج على الفور، ذكره أبو بكر في "كتاب الخلاف"','117','15','0');
INSERT INTO [t13922] VALUES (33,'مسألة أشهر الحج: شوال، وذو القعدة، وعشرة أيام من ذي الحجة','134','15','0');
INSERT INTO [t13922] VALUES (34,'مسألة ينعقد الإحرام بالحج فيغير أشهره','144','15','0');
INSERT INTO [t13922] VALUES (35,'مسألة الأفضل أن يحرم من الميقات','158','15','0');
INSERT INTO [t13922] VALUES (36,'مسألة يحرم في دبر صلاته','164','15','0');
INSERT INTO [t13922] VALUES (37,'مسألة التلبية غير واجبة, ويدخل في الإحرام بمجرد النية','171','15','0');
INSERT INTO [t13922] VALUES (38,'مسألة إظهار التلبية غير مسنون في الأمصار ومساجد الأمصار، وإنما هو مسنون في الصحاري والبراري','178','15','0');
INSERT INTO [t13922] VALUES (39,'مسألة لا تستحب الزيادة على تلبية النبي صلى الله عليه وسلم، فإن زاد جاز','180','15','0');
INSERT INTO [t13922] VALUES (40,'مسألة يقطع المعتمر التلبية إذا افتتح الطواف','185','15','0');
INSERT INTO [t13922] VALUES (41,'مسألة ويقطع الحاج التلبية حين يرمي جمرة العقبة','186','15','0');
INSERT INTO [t13922] VALUES (42,'مسألة لا يكره فعل العمرة يوم عرفة، ويوم النحر، وأيام التشريق على ظاهر كلام أحمد في رواية أبي الحارث: يعتمر الرجل متى شاء؛ في شعبان، أو في رمضان','189','15','0');
INSERT INTO [t13922] VALUES (43,'مسألة يجوز فعل العمرة في السنة دفعتين، وأكثر','194','15','0');
INSERT INTO [t13922] VALUES (44,'مسألة العمرة واجبة','197','15','0');
INSERT INTO [t13922] VALUES (45,'مسألة التمتع أفضل من الإفراد والقران، والإفراد أفضل من القران','210','15','0');
INSERT INTO [t13922] VALUES (46,'مسألة الأفضل للمتمتع أن يحرم بالحج يوم التروية','238','15','0');
INSERT INTO [t13922] VALUES (47,'مسألة يجوز فسخ الحج إلى العمرة','242','15','0');
INSERT INTO [t13922] VALUES (48,'مسألة المكي يصح له التمتع والقران، ولا يكره له ذلك، إلا أنه لا يلزمه دم','253','15','0');
INSERT INTO [t13922] VALUES (49,'مسألة إذا رجع المتمتع إلى الميقات بعد الفراغ من العمرة لم يسقط عنه دم المتعة، فإن رجع إلى موضع تقصر فيها الصلاة سقط عنه دم المتعة','259','15','0');
INSERT INTO [t13922] VALUES (50,'مسألة إذا أحرم بعمرة في رمضان، وطاف لها في شوال، وحج من عامه ذلك، لم يكن متمتعا حتى يحرم بالعمرة في أشهر الحج','264','15','0');
INSERT INTO [t13922] VALUES (51,'مسألة يجب دم التمتع والصوم عنه يوم النحر','267','15','0');
INSERT INTO [t13922] VALUES (52,'مسألة لا يجوز ذبح هدي المتعة قبل طلوع الفجر من يوم النحر','270','15','0');
INSERT INTO [t13922] VALUES (53,'مسألة إذا صام المتمتع ثلاثة أيام بعدما أحرم بالعمرة أجزأه','278','15','0');
INSERT INTO [t13922] VALUES (54,'مسألة إذا لم يصم المتمتع قبل يوم النحر، صامها قضاء، وعليه دم لتأخيرها عن أيام الحج، أو كان واجدا للهدي فأخره عن أيام النحر، فهل يجب عليه هديان؛ أحدهما لأجل التمتع والقران، والثاني لتأخيره ذلك عن وقته؟','285','15','0');
INSERT INTO [t13922] VALUES (55,'مسألة إذا صام المتمتع السبعة بعد الفراغ من الحج قبل الرجوع إلى أهله أجزأه','292','15','0');
INSERT INTO [t13922] VALUES (56,'مسألة يستحب للمتمتع أن يصوم قبل التروية بيوم، ويوم التروية، ويوم عرفة','297','15','0');
INSERT INTO [t13922] VALUES (57,'مسألة في المتمتع إذا دخل في الصوم، ثم وجد الهدي في صيامه، أجزأه المضي فيه','299','15','0');
INSERT INTO [t13922] VALUES (58,'مسألة المتمتع الذي يسوق الهدي لا يحل إلا يوم النحر، فإذا كان يوم النحر ذبح وحل، فإذا طاف وسعى لعمرته لم يحل منها، ولكن يحرم بالحج، ثم لا يحل حتى يتحلل منهما معا','307','15','0');
INSERT INTO [t13922] VALUES (59,'مسألة حاضرو المسجد الحرام هم أهل الحرم، ومن كان من الحرم على مسافة لا تقصر في مثلها الصلاة','312','15','0');
INSERT INTO [t13922] VALUES (60,'مسألة إذا جاوز الميقات غير محرم، ثم أحرم، ثم عاد إلى ميقات، لم يسقط عنه الدم؛ لبي أو لم يلب','317','15','0');
INSERT INTO [t13922] VALUES (61,'مسألة المكي إذا خرج إلى الحل، فأحرم منه بالحج، لم يلزمه الدم؛ سواء عاد إلى الحرم، أو لم يعد، ومضى على إحرامه إلى عرفة','323','15','0');
INSERT INTO [t13922] VALUES (62,'مسألة إذا جاوز الميقات غير محرم، ثم أحرم بحجة أو عمرة، ثم جامع فيها، فعليه قضاؤها، فإن قضاها لم يسقط عنه الدم لترك الميقات في أصح الروايتين','326','15','0');
INSERT INTO [t13922] VALUES (63,'مسألة يستحب لمن أراد الإحرام أن يتطيب لإحرامه','328','15','0');
INSERT INTO [t13922] VALUES (64,'مسألة إذا أحرم بنسك، ثم نسي ما أحرم به، فهو مخير؛ إن شاء صيره حجا، وإن شاء صيره عمرة','331','15','0');
INSERT INTO [t13922] VALUES (65,'مسألة لا يجوز للمحرمة لبس القفازين','334','15','0');
INSERT INTO [t13922] VALUES (66,'مسألة إذا لم يجد المحرم الإزار لبس السراويل، ولا فداء عليه','338','15','0');
INSERT INTO [t13922] VALUES (67,'مسألة إذا لم يجد المحرم النعلين لبس الخفين، ولا يقطعهما، ولا فدية عليه','344','15','0');
INSERT INTO [t13922] VALUES (68,'مسألة إذا لبس [الخفين] المقطوعين مع وجود النعلين افتدى','350','15','0');
INSERT INTO [t13922] VALUES (69,'مسألة إذا لبس المحرم القباء، فادخل كتفيه فيه لزمته الفدية','352','15','0');
INSERT INTO [t13922] VALUES (70,'مسألة للمحرم أن يغطي وجهه في أصح الروايتين','353','15','0');
INSERT INTO [t13922] VALUES (71,'مسألة لا يستظل المحرم على المحمل، فإن فعل افتدى في أصح الروايتين','359','15','0');
INSERT INTO [t13922] VALUES (72,'مسألة إذا لبس أو تطيب ناسيا فعليه الفدية في أصح الروايتين','365','15','0');
INSERT INTO [t13922] VALUES (73,'مسألة قليل اللبس وكثيره سواء في إيجاب الدم، وكذلك الحكم في الطيب','373','15','0');
INSERT INTO [t13922] VALUES (74,'مسألة إذا دهن المحرم بزيت، أو شيرج، فلا فدية عليه في أصح الروايتين، وسواء في ذلك استعماله في رأسه، أو بدنه','376','15','0');
INSERT INTO [t13922] VALUES (75,'مسألة إذا لبس المحرم ثوبا مصبوغا بعصفر جاز له ذلك، ولا فدية عليه','380','15','0');
INSERT INTO [t13922] VALUES (76,'مسألة إذا خضب المحرم لحيته بالحناء، أو يديه، أو رجليه، فلا فدية عليه، كالمعصفر','387','15','0');
INSERT INTO [t13922] VALUES (77,'مسألة فإن لبس ثوبا مبخرا بعود أو ند فعليه الفدية','389','15','0');
INSERT INTO [t13922] VALUES (78,'مسألة إذا شم المحرم شيئا من الرياحين لم يلزمه شيء في إحدى الروايتين','392','15','0');
INSERT INTO [t13922] VALUES (79,'مسألة إذا حلق المحرم ثلاث شعرات من رأسه- أو قصر- قبل أن يحل له ذلك، فعليه دم في أصح الروايتين','395','15','0');
INSERT INTO [t13922] VALUES (80,'مسألة فإن حلق شعرة ففيها مد من طعام، وفي شعرتين مدان في أصح الروايات','399','15','0');
INSERT INTO [t13922] VALUES (81,'مسألة إذا حلق المحرم شعر بدنه لزمته الفدية','401','15','0');
INSERT INTO [t13922] VALUES (82,'مسألة إذا حل له الحلق، فحلق جميع رأسه- أو قصر- أجزأه، وإن حلق- أو قصر- أقل من ذلك لم يجزئه على المنصوص في المذهب؛ فإن في رواية ابن إبراهيم وأبي داود','403','15','0');
INSERT INTO [t13922] VALUES (83,'مسألة على المحصر حلق أو تقصير في أصح الروايتين','406','15','0');
INSERT INTO [t13922] VALUES (84,'مسألة إذا حلق القارن والمتمتع قبل أن يذبح، أو يرمي، فليس عليه إلا دم القران في أصح الروايتين','409','15','0');
INSERT INTO [t13922] VALUES (85,'مسألة إذا أخر الحلاق عن أيام النحر لم يلزمه دم في أصح الروايتين','416','15','0');
INSERT INTO [t13922] VALUES (86,'مسألة إذا حلق المحرم رأس حلال، فلا شيء عليه','419','15','0');
INSERT INTO [t13922] VALUES (87,'مسألة إذا حلق الحلال رأس المحرم، وهو نائم، أو مكره، فالفدية على الحالق','426','15','0');
INSERT INTO [t13922] VALUES (88,'مسألة إذا حلق المحرم شعر المحرم بإذنه, فعلى المحلوق شعره فدية, ولا شيء على الحالق','428','15','0');
INSERT INTO [t13922] VALUES (89,'مسألة الحلق في الإحرام نسك يثاب على فعله ويعاقب على تركه','429','15','0');
INSERT INTO [t13922] VALUES (90,'مسألة إذا كان المحرم أصلع, أو محلوق الرأس, استحب إمرار الموسى على رأسه, ولم يجب عليه','435','15','0');
INSERT INTO [t13922] VALUES (91,'مسألة إذا حلق لغير عذر لزمه دم, ولم يخير بين ثلاثة أشياء؛ كاللبس والطيب','439','15','0');
INSERT INTO [t13922] VALUES (92,'مسألة إذا غسل المحرم رأسه بالسدر والخطمي لم تلزمه الفدية في إحدى الروايتين','441','15','0');
INSERT INTO [t13922] VALUES (93,'مسألة إذا قلم ثلاثة أظفار فصاعدا, ففيها دم, وإن قلم أقل من ذلك, فهو على الروايات التي تقدمت في حلق الشعر','444','15','0');
INSERT INTO [t13922] VALUES (94,'مسألة إذا حلق ثم حلق، أو قلم ثم قلم، أو لبس ثم لبس، أو تطيب ثم تطيب، أو وطئ ثم وطئ، ولم يكفر عن الأول، فكفارة واحدة في أصح الروايتين','450','15','0');
INSERT INTO [t13922] VALUES (95,'مسألة إذا وطئ وكفر، ثم وطئ ثانيا، فعليه كفارة ثانية','458','15','0');
INSERT INTO [t13922] VALUES (96,'مسألة إذا جمع في حلق الشعر بين الرأس والبدن دفعة واحدة؛ فحلق من شعر رأسه ثلاث شعرات فصاعدا، ومن بدنه ثلاث شعرات فصاعدا، فعليه فديتان','460','15','0');
INSERT INTO [t13922] VALUES (97,'مسألة فإن أصاب صيدا، وحلق، وتطيب، ولبس على وجه الرفض لإحرامه، فعليه لكل واحد من ذلك كفارة','461','15','0');
INSERT INTO [t13922] VALUES (98,'مسألة نكاح المحرم لنفسه ولغيره باطل','463','15','0');
INSERT INTO [t13922] VALUES (99,'مسألة لا تصح الرجعة في حال الإحرام في أصح الروايتين','480','15','0');
INSERT INTO [t13922] VALUES (100,'مسألة إذا أحرم بالحج من مكة؛ إما أن يكون متمتعا، أو من أهل مكة، ففي حقه طواف قدوم مسنون، لكنه لا يطوف طواف القدوم حتى يرجع] من [مني','488','15','0');
INSERT INTO [t13922] VALUES (101,'مسألة استلام الركن اليماني مسنون','491','15','0');
INSERT INTO [t13922] VALUES (102,'مسألة إذا استلم الركن اليماني لم يستحب تقبيل يده','495','15','0');
INSERT INTO [t13922] VALUES (103,'مسألة إذا لم يقدر على تقبيل الحجر، وضع يده عليه، ثم وضعها على فيه، وقبلها','496','15','0');
INSERT INTO [t13922] VALUES (104,'مسألة لا تكره القراءة في الطواف في أصح الروايتين','498','15','0');
INSERT INTO [t13922] VALUES (105,'مسألة إذا طاف محدثا، أو على بدنه نجاسة، أو مكشوف العورة، لم يجزئه، وعليه الإعادة في أصح الروايتين','501','15','0');
INSERT INTO [t13922] VALUES (106,'مسألة إذا انعكس الطواف - وهو أن يجعل البيت عن يمينه - لم يغنه','507','15','0');
INSERT INTO [t13922] VALUES (107,'مسألة إذا ترك بعض الطواف لم يعتد به، ولم يجبره بالدم','513','15','0');
INSERT INTO [t13922] VALUES (108,'مسألة إذا سلك في الطواف الحجر لم يجزئه إلا أن يستأنف الطواف، فيبنى على الموضع الذي دخل من الحجر','519','15','0');
INSERT INTO [t13922] VALUES (109,'مسألة إذا طاف راكبا لغير عذر لم يجزئه في إحدى الروايتين','521','15','0');
INSERT INTO [t13922] VALUES (110,'مسألة طواف الحامل غير جائز عنه، وطواف المحمول كطواف الراكب؛ إن كان لعذر أجزأه، وإن كان لغير عذر، فعلى الروايتين','527','15','0');
INSERT INTO [t13922] VALUES (111,'مسألة يجوز تقديم السعي على طواف الزيارة، وهو أن يفعله عقيب طواف القدوم','530','15','0');
INSERT INTO [t13922] VALUES (112,'مسألة ركعتا الطواف غير واجبتين','531','15','0');
INSERT INTO [t13922] VALUES (113,'مسألة لا بأس أن يجمع بين الأسابيع من غير أن يفصل بين كل أسبوعين بركعتين، ثم يصلي بعد ذلك','535','15','0');
INSERT INTO [t13922] VALUES (114,'مسألة إذا أخر طواف الزيارة إلى أيام التشريق لم يلزمه شيء','541','15','0');
INSERT INTO [t13922] VALUES (115,'مسألة أول وقت طواف الزيارة إذا مضى النصف الأول من ليلة النحر، وذلك مبنى على رمي جمرة العقبة؛ يجوز في النصف الثاني','544','15','0');
INSERT INTO [t13922] VALUES (116,'مسألة طواف الزيارة يفتقر إلى تعيين نية الفرض، فإن طاف بنية التطوع بعد دخول وقته، أو طاف للوداع، لم يقع عن طواف الزيارة','548','15','0');
INSERT INTO [t13922] VALUES (117,'مسألة السعي ركن في الحج، لا ينوب عنه دم في أصح الروايتين','550','15','0');
INSERT INTO [t13922] VALUES (118,'مسألة يكفي القارن طواف واحد وسعي واحد لهما في أصح الروايتين','559','15','0');
INSERT INTO [t13922] VALUES (119,'مسألة إذا وقف القارن بعرفة قبل الطواف والسعي لم يصر رافضا للعمرة','576','15','0');
INSERT INTO [t13922] VALUES (120,'مسألة يجوز للحاج الجمع بين صلاتي عرفة وحده','580','15','0');
INSERT INTO [t13922] VALUES (121,'مسألة للحلال أن يجمع بين صلاتي عرفة إذا كان مسافرا','582','15','0');
INSERT INTO [t13922] VALUES (122,'مسألة وقت الوقوف بعرفة من طلوع الفجر الثاني من يوم عرفة إلى طلوع الفجر الثاني من يوم النحر','583','15','0');
INSERT INTO [t13922] VALUES (123,'مسألة إذا دفع من عرفات قبل غروب الشمس، ولم يعد إليها، فعليه دم','591','15','0');
INSERT INTO [t13922] VALUES (124,'مسألة إذا صلى المغرب في طريق المزدلفة أجزأته صلاته، ولم يكن مسيئا','596','15','0');
INSERT INTO [t13922] VALUES (125,'مسألة يجوز الدفع من مزدلفة بعد نصف الليل من ليلة النحر','600','15','0');
INSERT INTO [t13922] VALUES (126,'مسألة فإن دفع من المزدلفة قبل نصف الليل، أو لم يبت بها جملة لغير عذر؛ فهل عليه دم، أم لا؟','605','15','0');
INSERT INTO [t13922] VALUES (127,'مسألة يجوز رمي جمرة العقبة بعد نصف الليل، وقبل طلوع الفجر','606','15','0');
INSERT INTO [t13922] VALUES (128,'مسألة لا يجوز رمي الجمار إلا بالحجر خاصة','610','15','0');
INSERT INTO [t13922] VALUES (129,'مسألة إذا رمى بحجر قد رمى به غيره لم يجزئه','613','15','0');
INSERT INTO [t13922] VALUES (130,'مسألة فإذا وقعت حصاة في ثوب إنسان، فنفضها عن ثوبه، فوقعت في المرمى، فحكى أبو بكر في "الخلاف" عن أحمد جواز ذلك في رواية بكر بن محمد خلافا للشافعي في قوله: لا يجزئه','616','15','0');
INSERT INTO [t13922] VALUES (131,'مسألة إذا رمى في آخر أيام التشريق قبل الزوال لم يجزئه','617','15','0');
INSERT INTO [t13922] VALUES (132,'مسألة إذا غابت الشمس يوم النفر الأول، وهو بمنى، فعليه أن يبيت الليلة بها، ويرمي من الغد','621','15','0');
INSERT INTO [t13922] VALUES (133,'مسألة إذا بدأ في اليوم الثاني بجمرة العقبة، ثم بالوسطى، ثم بالأولى، لم يجزئه إلا مرتبا، ويعيد الوسطى والأخيرة','623','15','0');
INSERT INTO [t13922] VALUES (134,'مسألة إذا أخر رمي يوم إلى الغد -أو إلى ما بعده- رماه، ولا دم عليه، وأيام الرمي كلها بمنزلة اليوم الواحد، فإن ترك الرمي في يوم منها، رماه فيما بعد إلى أن تنقضي أيام التشريق','626','15','0');
INSERT INTO [t13922] VALUES (135,'مسألة لا يختلف المذهب إذا ترك ثلاث حصيات: أنه يجب عليه الفدية، وإذا ترك أقل من ثلاث ففيه ثلاث روايات','630','15','0');
INSERT INTO [t13922] VALUES (136,'مسألة فإن ترك ثلاث حصيات ففيها دم، وفي جميع الجمار دم','637','15','0');
INSERT INTO [t13922] VALUES (137,'مسألة ليس في يوم النحر خطبة','639','15','0');
INSERT INTO [t13922] VALUES (138,'مسألة في النفر الأول خطبة مسنونة، وهو اليوم الثاني من أيام التشريق','643','15','0');
INSERT INTO [t13922] VALUES (139,'مسألة ليس في اليوم السابع خطبة','644','15','0');
INSERT INTO [t13922] VALUES (140,'مسألة إذا ترك المبيت بمنى الليالي الثلاث من غير عذر فقد أساء، وهل يجب عليه الدم، أم لا؟','646','15','0');
INSERT INTO [t13922] VALUES (141,'مسألة طواف الصدر واجب، وتركه لغير عذر يوجب دما','651','15','0');
INSERT INTO [t13922] VALUES (142,'مسألة إذا طاف للصدر، ثم أقام بعد ذلك لشراء حاجة، أو عيادة مريض ونحوه، لم يجزئه من طواف الصدر، وعليه أن يطوف حين يخرج','654','15','0');
INSERT INTO [t13922] VALUES (143,'مسألة والصبي له حج صحيح، فإن كان مميزا، فأحرم بإذن الولي، صح إحرامه، وإن لم يكن مميزا، فأحرم عنه الولي، صار محرما بإحرامه، ويجتنب ما يجتنب المحرم، فإن فعل شيئا من محظورات الإحرام لزمته الفدية','657','15','0');
INSERT INTO [t13922] VALUES (144,'مسألة إذا حج الولي بالصبي فقياس قول أحمد: أن النفقة في مال الصبي؛ لأنه قال: يضحي عنه الوصي من مال الصبي','664','15','0');
INSERT INTO [t13922] VALUES (145,'مسألة إذا أغمي على رجل من أهل الرفقة، فأحرم عنه رجل من أهل الرفقة، لم يصر محرما، ولم ينعقد الإحرام','665','15','0');
INSERT INTO [t13922] VALUES (146,'مسألة إذا أحرم العبد، ثم عتق قبل الوقوف، أجزأه عن حجة الإسلام','669','15','0');
INSERT INTO [t13922] VALUES (147,'مسألة إذا أحرم العبد بإذن سيده لم يكن له أن يحلله، وكذلك الأمة','673','15','0');
INSERT INTO [t13922] VALUES (148,'مسألة فإذا أحرم العبد بغير إذن سيده انعقد إحرامه، وكذلك الزوجة إذا أحرمت بحجة التطوع بغير إذن الزوج','675','15','0');
INSERT INTO [t13922] VALUES (149,'مسألة فإن أحرمت المرأة بحجة الإسلام لم يجز لزوجها أن يحللها رواية واحدة','681','15','0');
INSERT INTO [t13922] VALUES (150,'مسألة إذا اشترى الرجل جارية محرمة، وقد كان البائع أذن لها في الإحرام، لم يكن للمشتري أن يحللها','686','15','0');
INSERT INTO [t13922] VALUES (151,'مسألة في العبد يدخل مكة مع مولاه بغير إحرام، ثم يأذن له مولاه، أو يعتقه، فيحرم، والصبي يدخل مكة بغير إحرام، ثم يبلغ، أو يأذن له وليه، فيحرم، فلا دم عليهما','687','15','0');
INSERT INTO [t13922] VALUES (152,'مسألة في نصراني دخل مكة، فأسلم، ثم أحرم منها، فعليه دم لترك الميقات في أصح الروايتين','689','15','0');
INSERT INTO [t13922] VALUES (153,'مسألة من أراد دخول مكة لحاجة لا تتكرر، كالتجارة ونحوها، لزمه دخولها بإحرام، سواء كان منزله الميقات، أو وراء الميقات إلينا، أو إلى مكة','691','15','0');
INSERT INTO [t13922] VALUES (154,'مسألة إذا جاوز الميقات غير محرم لزمه إحرام على وجه القضاء، فإن أدى به حجة الإسلام في سنته سقط عنه، وإن أخره إلى السنة الثانية لم تجزأه حجة الإسلام عنه، ولزمه حجة، أو عمرة','700','15','0');
INSERT INTO [t13922] VALUES (155,'مسألة إذا أحرم بحجتين، أو عمرتين، لزمه إحرام واحد','704','15','0');
INSERT INTO [t13922] VALUES (156,'مسألة يضيف الحج إلى العمرة، ولا يضيف العمرة إلى الحج، فإن فعل لم تنعقد العمرة على ظاهر كلام أحمد في مواضع','709','15','0');
INSERT INTO [t13922] VALUES (157,'مسألة إذا أفسد الحج لم يخرج منه بالإفساد، بل يلزمه المضي في فاسده','713','15','0');
INSERT INTO [t13922] VALUES (158,'مسألة إذا أفسد الحج بالوطء، وعادا للقضاء؛ فإنهما يتفرقان','716','15','0');
INSERT INTO [t13922] VALUES (159,'مسألة إذا وطئ قبل الوقوف فسد حجه، وعليه بدنة','720','15','0');
INSERT INTO [t13922] VALUES (160,'مسألة إذا وطئ بعد الوقوف بعرفة، وقبل التحلل، فسد حجه','723','15','0');
INSERT INTO [t13922] VALUES (161,'مسألة إذا وطئ بعد الرمي، وقبل طواف الإفاضة، مضى في بقية الحج في الإحرام الذي أفسده، ويحرم بعد ذلك من التنعيم؛ ليقضي الطواف والسعي بإحرام صحيح','729','15','0');
INSERT INTO [t13922] VALUES (162,'مسألة يباح له بعد التحلل الأول كل شيء إلا النساء، فلا يجوز له الوطء في الفرج، ولا القبلة، ولا اللمس بشهوة، ولا عقد النكاح، ويباح له ما عدا ذلك من الطيب، وقتل الصيد، وغير ذلك','733','15','0');
INSERT INTO [t13922] VALUES (163,'مسألة جماع الناسي يفسد الإحرام','736','15','0');
INSERT INTO [t13922] VALUES (164,'مسألة إذا وطئ دون الفرج، فأنزل، أو قبل، أو لمس، فأنزل، فسد حجه في إحدى الروايتين','741','15','0');
INSERT INTO [t13922] VALUES (165,'مسألة فإن قبل، أو لمس، أو وطئ دون الفرج، فلم ينزل وجبت عليه الفدية','745','15','0');
INSERT INTO [t13922] VALUES (166,'مسألة فإذا كرر النظر، فأنزل، وجبت عليه بدنة، وإن أمذى فعليه شاة في أصح الروايات','747','15','0');
INSERT INTO [t13922] VALUES (167,'مسألة إذا كرر النظر، فأنزل، لم يفسد حجه، وعليه بدنة','748','15','0');
INSERT INTO [t13922] VALUES (168,'مسألة إذا وطئ امرأة في دبرها، أو عمل عمل قوم لوط، أو وطئ بهيمة، فسد حجه، وعليه البدنة','749','15','0');
INSERT INTO [t13922] VALUES (169,'مسألة إذا وطئ في العمرة أفسدها، وعليه القضاء وشاة','751','15','0');
INSERT INTO [t13922] VALUES (170,'مسألة إذا وطئ القارن، فأفسد حجه وعمرته، لم يسقط عنه دم في القران بالإفساد، وكذلك المتمتع','753','15','0');
INSERT INTO [t13922] VALUES (171,'مسألة لا يجوز تفريق الهدي -عن المتعة، والقران، وما كان معناه من الهدي الواجب بترك الإحرام من الميقات- على غير فقراء الحرم، وكذلك الإطعام في جزاء الصيد، ويجوز ذلك في فدية الأذى، وما كان في معناه من اللباس، والطيب، ودم الإحصار إذا وجد سبب ذلك في الحل','759','15','0');
INSERT INTO [t13922] VALUES (172,'مسألة إذا ذبح الهدي، ثم سرق، لم يلزمه غيره، وسقط عنه الواجب','772','15','0');
INSERT INTO [t13922] VALUES (173,'مسألة إذا فسد الحج لزمه القضاء من أبعد الموضعين؛ إما الميقات الشرعي، أو الموضع الذي أحرم منه. وكذلك إذا أفسد العمرة لزمه القضاء من أبعد الموضعين','774','15','0');
INSERT INTO [t13922] VALUES (174,'مسألة ومن فاته الحج بعذر من مرض، أو عدو، أو ضل الطريق، أو أخطأ العدد، أو أبطأ سيره بغير عذر مثل التواني والتشاغل بما لا يعنيه، انقطع إحرام الحج، وتحول إحرام عمرة','779','15','0');
INSERT INTO [t13922] VALUES (175,'مسألة ويلزمه مع العمرة القضاء والهدي في أصح الروايتين','788','15','0');
INSERT INTO [t13922] VALUES (176,'مسألة تجب الفدية بقتل الصيد خطأ','794','15','0');
INSERT INTO [t13922] VALUES (177,'مسألة العائد لقتل الصيد عليه الجزاء ثانيا','796','15','0');
INSERT INTO [t13922] VALUES (178,'مسألة إذا قتل صيدا له مثل من النعم ضمنه بمثله','800','15','0');
INSERT INTO [t13922] VALUES (179,'مسألة إذا قتل صيدا لم تحكم فيه الصحابة، جاز أن يكون الحاكمان القاتلين، أو أحدهما','812','15','0');
INSERT INTO [t13922] VALUES (180,'مسألة إذا قتل صغار الصيود التي لها مثل، ضمن الصغار بمثلها من النعم، وكذلك إن قتل صيدا أعور، أو مكسور اليدين، فداه بمثله','813','15','0');
INSERT INTO [t13922] VALUES (181,'مسألة إذا جرح صيدا ضمن ما نقص منه بالجرح','816','15','0');
INSERT INTO [t13922] VALUES (182,'مسألة بيض النعامة يضمن بقيمته','818','15','0');
INSERT INTO [t13922] VALUES (183,'مسألة في حمام الحل والحرم شاة','820','15','0');
INSERT INTO [t13922] VALUES (184,'مسألة ويضمن ما دون الحمام بقيمته، كالعصافير، والقطا، والسمان، ونحو ذلك','821','15','0');
INSERT INTO [t13922] VALUES (185,'مسألة وما حكم فيه صحابيان: أنه مثل للمقتول، استقر حكمهما فيه، فلا يعدل عن ذلك','823','15','0');
INSERT INTO [t13922] VALUES (186,'مسألة تعتبر في إخراج الطعام قيمة النظير، لا قيمة الصيد، فإذا قتل صيدا له مثل، وأراد التقويم، وأن يشتري بالقيمة طعاما؛ فإنه يقوم مثل الصيد','824','15','0');
INSERT INTO [t13922] VALUES (187,'مسألة إذا جرح صيدا، وغاب عنه، ولم يعرف خبره، فعليه جزاؤه','826','15','0');
INSERT INTO [t13922] VALUES (188,'مسألة كفارة الجزاء على التخيير؛ فإن قتل صيدا له مثل، فهو مخير؛ إن شاء أخرج المثل، وإن شاء قوم المثل دراهم، واشترى بها طعاما، وتصدق به، وإن شاء صام عن كل مد يوما. وفي الصيد الذي لا مثل له يخير بين الإطعام والصيام في أصح الروايتين','827','15','0');
INSERT INTO [t13922] VALUES (189,'مسألة الصوم عن كل مد بر يوما، وعن كل نصف صاع تمرا وشعيرا يوما','831','15','0');
INSERT INTO [t13922] VALUES (190,'مسألة إذا اصطاد الحلال صيدا للمحرم لم يجز للمحرم أكله؛ سواء اصطاده بعلمه، أو بغير علمه','832','15','0');
INSERT INTO [t13922] VALUES (191,'مسألة إذا قتل المحرم صيدا، ثم أكل منه، لم يلزمه للأكل شيء','836','15','0');
INSERT INTO [t13922] VALUES (192,'مسألة فإن أكل المحرم من لحم صيد صيد لأجله، وجب عليه الجزاء','842','15','0');
INSERT INTO [t13922] VALUES (193,'مسألة إذا اضطر المحرم إلى ميتة وصيد أكل الميتة، ولم يأكل الصيد','843','15','0');
INSERT INTO [t13922] VALUES (194,'مسألة إذا ذبح المحرم صيدا، فهو ميتة لا يحل أكله','845','15','0');
INSERT INTO [t13922] VALUES (195,'مسألة فإذا ذبح المحل صيدا في الحرم لم يحل أكله، كالمحرم إذا ذبح صيدا','851','15','0');
INSERT INTO [t13922] VALUES (196,'مسألة إذا دل المحرم حلالا أو محرما على صيد، فقتله، فعلى الدال الجزاء','854','15','0');
INSERT INTO [t13922] VALUES (197,'مسألة فإن دل محل حلالا على صيد في الحرم، فعلى الدال، والقاتل الجزاء','862','15','0');
INSERT INTO [t13922] VALUES (198,'مسألة صيد الحرم مضمون على الصبي والذمي','867','15','0');
INSERT INTO [t13922] VALUES (199,'مسألة إذا اشترك جماعة محرمون في قتل صيد فعليهم جزاء واحد','868','15','0');
INSERT INTO [t13922] VALUES (200,'مسألة القارن إذا قتل صيدا لزمه جزاء واحد، وكذلك إذا تطيب، أو لبس، أو وطئ','884','15','0');
INSERT INTO [t13922] VALUES (201,'مسألة إذا ابتدأ المحرم سبعا فقتله، فلا جزاء عليه في رواية حنبل، فقال: يقتل المحرم الكلب العقور، والذئب، والسبع، وكل ما عد من السباع، ولا كفارة عليه، ويقتل القرد والنسر والعقاب إذا وثب، ولا كفارة. فإن قتل شيئا من هذه من غير أن تعدو عليه، فلا كفار','892','15','0');
INSERT INTO [t13922] VALUES (202,'مسألة يجوز للمحرم أن يقرد بعيره','902','15','0');
INSERT INTO [t13922] VALUES (203,'مسألة إذا صال على المحرم الصيد قتله، ولا جزاء عليه على قياس قول أحمد في النحل: إذا لاح عليه قتله، ولا ضمان عليه','904','15','0');
INSERT INTO [t13922] VALUES (204,'مسألة إذا قتل المحرم صيدا مملوكا لزمه الجزاء','905','15','0');
INSERT INTO [t13922] VALUES (205,'مسألة إذا ملك صيدا، ثم أحرم لم يزل ملكه عنه، فإن كان في يده فعليه إرساله، وإن كان في بيته لم يجب عليه إرساله','907','15','0');
INSERT INTO [t13922] VALUES (206,'مسألة إذا اصطاد صيدا، وهو حلال، ثم أحرم، فأرسله من يده حلال أو محرم بغير أمره، لم يضمن','912','15','0');
INSERT INTO [t13922] VALUES (207,'مسألة في شجرة أصلها في الحرم، وأغصانها في الحل وقف طائر على هذه الأغصان، فقتله محل في الحل، فعليه الجزاء في إحدى الروايتين','915','15','0');
INSERT INTO [t13922] VALUES (208,'مسألة ضمان صيد الحرم مضمون على المحل والمحرم','918','15','0');
INSERT INTO [t13922] VALUES (209,'مسألة للصوم مدخل في ضمان صيد الحرم','920','15','0');
INSERT INTO [t13922] VALUES (210,'مسألة ما أدخله الحلال الحرم من الصيد؛ فإنه يرسله','923','15','0');
INSERT INTO [t13922] VALUES (211,'مسألة شجر الحرم مضمون على المحل والمحرم','928','15','0');
INSERT INTO [t13922] VALUES (212,'مسألة وما أنبته الآدميون من الشجر يجوز قطعه، ولا ضمان عليه، وما نبت بنفسه، فلا يجوز قطعه، وإن قطعه ضمنه؛ سواء كان من جنس ما ينبته الآدميون، أو لم يكن','930','15','0');
INSERT INTO [t13922] VALUES (213,'مسألة يضمن الشجرة الكبيرة بالبقرة، والصغيرة بالشاة','933','15','0');
INSERT INTO [t13922] VALUES (214,'مسألة لا يجوز أن يرعى حشيش الحرم','934','15','0');
INSERT INTO [t13922] VALUES (215,'مسألة صيد المدينة محرم اصطياده، وكذلك شجرها يحرم قطعه','937','15','0');
INSERT INTO [t13922] VALUES (216,'مسألة يضمن صيد المدينة وشجرها بالجزاء في أصح الروايتين','942','15','0');
INSERT INTO [t13922] VALUES (217,'مسألة وجزاء صيدها سلب القاتل، يكون للذي سلبه','945','15','0');
INSERT INTO [t13922] VALUES (218,'مسألة ولا يحرك صيد وج، ولا شجرها، وهي بالطائف','946','15','0');
INSERT INTO [t13922] VALUES (219,'مسألة مكة أفضل من المدينة في إحدى الروايتين','947','15','0');
INSERT INTO [t13922] VALUES (220,'مسألة تستحب المجاورة بمكة','952','15','0');
INSERT INTO [t13922] VALUES (221,'مسألة الهدي واجب على المحصر','954','15','0');
INSERT INTO [t13922] VALUES (222,'مسألة وينحصر المحصر هديه في موضع تحلله من حل، أو حرم، رواية واحدة. وهل يؤخر النحر والتحلل إلى يوم النحر، أم ينحر ويتحلل في وقت الحصر؟ على روايتين','958','15','0');
INSERT INTO [t13922] VALUES (223,'مسألة إذا أحصر في حج التطوع، فحل منه بالهدي، لم يلزمه القضاء في أصح الروايتين','970','15','0');
INSERT INTO [t13922] VALUES (224,'مسألة إذا أحصر في حجة الفرض، فحل منها بالهدي، لزمه قضاء الحج، ولم تلزمه عمرة معها','976','15','0');
INSERT INTO [t13922] VALUES (225,'مسألة هدي الإحصار يجزئ عنه الصيام','979','15','0');
INSERT INTO [t13922] VALUES (226,'مسألة ومقدار الصوم عشرة أيام','981','15','0');
INSERT INTO [t13922] VALUES (227,'مسألة ولا يجوز التحلل حتى يأتي بالبدل؛ الذي هو الصوم، كما لا يحل حتى يأتي بالمبدل؛ الذي هو الدم','982','15','0');
INSERT INTO [t13922] VALUES (228,'مسألة إذا وقف بعرفة، وصد عن البيت، فله التحلل على ظاهر قول أحمد في المحصر: له التحلل، ولم يفرق','983','15','0');
INSERT INTO [t13922] VALUES (229,'مسألة إذا مرض المحرم لم يجز له التحلل، ويقيم على إحرامه حتى يصل إلى البيت، فإذا فاته الحج يفعل ما يفعله الفائت من عمل العمرة والهدي والقضاء على الصحيح من الروايتين','988','15','0');
INSERT INTO [t13922] VALUES (230,'مسألة إذا شرط المحرم: أنه إذا مرض، أو أخطأ العدد: أن محلي حيث حبستني، جاز له التحلل عند وجود الشرط، وكذلك إن شرط: إن حصرني عدو، تحلل، ولا دم عليه. فيستفيد بالشرط عند المرض والخطأ التحلل وإسقاط الدم، وعند العدو إسقاط الدم','1000','15','0');
INSERT INTO [t13922] VALUES (231,'مسألة لا تحج المرأة إلا مع ذي محرم','1004','15','0');
INSERT INTO [t13922] VALUES (232,'مسألة إشعار البدن من الإبل والبقر وتقليدها مسنون','1021','15','0');
INSERT INTO [t13922] VALUES (233,'مسألة وصفة الإشعار: أن يشق صفحة سنامها الأيمن في أصح الروايات','1028','15','0');
INSERT INTO [t13922] VALUES (234,'مسألة التقليد مسنون في الغنم','1029','15','0');
INSERT INTO [t13922] VALUES (235,'مسألة ليس من شرط الهدي أن يوقف بعرفة، ولا الجمع بين الحل والحرم، فإذا اشتراه في الحرم، ونحره في الحرم، ولم يعرف به، أجزأه','1034','15','0');
INSERT INTO [t13922] VALUES (236,'مسألة أي موضع نحر من الحرم أجزأه','1036','15','0');
INSERT INTO [t13922] VALUES (237,'مسألة يجوز أن يشترك السبعة في البدنة والبقرة سواء كان هديهم تطوعا، أو واجبا، سواء اتفقت جهات قربهم، أو اختلفت، وكذلك إن كان بعضهم متطوعا، وبعضهم عن واجب، وكان بعضهم يريد اللحم، وبعضهم متقربا','1038','15','0');
INSERT INTO [t13922] VALUES (238,'مسألة لا يأكل من شيء من الهدايا إلا هدي التمتع والقران والتطوع إذا بلغ محله في أصح الروايتين','1045','15','0');
INSERT INTO [t13922] VALUES (239,'مسألة إذا أوجبت بدنة، جاز بيعها، وعليه بدنة مكانها، فإن لم يوجب مكانها حتى زادت في بدن، أو شعر، أو ولدت، كان عليه مثلها زائدة، ومثل ولدها، ولو أوجب مكانها قبل الزيادة والولد، لم يكن عليه شيء في الزيادة','1051','15','0');
INSERT INTO [t13922] VALUES (240,'مسألة إذا نذر هديا، لزمه شاة، فإن أخرج جزورا أو بقرة كان أفضل، ولا يجزئ فيه إلا ما يجزئ في الأضحية','1055','15','0');
INSERT INTO [t13922] VALUES (241,'مسألة إذا قال: لله علي أن أهدي بدنة، فإن نوى بها شيئا، فهو ما نوى، وإن لم تكن له نية، فهو مخير بين الجزور وبين البقرة في إحدى الروايتين','1056','15','0');
INSERT INTO [t13922] VALUES (242,'مسألة إذا أوجبت هديا، أو أضحية معينة، ثم أصابها عور، أجزأت عنه','1059','15','0');
INSERT INTO [t13922] VALUES (243,'مسألة فإن فقأ رجل عين الهدي تصدق بالأرش','1063','15','0');
INSERT INTO [t13922] VALUES (244,'مسألة إذا قال: (لله علي أن أتصدق بفدية) ولم ينو شيئا، ولم ينحرها إلا في الحرم','1064','15','0');
INSERT INTO [t13922] VALUES (245,'مسألة فإن غصب شاة، فذبحها لمتعته، أو قرانه، ثم أجازه مالكها، أو ضمنها، لم يجز','1065','15','0');
INSERT INTO [t13922] VALUES (246,'مسألة إذا حج، ثم ارتد، ثم أسلم، فعليه حجة الإسلام، ولا يعتد بما كان فعله','1066','15','0');
INSERT INTO [t13922] VALUES (247,'كتاب البيوع','1070','0','0');
INSERT INTO [t13922] VALUES (248,'مسألة بيع ما لم يره المشتري ولا البائع من غير صفة غير جائز في أصح الروايتين','1070','247','0');
INSERT INTO [t13922] VALUES (249,'مسألة بيع الأعيان الغائبة بالصفة جائز، وكذلك الحاضرة التي تشق رؤيتها، كالأعدال تباع على [البرنامج] وشبهه','1089','247','0');
INSERT INTO [t13922] VALUES (250,'مسألة شراء الأعمى جائز','1096','247','0');
INSERT INTO [t13922] VALUES (251,'مسألة خيار المجلس ثابت في عقد البيع إلى أن يفترقا','1097','247','0');
INSERT INTO [t13922] VALUES (252,'مسألة لا ينقطع خيار المجلس بالتخاير، وسواء تخايرا في نفس العقد؛ فتعاقدا على أن لا خيار بينهما، أو تخايرا بعد العقد في المجلس في أصح الروايتين','1119','247','0');
INSERT INTO [t13922] VALUES (253,'مسألة ينتقل الملك في بيع الخيار بنفس العقد سواء في ذلك خيار المجلس، أو خيار الشرط','1124','247','0');
INSERT INTO [t13922] VALUES (254,'مسألة فإن أعتق البائع العبد في مدة الخيار، وتمم المشتري العقد، أو فسخه، لم ينفذ عتقه','1133','247','0');
INSERT INTO [t13922] VALUES (255,'مسألة فإن أعتق المشتري في مدة الخيار نفذ عتقه','1138','247','0');
INSERT INTO [t13922] VALUES (256,'مسألة إذا وطئ البائع الأمة قبل القبض رجع المشتري عليه بالمهر','1142','247','0');
INSERT INTO [t13922] VALUES (257,'مسألة فإن باع البائع، أو وهب وأقبض، أو أوقف في مدة الخيار، لم ينفذ شيء من ذلك، ولم يكن فسخا للبيع','1143','247','0');
INSERT INTO [t13922] VALUES (258,'مسألة إذا وطئ البائع في مدة الخيار قبل الفسخ مع علمه بالتحريم؛ فهل يجب عليه الحد؟','1146','247','0');
INSERT INTO [t13922] VALUES (259,'مسألة إذا كان المبيع عبدا، فمات في مدة الخيار، لم يبطل الخيار في أصح الروايتين','1148','247','0');
INSERT INTO [t13922] VALUES (260,'مسألة إذا ابتاع ثوبا من أحد هذين الثوبين، أو من أحد ثلاثة، أو من أحد أربعة، فالعقد فاسد','1152','247','0');
INSERT INTO [t13922] VALUES (261,'مسألة إذا اشترى شيئا على أنه بالخيار إلى الليل، أو إلى الظهر، أو إلى الغد، فله الخيار إلى أن يطلع الفجر، وإلى أن تزول الشمس، وإلى أن تغرب الشمس في أصح الروايتين','1160','247','0');
INSERT INTO [t13922] VALUES (262,'مسألة يجوز شرط الخيار أكثر من ثلاث','1166','247','0');
INSERT INTO [t13922] VALUES (263,'مسألة إذا اشترى سلعة، وشرط: أنه إن لم ينقد الثمن إلى ثلاثة أيام، فلا بيع بينهما، فالبيع جائز','1175','247','0');
INSERT INTO [t13922] VALUES (264,'مسألة إذا اشترى جارية على أنه بالخيار ثلاثة أيام، فلمسته الجارية في مدة الثلاث لشهوة، وأقر المشتري أنها فعلت ذلك، لم يبطل خياره','1177','247','0');
INSERT INTO [t13922] VALUES (265,'مسألة إذا شرط الخيار، وسكتا عن ضرب مدته، بطل البيع في أصح الروايتين','1180','247','0');
INSERT INTO [t13922] VALUES (266,'مسألة إذا شرطا أجلا مجهولا، أو خيارا مجهولا، فالعقد باطل، وإن اتفقا على إسقاطه قبل مضي جزء من المدة المجهولة','1181','247','0');
INSERT INTO [t13922] VALUES (267,'مسألة إذا شرطا الخيار لأحدهما، فاختار فسخ العقد بغير محضر من الآخر جاز','1190','247','0');
INSERT INTO [t13922] VALUES (268,'مسألة إذا ابتاع شيئا وشرط الخيار لغيره صح؛ سواء شرط الخيار لنفسه، وجعله وكيلا له في الإمضاء والرد، أو شرطه للوكيل دونه، إلا أنه إن شرطه لنفسه، وجعله وكيلا، كان له دون الوكيل، وإن شرطه للوكيل، كان الخيار لهما','1196','247','0');
INSERT INTO [t13922] VALUES (269,'مسألة إذا اشترى رجلان من رجل عبدا على أنهما بالخيار ثلاثة أيام، فرضي أحدهما، كان للآخر أن يرد نصيبه خاصة على قياس قوله: إذا اشترى رجلان عبدا، فوجدا به عيبا، فرضي أحدهما، كان للآخر أن يرد نصيبه خاصة','1198','247','0');
INSERT INTO [t13922] VALUES (270,'مسألة خيار الشرط لا يورث','1201','247','0');
INSERT INTO [t13922] VALUES (271,'مسألة إذا مضت مدة الخيار، ولم يكن من مشترطه رد ولا إجازة حكم عليه بنفس مضي المدة','1216','247','0');
INSERT INTO [t13922] VALUES (272,'مسألة إذا تقدم القبول على الإيجاب لم ينعقد البيع سواء كان القبول بلفظ الماضي مثل أن يقول: ابتعت منك بكذا، أو كان بلفظ الطلب، فقال: بعني بكذا، وكذلك النكاح في إحدى الروايتين','1218','247','0');
INSERT INTO [t13922] VALUES (273,'مسألة إذا تبايعا بما لا يتغابن الناس بمثله في العادة، وكان أحدهما ممن لا يخبر سعر ذلك المبيع، فله الخيار','1223','247','0');
INSERT INTO [t13922] VALUES (274,'مسألة الربا ثابت في غير الأعيان المنصوص عليها على ما نبينه: وهو قول الجماعة.','1233','247','0');
INSERT INTO [t13922] VALUES (275,'مسألة العلة في فساد العقد عند وجود التفاضل زيادة كيل في جنس المكيلات في أصح الروايات: رواها الجماعة عنه','1236','247','0');
INSERT INTO [t13922] VALUES (276,'مسألة لا يجوز بيع تمرة بتمرتين، وحبة بحبتين','1257','247','0');
INSERT INTO [t13922] VALUES (277,'مسألة [علة] تحريم البيع متفاضلا في الذهب والفضة زيادة وزن في جنس','1259','247','0');
INSERT INTO [t13922] VALUES (278,'مسألة لا يجوز بيع فلس بفلسين سواء كانت نافقة, أو كاسدة, وسواء كانت بأعيانها','1266','247','0');
INSERT INTO [t13922] VALUES (279,'مسألة يجري الربا في معمول الصفر والنحاس والرصاص, ونحو ذلك','1268','247','0');
INSERT INTO [t13922] VALUES (280,'مسألة التفاضل جائز في الماء','1270','247','0');
INSERT INTO [t13922] VALUES (281,'مسألة إذا باع جنسا يجري فيه الربا بعضه ببعض, وتفرقا قبل القبض, بطل البيع, وذلك مثل المكيل بعضه ببعض, والموزون بعضه ببعض','1271','247','0');
INSERT INTO [t13922] VALUES (282,'مسألة الحنطة والشعير جنسان, يجوز بيع أحدهما بالآخر متفاضلا ومتماثلا','1276','247','0');
INSERT INTO [t13922] VALUES (283,'مسألة ما لا ربا فيه يجوز بيع بعضه ببعض نساء, وهو ما عدا المكيل والموزون في أصح الروايات','1279','247','0');
INSERT INTO [t13922] VALUES (284,'مسألة إذا دخل المسلم دار الحرب بأمان, فباع من أهل الحرب درهما بدرهمين, أو درهمين بدرهم, لم يجز','1289','247','0');
INSERT INTO [t13922] VALUES (285,'مسألة المكيلات المنصوص عليها مكيلة أبدا, لا يجوز بيع بعضها ببعض إلا كيلا, والموزونات المنصوص عليها موزونة أبدا, وما لم ينص على تحريم التفاضل فيه كيلا ولا وزنا, فالمرجع فيه إلى عرف العادة بالحجاز في عهد رسول الله - صلى الله عليه وسلم -؛ فما كانت','1294','247','0');
INSERT INTO [t13922] VALUES (286,'مسألة لا يجوز بيع الحنطة بالدقيق في أصح الروايتين','1296','247','0');
INSERT INTO [t13922] VALUES (287,'مسألة يجوز بيع الدقيق بالدقيق إذا كانا على صفة واحدة من النعومة','1301','247','0');
INSERT INTO [t13922] VALUES (288,'مسألة يجوز بيع الخبز وزنا','1304','247','0');
INSERT INTO [t13922] VALUES (289,'مسألة لا يجوز بيع الحنطة المبلولة باليابسة','1306','247','0');
INSERT INTO [t13922] VALUES (290,'مسألة خل العنب وخل التمر جنسان, يجوز التفاضل فيهما','1307','247','0');
INSERT INTO [t13922] VALUES (291,'مسألة لا يجوز بيع شيء من المكيلات والموزونات على التحري','1310','247','0');
INSERT INTO [t13922] VALUES (292,'مسألة اللحوم أجناس باختلاف أصولها, وكذلك الألبان في إحدى الروايات','1313','247','0');
INSERT INTO [t13922] VALUES (293,'مسألة لا يجوز بيع الرطب بالتمر','1318','247','0');
INSERT INTO [t13922] VALUES (294,'مسألة يجوز بيع الرطب بالرطب','1327','247','0');
INSERT INTO [t13922] VALUES (295,'مسألة لا يجوز بيع جنس فيه الربا بعضه ببعض, ومع أحدهما, أو معهما, من غير جنسهما مثل مد عجوة ودرهم بمدي عجوة, أو درهمين وثوب بدرهمين, أو مد حنطة ومد شعير بمدي حنطة ومد شعير, أو مد حنطة جيدة ومد حنطة رديئة بمدين جيدين, أو رديئين, أو وسطين, أو دينار جيد','1331','247','0');
INSERT INTO [t13922] VALUES (296,'مسألة إذا ابتاع عبدا- ومعه مال- بثمن من جنس المال الذي معه, وكان المال بقدر الثمن, أو أكثر منه, ولم يقصد المال, وإنما قصد العبد؛ لنفاسة في حسنه وجماله, إن كانت أمة يقصد الاستمتاع بها, أو كان عبدا يقصد قوته وصناعته وعقله, جاز البيع','1343','247','0');
INSERT INTO [t13922] VALUES (297,'مسألة لا يجوز بيع اللحم المأكول بالحيوان المأكول','1347','247','0');
INSERT INTO [t13922] VALUES (298,'مسألة هل يجوز بيع شاة في ضرعها لبن بلبن؟ أو شاة عليها صوف بصوف؟ يتخرج على روايتين نص عليهما في بيع النوى بالتمر الذي فيه النوى','1355','247','0');
INSERT INTO [t13922] VALUES (299,'مسألة إذا تبايعا دراهم بدنانير في الذمة بالصفة, وتقابضا, ثم وجد أحدهما بعد التفرق عيبا من جنسه, كان له البدل','1359','247','0');
INSERT INTO [t13922] VALUES (300,'مسألة الدراهم والدنانير تتعينان بالعقد إذا عينت','1361','247','0');
INSERT INTO [t13922] VALUES (301,'مسألة من باع نخلا غير مؤبر، فثمرته للمشتري','1373','247','0');
INSERT INTO [t13922] VALUES (302,'مسألة إذا باع نخلا مؤبرا، فثمرته للبائع، وله تركها حتى تدرك','1381','247','0');
INSERT INTO [t13922] VALUES (303,'مسألة إذا باع الثمرة قبل بدو صلاحها لم يجز، إلا أن يشترط قطعها','1384','247','0');
INSERT INTO [t13922] VALUES (304,'مسألة إذا باع الثمرة بعد بدو صلاحها بشرط التبقية صح','1392','247','0');
INSERT INTO [t13922] VALUES (305,'مسألة إذا اشترى الثمرة قبل بدو صلاحها بشرط القطع، فلم يقطعها حتى بدا صلاحها، وأتى عليها أوان جذاذها، بطل البيع، وتكون الثمرة بزيادتها للبائع، ويرد الثمن على المشتري في أصح الروايتين','1394','247','0');
INSERT INTO [t13922] VALUES (306,'مسألة إذا بدا الصلاح في نوع من الثمار كان صلاحا لبقية ذلك النوع في قراح واحد في إحدى الروايتين','1403','247','0');
INSERT INTO [t13922] VALUES (307,'مسألة يجوز بيع ما ظهر من المقاثي والمباطح دون ما بطن','1408','247','0');
INSERT INTO [t13922] VALUES (308,'مسألة يجوز بيع الباقلا في قشره الأعلى، وكذلك بيع الحنطة في سنبلها','1411','247','0');
INSERT INTO [t13922] VALUES (309,'مسألة لا يجوز أن يبيع ثمرة بستان ويستثني منه أمدادا معلومة، ولا أن يبيع صبرة ويستثني منها أقفزة','1415','247','0');
INSERT INTO [t13922] VALUES (310,'مسألة فإن باع شاة واستثنى الرأس والسواقط من الجلد والأطراف جاز','1419','247','0');
INSERT INTO [t13922] VALUES (311,'مسألة توضع الجائحة عن المشتري في ما قل وكثر في أصح الروايتين','1422','247','0');
INSERT INTO [t13922] VALUES (312,'مسألة بيع العرايا جائز- وهو: بيع ثمرة على النخل خرصا بمثله من التمر الموضوع على الأرض نقدا من الواهب لها ومن غيره- إذا كان دون خمسة أو سق لمن به حاجة إلى أكل الرطب، ولا ثمن معه، فإن لم يكن به حاجة لم يجز','1436','247','0');
INSERT INTO [t13922] VALUES (313,'مسألة إذا قال: بعتك هذه الصبرة؛ كل قفيز بدرهم، فالبيع صحيح في جميعها على ما سمى','1453','247','0');
INSERT INTO [t13922] VALUES (314,'مسألة إذا قال: (بعتك ذراعا من هذه الدار)، وهما يعلمان مبلغ ذرعان الدار، صح البيع','1457','247','0');
INSERT INTO [t13922] VALUES (315,'مسألة إذا كان البائع يعلم كيل الصبرة، فباعها جزافا، لم يجز، إلا بعد أن يعلم المبتاع بقدر كيلها، فإن لم يبين له كان للمبتاع الرد','1460','247','0');
INSERT INTO [t13922] VALUES (316,'مسألة لا يجوز بيع المكيلات بعضها ببعض جزافا، وكذلك الموزونات','1462','247','0');
INSERT INTO [t13922] VALUES (317,'مسألة إذا كان المبيع ثوبا, أو عبدا, أو دارا, أو صبرة, ونحوه مما يتعين ملك المشتري فيه, وتلف قبل قبضه, فهو من مال المشتري','1465','247','0');
INSERT INTO [t13922] VALUES (318,'مسألة إذا اشترى قفيزا من صبرة, أو عشرة أمناء حديد من زبرة, أو مئتي بيضة من ألف بيضة, ونحو ذلك مما لا يتعين, فتلف قبل قبضه','1474','247','0');
INSERT INTO [t13922] VALUES (319,'مسألة إذا كان المبيع ثوبا, أو عبدا, أو دابة, أو دارا, ونحو ذلك مما هو متعين, جاز التصرف فيه قبل قبضه','1479','247','0');
INSERT INTO [t13922] VALUES (320,'مسألة إذا أصدقها عبدا, أو ثوبا, أو دارا, ونحو ذلك مما يتعين ملكها فيه, جاز لها التصرف فيه قبل قبضه. وإن لم يكن متعينا مثل قفيز من صبرة ونحو ذلك, لم يجز لها التصرف فيه قبل قبضه. وكذلك الجعل في الخلع','1492','247','0');
INSERT INTO [t13922] VALUES (321,'مسألة التخلية قبض فيما ينقل ويحول في إحدى الروايتين','1495','247','0');
INSERT INTO [t13922] VALUES (322,'مسألة إذا باع طعاما بثمن إلى أجل, فلما حل الأجل باع المشتري من البائع ذلك الطعام بالثمن الذي له عليه, لم يصح البيع','1500','247','0');
INSERT INTO [t13922] VALUES (323,'مسألة التصرية تثبت الخيار للمبتاع في الرد, ورد صاعا من تمر؛ لما احتلبه من اللبن','1502','247','0');
INSERT INTO [t13922] VALUES (324,'مسألة إذا ابتاع جارية فولدت, أو نخلا فأثمرت, ثم ظهر على عيب, كان له الرد بالعيب, وإمساك النماء','1518','247','0');
INSERT INTO [t13922] VALUES (325,'مسألة إذا اشترى أمة حاملا, فولدت عنده, ثم وجد بها عيبا, فأراد رد الأم, وإمساك الولد, لم يكن له ذلك, وكان بالخيار بين رد الأم مع الولد, والإمساك وأخذ الأرش','1530','247','0');
INSERT INTO [t13922] VALUES (326,'مسألة إذا اشترى جارية حاملا, فولدت قبل القبض, أو بهيمة فنتجت, وولدت في يد البائع قبل القبض, ثم قبضها المشتري, لم يدخل الولد في البيع, ولا يكون له حصة من الثمن, وهو للمشتري, فلو وجد بالأم عيبا ردها بجميع الثمن','1533','247','0');
INSERT INTO [t13922] VALUES (327,'مسألة إذا وجد بالمبيع عيبا بعد القبض انفسخ العقد بقول المشتري, ولم يعتبر في ذلك تراضيهما, ولا حكم الحاكم','1536','247','0');
INSERT INTO [t13922] VALUES (328,'مسألة إذا علم بالعيب فهو على خياره ما لم يرض به، أو يفعل ما يستدل به على الرضا','1539','247','0');
INSERT INTO [t13922] VALUES (329,'مسألة إذا وطئ الأمة المبتاعة، ثم وجد بها عيبا، فله أن يردها؛ بكرا كانت، أو ثيبا في أصح الروايتين','1541','247','0');
INSERT INTO [t13922] VALUES (330,'مسألة إذا تصرف المشتري في المبيع أن كان ثوبا فقطعه، أو حدث به عنده عيب، أو جنى عليه جناية، ثم ظهر على عيب كان عند البائع، فهو بالخيار','1553','247','0');
INSERT INTO [t13922] VALUES (331,'مسألة إذا اشترى شيئا مأكولا في جوفه، كالجوز واللوز والبيض والرمان والبطيخ، فكسره، فوجده فاسدا، فنقل ابن منصور: له الرد، وأخذ الثمن','1561','247','0');
INSERT INTO [t13922] VALUES (332,'مسألة إذا اطلع المشتري على العيب، فطالب بأخذ الأرش مع إمكان الرد، جاز','1562','247','0');
INSERT INTO [t13922] VALUES (333,'مسألة إذا اشترى عبدين، أو ثوبين، فوجد بأحدهما عيبا، كان له رده دون الصحيح في إحدى الروايتين','1569','247','0');
INSERT INTO [t13922] VALUES (334,'مسألة إذا اشترى عبدا فقتله، أو طعاما فأكله، ثم ظهر على عيب رجع بأرشه','1576','247','0');
