INSERT INTO [t1415] VALUES (1,'في البدء كانت هذه الكلمة!','2','0','0');
INSERT INTO [t1415] VALUES (2,'الفصل الأول (الشبهات اللغوية)','8','0','0');
INSERT INTO [t1415] VALUES (3,'الشبهات اللغوية','9','0','0');
INSERT INTO [t1415] VALUES (4,'٢- ويقول الكاتب النزق (ص ١٠٧) إن في نصب "الظالمين" في قوله تعالى في الآية الرابعة والعشرين بعد المائة من سورة "البقرة": "قال (أي الله لإبراهيم)','18','3','0');
INSERT INTO [t1415] VALUES (5,'كذلك يخطئ الدعي قوله تعالى في الآية ٥٦ من سورة "الأعراف": {إن رحمت الله قريب من المحسنين}','21','3','0');
INSERT INTO [t1415] VALUES (6,'٤- ومن جرأة هذا العيى تخطئته قوله عز شأنه عن بني إسرائيل في الآية ١٦٠ من سورة "الأعراف": {وقطعناهم اثنتي عشرة أسباطا أمما}','24','3','0');
INSERT INTO [t1415] VALUES (7,'٥- ومن سخافاته الطفولية أيضا توهمه أن من الواجب تغيير قوله تعالى في الآية ١٩ من سورة "الحج": {هذان خصمان اختصموا في ربهم}','26','3','0');
INSERT INTO [t1415] VALUES (8,'٦- أما الغلطة السادسة التي لا وجود لها إلا في ذهن ذلك المأفون المسكون بالأوهام والضلالات فهي زعمه أنه كان يجب أن يقال: "وخضتم كالذين خاضوا"','28','3','0');
INSERT INTO [t1415] VALUES (9,'٧- ونبلغ الاعتراض السابع، وفيه يقول عبدنا الفاضي (الذي يمتلئ كتابه الحقير بالأخطاء النحوية','32','3','0');
INSERT INTO [t1415] VALUES (10,'٨- أما الاعتراض الثامن فهو قول الآخر إن الضمير في كلمة "بنورهم" من قوله تعالى عن المنافقين في الآية ١٧ من سورة "البقرة": {مثلهم كمثل الذي استوقد نارا فلما أضاءت ما حوله ذهب الله بنورهم}','35','3','0');
INSERT INTO [t1415] VALUES (11,'٩- ومن الإيجاز القرآني البليغ نصب {المقيمين الصلاة}','37','3','0');
INSERT INTO [t1415] VALUES (12,'١٠- أما ما أقدم عليه هذا الطائش من تخطئة قوله تعالى في الآية العاشرة من سورة "هود": {ولئن أذقناه نعماء بعد ضراء مسته ليقولن ذهب السيئات عني}','39','3','0');
INSERT INTO [t1415] VALUES (13,'١١- وفي قوله تعالى في الآية ٨٠ من سورة "البقرة" حكاية لمزاعم اليهود وأمانيهم الباطلة من أنهم','40','3','0');
INSERT INTO [t1415] VALUES (14,'١٢- وهنا نصل إلى الاعتراض الثاني عشر الذي يقول فيه الببغاء إن عبارة "أياما معدودات"','43','3','0');
INSERT INTO [t1415] VALUES (15,'١٣- ويستنكر عبد الفاضي استخدام القرآن الكريم لصيغة "إلياسين" (بدل "إلياس")','44','3','0');
INSERT INTO [t1415] VALUES (16,'١٤- كذلك يعترض المتنطع على استخدام الآية ١٧٧ من سورة "البقرة" لكلمة "البر" وصفا لـ {من آمن بالله واليوم الآخر ... }','47','3','0');
INSERT INTO [t1415] VALUES (17,'١٥- ونصل الآن إلى الاعتراض الخامس عشر فنجد أنفسنا لا نزال مع الآية السابقة، حيث يزعم صويحبنا إنه كان يجب أن يقال','51','3','0');
INSERT INTO [t1415] VALUES (18,'١٦- وفي قوله تعالى في الآية ٥٩ من سورة "آل عمران": {إن مثل عيسى عند الله كمثل آدم خلقه من تراب ثم قال له كن فيكون}','52','3','0');
INSERT INTO [t1415] VALUES (19,'١٧- وفي قوله تعالى في الآية ١٥ من "يوسف" عن إخوته عليه السلام وعزمهم على التخلص منه حتى يخلو لهم وجه أبيهم','55','3','0');
INSERT INTO [t1415] VALUES (20,'١٨- ويمضي صويحبنا الأحمق في لجاجاته قائلا إن التركيب في الآية التاسعة من سورة "الفتح" يؤدي إلى اضطراب المعنى.','57','3','0');
INSERT INTO [t1415] VALUES (21,'١٩- وبالمثل يخلق أحمقنا برعونته مشكلة أخرى لا وجود لها إلا في عقله، إذ يقول إن "سلاسل" و"قوارير" في الآيتين','60','3','0');
INSERT INTO [t1415] VALUES (22,'٢١- ويأخذ المتنطع الفارغ العقل على قوله جل من قائل في الآية ١٩٦ من "البقرة" عمن تمتع بالعمرة إلى الحج','62','3','0');
INSERT INTO [t1415] VALUES (23,'٢٢- ومن "عشرة كاملة" إلى لغة "أكلوني البراغيث" كما يسميها النحاة.','66','3','0');
INSERT INTO [t1415] VALUES (24,'٢٣- والآخر يعيب الالتفات من الخطاب إلى الغيبة في قوله تعالى في الآية ٢١ من "يونس": {هو الذي يسيركم في البر والبحر حتى إذا كنتم في الفلك وجرين بهم بريح طيبة','68','3','0');
INSERT INTO [t1415] VALUES (25,'٢٤- كذلك يستغرب جاهلنا أن القرآن لم يقل في الآية ٦٢ من سورة "التوبة": "والله ورسوله أحق أن يرضوهما" بدلا من {والله ورسوله أحق أن يرضوه}','71','3','0');
INSERT INTO [t1415] VALUES (26,'٢٥- ونأتي إلى آخر الشبه الموجودة في فصل الكتاب الخامس المسمى "أسئلة لغوية"، وهي تتعلق بجمع كلمة "قلب" في قوله عز شأنه يخاطب عائشة وحفصة - رضي الله عنهما - وأرضاهما','72','3','0');
INSERT INTO [t1415] VALUES (27,'وهناك شبهات لغوية أخرى أوردها هذا الشقي في مواضع أخرى من كتابه منها قوله: "جاء في فواتح ٢٩ سورة بالقرآن حروف عاطلة','77','3','0');
INSERT INTO [t1415] VALUES (28,'ومن اعتراضاته الحمقاء قوله: "كيف يكون القرآن عربيا مبينا وبه كلمات أعجمية كثيرة','81','3','0');
INSERT INTO [t1415] VALUES (29,'وتحت عنوان "الكلام المتكرر"، وهو أحد عناوين الفصل التاسع المسمى "أسئلة فنية"','85','3','0');
INSERT INTO [t1415] VALUES (30,'ومما يجلب به ذلك الأخرق أيضا من شبهات تبعث على القهقهة ما قاله كذلك في هذا الفصل تحت عنوان "الكلام الغريب"','89','3','0');
INSERT INTO [t1415] VALUES (31,'شبهات خاصة بالمضمون','95','0','0');
INSERT INTO [t1415] VALUES (32,'ويستنكر الشقي أن يكون إسماعيل عليه السلام رسولا نبيا طبقا لما جاء في سورة "مريم" /٥٤، قائلا: "كيف يكون إسماعيل نبيا','102','31','0');
INSERT INTO [t1415] VALUES (33,'وبالنسبة لما جاء في القرآن الكريم عن امرأة العزيز ودعوتها من يلكن سيرتها من نسوة المدينة إلى متكإ في بيتها واعترافها أمامهن بأنها مشغوفة بيوسف','113','31','0');
INSERT INTO [t1415] VALUES (34,'ومما له صلة بموضوعنا واعترض به الطائش على الوحي الإلهي قوله إن القرآن قد ذكر أن الذي صنع العجل لبني إسرائيل في التيه هو السامري','133','31','0');
INSERT INTO [t1415] VALUES (35,'ومن اعتراضات جاهلنا أيضا اعتراضه على ما جاء في سورة "ص"/٥٥ من قول الحق تبارك وتعالى لأيوب عليه السلام: {وخذ بيدك ضغثا فاضرب به ولا تحنث}','138','31','0');
INSERT INTO [t1415] VALUES (36,'ويأخذ العبد الفاضي على القرآن ما يسميه "خلط الأسماء" حيث تقول الآيات ٨٤ - ٨٦ من سورة "الأنعام" عن إبراهيم عليه السلام: {ووهبنا له إسحاق ويعقوب كلا هدينا ونوحا هدينا من قبل','148','31','0');
INSERT INTO [t1415] VALUES (37,'وتحت عنوان "الوحي الذي يشك فيه مبلغه" يسوق الشقي اللعين قوله تعالى مخاطبا رسوله عليه السلام في بدايات الوحي','157','31','0');
INSERT INTO [t1415] VALUES (38,'ويستمر التعيس في تخبطاته فيقول إن قوله عز شأنه في الآية ٢٣ من "المائدة" عن يهود المدينة: {وكيف يحكمونك وعندهم التوراة فيها حكم الله}','160','31','0');
