INSERT INTO [t14242] VALUES (1,'مقدمة','2','0','0');
INSERT INTO [t14242] VALUES (2,'تفسير القرآن وغير ذلك','3','0','0');
INSERT INTO [t14242] VALUES (3,'السؤال (١): ما هو إعراب «كلمة» في قوله تعالى: {كبرت كلمة}؟','3','2','0');
INSERT INTO [t14242] VALUES (4,'السؤال (٢): قوله تعالى: {إنما النجوى من الشيطان ليحزن الذين آمنوا} ما هو إعراب «الذين»؟','3','2','0');
INSERT INTO [t14242] VALUES (5,'السؤال (٣): ما تفسير قوله تعالى: {والشفع والوتر}؟','3','2','0');
INSERT INTO [t14242] VALUES (6,'السؤال (٤): إذا قلنا: إن قوله تعالى: {عاملة ناصبة} في يوم القيامة، فما هو المعنى على هذا التفسير؟','3','2','0');
INSERT INTO [t14242] VALUES (7,'السؤال (٥): قوله تعالى: {الذي يصلى النار الكبرى} مفهوم الآية أن هناك نارا صغرى، وذكرتم في شرح العقيدة الواسطية أن النار نار واحدة، فما الجواب عن الآية؟','3','2','0');
INSERT INTO [t14242] VALUES (8,'السؤال (٦): ما هي صحف موسى المذكورة في قوله تعالى: {صحف إبراهيم وموسى}.','4','2','0');
INSERT INTO [t14242] VALUES (9,'السؤال (٧): ما هو تفسير قوله تعالى: {أولم يروا أنا خلقنا لهم مما عملت أيدينا أنعاما فهم لها مالكون}.','4','2','0');
INSERT INTO [t14242] VALUES (10,'السؤال (٨): ما معنى قوله تعالى: {عدوا بغير علم}؟','4','2','0');
INSERT INTO [t14242] VALUES (11,'السؤال (٩): يقول بعض العلماء: إن الخوف على المال والولد لا يبيح التقية مستدلا على ذلك بوجوب الهجرة فما رأيكم؟','4','2','0');
INSERT INTO [t14242] VALUES (12,'السؤال (١٠): هل يجوز كتابة أي شيء على حواشي المصحف كما يقع في مصاحف الصغار؟','4','2','0');
INSERT INTO [t14242] VALUES (13,'السؤال (١١): سألته رحمه الله: لو وضع الإنسان معاني بعض الكلمات أو كتب بعض القراءات على الحاشية؟','5','2','0');
INSERT INTO [t14242] VALUES (14,'السؤال (١٢): ما حكم سماع القران والإنسان مشغول؟','5','2','0');
INSERT INTO [t14242] VALUES (15,'السؤال (١٣): ما حكم استماع القرآن من البعض دون البعض الآخر؟','5','2','0');
INSERT INTO [t14242] VALUES (16,'السؤال (١٤): ما حكم وضع الآيات عند افتتاح الدكان مثلا كآية التوبة {وقل اعملوا فسيرى الله عملكم. . . .} وآية الفتح {إنا فتحنا لك فتحا مبينا}؟','5','2','0');
INSERT INTO [t14242] VALUES (17,'السؤال (١٥): ما حكم وضع وليمة عند حفظ القرآن؟','5','2','0');
INSERT INTO [t14242] VALUES (18,'السؤال (١٦): ما حكم التحرك عند قراءة القرآن بلا قصد؟','5','2','0');
INSERT INTO [t14242] VALUES (19,'السؤال (١٧): ما حكم فتح أوراق المصحف ببل الأصبع؟','5','2','0');
INSERT INTO [t14242] VALUES (20,'السؤال (١٨): ما معنى قوله تعالى: {والذين هم به مشركون}؟','6','2','0');
INSERT INTO [t14242] VALUES (21,'السؤال (١٩): هل نقول: إن سورتي الأنفال والتوبة سورة واحدة لأنه لم تنزل بينهما بسملة؟','6','2','0');
INSERT INTO [t14242] VALUES (22,'السؤال (٢٠): ما حكم ترتيل الحديث كالقرآن؟','6','2','0');
INSERT INTO [t14242] VALUES (23,'السؤال (٢١): كيف نجمع بين قوله تعالى: {ادعوهم لآبائهم} وقوله صلى الله عليه وسلم: «من أحب أن يقرأ القرآن غضا كما أنزل فليقرأ على قراءة ابن أم عبد»، وكذلك قولهم: زينب بنت أم سلمه ومحمد ابن الحنيفة؟','6','2','0');
INSERT INTO [t14242] VALUES (24,'الحديث النبوي','7','0','0');
INSERT INTO [t14242] VALUES (25,'السؤال (٢٢): ما هو الجمع بين قوله صلى الله عليه وسلم: «لكل نبي حواري» وقوله: «يأتي النبي وليس معه أحد»؟','7','24','0');
INSERT INTO [t14242] VALUES (26,'السؤال (٢٣): ما هو الجمع بين قوله صلى الله عليه وسلم: «فإن الشيطان يبيت على خيشومه» وقوله صلى الله عليه وسلم: «من قرأ آية الكرسي في ليلة لم يقربه شيطان حتى يصبح»، وقوله صلى الله عليه وسلم: «من ذكر الله عند دخوله وطعامه يقول الشيطان: لا مبيت لكم ولا عشاء»؟','7','24','0');
INSERT INTO [t14242] VALUES (27,'السؤال (٢٤): قوله صلى الله عليه وسلم: «اتقوا اللعانين» هل يفيد لعن المتخلي بعينه؟','8','24','0');
INSERT INTO [t14242] VALUES (28,'السؤال (٢٥): قول الرسول صلى الله عليه وسلم: «آية المنافق ثلاث. . .» الحديث، هل هو نفاق عملي؟ وما معنى قوله: «كان منافقا خالصا»؟','8','24','0');
INSERT INTO [t14242] VALUES (29,'السؤال (٢٦): ما الجمع بين هذه الأحاديث: «الإسلام يهدم ما كان قبله»، و «إذا أسلم العبد فحسن إسلامه. . . .»، وحديث «أخذ بالأول والآخر»؟','8','24','0');
INSERT INTO [t14242] VALUES (30,'السؤال (٢٧): ما المراد بالقرب في قوله صلى الله عليه وسلم: «أقرب ما يكون العبد من ربه وهو ساجد».','9','24','0');
INSERT INTO [t14242] VALUES (31,'السؤال (٢٨): ما هو الجمع بين حديث عائشة رضي الله عنها في عدد المصلين على الجنازة وهو مائة وحديث ابن عباس رضي الله عنها وهو أربعون، هل نقول: إن في الأربعين وصف عدم الإشراك بالله شيئا، ولهذا اكتفي بالأربعين ومع تخلف هذا الوصف لابد من مائة؟','9','24','0');
INSERT INTO [t14242] VALUES (32,'السؤال (٢٩): وسألته رحمه الله: عن الجمع بين أحاديث التسمية عند الوضوء وحديث أن الرسول صلى الله عليه وسلم سلم عليه رجل فلم يرد عليه حتى تطهر، وكذا حديث: «إني كرهت أن أذكر الله إلا على طهر»؟','10','24','0');
INSERT INTO [t14242] VALUES (33,'العقيدة وحكم بعض الألفاظ','11','0','0');
INSERT INTO [t14242] VALUES (34,'السؤال (٣٠): ما حكم قولهم: «وامعتصماه!»؟','11','33','0');
INSERT INTO [t14242] VALUES (35,'السؤال (٣١): يقال: إن أسماء الله الـ] ٩٩] موجودة في الكفين، حيث إن أحد الكفين موجود فيه] ٨١] والآخر] ١٨] فهل هذا صحيح؟','11','33','0');
INSERT INTO [t14242] VALUES (36,'السؤال (٣٢): ما حكم عبارة يقولها ابن القيم: «سقط من عين الله»؟','11','33','0');
INSERT INTO [t14242] VALUES (37,'السؤال (٣٣): ما حكم قولنا لشخص: إنه «أهل الثناء»؟','11','33','0');
INSERT INTO [t14242] VALUES (38,'السؤال (٣٤): عبارة يقولها بعض الناس، وهى قولهم «ما له رب»؟','11','33','0');
INSERT INTO [t14242] VALUES (39,'السؤال (٣٥): يقول ابن القيم في «إعلام الموقعين» في حق الصحابة الذين صلوا العصر في بني قريظة: إنهم سلف أهل الظاهر. فهل في هذه العبارة شيء؟','11','33','0');
INSERT INTO [t14242] VALUES (40,'السؤال (٣٦): ما حكم قول بعضهم: «يا إلهي أنت جاهي. . .»؟','11','33','0');
INSERT INTO [t14242] VALUES (41,'السؤال (٣٧): إذا سئل إنسان عما يحفظ من المتون، فهل له أن يقول: «أحفظ القرآن»؟','12','33','0');
INSERT INTO [t14242] VALUES (42,'السؤال (٣٨): ما حكم قول العامة: عزيز، دحيم؟','12','33','0');
INSERT INTO [t14242] VALUES (43,'السؤال (٣٩): ما حكم التسمية ب (جار الله، رفيق الله)؟','12','33','0');
INSERT INTO [t14242] VALUES (44,'السؤال (٤٠): هل التوراة كلام الله؟','12','33','0');
INSERT INTO [t14242] VALUES (45,'السؤال (٤١): قول مالك: إن الإيمان يزيد ولا ينقص، هل هو تأدبا مع النصوص أو عقيدة يعتقدها رحمه الله؟','12','33','0');
INSERT INTO [t14242] VALUES (46,'السؤال (٤٢): مقالة شيخ الإسلام في المأمون: «لا أظن الله يغفل المأمون» هل هي من التألي؟ وأين قال الشيخ هذه المقالة؟','12','33','0');
INSERT INTO [t14242] VALUES (47,'السؤال (٤٣): وسألته مرة أخرى عن مقالة شيخ الإسلام؟','13','33','0');
INSERT INTO [t14242] VALUES (48,'السؤال (٤٤): حكم قول بعضهم لم يكن التاريخ حاكما عادلا؟','13','33','0');
INSERT INTO [t14242] VALUES (49,'السؤال (٤٥): وسألت الشيخ رحمه الله عن قول بعضهم في مالك: «فمن قاسه بالشمس يبخسه حقه»؟','13','33','0');
INSERT INTO [t14242] VALUES (50,'السؤال (٤٦): يقول الناس: هلك فلان أو فلان الهالك على جهة الذم فهل هذا صحيح؟','14','33','0');
INSERT INTO [t14242] VALUES (51,'السؤال (٤٧): وسألته عن قول السفاريني في كتابه لوامع الأنوار البهية (١/ ١): «تنزهت عن سمات الحدوث صفاته»؟','14','33','0');
INSERT INTO [t14242] VALUES (52,'السؤال (٤٨): ما حكم السؤال بجاه الله؟','14','33','0');
INSERT INTO [t14242] VALUES (53,'السؤال (٤٩): ما حكم قول بعضهم: السيدة عائشة؟','14','33','0');
INSERT INTO [t14242] VALUES (54,'السؤال (٥٠): ما حكم قول العامة: «عز الله»؟','14','33','0');
INSERT INTO [t14242] VALUES (55,'السؤال (٥١): ما حكم عبارة «الله غني عن التعريف» و «طالما إن الله موجود. . . .»؟','14','33','0');
INSERT INTO [t14242] VALUES (56,'السؤال (٥٢): وسألته رحمه الله عن قول السفاريني في كتابه «غذاء الألباب» (١/ ٤٢٦)','15','33','0');
INSERT INTO [t14242] VALUES (57,'السؤال (٥٣): ما حكم قول العامة لمن قدم له قريب: «قرت عينك» ورده عليهم: «برؤية نبيك»؟','15','33','0');
INSERT INTO [t14242] VALUES (58,'السؤال (٥٤): وقع في كتيب صغير باسم «أقسام التوحيد»] وهو عبارة عن سؤال موجه للشيخ رحمه الله في نور على الدرب [: الشرك الصغير والكبير، فهل هذا من تعبيركم؟','15','33','0');
INSERT INTO [t14242] VALUES (59,'السؤال (٥٥): سئلتم عما يقرأ الإمام على جماعته من الكتب فذكرتم أنه يجمع بين كتب الأحكام وكتب الوعظ؛ لأن كتب الأحكام جافة، أليس في كلمة جافة شيء؟','15','33','0');
INSERT INTO [t14242] VALUES (60,'السؤال (٥٦): ما حكم فك السحر بالسحر؟','16','33','0');
INSERT INTO [t14242] VALUES (61,'السؤال (٥٧): وقع في شرحكم لكتاب التوحيد المطبوع (١/ ٩٤) عند ذكر العين قولكم: يقولون لإبطال العين أن يصلي على العائن صلاة الجنازة؛ فما صحة ذلك؟','16','33','0');
INSERT INTO [t14242] VALUES (62,'السؤال (٥٨): ما الفرق بين دعاء صفة من صفات الله وبين الحلف بالصفة، والحديث الذي فيه: «برحمتك أستغيث»؟','16','33','0');
INSERT INTO [t14242] VALUES (63,'السؤال (٥٩): هل نقول: إن بعض أسماء الله وصفاته أكمل من بقية الأسماء أم لا؟','16','33','0');
INSERT INTO [t14242] VALUES (64,'السؤال (٦٠): يقول شيخ الإسلام في الحموية وهو يتكلم عن صفات الله: بما وصف به نفسه أو وصفه به رسوله بما وصفه السابقون الأولون، فهل السابقون الأولون يستقلون بوصف الله بصفة من الصفات؟','17','33','0');
INSERT INTO [t14242] VALUES (65,'السؤال (٦١): نقل لي بعضهم أنك تنكر قول من يقول إذا قيل له: تفضل مثلا: لا جزاك الله خيرا، فهل هذا صحيح؟','17','33','0');
INSERT INTO [t14242] VALUES (66,'السؤال (٦٢): إذا قال قائل: وكان الفضل الأول لفلان، يعني: بعض مشايخه، فهل هذا جائز؟','17','33','0');
INSERT INTO [t14242] VALUES (67,'السؤال (٦٣): إذا كتبت مثلا فلان العزيز أو الكريم ثم أردت إتلاف هذه الورقة، فهل يلزمني محو (العزيز والكريم): لأنهما من أسماء الله؟','17','33','0');
INSERT INTO [t14242] VALUES (68,'السؤال (٦٤): سمعتكم في بعض الدروس تقولون: إن الرسول طب القلوب والأبدان أليس في العبارة محذور؟','18','33','0');
INSERT INTO [t14242] VALUES (69,'السؤال (٦٥): من العلماء من يقول: إن أبوي النبي صلى الله عليه وسلم وابن جدعان بلغتهم دعوة أبينا إبراهيم عليه السلام فما صحة ذلك؟','18','33','0');
INSERT INTO [t14242] VALUES (70,'السؤال (٦٦): ما حكم قوله «شاءت أقدار الله» و «شاءت حكمة الله» والأخير وقع في كلام ابن القيم في احد كتبه؟','18','33','0');
INSERT INTO [t14242] VALUES (71,'السؤال (٦٧): ما حم قول العامة: بصلاة أمك وأبوك؟','18','33','0');
INSERT INTO [t14242] VALUES (72,'السؤال (٦٨): ما حكم ما يفعله بعض الناس من ذبح ديك ومسح عتبة الباب بدمه، وكذا وضع ملح بدعوى منع الجن؟','19','33','0');
INSERT INTO [t14242] VALUES (73,'السؤال (٦٩): هل تثبت صفة الشم لله؟','19','33','0');
INSERT INTO [t14242] VALUES (74,'السؤال (٧٠): ما حكم قولهم: «سبحان اللي (الذي) يغير ولا يتغير»؟','19','33','0');
INSERT INTO [t14242] VALUES (75,'السؤال (٧١): ما حكم قولنا في الرسول صلى الله عليه وسلم: «طب القلوب والأبدان»؟','19','33','0');
INSERT INTO [t14242] VALUES (76,'السؤال (٧٢): ما حكم قول القائل " لكم خالص حبي " أو خالص شكري"؟','19','33','0');
INSERT INTO [t14242] VALUES (77,'السؤال (٧٣): سألته هل يمكن للكافر أن يخلص في عبادته؟','20','33','0');
INSERT INTO [t14242] VALUES (78,'السؤال (٧٤): سألت شيخنا ابن عثيمين عن حكم قول: "ظلم ربه"؟','20','33','0');
INSERT INTO [t14242] VALUES (79,'الطهارة','21','0','0');
INSERT INTO [t14242] VALUES (80,'السؤال (٧٥): هل يطهر جلد غير مأكول اللحم بالدباغ؟','21','79','0');
INSERT INTO [t14242] VALUES (81,'السؤال (٧٦): لو انغمس محدث حدثا أصغر في ماء ناويا رفع حدثه فهل يرتفع؟','21','79','0');
INSERT INTO [t14242] VALUES (82,'السؤال (٧٧): ما حكم خروج الريح من قبلي الرجل والمرأة؟','21','79','0');
INSERT INTO [t14242] VALUES (83,'السؤال (٧٨): ما حكم الكلام في الحمام حال الوضوء؟','21','79','0');
INSERT INTO [t14242] VALUES (84,'السؤال (٧٩): إذا كان الغلام الذكر يرضع لبنا صناعيا فهل ينضح بوله؟','21','79','0');
INSERT INTO [t14242] VALUES (85,'السؤال (٨٠): ما هي كيفية المسح على الخفين؟','21','79','0');
INSERT INTO [t14242] VALUES (86,'السؤال (٨١): هل الأفضل أن سأل عن اللحم لاحتمال كونه جزورا أولا أسأل؟','21','79','0');
INSERT INTO [t14242] VALUES (87,'السؤال (٨٢): ما حكم إذا قطع الوضوء قبل أن ينتهي منه؟','22','79','0');
INSERT INTO [t14242] VALUES (88,'السؤال (٨٣): إذا لم يستطع الإنسان أن يتوضأ بنفسه فهل يوضئه غيره أو يتيمم؟','22','79','0');
INSERT INTO [t14242] VALUES (89,'السؤال (٨٤): هل يتيمم إذا دخل الوقت وهو يؤمل وجود الماء لو مشى قليلا؟','22','79','0');
INSERT INTO [t14242] VALUES (90,'السؤال (٨٥): ما حكم إنشاء الوضوء في وقت النهي ثم الصلاة بعده؟','22','79','0');
INSERT INTO [t14242] VALUES (91,'السؤال (٨٦): هل يجوز الاغتسال في الماء الراكد للتبرد؟','22','79','0');
INSERT INTO [t14242] VALUES (92,'السؤال (٨٧): إذا كان يشق التحرز من الأسد ونحوه فهل يلحق بالهرة؟','22','79','0');
INSERT INTO [t14242] VALUES (93,'السؤال (٨٨): إذا وقع البول في الأرض الصلبة فهل يكاثر عليه الماء كالأرض الرخوة أم ماذا؟','23','79','0');
INSERT INTO [t14242] VALUES (94,'السؤال (٨٩): هل الوضوء الذي يكون مع غسل الجنابة يرفع الحدث الأصغر؟','23','79','0');
INSERT INTO [t14242] VALUES (95,'السؤال (٩٠): هل يجوز تطهير الدهن بالمكاثرة؟','23','79','0');
INSERT INTO [t14242] VALUES (96,'السؤال (٩١): إذا اغتسل من عليه جنابة وعلى جسمه لزقة لا يضره إزالتها وصلى بذلك الغسل فماذا عليه؟','23','79','0');
INSERT INTO [t14242] VALUES (97,'السؤال (٩٢): قول الفقهاء: إن من حدثه دائم لا ينوى رفع الحدث وإنما ينوى استباحة الصلاة هل هذا صحيح؟','23','79','0');
INSERT INTO [t14242] VALUES (98,'السؤال (٩٣): هل الكيفية الواردة في مسح الرأس في حيث عبد الله بن زيدا واجبة؛ لأنها طريق للاستيعاب وخاصة في الشعر الكثيف؟','24','79','0');
INSERT INTO [t14242] VALUES (99,'السؤال (٩٤): ما حكم مسح الأذنين في الوضوء؟','24','79','0');
INSERT INTO [t14242] VALUES (100,'السؤال (٩٥): إذا لبس العمامة وظهر شيء من رأسه هل يجب مسحه؟ وهل يجب عليه إظهار الناصية؟','24','79','0');
INSERT INTO [t14242] VALUES (101,'السؤال (٩٦): إذا رد الشعر النازل عن محل الفرض فجعله على الرأس فهل يجوز أن يمسح عليه؟','24','79','0');
INSERT INTO [t14242] VALUES (102,'السؤال (٩٧): الحق الفقهاء بمدافعة الأخبثين الريح فكيف يتصور ذلك؟','25','79','0');
INSERT INTO [t14242] VALUES (103,'السؤال (٩٨): قول شيخ الإسلام رحمه الله: «إذا أجنب وخاف إن اغتسل شط فيه» فإنه يتيمم هل هو وجيه؟','25','79','0');
INSERT INTO [t14242] VALUES (104,'السؤال (٩٩): في حديث عمرو بن عبسة قول النبي صلى الله عليه وسلم: «ثم يمسح برأسه إلا خرت خطاياه رأسه من أطراف شعره مع الماء» فهل يؤخذ من هذا استحباب غسل الرأس كبقية الأعضاء خاصة أنه لا يمكن حمل الحديث على التغليب؟','25','79','0');
INSERT INTO [t14242] VALUES (105,'السؤال (١٠٠): إنسان أكل لحم جذور ثم توضأ ثم إنه بلع ما بين أسنانه فهل تنتقض طهارته؟','25','79','0');
INSERT INTO [t14242] VALUES (106,'السؤال (١٠١): ما حكم التسمية في الخلاء إذا أراد الوضوء؟','26','79','0');
INSERT INTO [t14242] VALUES (107,'السؤال (١٠٢): ذكرتم أن مباشرة غسل أعضاء الغير محرمة فهل هذا صحيح؟','26','79','0');
INSERT INTO [t14242] VALUES (108,'السؤال (١٠٣): هل يمكن أن يمسح على جبيرة وعلى خف كأن تكون الجبيرة في رجله اليمنى والخف في اليسرى؟','26','79','0');
INSERT INTO [t14242] VALUES (109,'السؤال (١٠٤): قلتم: يمسح على كل ما يسمى خفا، فهل يدخل في عموم كلامكم الكندرة التي تكون عادة دون الكعبين؟','26','79','0');
INSERT INTO [t14242] VALUES (110,'السؤال (١٠٥): لو مسح مقيم ثم سافر فهل يتم مسح مسافر؟','26','79','0');
INSERT INTO [t14242] VALUES (111,'السؤال (١٠٦): ما هو القول الراجح في العمامة من جهة التوقيت؟','26','79','0');
INSERT INTO [t14242] VALUES (112,'السؤال (١٠٧): كيف تمسح وهل يجب أن يمسح ما ظهر من الرأس وكذا الأذنين؟','27','79','0');
INSERT INTO [t14242] VALUES (113,'السؤال (١٠٨): وسألته عن الوضوء للغضب إذا قلنا: إنه يرفع الحدث، فلماذا لا نقول: إن الغسل للجمعة يرفع حدث الجنابة؛ لأن كليهما تطهر مشروع؟','27','79','0');
INSERT INTO [t14242] VALUES (114,'السؤال (١٠٩) هل يمسح على الربطة إذا ربطها على ألم من المفصل مثلا ولا يضره وصول الماء إلى مكان الألم لكن قال له الطبيب: أبق الربطة أسبوعا مثلا؟','27','79','0');
INSERT INTO [t14242] VALUES (115,'السؤال (١١٠): هل يقال ذكر دخول الخلاء والخروج منه إذا دخل لغير قضاء الحاجة؟','27','79','0');
INSERT INTO [t14242] VALUES (116,'السؤال (١١١): إذا كان الطفل صغيرا هل تلقنه أمه ذكر دخول الخلاء أم تقوله هي بنفسها؟','27','79','0');
INSERT INTO [t14242] VALUES (117,'السؤال (١١٢): ما حكم ما تراه الحامل من الدم؟ وما حكم إذا سقط الجنين؟','27','79','0');
INSERT INTO [t14242] VALUES (118,'السؤال (١١٣): لكن ما حكم إذا رأت الدم في الشهر الرابع، ثم سقط الجنين وقال الأطباء: إن الجنين ميت لما كان عمره ثلاثة أسابيع فهل تقضى الصلوات التي تركتها بناء على أنه كان حيا وسقط بعد التخلق؟','28','79','0');
INSERT INTO [t14242] VALUES (119,'السؤال (١١٤): ما حكم السؤال عن اللحم المقدم للأكل هل هو لحم جزور أم لا؟','28','79','0');
INSERT INTO [t14242] VALUES (120,'السؤال (١١٥): إذا تيمم ثم لبس جوربيه فهل له أن يمسح عليهما؟','28','79','0');
INSERT INTO [t14242] VALUES (121,'السؤال (١١٦): ثبت في الصحيح أن الرسول صلى الله عليه وسلم قال لمن جامع زوجته ولم ينزل " اغسل ذكرك وما أصابك منها" أفلا يدل هذا على نجاسة رطوبة فرج المرأة؟','28','79','0');
INSERT INTO [t14242] VALUES (122,'السؤال (١١٧): شخص على يده بوية وأزال ما استطاع وعجز عن شيء منها فماذا يفعل؟','28','79','0');
INSERT INTO [t14242] VALUES (123,'الحيض والنفاس','29','0','0');
INSERT INTO [t14242] VALUES (124,'السؤال (١١٨): هل مجرد النقاء طهر في النفاس؟ وما حكمه إذا طال؟','29','123','0');
INSERT INTO [t14242] VALUES (125,'السؤال (١١٩): إذا زاد الدم على المرأة أكثر من خمسة عشر يوما فما حكمه؟','29','123','0');
INSERT INTO [t14242] VALUES (126,'السؤال (١٢٠): إذا رأت المرأة صفرة أو كدرة في موعد دورتها فهل تعتبره حيضا؟','29','123','0');
INSERT INTO [t14242] VALUES (127,'السؤال (١٢١): انقطع عن امرأة الدم شهران على أنها حامل، ثم خرج منها دم كثير فما حكم هذا الدم؟ علما أنه تبين أنه ليس بحمل؟','29','123','0');
INSERT INTO [t14242] VALUES (128,'السؤال (١٢٢): إذا أصاب امرأة دم فظنت أنه حيض فتركت الصلاة، ثم بعد السؤال تبين لها أنه دم استحاضة فهل تقضى الصلوات بوضوء واحد؟','29','123','0');
INSERT INTO [t14242] VALUES (129,'السؤال (١٢٣): ما حكم نزول الصفرة والكدرة وخاصة إذا كانت قريبة من دورتها الشهرية أو في أيام دورتها؟','30','123','0');
INSERT INTO [t14242] VALUES (130,'السؤال (١٢٤): إذا أسقطت امرأة ما في بطنها قبل تخلق الجنين، فإنها تصلى وتصوم كما هو معلوم، ولكن ما الحكم إذا وافق هذا الدم الدورة الشهرية؟','30','123','0');
INSERT INTO [t14242] VALUES (131,'السؤال (١٢٥): ما حكم الدم الذي تراه الحامل قبل الولادة وهل تمسك عن الصلاة إذا رأت دما؟','30','123','0');
INSERT INTO [t14242] VALUES (132,'الصلاة','31','0','0');
INSERT INTO [t14242] VALUES (133,'السؤال (١٢٦): وسألته رحمه الله: ما المراد بالساعة في قوله صلى الله عليه وسلم: من راح في الساعة الأولى يوم الجمعة كان كمن قدم بدنة. . .» الحديث؟','31','132','0');
INSERT INTO [t14242] VALUES (134,'السؤال (١٢٧): أطلق الشارع على الصلاة التسبيح فهل يكون التسبيح ركنا فيها؟','31','132','0');
INSERT INTO [t14242] VALUES (135,'السؤال (١٢٨): ما حكم من أصيب بغيبوبة طويلة فهل يقضى الصلاة والصوم؟','31','132','0');
INSERT INTO [t14242] VALUES (136,'السؤال (١٢٩): ما المراد بطهارة البقعة التي يصلى عليها المصلى؟','31','132','0');
INSERT INTO [t14242] VALUES (137,'السؤال (١٣٠): ما المراد بالثوب الشفاف الذي لا تجوز الصلاة فيه؟','31','132','0');
INSERT INTO [t14242] VALUES (138,'السؤال (١٣١): من له زوجة في بلد هل يتم أو يقصر؟','32','132','0');
INSERT INTO [t14242] VALUES (139,'السؤال (١٣٢): هل يجوز جمع التقديم بين الظهر والعصر أو المغرب والعشاء مع جزمه بأنه سيصل بلده قبل دخول وقت الثانية؟','32','132','0');
INSERT INTO [t14242] VALUES (140,'السؤال (١٣٣): إذا زاد ذكرا مشروعا في غير محله كأن قرأ سورة قصيرة بعد الفاتحة في الركعة الأخيرة من المغرب أو الركعتين الأخيرتين من الرباعية فهل يسجد للسهو، وكذلك لو صلى على النبي صلى الله عليه وسلم في التشهد الأول؟','32','132','0');
INSERT INTO [t14242] VALUES (141,'السؤال (١٣٤): ما حكم نسيان سجود السهو؟','32','132','0');
INSERT INTO [t14242] VALUES (142,'السؤال (١٣٥): ما حكم من وضع يدا وحدة على خاصرته في الصلاة؟','32','132','0');
INSERT INTO [t14242] VALUES (143,'السؤال (١٣٦): ما حكم وضع المدخنة أو الدفاية أمام المصلين؟','32','132','0');
INSERT INTO [t14242] VALUES (144,'السؤال (١٣٧): ما الحكم إذا ترك المأموم سجدة التلاوة مع إمامه؟','33','132','0');
INSERT INTO [t14242] VALUES (145,'السؤال (١٣٨): ما هو الدوران في المنارة؟','33','132','0');
INSERT INTO [t14242] VALUES (146,'السؤال (١٣٩): إذا كان المسافر يعلم أنه سيمكث ثلاثة أيام مثلا فهل يقصر على قول الجمهور؟','33','132','0');
INSERT INTO [t14242] VALUES (147,'السؤال (١٤٠): ما حكم صلاة الجزارين وأصحاب البويات؟','33','132','0');
INSERT INTO [t14242] VALUES (148,'السؤال (١٤١): ما حكم البصاق في المنديل وهو متجه للقبلة في الصلاة؟','33','132','0');
INSERT INTO [t14242] VALUES (149,'السؤال (١٤٢): هل النهى عن البصاق في القبلة عام أم خاص بالصلاة؟','33','132','0');
INSERT INTO [t14242] VALUES (150,'السؤال (١٤٣): ما حكم الزيادة على التسبيح ونحوه من الأذكار التي بعد الصلاة؟','34','132','0');
INSERT INTO [t14242] VALUES (151,'السؤال (١٤٤): إذا خشي من رد المرأة حين مرورها وهو يصلى أن يقع في فتنة فهل له تركها؟','34','132','0');
INSERT INTO [t14242] VALUES (152,'السؤال (١٤٥): إذا أتم المسافر خلف الإمام المقيم فهل يصلي السنة الراتبة البعدية إذا كان لها سنة راتبة؟','34','132','0');
INSERT INTO [t14242] VALUES (153,'السؤال (١٤٦): ما حكم الصلاة في الكنيسة؟','34','132','0');
INSERT INTO [t14242] VALUES (154,'السؤال (١٤٧): ما حكم الاجتماع للتعزية؟','34','132','0');
INSERT INTO [t14242] VALUES (155,'السؤال (١٤٨): ما الجمع بين قوله صلى الله عليه وسلم: «إن الله ينور قبورهم بصلاتي عليهم»','35','132','0');
INSERT INTO [t14242] VALUES (156,'السؤال (١٤٩): ما المراد بالشيطان الذي يهرب وله ضراط إذا سمع التأذين؟','35','132','0');
INSERT INTO [t14242] VALUES (157,'السؤال (١٥٠): ما حكم من صلى قصرا ثم تذكر أن الواجب في حقه الإتمام فهل يبني أو يستأنف من جديد؟','36','132','0');
INSERT INTO [t14242] VALUES (158,'السؤال (١٥١): هل يتعدد القيراط بتعدد الجنائز؟','36','132','0');
INSERT INTO [t14242] VALUES (159,'السؤال (١٥٢): ما حكم تأمين المأمومين في خطبة الجمعة؟','36','132','0');
INSERT INTO [t14242] VALUES (160,'السؤال (١٥٣): ما حكم رفع اليدين للدعاء في المقبرة؟','36','132','0');
INSERT INTO [t14242] VALUES (161,'السؤال (١٥٤): بعض الناس يدخل المسجد بعد أذان صلاة الفجر فيصلى أربع ركعات؛ ركعتين تحية المسجد وركعتين راتبة الفجر، فما حكم ذلك؟','36','132','0');
INSERT INTO [t14242] VALUES (162,'السؤال (١٥٥): متى يكون مدركا تكبيرة الإحرام؟','36','132','0');
INSERT INTO [t14242] VALUES (163,'السؤال (١٥٦): قوم مسافرون عزموا على تأخير صلاة الظهر إلى العصر جمعا ثم وصلوا مطار الرياض وهم من أهل الرياض فهل يقصرون ويجمعون في المطار؟','37','132','0');
INSERT INTO [t14242] VALUES (164,'السؤال (١٥٧): هل الذي تفوته دائما بعض الصلاة يأثم؟','37','132','0');
INSERT INTO [t14242] VALUES (165,'السؤال (١٥٨): ما حكم لبس المرأة النقاب في الصلاة؟','37','132','0');
INSERT INTO [t14242] VALUES (166,'السؤال (١٥٩): هل يحتسب المسبوق بالركعة الزائدة؟','37','132','0');
INSERT INTO [t14242] VALUES (167,'السؤال (١٦٠): هل يتابع المسبوق بركعة الإمام إذا قام إلى ثالثة في فجر مع أن المأموم قد سمع الإمام وهو يجهر بالفاتحة؟','37','132','0');
INSERT INTO [t14242] VALUES (168,'السؤال (١٦١): إذا صلى الإمام خمسا في رباعية فلما سلم نبه فسجد للسهو فهل يتابعه المسبوق بركعة في هذا السجود مع أن صلاته تامة؟','38','132','0');
INSERT INTO [t14242] VALUES (169,'السؤال (١٦٢): هل يصدق على من أوتر بعد العشاء وراتبتها أنه قام الليل؟','38','132','0');
INSERT INTO [t14242] VALUES (170,'السؤال (١٦٣): ما حكم من قام إلى ثالثة في ليل وهو يريد أن يوتر (أي أن هذا الشفع الذي قام فيه هو آخر شفع) فهل يستمر أم يرجع مع أنه جلس للتشهد في الثانية؟','38','132','0');
INSERT INTO [t14242] VALUES (171,'السؤال (١٦٤): إذا دخل وقت الصلاة في الطائرة فهل يصلى بالإيماء أم يؤخرها؟','38','132','0');
INSERT INTO [t14242] VALUES (172,'السؤال (١٦٥): إذا أخر جماعة أذان العشاء مثلا لخروجهم في نزهة ونحو ذلك فهل يدخل وقت سنة مابين الأذانين إذا دخل وقت العشاء أو لا يدخل وقتها حتى يؤذنوا؟','38','132','0');
INSERT INTO [t14242] VALUES (173,'السؤال (١٦٦): ما حكم قضاء راتبة الفجر بعد مدة من أداء صلاة الفريضة؟','39','132','0');
INSERT INTO [t14242] VALUES (174,'السؤال (١٦٧): قلتم - حفظكم الله -: إن المريض الذي يرجى برؤه يؤخر لعله يبرأ فيصلى الجمعة مع الإمام ولا يبادر فيصلى الظهر، فما تقولون في العادم للماء، هل ينتظر لعله يجد الماء أو يصلى بالتيمم في أول الوقت؟','39','132','0');
INSERT INTO [t14242] VALUES (175,'السؤال (١٦٨): إذا تبين أن القبلة إلى جهة اليمين أو الشمال قليلا ولكن الصفوف التي خلف الإمام لا يمكن أن تغير اتجاهها؛ لأنها في المسجد، فهل للإمام أن يتجه للقبلة الصحيحة أم يبقى على الجهة الأولى تبعا للمأمومين؟','39','132','0');
INSERT INTO [t14242] VALUES (176,'السؤال (١٦٩): ما حكم إذا تذكر صلاة فائتة وهو في صلاة؟','39','132','0');
INSERT INTO [t14242] VALUES (177,'السؤال (١٧٠) فصل الفقهاء في ترك التشهد الأول فما ترون حفظكم الله؟','39','132','0');
INSERT INTO [t14242] VALUES (178,'السؤال (١٧١): هل يتابع المأموم الإمام في سجود السهو بعد السلام وهو عليه قضاء؟','39','132','0');
INSERT INTO [t14242] VALUES (179,'السؤال (١٧٢) إذا شك إنسان في صلاته فلم يدر كم صلى ثلاثا أم أربعا وكان من عادته أن يصلى الرباعية في ست دقائق مثلا فهل له أن ينظر في الساعة؟','40','132','0');
INSERT INTO [t14242] VALUES (180,'السؤال (١٧٣): إذا صلى رباعية خلف إمام يصلى المغرب فهل إذا جلس الإمام للتشهد الأخير ينوى المفارقة أم يتابعه؟','40','132','0');
INSERT INTO [t14242] VALUES (181,'السؤال (١٧٤): هل قيام ما قرب من آخر الليل أفضل من قيام أول الليل؟','40','132','0');
INSERT INTO [t14242] VALUES (182,'السؤال (١٧٥): إذا نسي سجود السهو فما الحكم علما بأن شيخ لإسلام يرى أنه لا','40','132','0');
INSERT INTO [t14242] VALUES (183,'السؤال (١٧٦): إذا فاتت شخصا صلاة الجماعة فهل الأفضل له الصلاة في المسجد أم في البيت؟','40','132','0');
INSERT INTO [t14242] VALUES (184,'السؤال (١٧٧): هل الأفضل المبادرة إلى الصلاة أم الاشتغال بالعلم إلى حين الإقامة؟','41','132','0');
INSERT INTO [t14242] VALUES (185,'السؤال (١٧٨): ما حكم لو أوتر بثلاث كمغرب؟','41','132','0');
INSERT INTO [t14242] VALUES (186,'السؤال (١٧٩): متى يقضى الوتر إذا فات؟','41','132','0');
INSERT INTO [t14242] VALUES (187,'السؤال (١٨٠): إذا فاتت أحدا الصلاة مع الجماعة فهل يجب عليه أن يصلى مع الجماعة الثانية، وكذا لو كان في رحله هل يجب عليه أن يصلى مع الجماعة؟','41','132','0');
INSERT INTO [t14242] VALUES (188,'السؤال (١٨١): وسألته عن فتواه في عدم مشروعية الذهاب إلى المصاب بميت في بيته؟','41','132','0');
INSERT INTO [t14242] VALUES (189,'السؤال (١٨٢): ما معنى قول الرسول صلى الله عليه وسلم: «من نام عن حزبه. . . .» الحديث؟','42','132','0');
INSERT INTO [t14242] VALUES (190,'السؤال (١٨٣): وسألته عن مقالة نقلها صاحب «الفروع» (١/ ٥٣٣) عن ابن عقيل في التفاضل بين الأعمال وهى قوله: «لا يقع لهم (أي: الناس) أن العمل سوى ركعات يتنفل بها الإنسان في جوف الليل، تلك عبادة الكسالى العجزة»؟','42','132','0');
INSERT INTO [t14242] VALUES (191,'السؤال (١٨٤): هل يعيد الصلاة من صلى إلى غير القبلة ساهيا؟','42','132','0');
INSERT INTO [t14242] VALUES (192,'السؤال (١٨٥): هل لمن كان في الحضر أن يجتهد في القبلة؟','42','132','0');
INSERT INTO [t14242] VALUES (193,'السؤال (١٨٦): إذا وقع الكسوف في ساعة متأخرة من الليل فهل يلزم الإنسان بالصلاة، وإذا حصل للزوجة روعة من ذلك فأبت على زوجها الذهاب فه لله ذلك؟','42','132','0');
INSERT INTO [t14242] VALUES (194,'السؤال (١٨٧): كيف أنكر على من يبيع والإمام يخطب يوم الجمعة؟','43','132','0');
INSERT INTO [t14242] VALUES (195,'السؤال (١٨٨): ما حكم تخطى الرقاب بعد صلاة الجمعة؟','43','132','0');
INSERT INTO [t14242] VALUES (196,'السؤال (١٨٩): ما حكم الانحراف عن القبلة قليلا مع تيقنها بالبوصلة؟','43','132','0');
INSERT INTO [t14242] VALUES (197,'السؤال (١٩٠): معلوم أن الرواتب أوكد من صلاة الضحى ونحوها ومع ذلك تترك في السفر أفلا يدل ذلك على ترك ما هو دونها في الأوكدية؟','43','132','0');
INSERT INTO [t14242] VALUES (198,'السؤال (١٩١): معلوم أنه إذا وافق يوم العيد يوم الجمعة سقطت الجمعة عمن صلى العيد، لكن هل يجوز أن يصلى الظهر منفردا أو يجب عليه أن يصلي جماعة فإن لم يجد صلى جمعة؟','43','132','0');
INSERT INTO [t14242] VALUES (199,'السؤال (١٩٢): هل ينعقد النفل في وقت النهي مع الجهل؟','43','132','0');
INSERT INTO [t14242] VALUES (200,'السؤال (١٩٣): إذا دخل وقت النهى وهو في نافلة فما حكم ذلك؟','44','132','0');
INSERT INTO [t14242] VALUES (201,'السؤال (١٩٤): إذا نذر صلاة في وقت النهى فما الحكم؟','44','132','0');
INSERT INTO [t14242] VALUES (202,'السؤال (١٩٥): يدعو بعض الأئمة بقوله: «اللهم نجنا من ضمة القبر»، فما حكم هذا الدعاء؟','44','132','0');
INSERT INTO [t14242] VALUES (203,'السؤال (١٩٦): هل تجوز الصلاة على القبر في وقت النهي؟','44','132','0');
INSERT INTO [t14242] VALUES (204,'السؤال (١٩٧): إذا تكرر تلاوة آية فيها سجدة فما حكم ذلك؟','44','132','0');
INSERT INTO [t14242] VALUES (205,'السؤال (١٩٨): إذا كان يصلى في مصلى أربعة فروض في اليوم الواحد فهل يعطى حكم المسجد؟','44','132','0');
INSERT INTO [t14242] VALUES (206,'السؤال (١٩٩): إذا قام من يعظ بعد الصلاة مباشرة أو قرأ الإمام من كتاب فهل تسقط أذكار الصلاة أو له أن يقولها بعد ذلك وإن طالت الموعظة؟','45','132','0');
INSERT INTO [t14242] VALUES (207,'السؤال (٢٠٠): إذا امتلأ المسجد الحرام بالمصلين حتى جلس الناس صفوفا في المسعى ثم دخل في المسعى فهل يصلى تحية المسجد بحجة اتصال الصفوف وأن من في المسعى حكم من في المسجد؟','45','132','0');
INSERT INTO [t14242] VALUES (208,'السؤال (٢٠١): إذا ترك الإمام التشهد الأول واستتم قائما وقلنا: لا يرجع وإن لم يقرأ الفاتحة فهل يسبح المأموم ليعلمه أنه ترك واجبا أم لا؟','45','132','0');
INSERT INTO [t14242] VALUES (209,'السؤال (٢٠٢): ذكر صاحب «كشاف القناع» (١/ ٣٥٤) أثرا عن ابن عباس أن من قام إلى الركعة الثانية أو الرابعة ثم قدم رجله أن صلاته تبطل، فما رأيكم بهذا الأثر؟','45','132','0');
INSERT INTO [t14242] VALUES (210,'السؤال (٢٠٣): إذا أراد الإنسان سفرا بالطائرة فهل له أن يجمع بين الظهرين في الحضر؟','46','132','0');
INSERT INTO [t14242] VALUES (211,'السؤال (٢٠٤): هل للمأموم أن يتأخر بعد سلام إمامه ليدعو؟','46','132','0');
INSERT INTO [t14242] VALUES (212,'السؤال (٢٠٥): وسألته عن رجل صلى على جنين عمره أربعة أشهر ونصف، ثم دفنه في أرض طيبة؟','46','132','0');
INSERT INTO [t14242] VALUES (213,'السؤال (٢٠٦): ما حكم ترتيل بعض الخطباء للآية في الخطبة؟','46','132','0');
INSERT INTO [t14242] VALUES (214,'السؤال (٢٠٧): ما حكم المعانقة في الأعياد وفي التعزية؟','46','132','0');
INSERT INTO [t14242] VALUES (215,'السؤال (٢٠٨): صلى مسافر ركعتين خلف مقيم ولم يصل أربعا فما الحكم عند الإعادة هل يصلي أربعا أو اثنتين؟','46','132','0');
INSERT INTO [t14242] VALUES (216,'السؤال (٢٠٩): إذا تبايع رجلان بعد نداء الجمعة الثاني فإن البيع لا يصلح؛ وذلك لأن النهي عائد إلى نفس المعاملة وقلنا بصحة إمامة من أم الناس بلا إذن الإمام أو عذره مع أن النهي عائد إلى ذات الإمامة فما هو الفرق؟','47','132','0');
INSERT INTO [t14242] VALUES (217,'السؤال (٢١٠): يقول بعض العلماء إن الخمرة التي يصلى عليها تكون قدر الوجه أفلا يكون فيه تشبها بالرافضة؟','47','132','0');
INSERT INTO [t14242] VALUES (218,'السؤال (٢١١): إذا دخل المأموم والإمام راكع فكبر تكبيرة نوى بها الإحرام والركوع فهل يصح؟','47','132','0');
INSERT INTO [t14242] VALUES (219,'السؤال (٢١٢): وإذا كبر ولم ينو شيئا؟','47','132','0');
INSERT INTO [t14242] VALUES (220,'السؤال (٢١٣): يقول بعض الفقهاء: لا يقصد المسجد لإعادة الجماعة ولو صلى وحده فهل هذا صحيح؟','47','132','0');
INSERT INTO [t14242] VALUES (221,'السؤال (٢١٤): يقول الفقهاء الحنابلة وكذا الشافعية: إن المأموم إذا أدرك الإمام في غير الركوع فإنه يكبر للإحرام ثم ينحط بلا تكبير فما رأيكم؟','48','132','0');
INSERT INTO [t14242] VALUES (222,'السؤال (٢١٥): هل يجوز أن يذهب إلى مسجد قباء ليصلي في وقت النهي؟','48','132','0');
INSERT INTO [t14242] VALUES (223,'السؤال (٢١٦): يرى شيخ الإسلام أن سجدة التلاوة ليست بصلاة؛ ثم إذا ذكر أوقات النهى يذكر أنه يجوز فعل ذوات الأسباب في أوقات النهي ويذكر معها سجدة التلاوة، فما هو الجمع بين قوليه؟','48','132','0');
INSERT INTO [t14242] VALUES (224,'السؤال (٢١٧): إذا صلى العشاء قصرا ووصل إلى بلده قبل خروج وقت العشاء فهل يصلي راتبة العشاء؟','48','132','0');
INSERT INTO [t14242] VALUES (225,'السؤال (٢١٨): هل نقول أن كل من تعمد مبطلا في الصلاة فقد أتى كبيرة قياسا على مسابقة الإمام؟','48','132','0');
INSERT INTO [t14242] VALUES (226,'السؤال (٢١٩): ما المراد بقول الفقهاء في باب الإمامة: «وحر أولى من عبد» مع قولهم قبل ذلك: «ثم من قرع»؟','49','132','0');
INSERT INTO [t14242] VALUES (227,'السؤال (٢٢٠): ما العمل إذا علم المأموم بحدث إمامه أثناء الصلاة؟','49','132','0');
INSERT INTO [t14242] VALUES (228,'السؤال (٢٢١): هل يصلي في وقت النهى من صلى الصلاة في جماعة مع غيره لتحصل للثاني جماعة؟','49','132','0');
INSERT INTO [t14242] VALUES (229,'السؤال (٢٢٢): قول الفقهاء إذا ترك الإمام ما يعتقده واجبا وحده عمدا: بطلت صلاتهما، هل هذا صحيح؟','49','132','0');
INSERT INTO [t14242] VALUES (230,'السؤال (٢٢٣): ما حكم ضرب من يعبث في الصلاة من الأطفال أثناء الصلاة؟','50','132','0');
INSERT INTO [t14242] VALUES (231,'السؤال (٢٢٤): إذا كانت المرأة قارئة والرجل أميا فهل تؤمه في الصلاة؟','50','132','0');
INSERT INTO [t14242] VALUES (232,'السؤال (٢٢٥): ثبت في الصحيح أن النبي صلى الله عليه وسلم صلى بأصحابه الفريضة في بيته عندما سقط من فرسه فما وجه هذا الحديث؟','50','132','0');
INSERT INTO [t14242] VALUES (233,'السؤال (٢٢٦): ما حكم إذا شك وهو في التشهد الأخير هل هذا التشهد للشفع أم للوتر، وهو من عادته الفصل بين الشفع والوتر؟','50','132','0');
INSERT INTO [t14242] VALUES (234,'السؤال (٢٢٧): ما حكم إمامة من عدم الماء والتراب؟','50','132','0');
INSERT INTO [t14242] VALUES (235,'السؤال (٢٢٨) هل تلحق صلاة الراتبة في مكتب (العمل) وبيت القريب بصلاته في بيته؟','51','132','0');
INSERT INTO [t14242] VALUES (236,'السؤال (٢٢٩): هل الصف الأول من الدور الثاني والسطح في المسجد الحرام مثل الصف الأول تحته أم هناك فرق؟','51','132','0');
INSERT INTO [t14242] VALUES (237,'السؤال (٢٣٠): إذا كان أكثر الجماعة يكرهون إمامهم بحق إلا أن هذا المسجد يصلي فيه غير الجماعة الراتبة، فهل العبرة بكراهة الجماعة الراتبة أو بمجموع من في المسجد؟','51','132','0');
INSERT INTO [t14242] VALUES (238,'السؤال (٢٣١) ما حكم لو صلى شخص الفجر خلف من يصلي صلاة الاستسقاء وكذا صلاة الكسوف؟','51','132','0');
INSERT INTO [t14242] VALUES (239,'السؤال (٢٣٢): إذا أحرم الإمام أو المأموم بصلاة حاضرة ثم تذكر أن عليه صلاة فائتة فما يفعل؟','51','132','0');
INSERT INTO [t14242] VALUES (240,'السؤال (٢٣٣): إذا لحن الإمام في الفاتحة لحنا يحيل المعنى ولم يصلحه فما الحكم؟ وإذا أصلحه فهل عليه سجود سهو؟','52','132','0');
INSERT INTO [t14242] VALUES (241,'السؤال (٢٣٤): ما حكم من صلى صلاة العشاء مقصورة خلف المغرب؟','52','132','0');
INSERT INTO [t14242] VALUES (242,'السؤال (٢٣٥): إذا صلى اثنان خلف الصف ثم انصرف أحدهم لحدث ونحوه فماذا يفعل الثاني؟','52','132','0');
INSERT INTO [t14242] VALUES (243,'السؤال (٢٣٦): ما حكم مصافة من دون التمييز؟','52','132','0');
INSERT INTO [t14242] VALUES (244,'السؤال (٢٣٧): ما حكم أخذ الزينة لصلاة الليل؟','53','132','0');
INSERT INTO [t14242] VALUES (245,'السؤال (٢٣٨): إذا حضرت صلاة الجماعة ومعه طفله الصغير وخشي عليه فهل يعذر في ترك الجماعة؟','53','132','0');
INSERT INTO [t14242] VALUES (246,'السؤال (٢٣٩): وسألت شيخنا ابن عثيمين رحمه الله ما حكم قراءة الفاتحة في القيام الثاني من صلاة الكسوف هل هي واجبة؟','53','132','0');
INSERT INTO [t14242] VALUES (247,'السؤال (٢٤٠): وسألته عن حكم صلاة المسافر العشاء خلف مقيم يصلي المغرب؟','53','132','0');
INSERT INTO [t14242] VALUES (248,'السؤال (٢٤١): هل قول الإمام للمأمومين قبل الصلاة: استووا، تناظروا، تقاربوا جائزة أم هي من الألفاظ التوقيفية؟','54','132','0');
INSERT INTO [t14242] VALUES (249,'السؤال (٢٤٢): ذكر شيخ الإسلام وغيره الإجماع على أن المسبوق إذا قضى ما عليه فإنه يقرأ في الركعة الثالثة والرابعة الفاتحة وسورة مع أنهم مختلفون هل ما يدركه المأموم هو أول صلاته أو آخرها؟','54','132','0');
INSERT INTO [t14242] VALUES (250,'السؤال (٢٤٣): وسألته رحمه الله: من نسي صلاة ثم تذكرها ولم يبق من الوقت إلا مقدار فعلها فهل يصلي الراتبة القبلية إن كان لها راتبة؟','54','132','0');
INSERT INTO [t14242] VALUES (251,'السؤال (٢٤٤): وسألته عن حكم الصلاة في مسجد فيه قبر؟','54','132','0');
INSERT INTO [t14242] VALUES (252,'السؤال (٢٤٥): جوز الحنابلة فعل الصلاة المنذورة في أوقات النهي فهل مرادهم أن ينذر صلاة ركعتين مثلا في وقت النهي أو مرادهم أن يقول إذا قدم زيد فلله علي أن أصلي ركعتين فقدم في وقت النهي؟','55','132','0');
INSERT INTO [t14242] VALUES (253,'السؤال (٢٤٦): سألت ابن عثيمين عن حكم دخول الكنيسة؟','55','132','0');
INSERT INTO [t14242] VALUES (254,'السؤال (٢٤٧): إذا قدر المريض على أن يصلي قائما علي ركبتيه فهل يلزمه ذلك أم يصلي جالسا؟','56','132','0');
INSERT INTO [t14242] VALUES (255,'السؤال (٢٤٨): إذا صلى المريض مستلقيا فهل يضع تحت رأسه شيئا؟','56','132','0');
INSERT INTO [t14242] VALUES (256,'السؤال (٢٤٩): إذا صلي على جنبه فهل يومئ بظهره أم برأسه؟','56','132','0');
INSERT INTO [t14242] VALUES (257,'السؤال (٢٥٠): إذا أومأ بالسجود فهل يضع يديه علي الأرض؟','56','132','0');
INSERT INTO [t14242] VALUES (258,'السؤال (٢٥١): وهل يلزمه رفع العجيزة في هذه الحالة؟','56','132','0');
INSERT INTO [t14242] VALUES (259,'السؤال (٢٥٢): هل الليلة الباردة عذر في ترك الجماعة؟','56','132','0');
INSERT INTO [t14242] VALUES (260,'السؤال (٢٥٣): إذا كان بينه وبين الصف الذي هو مسامت له فراغ كثير فهل يصدق عليه أنه خلف الصف؟','57','132','0');
INSERT INTO [t14242] VALUES (261,'السؤال (٢٥٤): ما حكم لو تذكر الجماع فانتصب ذكره وهو في الصلاة؟','57','132','0');
INSERT INTO [t14242] VALUES (262,'السؤال (٢٥٥): إذا غابت الشمس ثم طار بالطائرة باتجاه المغرب فرأى الشمس فصلى العصر فهل يكون مصليا في الوقت؟','57','132','0');
INSERT INTO [t14242] VALUES (263,'السؤال (٢٥٦): ما حكم متابعة الإمام في دعاء الختمة؟ وهل من فارق الإمام في هذه الحالة لا يدخل في حديث أبي ذر رضي الله عنه «من صلى مع إمامه حتى ينصرف كتب له قيام ليلة»؟','57','132','0');
INSERT INTO [t14242] VALUES (264,'السؤال (٢٥٧): إذا حان وقت الإقامة كما هو مقرر من وزارة الأوقاف ودخل الإمام المسجد، فهل الأولى أن يصلي بالجماعة أو يصلي راتبة الصلاة القبلية؟','58','132','0');
INSERT INTO [t14242] VALUES (265,'السؤال (٢٥٨): ما حكم شد الرحل لإمامة جماعة في التراويح في مسجد معين؟','58','132','0');
INSERT INTO [t14242] VALUES (266,'السؤال (٢٥٩): إذا كانت الأرض مبتلة وقلنا: يومئ بالسجود فماذا عن الجلوس؟','58','132','0');
INSERT INTO [t14242] VALUES (267,'السؤال (٢٦٠): ما حكم لو صلى في الطائرة بالإيماء مع تمكنه من الصلاة في المطار أو كانت الصلاة مما تجمع إلى ما بعدها؟','58','132','0');
INSERT INTO [t14242] VALUES (268,'السؤال (٢٦١): إذا قلنا: إن الطبيب الكافر إذا كان ثقة، وقال لمريض: صل مستلقيا لأنه أمكن في مداواتك، أخذنا بقوله فما الحكم إذا اختلف طبيبان مسلم وكافر؟','59','132','0');
INSERT INTO [t14242] VALUES (269,'السؤال (٢٦٢): ما حكم لو صلى الصغير الذي لم يبلغ الصلاة المكتوبة جالسا؟','59','132','0');
INSERT INTO [t14242] VALUES (270,'السؤال (٢٦٣): يفرق بعض الفقهاء بين الذي يعجز عن القيام لعلة فيه وبين الذي يستطيع القيام ولكن لا يستطيع الإتيان به لقصر سقف الحجرة، فقالوا في الأول: يصلي ولو كراكع وفي الثاني يصلي جالسا فما هو رأي سماحتكم؟','59','132','0');
INSERT INTO [t14242] VALUES (271,'السؤال (٢٦٤): إذا قام ليلة الجمعة وقام الليلة التي قبلها أو الليلة التي بعدها فهل تزول الكراهة؟','59','132','0');
INSERT INTO [t14242] VALUES (272,'السؤال (٢٦٥): هل يترخص في سفر المعصية؟','59','132','0');
INSERT INTO [t14242] VALUES (273,'السؤال (٢٦٦): هل يقصر الصلاة إذا خرج للنزهة؟','59','132','0');
INSERT INTO [t14242] VALUES (274,'السؤال (٢٦٧): صلى شخص المغرب في سفر، ثم قدم بلده قبل دخول وقت العشاء فرأى أنه متعب، فهل له الجمع؟','60','132','0');
INSERT INTO [t14242] VALUES (275,'السؤال (٢٦٨): قول الفقهاء: «ويجوز لمرضع لمشقة كثرة نجاسة الجمع» هل يصدق هذا في عصرنا الحاضر؟','60','132','0');
INSERT INTO [t14242] VALUES (276,'السؤال (٢٦٩): إذا أبى الإمام أن يجمع للمطر ورأي المأمومون أو بعضهم أن ذلك مبيح للجمع فهل يجمعون في المسجد؟','60','132','0');
INSERT INTO [t14242] VALUES (277,'السؤال (٢٧٠): قول الفقهاء: «له الجمع ولو صلى في بيته» هل هذا صحيح؟','60','132','0');
INSERT INTO [t14242] VALUES (278,'السؤال (٢٧١): وجود المطر عذر مبيح لترك الجماعة وكذا هو عذر للجمع، فهل يعني ذلك أن المطر الذي يبل الثياب إذا وجد قبل خروج الإنسان من منزله فإن له ترك الجماعة، وإذا وجد بعد ذلك فله الجمع؟','60','132','0');
INSERT INTO [t14242] VALUES (279,'السؤال (٢٧٢): قول الفقهاء: «إذا نسي ركنا من الصلاة الأولى المجموعة أعاد الأولى والثانية» هل هذا صحيح؟','61','132','0');
INSERT INTO [t14242] VALUES (280,'السؤال (٢٧٣): وقولهم: «إن ترك ركنا ولم يدر من أيهما تركه أعادهما جميعا، هل هذا صحيح»؟','61','132','0');
INSERT INTO [t14242] VALUES (281,'السؤال (٢٧٤): إذا قدم من سفر في وقت الأولى وقد شرع في الثانية المجموعة كالعصر أو العشاء، فهل يتمها فرضا أو نفلا؟','61','132','0');
INSERT INTO [t14242] VALUES (282,'السؤال (٢٧٥): إذا أخر صلاة الظهر إلى العصر وهو في سفر ثم قدم بلده في وقت العصر فهل يصلي الظهر أربعا أو اثنين؟','61','132','0');
INSERT INTO [t14242] VALUES (283,'السؤال (٢٧٦): هل الترتيب بين المجموعتين شرط مطلقا أو مع الذكر؟','62','132','0');
INSERT INTO [t14242] VALUES (284,'السؤال (٢٧٧): نوى شخص أن يصلي الظهر والعصر جمع تأخير ثم لم يصلهما حتى بقي من وقت العصر ما لا يسع إلا لواحدة منهما، فهل يرتب هنا أو يصلي العصر أولا؟','62','132','0');
INSERT INTO [t14242] VALUES (285,'السؤال (٢٧٨): لو وقع عذر يبيح الجمع بين الصلاتين كمطر ولكن أبى الإمام الجمع فهل للمأمومين الجمع؟','62','132','0');
INSERT INTO [t14242] VALUES (286,'السؤال (٢٧٩): وسألته عن رجل ذهب إلى المطار للسفر والمطار خارج البلد إلا أنه ليس عنده حجز مؤكد بل في قائمة الانتظار، فهل يجوز له القصر؟','62','132','0');
INSERT INTO [t14242] VALUES (287,'السؤال (٢٨٠): إذا سمع المسافر النداء للصلاة وهو ما زال في بلده. فهل يلزم بالصلاة مع الجماعة أو يمضي في سفره؟','62','132','0');
INSERT INTO [t14242] VALUES (288,'السؤال (٢٨١): وإذا سمع الإقامة فما الحكم؟','63','132','0');
INSERT INTO [t14242] VALUES (289,'السؤال (٢٨٢): إذا سلم الإمام قبل إتمام الصلاة ثم نبه أو تنبه فما الحكم في ذلك؟','63','132','0');
INSERT INTO [t14242] VALUES (290,'السؤال (٢٨٣): قلتم: لا يجوز للمسافر أن يجمع العصر مع الجمعة إلا إذا نوى بالجمعة الظهر فيجوز لكن يفوته الأجر، فهل نأخذ من ذلك أن لكل من لم تجب عليه الجمعة كالمرأة والصبي أن يحضر وينويها ظهرا؟','63','132','0');
INSERT INTO [t14242] VALUES (291,'السؤال (٢٨٤): ما هو القول الراجح في صلاة المسافر الجمعة؟','63','132','0');
INSERT INTO [t14242] VALUES (292,'السؤال (٢٨٥): هل يجوز للمسافر إذا دخل بلدا وهو يريد أن يقيم فيه أياما الجمع بين الظهرين والعشاءين؛ لأنه إذا جمع لم يصل العصر والعشاء مع الجماعة مع أنه يسمع النداء؟','64','132','0');
INSERT INTO [t14242] VALUES (293,'السؤال (٢٨٦): قول الفقهاء: «إذا بلغ الصبي بعد أن صلى الظهر يوم الجمعة فإنه يجب عليه أن يصلي الجمعة فإن صلوا أعاد الظهر» هل قولهم صحيح؟','64','132','0');
INSERT INTO [t14242] VALUES (294,'السؤال (٢٨٧): علل بعض الفقهاء عدم انعقاد الجمعة بالعبد والمسافر بقوله: لو انعقدت بهم لانعقدت بهم منفردين كالأحرار المقيمين. فهل هذا صحيح؟','64','132','0');
INSERT INTO [t14242] VALUES (295,'السؤال (٢٨٨): هل ينبني على خلاف الفقهاء في كون صلاة الجمعة صلاة مستقلة شيء؟','64','132','0');
INSERT INTO [t14242] VALUES (296,'السؤال (٢٨٩): إذا أدرك المسافر التشهد من صلاة الجمعة فهل يصلي ظهرا مقصورة؟','64','132','0');
INSERT INTO [t14242] VALUES (297,'السؤال (٢٩٠): إذا أدرك من صلاة الجنازة التكبيرة الثالثة ثم سلم الإمام فهل يقضي المأموم ما فاته من التكبير؟','65','132','0');
INSERT INTO [t14242] VALUES (298,'السؤال (٢٩١): متى تفوت تكبيرة الإحرام؟ وما صحة قول من يقول: إنها تفوت إذا شرع الإمام بالفاتحة؟','65','132','0');
INSERT INTO [t14242] VALUES (299,'السؤال (٢٩٢): أحيانا يكون الإنسان مستعجلا فلا يأتي بأذكار الصلاة إلا بعد الراتبة البعدية فهل يفوته فضلها؟','65','132','0');
INSERT INTO [t14242] VALUES (300,'السؤال (٢٩٣): ما حكم ظهور شيء من الصدر أو الظهر في أثناء الصلاة؟ وهل الأمر بستر العاتق أمر بسترهما؟','66','132','0');
INSERT INTO [t14242] VALUES (301,'السؤال (٢٩٤): لو صلى إنسان وهو مسبل إزاره فهل تبطل صلاته؟','66','132','0');
INSERT INTO [t14242] VALUES (302,'السؤال (٢٩٥): وسألته رحمه الله عن وجود أكثر من صبي غير مميز في الصف هل وجودهم قاطعا للصف؟','66','132','0');
INSERT INTO [t14242] VALUES (303,'السؤال (٢٩٦): هل يكبر التكبير المقيد خلف صلاة العيد وخلف صلاة مقضية؟','66','132','0');
INSERT INTO [t14242] VALUES (304,'السؤال (٢٩٧): يجوز الفقهاء أداء صلاة الكسوف في البيت، لكن ورد في بعض الأحاديث «فافزعوا إلى المساجد» فما رأيكم؟','66','132','0');
INSERT INTO [t14242] VALUES (305,'السؤال (٢٩٨): إذا سبح اثنان من المأمومين على أن الإمام أخطأ في الصلاة وهو لم يخطئ فماذا يفعل من تيقن عدم خطأ الإمام؟','67','132','0');
INSERT INTO [t14242] VALUES (306,'السؤال (٢٩٩): ما حكم لو جمع العصر مع الجمعة مدة جهلا منه؟','67','132','0');
INSERT INTO [t14242] VALUES (307,'السؤال (٣٠٠): هل تسقط تحية المسجد بطول الفصل؟','67','132','0');
INSERT INTO [t14242] VALUES (308,'السؤال (٣٠١): قلتم: إذا دخل المسجد الحرام للطواف فإنه تحيته الطواف وإذا لم يرد الطواف فإنه يصلي ركعتين تحية المسجد كغيره من المساجد مع أنه إذا طاف سوف يصلي ركعتي الطواف؟','67','132','0');
INSERT INTO [t14242] VALUES (309,'السؤال (٣٠٢): سبق وأن سألتكم عن سقوط تحية المسجد إذا طال الفصل وقلتم بسقوطها لكن ما الجواب عن حديث أنس مرفوعا: «من نام عن صلاة أو نسيها فليصلها إذا ذكرها لا كفارة لها إلا ذلك» و (صلاة) نكرة في سياق الشرط فتعم؟','68','132','0');
INSERT INTO [t14242] VALUES (310,'السؤال (٣٠٣): لو كان الإنسان مستعجلا فقرأ آية الكرسي في الركعة الأولى من الراتبة البعدية للظهر مثلا وقرأ سورة الإخلاص في الركعة الثانية من الراتبة، فهل في هذا بأس وهل يفوته الأجر الخاص بقراءة آية الكرسي دبر كل صلاة؟','68','132','0');
INSERT INTO [t14242] VALUES (311,'السؤال (٣٠٤): ما حكم إعادة صلاة الجنازة في وقت النهي؟','68','132','0');
INSERT INTO [t14242] VALUES (312,'السؤال (٣٠٥): ذكر الشيخ ناصر الألباني حديثا في كتابه الجنائز أن رسول الله صلى الله عليه وسلم دخل على رجل من الأنصار يعوده فقال له: «قل: لا إله إلا الله» وقال: إن في هذا ردا على من قال: لا يلقن بـ «قل» مع أنه وقع في الحديث قول الأنصاري: أو تنفعني فما رأيكم؟','69','132','0');
INSERT INTO [t14242] VALUES (313,'السؤال (٣٠٦): كثير من الناس إذا دخل المسجد الحرام يجلس لشرب ماء زمزم قبل أن يصلي تحية المسجد فما حكم ذلك؟','69','132','0');
INSERT INTO [t14242] VALUES (314,'السؤال (٣٠٧): ما هو الجمع بين قول الفقهاء إن المسبوق في صلاة الجنازة له أن يسلم مع الإمام وله أن يتابع التكبير، وبين قولهم: إذا نسي التكبير يقضي مع قرب الفصل أو يستأنف؟','69','132','0');
INSERT INTO [t14242] VALUES (315,'السؤال (٣٠٨): قول الفقهاء: لا تصح الصلاة على ميت في تابوت؟','70','132','0');
INSERT INTO [t14242] VALUES (316,'السؤال (٣٠٩): بعض الأئمة في رمضان يقرأ القرآن في المغرب والفجر والعشاء لكي يختم القرآن، فما رأيكم في هذا العمل؟','70','132','0');
INSERT INTO [t14242] VALUES (317,'السؤال (٣١٠): إذا دخل إنسان المسجد وهم في صلاة العشاء قد جمعوها مع المغرب جمع تقديم وقلنا له: ادخل معهم بنية المغرب فهل إذا سلم من المغرب يجمع معها العشاء أو نقول له: لا تجمع لأنك لا تستفيد من هذا الجمع؟','70','132','0');
INSERT INTO [t14242] VALUES (318,'السؤال (٣١١): وسألته عن مريض يصلي على كرسي مع الجماعة ويؤخر الكرسي عن الصف؛ لأنه يستطيع القيام، فإذا قام صار مع الصف وإذا جلس على الكرسي تخلف عن الصف فما الحكم في هذه الصورة؟','70','132','0');
INSERT INTO [t14242] VALUES (319,'السؤال (٣١٢): ما حكم الكتابة على القبور؟','71','132','0');
INSERT INTO [t14242] VALUES (320,'السؤال (٣١٣): ما حكم المشي بين القبور بالنعال؟','71','132','0');
INSERT INTO [t14242] VALUES (321,'السؤال (٣١٤): ما حكم إنارة سور المقبرة؟','71','132','0');
INSERT INTO [t14242] VALUES (322,'السؤال (٣١٥): ألا يدل حديث أبي بكرة في سقوط الفاتحة على وجوبها في حق المأموم وأنها ليست بركن؟','71','132','0');
INSERT INTO [t14242] VALUES (323,'السؤال (٣١٦): إذا قلنا: إن التصفيق جائز فما الجواب عن قوله صلى الله عليه وسلم: «إنما التصفيق للنساء»؟','72','132','0');
INSERT INTO [t14242] VALUES (324,'السؤال (٣١٧): إذا دخل المسجد وهم سجود فكبر للإحرام ولم يتابع بل ظل واقفا فما حكم ذلك.','72','132','0');
INSERT INTO [t14242] VALUES (325,'السؤال (٣١٨): إذا دخل الإنسان المسجد ولم يجد سترة يصلي إليها النافلة إلا في مكان بعيد عن الصف الأول، فأيهم أفضل الصلاة إلى سترة مع العبد عن الصف الأول أو الصلاة في الصف الأول بدون سترة؟','72','132','0');
INSERT INTO [t14242] VALUES (326,'السؤال (٣١٩): يستحب الفقهاء القبر عند رجل صالح فما رأيكم؟','72','132','0');
INSERT INTO [t14242] VALUES (327,'السؤال (٣٢٠): ذهبت المرأة إلى أناس وحضرت الصلاة فسألتهم عن القبلة فدلوها عليها، فصلت ثم تبين أن أهل البيت قد أخطئوا في تحديد القبلة، فهل عليها إعادة ما صلت أم لا؟','73','132','0');
INSERT INTO [t14242] VALUES (328,'السؤال (٣٢١): إذا صف أكثر من صبي وهم دون التمييز فهل يقطعون الصف؟','73','132','0');
INSERT INTO [t14242] VALUES (329,'السؤال (٣٢٢): ما حكم الصلاة في المقبرة؟ وإذا صلى ناسيا ما حكم صلاته؟','73','132','0');
INSERT INTO [t14242] VALUES (330,'السؤال (٣٢٣): ما حكم الصلاة في مسجد فيه قبر؟','73','132','0');
INSERT INTO [t14242] VALUES (331,'السؤال (٣٢٤): هل هناك قدر جائز في الاجتماع عند الميت؟','73','132','0');
INSERT INTO [t14242] VALUES (332,'السؤال (٣٢٥): روى المغيرة بن شعبة رضي الله عنه مرفوعا: «من نيح عليه يعذب بما نيح عليه» رواه البخاري ومسلم، زاد مسلم: «يوم القيامة» قال الشيخ الألباني رحمة الله: كنت أميل إلى اختيار شيخ الإسلام أن العذاب بمعنى التألم حتى وقفت على هذه الزيادة فترجح عندي قول من قال: إنه محمول على من أوصى بذلك، فما رأي فضيلتكم بهذه الزيادة؟','74','132','0');
INSERT INTO [t14242] VALUES (333,'السؤال (٣٢٦): أراد رجل أن يصلي ثلاث ركعات سردا في قيام الليل فنسي فجلس في الثانية فما الحكم؟','74','132','0');
INSERT INTO [t14242] VALUES (334,'السؤال (٣٢٧): ما رأيكم بالسجادة التي تكون فيها صورة الحرم الملكي أو المدني؟','75','132','0');
INSERT INTO [t14242] VALUES (335,'السؤال (٣٢٨): إذا صلى في مسجد فيه قبر وجهل الحال: هل القبر سابق أم لا وهل هو في القبلة أم لا، فما حكم صلاته؟','75','132','0');
INSERT INTO [t14242] VALUES (336,'السؤال (٣٢٩): ما حكم إذا أراد مسافر أن يصلي العشاء خلف من يصلي المغرب؟','75','132','0');
INSERT INTO [t14242] VALUES (337,'السؤال (٣٣٠): سمعتكم تجوزون الصلاة والعاتق مكشوف وتستدلون علي ذلك بحديث جابر مع أن لفظ حديث جابر: أنه يصلى، وقد عقد الإزار من قبل قفاه. كذا في «صحيح البخاري»، فما رأيكم؟','75','132','0');
INSERT INTO [t14242] VALUES (338,'السؤال (٣٣١): ما حكم صلاة المأموم جالسا مع القدرة على القيام خلف إمام قائم في نفل؟','76','132','0');
INSERT INTO [t14242] VALUES (339,'السؤال (٣٣٢): تيقن مأموم أن إمامه نقص في صلاته ركعة فما هو الواجب عليه فعله إذا كان عليه قضاء وإذا لم يكن عليه قضاء؟','76','132','0');
INSERT INTO [t14242] VALUES (340,'السؤال (٣٣٣): ما حكم السفر للصلاة على الجنازة؟ حيث سافر الكثير للصلاة على جنازة شيخنا الشيخ عبد العزيز بن باز رحمه الله؟','76','132','0');
INSERT INTO [t14242] VALUES (341,'السؤال (٣٣٤): وقع اختلاف بين الناس هلي يصلى على الشيخ صلاة الغائب في المنطقة الشرقية مثلا قبل أيصلى عليه في مكة أو تكون الصلاة على روح الميت؟','77','132','0');
INSERT INTO [t14242] VALUES (342,'السؤال (٣٣٥): ما يقع الآن في وسائل الإعلام من الثناء على الشيخ ونحوه ذلك؟','77','132','0');
INSERT INTO [t14242] VALUES (343,'السؤال (٣٣٦): هل يسجد الرجل لتلاوة المرأة إذا سجدت وكان منصتا لقراءتها؟','77','132','0');
INSERT INTO [t14242] VALUES (344,'السؤال (٣٣٧): صلى شخص الظهر في دار إقامته ثم سافر فهل له أن يصلي العصر في مطار الملك خالد قبل أن يدخل وقتها؟','77','132','0');
INSERT INTO [t14242] VALUES (345,'السؤال (٣٣٨): ما هو الوتر هل هو الركعة التي تصلى مفردة وما قبلها ليس منه؟ أو الوتر هو مجموع ما يصلي بالليل وإن كان قد صلى مثنى مثنى؟','78','132','0');
INSERT INTO [t14242] VALUES (346,'السؤال (٣٣٩): هل يشترط أن يكون المسجد هو الأعلى أو ليس فوقه شيء من البنيان؟','78','132','0');
INSERT INTO [t14242] VALUES (347,'السؤال (٣٤٠): إذا خرج جماعة إلى استراحة مثلا وأكل بعضهم بصلا ولم يأكل البعض الآخر فهل للذين أكلوا البصل أن يصلوا معهم؟','78','132','0');
INSERT INTO [t14242] VALUES (348,'الزكاة','79','0','0');
INSERT INTO [t14242] VALUES (349,'السؤال (٣٤١): ما حكم دفع الزكاة أو الصدقة لمدخن؟','79','348','0');
INSERT INTO [t14242] VALUES (350,'السؤال (٣٤٢): أم كانت تخفي بعض مال ولدها فلما مرت سنتان على المال أخرجته فأعطته إياه فهل عليه زكاة؟','79','348','0');
INSERT INTO [t14242] VALUES (351,'السؤال (٣٤٣): إذا فعل الابن حادثا بالسيارة وأتلف سيارة الغير فهل يجوز للأب أن يدفع زكاته للابن في هذه الحال؟','79','348','0');
INSERT INTO [t14242] VALUES (352,'السؤال (٣٤٤): إذا احتاج إنسان للسفر إلى بلد معين وليس معه دراهم، فهل تجوز له الزكاة؟','79','348','0');
INSERT INTO [t14242] VALUES (353,'السؤال (٣٤٥): إذا قال لي إنسان: أنا محتاج للزكاة فأعطني إياها أو سلفني، فأعطيته مالا ونويت إن كانت الزكاة تجوز له فزكاة وإلا فسلف، فما حكم ذلك؟','79','348','0');
INSERT INTO [t14242] VALUES (354,'السؤال (٣٤٦): إذا كان غارما لغيره ولكنه دفع الدراهم فهل له الرجوع في أخذ ذلك من الزكاة؟','79','348','0');
INSERT INTO [t14242] VALUES (355,'السؤال (٣٤٧): ما حكم من اشترى أرضا للسكن ثم عدل عن ذلك وأراد بيعها فهل يزكيها إذا دار الحول؟','80','348','0');
INSERT INTO [t14242] VALUES (356,'السؤال (٣٤٨): فيه إنسان يحتاج للزكاة وأعطيته (٢٠٠٠) ريال كعربون على شقة يريد استئجارها إلا أنه أكلها فهل علي ضمان؟','80','348','0');
INSERT INTO [t14242] VALUES (357,'السؤال (٣٤٩): باع رجل بيتا له بمبلغ مؤجل يحل كله بعد ثلاث سنوات فهل عليه زكاة؟','80','348','0');
INSERT INTO [t14242] VALUES (358,'السؤال (٣٥٠): ما هو حد الفقير؟','80','348','0');
INSERT INTO [t14242] VALUES (359,'السؤال (٣٥١): من كان عنده عروض تجارة فهل يزكي قيمتها وإن ذهب بنفقة ونحوها؟','80','348','0');
INSERT INTO [t14242] VALUES (360,'السؤال (٣٥٢): هل يجوز إعطاء الخدم من الزكاة؟','81','348','0');
INSERT INTO [t14242] VALUES (361,'السؤال (٣٥٣): إذا بنى شخص عمارة ليؤجرها أو بناها للبيع فهل يزكي عن العمارة إذا حال عليها الحول في الصورتين؟','81','348','0');
INSERT INTO [t14242] VALUES (362,'السؤال (٣٥٤): هل يتصور أن يزكي المال الواحد أكثر من واحد؟','81','348','0');
INSERT INTO [t14242] VALUES (363,'السؤال (٣٥٥): هل يجوز أن نشتري من الزكاة غرفة نوم ونحوها من الكماليات لمن يحتاج إلى ذلك؟','81','348','0');
INSERT INTO [t14242] VALUES (364,'السؤال (٣٥٦): طلب مني بعض المستخدمين أن أسلفه مبلغا ليشتري به ملابس للعيد فهل له ذلك؟ وإذا كان الجواب بنعم، هلي يجوز أن أعطيه من الزكاة؟','82','348','0');
INSERT INTO [t14242] VALUES (365,'السؤال (٣٥٧): هل في المال المساهم فيه في أرض ونحوها زكاة؟','82','348','0');
INSERT INTO [t14242] VALUES (366,'السؤال (٣٥٨): إذا اشترى شخص أرضا للسكن ثم أراد بيعها وحال الحول فهل يزكيها؟','82','348','0');
INSERT INTO [t14242] VALUES (367,'السؤال (٣٥٩): إذا وجب علي من عنده غنم سائمة إخراج شاة للزكاة فهل يجوز له أن يذبحها أجزاء ويوزعها علي المستحقين أو يصنع طعاما ويدعو عليها المساكين؟','82','348','0');
INSERT INTO [t14242] VALUES (368,'السؤال (٣٦٠): رجل عنده صدقة للتوزيع فهل يجوز له أن يعطيها عاملا عنده لكي ينصح في العمل؟','82','348','0');
INSERT INTO [t14242] VALUES (369,'السؤال (٣٦١): شخص عنده أرض ليبني عليها عمارة للاستغلال وللاستثمار فهل يزكي الأرض قبل البناء عليها إذا حال عليها الحول؟','83','348','0');
INSERT INTO [t14242] VALUES (370,'السؤال (٣٦٢): استأجر شخص دكانا وكلما توفر عنده مال استأجر دكانا آخر وهكذا من غير أن يحول الحول على المال المستفاد فهل عليه زكاة؟','83','348','0');
INSERT INTO [t14242] VALUES (371,'السؤال (٣٦٣): كيف تزكى الرواتب مع أنها في الغالب تنفق قبل تمام الحول فإن المال الذي بحوزته الآن هو من آخر راتب استلمه؟','83','348','0');
INSERT INTO [t14242] VALUES (372,'السؤال (٣٦٤): هل يزكي أصحاب دكاكين الخضرة والفواكه علي نفس الخضرة والفواكه أو يزكون قيمتها فقط؟','84','348','0');
INSERT INTO [t14242] VALUES (373,'السؤال (٣٦٥): هل يجوز دفع الزكاة إلى بنت ولد الأخ وهي متزوجة لكنها بحاجة؟','84','348','0');
INSERT INTO [t14242] VALUES (374,'السؤال (٣٦٦): شخص عجل زكاته لمصلحة ثم أن هذا المال الذي عجل زكاته نفد ثم استفاد مالا آخر فهل يجعل الزكاة عن هذا المال الجديد؟','84','348','0');
INSERT INTO [t14242] VALUES (375,'السؤال (٣٦٧): إذا استلم الموظف مستحقا له من الدولة بعد مضي سنتين أو أكثر فهل يزكيه؟','84','348','0');
INSERT INTO [t14242] VALUES (376,'السؤال (٣٦٨): إذا اشترى أرضا ليبني عليها عمارة من شقق للتأجير فدار الحول على هذه الأرض ولم يبن العمارة فهل عليه زكاة؟','84','348','0');
INSERT INTO [t14242] VALUES (377,'السؤال (٣٦٩): وما الحكم إذا أراد أن يبني عليها عمارة للتجارة؟','84','348','0');
INSERT INTO [t14242] VALUES (378,'السؤال (٣٧٠): إذا أعطى إنسان زكاته لمستحق وله على هذا المستحق دين ووقع في نفسه أن لو أعطاه بعض ماله عليه فهل في هذا شيء؟','85','348','0');
INSERT INTO [t14242] VALUES (379,'السؤال (٣٧١): يستلم الموظف في الدولة خارج الدوام بعد سنتين مثلا فهل يزكيه؟','85','348','0');
INSERT INTO [t14242] VALUES (380,'السؤال (٣٧٢): ورث أناس من مورثهم مالا وهذا المال لا يزال مع الوصي ولم يستلم كل وارث نصيبه فهل عليه زكاة.','85','348','0');
INSERT INTO [t14242] VALUES (381,'السؤال (٣٧٣): ما حكم من يشتري أرضا للتجارة، ثم يبيعها قبل تمام الحول ويشتري أخرى فهل يستأنف حولا أم لا؟','85','348','0');
INSERT INTO [t14242] VALUES (382,'السؤال (٣٧٤): إذا ساهم إنسان في بعض العقارات فهل يزكيها كل سنة مع أنه لو طالب بها لم يمكن من ذلك؟','85','348','0');
INSERT INTO [t14242] VALUES (383,'السؤال (٣٧٥): رجل كان يقوم بتفطير الصائمين بأموال تأتيه من المحسنين وفي العشرة الأخيرة لم يأته شيء فدفع من جيبه وتحمل حمالة بذلك، فهل يجوز صرف الزكاة له؟ وما حكم إذا أعطي فيما بعد أموالا لتفطير صائم فهل يأخذها له؟','86','348','0');
INSERT INTO [t14242] VALUES (384,'السؤال (٣٧٦): نام شخص عن صلاة العيد في الدمام فلما استيقظ اتصل على آخر في الرياض فوكله بإخراج الزكاة في الرياض لتقع في وقتها فهل هذا مشروع؟','86','348','0');
INSERT INTO [t14242] VALUES (385,'السؤال (٣٧٧): وهل تقع أداء؟','86','348','0');
INSERT INTO [t14242] VALUES (386,'السؤال (٣٧٨): وهل لو فعل هذه الحيلة المفرط تنفعه؟','86','348','0');
INSERT INTO [t14242] VALUES (387,'السؤال (٣٧٩): هل تزكى الأموال التي في جمعية الموظفين ونحوهم؟','86','348','0');
INSERT INTO [t14242] VALUES (388,'الصوم','87','0','0');
INSERT INTO [t14242] VALUES (389,'السؤال (٣٨٠): هل يجوز التنفل قبل قضاء ما عليه من رمضان؟','87','388','0');
INSERT INTO [t14242] VALUES (390,'السؤال (٣٨١): ما حكم تجميع الريق ثم بلعه وهو صائم؟','87','388','0');
INSERT INTO [t14242] VALUES (391,'السؤال (٣٨٢): صان إنسان خمسة أيام من ست أيام من شوال ولما أراد أن يصوم اليوم السادس وكان يوافق يوم الخميس دعي إلى وليمة فأجاب وأكل ثم صام الجمعة فهل يجب أن يصوم السبت؟','87','388','0');
INSERT INTO [t14242] VALUES (392,'السؤال (٣٨٣): مسألة تقع كثيرا وهي أن كثيرا من الناس يفطر على أذان بعض المؤذنين، ثم يسمع آخر بعد إفطاره فما الحكم؟','87','388','0');
INSERT INTO [t14242] VALUES (393,'السؤال (٣٨٤): يقول الفقهاء: يحرم بلع النخامة ويفطر بها. وما تقولون في هذا؟ وما حكم ذلك في الصلاة؟','87','388','0');
INSERT INTO [t14242] VALUES (394,'السؤال (٣٨٥): هل يجوز للإنسان أن يصوم الدهر كله بحجة أن الصوم له وجاء؟','88','388','0');
INSERT INTO [t14242] VALUES (395,'السؤال (٣٨٦): من كان لا يستطيع الصيام ووجب في حقه إطعام مسكين كل يوم، فهل له أن يطعم ثلاثين مسكينا أول الشهر؟','88','388','0');
INSERT INTO [t14242] VALUES (396,'السؤال (٣٨٧): إذا مات من عليه صيام شهرين متتابعين فهل يجزئ أن يصوم ستون شخصا في يوم واحد؟','88','388','0');
INSERT INTO [t14242] VALUES (397,'السؤال (٣٨٨): ما حكم استعمال البخور في أثناء الصوم؟','88','388','0');
INSERT INTO [t14242] VALUES (398,'السؤال (٣٨٩): ما حكم استعمال البخور في نهار رمضان؟','89','388','0');
INSERT INTO [t14242] VALUES (399,'السؤال (٣٩٠): إذا كان هناك صيام غرة على الناس، فهل يلزمون بصيامه ثاني أيام العيد؛ لأن رمضان لم يتم أو هم بالخيار؟','89','388','0');
INSERT INTO [t14242] VALUES (400,'السؤال (٣٩١): وسألته عن رجل يسند منخراه فلا يستطيع التنفس ويصاب بصداع؟','89','388','0');
INSERT INTO [t14242] VALUES (401,'السؤال (٣٩٢): إذا تعمد الإفطار، ثم جامع فهل عليه كفارة؟','89','388','0');
INSERT INTO [t14242] VALUES (402,'السؤال (٣٩٣): ما هي أحوال صيام عاشوراء؟','89','388','0');
INSERT INTO [t14242] VALUES (403,'السؤال (٣٩٤): قول الفقهاء: يحرم بلع النخامة ويفطر بها هل هذا صحيح؟','90','388','0');
INSERT INTO [t14242] VALUES (404,'السؤال (٣٩٥): قاس بعضهم على ذلك أنه لو بلع النخامة في الصلاة فإنه صلاته تبطل فهل هذا صحيح؟','90','388','0');
INSERT INTO [t14242] VALUES (405,'السؤال (٣٩٦): نقل عنكم أنكم تقولون: إن قوله صلى الله عليه وسلم: «ذهب الظمأ وابتلت العروق» لا يقوله إلا من حصل له الظمأ فعلا فهل هذا صحيح؟','90','388','0');
INSERT INTO [t14242] VALUES (406,'السؤال (٣٩٧): هل يجب على من عليه صيام أيام من رمضان وبقي مثلها من شعبان أن يقدر شعبان ناقصا؟','91','388','0');
INSERT INTO [t14242] VALUES (407,'السؤال (٣٩٨): هل يفطر المؤذن أولا ثم يؤذن أم العكس؟','91','388','0');
INSERT INTO [t14242] VALUES (408,'السؤال (٣٩٩): سألته رحمه الله عن قول شيخ الإسلام في الاختيارات "يتوجه جواز الصوم عن حي عاجز معسر "؟','91','388','0');
INSERT INTO [t14242] VALUES (409,'السؤال (٤٠٠): إذا لم يبق من شعبان إلا بقدر ما عليه من قضاء رمضان السابق فهل يجب عليه أن يقدر شعبان ناقصا لاحتمال نقصان الشهر فيبقى في ذمته يوم لم يصمه؟','91','388','0');
INSERT INTO [t14242] VALUES (410,'السؤال (٤٠١): لو أغمي على المكلف في شهر رمضان من قبل طلوع الفجر حتى غربت الشمس فهل يقضي أم لا؟','91','388','0');
INSERT INTO [t14242] VALUES (411,'السؤال (٤٠٢): سألته رحمه الله عن قول شيخ الإسلام إن من أفطر عمدا فلا يقضي مع أنه يصحح حديث " من استقاء عمدا فليقض " فهل فيه تناقض؟','92','388','0');
INSERT INTO [t14242] VALUES (412,'السؤال (٤٠٣): قول الفقهاء: إذا حجم أو احتجم وظهر دم، ما هو مقدار الدم؟','92','388','0');
INSERT INTO [t14242] VALUES (413,'السؤال (٤٠٤): إذا احتجم ولم يخرج إلا دم يسير هل يفطر؟','92','388','0');
INSERT INTO [t14242] VALUES (414,'السؤال (٤٠٥): شخص صائم وغابت الشمس إلا أنه كان على مكان مرتفع ويرى الشمس مع أن من تحته قد غابت عنهم فهل يفطر؟','92','388','0');
INSERT INTO [t14242] VALUES (415,'السؤال (٤٠٦): سألت ابن عثيمين عن حكم الترتيب في قضاء ما عليه من رمضان أو رمضانين؟','92','388','0');
INSERT INTO [t14242] VALUES (416,'السؤال (٤٠٧): سألت ابن عثمين عن قول الفقهاء: إن الريق إذا خرج بين الشفتين ثم بلعه الصائم أفطر؟','92','388','0');
INSERT INTO [t14242] VALUES (417,'الحج والعمرة','93','0','0');
INSERT INTO [t14242] VALUES (418,'السؤال (٤٠٨): هل تكفي نية الدخول في النسك؟','93','417','0');
INSERT INTO [t14242] VALUES (419,'السؤال (٤٠٩): ما حكم عقد الرداء بأن يجعل له مشبك أو زرار؟','93','417','0');
INSERT INTO [t14242] VALUES (420,'السؤال (٤١٠): ما حكم إذا قصد المحرم بما يحمله على رأسه التغطية؟','93','417','0');
INSERT INTO [t14242] VALUES (421,'السؤال (٤١١): ما حكم لو وضع المحرم يده على رأسه سواء قصد التغطية أم لا؟','93','417','0');
INSERT INTO [t14242] VALUES (422,'السؤال (٤١٢): ما حكم لو نام المحرم على مخدة يغوص فيها رأسه؟','93','417','0');
INSERT INTO [t14242] VALUES (423,'السؤال (٤١٣): ما هو ضابط الطيب الذي ينهى عنه المحرم؟','93','417','0');
INSERT INTO [t14242] VALUES (424,'السؤال (٤١٤): ما حكم من كان مقيما دون المواقيت ثم ذهب خارج المواقيت فهل يحرم من الميقات الذي يمر به أم يحرم من أهله؟','93','417','0');
INSERT INTO [t14242] VALUES (425,'السؤال (٤١٥): ما حكم السكن في المخيمات التابعة للدولة علما بأنه ليس تبعا لهم؟','94','417','0');
INSERT INTO [t14242] VALUES (426,'السؤال (٤١٦): إذا طاف اثنان وشك أحدهما كم شرطا طاف فهل يرجع إلى قوله صاحبه؟','94','417','0');
INSERT INTO [t14242] VALUES (427,'السؤال (٤١٧): ما حكم الرمي بالليل؟','94','417','0');
INSERT INTO [t14242] VALUES (428,'السؤال (٤١٨): كيف تقصر المرأة من شعرها؟','94','417','0');
INSERT INTO [t14242] VALUES (429,'السؤال (٤١٩): هل يرمي من ذهب مع الضعفاء؟','94','417','0');
INSERT INTO [t14242] VALUES (430,'السؤال (٤٢٠): ما حكم رمي جمرة العقبة في اليوم الحادي عشر قبل الزوال؟','94','417','0');
INSERT INTO [t14242] VALUES (431,'السؤال (٤٢١): ما الجواب عن قوله صلى الله عليه وسلم: «لا أحل حتى أنحر» فإنه ظاهر في تعلق النحر بالتحلل خلافا للفقهاء؟','95','417','0');
INSERT INTO [t14242] VALUES (432,'السؤال (٤٢٢): إذا ترك شوطا من السعي نسيانا وحلق رأسه، ثم تذكر فهل يسعى شوطا واحدا أم يعيد؟','95','417','0');
INSERT INTO [t14242] VALUES (433,'السؤال (٤٢٣): ما حكم الإتيان بعمرة ثانية في السفر الواحد إذا كانت لمتوفى لم يعتمر العمرة الواجبة؟','95','417','0');
INSERT INTO [t14242] VALUES (434,'السؤال (٤٢٤): من اعتمر في رمضان، ثم أراد أن يحج من عامه فهل الأفضل التمتع أم الإفراد لقول شيخ الإسلام: «إن أفرد العمرة بسفره، ثم أفرد الحج أنه أفضل من التمتع بالاتفاق»؟','95','417','0');
INSERT INTO [t14242] VALUES (435,'السؤال (٤٢٥): إذا خلع المحرم رداءه وفيه طيب فهل يجوز أن يرده بلا غسل بحجة أنه مستديم؟','96','417','0');
INSERT INTO [t14242] VALUES (436,'السؤال (٤٢٦): ما حكم الحج أو العمرة تطوعا عن الوالدين وهم أحياء؟','96','417','0');
INSERT INTO [t14242] VALUES (437,'السؤال (٤٢٧): في الصحيح يقول النبي صلى الله عليه وسلم لعبد الرحمن بن أبي بكر: «أخرج بأختك من الحرم فلتهل بعمرة ثم افرغا من طوافكما» ما المراد بالطواف هنا؟','96','417','0');
INSERT INTO [t14242] VALUES (438,'السؤال (٤٢٨): إذا تعدى الميقات ثم عاد فأحرم منه فهل يجزئ ذلك؟','96','417','0');
INSERT INTO [t14242] VALUES (439,'السؤال (٤٢٩): وسألته عن ترك الواجب في الإحرام وأنه كان يقول بأن تاركه ليس عليه دم؟','96','417','0');
INSERT INTO [t14242] VALUES (440,'السؤال (٤٣٠): إذا خافت المرأة نزول الحيض فهل لها أن تشترط عند إحرامها بحج أو عمرة؟','97','417','0');
INSERT INTO [t14242] VALUES (441,'السؤال (٤٣١): ما حكم إذا خالف ترتيب الرمي جاهلا؟','97','417','0');
INSERT INTO [t14242] VALUES (442,'السؤال (٤٣٢): هل قصر الصلاة في الحج قصر سفر أو نسك؟ وما حكم قصر أهل مكة؟','97','417','0');
INSERT INTO [t14242] VALUES (443,'السؤال (٤٣٣): ما حكم رمي جمرة العقبة من الخلف؟','97','417','0');
INSERT INTO [t14242] VALUES (444,'السؤال (٤٣٤): تعدى شخص الميقات ناويا العمرة فلما قيل له: ارجع إلى الميقات وإلا عليك دم فقال: إذا لا أعتمر، فما حكم ذلك؟ وهل هو بالخيار إما أن يرجع ولا يفدي أو يحرم من مكانه ويفدي؟','97','417','0');
INSERT INTO [t14242] VALUES (445,'السؤال (٤٣٥): إذا حج أو اعتمر عن مسلم حي عاجز أو ميت فهل يخص الدعاء للمستنيب أو يدعو النائب لنفسه؟','98','417','0');
INSERT INTO [t14242] VALUES (446,'السؤال (٤٣٦): إذا طاف ناس طواف الوداع ومعهم من لم يوادع فهل لهؤلاء الذين وادعوا أن يشتروا هدايا ووصايا؟','98','417','0');
INSERT INTO [t14242] VALUES (447,'السؤال (٤٣٧): إذا طيف بشخص محمول ولم يكن البيت عن يساره فما حكم طوافه؟','98','417','0');
INSERT INTO [t14242] VALUES (448,'السؤال (٤٣٨): هل يلزم أن تكون الكعبة عن يساره طيلة الطواف؟ وهل يضر الانحراف اليسير عنها؟','98','417','0');
INSERT INTO [t14242] VALUES (449,'السؤال (٤٣٩): من أتى بعمرة في أشهر الحج ومن نيته أن يحج وأراد الخروج إلى جدة بين العمرة والحج فهل يلزمه الوداع؟','98','417','0');
INSERT INTO [t14242] VALUES (450,'السؤال (٤٤٠): هل يجوز أن تزال الستارة عن الكعبة؟','99','417','0');
INSERT INTO [t14242] VALUES (451,'السؤال (٤٤١): ماذا على من قطع الطواف في أثنائه لعذر هل يتم ما بقي أم يستأنف؟','99','417','0');
INSERT INTO [t14242] VALUES (452,'السؤال (٤٤٢): هل يصح طواف من مر بالمسعى في طوافه؟','99','417','0');
INSERT INTO [t14242] VALUES (453,'السؤال (٤٤٣): إذا أتى شخص بعمرة ثم سافر إلى جدة ونحوها بنية الرجوع إلى مكة فهل يوادع؟','99','417','0');
INSERT INTO [t14242] VALUES (454,'السؤال (٤٤٤): إذا احرم بالعمرة قبل دخول شهر رمضان، ثم طاف وسعى في رمضان فهل يكون ذلك عمرة في رمضان؟','99','417','0');
INSERT INTO [t14242] VALUES (455,'السؤال (٤٤٥): فقلت له؛ فإن تعدى الميقات وهو حلال ومن نيته أن يرجع إلى الميقات فيحرم منه بعد دخول شهر رمضان فهل يجوز ذلك؟','100','417','0');
INSERT INTO [t14242] VALUES (456,'السؤال (٤٤٦): وسألته رحمه الله فيما لو اعتمر في أول ليلة من رمضان فهل يكون ذلك عمرة رمضان؟','100','417','0');
INSERT INTO [t14242] VALUES (457,'السؤال (٤٤٧): لو سعى في الدور الثاني بلا حاجة كزحمة فهل يجوز؟','100','417','0');
INSERT INTO [t14242] VALUES (458,'السؤال (٤٤٨): شخص طاف وسعى للعمرة ثم أدخل الحج قبل التقصير، وكذا لو قصر تقصيرا غير مجزئ ثم أحرم بالحج فما الحكم؟','100','417','0');
INSERT INTO [t14242] VALUES (459,'السؤال (٤٤٩): إذا اعتمر إنسان في أشهر الحج ثم سافر مسافة قصر ثم أراد الرجوع إلى مكة فهل يلزمه الإحرام من الميقات؟','100','417','0');
INSERT INTO [t14242] VALUES (460,'السؤال (٤٥٠): إذا أخر سعي الحج إلى ما بعد طواف الوداع فما الحكم؟','101','417','0');
INSERT INTO [t14242] VALUES (461,'السؤال (٤٥١): قرأت لكم في بعض فتاوى الحج فيمن طاف وسعى وقصر تقصيرا غير مجزئ ثم لبس ثيابه أنه يخلع ثيابه ثم يقصر التقصير المجزئ أو يحلق، وفي بعض الأجوبة قلتم: لكن إذا فات الأوان فعليه دم، فمتى يحصل الفوت؟','101','417','0');
INSERT INTO [t14242] VALUES (462,'السؤال (٤٥٢): إذا أحرم بالحج من الميقات ثم ذهب إلى المشاعر مباشرة ولم يطف طواف القدوم فهل يشرع له إذا تحلل التحلل الأول أن يرمل في طواف الإفاضة؟','101','417','0');
INSERT INTO [t14242] VALUES (463,'السؤال (٤٥٣): هل يجوز توكيل أحد لرمي الجمار وهو لم يحج تلك السنة؟','101','417','0');
INSERT INTO [t14242] VALUES (464,'السؤال (٤٥٤): هل يمكن توكيل أكثر من واحد لرمي الجمار؟','101','417','0');
INSERT INTO [t14242] VALUES (465,'السؤال (٤٥٥): وسئل رحمه الله وأنا أسمع، وكان ذلك في حج عام ١٤١٥هـ عن المكي لو حج هل عليه طواف وداع؟','102','417','0');
INSERT INTO [t14242] VALUES (466,'السؤال (٤٥٦): وسئل وأنا أسمع عن الحلق: هل يجب أن يكون في الحرم.','102','417','0');
INSERT INTO [t14242] VALUES (467,'السؤال (٤٥٧): وسئل وأنا أسمع عن حاج طاف للعمرة وسعى ولم يقصر التقصير الواجب وكذا فعل في الحج.','102','417','0');
INSERT INTO [t14242] VALUES (468,'السؤال (٤٥٨): وسمعته يقول: إن أخر طواف الإفاضة فجعله آخر شيء؟','102','417','0');
INSERT INTO [t14242] VALUES (469,'السؤال (٤٥٩): وسمعته يقول: لا يحرم على المحرم بحج أو عمرة أن يأخذ شيئا من بشرته.','102','417','0');
INSERT INTO [t14242] VALUES (470,'السؤال (٤٦٠): وقال فيمن ترك حصاة واحدة من جمرة العقبة وحلق وتحلل: إن بعض العلماء يتسامح في ذلك، ولكن الأحوط أن يعيد رمي الجمرة بسبع حصيات، وإن كان قد طاف وسعى فقد تحلل على قول بعض العلماء، وإن لم يطف فعليه أن يلبس إحرامه قبل الرمي.','103','417','0');
INSERT INTO [t14242] VALUES (471,'السؤال (٤٦١): وقال في ماء وضع فيه ماء ورد: لا يجوز للمحرم أن يشربه إن ظهر ريحه وكذا القهوة إذا وضع فيها زعفران.','103','417','0');
INSERT INTO [t14242] VALUES (472,'السؤال (٤٦٢): وقال في قارن طاف وسعى وقصر ناسيا: إن تقصيره هذا لم يرد به التحلل فهو على قرانه وليس على سعي آخر.','103','417','0');
INSERT INTO [t14242] VALUES (473,'السؤال (٤٦٣): وقال رحمه الله: ومن غلب على ظنه أن الحصى وقع في المرمى فإنه يجزئه، فإن شك بعد ذلك فلا بضر، وأما إذا شك وهو في مكانه فإنه يرمي حصاة بدل التي شك فيها.','103','417','0');
INSERT INTO [t14242] VALUES (474,'السؤال (٤٦٤): إذا حرم بثيابه فهل عليه على كل شيء فدية؟','103','417','0');
INSERT INTO [t14242] VALUES (475,'السؤال (٤٦٥): إذا اعتمر عن أبيه وحج عن أمه فهل يكون متمتعا وكذا لو أوصاه أجنبيان أحداهما- بعمرة والآخر بحج وعلى من يكون الدم؟','103','417','0');
INSERT INTO [t14242] VALUES (476,'السؤال (٤٦٦): سمعتكم في سؤال على الهاتف تقولون عن التقصير بالحج أو العمرة إنه لا يجزئ إلا تقصير واضح، فهل هذا صحيح؟','104','417','0');
INSERT INTO [t14242] VALUES (477,'السؤال (٤٦٧): هل يجب الحج على المعتدة وماذا لو شرعت في الحج ثم جاءها الخبر؟','104','417','0');
INSERT INTO [t14242] VALUES (478,'السؤال (٤٦٨): إذا أحرم الإنسان بالحج، ثم ذهب إلى المشاعر ولم يقدم مكة فلما تحلل في اليوم العاشر نزل مكة ليطوف طواف الإفاضة، فهل يشرع له الرمل في هذا الطواف؛ لأنه يصدق عليه أنه طواف أول ما يقدم؟','104','417','0');
INSERT INTO [t14242] VALUES (479,'السؤال (٤٦٩): إذا أتى بعمرة ثم أراد أن يذهب إلى جدة ويرجع إلى مكة فهل يوادع؟ وما حكم وداعه لو وادع؟','104','417','0');
INSERT INTO [t14242] VALUES (480,'السؤال (٤٧٠): وسألته رحمه الله عمن فعل ذلك بنية الرجوع إلا أنه لم يرجع، بل ذهب إلى بلده؟','104','417','0');
INSERT INTO [t14242] VALUES (481,'السؤال (٤٧١): سألتكم عمن أتى بعمرة ولم يبق عليه إلا الوداع وأراد الذهاب إلى جدة والرجوع إلى مكة أنه لا يوادع مع أنه خرج من الحرم؟','105','417','0');
INSERT INTO [t14242] VALUES (482,'السؤال (٤٧٢): جماعة طافوا وسعوا في العمرة ونسوا التقصير وذهبوا إلى جدة، ثم أحرموا بعمرة أخرى وطافوا وسعوا وقصروا، ثم علموا أنهم لم يقصروا للعمرة الأولى فما الحكم في ذلك؟','105','417','0');
INSERT INTO [t14242] VALUES (483,'السؤال (٤٧٣): وسألته عن شخص نوى أن يعتمر فلما تعدى الميقات ألغاها فهل عليه شيء؟','105','417','0');
INSERT INTO [t14242] VALUES (484,'السؤال (٤٧٤): إذا أنهى الإنسان مناسك العمرة ولم يبق إلا طواف الوداع وأراد السفر إلى جدة ومن نيته الرجوع إلى مكة فهل يوادع؛ لأنه سوف يخرج من الحرم؟','105','417','0');
INSERT INTO [t14242] VALUES (485,'السؤال (٤٧٥): ما حكم من نسي التقصير في العمرة، ثم ذكر أذكر هل يلزمه أن يخلع ثيابه؟','105','417','0');
INSERT INTO [t14242] VALUES (486,'السؤال (٤٧٦): طاف إنسان في العمرة من داخل الحجر وسعى وقصر فما حكم إذا أتى بعدها بعمرة صحيحة أو لم يأت بعمرة أخرى وطال الفصل؟','106','417','0');
INSERT INTO [t14242] VALUES (487,'السؤال (٤٧٧): سبق أن سألتكم عن شخص لما تعدى الميقات وهو مريد الحج أو العمرة وقيل له: عليك دم إلا إذا رجعت إلى الميقات فألغى العمرة، فقلتم: ليس عليه شيء، لكن لو كان هذا الشخص لم يحج حجة الإسلام أو لم يعتمر عمرة الإسلام فهل يلزمه الإحرام؟','106','417','0');
INSERT INTO [t14242] VALUES (488,'السؤال (٤٧٨): ما حكم من تعدى ميقات أهل نجد وهو من الرياض فأحرم من الطائف؟','106','417','0');
INSERT INTO [t14242] VALUES (489,'السؤال (٤٧٩): امرأة اكتملت فيها شروط الوجوب في الحج ومنها المحرم وهو أخوها مثلا إلا أن الزوج أبى أن يأذن لها فهل يشترط إذن الزوج؟','106','417','0');
INSERT INTO [t14242] VALUES (490,'السؤال (٤٨٠): ما حكم لو طاف طواف الوداع، ثم ذهب إلى عرفة ليمكث فيها حتى يسافر؟','106','417','0');
INSERT INTO [t14242] VALUES (491,'السؤال (٤٨١): لكن لو خرج إلى عرفة ولم يوادع فما الحكم؟','107','417','0');
INSERT INTO [t14242] VALUES (492,'السؤال (٤٨٢): ما حكم إذا أتى الإنسان بعمرة ولم يوادع ثم خرج خارج الميقات بنية الرجوع؟','107','417','0');
INSERT INTO [t14242] VALUES (493,'السؤال (٤٨٣): إذا اعتمر الإنسان ثم ذهب إلى الطائف وأراد الحج فهل يحرم بالحج من الطائف أو يؤخره إلى مكة؟','107','417','0');
INSERT INTO [t14242] VALUES (494,'السؤال (٤٨٤): لو اعتمر الإنسان وسافر خارج الميقات بنية الرجوع وأطال الإقامة هناك فهل فيه شيء؟','107','417','0');
INSERT INTO [t14242] VALUES (495,'السؤال (٤٨٥): ما حكم من لبى بالحج ثم فسخه إلى عمرة، ويعد أن اعتمر بدا له ألا يحج؟','107','417','0');
INSERT INTO [t14242] VALUES (496,'السؤال (٤٨٦): امرأة كانت تدف أمها بعربية حال الطواف فنامت الأم فما حكم طوافها؟','107','417','0');
INSERT INTO [t14242] VALUES (497,'السؤال (٤٨٧): أخر حاج طواف الإفاضة ونوى أن يكون عن الوداع ولم يكن سعى سعي الحج، فهل يلزمه طواف الوداع أو يكفي طواف الإفاضة؟','108','417','0');
INSERT INTO [t14242] VALUES (498,'السؤال (٤٨٨): ما حكم من أتى بعمرة في أشهر الحج ثم ذهب إلى المدينة مثلا وهو من أهل الرياض، ثم أراد الذهاب إلى مكة فهل يلزمه الإحرام إذا مر بميقات أهل المدينة؟','108','417','0');
INSERT INTO [t14242] VALUES (499,'السؤال (٤٨٩): لو أتى بعمرة وهو من أهل الرياض ثم سافر إلى الطائف أو المدينة فهل يجوز أن يأتي بعمرة ثانية؟','108','417','0');
INSERT INTO [t14242] VALUES (500,'السؤال (٤٩٠): سألت الشيخ ما حكم لو قدم الحاج سعي الحج على طواف الإفاضة في يوم العيد فما بعده ولم يطف بعده أي أنه سعى يوم العيد وطاف يوم الحادي عشر مثلا؟','108','417','0');
INSERT INTO [t14242] VALUES (501,'السؤال (٤٩١): سألته رحمه الله: لو وكلت المرأة من يرمي عنها وهي قادرة على الرمي بنفسها فما حكم ذلك مع بقاء وقت الرمي وما حكمه إذا انتهى وقت الرمي؟','109','417','0');
INSERT INTO [t14242] VALUES (502,'السؤال (٤٩٢):من أقام في العزيزية لعدم وجود مكان في منى فهل يصلي كما يصلي أهل منى في مكانه أو يؤمر بأن يصلي في المسجد لأنه يسمع النداء والمسجد قريب؟','109','417','0');
INSERT INTO [t14242] VALUES (503,'البيع والربا والصرف','110','0','0');
INSERT INTO [t14242] VALUES (504,'السؤال (٤٩٣): ما حكم أخذ العوض على الواسطة «الشفاعة»؟','110','503','0');
INSERT INTO [t14242] VALUES (505,'السؤال (٤٩٤): ما حكم إذا قال شخص لآخر: اختر ما شئت من السيارات مثلا ثم أشتريها لك وأقسطها عليك؟','110','503','0');
INSERT INTO [t14242] VALUES (506,'السؤال (٤٩٥): ما حكم صرف دراهم من الحلاق ونحوه؟','110','503','0');
INSERT INTO [t14242] VALUES (507,'السؤال (٤٩٦): ما حكم من أراد صرف [٥٠٠] ريال مثلا ولم يجد إلا [٤٠٠] ريال؟','110','503','0');
INSERT INTO [t14242] VALUES (508,'السؤال (٤٩٧): من نظام سيارة الأجرة «الليموزين» أن يأتي السائق يوميا بـ[٢٠٠] ريال وله راتب شهري [٢٠٠٠] ريال، وإذا لم يأت بالمطلوب خصم من راتبه فما حكم ذلك؟','110','503','0');
INSERT INTO [t14242] VALUES (509,'السؤال (٤٩٨): قول النبي صلى الله عليه وسلم في حديث ابن عمر رضي الله عنهما: «لا بأس إذا كان بسعر يومها» ما المراد باليوم في هذا الحديث؟','111','503','0');
INSERT INTO [t14242] VALUES (510,'السؤال (٤٩٩): ما حكم بيع السلعة في المكان نفسه إذا كان في السوق العام؟','111','503','0');
INSERT INTO [t14242] VALUES (511,'السؤال (٥٠٠): ما حكم بيع الشيء المحرم كالحرير على الكافر؟','111','503','0');
INSERT INTO [t14242] VALUES (512,'السؤال (٥٠١): ما حكم بيع الأرض على المخطط؟','112','503','0');
INSERT INTO [t14242] VALUES (513,'السؤال (٥٠٢): ما حكم بيع الرقم فإن بعضهم تخرج له منحة أرض من الدولة فيعطى رقم القطعة فهل يجوز ذلك؟','112','503','0');
INSERT INTO [t14242] VALUES (514,'السؤال (٥٠٣): وسألته رحمه الله عن رجل اشترى أرضا من صاحبها بسعر يومها وهو يعلم أن سعرها سوف يرتفع بعد فترة فما حكم ذلك؟','112','503','0');
INSERT INTO [t14242] VALUES (515,'السؤال (٥٠٤): سبق أن سألتكم عن أخذ العوض مقابل الشفاعة فهل أخذ الهدية مثل ذلك أو يجوز أخذها لدخولها في عموم قوله صلى الله عليه وسلم: «من صنع لكم معروفا فكافئوه»؟','112','503','0');
INSERT INTO [t14242] VALUES (516,'السؤال (٥٠٥): إذا قال البائع: اشتر واحدة، وخذ واحدة أو اشتر واحد وخذ هدية فهل هذا جائز؟','113','503','0');
INSERT INTO [t14242] VALUES (517,'السؤال (٥٠٦): وسألته عما وقع في إعلام الموقعين (٣/ ٥٠): «وأخذوا هم والناس بحديث ابن عمر أنه اشترى جملا شاردا بأصح سند يكون»؟','113','503','0');
INSERT INTO [t14242] VALUES (518,'السؤال (٥٠٧): من المعلوم أن الذهب القديم أرخص من الجديد فهل يجوز بيع ذهب قديم بذهب جديد مع الاتفاق في الوزن والاختلاف في القيمة؟','113','503','0');
INSERT INTO [t14242] VALUES (519,'السؤال (٥٠٨): ما الحكم في أخذ مبلغ من المال عند التوسط (الشفاعة) للغير لدى الآخرين؟','113','503','0');
INSERT INTO [t14242] VALUES (520,'السؤال (٥٠٩): ما حكم شراء الطيور المحنطة؟','114','503','0');
INSERT INTO [t14242] VALUES (521,'السؤال (٥١٠): لو ذكي الطير ذكاة شرعية ثم أعيد رأسه عليه فهل يجوز شراؤه، وكذا لو صيد الطير أو غيره ببندقية ثم حنط؟','114','503','0');
INSERT INTO [t14242] VALUES (522,'السؤال (٥١١): وسألته عن صناديق الاستثمار في البنوك وهي تزعم أنها تقوم بالمضاربة فهل يوثق بهم؟','114','503','0');
INSERT INTO [t14242] VALUES (523,'السؤال (٥١٢): إذا كان رصيدك في بنك الراجحي مثلا وسحبت مبلغا من صراف غير تابع لبنك الراجحي ففي هذه الحالة يأخذ البنك الذي يتبعه هذا الصراف من بنك الراجحي أربعة ريالات فهل في هذا شيء؟','114','503','0');
INSERT INTO [t14242] VALUES (524,'السؤال (٥١٣): إذا اضطر الإنسان إلى وضع دراهمه في البنك فهل يضعه في الراجحي؟','115','503','0');
INSERT INTO [t14242] VALUES (525,'السؤال (٥١٤): ما حكم بيع الهاتف الجوال؟','115','503','0');
INSERT INTO [t14242] VALUES (526,'السؤال (٥١٥): اشترى إنسان كتابا فوجده مكتوبا عليه «٠» فماذا يفعل؟','115','503','0');
INSERT INTO [t14242] VALUES (527,'السؤال (٥١٦): نقل عنكم أنكم وسعتم في مسألة تحويل العملة من بلد إلى آخر بدون قبض أو تحويلها كما هي إلى البلد الآخر فهل هذا صحيح؟','115','503','0');
INSERT INTO [t14242] VALUES (528,'السؤال (٥١٧): يقول شيخ الإسلام في مسألة التورق، إنه إذا اشترى السلعة بقصد التجارة فلا بأس، فما الفرق بينها وبين مسألة التورق المحرمة عنده؟','115','503','0');
INSERT INTO [t14242] VALUES (529,'السؤال (٥١٨): ما رأيكم في تجميع الصور التي مع بعض المشروبات أو المأكولات بغرض الفوز بجائزة؟','116','503','0');
INSERT INTO [t14242] VALUES (530,'السؤال (٥١٩): ذكرتم أن الأسواق التي تضع جوائز لمن اشترى منها وأسعارها سعر السوق بلا زيادة أن هذا جائز؛ لكن ما حكم قصد هذه الأسواق مع وجود أسواق أقرب منها لكن ليس فيها جوائز كهذه؟','116','503','0');
INSERT INTO [t14242] VALUES (531,'السؤال (٥٢٠): يعطي الموظف راتبه في شيك ويمتنع البنك من صرفه إلا بعد أيام فيذهب الموظف إلى بنك آخر ويعطيه الراتب حالا ويأخذ منه خمسين ريالا فما حكم ذلك؟','116','503','0');
INSERT INTO [t14242] VALUES (532,'السؤال (٥٢١): إذا وقع إنسان في مسألة العينة ثم جاء يسأل فما الحكم إذا كان العقد ما زال قائما أو بعد نهايته؟','116','503','0');
INSERT INTO [t14242] VALUES (533,'السؤال (٥٢٢): إذا قال من يريد أن يبني لآخر بيتا في العقد: نقوم ببناء بيت لك بمبلغ مليون ريال مثلا فإن سلمت المبلغ كاملا عند تسليم المفتاح فإنه يسقط عنك مثلا «مائتين ألف ريال» فما حكم هذه الصورة وهل هي داخلة في قولهم: «ضع وتعجل»؟','117','503','0');
INSERT INTO [t14242] VALUES (534,'السؤال (٥٢٣): إذا قال: بعت عليك هذه السلعة بعشرة حالة أو بعشرين مؤجلة فهل يصح هذا العقد؟','117','503','0');
INSERT INTO [t14242] VALUES (535,'السؤال (٥٢٤): ما حكم التأجير المنتهي بالتمليك؟','117','503','0');
INSERT INTO [t14242] VALUES (536,'السؤال (٥٢٥): اشترى خمرا ولم يدفع ثمنه ثم تاب فماذا يعمل؟','117','503','0');
INSERT INTO [t14242] VALUES (537,'السؤال (٥٢٦): يوجد بطاقة تصرف للبعض تمكنه من أن يشتري من بعض المحلات مجانا أو مقابل مبلغ سنوي؟','118','503','0');
INSERT INTO [t14242] VALUES (538,'السؤال (٥٢٧): إنسان عليه دين وذكر أنه إذا لم يأت بما عليه فإنه يحبس، وعرض عليه بعض البنوك بأنه يعطيه ما عليه من الدين لكن بفائدة فهل يجوز هذا للضرورة؟','118','503','0');
INSERT INTO [t14242] VALUES (539,'السؤال (٥٢٨): ما حكم شراء الذهب بالشيك المصدق؟','118','503','0');
INSERT INTO [t14242] VALUES (540,'السؤال (٥٢٩): وما حكم شراء الذهب ببطاقة الصرف الآلي حيث يكون عند بعض المحلات مكينة سحب فبمجرد إدخال البطاقة يخصم من حساب المشتري لكن لا تدخل في حساب البائع إلا بالمطالبة؟','118','503','0');
INSERT INTO [t14242] VALUES (541,'السؤال (٥٣٠): إذا وكل البائع شخصا وقال: بع هذه السلعة بمائة ريال مثلا، وما زاد فهو لك، فما حكم ذلك؟','119','503','0');
INSERT INTO [t14242] VALUES (542,'السؤال (٥٣١): وما حكم إذا سأل أحدهما عدم الخسارة؟','119','503','0');
INSERT INTO [t14242] VALUES (543,'السؤال (٥٣٢): أراد شخص أن يشتري ذهبا فنقص عليه المبلغ، فما حكم الآتي','119','503','0');
INSERT INTO [t14242] VALUES (544,'السؤال (٥٣٣): أ- إذا أقرضه صاحب المحل باقي المبلغ.','119','503','0');
INSERT INTO [t14242] VALUES (545,'السؤال (٥٣٤): ب- إذا أقرضه آخر باقي المبلغ؟','119','503','0');
INSERT INTO [t14242] VALUES (546,'السؤال (٥٣٥): في خارج المملكة إذا أراد الإنسان أن يسحب مبلغا بواسطة بطاقة الصراف فإنه الآلة تخرج المبلغ بالدولار مع أنه إنما أودع بالريال فما حكم ذلك؟','119','503','0');
INSERT INTO [t14242] VALUES (547,'السؤال (٥٣٦): ما رأيكم بالإيجار المنتهي بالتمليك؟','120','503','0');
INSERT INTO [t14242] VALUES (548,'السؤال (٥٣٧): إذا أراد أن يصرف ٥٠٠ ريال مثلا ولم يجد إلا ٤٠٠ ريال فهل يجوز أن يقول لصاحب الدكان: أعطني بالمائة مقاضي؟','120','503','0');
INSERT INTO [t14242] VALUES (549,'السؤال (٥٣٨): كان شيخنا ابن عثيمين يمنع ما يسمى ببيع التصريف ومنه بيع الجرايد والألبان ومشتقاته حيث تحاسب الشركة صاحب الدكان على ما تصرف منها وتأخذ الباقي ويرى أن الأسلم والأبرأ أن يجعله وكيلا له في البيع فكل جريدة يبيعها فله كذا فإن بقي شيء من الجرايد أخذه ويرى أن هذا توكيل جائز.','120','503','0');
INSERT INTO [t14242] VALUES (550,'السؤال (٥٣٩): ما حكم بيع أو إهداء الحيات والعقارب؟','120','503','0');
INSERT INTO [t14242] VALUES (551,'السؤال (٥٤٠): كان شيخنا ابن عثيمين رحمه الله يحرم ما يسمى بالبوفيه المفتوح لما فيه من الجهالة ثم توقف فيه ثم أجازه لأنه مما يتسامح فيه ولأنه دخل على بصيرة.','120','503','0');
INSERT INTO [t14242] VALUES (552,'السؤال (٥٤١):عادة تكون السلع التي عليها ضمان أكثر سعرا من التي بدون ضمان فسألت ابن عثيمين عن حكم شرائها فقال: لا يجوز بل يشتريها بسعر السوق بدون ضمان.','121','503','0');
INSERT INTO [t14242] VALUES (553,'السؤال (٥٤٢): ما حكم اشتراط بعض البنوك على من أراد أن يشتري منها سلعة أن يحول عليها الراتب لتستوفي حقها منه؟','121','503','0');
INSERT INTO [t14242] VALUES (554,'القرض','122','0','0');
INSERT INTO [t14242] VALUES (555,'السؤال (٥٤٣): إذا علمت من حال شخص أنه إذا اقترض قرضا ثم أعاده فإنه يهدي هدية فهل لي أن أقرضه؟','122','554','0');
INSERT INTO [t14242] VALUES (556,'السؤال (٥٤٤): بعض الناس يتعامل معاملة ربوية مع علمه بالتحريم، فمثلا يقترض مبلغا على أن يرده بزيادة ثم يقول، هذا حرام فلا أعطيك الزيادة فما حكم ذلك؟ وما الحكم إذا كان لا يعلم بالتحريم ثم علم؟','122','554','0');
INSERT INTO [t14242] VALUES (557,'السؤال (٥٤٥): إذا أقرضه بعيرا على أن يعطيه بعيرين فهل يجوز ذلك؟','122','554','0');
INSERT INTO [t14242] VALUES (558,'السؤال (٥٤٦): إنسان له دين على أبيه وأبوه يتعامل بالربا مع البنوك، فلما أخذ الفائدة (الربا) قضى دينه منها فهل يجوز للابن أخذ ذلك؟','123','554','0');
INSERT INTO [t14242] VALUES (559,'السؤال (٥٤٧): إذا أيس إنسان من استخراج دينه من شخص، فهل له أن يقول لآخر: استخرجه لي ولك منه كذا؟','123','554','0');
INSERT INTO [t14242] VALUES (560,'السؤال (٥٤٨): لكن لو اشتراه آخر بأقل فقال: بعني دينك الذي على فلان وهو [١٠٠٠] ريال مثلا بـ[٨٠٠] ريال وأنا آخذه منه فهل يجوز ذلك؟','123','554','0');
INSERT INTO [t14242] VALUES (561,'السؤال (٥٤٩): هل من مسالة الظفر إذا أعطى إنسان آخر عارية فأضاعها مفرطا، ثم إن صاحب العارية اقترض منه مبلغا يقابل قيمة العارية فهل له أن يجعل هذا المبلغ مقابل العارية.','123','554','0');
INSERT INTO [t14242] VALUES (562,'السؤال (٥٥٠): أقرض شخص شخصا مبلغا من المال فلما تأخر في التسديد نوى أن يبرئه من الدين فهل يبرأ بذلك ولا يجوز له الرجوع أم لابد من التلفظ بذلك؟','124','554','0');
INSERT INTO [t14242] VALUES (563,'السؤال (٥٥١): شخص له دين على شخص قدره مائة ريال بدون بينة وأنكر من عليه الدين ذلك، فقال له شخص آخر له دين قدره ألفان ريال على المنكر أيضا: سوف أقر له بدين] ٢٠٠٠ [ريال ثم أحيلك عليه فإن أعطاك الألفين خذ المائة التي لك وأعطني الباقي، فهل يجوز ذلك؟ وما هو ضابط الحيل الجائزة؟','124','554','0');
INSERT INTO [t14242] VALUES (564,'الرهن','125','0','0');
INSERT INTO [t14242] VALUES (565,'السؤال (٥٥٢): لو تراهن اثنان على إن فاز الفريق الفلاني فعليه عزيمة فهل يجوز ذلك؟','125','564','0');
INSERT INTO [t14242] VALUES (566,'السؤال (٥٥٣): وما حكم حضور هذه العزيمة؟','125','564','0');
INSERT INTO [t14242] VALUES (567,'السؤال (٥٥٤): لكن لو وضع عزيمة بلا رهان فهل يجوز؟','125','564','0');
INSERT INTO [t14242] VALUES (568,'الوكالة','126','0','0');
INSERT INTO [t14242] VALUES (569,'السؤال (٥٥٥): إذا وكلني شخص في شراء سلعة فلما اشتريتها أعطيت هدية إما بطلب مني أو بدون طلب فما الحكم؟','126','568','0');
INSERT INTO [t14242] VALUES (570,'السؤال (٥٥٦): إذا وكل الموكل الوكيل أن يبيع سلعة بعشرة فهل للوكيل أن يشتريها؟','126','568','0');
INSERT INTO [t14242] VALUES (571,'السؤال (٥٥٧): إذا عرض الوكيل السلعة في سوق من يزيد ثم وقفت على سعر معين فهل للوكيل أن يشتريها بما وقفت عليه؟','126','568','0');
INSERT INTO [t14242] VALUES (572,'الشركة','127','0','0');
INSERT INTO [t14242] VALUES (573,'السؤال (٥٥٨): سمعتكم في درس القراض من بلوغ المرام أنكم قلتم: إذا اتفق المضارب مع المضارب على جزء مشاع وظهر أنه حاباه فإنه يرجع إلى سهم المثل، فهل هذا صحيح؟','127','572','0');
INSERT INTO [t14242] VALUES (574,'السؤال (٥٥٩): سألته عن حكم شراء الشريك من مال الشركة كأن يشترك ثلاثة في شركة لبيع السيارات فهل يجوز لأحدهم أن يشتري سيارة لنفسه؟','127','572','0');
INSERT INTO [t14242] VALUES (575,'الإجارة','128','0','0');
INSERT INTO [t14242] VALUES (576,'السؤال (٥٦٠): ما حكم أخذ الصابون ونحوه من الفندق بحجة تحليل الأجرة؟','128','575','0');
INSERT INTO [t14242] VALUES (577,'السؤال (٥٦١): إذا طلب صاحب الأجرة أكثر من العادة وأبى أن يأخذ الأجرة المتعارف عليها فما الحكم؟','128','575','0');
INSERT INTO [t14242] VALUES (578,'السؤال (٥٦٢): ما حكم ما يسمى بنقل القدم؟','128','575','0');
INSERT INTO [t14242] VALUES (579,'السؤال (٥٦٣): ما حكم تأجير البيت على الكافر في الجزيرة العربية؟','128','575','0');
INSERT INTO [t14242] VALUES (580,'السؤال (٥٦٤): دفع شخص لصاحب محل ذهب حليا له ليصلحه فضيعه فما الحكم؟','128','575','0');
INSERT INTO [t14242] VALUES (581,'السؤال (٥٦٥): هل تلزم الصبرة الوارث أم هو مخير؟','129','575','0');
INSERT INTO [t14242] VALUES (582,'السؤال (٥٦٦): ما معنى قول الفقهاء: «من صور المنفعة ممر في دار»؟','129','575','0');
INSERT INTO [t14242] VALUES (583,'السؤال (٥٦٧): تضع بعض الفنادق داخل الغرفة ثلاجة فيها مشروبات ولكن بسعر مرتفع فهل يجوز أن أستهلك منها ما أريد ثم أحضر مثلها من السوق؟','129','575','0');
INSERT INTO [t14242] VALUES (584,'السؤال (٥٦٨): أجر شخص بيته فوضع المستأجر دشا فهل له أن يخرجه؟','129','575','0');
INSERT INTO [t14242] VALUES (585,'السؤال (٥٦٩): فيه موظف يعقب على مستحقات له ولزملائه كخارج الدوام مثلا، ويقوم باستلام كامل المبلغ وتوزيعه على زملائه ويأخذ من كل موظف عشرة ريالات مقابل ذلك فهل يجوز له ذلك؟','129','575','0');
INSERT INTO [t14242] VALUES (586,'السؤال (٥٧٠): ما حكم دفع ثمن العربية التي تكون للعاجزين عن السعي بين الصفا والمروة وكذا لو احتاج إلى صرف؟','129','575','0');
INSERT INTO [t14242] VALUES (587,'السؤال (٥٧١): تشترط كثير من المغاسل أنها غير مسؤولة عن الثياب التي يتخلف عنها أصحابها فهل لصاحب المغسلة التصرف في هذه الملابس ببيع ونحوه؟','130','575','0');
INSERT INTO [t14242] VALUES (588,'السؤال (٥٧٢): ما حكم لو قال شخص لآخر: أعطني مبلغا من المال وأطبق أرضك في مكان جيد؟','130','575','0');
INSERT INTO [t14242] VALUES (589,'اللقطة','131','0','0');
INSERT INTO [t14242] VALUES (590,'السؤال (٥٧٣): إذا وجد شخص لقطة حول مسجد فهل يكفي أن يضع لافتة من خارج المسجد ويكون ذلك تعريفا؟','131','589','0');
INSERT INTO [t14242] VALUES (591,'السؤال (٥٧٤): ماذا أفعل إذا وجدت ساعة أو مفاتيح في المسجد؟','131','589','0');
INSERT INTO [t14242] VALUES (592,'السؤال (٥٧٥): إذا ركب مع صاحب سيارة الأجرة أناس من جدة إلى مكة مثلا فوجد صاحب الأجرة في سيارته لقطة فماذا يفعل بها؟','131','589','0');
INSERT INTO [t14242] VALUES (593,'الوقف','132','0','0');
INSERT INTO [t14242] VALUES (594,'السؤال (٥٧٦): ما حكم نقل المصاحف من مسجد إلى آخر بحجة احتياج الآخر لذلك واستغناء الأول؟','132','593','0');
INSERT INTO [t14242] VALUES (595,'السؤال (٥٧٧): هل يجوز الوقف على الأولاد؟','132','593','0');
INSERT INTO [t14242] VALUES (596,'السؤال (٥٧٨): إذا أوقف رجل بخورا على المسجد، فهل يجوز للجماعة أن يتبخروا منه؟','132','593','0');
INSERT INTO [t14242] VALUES (597,'الهبة والعطية','133','0','0');
INSERT INTO [t14242] VALUES (598,'السؤال (٥٧٩): إذا أهدي للإنسان شيء غالي الثمن فهل يلبسه أو يبيعه لئلا يتهم بالإسراف؟','133','597','0');
INSERT INTO [t14242] VALUES (599,'السؤال (٥٨٠): هل يعدل بين الأطفال الذكور والإناث حتى في الأشياء الصغيرة كالحلوى مثلا؟','133','597','0');
INSERT INTO [t14242] VALUES (600,'السؤال (٥٨١): هل يجب العدل بين الأولاد في شراء الأشياء مع أنه هو الذي اختارها لنفسه؟','133','597','0');
INSERT INTO [t14242] VALUES (601,'السؤال (٥٨٢): ما حكم قبول هدية البنوك، وإذا كان على الهدية اسم البنك فما حكمه، وإذا كانت الهدية تقويما بأوقات الصلاة، هل يجوز إدخاله المسجد؟','133','597','0');
INSERT INTO [t14242] VALUES (602,'السؤال (٥٨٣): أهديت لبعض الإخوان كتابا وبعد مدة اشترى هذا الأخ نفس الكتاب ثم أعاد علي الكتاب الذي أهديته كهدية منه لي فهل أقبل هذه الهدية؟','133','597','0');
INSERT INTO [t14242] VALUES (603,'السؤال (٥٨٤): في الموطأ بسند صحيح أن أبا بكر الصديق رضي الله عنه نحل عائشة رضي الله عنها نحلة دون إخوتها، فلما حضرته الوفاة قال لها: لو كنت حزتيه لكان لك، أما الآن فقد وقع فيه الميراث، والفقهاء استدلوا به على جواز الرجوع بالهبة قبل قبضها لكن فيه إشكال، وهو تفضيل بعض الأولاد على بعض، فما هو الجواب؟','134','597','0');
INSERT INTO [t14242] VALUES (604,'السؤال (٥٨٥): ساهم شخص هو وأمه في أراض فربحت ثم ساهما مرة أخرى برأس المال السابق مع ربحه في أرض أخرى ولم تبع الأرض، فأرادت الأم من ولدها أن يشتري منها حصتها من المساهمة؛ لأنها بحاجة إلى المال، فهل يجوز ذلك؟','134','597','0');
INSERT INTO [t14242] VALUES (605,'السؤال (٥٨٦): إذا كان للوالد مكافأة شهرية مثلا فهل للوالد أن يأكلها مع غناه عنها؟','134','597','0');
INSERT INTO [t14242] VALUES (606,'الوصايا','135','0','0');
INSERT INTO [t14242] VALUES (607,'السؤال (٥٨٧): وسألته رحمه الله: عن امرأة ذات مال طلبت مني أن أكتب لها وصية؟','135','606','0');
INSERT INTO [t14242] VALUES (608,'السؤال (٥٨٨): أوصى شخص بالثلث في شراء عقار وحال على هذا المبلغ حول كامل بحجة غلاء العقار، فهل عليه زكاة، وما حكم هذا التأخير؟','135','606','0');
INSERT INTO [t14242] VALUES (609,'السؤال (٥٨٩): إذا شهد شاهد في وصية وهو لا يعرف الموصي فهل في هذا بأس؟','135','606','0');
INSERT INTO [t14242] VALUES (610,'السؤال (٥٩٠): ما حكم إذا أوصى الوارث بأضحية كأن يقول: يضحي عني وعن زوجتي؟','135','606','0');
INSERT INTO [t14242] VALUES (611,'المواريث','136','0','0');
INSERT INTO [t14242] VALUES (612,'السؤال (٥٩١): هل يشترك الورثة في أثاث البيت ونحوه؟','136','611','0');
INSERT INTO [t14242] VALUES (613,'السؤال (٥٩٢): إذا أخذ شخص مالا غصبا ثم مات صاحب المال ولم يكن له وارث سوى هذا الغاصب فما الحكم؟','136','611','0');
INSERT INTO [t14242] VALUES (614,'العتق','137','0','0');
INSERT INTO [t14242] VALUES (615,'السؤال (٥٩٣): أصل الرق الكفر فهل يعتق إذا أسلم؟','137','614','0');
INSERT INTO [t14242] VALUES (616,'النكاح وما يتعلق به','138','0','0');
INSERT INTO [t14242] VALUES (617,'السؤال (٥٩٤): ما حكم طلاق النفساء؟','138','616','0');
INSERT INTO [t14242] VALUES (618,'السؤال (٥٩٥): ما حكم الخطبة على خطبة أخيه إذا كان الخاطب الثاني مستقيما؟','138','616','0');
INSERT INTO [t14242] VALUES (619,'السؤال (٥٩٦): فيه عاده عند بعض الناس، وهي أن الزوج يرى أقاربه من النساء دم بكارة زوجته فما حكم ذلك؟','138','616','0');
INSERT INTO [t14242] VALUES (620,'السؤال (٥٩٧): ما حكم إجابة الدعوة؟','138','616','0');
INSERT INTO [t14242] VALUES (621,'السؤال (٥٩٨): إذا كان الداعي قد دعاك مجاملة فما حكم إجابة دعوته؟','138','616','0');
INSERT INTO [t14242] VALUES (622,'السؤال (٥٩٩): هل يكفي لمن دعي لوليمة العرس أن يسلم على الداعي ثم يذهب؟','138','616','0');
INSERT INTO [t14242] VALUES (623,'السؤال (٦٠٠): أهل البنت المخطوبة كالأخوات مثلا إذا جاء الخاطب يرينه فما حكم ذلك؟','139','616','0');
INSERT INTO [t14242] VALUES (624,'السؤال (٦٠١): ما حكم إعطاء الخاطب صورة المخطوبة؟','139','616','0');
INSERT INTO [t14242] VALUES (625,'السؤال (٦٠٢): ما حكم اشتراط الزوج بعض راتب زوجته؟','139','616','0');
INSERT INTO [t14242] VALUES (626,'السؤال (٦٠٣): ما حكم من يتزوج ثيبا ثم يسافر بها ويغيب أكثر من ثلاثة أيام مع أن له زوجات أخريات فهل يجوز له ذلك؟','139','616','0');
INSERT INTO [t14242] VALUES (627,'السؤال (٦٠٤): ما حكم العزل؟ ونقل عنكم - حفظكم الله - أن اللولب أضر من حبوب منع الحمل فهل هذا صحيح؟','139','616','0');
INSERT INTO [t14242] VALUES (628,'السؤال (٦٠٥): ما حكم استعمال الدف فيما يسمى بالتجوال مع بعده عن يوم العرس؟','140','616','0');
INSERT INTO [t14242] VALUES (629,'السؤال (٦٠٦): هل يجوز للمرأة المحادة أن تحضر وليمة ولدها الذي يسكن في الدور العلوي في نفس البيت التي هي فيه مع أنه ليس بينهما تلك الرسميات كما هو معلوم؟','140','616','0');
INSERT INTO [t14242] VALUES (630,'السؤال (٦٠٧): هل يجوز للابن أن يأخذ ما يكفيه، من أبيه إذا لم ينفق عليه؟','140','616','0');
INSERT INTO [t14242] VALUES (631,'السؤال (٦٠٨): إذا رأى إنسان امرأة ليخطبها ثم تركها ثم جاء آخر ليخطبها فسأل الخاطب الثاني الخاطب الأول عن أسباب تركه لها فهل يجوز له ذكر الأسباب؟','140','616','0');
INSERT INTO [t14242] VALUES (632,'السؤال (٦٠٩): هل يجوز للمرأة التي تريد أن تخالع زوجها لضربه إياها وسوء خلقه أن تسافر بغير إذنه وهي لم تخالع بعد؟','140','616','0');
INSERT INTO [t14242] VALUES (633,'السؤال (٦١٠): إذا راجع زوجته المطلقة في العدة ولم يشهد فهل تصح الرجعة؟','141','616','0');
INSERT INTO [t14242] VALUES (634,'السؤال (٦١١): إذا هجر زوجته فهل يسلم عليها؟','141','616','0');
INSERT INTO [t14242] VALUES (635,'السؤال (٦١٢): يقوم بعض عاقدي الأنكحة بوضع يد ولي الزوجة مع الزوج كالتصافح فما حكم ذلك؟','141','616','0');
INSERT INTO [t14242] VALUES (636,'السؤال (٦١٣): قول بعض العلماء: «إن إضمار نية الطلاق كإضمار نية التحليل» هل يعني أن هذا إجماع منهم في أن نية التحليل مؤثرة؟','141','616','0');
INSERT INTO [t14242] VALUES (637,'السؤال (٦١٤): ما حكم نية التحليل إذا كانت من المرأة؟','141','616','0');
INSERT INTO [t14242] VALUES (638,'السؤال (٦١٥): طلق رجل امرأته ثم إنه راجعها في العدة ولكنها أبت الرجوع وبعد سبعة أشهر طلقها طلقة ثانية ولم يخبرها فهل يجب عليه إخبارها؟','141','616','0');
INSERT INTO [t14242] VALUES (639,'السؤال (٦١٦): ما حكم لو شرطت المرأة أن تكن العصمة بيدها عند عقد النكاح؟','142','616','0');
INSERT INTO [t14242] VALUES (640,'السؤال (٦١٧): يقع كثير من العامة النكاح بلا شهود ويأتي منها بأولاد ويشتهر أن هذه المرأة زوجة فلان فهل يلزم تجديد النكاح؟','142','616','0');
INSERT INTO [t14242] VALUES (641,'السؤال (٦١٨): هل يلزم إخبار الزوجة بالطلاق؟','142','616','0');
INSERT INTO [t14242] VALUES (642,'السؤال (٦١٩): رجل متزوج بامرأة ولما خطب امرأة أخرى شرطوا عليه خادمة فهل يلزمه أن يوفر للأولى خادمة من باب العدل؟','142','616','0');
INSERT INTO [t14242] VALUES (643,'السؤال (٦٢٠): ما رأيكم بما يسمونه بالمسيار؟','143','616','0');
INSERT INTO [t14242] VALUES (644,'السؤال (٦٢١): ذكر شيخ الإسلام في الفتاوى (٣٢/ ١٨) وكذا ابن القيم أن عقد النكاح في المسجد مستحب وسمعتكم تقولون: إنه بدعة، فما رأي سماحتكم؟','143','616','0');
INSERT INTO [t14242] VALUES (645,'السؤال (٦٢٢): وسألته رحمه الله عن قول شيخ الإسلام في رجل طلق زوجته ثلاثا قبل الدخول، فهو كما لو طلقها بعد الدخول عند الأئمة الأربعة لا تحل له حتى تنكح وزجا غيره ويدخل بها، فإذا طلقها قبل الدخول لم تحل للأول (الفتاوى ٣٢/ ٨٠).','143','616','0');
INSERT INTO [t14242] VALUES (646,'السؤال (٦٢٣): وسألته عن قول شيخ الإسلام في الفتاوى (٣٢/ ٨٩): إن الله حرم الطلاق في مواضع باتفاق العلماء، كما إذا طلقها في الحيض ولم تكن سألته الطلاق فإن هذا الطلاق حرام باتفاق العلماء، هل قول الشيخ؛ ولم تكن سألته الطلاق، قيد؟','144','616','0');
INSERT INTO [t14242] VALUES (647,'السؤال (٦٢٤): وسألته عن قول شيخ الإسلام في الفتاوى (٣٢/ ٩٥): إن المرأة المعتدة لا يحل لغير زوجها أن يصرح بخطبتها، كيف يقال: يصرح بخطبتها وهي زوجته؟','144','616','0');
INSERT INTO [t14242] VALUES (648,'السؤال (٦٢٥): وسألته رحمه الله: عن قول الشيخ في الفتاوى (٣٢/ ١٤٨): " فالعدة والردة والإحرام تمنع ابتداء النكاح دون دوامه" سألته عن الردة؟','144','616','0');
INSERT INTO [t14242] VALUES (649,'السؤال (٦٢٦): لماذا لا يقع الطلاق في الحيض ويقع في النفاس؟','144','616','0');
INSERT INTO [t14242] VALUES (650,'السؤال (٦٢٧): إذا قال الزوج لزوجته: سوف أرسل ورقة طلاقك، فهل يقع بذلك طلاق؟','145','616','0');
INSERT INTO [t14242] VALUES (651,'السؤال (٦٢٨): ما حكم الأجرة التي تأخذها الطقاقات؟','145','616','0');
INSERT INTO [t14242] VALUES (652,'السؤال (٦٢٩): رجل عنده زوجتان إحداهما معلمة، فهل يجب أن ينفق عليها كما ينفق على المعدمة؟','145','616','0');
INSERT INTO [t14242] VALUES (653,'السؤال (٦٣٠): قول الرسول صلى الله عليه وسلم: " لعنتها الملائكة حتى تصبح" فهل إذا امتنعت من الجماع في النهار نقول: حتى تمسي أم لا؟','145','616','0');
INSERT INTO [t14242] VALUES (654,'السؤال (٦٣١): إذا قال لزوجته إذا دخل شهر رمضان فأنت طالق وصادف دخول رمضان وهي حائض فهل يقع الطلاق؟','145','616','0');
INSERT INTO [t14242] VALUES (655,'السؤال (٦٣٢): في مسألة رضاع الكبير لماذا لا نقول إنها تجوز إذا كان الإنسان قد تربى عند هذه العائلة من صغره حتى كبر فنقول للمرأة حينئذ أرضعيه تحرمي عليه؟','146','616','0');
INSERT INTO [t14242] VALUES (656,'السؤال (٦٣٣): إذا جومعت المرأة في نهار رمضان وهي مطاوعة ثم حاضت أو نفست آخر النهار فهل عليها كفارة؟','146','616','0');
INSERT INTO [t14242] VALUES (657,'السؤال (٦٣٤): وسألته عن رجل جامع زوجته ثم كفر ثم جامع مرة ثانية في نفس اليوم فهل عليه كفارة؟','146','616','0');
INSERT INTO [t14242] VALUES (658,'السؤال (٦٣٥): سألت ابن عثيمين: هل يجوز للمرأة المحادة أن تبخر بيتها؟','146','616','0');
INSERT INTO [t14242] VALUES (659,'النفقات','147','0','0');
INSERT INTO [t14242] VALUES (660,'السؤال (٦٣٦): ما هو ضابط وجوب النفقة على القريب؟','147','659','0');
INSERT INTO [t14242] VALUES (661,'الجنايات والحدود','148','0','0');
INSERT INTO [t14242] VALUES (662,'السؤال (٦٣٧) إذا وقع حادث وقرر المرور أن الخطأ ١٠٠% على المقابل وقد مات شخصان فهل عليه شيء؟','148','661','0');
INSERT INTO [t14242] VALUES (663,'السؤال (٦٣٨): ما رأيكم في حكم عمر رضي الله عليه في عدم قطع يد السارق عام الرمادة؟','148','661','0');
INSERT INTO [t14242] VALUES (664,'السؤال (٦٣٩) وسألته رحمه الله عن قول الفقهاء: " يجوز لمن عليه القود التخلف عن الجمعة والجماعة إن رجا العفو"؟','148','661','0');
INSERT INTO [t14242] VALUES (665,'السؤال (٦٤٠): قيل لبعضهم: يا ولد فلانة فغضب فهل غضبه في محله؟','149','661','0');
INSERT INTO [t14242] VALUES (666,'السؤال (٦٤١): هل يجوز للإنسان أن يقع في ذهنه أن المغيرة بن شعبة رضي الله عنه قد خلا بامرأة وفعل معها ما لا يجوز إلا أنه لا يعتقد أنه زنى بها؛ لأن الشهود قد اتفقوا على هذا القدر واختلفوا في الإيلاج؟','149','661','0');
INSERT INTO [t14242] VALUES (667,'السؤال (٦٤٢): قال ابن القيم رحمه الله في كتابه" الجواب الكافي": " لا يجتمع حد وكفارة. فقال شيخنا عبد العزيز بن باز رحمه الله: إلا إذا زنى في رمضان فإن عليه الحد والكفارة". فهل هذه الصورة داخلة في كلام ابن القيم أو لم يردها أصلا؟','149','661','0');
INSERT INTO [t14242] VALUES (668,'الأطعمة والعقيقة والأضحية','150','0','0');
INSERT INTO [t14242] VALUES (669,'السؤال (٦٤٣): ما حكم شرب الدم (دم الإنسان)؟','150','668','0');
INSERT INTO [t14242] VALUES (670,'السؤال (٦٤٤): هل الدم المسفوح هو الذي يخرج منها حال الذبح فقط؟','150','668','0');
INSERT INTO [t14242] VALUES (671,'السؤال (٦٤٥): ما حكم الإيتار في الأكل؟','150','668','0');
INSERT INTO [t14242] VALUES (672,'السؤال (٦٤٦): نوى إنسان أن يعق عن ولده فلما قدم له قريب أو شفي له مريض كان ذلك كالدافع لفعل هذه العقيقة فما حكم ذلك؟','150','668','0');
INSERT INTO [t14242] VALUES (673,'السؤال (٦٤٧): ما حكم إذا عق الإنسان بأكثر من المشروع كأن عق بأربع؟','150','668','0');
INSERT INTO [t14242] VALUES (674,'السؤال (٦٤٨): ما حكم مشط الشعر لمن أراد أن يضحي؟','151','668','0');
INSERT INTO [t14242] VALUES (675,'السؤال (٦٤٩): لو فعل أحدنا بفرسه كما فعل سليمان عليه الصلاة والسلام بفرسه فهل تكون ميتة؟','151','668','0');
INSERT INTO [t14242] VALUES (676,'السؤال (٦٥٠): ما حكم تحنية الأضاحي؟','151','668','0');
INSERT INTO [t14242] VALUES (677,'السؤال (٦٥١): هل يجزي التيس في العقيقة؟','151','668','0');
INSERT INTO [t14242] VALUES (678,'السؤال (٦٥٢): ما حكم شرب البيرة؟','151','668','0');
INSERT INTO [t14242] VALUES (679,'السؤال (٦٥٣): ما حكم أكل دود التين والفول والسوس ونحو ذلك؟','151','668','0');
INSERT INTO [t14242] VALUES (680,'السؤال (٦٥٤): ما حكم التمشيط إذا كان يؤدي إلى تساقط الشعر الذي لولا التمشيط ما سقط؟','151','668','0');
INSERT INTO [t14242] VALUES (681,'السؤال (٦٥٥): إذا رمي الطريدة بحجر فأصابها مع رأسها فماتت لم ينهر الدم فهل تكون وقيدة أم تحل؟','152','668','0');
INSERT INTO [t14242] VALUES (682,'السؤال (٦٥٦): ما حكم دوس التمر؟','152','668','0');
INSERT INTO [t14242] VALUES (683,'السؤال (٦٥٧): وسألته عن قوله رحمه الله: إنه ليس في الشريعة حيوان بعضه حلال وبعضه حرام فما الجواب عن تحريم الدم المسفوح؟','152','668','0');
INSERT INTO [t14242] VALUES (684,'السؤال (٦٥٨): إذا فات ذبح العقيقة يوم السابع، فهل نقول: إن اليوم الثامن أولى من الرابع عشر لقربه من السابع؟','152','668','0');
INSERT INTO [t14242] VALUES (685,'السؤال (٦٥٩): ما حكم وضع النعمة (الطعام) في الحمام؟','152','668','0');
INSERT INTO [t14242] VALUES (686,'السؤال (٦٦٠): وسئل- وأنا أسمع- عن أضحية تعيبت بعد تعيينها فقال تجزئ.','153','668','0');
INSERT INTO [t14242] VALUES (687,'السؤال (٦٦١): إذا سافر إنسان وترك زوجته عند أهلها فهل يوكلهم في ذبح الأضحية عنه؟','153','668','0');
INSERT INTO [t14242] VALUES (688,'السؤال (٦٦٢): إذا كان له أكثر من امرأة وكل امرأة في بيت مستقل فهل يضحي لكل واحدة أضحية أو تكفي واحدة؟','153','668','0');
INSERT INTO [t14242] VALUES (689,'السؤال (٦٦٣): إذا قلنا: إن الأضحية غير مشروعة للحاج فهل يضحي المفرد؟','153','668','0');
INSERT INTO [t14242] VALUES (690,'السؤال (٦٦٤): وسألته عن حكم ما يسمى عند العامة "قريش" وهو الاجتماع في أخر شعبان على عشاء ونحوه؟','153','668','0');
INSERT INTO [t14242] VALUES (691,'السؤال (٦٦٥): ما حكم عزيمة التلميذ لمعلمه والموظف لمديره هل هي من الرشوة وداخلة في حديث "هدايا العمال غلول"؟','154','668','0');
INSERT INTO [t14242] VALUES (692,'السؤال (٦٦٦): إذا أهدى شخص ثمن أضحية لآخر فهل يمتنع المهدى من أخذ الشعر ونحوه؟','154','668','0');
INSERT INTO [t14242] VALUES (693,'السؤال (٦٦٧): ضحى شخص بعدة أضاحي فهل يجوز الأخذ من شعره وظفره بذبح الأول أم لابد من ذبح الجميع؟','154','668','0');
INSERT INTO [t14242] VALUES (694,'السؤال (٦٦٨): ما حكم التسمية ب " إيمان وملاك وهدى"؟','155','668','0');
INSERT INTO [t14242] VALUES (695,'الأيمان والنذور','156','0','0');
INSERT INTO [t14242] VALUES (696,'السؤال (٦٦٩): رجل صاحب أسرة عددهم تسعة وهو عاشرهم وهو فقير، فهل يعطى كفارة يمين هو وأفراد أسرته علما أن من أفراد أسرته من يرضع؟','156','695','0');
INSERT INTO [t14242] VALUES (697,'السؤال (٦٧٠): ما حكم قول الإنسان: «عهد علي أن أصلي لله كذا»؟','156','695','0');
INSERT INTO [t14242] VALUES (698,'السؤال (٦٧١): إذا حلف إنسان على آخر فقال له مثلا: «والله لتأكلن» على وجه الإكرام لا الإلزام فهل عليه كفارة إذا لم يأكل؟','156','695','0');
INSERT INTO [t14242] VALUES (699,'السؤال (٦٧٢): قال شخص: إن لم أفعل كذا فعلي صيام ثلاثة أيام، فهذا يخير بين صيام ثلاثة أيام أو يكفر عن يمينه، والسؤال- حفظكم الله-: إذا صام ثلاثة أيام بينة كفارة اليمين مع قدرته على الإطعام فهل يجزئه عن النذر؟','156','695','0');
INSERT INTO [t14242] VALUES (700,'السؤال (٦٧٣): ما حكم قوله «أشهد بالله»؟','156','695','0');
INSERT INTO [t14242] VALUES (701,'السؤال (٦٧٤): ما الجواب عن قوله صلى الله عليه وسلم: «أفلح وأبيه إن صدق»؟','157','695','0');
INSERT INTO [t14242] VALUES (702,'السؤال (٦٧٥): ما حكم إذا تعددت الأيمان والمحلوف عليه ثم إنه فعل واحدا مما حلف عليه، وكفر عنه وأراد بالكفارة الكفارة عنه وتحلة عن الباقي التي لم يحنث فيها، فهل يجزئه ذلك؟','157','695','0');
INSERT INTO [t14242] VALUES (703,'السؤال (٦٧٦): إذا أراد بحلفه إكرام الغير فهل يحنث بذلك؟','157','695','0');
INSERT INTO [t14242] VALUES (704,'السؤال (٦٧٧): إذا حلف أن يصلي فهل يجب عليه ذلك؟','157','695','0');
INSERT INTO [t14242] VALUES (705,'السؤال (٦٧٨): نذرت امرأة أن تصوم كل يوم اثنين ثم أنها لم تصم فماذا عليها؟','158','695','0');
INSERT INTO [t14242] VALUES (706,'السؤال (٦٧٩): إذا حلف يمينا واحدة على أفعال متعددة كأن قال: والله لا أشرب ولا آكل ولا أزور فلانا، ثم أنه شرب وأكل وزار فلانا فهل عليه كفارة واحدة أو ثلاث، وهل إذا فعل واحدا منها ثم كفر عنه تنحل يمينه؟','158','695','0');
INSERT INTO [t14242] VALUES (707,'السؤال (٦٨٠): إنسان عاهد الله ألا يعود في معصية معينة واستحضر آية التوبة {ومنهم من عاهد الله لئن آتانا من فضله} الآية، ثم أنه رجع إلى المعصية فما حكم هذه المعاهدة؟','158','695','0');
INSERT INTO [t14242] VALUES (708,'السؤال (٦٨١): إذا المعاهدة والوعد لهما حكم النذر؟','158','695','0');
INSERT INTO [t14242] VALUES (709,'السؤال (٦٨٢): نذر إنسان أن يصلي كل يوم ركعتين أو نذر أن يصوم يوما ويفطر يوما فهذا نذر طاعة يجب الوفاء به، فما الحكم لو ترك بعض الأيام فلم يصل أو لم يصم؟','159','695','0');
INSERT INTO [t14242] VALUES (710,'السؤال (٦٨٣): سألت شيخنا رحمه الله عن رجل نذر صلاة أو اعتكافا ثم مات ولم يف بالنذر وكان مفرطا في ذلك؟','159','695','0');
INSERT INTO [t14242] VALUES (711,'السؤال (٦٨٤): إذا نذر صيام شهر معين ودخل الشهر وهو مريض ثم مات فهل يقضى عنه؟','159','695','0');
INSERT INTO [t14242] VALUES (712,'السؤال (٦٨٥): إذا كان عليه صوم نذر وقضاء من رمضان فهل له أن يبدأ بالنذر؟','159','695','0');
INSERT INTO [t14242] VALUES (713,'اللباس والزينة','160','0','0');
INSERT INTO [t14242] VALUES (714,'السؤال (٦٨٦): ما حكم لبس الشراب اللحمي بالنسبة للنساء؟','160','713','0');
INSERT INTO [t14242] VALUES (715,'السؤال (٦٨٧): ما حكم لبس «الباروكة»؟','160','713','0');
INSERT INTO [t14242] VALUES (716,'السؤال (٦٨٨): وسألته مرة أخرى عن الباروكة في تاريخ١٤/ ٩/١٤٢١هـ فقال حفظه الله وشفاه؟','160','713','0');
INSERT INTO [t14242] VALUES (717,'السؤال (٦٨٩): ما حكم قياس الرجل لثوب يريد أن يشتريه لزوجته؟','160','713','0');
INSERT INTO [t14242] VALUES (718,'السؤال (٦٩٠): هل يجوز لبس ثياب فيها ذهب ولكنه قليل؟','160','713','0');
INSERT INTO [t14242] VALUES (719,'السؤال (٦٩١): ما حكم استعمال القلم المذهب؟','161','713','0');
INSERT INTO [t14242] VALUES (720,'السؤال (٦٩٢): وما حكم استعمال القلم الذي ريشته فقط مذهبه؟','161','713','0');
