INSERT INTO [t14242] VALUES (797,'السؤال (٧٦٨): قول العلماء: «حواء بنت آدم» هل هذا صحيح؟','175','752','0');
INSERT INTO [t14242] VALUES (798,'السؤال (٧٦٩): قولهم عمن ليس بسعودي: «أجنبي» هل فيه شيء؟','175','752','0');
INSERT INTO [t14242] VALUES (799,'السؤال (٧٧٠): ما حكم دفن الطير ونحوه؟','175','752','0');
