INSERT INTO [t14242] VALUES (804,'السؤال (٧٧٥): يشترط بعض من يقرأ على المرضى طهارة لمحل وكذا طهارة المريض إذا كانت امرأة ألا تكون حائضا فما رأيكم؟','176','752','0');
INSERT INTO [t14242] VALUES (805,'السؤال (٧٧٦): ما حكم القيام للمصحف، وما رأيكم بقول شيخ الإسلام: إذا اعتاد الناس قيام بعضهم لبعض فقيامهم للمصحف من باب أولى؟','176','752','0');
INSERT INTO [t14242] VALUES (806,'السؤال (٧٧٧): ما هو استفتاح الفأل بالمصحف وما حكمه؟','176','752','0');
INSERT INTO [t14242] VALUES (807,'السؤال (٧٧٨): هل يجوز الاحتفاظ بما يسمى بـ «النقتف»: أي: أصل الصورة خاصة وأنه عند سحب صور منه يكون أقل سعر من ابتداء التصوير؟','176','752','0');
INSERT INTO [t14242] VALUES (808,'السؤال (٧٧٩): سبق أن سألتك عن الضرب على الجدار والباب ونحوهما ضربا يشبه الأنغام، فقلت: لا بأس بذلك؛ لأنهما ليسا من آلات المعازف. ثم سمعتك تسأل نفس السؤال فقلت: إن أقل أحواله الكراهة فما رأيكم؟','177','752','0');
INSERT INTO [t14242] VALUES (809,'السؤال (٧٨٠): بعض القراء على المرضى يقرؤون بالميكروفون، فما حكم ذلك وإذا كان فيه إزعاج على الجيران فما حكمه؟','177','752','0');
INSERT INTO [t14242] VALUES (810,'السؤال (٧٨١): فيه امرأة تبيع بضاعة على نساء فإذا سئلت: من صاحبة هذه البضاعة؟ تقول: امرأة (تريد نفسها) ولكنها تورى فما حكم ذلك؟','177','752','0');
INSERT INTO [t14242] VALUES (811,'السؤال (٧٨٢): ما حكم أخذ منديل من المسجد ثم وضعه في الجيب بلا استعمال؟','177','752','0');
INSERT INTO [t14242] VALUES (812,'السؤال (٧٨٣): إذا تساوت المصلحة والمفسدة فما الحكم؟','177','752','0');
INSERT INTO [t14242] VALUES (813,'السؤال (٧٨٤): ما حكم المصافحة عند الدخول في المجلس؟','177','752','0');
