INSERT INTO [t14242] VALUES (817,'السؤال (٧٨٨): هل يشرع إذا صعد أعلى البيت أو الدرج أن يكبر وإذا كان أسفله أن يسبح قياسا على فعل الصحابة رضي الله عنهم؟','178','752','0');
INSERT INTO [t14242] VALUES (818,'السؤال (٧٨٩): هل يعتد بخلاف الظاهرية؟','178','752','0');
INSERT INTO [t14242] VALUES (819,'السؤال (٧٩٠): ما حكم قولهم لأولادهم الصغار: «يا حمار» «يا كلب» ونحو ذلك؟','179','752','0');
INSERT INTO [t14242] VALUES (820,'السؤال (٧٩١): ما حكم مسح آثار العائن للاستشفاء بها؟','179','752','0');
INSERT INTO [t14242] VALUES (821,'السؤال (٧٩٢): ما حكم شراء فيديو للأطفال لمشاهدة الأفلام والأشياء المفيدة؟','179','752','0');
INSERT INTO [t14242] VALUES (822,'السؤال (٧٩٣): هل السلالم الكهربائية بالمسجد الحرام داخله في حد المسجد أم لا؟ وإذا قلنا: إنها داخلة فيه فهل يجوز رمى أجزاء السواك ونحوه؟','179','752','0');
INSERT INTO [t14242] VALUES (823,'السؤال (٧٩٤): نرى بعض العامة إذا صنع طعاما قال: اللهم اجعل ثوابه وأجره لفلان من الأموات فما حكم ذلك؟','179','752','0');
INSERT INTO [t14242] VALUES (824,'السؤال (٧٩٥): فيه من المسلمين من يجعل اسمه مركبا مثل (محمد مبارك، محمد نور) ونحو ذلك فما الحكم؟','180','752','0');
INSERT INTO [t14242] VALUES (825,'السؤال (٧٩٦): إذا أصر إنسان على فعل معصية كالتدخين مثلا، فهل لي أن أقول له: إذا كنت ولا بد مدخنا فدخن بعيدا عني؟ وهل في هذا إقرار للمعصية؟','180','752','0');
INSERT INTO [t14242] VALUES (826,'السؤال (٧٩٧): ما حكم إبادة الحشرات بهذه الأجهزة الكهربائية؟ وهل يكون داخلا في حديث: «لا يعذب بالنار إلا رب النار»؟','180','752','0');
INSERT INTO [t14242] VALUES (827,'السؤال (٧٩٨): ما حكم السلام على المسلم بلفظ «السلام على من اتبع الهدى»؟','181','752','0');
INSERT INTO [t14242] VALUES (828,'السؤال (٧٩٩): ما رأيكم في علك العلك أمام الناس؟','181','752','0');
INSERT INTO [t14242] VALUES (829,'السؤال (٨٠٠): ما حكم تصوير الأشياء المباحة بالفيديو كحفلة أطفال صغار ونحو ذلك؟','181','752','0');
INSERT INTO [t14242] VALUES (830,'السؤال (٨٠١): متى تجوز التورية؟','181','752','0');
INSERT INTO [t14242] VALUES (831,'السؤال (٨٠٢): وسألته عن حكم التضييق على رافضي في العمل؟','181','752','0');
INSERT INTO [t14242] VALUES (832,'السؤال (٨٠٣): ما حكم اقتناء التلفاز؟','182','752','0');
INSERT INTO [t14242] VALUES (833,'السؤال (٨٠٤): فيه بعض الرواة يسمى «عبد خير» فما حكم هذه التسمية؟','182','752','0');
INSERT INTO [t14242] VALUES (834,'السؤال (٨٠٥): ما حكم تمني الفاحشة؟ وهل يأثم؟','182','752','0');
INSERT INTO [t14242] VALUES (835,'السؤال (٨٠٦): بعض الناس يقول: الأولى ترك إطفاء النار إذا كانت في البر فهل هذا صحيح؟','182','752','0');
INSERT INTO [t14242] VALUES (836,'السؤال (٨٠٧): وبعضهم يقول: لا تصب الماء الحار على الأرض إلا بتسمية خشية دخول الجن؟','182','752','0');
INSERT INTO [t14242] VALUES (837,'السؤال (٨٠٨): هل ورد ما يدل على النفث في الرقية إذا كانت من الأدعية النبوية؟','182','752','0');
INSERT INTO [t14242] VALUES (838,'السؤال (٨٠٩): إنسان يريد أن يستقدم زوجته من الخارج ولا يستطيع ذلك إلا بدفع مبلغ من المال فهل له ذلك؟','183','752','0');
INSERT INTO [t14242] VALUES (839,'السؤال (٨١٠): ما رأيكم في قولهم: «حقوق الطبع محفوظة»؟','183','752','0');
INSERT INTO [t14242] VALUES (840,'السؤال (٨١١): ما حكم كمصافحة الكافر؟','183','752','0');
INSERT INTO [t14242] VALUES (841,'السؤال (٨١٢): يقرر فضيلتكم أن هناك أشياء جائزة وغير مشروعة أفلا يرد على هذه السنة التقريرية؟','183','752','0');
INSERT INTO [t14242] VALUES (842,'السؤال (٨١٣): نقل عنكم أنكم سئلتم عن لبس العقال فأخذتم عقال أحد الحاضرين فلبسته طيلة الدرس؟','183','752','0');
INSERT INTO [t14242] VALUES (843,'السؤال (٨١٤): ما حكم ابتداء الكافر بالسلام إذا كان لقصد التأليف؟','184','752','0');
INSERT INTO [t14242] VALUES (844,'السؤال (٨١٥): وهل يجوز أن يقول له: كيف أمسيت ونحو ذلك مطلقا أو عند الحاجة؟','184','752','0');
INSERT INTO [t14242] VALUES (845,'السؤال (٨١٦): يوجد بيالات مكتوب عليها من تحت «العبد الكريم»، فما حكم استعمال هذه البيالات؟','184','752','0');
INSERT INTO [t14242] VALUES (846,'السؤال (٨١٧): يحتاج الإنسان إذا كان في حلقة علم وأراد الذهاب إلى مكانه أن يرفع قدميه على كتب العلم فهل في هذا شيء؟','184','752','0');
INSERT INTO [t14242] VALUES (847,'السؤال (٨١٨): هل تأمير الأمير خاص في حال السفر أو يستمر ولو نزلوا في المدينة التي سافروا إليها؟','184','752','0');
INSERT INTO [t14242] VALUES (848,'السؤال (٨١٩): ذكر النووي أن فرض الكفاية إذا قام به طائفة وسقط الفرض بفعلهم، ثم فعلته طائفة أخرى يكون أيضا في حقهم فرض كفاية لا نافلة، فهل قوله هذا صحيح؟','184','752','0');
INSERT INTO [t14242] VALUES (849,'السؤال (٨٢٠): سمعتكم في درس بلوغ المرام تقولون: إن المساء يبدأ من صلاة العصر، فهل المساء يبدأ من صلاة العصر أم من أذان العصر؟','185','752','0');
INSERT INTO [t14242] VALUES (850,'السؤال (٨٢١): وسألته رحمه الله: عما اشتهر في الآونة الأخيرة من أن امرأة رأت رؤيا في رجل بعد ما حج ورأته وهو عريان، وأن الشيخ محمدا أول الرؤيا بأن هذا دليل على أن الرجل عفر له ذنبه، وأن المرأة قالت للشيخ رحمه الله: إن هذا الرجل الذي رأيته هو أنت يا شيخ وأن الشيخ بكى في الراديو وكان ذلك في سؤال على الهاتف؟','185','752','0');
INSERT INTO [t14242] VALUES (851,'السؤال (٨٢٢): يعلق بعض أصحاب الإبل على أعناقها قوطي يخرق من الوسط ويوضع فيه حصاة فتخرج صوتا مع الحركة، فهل هذا داخل في الجرس المنهي عنه؟','185','752','0');
INSERT INTO [t14242] VALUES (852,'السؤال (٨٢٣): سمعتكم تقولون إن الأصل في الحيوان التحريم بالنسبة للعب الأطفال بخلاف العرائس لكن روى أبو داود بسند صحيح عن عائشة رضي الله عنها أنه كان لها خيل له جناحان فما هو الجواب عن هذا الحديث؟','186','752','0');
INSERT INTO [t14242] VALUES (853,'السؤال (٨٢٤): هل يلزم الجار ببناء سور إذا كان له نوافذ مشرفة على جاره؟','186','752','0');
INSERT INTO [t14242] VALUES (854,'السؤال (٨٢٥): ترك شخص التدخين فقال له صاحبه: إن تركته حتى رمضان فعلي ذبيحة فما الحكم؟','186','752','0');
INSERT INTO [t14242] VALUES (855,'السؤال (٨٢٦): سالت ابن عثيمين رحمه الله عن حكم الأناشيد التي فيها دفوف للأطفال؟','186','752','0');
INSERT INTO [t14242] VALUES (856,'السؤال (٨٢٧): سألت ابن عثيمين إذا سألني أحد عن عالم هل هو جيد في الحديث فهل أجيبه؟','187','752','0');
INSERT INTO [t14242] VALUES (857,'السؤال (٨٢٨): وسألته عن قول الشيخ علي الحلبي إنه اجتمع بكم وإنكم مستاءون من قرار اللجنة الدائمة ضده؟','187','752','0');
INSERT INTO [t14242] VALUES (858,'السؤال (٨٢٩): ما حكم سفر الإنسان مع أهله سياحة إلى البلاد الإسلامية التي تكثر فيها المنكرات؟','187','752','0');
