INSERT INTO [t14280] VALUES (1114,'شرح القرافي: قال صاحب (المجمل) في اللغة: إذا أزال الشيء الشيء فقد نسخه','2300','1112','0');
INSERT INTO [t14280] VALUES (1115,'(فائده) رأيت في شرح المقامات أن بعض الفضلاء بعث بناسخ إلى صديق له ... إلخ','2302','1112','0');
