INSERT INTO [t14280] VALUES (1118,'شرح القرافي: قوله: " وإنما أثرنا لفظ الخطاب على لفظ النص ليشمل اللفظ، والفحوى، والمفهوم','2307','1112','0');
INSERT INTO [t14280] VALUES (1119,'(سؤال) قال في " التلخيص ": إن المفهوم وإن قلنا: إنه دليل لا يجوز التخصص به','2312','1112','0');
