INSERT INTO [t14280] VALUES (1123,'(سؤال) قال النقشواني: ينتقض حده بالشرط الوارد عقيب الجمل الكثيرة، والاستثناء، أو الصفة؛ فإنه طريق شرعي متراخ عن طريق شرعي','2316','1112','0');
INSERT INTO [t14280] VALUES (1124,'(سؤال) قال: ينتقض بكل خطاب دال على ثبوت الحكم على الأبد','2317','1112','0');
INSERT INTO [t14280] VALUES (1125,'(تنبيه) أسقط " المنتخب " و " التنقيح " قوله: " فعل الله تعالى " فلم يذكر هذا الاحتراز ألبتة','2317','1112','0');
INSERT INTO [t14280] VALUES (1126,'المسألة الثالثة قال الرازي: قال القاضي أبو بكر رحمه الله: النسخ رفع، ومعناه: أن خطاب الله تعالى تعلق بالفعل، بحيث لولا طريان الناسخ لبقى','2318','1112','0');
INSERT INTO [t14280] VALUES (1127,'شرح القرافي: قوله: " قال الأستاذ: الخطاب انتهى بذاته "','2322','1112','0');
INSERT INTO [t14280] VALUES (1128,'(فائدة) إلزام الإمام والفقهاء القاضي المحال في العلم والخبر','2326','1112','0');
INSERT INTO [t14280] VALUES (1129,'(سؤال) قال النقشواني: قوله: " ليس اندفاع الباقي بأولى من طريان الطارئ ". ممنوع','2326','1112','0');
INSERT INTO [t14280] VALUES (1130,'(تنبيه) قال سراج الدين: " لا نسلم عدم الأولوية؛ إذ العلة التامة لعدم الشيء تنافي وجوده، وبالعكس','2327','1112','0');
INSERT INTO [t14280] VALUES (1131,'(تنبيه) كلام القاضي في كتبه، والغزالي في (المستصفى) هو ما نقله التبريزي ونحو منه','2332','1112','0');
INSERT INTO [t14280] VALUES (1132,'(تنبيه) صرح الفقهاء بأن النسخ تخصيص في الازمان','2333','1112','0');
INSERT INTO [t14280] VALUES (1133,'المسألة الرابعة قال الرازي: النسخ عندنا جائز عقلا، وواقع سمعا؛ خلافا لليهود','2335','1112','0');
INSERT INTO [t14280] VALUES (1134,'شرح القرافي: قال سيف الدين: منع أبو مسلم الأصبهاني وقوع النسخ شرعا، وجوزه عقلا، ولم ينكر وقوعه من الملل إلا اليهود','2342','1112','0');
INSERT INTO [t14280] VALUES (1135,'(فائدة) ابو مسلم الاصبهاني، حيث وقع، فهو كنية لا اسم','2343','1112','0');
INSERT INTO [t14280] VALUES (1136,'(فائدة) رأيت بعض اللغويين ينقل في (بخت نصر) لغتين (نصر) و (نصر) بتشديد الصاد، وتسكينها','2348','1112','0');
INSERT INTO [t14280] VALUES (1137,'(فائدة) ناظرت بعض اليهود، فقال: كيف تدعون أن شرعنا غير متواتر','2348','1112','0');
INSERT INTO [t14280] VALUES (1138,'(فائدة) ربما خطر بالبال أن بختنصر كيف يعدم بسببه اليهود، مع تفرقهم في أقطار الأرض، فالعادة تحيل ذلك','2349','1112','0');
INSERT INTO [t14280] VALUES (1139,'(تنبيه) زاد سراج الدين فقال: على الآية:" ملزومية الشيء لغيره لا تقتضي وقوعه، ولا صحة وقوعه "','2350','1112','0');
INSERT INTO [t14280] VALUES (1140,'المسألة الخامسة قال الرازي: اتفقت الأمة على جواز نسخ القران، وقال ابو مسلم بن بحر الأصفهاني: لا يجوز','2351','1112','0');
INSERT INTO [t14280] VALUES (1141,'شرح القرافي: قوله: " نسخ الآية إزالتها من اللوح المحفوظ "','2353','1112','0');
INSERT INTO [t14280] VALUES (1142,'المسألة الخامسة في جواز نسخ القرآن','2353','1112','0');
INSERT INTO [t14280] VALUES (1143,'(قاعدة) وقع في القرآن (بين الأيدي)، والمراد به الماضي','2355','1112','0');
INSERT INTO [t14280] VALUES (1144,'(فائدة) قال (المنتخب): أبو مسلم بن عمر، والذي وجدته في عدة من نسخ (المحصول): ابن بحر، وقاله ابن برهان في كتابه المسمى بـ (الأوسط)','2356','1112','0');
INSERT INTO [t14280] VALUES (1145,'المسألة السادسة قال الرازي: اختلفوا في نسخ الشيء قبل مضي وقت فعله','2357','1112','0');
INSERT INTO [t14280] VALUES (1146,'المسالة السادسة (في النسخ قبل مضي الوقت)','2362','1112','0');
INSERT INTO [t14280] VALUES (1147,'شرح القرافي: قلت: المسائل في هذا المعنى أربع','2362','1112','0');
INSERT INTO [t14280] VALUES (1148,'(فائدة) قال الغزالي في (المستصفى): أجابوا عن قضية إبراهيم عليه السلام بخمسة أوجه','2368','1112','0');
INSERT INTO [t14280] VALUES (1149,'(تنبيه) قال التبريزي: قولهم: "كان يظن الأمر بالذبح"','2370','1112','0');
INSERT INTO [t14280] VALUES (1150,'(تنبيه) فهرس سيف الدين المسألة فقال: اتفق القائلون بجواز النسخ على جوازه بعد التمكن، واختلفوا قبل دخول الوقت','2371','1112','0');
INSERT INTO [t14280] VALUES (1151,'المسالة السابعة قال الرازي: يجوز نسخ الشيء، لا إلى بدل؛ خلافا لقوم','2372','1112','0');
INSERT INTO [t14280] VALUES (1152,'شرح القرافي: قوله: "احتجوا بقول الله تعالى: {ما ننسخ من آية}','2372','1112','0');
INSERT INTO [t14280] VALUES (1153,'المسألة الثامنة قال الرازي: يجوز نسخ الشيء إلى ما هو أثقل منه؛ خلافا لبعض أهل الظاهر','2374','1112','0');
INSERT INTO [t14280] VALUES (1154,'شرح القرافي: قوله: "نسخ الحبس في البيوت بالجلد"','2374','1112','0');
INSERT INTO [t14280] VALUES (1155,'(فائدة) تعلق المعتزلة بهذه الآية بأن الله تعالى لا يريد لنا إلا الخير والتسهيل والمصالح','2377','1112','0');
INSERT INTO [t14280] VALUES (1156,'(تنبيه) زاد التبريزي فقال: يجوز النسخ للأثقل','2378','1112','0');
INSERT INTO [t14280] VALUES (1157,'المسألة التاسعة قال الرازي: يجوز نسخ التلاوة دون الحكم، وبالعكس','2379','1112','0');
INSERT INTO [t14280] VALUES (1158,'(فائدة) قال سيف الدين: "لم يخالف في ذلك إلا طائفة شاذة من المعتزلة"','2380','1112','0');
INSERT INTO [t14280] VALUES (1159,'المسألة العاشرة قال الرازي: الخبر: إما أن يكون خبرا عما لا يجوز تغيره','2381','1112','0');
INSERT INTO [t14280] VALUES (1160,'شرح القرافي: قال سيف الدين: إما أن ينسخ نفس الخبر، أو مدلوله، أو ثمرته','2382','1112','0');
INSERT INTO [t14280] VALUES (1161,'المسألة الحادية عشرة قال الرازي: إذا قال الله تعالى: (افعلوا هذا الفعل أبدا)، يجوز نسخه؛ خلافا لقوم','2387','1112','0');
INSERT INTO [t14280] VALUES (1162,'شرح القرافي: قوله: " إن قوله: (افعلوا أبدا قائم مقام التنصيص على أعيان الأزمنة بخصوصياتها "','2388','1112','0');
INSERT INTO [t14280] VALUES (1163,'القسم الثاني في الناسخ والمنسوخ وفيه مسائل','2389','1111','0');
INSERT INTO [t14280] VALUES (1164,'شرح القرافي: قوله: "يجوز نسخ الآحاد بالآحاد كقوله عليه السلام: (كنت نهيتكم عن زيارة القبور ألا فزوروها)','2392','1163','0');
INSERT INTO [t14280] VALUES (1165,'(قاعدة) يشترط في الناسخ أن يكون مساويا، أو أقوى، فلذلك ينسخ المتواتر بالمتواتر دون الآحاد','2395','1163','0');
INSERT INTO [t14280] VALUES (1166,'(قاعدة) إذا دار المصدر بين أن يكون مضافا للفاعل أو المفعول','2396','1163','0');
INSERT INTO [t14280] VALUES (1167,'(تنبيه) زاد التبريزي فقال: كل دليل يفيد وجوب العمل به بشرط انتفاء غيره','2397','1163','0');
INSERT INTO [t14280] VALUES (1168,'المسألة الثانية قال الرازي: قال الأكثرون: يجوز نسخ الكتاب، ودليله ما ذكرناه في الرد على أبي مسلم الأصفهاني','2398','1163','0');
INSERT INTO [t14280] VALUES (1169,'شرح القرافي: قوله: "ليس في الكتاب ما يتوهم دليلا على التوجه إلى بيت المقدس إلا قوله تعالى: {فثم وجه الله}','2400','1163','0');
INSERT INTO [t14280] VALUES (1170,'(سؤال) قال النقشواني: التوجه للبيت المقدس لم يرد فيه كتاب ولا سنة غير أنا لما أمرنا بالصلاة','2402','1163','0');
INSERT INTO [t14280] VALUES (1171,'((تنبيه)) زاد التبريزي فقال على قوله: "لعله نسخ بقرآن نسخت تلاوته" أن الأدلة لا تندفع بالأوهام والوساس','2403','1163','0');
INSERT INTO [t14280] VALUES (1172,'المسألة الثالثة قال الرازي: نسخ الكتاب بالسنة المتواترة جائز وواقع، وقال الشافعي رضي الله عنه: لم يقع','2404','1163','0');
INSERT INTO [t14280] VALUES (1173,'شرح القرافي: قوله "نسخ الحبس في البيوت بالجلد، ثم نسخ بالرجم"','2408','1163','0');
INSERT INTO [t14280] VALUES (1174,'(سؤال) قال النقشواني: لا يستقيم أن آية الحبس منسوخة','2411','1163','0');
INSERT INTO [t14280] VALUES (1175,'المسألة الرابعة قال الرازي: في كون الإجماع منسوخا وناسخا','2412','1163','0');
INSERT INTO [t14280] VALUES (1176,'شرح القرافي: قال سيف الدين: كون الإجماع ينسخ الحكم الثابت به، نفاه الأكثرون، وجوزه الأقلون','2414','1163','0');
INSERT INTO [t14280] VALUES (1177,'(سؤال) منع انعقاد الإجماع في زمانه عليه السلام وجوز بعد ذلك نسخ القياس في زمانه عليه السلام بالإجماع','2415','1163','0');
INSERT INTO [t14280] VALUES (1178,'(تنبيه) لم يتعرض سيف الدين لكون الإجماع لا ينعقد في زمان رسول الله صلى الله عليه وسلم','2417','1163','0');
INSERT INTO [t14280] VALUES (1179,'المسألة الخامسة قال الرازي: في كون القياس منسوخا وناسخا','2418','1163','0');
INSERT INTO [t14280] VALUES (1180,'شرح القرافي: قوله: "ينعقد الإجماع على أحد القولين، فيكون ناسخا للقياس الذي هو مستند أحد القولين في زمانه عليه السلام"','2419','1163','0');
INSERT INTO [t14280] VALUES (1181,'(تنبيه) قال سيف الدين: منع الحنابلة، والقاضي عبد الجبار في بعض أقواله بنسخ حكم القياس','2420','1163','0');
INSERT INTO [t14280] VALUES (1182,'المسألة السادسة قال الرازي: في كون الفحوى منسوخا وناسخا','2423','1163','0');
INSERT INTO [t14280] VALUES (1183,'شرح القرافي: قال سيف الدين: تردد قول القاضي عبد الجبار في نسخ الفحوى دون الأصل','2423','1163','0');
INSERT INTO [t14280] VALUES (1184,'القسم الثالث فيما ظن أنه ناسخ، وليس كذلك، وفيه مسائل','2425','1111','0');
INSERT INTO [t14280] VALUES (1185,'المسألة الأولى قال الرازي: اتفق العلماء على أن زيادة عبادة على العبادات لا يكون نسخا للعبادات','2425','1184','0');
INSERT INTO [t14280] VALUES (1186,'شرح القرافي: قال سيف الدين: منهم من قال','2431','1184','0');
INSERT INTO [t14280] VALUES (1187,'(تنبيه) قال التبريزي خلاف ما قال المصنف فقال: قطع يسار السارق في الثانية، ورجله في الثالثة ليس نسخا لآية السرقة','2439','1184','0');
INSERT INTO [t14280] VALUES (1188,'المسألة الثانية قال الرازي: لا شك في أن النقصان من العبادة نسخ لما أسقط','2442','1184','0');
INSERT INTO [t14280] VALUES (1189,'شرح القرافي: قال سيف الدين: اتفقوا على أن نسخ سنة من السنن، كنسخ ستر الرأس؛ والوقوف على يمين الإمام، لا يكون نسخا لتلك العبادة','2443','1184','0');
INSERT INTO [t14280] VALUES (1190,'(تنبيه) اختار التبريزي أن نسخ الجزء نسخ للعبادة','2445','1184','0');
INSERT INTO [t14280] VALUES (1191,'القسم الرابع في الطريق الذي به يعرف كون الناسخ ناسخا، والمنسوخ منسوخا','2447','1111','0');
INSERT INTO [t14280] VALUES (1192,'شرح القرافي: قوله: (يعرف النسخ بالإتيان بالنقيض أو الضد)','2449','1191','0');
INSERT INTO [t14280] VALUES (1193,'(تنبيه) زاد التبريزي فقال: المتقدم الصحبة يفيد التقدم إن قال: سمعه من رسول الله - صلى الله عليه وسلم - وإلا فل','2450','1191','0');
INSERT INTO [t14280] VALUES (1194,'(مسألة) قال سيف الدين: إذا نسخ حكم أصل القياس هل يبقي حكم الفرع؟','2450','1191','0');
INSERT INTO [t14280] VALUES (1195,'(مسألة) قال سيف الدين: لا أعرف خلافا أن الناسخ إذا كان مع جبريل - عليه السلام - لم ينزل به للنبي - عليه السلام - لم يثبت حكمه في حق المكلفين','2451','1191','0');
INSERT INTO [t14280] VALUES (1196,'(مسألة) قال الغزالي في (المستصفى): يجوز نسخ المنطوق باجتهاد النبي - عليه السلام - وقياسه','2453','1191','0');
INSERT INTO [t14280] VALUES (1197,'(مسألة)','2453','1191','0');
INSERT INTO [t14280] VALUES (1198,'(مسألة) قال الغزالي في (المستصفي): لا يجوز نسخ منطوق النص القاطع بالقياس المعلوم بالظن والاجتهاد، كان جليا أو خفيا، خلافا لمن شذ فقال: ما جاز التخصيص به جاز النسخ به','2454','1191','0');
INSERT INTO [t14280] VALUES (1199,'(مسألة) قال الشيخ أبو إسحاق في (اللمع): لا يجوز النسخ إلا في التكاليف بما يصح وقوعه على وجهين كالعبادات','2455','1191','0');
INSERT INTO [t14280] VALUES (1200,'(مسألة) قال الشيخ أبو إسحاق في (اللمع): الصحيح من المذهب جواز النسخ بدليل الخطاب؛ لأنه في معنى النطق','2455','1191','0');
INSERT INTO [t14280] VALUES (1201,'(مسألة) قال الشيخ المعروف بـ (العالمي) في كتابه: النسخ بالإقرار جائز','2456','1191','0');
INSERT INTO [t14280] VALUES (1202,'الكلام في الإجماع','2457','0','0');
INSERT INTO [t14280] VALUES (1203,'القسم الأول في أصل الإجماع','2457','1202','0');
INSERT INTO [t14280] VALUES (1204,'شرح القرافي: قوله: (الإجماع اتفاق أهل الحل والعقد من أمة محمد - صلى الله عليه وسلم - على أمر من الأمور)','2458','1203','0');
INSERT INTO [t14280] VALUES (1205,'(سؤال) جعل (أجمع) مشتركا بين العزم والاتفاق','2459','1203','0');
INSERT INTO [t14280] VALUES (1206,'المسألة الثانية قال الرازي: من الناس من زعم أن اتفاقهم على الحكم الواحد الذي لا يكون معلوما بالضرورة محال','2460','1203','0');
INSERT INTO [t14280] VALUES (1207,'شرح القرافي: قوله: (يمتنع إجماعهم على غير الضروري، كامتناعه في الكلمة المعينة، والمأكول المعين)','2464','1203','0');
INSERT INTO [t14280] VALUES (1208,'(تنبيه) أكثر الإجماعات، بل الكل إلا اليسير منها جدا إجماع الصحابة رضوان الله عليهم','2466','1203','0');
INSERT INTO [t14280] VALUES (1209,'(تنبيه) قال التبريزي: الحجاج أكثر من علماء الأعصار أضعافا، وهم يجتمعون على كلمة التلبية في يوم واحد','2467','1203','0');
INSERT INTO [t14280] VALUES (1210,'المسألة الثالثة قال الرازي: إجماع أمة محمد - صلى الله عليه وسلم - حجة؛ خلافا للنظام، والشيعة، والخوارج','2468','1203','0');
INSERT INTO [t14280] VALUES (1211,'شرح القرافي: قوله: (خلافا للخوارج والنظام والشيعة)','2490','1203','0');
INSERT INTO [t14280] VALUES (1212,'(فائدة)','2490','1203','0');
INSERT INTO [t14280] VALUES (1213,'(فائدة) قلنا: لا نسلم أن اجتماع الأدلة على المدلول الواحد يزيد في غلبة الظن','2492','1203','0');
INSERT INTO [t14280] VALUES (1214,'(تنبيه) اختلف العلماء في لفظ (غير)','2493','1203','0');
INSERT INTO [t14280] VALUES (1215,'(تنبيه) غير سراج الدين وزاد، فقال: [إن المعلق بالشرط] إن لم يكن عدما عند عدمه حصل الغرض','2506','1203','0');
INSERT INTO [t14280] VALUES (1216,'المسلك الثاني قال الرازي: التمسك بقوله، عز وجل:} وكذلك جعلناكم أمة وسطا؛ لتكونوا شهداء على الناس','2511','1203','0');
INSERT INTO [t14280] VALUES (1217,'شرح القرافي: قوله: (الوسط من كل شيء خياره)','2515','1203','0');
INSERT INTO [t14280] VALUES (1218,'(فائدة) دخل عمر بن عبد العزيز على عبد الملك بن مروان، فقال له: كيف نفقتك في أهلك؟ فقال له: حسنه بين سيئتين يا أمير المؤمنين','2515','1203','0');
INSERT INTO [t14280] VALUES (1219,'(فائدة) قال النحاة: (وسط) بالفتح: اسم، و (وسط) بالتسكين: ظرف، مثل (بين) مسكن الوسط','2515','1203','0');
INSERT INTO [t14280] VALUES (1220,'المسلك الثالث قال الرازي: قوله تعالى:} كنتم خير أمة أخرجت للناس تأمرون بالمعروف وتنهون عن المنكر','2521','1203','0');
INSERT INTO [t14280] VALUES (1221,'شرح القرافي: قوله: (لو أجمعوا على خطأ لكانوا قد أجمعوا على منكر)','2525','1203','0');
INSERT INTO [t14280] VALUES (1222,'(سؤال) قوله: (تأمرون بالمعروف وتنهون عن المنكر)','2528','1203','0');
INSERT INTO [t14280] VALUES (1223,'المسلك الرابع قال الرازي: التمسك بما روى عن النبي - صلى الله عليه وسلم -: (أن أمته لا تجتمع على خطأ)','2529','1203','0');
INSERT INTO [t14280] VALUES (1224,'شرح القرافي: قال صاحب (المجمل): الربقة: قلادة كالخيط','2538','1203','0');
INSERT INTO [t14280] VALUES (1225,'(سؤال) قوله عليه السلام: (لا تزال طائفة من أمتي) ونحوه من النصوص - إن أريد بالأمة - هاهنا - أهل العقد والحل الذين هم مجتهدون، فقد انقطعوا من بعد ثلاثمائة، ولم يبق إلا المقلدون','2541','1203','0');
INSERT INTO [t14280] VALUES (1226,'المسلك الخامس قال الرازي: دليل العقل: وهو الذي عول عليه إمام الحرمين، رحمه ال','2545','1203','0');
INSERT INTO [t14280] VALUES (1227,'شرح القرافي: قوله: (لم لا يكون التابعون أجمعوا على المنع من مخالفة الإجماع لأمارة؟)','2546','1203','0');
INSERT INTO [t14280] VALUES (1228,'(سؤال) قوله: (إطباق الجمع العظيم إما أن يكون لدلالة، أو لأمارة)','2546','1203','0');
INSERT INTO [t14280] VALUES (1229,'(سؤال) قال التبريزي: الاتفاق على العمل بخبر عبد الرحمن، وأمثاله ليس نقضا على هذه القاعدة','2546','1203','0');
INSERT INTO [t14280] VALUES (1230,'(سؤال) قال التبريزي: قوله: (دفع الضرر المظنون واجب) - ممنوع من حيث هو ضرر','2547','1203','0');
INSERT INTO [t14280] VALUES (1231,'المسألة الرابعة قال الرازي: أما الشيعة فقد استدلوا على أن الإجماع حجة؛ بأن زمان التكليف لا يخلو عن الإمام المعصوم','2549','1203','0');
INSERT INTO [t14280] VALUES (1232,'شرح القرافي: قوله: (لا فرق في العقل بين عدم فعل اللطف وبين فعل المفسدة)','2563','1203','0');
INSERT INTO [t14280] VALUES (1233,'القسم الثاني قال الرازي: فيما أخرج من الإجماع، وهو منه','2565','1202','0');
INSERT INTO [t14280] VALUES (1234,'المسألة الأولي: كل مسألة فالحكم فيها: إما أن يكون بالإيجاب الكلي، أو بالسلب الكلي، أو بالإيجاب في البعض، والسلب في البعض','2565','1233','0');
INSERT INTO [t14280] VALUES (1235,'شرح القرافي: قوله: (فإذا اختلف أهل العصر الأول على قولين من هذه الثلاثة)','2567','1233','0');
INSERT INTO [t14280] VALUES (1236,'(تنبيه) قال التبريزي: إجماعهم على عدم حرمان الجد ليس التفاتا إلى أن القول بأقل ما قيل تمسك بالإجماع','2570','1233','0');
INSERT INTO [t14280] VALUES (1237,'المسألة الثانية قال الرازي: الأمة لم تفصل بين مسألتين، فهل لمن بعدهم أن يفصل بينهما؟','2571','1233','0');
INSERT INTO [t14280] VALUES (1238,'شرح القرافي: الفرق بين هذه المسألة والتي قبلها','2573','1233','0');
INSERT INTO [t14280] VALUES (1239,'(تنبيه) قال التبريزي: إن اختلفوا في الحكم وقد جمعهما رابطة تجرى مجرى الحكم كالعمة والخالة تجمعهما رابطة المحرمية، فالأظهر أن الفصل بين القولين خرق للإجماع','2576','1233','0');
INSERT INTO [t14280] VALUES (1240,'المسألة الثالثة قال الرازي: يجوز حصول الاتفاق بعد الخلاف وقال الصيرفي: لا يجوز','2577','1233','0');
INSERT INTO [t14280] VALUES (1241,'شرح القرافي','2577','1233','0');
INSERT INTO [t14280] VALUES (1242,'(تنبيه) قال التبريزي: الإجماع الأول لم ينعقد على كون كل واحد من القولين حقا','2578','1233','0');
INSERT INTO [t14280] VALUES (1243,'المسألة الرابعة قال الرازي: إذا اتفق أهل العصر الثاني على أحد قولي أهل العصر الأول، كان ذلك إجماعا، لا تجوز مخالفته','2580','1233','0');
INSERT INTO [t14280] VALUES (1244,'شرح القرافي: قوله: (لأنه إجماع حدث بعد ما لم يكن، فيكون حجة، كما إذا حدث بعد تردد أهل الإجماع فيه حال التفكر)','2583','1233','0');
INSERT INTO [t14280] VALUES (1245,'المسألة الخامسة قال الرازي: أهل العصر، إذا انقسموا إلى قسمين، ثم مات أحد القسمين، صار قول الباقين إجماعا','2586','1233','0');
INSERT INTO [t14280] VALUES (1246,'شرح القرافي: قلنا: ينبغي أن يتخرج على هذا أن قول الميت هل هو معتبر أم لا؟','2586','1233','0');
INSERT INTO [t14280] VALUES (1247,'المسألة السادسة قال الرازي: أهل العصر، إذا اختلفوا على قولين، ثم رجعوا إلى أحد ذينك القولين، هل يكون ذلك إجماعا؟','2588','1233','0');
INSERT INTO [t14280] VALUES (1248,'شرح القرافي: قال إمام الحرمين: الذي اختاره إن كان الرجوع بقرب الاختلاف، كان إجماعا، وإن كان بعد تطاول المدة، واستقرار النظر، فلا يكون ذلك إجماعا','2589','1233','0');
INSERT INTO [t14280] VALUES (1249,'(سؤال) ما الفرق بين هذه المسألة وبين المسألة الثالثة','2589','1233','0');
INSERT INTO [t14280] VALUES (1250,'المسألة السابعة قال الرازي: انقراض العصر غير معتبر عندنا في الإجماع؛ خلافا لبعض الفقهاء والمتكلمين، منهم الأستاذ أبو بكر بن فورك','2591','1233','0');
INSERT INTO [t14280] VALUES (1251,'شرح القرافي: (فائدة) (فورك) قال المحدثون: الصحيح فيه ضم الفاء','2593','1233','0');
INSERT INTO [t14280] VALUES (1252,'(فائدة) قال سيف الدين: القائلون بانقراض العصر اختلفوا في إدخال من أدرك المجمعين','2594','1233','0');
INSERT INTO [t14280] VALUES (1253,'(فائدة) قال المحدثون: (عبيدة السلماني) من أصحاب علي - رضي الله عنه - وخواصه','2595','1233','0');
INSERT INTO [t14280] VALUES (1254,'المسألة الثامنة قال الرازي: اختلفوا في أنا لو جوزنا انعقاد الإجماع عن السكوت، فهل يعتبر فيه الانقراض؟','2598','1233','0');
INSERT INTO [t14280] VALUES (1255,'شرح القرافي: قوله: (السكوت إن دل على الرضا، فلا حاجة للموت، وإن لم يدل، فلا يؤثر الموت)','2598','1233','0');
INSERT INTO [t14280] VALUES (1256,'المسألة التاسعة قال الرازي: الإجماع المروى بطريق الآحاد حجة؛ خلافا لأكثر الناس','2599','1233','0');
INSERT INTO [t14280] VALUES (1257,'شرح القرافي: قوله: (العمل به يقتضي دفع الضرر بالمظنون)','2599','1233','0');
INSERT INTO [t14280] VALUES (1258,'(تنبيه) قال التبريزي على تمسكه: هذا قياس الإجماع، وليس بحجة في الأصول','2600','1233','0');
INSERT INTO [t14280] VALUES (1259,'القسم الثالث فيما أدخل في الإجماع، وليس منه','2601','1202','0');
INSERT INTO [t14280] VALUES (1260,'شرح القرافي: قوله: (مذهب الشافعي ليس إجماعا، ولا حجة على آخره)','2603','1259','0');
INSERT INTO [t14280] VALUES (1261,'(فائدة) قال القاضي عبد الوهاب المالكي في (الملخص): هذه المسألة فيها أقسام','2605','1259','0');
INSERT INTO [t14280] VALUES (1262,'المسألة الثانية قال الرازي: اختلفوا فيما إذا قال بعض الصحابة قولا، ولم يعرف له مخالف','2607','1259','0');
INSERT INTO [t14280] VALUES (1263,'شرح القرافي: الفرق بين هذه المسألة والتي قبلها - الساكت حاضر، وهاهنا القائل: لم يبلغنا أنه حضر أحد','2607','1259','0');
INSERT INTO [t14280] VALUES (1264,'المسألة الثالثة قال الرازي: إذا استدل أهل العصر بدليل، أو ذكروا تأويلا، ثم استدل أهل العصر الثاني بدليل آخر، أو ذكروا تأويلا آخر، فقد اتفقوا على أنه لا يجوز إبطال التأويل القديم','2609','1259','0');
INSERT INTO [t14280] VALUES (1265,'شرح القرافي: قوله: (قد دللنا على أنه لا يجوز استعمال المشترك في معنييه)','2610','1259','0');
INSERT INTO [t14280] VALUES (1266,'المسألة الرابعة قال الرازي: قال مالك: إجماع أهل المدينة وحدها حجة','2612','1259','0');
INSERT INTO [t14280] VALUES (1267,'شرح القرافي: قوله: (الأماكن لا تؤثر في كون الأقوال حجة)','2615','1259','0');
INSERT INTO [t14280] VALUES (1268,'(سؤال) لا دلالة في الحديث؛ لأن الخبث في عرف الشرع هو ما نهي عنه','2626','1259','0');
INSERT INTO [t14280] VALUES (1269,'(فائدة) قال الغزالي في (المستصفى): قال قوم: إجماع الحرمين: (مكة) و (المدينة)، والمصرين: (الكوفة) و (البصرة) حجة','2626','1259','0');
INSERT INTO [t14280] VALUES (1270,'المسألة الخامسة قال الرازي: إجماع العترة وحدها ليس بحجة؛ خلافا للزيدية والإمامية','2627','1259','0');
INSERT INTO [t14280] VALUES (1271,'شرح القرافي: قوله: (إن عليا خالفه الصحابة)','2629','1259','0');
INSERT INTO [t14280] VALUES (1272,'(سؤال) تقدم في (باب الأفعال) الكلام على عصمة الأنبياء - عليهم السلام - وتحقيق معنى العصمة','2633','1259','0');
INSERT INTO [t14280] VALUES (1273,'(سؤال) إذا تعذر حمل العموم على ظاهره، يحمل على التخصيص','2634','1259','0');
INSERT INTO [t14280] VALUES (1274,'(سؤال في الآية) إن قوله تعالى:} ليذهب {[الأحزاب:٣٣] لفظ مستقبل لا يختص بزمان','2635','1259','0');
INSERT INTO [t14280] VALUES (1275,'(سؤال) إذا كان لفظ (الأهل) مشتركا، فلعل رسول الله - صلى الله عليه وسلم - فهم منه أنه استعمل في مفهوميه، وهو الراجح؛ لأنه جمع بين الأحاديث','2635','1259','0');
INSERT INTO [t14280] VALUES (1276,'(تنبيه) غير سراج الدين فقال في الجواب: ظاهر الآية يقتضي حصر إرادة إزالة الرجس في أهل البيت، وهو غير مراد','2636','1259','0');
INSERT INTO [t14280] VALUES (1277,'(فائدة) (عترة الرجل) بالتاء اليابسة: أقاربه الأدنون، وعشيرته الأخصون به','2636','1259','0');
INSERT INTO [t14280] VALUES (1278,'المسألة السادسة قال الرازي: إجماع الأئمة الأربعة وحدهم ليس بحجة','2637','1259','0');
INSERT INTO [t14280] VALUES (1279,'المسألة السابعة قال الرازي: إجماع الصحابة مع مخالفة من أدركهم من التابعين ليس بحجة خلافا لبعضهم','2639','1259','0');
INSERT INTO [t14280] VALUES (1280,'شرح القرافي: قوله: (احتجوا بقوله تعالى:} لقد رضي الله عن المؤمنين إذ يبايعونك تحت الشجرة','2640','1259','0');
INSERT INTO [t14280] VALUES (1281,'(سؤال) ما الفرق بين هذه المسألة، وبين مسألة اشتراط انقراض العصر','2642','1259','0');
INSERT INTO [t14280] VALUES (1282,'(سؤال) على قوله: (رجع ابن عمر لسعيد بن جبير وغيره)، فإنه غير متجه','2643','1259','0');
INSERT INTO [t14280] VALUES (1283,'المسألة الثامنة قال الرازي: اختلفوا في انعقاد الإجماع، مع مخالفة المخطئين من أهل القبلة في مسائل الأصول','2644','1259','0');
INSERT INTO [t14280] VALUES (1284,'القرافي: قوله: (يثبت كفرهم بإجماعنا، وإلا لزم الدور)','2644','1259','0');
INSERT INTO [t14280] VALUES (1285,'المسألة التاسعة قال الرازي: الإجماع لا يتم مع مخالفة الواحد والاثنين؛ خلافا لأبي الحسين الخياط من المعتزلة، ومحمد بن جرير الطبري، وأبي بكر الرازي','2645','1259','0');
INSERT INTO [t14280] VALUES (1286,'شرح القرافي: قلت: لم يفهرس سيف الدين هكذا، بل قال: اختلفوا في انعقاد إجماع الأكثر مع مخالفة الأقل','2648','1259','0');
INSERT INTO [t14280] VALUES (1287,'القسم الرابع فيما يصدر عنه الإجماع','2650','1202','0');
INSERT INTO [t14280] VALUES (1288,'المسألة الأولي: لا يجوز حصول الإجماع إلا عن دلالة، أو أمارة، وقال قوم: يجوز صدوره عن التبخيت','2650','1287','0');
INSERT INTO [t14280] VALUES (1289,'شرح القرافي: قوله: (يجوز صدوره عن التبخيت)','2651','1287','0');
INSERT INTO [t14280] VALUES (1290,'المسألة الثانية قال الرازي: القائلون بأنه لا ينعقد الإجماع إلا عن طريق اتفقوا على جواز وقوعه عن الدلالة','2655','1287','0');
INSERT INTO [t14280] VALUES (1291,'شرح القرافي: قوله: (قال ابن جرير: ذلك غير ممكن)','2657','1287','0');
INSERT INTO [t14280] VALUES (1292,'(تنبيه) تقدم أول الكتاب الفرق بين: الدليل، والأمارة، والطريق','2658','1287','0');
INSERT INTO [t14280] VALUES (1293,'المسألة الثالثة قال الرازي: قال أبو عبد الله البصري: (الإجماع الموافق لمقتضى خبر يدل على أن ذلك الإجماع؛ لأجل ذلك الخبر)','2659','1287','0');
INSERT INTO [t14280] VALUES (1294,'القسم الخامس في المجمعين','2661','1202','0');
INSERT INTO [t14280] VALUES (1295,'شرح القرافي: قوله: (لفظ الأمة يتناول كافة الأمة)','2664','1294','0');
INSERT INTO [t14280] VALUES (1296,'المسألة الرابعة قال الرازي: المعتبر بالإجماع في كل فن- أهل الاجتهاد في ذلك الفن، وإن لم يكونوا من أهل الاجتهاد في غيره','2667','1294','0');
INSERT INTO [t14280] VALUES (1297,'شرح القرافي: قلنا: هذه المسألة والمسألة التي بعدها في أن الفقيه الذي لا يعرف الأصول لا عبرة بقوله','2668','1294','0');
INSERT INTO [t14280] VALUES (1298,'المسألة الخامسة لا يعتبر في المجمعين بلوغهم إلى حد التواتر','2669','1294','0');
INSERT INTO [t14280] VALUES (1299,'المسألة السادسة إجماع غير الصحابة حجة؛ خلافا لأهل الظاهر','2669','1294','0');
INSERT INTO [t14280] VALUES (1300,'شرح قال إمام الحرمين في (ألبرهان): قال بعضهم: لا يجوز نقصان الأئمة عن حد التواتر','2672','1294','0');
INSERT INTO [t14280] VALUES (1301,'القسم السادس فيما عليه ينعقد الإجماع','2674','1202','0');
INSERT INTO [t14280] VALUES (1302,'المسألة الأولى: كل ما لا يتوقف العلم بكون الإجماع حجة على العلم به أمكن إثباته بالإجماع','2674','1301','0');
INSERT INTO [t14280] VALUES (1303,'شرح القرافي: قوله: (لا يمكن إثبات الصانع وقدرته وعلمه بجميع المعلومات والنبوة بالإجماع)','2674','1301','0');
INSERT INTO [t14280] VALUES (1304,'المسألة الثالثة هل يجوز أن تنقسم الأمة إلى قسمين، وأحد القسمين مخطئون في مسألة، والقسم الآخر مخطئون في مسألة أخرى؟','2677','1301','0');
INSERT INTO [t14280] VALUES (1305,'شرح القرافي: قوله: (خطؤهم في مسألتين لا يخرجهم عن أن يكونوا اتفقوا على الخطأ)','2677','1301','0');
INSERT INTO [t14280] VALUES (1306,'المسألة الرابعة لا يجوز اتفاق الأمة على الكفر، وحكي عن قوم: أنه يجوز أن ترتد الأمة','2679','1301','0');
INSERT INTO [t14280] VALUES (1307,'شرح القرافي: قوله: (إذا فعلت ذلك لم يكونوا مؤمنين)','2679','1301','0');
INSERT INTO [t14280] VALUES (1308,'المسألة الخامسة يجوز اشتراك الأمة في عدم العلم بما لم يكلفوا به','2681','1301','0');
INSERT INTO [t14280] VALUES (1309,'شرح القرافي: قلت: قد فهرس سيف الدين هذه المسألة بصورة أخرى','2681','1301','0');
INSERT INTO [t14280] VALUES (1310,'القسم السابع في حكم الإجماع','2682','1202','0');
INSERT INTO [t14280] VALUES (1311,'المسألة الأولى: جاحد الحكم المجمع عليه لا يكفر؛ خلافا لبعض الفقهاء','2682','1310','0');
INSERT INTO [t14280] VALUES (1312,'شرح القرافي','2682','1310','0');
INSERT INTO [t14280] VALUES (1313,'(تنبيه) ليس تكفيره عند من كفره لأجل طعنه على الإجماع بتجويز الخطأ عليهم','2683','1310','0');
INSERT INTO [t14280] VALUES (1314,'(تنبيه) قال إمام الحرمين في (البرهان): انتشر في لسان الفقهاء أن خارق الإجماع يكفر قال: وهذا باطل قطعا','2685','1310','0');
INSERT INTO [t14280] VALUES (1315,'المسألة الثانية قال الرازي: الإجماع الصادر عن الاجتهاد حجة؛ خلافا للحاكم صاحب (المختصر)','2686','1310','0');
INSERT INTO [t14280] VALUES (1316,'شرح القرافي: قلت: هذه المسألة راجعة لانعقاد الإجماع عن الأمارة، وقد تقدم','2686','1310','0');
INSERT INTO [t14280] VALUES (1317,'المسألة الثالثة قال الرازي: اختلفوا في أنه، هل يجوز انعقاد الإجماع، بعد إجماع على خلافه؟','2687','1310','0');
INSERT INTO [t14280] VALUES (1318,'شرح القرافي','2687','1310','0');
INSERT INTO [t14280] VALUES (1319,'المسألة الرابعة قال الرازي: إذا أجمعوا على شيء، وعارضه قول الرسول - صلى الله عليه وسلم -','2688','1310','0');
INSERT INTO [t14280] VALUES (1320,'شرح القرافي: قلت: هذه المسألة يشترط فيها أن يكون السند متواترا، وإلا قدم الإجماع مطلقا','2688','1310','0');
INSERT INTO [t14280] VALUES (1321,'(مسألة) قال سيف الدين: اختلفوا هل يكون وجود خبر أو دليل لا معارض له، وتشترك الأمة في عدم العلم به','2688','1310','0');
INSERT INTO [t14280] VALUES (1322,'(مسألة) قال إمام الحرمين في (البرهان): قال معظم الأصوليين: الورع معتبر في أصل الإجماع','2689','1310','0');
INSERT INTO [t14280] VALUES (1323,'"مسألة" قال إمام الحرمين في "البرهان": اختلف الأصوليون في الإجماع في الأمم السالفة هل كان حجة؟','2690','1310','0');
INSERT INTO [t14280] VALUES (1324,'"مسألة" قال الغزالي في "المستصفى" قال قوم: إجماع أهل الحرمين: "مكة" و"المدينة"، والمصرين: "الكوفة" و"البصرة" حجة','2690','1310','0');
INSERT INTO [t14280] VALUES (1325,'"مسألة" قال أبو يعلى الحنبلي في "العمدة": المجمع عليه إذا تقدر حاله جاز تركه','2691','1310','0');
INSERT INTO [t14280] VALUES (1326,'"مسألة" قال: القاضي عبد الوهاب المالكي، إذا استدل الإجماع بدليل، هل يجوز أن يستدل على ذلك الحكم بغيره؟','2691','1310','0');
INSERT INTO [t14280] VALUES (1327,'الكلام في الأخبار','2693','0','0');
INSERT INTO [t14280] VALUES (1328,'أما المقدمة','2693','1327','0');
INSERT INTO [t14280] VALUES (1329,'المسألة الأولى: لفظ الخبر حقيقة في القول المخصوص، وقد يستعمل في غير القول','2693','1328','0');
INSERT INTO [t14280] VALUES (1330,'شرح القرافي: قوله: "الخبر حقيقة في القول المخصوص، مجاز في غيره"','2693','1329','0');
INSERT INTO [t14280] VALUES (1331,'"فائدة" يقول أرباب علم البيان عن هذه المجازات: إخبار بلسان الحال، ويجعلونه قسيما للإخبار عن لسان المقال','2696','1329','0');
INSERT INTO [t14280] VALUES (1332,'المسألة الثانية قال الرازي: ذكروا في حده أمورا ثلاثة','2697','1328','0');
INSERT INTO [t14280] VALUES (1333,'شرح القرافي: قوله: "يحتمل التصديق والتكذيب"','2701','1332','0');
INSERT INTO [t14280] VALUES (1334,'المسألة الثالثة قيل: لابد في الخبر من الإرادة','2701','1328','0');
INSERT INTO [t14280] VALUES (1335,'"الفائدة" قال سيف الدين: أجاب الجبائي بأنه يفيد صدق أحدهما حال صدق الآخر','2704','1334','0');
INSERT INTO [t14280] VALUES (1336,'"فائدة" ينبغي في حد الخبر أن يقال: هو اللفظان فأكثر أسند بعض مسبباتهما لبعض إسنادا يحتمل التصديق والتكذيب','2707','1334','0');
INSERT INTO [t14280] VALUES (1337,'"فائدة" قال إمام الحرمين في اختصاره "الاقتصاد" للقاضي أبي بكر: ""الواو" في هذا الحد غلط؛ لأنها تشعر بقبول الضدين','2708','1334','0');
INSERT INTO [t14280] VALUES (1338,'"سؤال" قوله: "حقيقة الخبر ضرورية؛ لأن الخبر الخاص ضروري"','2708','1334','0');
INSERT INTO [t14280] VALUES (1339,'"سؤال" قال "النقشواني": قد يطلب تعريف الشيء تفصيلا من جميع وجوهه، وقد يطلب تعريفه من وجه','2709','1334','0');
INSERT INTO [t14280] VALUES (1340,'"تنبيه" زاد التبريزي فقال: الصدق والكذب وصفان للخبر لا نوعان','2709','1334','0');
INSERT INTO [t14280] VALUES (1341,'"قاعدة" الحقائق أربعة أقسام','2712','1334','0');
INSERT INTO [t14280] VALUES (1342,'المسألة الرابعة قال الرازي: إذا قال القائل: العالم حادث فمدلول هذا الكلام حكمه بثبوت الحدوث للعالم','2715','1328','0');
INSERT INTO [t14280] VALUES (1343,'شرح القرافي: قوله: "لو كان مدلول قولنا: العالم حادث نفس ثبوت الحدوث للعالم لكان، حيث وجد هذا القول، وجد حدوث العالم','2715','1342','0');
INSERT INTO [t14280] VALUES (1344,'"تنبيه" غير سراج الدين فقال: "لا يكون الخبر كذبا"، ولم يقل كما قال المصنف: "لا يكون الخبر كذبا"','2717','1342','0');
INSERT INTO [t14280] VALUES (1345,'المسألة الخامسة قال الرازي: اتفق الأكثرون على أن الخبر لابد وأن يكون: إما صدقا، وإما كذبا؛ خلافا للجاحظ','2718','1328','0');
INSERT INTO [t14280] VALUES (1346,'شرح القرافي: قوله: "إذا قال: زيد في الدار مع أنه ليس في الدار، وهو يظن أنه في الدار لم يقل أحد: إنه كذب"','2719','1345','0');
INSERT INTO [t14280] VALUES (1347,'القسم الأول في الخبر المقطوع به','2722','1327','0');
INSERT INTO [t14280] VALUES (1348,'الباب الأول في التواتر','2722','1347','0');
INSERT INTO [t14280] VALUES (1349,'المسألة الأولى: التواتر في أصل اللغة عبارة عن مجيء الواحد بعد الواحد بفترة بينهما','2722','1348','0');
INSERT INTO [t14280] VALUES (1350,'المسألة الثانية قال الرازي: أكثر العلماء اتفقوا على أن أمثال هذه الأخبار قد تفيد العلم','2724','1348','0');
INSERT INTO [t14280] VALUES (1351,'شرح القرافي: قوله: (قيام التفاوت بين الجزمين يدل على احتمال تطرق النقيض)','2726','1350','0');
INSERT INTO [t14280] VALUES (1352,'(تنبيه) قال سراج الدين على قوله: (كلامهم لا يستحق الجواب): بل جواب الأمل أن اليقينين يتفاوتان، وجواب الثاني أن ذلك الاحتمال يقين الارتفاع','2727','1350','0');
INSERT INTO [t14280] VALUES (1353,'المسألة الثالثة قال الرازي: العلم الحاصل عقيب خبر التواتر ضروري؛ وهو قول الجمهور','2728','1348','0');
INSERT INTO [t14280] VALUES (1354,'شرح القرافي: شبهة النظري أن الناس إذا كان في القضية أهوية، تطرق إليهم احتمال الكذب','2729','1353','0');
INSERT INTO [t14280] VALUES (1355,'(سؤال) قال النقشواني: دعوى المنصف أنه ضروري صحيحة، ودليله ضعيف','2730','1353','0');
INSERT INTO [t14280] VALUES (1356,'(الفرع) قال سيف الدين: إذا قلنا: يفيد العلم، فاتفقت الأشاعرة والمعتزلة أنه لا يؤكد، خلافا لبعض الناس','2730','1353','0');
INSERT INTO [t14280] VALUES (1357,'المسألة الرابعة قال الرازي: استدل أبو الحسن البصري على أن خبر أهل التواتر صدق','2731','1348','0');
INSERT INTO [t14280] VALUES (1358,'شرح القرافي: قوله: (استدل أبو الحسن على صدق التواتر بقوله: يستحيل أن يكون الكذب لا لغرض ومرجح، وإلا لزم الترجيح من غير مرجح)','2747','1357','0');
INSERT INTO [t14280] VALUES (1359,'(سؤال) إن قول كل واحد لو أفاد العلم اجتمع متواترات لأشكل باجتماع الأدلة اليقينية','2754','1357','0');
INSERT INTO [t14280] VALUES (1360,'المسألة الخامسة قال الرازي: في شرائط التواتر','2758','1348','0');
INSERT INTO [t14280] VALUES (1361,'شرح القرافي: قوله: (من الشروط الراجعة إلى أحوال المخبرين أن يكونوا مضطرين إلى ما أخبروا عنه','2765','1360','0');
INSERT INTO [t14280] VALUES (1362,'المسألة السابعة في عدد التواتر قوله: (منهم من اعتبر الاثنى عشر)','2767','1348','0');
INSERT INTO [t14280] VALUES (1363,'(فرع) قال سيف الدين: قال القاضي أبو بكر وأبو الحسين البصري: كل عدد وقع العلم بخبره في واقعة كشخص، لابد وأن يكون مفيدا للعلم في غير تلك الواقعة لغير ذلك الشخص','2769','1362','0');
INSERT INTO [t14280] VALUES (1364,'(تنبيه) قال التبريزي: لا يعتبر عدد مخصوص، بل التأثير للقرائن التي لا سبيل إلى ضبطها','2769','1362','0');
INSERT INTO [t14280] VALUES (1365,'(فائدة) قال سيف الدين: الشرائط المتفق عليها [منها] ما يرجع إلى المخبرين، وهي أربعة','2769','1362','0');
INSERT INTO [t14280] VALUES (1366,'المسألة الثامنة خبر التواتر المعنوي','2770','1348','0');
INSERT INTO [t14280] VALUES (1367,'الباب الثاني قال الرازي: فيما عدا التواتر؛ من الطرق الدالة على كون الخبر صدقا','2771','1347','0');
INSERT INTO [t14280] VALUES (1368,'شرح القرافي: قوله: اختلف أرباب الملل في الاستدلال على حسب اختلافهم في مسألتي الحسن والقبح والمخلوق)','2780','1367','0');
INSERT INTO [t14280] VALUES (1369,'(فائدة) الجم: معناه الكثير، ومنه قول العرب: جاءوا الجم الغفير','2786','1367','0');
INSERT INTO [t14280] VALUES (1370,'(تنبيه) قال التبريزى على قول المصنف في الدور بين الاستدلال بدلالة المعجزة على صدق الرسول','2786','1367','0');
INSERT INTO [t14280] VALUES (1371,'تنبيه) قال سراج الدين على قول المصنف: إذا كانت قدرته- تعالى- على تصديق الرسل','2788','1367','0');
INSERT INTO [t14280] VALUES (1372,'القول في الطرق الفاسدة وهي خمسة','2789','1367','0');
INSERT INTO [t14280] VALUES (1373,'شرح القرافي: قوله: (إذا أخبر بحضرة الرسول عليه السلام، وسكت عن الرد عليه، وقد استشهد به، وعلمنا علمه بذلك، وأمنا التغيير، فإن المخبر حينئذ يجب صدقه','2792','1367','0');
INSERT INTO [t14280] VALUES (1374,'الباب الثالث قال الرازي: في الخبر الذي يقطع بكونه كذبا، وهو أربعة','2794','1347','0');
INSERT INTO [t14280] VALUES (1375,'مسألة في أن الأخبار المروية عن الرسول - صلى الله عليه وسلم - بالآحاد قد وقع فيها ما يكون كذبا','2799','1374','0');
INSERT INTO [t14280] VALUES (1376,'شرح القرافي: قوله: (الخبر عن الشيء يتأخر في الرتبة عن المخبر عنه)','2818','1374','0');
INSERT INTO [t14280] VALUES (1377,'(تنبيه) قال سراج الدين على قول أن القائل: أنا كاذب ولم يكن كذب قط','2821','1374','0');
INSERT INTO [t14280] VALUES (1378,'(مسألة) في تعديل الصحابة قال سيف الدين: اختلفوا في الصحابي من هو؟','2822','1374','0');
INSERT INTO [t14280] VALUES (1379,'القسم الثاني قال الرازي: في الخبر الذي لا يقطع بكونه صدقا أو كذبا، وفيه أبواب','2824','1327','0');
INSERT INTO [t14280] VALUES (1380,'الباب الأول في إقامة الدليل على أنه حجة في الشرع','2824','1379','0');
INSERT INTO [t14280] VALUES (1381,'القسم الثاني فيما لا يقطع بكونه صدقا ولا كذبا','2834','1380','0');
INSERT INTO [t14280] VALUES (1382,'شرح القرافي: قوله: (الدليل على كونه حجة)','2834','1380','0');
INSERT INTO [t14280] VALUES (1383,'(تنبيه) ينبغي أن نعلم أن أصل القسمة ثلاثة','2834','1380','0');
INSERT INTO [t14280] VALUES (1384,'المسلك الثالث قال الرازي: السنة المتواترة','2839','1380','0');
INSERT INTO [t14280] VALUES (1385,'شرح القرافي: قوله: (كان عليه السلام يبعث رسله إلى القبائل، ولم يبلغوا حد التواتر)','2839','1380','0');
INSERT INTO [t14280] VALUES (1386,'المسلك الرابع قال الرازي: الإجماع: العمل بخبر الواحد الذي لا يقطع بصحته مجمع عليه بين الصحابة؛ فيكون العمل به حقا','2840','1380','0');
INSERT INTO [t14280] VALUES (1387,'شرح القرافي: قوله: (هذه الحجة إنما تعتبر في زمان التابعين)','2850','1380','0');
INSERT INTO [t14280] VALUES (1388,'المسلك الخامس (القياس)','2851','1380','0');
INSERT INTO [t14280] VALUES (1389,'شرح القرافي: (قوله: الفرق الأول ملغي؛ لأنه ينتقض بأصل الفتوى)','2852','1380','0');
INSERT INTO [t14280] VALUES (1390,'المسلك السادس (دليل العقل)','2853','1380','0');
INSERT INTO [t14280] VALUES (1391,'شرح القرافي: (قوله: العمل بخبر الواحد يفضى إلى دفع ضرر مظنون، فكان العمل به واجبا)','2855','1380','0');
INSERT INTO [t14280] VALUES (1392,'(أسئلة) قال النقشواني في قوله تعالى: {يحذرون ...} (سلمنا حمله على الأمر، لكن لا نسلم أنه الطلب الجازم','2857','1380','0');
INSERT INTO [t14280] VALUES (1393,'(تنبيه) قال التبريزي: سؤال أبي الحسين في أنهم كانوا محتاجين؛ ليس كذلك','2858','1380','0');
INSERT INTO [t14280] VALUES (1394,'الباب الثاني (في شرائط العمل بهذه الأخبار)','2862','1379','0');
INSERT INTO [t14280] VALUES (1395,'شرح القرافي: (رواية الصبي والمجنون لا تفيد الظن، فلا يجور العمل بهما كالخبر في أمور الدنيا)','2865','1394','0');
INSERT INTO [t14280] VALUES (1396,'المسألة الثانية قوله: (تقبل شهادته إذا تحملها في صغره، فكذلك روايته)','2866','1394','0');
INSERT INTO [t14280] VALUES (1397,'الشرط الرابع قال الرازي: العدالة وهي: هيئة راسخة في النفس تحمل على ملازمة التقوى والمروءة جميعا حتى تحصل ثقة النفس بصدقه','2868','1394','0');
INSERT INTO [t14280] VALUES (1398,'شرح القرافي: قوله: (المعتبر اجتناب الكبائر، وبعض الصغائر، كالتطفيف في الحبة، وسرقة باقة بقل)','2872','1394','0');
INSERT INTO [t14280] VALUES (1399,'(قاعدة) قال جماعة من العلماء: فالفرق بين الصغيرة والكبيرة يرجع إلى عظم المفسدة وصغرها','2873','1394','0');
INSERT INTO [t14280] VALUES (1400,'(سؤال) الإصرار على الصغيرة والكبيرة، فما ضابط الإصرار الذي يوصل للكبيرة؟','2874','1394','0');
INSERT INTO [t14280] VALUES (1401,'(جوابه) إن داوم على الصغيرة مداومة تخل بالثقة به كما تخل به بالكبيرة كان كبيرة، وإلا فلا','2874','1394','0');
INSERT INTO [t14280] VALUES (1402,'(تنبيه) وسمعته يقول: أجمعوا على أن غصب الحبة كبيرة، وسرقة الحبة كبيرة، وشهادة الزور كبيرة، وإن كان الضيع بها حقيرا','2874','1394','0');
INSERT INTO [t14280] VALUES (1403,'(فائدة) قال المازري في (شرح البرهان): المعتزلة نفاة العلم، والخوارج وغيرهم إذا لم نقل بتكفيرهم فسقناهم','2876','1394','0');
INSERT INTO [t14280] VALUES (1404,'(قاعدة) عدم المانع ليس بشرط، وعدم الشرط ليس بمانع، خلافا لما يتخيله كثير من الفقهاء','2878','1394','0');
INSERT INTO [t14280] VALUES (1405,'(المسألة الثانية) رواية المجهول غير مقبولة عند الشافعي، خلافا للحنفية','2879','1394','0');
INSERT INTO [t14280] VALUES (1406,'النوع الثاني قال الرازي: في طريق معرفة العدالة والجرح وهو أمران','2880','1394','0');
INSERT INTO [t14280] VALUES (1407,'شرح القرافي: قوله: (شرط بعضهم العدد في المزكي والجارح في الرواية، والشهادة)','2882','1394','0');
INSERT INTO [t14280] VALUES (1408,'(فائدة) رأيت لبعض المشايخ الذين اجتمعت بهم أن العبد لو روى حديثا يتضمن عتقه قبلت روايته، ولا يكون ذلك تهمة توجب رده','2884','1394','0');
INSERT INTO [t14280] VALUES (1409,'الشرط الخامس قال الرازي: أن يكون الراوي بحيث لا يقع له الكذب والخطأ','2885','1394','0');
INSERT INTO [t14280] VALUES (1410,'شرح القرافي: قوله: (لم يذكر سماعه، ولا رأى خطه، فعند الشافعي: تجوز روايته)','2887','1394','0');
INSERT INTO [t14280] VALUES (1411,'الفصل الثالث قال الرازي: (فيما جعل شرطا في الراوي، مع أنه غير معتبر)','2888','1394','0');
INSERT INTO [t14280] VALUES (1412,'شرح القرافي: قال ابن العربي في (المحصول) له: اشترط الجبائي في قبول الخبر اثنين، وشرط على الاثنين اثنين إلى أن ينتهي الخبر إلى التاسع','2893','1394','0');
INSERT INTO [t14280] VALUES (1413,'(سؤال) على قول الجبائي في اشتراط العدد: فلا يقبل الحديث إلا من اثنين','2894','1394','0');
INSERT INTO [t14280] VALUES (1414,'(سؤال) على قوله: لا يشترط أن يكون الراوي فقيها','2895','1394','0');
INSERT INTO [t14280] VALUES (1415,'(المسألة السادسة) تقبل رواية من لم يرو إلا خبرا واحدا','2895','1394','0');
INSERT INTO [t14280] VALUES (1416,'القسم الثاني (في البحث عن الأمور العائدة إلى المخبر عنه)','2896','1394','0');
INSERT INTO [t14280] VALUES (1417,'(القول فيما ظن أنه شرط في هذا الباب وليس بشرط)','2898','1394','0');
INSERT INTO [t14280] VALUES (1418,'المسألة الأولى: خبر الواحد، إذا عارضه: القياس فإما أن يكون خبر الواحد يقتضي تخصيص القياس، أو القياس يقتضي تخصيص خبر الواحد','2898','1394','0');
INSERT INTO [t14280] VALUES (1419,'شرح القرافي: (من قال بتخصيص العلة قال بتقديم الخبر على القياس)','2901','1394','0');
INSERT INTO [t14280] VALUES (1420,'(فائدة) المهراس: إناء تهرس فيه الحبوب حتى يزول قشرها ونحو ذلك','2904','1394','0');
INSERT INTO [t14280] VALUES (1421,'المسألة الثانية قال الرازي: إذا روى عن رسول الله - صلى الله عليه وسلم -: أنه عمل بخلاف موجب الخبر','2906','1394','0');
INSERT INTO [t14280] VALUES (1422,'المسألة الثالثة عمل أكثر الأمة بخلاف الخبر لا يوجب رده','2907','1394','0');
INSERT INTO [t14280] VALUES (1423,'المسألة الرابعة الحفاظ، إذا خالفوا الراوي في بعض ذلك الخبر، فقد اتفقوا على أن ذلك لا يقتضي المنع من قبول ما لم يخالفوه فيه','2908','1394','0');
INSERT INTO [t14280] VALUES (1424,'المسألة الخامسة خبر الواحد، إذا تكاملت شروط صحته، هل يجب عرضه على الكتاب؟','2909','1394','0');
INSERT INTO [t14280] VALUES (1425,'المسألة السادسة لا شبهة في أن الناسخ يجب أن يكون غير مقارن للكتاب','2910','1394','0');
INSERT INTO [t14280] VALUES (1426,'شرح القرافي: هذه المسألة تقدم بسط الكلام عليها في أفعاله- عليه السلام- إذا عارض قوله فعله','2910','1394','0');
INSERT INTO [t14280] VALUES (1427,'المسألة السابعة قال الرازي: اختلفوا فيما إذا كان مذهب الراوي بخلاف روايته','2911','1394','0');
INSERT INTO [t14280] VALUES (1428,'شرح القرافي: قلت: المراد بالراوي المباشر لرسول الله - صلى الله عليه وسلم - خاصة','2912','1394','0');
INSERT INTO [t14280] VALUES (1429,'المسألة الثامنة قال الرازي: خبر الواحد: إما أن يقتضي علما، أو عمل','2914','1394','0');
INSERT INTO [t14280] VALUES (1430,'شرح القرافي: قوله: (إن لم يكن في الأدلة القاطعة ما يدل عليه، فيجب رده؛ لئلا يلزم تكليف ما لا يطاق)','2915','1394','0');
INSERT INTO [t14280] VALUES (1431,'(فرع) قال أبو الحسين في (المعتمد): يقبل خبر الواحد في العمليات، وإن كان عبادة مبتدأة، أو ركنا، أو حدا، أو ابتداء نصاب أو تقدير','2918','1394','0');
INSERT INTO [t14280] VALUES (1432,'القسم الثالث (في الأخبار) وفيه مسائل)','2919','1394','0');
INSERT INTO [t14280] VALUES (1433,'المسألة الأولى: في كيفية ألفاظ الصحابة في نقل الأخبار عن رسول الله - صلى الله عليه وسلم -','2919','1394','0');
INSERT INTO [t14280] VALUES (1434,'(فائدة) قال القاضي عبد الوهاب في (الملخص): قال جماعة من العلماء: قول الصحابي: أمر رسول الله - صلى الله عليه وسلم - بكذا، أو نهى عن كذا، أو فرض كذا لا يقبل','2922','1394','0');
INSERT INTO [t14280] VALUES (1435,'المسألة الثانية (في كيفية رواية غير الصحابي)','2925','1394','0');
INSERT INTO [t14280] VALUES (1436,'المسألة الثالثة ذهب الشافعي -رضي الله عنه- إلى أن المرسل غير مقبول','2928','1394','0');
INSERT INTO [t14280] VALUES (1437,'المسألة الرابعة (في التدليس)','2935','1394','0');
INSERT INTO [t14280] VALUES (1438,'المسألة الخامسة (يجوز نقل الخبر بالمعنى)','2935','1394','0');
INSERT INTO [t14280] VALUES (1439,'المسألة السادسة الراويان، إذا اتفقا على رواية خبر، وانفرد أحدهما بزيادة، وهما ممن يقبل حديثهما','2938','1394','0');
INSERT INTO [t14280] VALUES (1440,'شرح القرافي: قوله: (السكون يفيد العلم)','2940','1394','0');
INSERT INTO [t14280] VALUES (1441,'(مسألة) قال الغزالي في (المستصفى): إذا كان في مسموعاته عن الزهري حديث شك في سماعه عنه','2941','1394','0');
INSERT INTO [t14280] VALUES (1442,'(مسألة) قال الغزالي في (المستصفى): إذا غلب على ظنه أن الحديث من مسموعاته من الزهري لم يجز له الرواية بالظن','2942','1394','0');
INSERT INTO [t14280] VALUES (1443,'(مسألة) قوله: (عدالة الأصل غير معلومة)','2942','1394','0');
INSERT INTO [t14280] VALUES (1444,'(فائدة) قال إمام الحرمين في (البرهان): (العمدة في قبول المرسل التفصيل)، فحيث حصلت غلبة الظن قبل','2943','1394','0');
INSERT INTO [t14280] VALUES (1445,'(سؤال) المرسل في الاصطلاح هو أن يسقط من السند صحابي، والإرسال خاص بالتابعين','2946','1394','0');
INSERT INTO [t14280] VALUES (1446,'(مسألة) يجوز نقل الخبر بالمعنى','2948','1394','0');
INSERT INTO [t14280] VALUES (1447,'(فائدة) قال النقشواني: هذه المسألة مختلفة الوضع','2951','1394','0');
INSERT INTO [t14280] VALUES (1448,'(فائدة) قال المازري في (شرح البرهان): إذا اعتقد مراد المتكلم ما اعتقده، لكن من جهة الاستنباط امتنع النقل بالمعنى','2952','1394','0');
INSERT INTO [t14280] VALUES (1449,'(فرع) قال المازري: قال أحمد: لا ينقل حديث النبي - صلى الله عليه وسلم - بالمعنى، بخلاف حديث الناس','2952','1394','0');
INSERT INTO [t14280] VALUES (1450,'(مسألة ((إذا انفرد الراوي بزيادة)','2953','1394','0');
INSERT INTO [t14280] VALUES (1451,'(فائدة) قال القاضي عبد الوهاب في (الملخص): قال جماعة: قول الصحابي: أمر النبي -عليه السلام- بكذا، أو نهى عن كذا، أو فرض كذا، ونحوه لا يقبل','2953','1394','0');
INSERT INTO [t14280] VALUES (1452,'(فوائد) تتعلق بكتاب الأخبار ينبغي أن تكون على خاطر الأصولي، يستعين بها على معرفة المرسل، والتدليس، وغيرها','2953','1394','0');
INSERT INTO [t14280] VALUES (1453,'(مسألة) قال سيف الدين: إذا سمع الراوي خبرا، فأراد نقل بعضه، وحذف بعضه، فلا يخلو إما أن يكون الخبر متضمنا لأحكام لا يتعلق بعضها ببعض أو ل','2960','1394','0');
INSERT INTO [t14280] VALUES (1454,'(مسألة) قال سيف الدين: اتفقت الشافعية، والحنابلة، وأبو يوسف، وأبو بكر الرازي، وأكثر الناس على قبول خبر الواحد فيما يوجب الحد','2962','1394','0');
INSERT INTO [t14280] VALUES (1455,'(مسألة) قال إمام الحرمين في (البرهان): قال الأستاذ أبو إسحاق: المستفيض واسطة بين التوتر والآحاد','2962','1394','0');
INSERT INTO [t14280] VALUES (1456,'(مسألة) قال إمام الحرمين في (البرهان): إذا وجد الناظر حديثا مسندا في كتاب مصحح، ولم يسترب في ثبوته، وتبين عدم اللبس فيه، وانتفى الريب، ولم يسمع الكتاب من شيخ، فلا يروه','2962','1394','0');
INSERT INTO [t14280] VALUES (1457,'(مسألة) قال الإمام في (البرهان): ظاهر مذهب الشافعي أن القراءة الشاذة المنقولة بأخبار الآحاد لا تنزل منزلة خبر الواحد، واحتج به أبو حنيفة','2963','1394','0');
INSERT INTO [t14280] VALUES (1458,'(مسألة) خبر الواحد إذا خالف الأصول، قال القاضي عبد الوهاب في (الملخص: قبله الحنفية، والشافعية، ومتقدمو المالكية','2965','1394','0');
INSERT INTO [t14280] VALUES (1459,'(مسألة) قال ابن العربي في (المحصول (: إذا ورد خبر بثبوت مستحيل مضاف إلى الله -تعالى- إن قبل التأويل أول','2966','1394','0');
INSERT INTO [t14280] VALUES (1460,'(مسألة) قال ابن برهان في كتاب (الأوسط): الرواية في النفي عند الشافعي مقبولة، خلافا للحنفية','2966','1394','0');
INSERT INTO [t14280] VALUES (1461,'(فائدة) قال الشيخ أبو إسحاق الشيرازي في (اللمع): أبو بكرة ومن جلد معه في القذف تقبل رواياتهم؛ لأنهم إنما أخرجوا ألفاظهم مخرج الشهادة، وجلده عمر -رضي الله عنه- باجتهاده، فلا ترد روايتهم','2967','1394','0');
INSERT INTO [t14280] VALUES (1462,'(فائدة) إذا اشترك رجلان في الاسم والنسب، وأحدهما عدل، والآخر فاسق، فإذا روي عن هذا الاسم لم يقبل حتى يعلم أنه عن العدل','2967','1394','0');
INSERT INTO [t14280] VALUES (1463,'الكلام في القياس','2968','0','0');
INSERT INTO [t14280] VALUES (1464,'المقدمة: ففيها مسائل','2968','1463','0');
INSERT INTO [t14280] VALUES (1465,'المسألة الأولى: في حد القياس','2968','1464','0');
INSERT INTO [t14280] VALUES (1466,'المسألة الثانية في الأصل والفرع','2974','1464','0');
INSERT INTO [t14280] VALUES (1467,'شرح القرافى: قال سيف الدين: القياس في اللغة التقدير','2976','1466','0');
INSERT INTO [t14280] VALUES (1468,'(سؤال) قياس لا فارق يرد على حد القياس؛ فإنه ليس فيه جامع','2985','1466','0');
INSERT INTO [t14280] VALUES (1469,'(التعريف الثاني) قوله: (إثبات مثل حكم معلوم لمعلوم آخر)','2988','1466','0');
INSERT INTO [t14280] VALUES (1470,'(فائدة) قال أبو الحسن البصري في كتابه الذي صنفه في القياس خاصة، وسماه كتاب (القياس)','2990','1466','0');
INSERT INTO [t14280] VALUES (1471,'(فائدة) قوله: (ينتقض بالمقدمتين والنتيجة)','2991','1466','0');
INSERT INTO [t14280] VALUES (1472,'(قاعدة) النذر لا يؤثر إلا في نقل المندوبات إلى الواجبات كما تقرر في الفقه','2992','1466','0');
INSERT INTO [t14280] VALUES (1473,'(سؤال) قال سيف الدين: يرد على الحد إشكال لا محيص عنه، وهو أن الحكم في الفرع نفيا أو إثباتا متفرع على القياس إجماعا','2994','1466','0');
INSERT INTO [t14280] VALUES (1474,'(المسألة الثانية) (في الأصل والفرع)','2995','1466','0');
INSERT INTO [t14280] VALUES (1475,'(تنبيه) قال التبريزي: قوله: (الحكم أصل في محل الوفاق فرع في محل الخلاف)','2996','1466','0');
INSERT INTO [t14280] VALUES (1476,'(فائدة) قال سيف الدين: يطلق الأصل على أمرين','2997','1466','0');
INSERT INTO [t14280] VALUES (1477,'شرح القرافى: قوله: (إلغاء الفارق يسميه الغزالي تنقيح المناط)','2999','1466','0');
INSERT INTO [t14280] VALUES (1478,'المسألة الثالثة','2999','1464','0');
INSERT INTO [t14280] VALUES (1479,'(فائدة) قال إمام الحرمين في (البرهان): اختلف في الإلحاق بنفي الفارق هل هو قياس أم لا؟ كإلحاق الأمة بالعبد في تقويم العتق','3003','1478','0');
INSERT INTO [t14280] VALUES (1480,'(فائدة) قال المصنف: (إلغاء الفارق تنقيح المناط عند الغزالي)، والذي قاله الغزالي في (المستصفى) أن تنقيح المناط تعيين العلة من أوصاف مذكورة','3003','1478','0');
INSERT INTO [t14280] VALUES (1481,'(فائدة) قال سيف الدين: للقياس عشرة شروط','3004','1478','0');
INSERT INTO [t14280] VALUES (1482,'فرع قال سيف الدين: اختلف الشافعية والحنفية هل حكم الأصل ثابت بالعلة','3007','1478','0');
INSERT INTO [t14280] VALUES (1483,'القسم الأول في إثبات كون القياس حجة','3018','1463','0');
INSERT INTO [t14280] VALUES (1484,'(سؤال) قال النقشواني: الاعتبار: المجاوزة','3021','1483','0');
INSERT INTO [t14280] VALUES (1485,'(تنبيه)','3021','1483','0');
INSERT INTO [t14280] VALUES (1486,'شرح القرافى: قوله: (سأله عما به يقضي بعد أن بعثه مع أن العلم بصلاحيته للقضاء شرط)','3030','1483','0');
INSERT INTO [t14280] VALUES (1487,'(تنبيه) زاد التبريزي فقال: ومن سياقات سماعه أن النبي - صلى الله عليه وسلم - لما أراد أن يبعث معاذا إلى (اليمن)، قال له: (كيف تقضي إذا عرض لك قضاء)؟','3031','1483','0');
INSERT INTO [t14280] VALUES (1488,'(سؤال) قال النقشواني: رسول الله - - صلى الله عليه وسلم - استعمل القياس، ولم يقل: (إن القياس حجة، وبينهما فرق عظيم؛ لأنه - عليه السلام - إذا استعمل القياس كانت مقدماته سالمة عن المطاعن قطعا','3036','1483','0');
INSERT INTO [t14280] VALUES (1489,'شرح القرافى: قوله: (وعن علي وزيد أنهما شبهاهما بغصني شجرة، وجدولى نهر)','3066','1483','0');
INSERT INTO [t14280] VALUES (1490,'(تنبيه)','3074','1483','0');
INSERT INTO [t14280] VALUES (1491,'المسلك السابع قال الرازي: وهو المعقول: أن القياس يفيد ظن دفع الضرر؛ فوجب جواز العمل به','3081','1483','0');
INSERT INTO [t14280] VALUES (1492,'شرح القرافي: قوله: (القياس يفيد ظن الضرر، فيجب العمل به .....)','3093','1483','0');
INSERT INTO [t14280] VALUES (1493,'(تنبيه) قال التبريزي: معنى قوله: (هبته وكان مهيبا)، هي هيبة تعظيم وتوقير، لا هيبة خوف وسراية ضرر','3098','1483','0');
INSERT INTO [t14280] VALUES (1494,'(فائدة) قال سيف الدين: القائلون بأن السماع دل على القياس، قالوا كلهم: إن ذلك الدليل قطعي، إلا أبا الحسن البصري، فإنه قال: ظني. قال: وهو المختار','3100','1483','0');
INSERT INTO [t14280] VALUES (1495,'(فائدة) قال ابن حزم في كتاب (النكت) له في إبطال الأمور الخمسة','3100','1483','0');
INSERT INTO [t14280] VALUES (1496,'المسألة الثانية قال الرازي: قال النظام: (النص على علة الحكم يفيد الأمر بالقياس)','3109','1483','0');
INSERT INTO [t14280] VALUES (1497,'شرح القرافي','3112','1483','0');
INSERT INTO [t14280] VALUES (1498,'(سؤال) قال النقشواني: إذا قطعنا بنفي الفوارق وجب الإلحاق للضرورة','3113','1483','0');
INSERT INTO [t14280] VALUES (1499,'(جوابه) لا نسلم أنه أمر بالقياس، بل نقطع بنفي الفوارق','3114','1483','0');
INSERT INTO [t14280] VALUES (1500,'(سؤال) قال النقشواني: الفرق بين الفعل والترك قوى؛ لأن القائل: (أعط هذا الفقير لفقره)، لا يلزم منه إعطاء كل فقير','3114','1483','0');
INSERT INTO [t14280] VALUES (1501,'(جوابه) أن ذلك معلم بالعرف مع العلة','3114','1483','0');
INSERT INTO [t14280] VALUES (1502,'تنبيه: قال النظام: التنصيص على العلة ينزل منزلة اللفظ العام في وجوب تعميم الحكم','3115','1483','0');
INSERT INTO [t14280] VALUES (1503,'المسألة الثالثة قال الرازي: إلحاق المسكوت عنه بالمنصوص عليه قد يكون ظاهرا جليا، وقد لا يكون كذلك','3116','1483','0');
INSERT INTO [t14280] VALUES (1504,'شرح القرافي','3118','1483','0');
INSERT INTO [t14280] VALUES (1505,'(فائدة) قال إمام الحرمين في (الرهان): قال معظم الأصوليين: ليس هذا الفحوى معدودا من الأقيسة، بل متلقى من اللفظ','3119','1483','0');
INSERT INTO [t14280] VALUES (1506,'المسألة الرابعة قال الرازى: ثبوت الحكم في الأصل: إما أن يكون يقينيا، أو لا يكون','3120','1483','0');
INSERT INTO [t14280] VALUES (1507,'(تنبيه) قال التبريزي: الحكم للأقوى: كقياس الأعمى على الأعرج في الأضحية في عدم الإجزاء','3122','1483','0');
INSERT INTO [t14280] VALUES (1508,'القسم الثاني قال الرازي: في الطرق الدالة على كون الوصف المعين علة للحكم في الأصل','3124','1463','0');
INSERT INTO [t14280] VALUES (1509,'شرح القرافي: قال الغزالي في (شفاء الغليل): قال قوم: إقامة الدليل على عدم الأصل غير واجب','3130','1508','0');
INSERT INTO [t14280] VALUES (1510,'(فائدة) قال القاضي عبد الوهاب في (الملخص)، والشيخ أبو إسحاق في (اللمع): العلة لها معنيان: لغوى واصطلاحي','3131','1508','0');
INSERT INTO [t14280] VALUES (1511,'الباب الأول قال الرازي: في الطرق الدالة على علية الوصف في الأصل، وهي عشرة','3139','1508','0');
INSERT INTO [t14280] VALUES (1512,'الفصل الأول (في النص)','3139','1511','0');
INSERT INTO [t14280] VALUES (1513,'شرح القرافي: قوله: ([الأصل] القاطع في الدلالة على المؤثرية كقولنا: العلة كذا، أو لسبب كذا، أو لموجب كذا، أو لأجل أنه كذا)','3140','1512','0');
INSERT INTO [t14280] VALUES (1514,'(فائدة) قال النحاة: اللام هذه لها سبعة معان','3141','1512','0');
INSERT INTO [t14280] VALUES (1515,'(فائدة) قال ابن جني في (المسائل الدمشقيات): (إن) لها سبعة معان','3142','1512','0');
INSERT INTO [t14280] VALUES (1516,'(فائدة) الباء: قال النحاه: لها خمسة معان','3143','1512','0');
INSERT INTO [t14280] VALUES (1517,'(تنبيه) غير التبريزي العبارة فقال: النص ينقسم إلى صريح، وإيماء','3144','1512','0');
INSERT INTO [t14280] VALUES (1518,'(فائدة) قال سيف الدين: ومن الصريح (من) كقوله تعالى: {من أجل ذلك كتبنا على بني إسرائيل}','3148','1512','0');
INSERT INTO [t14280] VALUES (1519,'(فائدة) قال إمام الحرمين في (البرهان (: نص الشارع على تعليل الحكم على وجه لا يتطرق التفصيل والتأويل إليه','3149','1512','0');
INSERT INTO [t14280] VALUES (1520,'الفصل الثاني (في الإيماء) قال الرازي: وهو على خمسة أنواع','3150','1511','0');
INSERT INTO [t14280] VALUES (1521,'شرح القرافي: قوله: (الجهل لا يكون مانعا من الإلزام؛ لئلا يلزم مخالفة الأصل)','3157','1520','0');
INSERT INTO [t14280] VALUES (1522,'(تنبيه) زاد التبريزي فغير، وقال: (قد قال قوم بمجرد الترتيب على الوصف كاف دون المناسبة، وهو باطل','3160','1520','0');
INSERT INTO [t14280] VALUES (1523,'(سؤال) قال النقشواني: اختار المصنف فيما تقدم أن العلة الشرعية معرفة، وغير التعريف لا يتأتى فيها','3160','1520','0');
INSERT INTO [t14280] VALUES (1524,'(سؤال) قال: قوله: (تقدم العلة على الحكم أقوى إشعارا بالعلية) الأمر بالعكس','3161','1520','0');
INSERT INTO [t14280] VALUES (1525,'(فائدة) قال الغزالي في (شفاء الغليل (: قد يجرى الاسم على اللسان ولا يكون مقصودا','3161','1520','0');
INSERT INTO [t14280] VALUES (1526,'(تنبيه) قال التبريزي: لا يدل على أن كل المدلول علة، بل على أن فيه علة','3162','1520','0');
INSERT INTO [t14280] VALUES (1527,'(النوع الثالث) قوله: (لو لم يكن كونها من الطوافين والطوافات مؤثرا في طهارتها لم يكن في ذكره فائدة)','3162','1520','0');
INSERT INTO [t14280] VALUES (1528,'(تنبيه) قال التريزي: الصحيح- هاهنا- أن التعليل مفهوم من المناسبة','3162','1520','0');
INSERT INTO [t14280] VALUES (1529,'(سؤال) لا يجمع بـ (الواو والنون) أو (الياء والنون) إلا صيغة من يعقل','3163','1520','0');
INSERT INTO [t14280] VALUES (1530,'(تنبيه) زاد التبريزي إيضاحا فقال: قال- عليه السلام- لابن مسعود: (ثمرة طيبة وماء طهور)','3164','1520','0');
INSERT INTO [t14280] VALUES (1531,'(تنبيه) قال التبريزي في هذا القسم الذي لم يكن الحكم مذكورا فيه: في هذا المثال (القاتل لا يرث): يتجه أن يقال: إن فهم العموم من اللفظ الفارق','3165','1520','0');
INSERT INTO [t14280] VALUES (1532,'الفصل الثالث في المناسبة','3168','1511','0');
INSERT INTO [t14280] VALUES (1533,'شرح القرافي: قوله: (اللذة إدراك الملائم، والألم إدراك المنافي)','3168','1532','0');
INSERT INTO [t14280] VALUES (1534,'المسألة الثانية قال الرازي: في تقسيم المناسب، وذلك من أوجه','3169','1532','0');
INSERT INTO [t14280] VALUES (1535,'شرح القرافي: قلت: معنى قوله: (الطريق يكون مؤديا، ومكملا، ومديما)','3176','1532','0');
INSERT INTO [t14280] VALUES (1536,'(فائدة) قال سيف الدين: قال أبو زيد: المناسبة ما لو عرض على العقلاء تلقته بالقبول','3177','1532','0');
INSERT INTO [t14280] VALUES (1537,'(فائدة) قال سيف الدين: لم تخل ملة من الملل، ولا شريعة من الشرائع عن رعاية الكليات الخمس','3178','1532','0');
INSERT INTO [t14280] VALUES (1538,'(فائدة) قال إمام الحرمين في (البرهان): قد يكون ما هو معلل من وجه دون وجه كنصب الزكوات والسرقات','3179','1532','0');
INSERT INTO [t14280] VALUES (1539,'(قاعدة) الكليات خمسة: النوع، والجنس، والفصل، والخاصة، والعرض العام','3182','1532','0');
INSERT INTO [t14280] VALUES (1540,'(تنبيه) قال التبريزي: المناسب إما أن يعلم اعتبار عينة في عين الحكم أو جنسه','3182','1532','0');
INSERT INTO [t14280] VALUES (1541,'(فائدة) قال سيف الدين: إن كان اعتبار الوصف بنص أو إجماع فهو المؤثر','3185','1532','0');
INSERT INTO [t14280] VALUES (1542,'(فائدة) قال الغزالي في (شفاء الغليل): المناسب: الذي لا يلائم نقل وجوده','3187','1532','0');
INSERT INTO [t14280] VALUES (1543,'(فائدة) قال الغزالي في (شفاء الغليل): المناسب ينقسم إلى حقيقي، وإقناعي، وخيالي','3187','1532','0');
INSERT INTO [t14280] VALUES (1544,'التقسيم الثالث الوصف باعتبار الملاءمة','3190','1532','0');
INSERT INTO [t14280] VALUES (1545,'(تنبيه) قال سراج الدين: المناسب إما ملائم، وهو ما وقع حكمه على وفق حكم آخر، وإما غير ملائم','3191','1532','0');
INSERT INTO [t14280] VALUES (1546,'(تنبيه) وقع بيني وبين أهل العصر بحث في معنى شهادة الأصل المعين هل يكفي فيه صورة النزاع وإن ورد نص فيها','3192','1532','0');
INSERT INTO [t14280] VALUES (1547,'المسألة الثالثة قال الرازي: في أن المناسبة لا تبطل بالمعارضة','3193','1532','0');
INSERT INTO [t14280] VALUES (1548,'شرح القرافي: قوله: (إن كانت إحدى المناسبتين أقوى لا يلزم التفاسد؛ لأنا بينا عدم المنافاة)','3214','1532','0');
INSERT INTO [t14280] VALUES (1549,'(تنبيه) زاد التبريزي فقال: العقلاء مجمعون على حسن ركون البحر عند غلبة السلامة','3216','1532','0');
INSERT INTO [t14280] VALUES (1550,'(فائدة) قال سيف الدين: في انخرام المصلحة بالمفسدة كانت مساوية أو راجحة قولان','3220','1532','0');
INSERT INTO [t14280] VALUES (1551,'القسم الثاني في الدلالة على المناسبة','3221','1532','0');
INSERT INTO [t14280] VALUES (1552,'(سؤال) قال النقشواني: لم لا يجوز أن يقال: إن أفعال الله- تعالى- وأحكامه كلها خيرات','3231','1532','0');
INSERT INTO [t14280] VALUES (1553,'(تنبيه) قال سراج الدين على قوله: (لو كان الحكم عين هذا الوصف- وهو في الأزل كان الحكم أزليا): لقائل أن يقول: هو معارض بمثله','3231','1532','0');
INSERT INTO [t14280] VALUES (1554,'الفصل الرابع (في المؤثر)','3235','1511','0');
INSERT INTO [t14280] VALUES (1555,'شرح القرافي: قوله: (هو أن يكون الوصف مؤثرا في جنس الحكم- في الأصول- دون وصف آخر','3235','1554','0');
INSERT INTO [t14280] VALUES (1556,'الفصل الخامس (في الشبه) والنظر في ماهيته ثم في إثباته','3236','1511','0');
INSERT INTO [t14280] VALUES (1557,'شرح القرافي: قوله: (الذي لا يناسب بذاته، ولا يستلزم ما يناسب بذاته الطرد)','3238','1556','0');
INSERT INTO [t14280] VALUES (1558,'(تنبيه) قال التبريزي بعد ذكره الحدين الذين في (المحصور): وقيل: هو الأخذ بأقوى المشبهين، فجعل قول الشافعي تفسيرا تاليا، وهو الظاهر من قول الشافعي','3239','1556','0');
INSERT INTO [t14280] VALUES (1559,'(فائدة) قال سيف الدين: اسم الشبه يتناول كل قياس لأجل مشابهة الفرع لأصله في جامعه','3240','1556','0');
INSERT INTO [t14280] VALUES (1560,'(فرع) قال بعض أصحابنا: الشبه إذا اعتبر جنسه في جنس الحكم دون اعتبار عينه في عين الحكم لا يكون حجة، بخلاف المناسب','3241','1556','0');
INSERT INTO [t14280] VALUES (1561,'(فائدة) قال الغزالي في (المستصفى): الشبه من شرطه احتياجه إلى ضرورة في استنباط مناط الحكم','3242','1556','0');
INSERT INTO [t14280] VALUES (1562,'(فائدة) قال إمام الحرمين في (البرهان): قال أحمد بالشبه في الصورة، وأبو حنيفة قال به- أيضا-','3243','1556','0');
INSERT INTO [t14280] VALUES (1563,'(فرع) قال إمام الحرمين في (البرهان): قال جماعة من المتأخرين: القياس ثلاثة','3244','1556','0');
INSERT INTO [t14280] VALUES (1564,'الفصل السادس: (في الدوران)','3246','1511','0');
INSERT INTO [t14280] VALUES (1565,'شرح القرافي: قوله: (غير هذا الوصف إما أن يكون موجودا قبل الحكم، فيلزم تخلف الحكم عن علته، وهو خلاف الأصل، أو غير موجود، فالأصل بقاؤه على العدم)','3252','1564','0');
INSERT INTO [t14280] VALUES (1566,'تنبيه: قال النقشواني: الدوران عين التجربة','3257','1564','0');
INSERT INTO [t14280] VALUES (1567,'سؤال: قال النقشواني: قوله: (غير هذا الوصف لم يكن موجودا قبل، وإلا لتخلف الحكم عن علته): طريقه لا يتوقف على الدوران','3257','1564','0');
INSERT INTO [t14280] VALUES (1568,'سؤال: قال النقشواني: ما ذكره في هذا الوجه يقتضي أن الحكم حادث','3257','1564','0');
INSERT INTO [t14280] VALUES (1569,'سؤال: قال: ولأنه في هذا الموضع احتج بالاستصحاب','3258','1564','0');
INSERT INTO [t14280] VALUES (1570,'سؤال: قال النقشواني: قوله: (بعض الدورانات تفيد الظن؛ فيكون الكل كذلك [للآية]) غير متجه','3258','1564','0');
INSERT INTO [t14280] VALUES (1571,'تنبيه: قال سراج الدين: علل الشرع معرفات، فجاز أن يكون العدم علة وجزء علة','3258','1564','0');
INSERT INTO [t14280] VALUES (1572,'الفصل السابع: (في السبر والتقسيم)','3267','1511','0');
INSERT INTO [t14280] VALUES (1573,'شرح القرافي: قلت: السبر أصله الاختبار','3270','1572','0');
INSERT INTO [t14280] VALUES (1574,'سؤال: قال النقشواني: قوله: (الأصل بقاء غير هذا الوصف على العدم) يقتضي أن هذه الطريقة مفتقرة إلى للاستصحاب','3272','1572','0');
INSERT INTO [t14280] VALUES (1575,'(جوابه) ما تقدم أن القياس وإن افتقر إلى الاستصحاب من هذا الوجه، لكن القياس أقوى منه من وجه آخر','3273','1572','0');
INSERT INTO [t14280] VALUES (1576,'(سؤال) قال التبريزي: قوله: (لا يبين بالمناسبة؛ لئلا يحتاج إليها فيما يدعيه علة) لا يلزم','3273','1572','0');
INSERT INTO [t14280] VALUES (1577,'(تنبيه) زاد التبريزي فقال: إذا لم يكن التركيب مجمعا عليه، ينفى التركيب في العلة بأنه على خلاف الأصل','3273','1572','0');
INSERT INTO [t14280] VALUES (1578,'(سؤال) قال إمام الحرمين في (البرهان): قال القاضي: السبر أقوى الطرق في إثبات العلة','3273','1572','0');
INSERT INTO [t14280] VALUES (1579,'الفصل الثامن (في الطرد)','3275','1511','0');
INSERT INTO [t14280] VALUES (1580,'القرافي: قلت: تقدم الفرق بين الطرد والطردي','3278','1579','0');
INSERT INTO [t14280] VALUES (1581,'(سؤال) قال النقشواني: إن أراد أنه يلزم من المقارنة التأثير فممنوع','3281','1579','0');
INSERT INTO [t14280] VALUES (1582,'(تنبيه) قال سراج الدين على قوله: (لو لم يحصل ظن العلية لما أسند إلى علة، وهو باطل، أو أسند إلى غيره، وهو يقتضي الشعور بالغير). قال: لقائل أن يقول: الإسناد إلى الغير يقتضي الشعور به جملة','3281','1579','0');
INSERT INTO [t14280] VALUES (1583,'(فائدة) قال سيف الدين: إثبات العلة بالطرد والعكس اختلف القائلون بدلالته على علية الوصف','3284','1579','0');
INSERT INTO [t14280] VALUES (1584,'(تنبيه) مثار الخلاف في هذه المواطن ملاحظة أن ما [رده] الصحابة- رضي الله عنهم-[رددناه]، وما أعملوه أعملناه','3285','1579','0');
INSERT INTO [t14280] VALUES (1585,'الفصل التاسع (في تنقيح المناط)','3286','1511','0');
INSERT INTO [t14280] VALUES (1586,'شرح القرافي: قلت: قد تقدم الفرق بين تنقيح المناط، وتحقيق المناط، وتخريج المناط','3287','1585','0');
INSERT INTO [t14280] VALUES (1587,'(تنبيه) أما الحصر فقد يتوصل إليه بعدم الوجدان، ويجري في التعبدات بعد البحث التام','3288','1585','0');
INSERT INTO [t14280] VALUES (1588,'الفصل العاشر (في الطرق الفاسدة) وهو طريقان','3291','1511','0');
INSERT INTO [t14280] VALUES (1589,'شرح القرافي: قلت: هذه الفهرسة تقتضي أن يكون قد تقدم له طرق','3291','1588','0');
INSERT INTO [t14280] VALUES (1590,'الباب الثاني: في الطرق الدالة على أن الوصف لا يكون علة، وهي خمسة','3293','1508','0');
INSERT INTO [t14280] VALUES (1591,'الفصل الأول (في النقض) وفيه مسائل','3293','1590','0');
INSERT INTO [t14280] VALUES (1592,'المسألة الأولى: وجود الوصف مع عدم الحكم يقدح في كونه علة','3293','1591','0');
INSERT INTO [t14280] VALUES (1593,'(فائدة) الفرق بين النقض، والعكس، والكسر؛ فإن الفقيه محتاج لذلك','3310','1591','0');
INSERT INTO [t14280] VALUES (1594,'(فائدة) قال سيف الدين: جوز أكثر الحنفية، ومالك وابن حنبل، تخصيص العلة المستنبطة، ومنعه أكثر الشافعية، وروي المنع عن الشافعي','3315','1591','0');
INSERT INTO [t14280] VALUES (1595,'المسألة الثانية (في كيفية دفع النقض)','3318','1591','0');
INSERT INTO [t14280] VALUES (1596,'شرح القرافي: قوله: (لا يمكن ألا تمنع العلة في صورة النقض أو عدم الحكم)','3321','1591','0');
INSERT INTO [t14280] VALUES (1597,'(سؤال) قال النقشواني: لا ينحصر دفع النقض بما ذكره','3324','1591','0');
INSERT INTO [t14280] VALUES (1598,'(تنبيه) قال التبريزي: ما يقع الاحتراز به عن النقض هل يجب ذكره في الدليل؟','3325','1591','0');
INSERT INTO [t14280] VALUES (1599,'(فائدة) قال سيف الدين: اختلفوا في النقض المكسور','3327','1591','0');
INSERT INTO [t14280] VALUES (1600,'(فائدة) قال أبو يعلى الحنبلي في (العمدة): إذا وقع النقض بتفسير علته بما يدفع النقض بتفسير مطابق للفظ العلة قبل منه','3328','1591','0');
INSERT INTO [t14280] VALUES (1601,'المسألة الثالثة قال الرازي: وهي مشتملة على فرعين من فروع تخصيص العلة','3329','1591','0');
INSERT INTO [t14280] VALUES (1602,'شرح القرافي: قوله: (تخلف الحكم في بعض الصور لا يقدح في كونها مستلزمة له غالبا)','3330','1591','0');
INSERT INTO [t14280] VALUES (1603,'المسألة الرابعة قال الرازي: في أن النقض: إذا كان واردا على سبيل الاستثناء، هل يقدح في العلة أم لا؟','3331','1591','0');
INSERT INTO [t14280] VALUES (1604,'شرح القرافي: قوله: (نعلم أن من لم يقدم على الجناية لا يؤاخذ بضمانها','3331','1591','0');
INSERT INTO [t14280] VALUES (1605,'المسألة الخامسة في الكسر','3333','1591','0');
INSERT INTO [t14280] VALUES (1606,'شرح القرافي: قال التبريزي: مثاله: تعليل الرخص في السفر من حيث إنه مشقة، فننقضه بمشقة المريض، والحمال','3333','1591','0');
INSERT INTO [t14280] VALUES (1607,'(فائدة) سمعت الشيخ عز الدين بن عبد السلام يقول: اتفقوا على أنه إذا قطع بانتفاء الحكمة لا يثبت الحكم','3335','1591','0');
INSERT INTO [t14280] VALUES (1608,'الفصل الثاني (في عدم التأثير)','3336','1590','0');
INSERT INTO [t14280] VALUES (1609,'شرح القرافي: قوله: (هو بقاء الحكم بدون ما فرض علة له)','3337','1608','0');
INSERT INTO [t14280] VALUES (1610,'(فائدة) قال الإمام في (البرهان): قال الجدليون: عدم التأثير في الوصف، وعدم التأثير في الأصل','3338','1608','0');
INSERT INTO [t14280] VALUES (1611,'(سؤال) قال النقشواني: ما ذكره في العلل الشرعية لا يرد على من يعلل بنفس الحكمة','3340','1608','0');
INSERT INTO [t14280] VALUES (1612,'(تنبيه) زاد التبريزي فقال: العكس إنما يلزم عند اتحاد العلة، وقد أجمعوا على جواز تعددها في الشرع','3340','1608','0');
INSERT INTO [t14280] VALUES (1613,'(فائدة) قال سيف الدين: العكس لغة: رد أول الأمر إلى آخره، وآخره إلى أوله','3341','1608','0');
INSERT INTO [t14280] VALUES (1614,'الفصل الثالث "في القلب" وفيه مسائل','3343','1590','0');
INSERT INTO [t14280] VALUES (1615,'المسألة الأولى: في حقيقته','3343','1614','0');
INSERT INTO [t14280] VALUES (1616,'شرح القرافي: قوله: "الوصف الواحد لا يناسب المتنافيين"','3346','1614','0');
INSERT INTO [t14280] VALUES (1617,'"سؤال" قال النقشوانى: ما ذكره في القلب معارضة في حكم المسألة','3348','1614','0');
INSERT INTO [t14280] VALUES (1618,'"تنبيه" زاد التبريزي فقال: يشترط في القلب الرد إلى أقل المعلل','3349','1614','0');
INSERT INTO [t14280] VALUES (1619,'"فائدة" قال الباجي في "الفصول": "لا يصح قلب القلب"','3349','1614','0');
INSERT INTO [t14280] VALUES (1620,'"فائدة" قال سيف الدين: القلب قلبان','3349','1614','0');
INSERT INTO [t14280] VALUES (1621,'الفصل الرابع "في القول بالموجب"','3355','1590','0');
INSERT INTO [t14280] VALUES (1622,'شرح القرافي: قال: "وحده: تسليم ما جعله المستدل موجب علته، مع استبقاء الخلاف"','3356','1621','0');
INSERT INTO [t14280] VALUES (1623,'"سؤال" "القلب" و"القول بالموجب" معارضة في الحكم، لا قدح في العلة','3358','1621','0');
INSERT INTO [t14280] VALUES (1624,'الفصل الخامس "في الفرق"','3363','1590','0');
INSERT INTO [t14280] VALUES (1625,'الباب الثالث فيما يظن أنه من مفسدات العلة، مع أنه ليس كذلك','3395','1508','0');
INSERT INTO [t14280] VALUES (1626,'"تنبيه" زاد سراج الدين فقال: الاقتصار على المشترك، وإن كان جائزا، لكنه غير لازم','3413','1625','0');
INSERT INTO [t14280] VALUES (1627,'المسألة الرابعة قال الرازي: يجوز التعليل بالعدم؛ خلافا لبعض الفقهاء','3415','1625','0');
INSERT INTO [t14280] VALUES (1628,'شرح القرافي: قوله: "إنه يفيد ظن العلية، والعمل بالظن واجب"','3418','1625','0');
INSERT INTO [t14280] VALUES (1629,'"سؤال" قال النقشواني: قوله- هاهنا-: "يجوز التعليل بالعدم_ يناقضه ما تقدم له','3419','1625','0');
INSERT INTO [t14280] VALUES (1630,'"تنبيه" زاد التبريزي قال: "لا يجوز التعليل بالعدم" خلافا للمصنف','3420','1625','0');
INSERT INTO [t14280] VALUES (1631,'المسألة الخامسة قال الرازي: للمانعين من التعليل بالعدم: أن يمنعوا من التعليل بالأوصاف الإضافية','3423','1625','0');
INSERT INTO [t14280] VALUES (1632,'شرح القرافي: قوله: "لو كان مسمى الإضافة وجوديا، كانت إضافة الإضافة وجودية، ولزم ما لا نهاية له"','3424','1625','0');
INSERT INTO [t14280] VALUES (1633,'"تنبيه" زاد التبريزي فقال: ليس من فروع المنع من التعليل بالعدم امتناع التعليل بالأوصاف الإضافية','3425','1625','0');
INSERT INTO [t14280] VALUES (1634,'المسألة السادسة قال الرازي: تعليل الحكم الشرعي جائز؛ خلافا لبعضهم','3427','1625','0');
INSERT INTO [t14280] VALUES (1635,'شرح القرافي: قوله: "الدوران يفيد الظن"','3429','1625','0');
INSERT INTO [t14280] VALUES (1636,'"سؤال" كيف يتصور في الأحكام الشرعية التقدم والتأخر مع أنها كلها قديمة','3430','1625','0');
INSERT INTO [t14280] VALUES (1637,'"جوابه" أن المراد تكامل شروط التعلق','3430','1625','0');
INSERT INTO [t14280] VALUES (1638,'"تنبيه" زاد التبريزي فقال: نحن نعلل جواز الانتفاع، وصحة البيع، ووجوب الزكاة، ونفقة المملوك بالملك، وهو حكم شرعي','3431','1625','0');
INSERT INTO [t14280] VALUES (1639,'المسألة السابعة قال الرازي: يجوز التعليل بالأوصاف العرفية','3432','1625','0');
INSERT INTO [t14280] VALUES (1640,'شرح القرافي: قوله: "شرطه الاطراد في جميع الأوقات، وإلا احتمل ألا يكون في زمانه عليه السلام"','3432','1625','0');
INSERT INTO [t14280] VALUES (1641,'المسألة الثامنة قال الرازي: يجوز بالتعليل بالوصف المركب عند الأكثرين، وقال قوم: لا يجوز','3433','1625','0');
INSERT INTO [t14280] VALUES (1642,'شرح القرافي: قوله: "الاقتران أو الدوران يفيد ظن العلية، والعمل بالظن واجب"','3437','1625','0');
INSERT INTO [t14280] VALUES (1643,'"تنبيه" زاد سراج الدين فقال على قوله: "إن العدم ليس علة ثبوتية دفعا للتسلسل": لقائل أن يقول: في هذين الجوابين نظر نبهنا عليه فيما تقدم','3440','1625','0');
INSERT INTO [t14280] VALUES (1644,'المسألة التاسعة قال الرازى: اتفقوا على أنه لا يجوز التعليل بالاسم','3445','1625','0');
INSERT INTO [t14280] VALUES (1645,'شرح القرافى: اتفقوا على أنه لا يجوز التعليل بالاسم','3445','1625','0');
INSERT INTO [t14280] VALUES (1646,'المسألة العاشرة قال الرازى: مذهب الشافعي- رضي الله عنه-: أنه يجوز التعليل بالعلة القاصرة؛ وهو قول أكثر المتكلمين','3447','1625','0');
INSERT INTO [t14280] VALUES (1647,'شرح القرافي: قوله: (الكائن في المحل الآخر هو مثل الكائن في محل الأصل','3451','1625','0');
INSERT INTO [t14280] VALUES (1648,'(سؤال) قال النقشواني: قوله: (تكشف عن المنع من القياس) لا يتم','3453','1625','0');
INSERT INTO [t14280] VALUES (1649,'(تنبيه) زاد سراج الدين فقال على قوله: (حكم الشيء حكم مثله؛ لأن عليته إنما هي باعتبار تلك الصفات الحاصلة)','3453','1625','0');
INSERT INTO [t14280] VALUES (1650,'(فائدة) قال سيف الدين: الخلاف في القاصرة إذا لم تكن منصوصة','3454','1625','0');
INSERT INTO [t14280] VALUES (1651,'المسألة الحادية عشرة قال الرازي: الحق أنه لا يجوز التعليل بالصفات المقدرة؛ خلافا لبعض الفقهاء العصريين','3455','1625','0');
INSERT INTO [t14280] VALUES (1652,'شرح القرافي: قوله: (إطلاق التصرف معلل بالملك، والملك معلل بصيغة الإيجاب والقبول، والدين مقدر في الذمة)','3456','1625','0');
INSERT INTO [t14280] VALUES (1653,'(سؤال) قال النقشواني: الملك في العرف، والشرع','3459','1625','0');
INSERT INTO [t14280] VALUES (1654,'(تنبيه) زاد سراج الدين فقال: لقائل أن يقول: لما فسرت الوجوب بتعلق الخطاب، وقد اعترفت أول الكتاب بحدوثه- افتقر إلى سبب حادث','3459','1625','0');
INSERT INTO [t14280] VALUES (1655,'المسألة الثانية عشرة قال الرازي: ها هنا أبحاث','3469','1625','0');
INSERT INTO [t14280] VALUES (1656,'شرح القرافي: قوله: (إن جاز اجتماع الشرطين فهو محال)','3470','1625','0');
INSERT INTO [t14280] VALUES (1657,'(سؤال) قال النقشواني: لم يبين أن العلة واحدة بالنوع، أو بالشخص','3471','1625','0');
INSERT INTO [t14280] VALUES (1658,'(فائدة) قال سيف الدين: اختلفوا في العلة الواحدة الشرعية، هل يكون لها حكمان شرعيان؟','3472','1625','0');
INSERT INTO [t14280] VALUES (1659,'المسألة الثالثة عشرة قال الرازي: قد يستدل بذات العلة على الحكم، وقد يستدل بعلية العلة على الحكم','3473','1625','0');
INSERT INTO [t14280] VALUES (1660,'شرح القرافي: قوله: (الأمور الإضافية يتوقف ثبوتها على كل واحد من المضافين)','3473','1625','0');
INSERT INTO [t14280] VALUES (1661,'(سؤال) قال النقشواني: كلامه متناقض','3474','1625','0');
INSERT INTO [t14280] VALUES (1662,'(تنبيه) زاد سراج الدين فقال: لقائل أن يقول: صدق قولنا: القتل سبب لوجوب القصاص لا يتوقف على وجوب القصاص','3475','1625','0');
INSERT INTO [t14280] VALUES (1663,'المسألة الرابعة عشرة قال الرازي: تعليل الحكم العدمي بالوصف الوجودي لا يتوقف على بيان ثبوت المقتضي لذلك الحكم','3476','1625','0');
INSERT INTO [t14280] VALUES (1664,'القسم الثالث في المباحث المتعلقة بالحكم والأصل والفرع','3489','1463','0');
INSERT INTO [t14280] VALUES (1665,'الباب الأول في مباحث الحكم','3489','1664','0');
INSERT INTO [t14280] VALUES (1666,'الباب الثاني في شرائط الأصل','3530','1664','0');
INSERT INTO [t14280] VALUES (1667,'الباب الثالث "في الفرع"','3538','1664','0');
INSERT INTO [t14280] VALUES (1668,'الكلام في التعادل والترجيح','3557','0','0');
INSERT INTO [t14280] VALUES (1669,'القسم الأول في التعادل','3557','1668','0');
INSERT INTO [t14280] VALUES (1670,'القسم الثاني (في مقدمات الترجيح)','3579','1668','0');
INSERT INTO [t14280] VALUES (1671,'القسم الثالث في تراجيح الأخبار','3612','1668','0');
INSERT INTO [t14280] VALUES (1672,'سؤال: قال النقشواني: دعواه الندرة في علو الإسناد ليست على الإطلاق','3621','1671','0');
INSERT INTO [t14280] VALUES (1673,'القول في التراجيح الراجعة إلى حال ورود الخبر','3622','1671','0');
INSERT INTO [t14280] VALUES (1674,'شرح القرافي: قوله: (الغالب في الآيات المكيات ما كان قبل الهجرة)','3624','1671','0');
INSERT INTO [t14280] VALUES (1675,'فائدة: قال سيف الدين: رواية متقدم الإسلام مقدمة لقوة أصالته في الإسلام ..','3625','1671','0');
INSERT INTO [t14280] VALUES (1676,'القول في التراجيح الراجعة إلى اللفظ','3626','1671','0');
INSERT INTO [t14280] VALUES (1677,'شرح القرافي: قوله: (ترجيح الحقيقة على المجاز ضعيف','3629','1671','0');
INSERT INTO [t14280] VALUES (1678,'القول في التراجيح الراجعة إلى الحكم','3634','1671','0');
INSERT INTO [t14280] VALUES (1679,'القرافي: قوله: (لو جعلنا المبقى متقدما على الناقل، لكان واردا حيث لا يحتاج إليه)','3639','1671','0');
INSERT INTO [t14280] VALUES (1680,'فائدة: قال بعض العلماء: في الأحكام يقدم الحظر؛ لأن النهي يعتمد المفاسد','3641','1671','0');
INSERT INTO [t14280] VALUES (1681,'القول في الترجيحات الحاصلة بالأمور الخارجة','3643','1671','0');
INSERT INTO [t14280] VALUES (1682,'شرح القرافي: لم أجد في هذا القسم للمصنف ما يحتاج الكلام عليه','3644','1671','0');
INSERT INTO [t14280] VALUES (1683,'تنبيه: تقدم رواية أهل الحرمين وبعض المفهومات على بعض، فليطالع من هناك','3653','1671','0');
INSERT INTO [t14280] VALUES (1684,'القسم الرابع: في تراجيح الأقيسة','3654','1668','0');
INSERT INTO [t14280] VALUES (1685,'شرح القرافي: قوله: (الترجيح بالشبه بالعقل، وقلة الاختلاف ضعيف جدا"','3657','1684','0');
INSERT INTO [t14280] VALUES (1686,'"سؤال" قال النقشواني: قوله: (التعليل بالحكمة أولى من التعليل بالإضافي"','3659','1684','0');
INSERT INTO [t14280] VALUES (1687,'"سؤال" قال النقشواني: التعليل بالعدم إنما يعلل به إذا اشتمل على حكمة','3660','1684','0');
INSERT INTO [t14280] VALUES (1688,'"جوابه" أنا نعلل عدم المعلول بعدم العلة','3660','1684','0');
INSERT INTO [t14280] VALUES (1689,'النوع الثاني قال الرازي: (القول في التراجيح العائدة إلى ما يدل على أن ذات العلة موجودة"','3661','1684','0');
INSERT INTO [t14280] VALUES (1690,'شرح القرافي: قوله: (العلم بوجود العلة قد يكون بديهيا، أو حسيا، أو استدلاليا بعقل محض، أو نقل محض، أو مركب منهما"','3663','1684','0');
INSERT INTO [t14280] VALUES (1691,'"القول في التراجيح الحاصلة بسبب الطرق الدالة على علية الوصف في الأصل"','3666','1684','0');
INSERT INTO [t14280] VALUES (1692,'مسألة: الدوران الحاصل في صورة واحدة - راجح على الحاصل في صورتين','3672','1684','0');
INSERT INTO [t14280] VALUES (1693,'(مسألة) الدوران الحاصل في صورة راجح على الحاصل في صورتين','3676','1684','0');
INSERT INTO [t14280] VALUES (1694,'النوع الرابع (في الترجيح الحاصلة بسبب دليل الحكم في الأصل)','3677','1684','0');
INSERT INTO [t14280] VALUES (1695,'شرح القرافي: (بينا أن مسائل أصول الفقه القطعية يستدل فيها بالظواهر','3678','1684','0');
INSERT INTO [t14280] VALUES (1696,'النوع الخامس القول في التراجيح الحاصلة بسبب كيفية الحكم','3680','1684','0');
INSERT INTO [t14280] VALUES (1697,'شرح القرافي: قوله: (المثبت بحكم شرعي راجح على المثبت بحكم عقلي)','3682','1684','0');
INSERT INTO [t14280] VALUES (1698,'(سؤال) قال النقشواني: في قوله هاهنا: لو قدرنا تقديم العلة المثبتة للحكم الشرعي لزم النسخ مرتين، يناقضه ما تقدم له في تعارض خبرين','3685','1684','0');
INSERT INTO [t14280] VALUES (1699,'النوع السادس في التراجيح الحاصلة بسبب مكان العلة','3687','1684','0');
INSERT INTO [t14280] VALUES (1700,'شرح القرافي: قوله: (طرح ما قلت فائدته أولى)','3689','1684','0');
INSERT INTO [t14280] VALUES (1701,'(سؤال) وقع له في هذا الباب ترجيح المتعدية على القاصرة، مع أن القاصرة لا قياس فيها، ولا تعدية','3690','1684','0');
INSERT INTO [t14280] VALUES (1702,'(فائدة) قال إمام الحرمين في (البرهان): في ذلك ثلاثة مذاهب','3691','1684','0');
INSERT INTO [t14280] VALUES (1703,'(سؤال) قال النقشواني: ترجيحة العلة للأكثر فروعا على الأقل - يقتضي ترجيح التعليل بالمشترك على الفاروق، وهو باطل','3691','1684','0');
INSERT INTO [t14280] VALUES (1704,'(جوابه) أن إضافة الحكم للفاروق تكون إضافة له، وللمشترك، فلم يلزم إلغاء أحد المناسبتين','3691','1684','0');
INSERT INTO [t14280] VALUES (1705,'(فائدة) قال سيف الدين: في ترجيح الأقيسة ترجيحات','3692','1684','0');
INSERT INTO [t14280] VALUES (1706,'(فائدة) قال سيف الدين: من الترجيحات ترجيح الحدود، وذلك من وجوه','3694','1684','0');
INSERT INTO [t14280] VALUES (1707,'(فائدة) قال إمام الحرمين في (البرهان): إذا عضد أحد القياسين قول صحابي، إن علمنا مذهب الصحابي حجة كان ذلك انضمام دليل إلى دليل','3696','1684','0');
INSERT INTO [t14280] VALUES (1708,'الكلام في الاجتهاد','3699','0','0');
INSERT INTO [t14280] VALUES (1709,'الركن الأول في الاجتهاد','3699','1708','0');
INSERT INTO [t14280] VALUES (1710,'الركن الثاني في المجتهد','3704','1708','0');
INSERT INTO [t14280] VALUES (1711,'قوله: (يجوز أن يكون في أحكام الرسول عليه السلام ما صدر عن اجتهاد)','3717','1710','0');
INSERT INTO [t14280] VALUES (1712,'(سؤال) قوله: (إنه - عليه السلام - قادر على الوحي)','3727','1710','0');
INSERT INTO [t14280] VALUES (1713,'(سؤال) قوله: (مخالف الرسول - عليه السلام - كافر)','3728','1710','0');
INSERT INTO [t14280] VALUES (1714,'(سؤال) لو قلنا: إن الله - تعالى - جعل لجبريل - عليه السلام - الاجتهاد، كما جعله للنبي - عليه السلام - لم يلزم منه مفسدة','3728','1710','0');
INSERT INTO [t14280] VALUES (1715,'(سؤال) قوله: (لو جاز عليه الخطأ، لكنا مأمورين بالخطأ)','3728','1710','0');
INSERT INTO [t14280] VALUES (1716,'(تنبيه)','3728','1710','0');
INSERT INTO [t14280] VALUES (1717,'(فائدة) قال سيف الدين: (يدل على اجتهاده - عليه السلام - قوله تعالى:} وشاورهم في الأمر {','3731','1710','0');
INSERT INTO [t14280] VALUES (1718,'(مسألة) في الاجتهاد لغير رسول الله - صلى الله عليه وسلم -','3733','1710','0');
INSERT INTO [t14280] VALUES (1719,'(سؤال) هذا الكلام مشكل؛ فإنه حكى المنع من الوقوع مطلقا، ثم قال: وأجازه قوم بشرط الإذن','3736','1710','0');
INSERT INTO [t14280] VALUES (1720,'(فائدة) قال سيف الدين: الأكثرون على جواز الاجتهاد لمن عاصره - صلى الله عليه وسلم - ومنعه الأقلون','3737','1710','0');
INSERT INTO [t14280] VALUES (1721,'(مسألة) في شرائط المجتهد','3738','1710','0');
INSERT INTO [t14280] VALUES (1722,'(تنبيه) قال التبريزي: يكفي من النحو واللغة الذي يحصل الفهم من مقاصد الكلام دون التغلغل في مشكلات سرائره','3747','1710','0');
INSERT INTO [t14280] VALUES (1723,'(فائدة) قال سيف الدين: وشرط المجتهد أن يكون عالما بوجود الله - تعالى - وما يجب له من الصفات','3749','1710','0');
INSERT INTO [t14280] VALUES (1724,'الركن الثالث المجتهد فيه','3751','1708','0');
INSERT INTO [t14280] VALUES (1725,'شرح القرافي: قوله: (اخترزنا بقولنا: ليس فيه دليل قاطع من وجوب الصلوات الخمس، ونحوها)','3751','1724','0');
INSERT INTO [t14280] VALUES (1726,'الركن الرابع حكم الاجتهاد','3753','1708','0');
INSERT INTO [t14280] VALUES (1727,'شرح القرافي: قوله: (وضع الله - تعالى - على هذه المطالب أدلة يقينية، ومكن العقلاء من معرفتها)','3778','1726','0');
INSERT INTO [t14280] VALUES (1728,'(سؤال) الآيات الواردة في ذم الظن يرد عليها أن الاعتقاد الجازم، وإن لم يطابق، فصاحبه لا يجوز خلافه','3784','1726','0');
INSERT INTO [t14280] VALUES (1729,'(تنبيه) قال التبريزى على قوله: (إن الله - تعالى - نصب على هذه المطالب أدلة قطعية): إنه ضعيف','3785','1726','0');
INSERT INTO [t14280] VALUES (1730,'(فائدة) قال الغزالي في (المستصفى): مسائل أصول الفقه نحو كون الإجماع حجة ..','3786','1726','0');
INSERT INTO [t14280] VALUES (1731,'(مسألة) اختلفوا في تصويب المجتهدين','3787','1726','0');
INSERT INTO [t14280] VALUES (1732,'(تنبيه) مذهب القياسين والفقهاء أن الراجح مصالح','3812','1726','0');
INSERT INTO [t14280] VALUES (1733,'(فائدة) قال سيف الدين: (وافق بشر المريسي على التأثيم ابن علية، وأبو بكر الأصم، ونفاة القياس كالظاهرية، والإمامية','3816','1726','0');
INSERT INTO [t14280] VALUES (1734,'(مسألة)','3818','1726','0');
INSERT INTO [t14280] VALUES (1735,'(فائدة) قال بعض المشايخ: إن السيف الآمدي قال: (الدليل على أن المصيب واحد، أن بعض المجتهدين أداه اجتهاده إلى أنه ليس كل مجتهد مصيبا','3818','1726','0');
INSERT INTO [t14280] VALUES (1736,'(فائدة) قال الشيخ عز الدين بن عبد السلام رحمه الله: (معنى قوله عليه السلام: (إذا اجتهد الحاكم فأخطأ فله أجر، وإن أصاب فله أجران) - أن الخطأ محمول على الخطأ في الأسباب','3819','1726','0');
INSERT INTO [t14280] VALUES (1737,'(مسألة) في نقض الاجتهاد','3819','1726','0');
INSERT INTO [t14280] VALUES (1738,'(تنبيه) قال التبريزي: (يجب على المجتهد ترك موجب اجتهاده لأجل حكم الحاكم في الظاهر، وفيما يحل له في الباطن)','3821','1726','0');
INSERT INTO [t14280] VALUES (1739,'(تنبيه) وقع في (التنبيه) للشيخ أبي إسحاق ما يشير إلى إمكان النقض','3821','1726','0');
INSERT INTO [t14280] VALUES (1740,'(تنبيه) الفرق بين ما يحدث من الحكم بالنذر، وبين ما يحدث بحكم الحاكم','3822','1726','0');
INSERT INTO [t14280] VALUES (1741,'(فائدة) قال سيف الدين: (اتفقوا على أن حكم الحاكم لا يجوز نقضه في المسائل الاجتهادية، لمصلحة الحكم','3823','1726','0');
INSERT INTO [t14280] VALUES (1742,'الكلام في المفتي والمستفتي','3825','0','0');
INSERT INTO [t14280] VALUES (1743,'القسم الأول في المفتي، وفيه مسائل','3825','1742','0');
INSERT INTO [t14280] VALUES (1744,'شرح القرافي: قوله: (إذا سئل ثانيا، وهو ذاكر للطريق الأول، فهو مجتهد يجوز له الفتيا)','3827','1743','0');
INSERT INTO [t14280] VALUES (1745,'(فائدة) قال سيف الدين: التقليد هو العمل بقول الغير من غير حجة ملزمة','3829','1743','0');
INSERT INTO [t14280] VALUES (1746,'(فائدة) قال سيف الدين: (يشترط في المفتى شروط الاجتهاد مع العدالة حتى يوثق به','3830','1743','0');
INSERT INTO [t14280] VALUES (1747,'(فائدة) قال سيف الدين: (اختلفوا في جواز الاستفتاء والتقليد في المسائل العلمية الأصولية في العقائد','3830','1743','0');
INSERT INTO [t14280] VALUES (1748,'(فائدة) قال سيف الدين: (إذا أفتى، ثم حدث مثل تلك الواقعة اختلفوا','3832','1743','0');
INSERT INTO [t14280] VALUES (1749,'(مسألة) (هل يجوز لغير المجتهد الفتوى بما يحكيه)؟','3833','1743','0');
INSERT INTO [t14280] VALUES (1750,'(سؤال) الموجب لاعتبار قول الميت، والاعتماد عليه صدوره عمن اتصف بأهلية الاجتهاد، وذلك لا يضاد طريان الغفلة','3834','1743','0');
INSERT INTO [t14280] VALUES (1751,'(سؤال) قال النقشواني: (علي - رضي الله عنه - مجتهد، ورواية المقداد له من باب روايات الأخبار للمجتهدين، وذلك ليس من باب الفتوى)','3835','1743','0');
INSERT INTO [t14280] VALUES (1752,'(تنبيه) قال التبريزى: قد يحتج في المجتهد ..','3835','1743','0');
INSERT INTO [t14280] VALUES (1753,'(فائدة) قال سيف الدين: (من ليس بمجتهد، هل يجوز له الفتوى بمذهب الجمهور كما في زماننا)؟','3836','1743','0');
INSERT INTO [t14280] VALUES (1754,'(فائدة) ينبغي أن يحذر مما وقع في زماننا من تساهل بعض الفقهاء بالفتوى من الكتب الغريبة التي ليس فيها رواية المفتى عن المجتهد بالسند الصحيح','3838','1743','0');
INSERT INTO [t14280] VALUES (1755,'القسم الثاني في المستفتي','3839','1742','0');
INSERT INTO [t14280] VALUES (1756,'شرح القرافى: قوله: (لو صحت تلك الدلالة لوجب القول بجواز التقليد في مسائل الأصول؛ لأنا نعلم أن الوقوف على تلك الدلائل لا يحصل إلا بعد الكد الكثير)','3851','1755','0');
INSERT INTO [t14280] VALUES (1757,'(تنبيه) قال التبريزى: (الشيعة هم الذين قالوا بحصر كل مدرك إلا الدليل السمعي القاطع ...','3855','1755','0');
INSERT INTO [t14280] VALUES (1758,'(فائدة) قال سيف الدين: (العامي، ومن ليس له أهلية الاجتهاد إذا كان محصلا لبعض العلوم المعتبرة في الاجتهاد يلزمه اتباع قول المجتهد','3856','1755','0');
INSERT INTO [t14280] VALUES (1759,'القسم الثالث فيما فيه الاستفتاء','3858','1742','0');
INSERT INTO [t14280] VALUES (1760,'شرح القرافى: قوله: (منهم من أوجب الأخذ بقول الأعلم، وهو الأقرب)','3860','1759','0');
INSERT INTO [t14280] VALUES (1761,'(فائدة) قال سيف الدين: (اتفقوا على جواز تقليد العامي لمن عرفه بالعلم، وأهلية الاجتهاد، وعلى امتناع اتباعه لمن عرفه بالضد، واختلفوا في جواز استفتاء من لم يعرفه بعلم، ولا جهالة)','3862','1759','0');
INSERT INTO [t14280] VALUES (1762,'(مسألة) الرجل الذي تنزل به الواقعة إما أن يكون عاميا صرفا','3862','1759','0');
INSERT INTO [t14280] VALUES (1763,'(سؤال) على استدلالهم بقوله تعالى: {أطيعوا الله وأطيعوا الرسول وأولى الأمر منكم}','3866','1759','0');
INSERT INTO [t14280] VALUES (1764,'(سؤال) على قوله: (الآيات دالة على الوجوب)','3866','1759','0');
INSERT INTO [t14280] VALUES (1765,'(تنبيه) زاد التبريزى: فقال: (المجتهد لو جوز أن يكون عند غيره نص يل على حكم الواقعة، وجب عليه طلبه، ولم يجز له الاجتهاد','3867','1759','0');
INSERT INTO [t14280] VALUES (1766,'(مسألة) (لا يجوز التقليد في أصول الدين)','3868','1759','0');
INSERT INTO [t14280] VALUES (1767,'(تنبيه) قال التبريزى: استدلال المصنف باطل من وجهين','3870','1759','0');
INSERT INTO [t14280] VALUES (1768,'(مسألة) قال سيف الدين: إذا اتبع العامي مجتهدا في حكم صلاته، وعمل بقوله، اتفقوا على أن ليس له الرجوع عنه في ذلك الحكم','3873','1759','0');
INSERT INTO [t14280] VALUES (1769,'(فائدة) كان الشيخ عز الدين يقول: (حيث قلنا بجواز التقليد، والانتقال في المذاهب؛ فإنما نقول به فيما لا ينقض فيه قضاء القاضي','3875','1759','0');
INSERT INTO [t14280] VALUES (1770,'(فائدة) إذا لم نجعله شرعا، وكان في المسألة قولان: هل يصير القول الأخير مجمعا عليه','3875','1759','0');
INSERT INTO [t14280] VALUES (1771,'(فائدة) قال بعض العلماء: (لا يجوز اتباع رخص المذاهب، بل يجوز الانتقال إلى مذهب بكماله)','3876','1759','0');
INSERT INTO [t14280] VALUES (1772,'(فائدة)','3876','1759','0');
INSERT INTO [t14280] VALUES (1773,'(مسألة) قال إمام الحرمين في (البرهان): (أجمع المحققون على أن العوام ليس لهم أن يتعلقوا بمذهب أعيان الصحابة -رضي الله عنهم -بل عليهم أن يتبعوا مذاهب الأئمة','3877','1759','0');
INSERT INTO [t14280] VALUES (1774,'(مسألة) قال سيف الدين: (اختلفوا هل يجوز خلو عصر من الأعصار عن مجتهد يمكن تفويض الفتاوى إليه؟','3878','1759','0');
INSERT INTO [t14280] VALUES (1775,'(مسألة) قال الشيخ أبو إسحاق في (اللمع): من اجتمعت فيه شرائط الفتوى؛ فإن كان في الإقليم غيره، لم يتعين عليه الفتيا، والتعليم؛ بل هو فرض كفاية','3880','1759','0');
INSERT INTO [t14280] VALUES (1776,'(مسألة) قال الشيخ: أبو إسحاق في (اللمع): (لا يجوز للمستفتى أن يستفتى كل من يتزيا بزى أهل العلم','3881','1759','0');
INSERT INTO [t14280] VALUES (1777,'(مسألة) قال أبو الخطاب الحنبلي في (التمهيد): (لله -تعالى -على الأحكام أدلة من الكتاب، أو السنة، أو القياس، خلاف لمن قال: لا دليل على الحكم سوى ظن المجتهد','3881','1759','0');
INSERT INTO [t14280] VALUES (1778,'(مسألة) قال أبو الخطاب الحنبلي في (التمهيد): (إذا أفتاه، ولم يعمل بفتواه حتى مات المفتى، فهل يجوز له العمل بما أفتاه؟','3882','1759','0');
INSERT INTO [t14280] VALUES (1779,'(مسألة) قال العالمي الحنفي في كتابه: إذا اعتدل القولان عن المفتى اختلفوا فيه','3882','1759','0');
INSERT INTO [t14280] VALUES (1780,'(مسألة) قال ابن برهان في كتاب (الأوسط): (إذا استفتى العامي في حادثة، ثم حدثت تلك الحادثة مرة أخرى، هل يجوز له الاكتفاء بتلك الفتوى؟','3882','1759','0');
INSERT INTO [t14280] VALUES (1781,'(مسألة) قال ابن برهان في كتاب (الأوسط): (لا يجوز تقليد المجتهد الميت، مع وجود المجتهد الحى؛ لأن الحى أولى)','3883','1759','0');
INSERT INTO [t14280] VALUES (1782,'(مسألة) قال ابن برهان: (من كان مقلدا لصاحب مذهب، وقد أحاط بنصوص ذلك المذهب، وكان مجتهدا بالقياس، كان مجتهدا في ذلك المذهب','3883','1759','0');
INSERT INTO [t14280] VALUES (1783,'(مسألة) قال ابن برهان في كتاب (الأوسط): لا شك أن أهل العترة من أهل الاجتهاد','3883','1759','0');
INSERT INTO [t14280] VALUES (1784,'الكلام فيما اختلف فيه المجتهدون من أدلة الشرع','3885','0','0');
INSERT INTO [t14280] VALUES (1785,'المسألة الأولى: في حكم الأفعال','3885','1784','0');
INSERT INTO [t14280] VALUES (1786,'شرح القرافي: قوله: (قال النحاة: (اللام للتمليك)','3893','1785','0');
INSERT INTO [t14280] VALUES (1787,'(سؤال) على استدلاله بالآية','3909','1785','0');
INSERT INTO [t14280] VALUES (1788,'(سؤال) على قوله: (تلك الحكمة إما عود النفع إليه، أو إلينا)','3909','1785','0');
INSERT INTO [t14280] VALUES (1789,'(تنبيه) قياسه -في هذه المسألة -في قوله: (انتفاع لا ضرر فيه على المالك قطعا، ولا على المنتفع ظاهرا، فيباح، كالاستصباح بسراج الغير)','3910','1785','0');
INSERT INTO [t14280] VALUES (1790,'(سؤال) على الاستدلال بالحديث','3910','1785','0');
INSERT INTO [t14280] VALUES (1791,'(تنبيه) قال التبريزى: (النفع هو الزيادة من الوجه الموافق للمصلحة','3911','1785','0');
INSERT INTO [t14280] VALUES (1792,'المسألة الثانية في استصحاب الحال','3913','1784','0');
INSERT INTO [t14280] VALUES (1793,'شرح القرافي: قوله: (الباقي كان ممكنا، والإمكان لازم له، فكل ممكن مفتقر للمؤثر)','3927','1792','0');
INSERT INTO [t14280] VALUES (1794,'(سؤال)','3932','1792','0');
INSERT INTO [t14280] VALUES (1795,'(تنبيه) قال التبريزى: (الاستصحاب ينقسم إلى','3932','1792','0');
INSERT INTO [t14280] VALUES (1796,'(فائدة) قال سيف الدين: (في الاستصحاب مذهب ثالث، وهو الترجيح به دون كونه دليلا)','3934','1792','0');
INSERT INTO [t14280] VALUES (1797,'(فرع) قال في (المحصول): (النافي لا دليل عليه)','3936','1792','0');
INSERT INTO [t14280] VALUES (1798,'المسألة الثالثة في الاستحسان','3937','1784','0');
INSERT INTO [t14280] VALUES (1799,'القرافي: قال النقشواني: أما ترتيبه كما ذكره أبو الحسين، ففيه نظر','3945','1798','0');
INSERT INTO [t14280] VALUES (1800,'(تنبيه) قال التبريزي: الكلام في صحة الاستحسان وفساده ينبني على فهم حقيقته','3948','1798','0');
INSERT INTO [t14280] VALUES (1801,'المسألة الرابعة قال الرازي: الحق أن قول الصحابي ليس بحجة، وقال قوم: إنه حجة مطلقا','3949','1784','0');
INSERT INTO [t14280] VALUES (1802,'القرافي: قوله: (أمر الله -تعالى -بالاعتبار، وذلك ينافى التقليد)','3953','1801','0');
INSERT INTO [t14280] VALUES (1803,'(فائدة) قال سيف الدين: (اتفقوا على أن مذهب الصحابي -في مسائل الاجتهاد -لا يكون حجة على غيره من الصحابة المجتهدين','3956','1801','0');
INSERT INTO [t14280] VALUES (1804,'(فائدة) قال الشيخ موفق الدين الحنبلي في كتاب (الروضة): (إذا اختلف الصحابة على قولين، لم يجز للمجتهد الأخذ بقول بعضهم من غير دليل','3956','1801','0');
INSERT INTO [t14280] VALUES (1805,'المسألة الخامسة قال الرازي: اختلفوا في أنه، هل يجوز أن يقول الله تعالى للنبي - صلى الله عليه وسلم - أو للعالم: (احكم، فإنك لا تحكم إلا بالصواب؟)','3958','1784','0');
INSERT INTO [t14280] VALUES (1806,'شرح القرافي: قوله: (إذا جعل الاختيار فيما تتم به المصلحة، وخيره بين الفعل والترك، سقط التكليف)','3967','1805','0');
INSERT INTO [t14280] VALUES (1807,'(فائدة) قال أبو يعلى الحنبلي في (العمدة): هذه المسألة مبنية على أنه - عليه السلام - هل له أن يجتهد أم لا؟','3978','1805','0');
INSERT INTO [t14280] VALUES (1808,'المسألة السادسة مذهب الشافعي - رضي الله عنه -: أنه يجوز الاعتماد في إثبات الأحكام على الأخذ بأقل ما قيل','3979','1784','0');
INSERT INTO [t14280] VALUES (1809,'شرح القرافي: قوله: (الثلث قال به كل الأمة)','3981','1808','0');
INSERT INTO [t14280] VALUES (1810,'(فائدة) قال القاضي عبد الوهاب المالكي في (الملخص): (صورة هذه المسألة أن يجني رجل على سلعة، فيختلف المقومون في تقويمها','3982','1808','0');
INSERT INTO [t14280] VALUES (1811,'المسألة السابعة قال الرازي: قال قوم: يجب على المكلف الأخذ بأخف القولين؛ للنص والمعقول','3984','1784','0');
INSERT INTO [t14280] VALUES (1812,'شرح القرافي: قوله: (وأما القياس، والتحامل على المعنى أولى من الفقير)','3985','1811','0');
INSERT INTO [t14280] VALUES (1813,'المسألة الثامنة الاستقراء المظنون: هو إثبات الحكم في كلى؛ لثبوته في بعض جزئياته','3987','1784','0');
INSERT INTO [t14280] VALUES (1814,'شرح القرافي','3987','1813','0');
INSERT INTO [t14280] VALUES (1815,'(سؤال) الاستدلال على عدم وجوب الوتر بكونه - صلى الله عليه وسلم - كان يؤديه على الراحلة مشكل','3989','1813','0');
INSERT INTO [t14280] VALUES (1816,'المسألة التاسعة في المصالح المرسلة','3990','1784','0');
INSERT INTO [t14280] VALUES (1817,'شرح القرافي: قوله: (ومن المصالح ما شهد الشرع ببطلانه','3993','1816','0');
INSERT INTO [t14280] VALUES (1818,'(سؤال) قال بعض علماء العصر: إذا قلتم بالمصلحة المرسلة، فكيف تصنعون في العمومات، والأدلة؟','4005','1816','0');
INSERT INTO [t14280] VALUES (1819,'(سؤال) ما الفرق بين المصلحة المرسلة، والاستحسان','4006','1816','0');
INSERT INTO [t14280] VALUES (1820,'المسألة العاشرة الاستدلال بعدم ما يدل على الحكم؛ على عدم الحكم - طريقة عول عليها بعض الفقهاء','4010','1784','0');
INSERT INTO [t14280] VALUES (1821,'شرح القرافي: قوله: (قصة معاذ تدل على انحصار الأدلة في الكتاب، والسنة، والإجماع، والقياس)','4017','1820','0');
INSERT INTO [t14280] VALUES (1822,'المسألة الحادية عشرة في تقرير وجوه من الأدلة التي يمكن التمسك بها في المسائل الفقهية','4019','1784','0');
INSERT INTO [t14280] VALUES (1823,'القرافي: قوفه: (المحكوم عليه كان معدوما في الأزل، فلا يكون الحكم ثابتا في الأزل؛ لأن ثبوت الحكم صفة الله - تعالى - وكلامه النفسي القديم','4025','1822','0');
INSERT INTO [t14280] VALUES (1824,'(تنبيه) قال التبريزي: (الحكم الشرعي لابد له من دليل شرعي، وهو قول الشارع','4028','1822','0');
