INSERT INTO [t14299] VALUES (1,'مقدمة','3','0','0');
INSERT INTO [t14299] VALUES (2,'فائدة [هل يتعدى "سمع" إلى مفعولين؟]','6','1','0');
INSERT INTO [t14299] VALUES (3,'فائدة [في قولنا "رضي الله عنه" و"رضوان الله عليه"]','7','1','0');
INSERT INTO [t14299] VALUES (4,'مسند أبي بن كعب رضي الله عنه','8','0','0');
INSERT INTO [t14299] VALUES (5,'١ - حديث "ففضت عرقا وكأنما أنظر إلى الله فرقا"','8','4','0');
INSERT INTO [t14299] VALUES (6,'٢ - حديث اللقطة "فإن جاء صاحبها وإلا استمتع بها".','12','4','0');
INSERT INTO [t14299] VALUES (7,'٣ - حديث: "يغسل ما مس المرأة منه".','12','4','0');
INSERT INTO [t14299] VALUES (8,'٤ - حديث موسى والخضر','13','4','0');
INSERT INTO [t14299] VALUES (9,'٥ - حديث: "فرج سقف بيتي" الحديث.','17','4','0');
INSERT INTO [t14299] VALUES (10,'٦ - حديث: "أتدري أي آية في كتاب الله معك أعظم".','18','4','0');
INSERT INTO [t14299] VALUES (11,'٧ - حديث: "أنه سأل رسول الله صلى الله عليه وسلم عن سورة وعده أن يعلمه إياها، فقال أبي: "فقلت: السورة التي قلت لي".','18','4','0');
INSERT INTO [t14299] VALUES (12,'٨ - حديث: "كان رسول الله صلى الله عليه وسلم يعلمنا إذا أصبحنا أصبحنا على فطرة لإسلام، وكلمة الإخلاص، وسنة نبينا صلى الله عليه وسلم، وملة أبينا إبراهيم حنيفا مسلما وما كان من المشركين".','19','4','0');
INSERT INTO [t14299] VALUES (13,'٩ - حديث "كأين تقرأ سورة الأحزاب أو كأين تعدها؟ قال: ثلاثا وسبعين آية. قال: قط".','19','4','0');
INSERT INTO [t14299] VALUES (14,'١٠ - حديث: "أنهم جمعوا القرآن في مصاحف في خلافة أبي بكر، وكان رجال يكتبون، ويمل عليهم .. ".','21','4','0');
INSERT INTO [t14299] VALUES (15,'١١ - حديث: "لما كان يوم الفتح قال رجل: لا قريش بعد اليوم".','21','4','0');
INSERT INTO [t14299] VALUES (16,'١٢ - حديث: "إن مطعم ابن آدم جعل مثلا للدنيا، وإن قزحه وملحه، فانظروا إلى ما يصير".','25','4','0');
INSERT INTO [t14299] VALUES (17,'١٣ - حديث: "جاءت الراجفة تتبعها الرادفة".','26','4','0');
INSERT INTO [t14299] VALUES (18,'١٤ - حديث: "مثلي في النبيين كمثل رجل بنى دارا فأحسنها وأكملها وترك فيها موضع لبنة لم يضعها، فجعل الناس يطوفون بالبنيان ويعجبون منه ويقولون: لو تم موضع اللبنة".','26','4','0');
INSERT INTO [t14299] VALUES (19,'١٥ - حديث: "قال معبد: أي رسول الله، يخشى على من شبهه".','27','4','0');
INSERT INTO [t14299] VALUES (20,'١٦ - حديث شرح الصدر','27','4','0');
INSERT INTO [t14299] VALUES (21,'١٧ - حديث: "إذا كان يوم القيامة كنت إمام النبيين وخطيبهم وصاحب شفاعتهم غير فخر".','28','4','0');
INSERT INTO [t14299] VALUES (22,'١٨ - حديث: "يوشك الفرات أن يحسر عن جبل من ذهب".','28','4','0');
INSERT INTO [t14299] VALUES (23,'١٩ - حديث: "صلى بنا رسول الله صلى الله عليه وسلم الصبح فقال: شاهد فلان؟ فقالوا: لا".','29','4','0');
INSERT INTO [t14299] VALUES (24,'٢٠ - حديث الصدقة.','30','4','0');
INSERT INTO [t14299] VALUES (25,'مسند أبي بن مالك رضي الله عنه','32','0','0');
INSERT INTO [t14299] VALUES (26,'٢١ - حديث "من أدرك والديه أو أحدهما ثم دخل النار من بعد فأبعده الله وأسحقه".','32','25','0');
INSERT INTO [t14299] VALUES (27,'مسند أحمر بن جزء','33','0','0');
INSERT INTO [t14299] VALUES (28,'٢٢ - حديث "إن كنا لنأوي لرسول الله صلى الله عليه وسلم [مما]، يجافي مرفقيه عن جنبيه [إذا سجد] ".','33','27','0');
INSERT INTO [t14299] VALUES (29,'مسند أسامة بن زيد رضي الله عنهما','34','0','0');
INSERT INTO [t14299] VALUES (30,'٢٣ - حديث "قلت: يا رسول الله، إنك تصوم حتى لا تكاد تفطر، وتفطر حتى لا تكاد تصوم إلا يومين. قال: أي يومين؟ ".','34','29','0');
INSERT INTO [t14299] VALUES (31,'٢٤ - حديث "فقال عبد الله بن أبي: لا أحسن من هذا".','35','29','0');
INSERT INTO [t14299] VALUES (32,'٢٥ - حديث "قد كنت أنهاك عن حب يهود".','36','29','0');
INSERT INTO [t14299] VALUES (33,'٢٦ - حديث قال: "رويدا أيها الناس عليكم السكينة".','36','29','0');
INSERT INTO [t14299] VALUES (34,'٢٧ - حديث " قلت: يا رسول الله الصلاة. قال: الصلاة أمامك ".','38','29','0');
INSERT INTO [t14299] VALUES (35,'٢٨ - حديث "ألا شققت عن قلبه".','38','29','0');
INSERT INTO [t14299] VALUES (36,'٢٩ - حديث "إن رجلا قال: يا رسول الله، إني أعزل عن امرأتي. قال: ولم؟ قال: شفقا على ولدها. فقال: إن كان ذلك فلا، ما ضار ذلك فارس والروم".','39','29','0');
INSERT INTO [t14299] VALUES (37,'٣٠ - حديث "لم يأتني جبريل منذ ثلاث".','39','29','0');
INSERT INTO [t14299] VALUES (38,'٣١ - حديث "قمت على باب الجنة','39','29','0');
INSERT INTO [t14299] VALUES (39,'٣٢ - حديث الطاعون "وإذا وقع بأرض وانتم بها فلا تخرجوا، لا يخرجكم إلا فرارا منه".','40','29','0');
INSERT INTO [t14299] VALUES (40,'٣٣ - حديث "إنما يرحم الله من عباده الرحماء".','41','29','0');
INSERT INTO [t14299] VALUES (41,'مسند أسامة بن شريك رضي الله عنه','41','0','0');
INSERT INTO [t14299] VALUES (42,'٣٤ - حديث "أتيت النبي صلى الله عليه وسلم وأصحابه عنده كأنما على رؤوسهم الطير".','41','41','0');
INSERT INTO [t14299] VALUES (43,'مسند أسامة بن عمير الهذلي أبي المليح رضي الله عنهما','43','0','0');
INSERT INTO [t14299] VALUES (44,'٣٥ - حديث "فأمر رسول الله صلى الله عليه وسلم مناديه أن الصلاة في الرحال".','43','43','0');
INSERT INTO [t14299] VALUES (45,'مسند أبي رافع مولى رسول الله صلى الله عليه وسلم','44','0','0');
INSERT INTO [t14299] VALUES (46,'٣٦ - حديث "فقال أف لك" وفى حديث ابن عباس "فجاء ينفض ثوبه ويقول: أف وتف".','44','45','0');
INSERT INTO [t14299] VALUES (47,'٣٧ - حديث "إنا آل محمد لا تحل لنا الصدقة".','45','45','0');
INSERT INTO [t14299] VALUES (48,'٣٨ - حديث "أما إنك لو سكت لناولتني ذراعا فذراعا ما سكت".','46','45','0');
INSERT INTO [t14299] VALUES (49,'٣٩ - حديث "لا ألفين أحدكم متكئا على أريكته".','46','45','0');
INSERT INTO [t14299] VALUES (50,'مسند أسيد بن حضير رضي الله عنه','47','0','0');
INSERT INTO [t14299] VALUES (51,'٤٠ - حديث "بينما هو يقرأ في الليل سورة البقرة وفرسه مربوطة إذ جالت الفرس','47','50','0');
INSERT INTO [t14299] VALUES (52,'مسند أسيد بن ظهير رضي الله عنه','48','0','0');
INSERT INTO [t14299] VALUES (53,'٤١ - حديث "الصلاة في مسجد قباء كعمرة".','48','52','0');
INSERT INTO [t14299] VALUES (54,'مسند الأسود بن سريع رضي الله عنه','49','0','0');
INSERT INTO [t14299] VALUES (55,'٤٢ - حديث "هات ما امتدحت به ربك".','49','54','0');
INSERT INTO [t14299] VALUES (56,'مسند الأشعث بن قيس الكندي رضي الله عنه','50','0','0');
INSERT INTO [t14299] VALUES (57,'٤٣ - حديث "لا يشكر الله من لا يشكر الناس".','50','56','0');
INSERT INTO [t14299] VALUES (58,'٤٤ - حديث "كان بيني وبين رجل خصومة في شيء فاختصمنا إلى النبي صلى الله عليه وسلم، فقال: شاهداك أو يمينه".','51','56','0');
INSERT INTO [t14299] VALUES (59,'٤٥ - حديث "فقال رسول الله صلى الله عليه وسلم: بينتك أنها بيرك وإلا فيمينه".','52','56','0');
INSERT INTO [t14299] VALUES (60,'مسند الأغر المزني رضي الله عنه','52','0','0');
INSERT INTO [t14299] VALUES (61,'٤٦ - حديث "إنه ليغان على قلبي".','52','60','0');
INSERT INTO [t14299] VALUES (62,'مسند أمية بن مخشي الخزاعي رضي الله عنه','53','0','0');
INSERT INTO [t14299] VALUES (63,'٤٧ - حديث "بسم الله أوله وآخره".','53','62','0');
INSERT INTO [t14299] VALUES (64,'مسند أنس بن مالك (رضي الله عنه)','53','0','0');
INSERT INTO [t14299] VALUES (65,'٤٨ - حديث الشفاعة، قوله: "يجمع المؤمنون يوم القيامة فيلهمون ذلك".','53','64','0');
INSERT INTO [t14299] VALUES (66,'٤٩ - حديث الغار، قوله: "إنه كان لي والدان فكنت أحلب لهما في إنائهما فآتيهما، فإذا وجدتهما راقدين قمت على رؤوسهما كراهية أن أرد سنتهما في رؤوسهما حتى يستيقظان متى استيقظا".','56','64','0');
INSERT INTO [t14299] VALUES (67,'٥٠ - حديث الأوعية، قوله: "فالرصاص والقارورة، قال: ما بأس بهما".','57','64','0');
INSERT INTO [t14299] VALUES (68,'٥١ - حديث "لا تزال جهنم تقول "هل من مزيد" حتى يضع فيها رب العزة قدمه، فتقول: "قط قط وعزتك".','57','64','0');
INSERT INTO [t14299] VALUES (69,'٥٢ - حديث "قوموا فلأصلي لكم".','58','64','0');
INSERT INTO [t14299] VALUES (70,'٥٣ - حديث "مر النبي صلى الله عليه وسلم بتمرة مسقوطة".','59','64','0');
INSERT INTO [t14299] VALUES (71,'٥٤ - : حديث "ما صليت وراء إمام قط أخف صلاة".','60','64','0');
INSERT INTO [t14299] VALUES (72,'٥٥ - حديث: "إن النبي صلى الله عليه وسلم وأبا بكر وعمر كانوا يفتتحون الصلاة بالحمد لله رب العالمين".','61','64','0');
INSERT INTO [t14299] VALUES (73,'٥٦ - حديث: "لقد رأيت الآن منذ صليت لكم الجنة والنار".','61','64','0');
INSERT INTO [t14299] VALUES (74,'٥٧ - حديث "يؤتي بالرجل يوم القيامة من أهل الجنة، فيقول له: يا بن آدم كيف وجدت منزلك؟ فيقول: أي رب خير منزل".','62','64','0');
INSERT INTO [t14299] VALUES (75,'٥٨ - حديث "إن رسول الله صلى الله عليه وسلم صعد أحدا فتبعه أبو بكر وعمر وعثمان فرجف بهم، فقال: اسكن نبي وصديق وشهيدان".','62','64','0');
INSERT INTO [t14299] VALUES (76,'٥٩ - حديث "لا عدوى ولا طيرة".','62','64','0');
INSERT INTO [t14299] VALUES (77,'٦٠ - حديث "إنه الإيمان حب الأنصار وإنه النفاق بغضهم".','63','64','0');
INSERT INTO [t14299] VALUES (78,'٦١ - حديث "إني لأول الناس تنشق الأرض عن جمجمتي يوم القيامة ولا فخر".','64','64','0');
INSERT INTO [t14299] VALUES (79,'٦٢ - حديث "إن الشيطان يجري من ابن آدم مجرى الدم".','64','64','0');
INSERT INTO [t14299] VALUES (80,'٦٣ - حديث "إن استطعت أن تصبح وتمسي ليس في قلبك غش لأحد من المسلمين فافعل".','64','64','0');
INSERT INTO [t14299] VALUES (81,'٦٤ - حديث "قدم علي على النبي خيالها من اليمن، فقال: بما أهللت؟ ".','65','64','0');
INSERT INTO [t14299] VALUES (82,'٦٥ - حديث "ولا تنقشوا في خواتيمكم عربي".','66','64','0');
INSERT INTO [t14299] VALUES (83,'٦٦ - حديث "وأيمن الذي نفسي بيده لو رأيتم ما رأيت لضحكتم قليلا ولبكيتم كثيرا".','66','64','0');
INSERT INTO [t14299] VALUES (84,'٦٧ - حديث "إنه أنزلت علي آنفا سورة" وفي حديث جرير "ذكرك آنفا".','67','64','0');
INSERT INTO [t14299] VALUES (85,'٦٨ - حديث "تلك صلاة المنافق، يجلس يرقب الشمس حتى إذا اصفرت قام فنقر أربعا لا يذكر الله فيها إلا قليلا".','68','64','0');
INSERT INTO [t14299] VALUES (86,'٦٩ - حديث "يتبع الميت ثلاث أهله وماله وعمله، فيرجع اثنان ويبقى واحد".','69','64','0');
INSERT INTO [t14299] VALUES (87,'٧٠ - حديث "لا يؤمن أحدكم حتى يحب لأخيه ما يحب لنفسه".','69','64','0');
INSERT INTO [t14299] VALUES (88,'٧١ - حديث "سألت الله عز وجل أيما إنسان من أمتي دعوت عليه أن يجعلها له مغفرة".','70','64','0');
INSERT INTO [t14299] VALUES (89,'٧٢ - حديث "كان يدخل الخلاء".','70','64','0');
INSERT INTO [t14299] VALUES (90,'٧٣ - حديث "لغدوة في سبيل الله أو روحة خير من الدنيا وما فيها".','71','64','0');
INSERT INTO [t14299] VALUES (91,'٧٤ - حديث "من نسى صلاة أو نام عنها".','71','64','0');
INSERT INTO [t14299] VALUES (92,'٧٥ - حديث "العيادة فواق ناقة".','71','64','0');
INSERT INTO [t14299] VALUES (93,'٧٦ - حديث "لبيك عمرة وحجا".','71','64','0');
INSERT INTO [t14299] VALUES (94,'٧٧ - حديث "كان النبي صلى الله عليه وسلم يصلي في نعليه".','72','64','0');
INSERT INTO [t14299] VALUES (95,'٧٨ - حديث الإسراء، قوله: "قالوا مرحبا به وأهلا".','72','64','0');
INSERT INTO [t14299] VALUES (96,'٧٩ - حديث "رصوا صفوفكم وقاربوا بينها وحاذوا بالأعناق، فوالذي نفسي بيده إني لأرى الشيطان يدخل من خلل الصف كأنها الحذف".','75','64','0');
INSERT INTO [t14299] VALUES (97,'٨٠ - حديث "ما من أحد يوم القيامة غني ولا فقير إلا ود أنما كان أوتي من الدنيا قوتا".','75','64','0');
INSERT INTO [t14299] VALUES (98,'٨١ - حديث "كان لا تشاء أن تراه من الليل مصليا إلا رأيته".','76','64','0');
INSERT INTO [t14299] VALUES (99,'٨٢ - حديث "الصلاة وما ملكت أيمانكم".','76','64','0');
INSERT INTO [t14299] VALUES (100,'٨٣ - حديث "اللهم إني أشهد أنك أنت الله لا إله إلا أنت أحدا صمدا ".','77','64','0');
INSERT INTO [t14299] VALUES (101,'٨٤ - حديث "إن الحمد لله وسبحان الله ولا إله إلا الله والله أكبر تساقط ذنوب العبد كما يتساقط ورق الشجر".','77','64','0');
INSERT INTO [t14299] VALUES (102,'٨٥ - حديث "قلت: يا رسول الله ولدت أم سليم، قال: الله أكبر، ما ولدت؟ قلت','77','64','0');
INSERT INTO [t14299] VALUES (103,'٨٦ - حديث "إن رجلين من أصحاب النبي صلى الله عليه وسلم خرجا من عند','78','64','0');
INSERT INTO [t14299] VALUES (104,'٨٧ - حديث "أما ترضى أن تكون لهم الدنيا ولنا الآخرة".','79','64','0');
INSERT INTO [t14299] VALUES (105,'٨٨ - حديث "لما ثقل رسول الله صلى الله عليه وسلم جعل يتغشاه الكرب. فقالت فاطمة: واكرب أبتاه. فلما مات قالت: يا أبتاه، أجاب ربا دعاه، يا أبتاه من ربه ما أدناه".','80','64','0');
INSERT INTO [t14299] VALUES (106,'٨٩ - حديث "لما قدم رسول الله صلى الله عليه وسلم المدينة أتاه المهاجرون فقالوا: يا رسول الله ما رأينا قوما أبذل من كثير ولا أحسن مواساة من قليل من قوم نزلنا بين أظهرهم".','80','64','0');
INSERT INTO [t14299] VALUES (107,'٩٠ - حديث "إذا تزوج العبد فقد استكمل نصف دينه فليتق الله في النصف الآخر".','81','64','0');
INSERT INTO [t14299] VALUES (108,'٩١ - حديث "اللهم إنك إن تشا لا تعبد بعد اليوم".','81','64','0');
INSERT INTO [t14299] VALUES (109,'٩٢ - حديث "من يشتري العبد؟ فقال: يا رسول الله، إذن والله تجدني كاسدا".','82','64','0');
INSERT INTO [t14299] VALUES (110,'٩٣ - حديث "لو خرجتم إلى ذود لنا فشربتم من ألبانها".','82','64','0');
INSERT INTO [t14299] VALUES (111,'٩٤ - حديث "إنه صلى الله عليه وسلم أتي بالبراق فاستصعب عليه، فقال له جبريل: أبمحمد تفعل هذا؟ فو الله ما ركبك أحد قط أكرم على الله منه. قالت: فأرفض عرقا".','83','64','0');
INSERT INTO [t14299] VALUES (112,'٩٥ - حديث "آتي باب الجنة فأستفتح، فيقول الخازن: من؟ فأقول: محمد. فيقول: بك أمرت أن لا أفتح لأحد قبلك".','83','64','0');
INSERT INTO [t14299] VALUES (113,'٩٦ - حديث "وإذا صلى جالسا فصلوا جلوسا أجمعون".','83','64','0');
INSERT INTO [t14299] VALUES (114,'٩٧ - حديث "من صلى الضحى ثنتي عشرة ركعة بنى الله له قصرا في الجنة".','84','64','0');
INSERT INTO [t14299] VALUES (115,'٩٨ - حديث "إني لأخشاكم لله".','84','64','0');
INSERT INTO [t14299] VALUES (116,'٩٩ - حديث "أنه صلى الله عليه وسلم قال: يا معاذ بن جبل".','85','64','0');
INSERT INTO [t14299] VALUES (117,'١٠٠ - حديث: "من كذب علي متعمدا فليتبوا مقعده من النار".','86','64','0');
INSERT INTO [t14299] VALUES (118,'١٠١ - حديث (لا يؤمن أحدكم حتى أكون أحب إليه من ولده ووالده والناس أجمعين).','87','64','0');
INSERT INTO [t14299] VALUES (119,'١٠٢ - حديث (سئل عن وقت صلاة الصبح فأمر بلالا حين طلع الفجر فأقام الصلاة، ثم أسفر الغد).','87','64','0');
INSERT INTO [t14299] VALUES (120,'١٠٣ - حديث (بعثت أنا والساعة كهاتين).','88','64','0');
INSERT INTO [t14299] VALUES (121,'١٠٤ - حديث (فلما نظروا إليه قالوا: محمد والخميس).','89','64','0');
INSERT INTO [t14299] VALUES (122,'١٠٥ - حديث (جاء أعرابي فبال في المسجد، فقال الصحابة: مه مه).','89','64','0');
INSERT INTO [t14299] VALUES (123,'١٠٦ - حديث "ثلاث من كن فيه وجد حلاوة الإيمان".','90','64','0');
INSERT INTO [t14299] VALUES (124,'١٠٧ - حديث "إذا جاء أحدكم الصلاة فليمض على هيئته، فليصل ما أدرك وليقض ما سبقه".','91','64','0');
INSERT INTO [t14299] VALUES (125,'١٠٨ - حديث "ما من مسلم يغرس غرسا أو يزرع زرعا فيأكل منه إنسان أو طير أو بهيمة إلا كانت له صدقة".','91','64','0');
INSERT INTO [t14299] VALUES (126,'١٠٩ - حديث "ما أعددت لها من كبير عمل صلاة ولا صوم".','91','64','0');
INSERT INTO [t14299] VALUES (127,'١١٠ - حديث "إن الله لغني أن يعذب هذا نفسه".','92','64','0');
INSERT INTO [t14299] VALUES (128,'١١١ - حديث "يا أنجشة رويدك سوقك بالقوارير".','92','64','0');
INSERT INTO [t14299] VALUES (129,'١١٢ - حديث "ما من رجل مسلم يموت له ثلاثة من ولده لم يبلغوا الحنث إلا أدخل الله عز وجل أبويهم الجنة بفضل رحمته إياهم".','93','64','0');
INSERT INTO [t14299] VALUES (130,'١١٣ - حديث "أتانا رسول الله صلى الله عليه وسلم في دارنا فاستسقى، فحلبنا له شاة لنا ثم شبته من ماء بئرنا".','95','64','0');
INSERT INTO [t14299] VALUES (131,'١١٤ - حديث "وإن وجدناه لبحرا".','95','64','0');
INSERT INTO [t14299] VALUES (132,'١١٥ - حديث "إذا أقرض أحدكم قرضا فأهدى إليه أو حمله فلا يقبلها".','96','64','0');
INSERT INTO [t14299] VALUES (133,'١١٦ - حديث "هذا جبل يحبنا ونحبه".','96','64','0');
INSERT INTO [t14299] VALUES (134,'١١٧ - حديث "من السنة إذا تزوج البكر أقام عندها سبعا".','97','64','0');
INSERT INTO [t14299] VALUES (135,'١١٨ - حديث "قوموا إلى جنة عرضها السماوات والأرض".','98','64','0');
INSERT INTO [t14299] VALUES (136,'١١٩ - حديث "لم يصدق نبي من الأنبياء ما صدقت".','98','64','0');
INSERT INTO [t14299] VALUES (137,'١٢٠ - حديث "قول أم حارثة لرسول الله صلى الله عليه وسلم: قد عرفت منزلة حارثة مني فإن يك في الجنة اصبر وأحتسب، وإن تكن الأخرى ترى ما أصنع".','98','64','0');
INSERT INTO [t14299] VALUES (138,'١٢١ - حديث "كتاب الله القصاص".','100','64','0');
INSERT INTO [t14299] VALUES (139,'١٢٢ - حديث "فكنا نراه يمشي بين أظهرنا رجلا من أهل الجنة".','100','64','0');
INSERT INTO [t14299] VALUES (140,'١٢٣ - حديث "إن حقا على الله أن لا يرتفع شيء من الدنيا إلا وضعه".','101','64','0');
INSERT INTO [t14299] VALUES (141,'١٢٤ - حديث "انطلقوا باسم الله وبالله على ملة رسول الله".','101','64','0');
INSERT INTO [t14299] VALUES (142,'١٢٥ - حديث "هؤلاء خطباء أمتك الذين يقولون [ما لا يفعلون] ".','101','64','0');
INSERT INTO [t14299] VALUES (143,'١٢٦ - حديث "إن الله لا يظلم مؤمنا حسنة يعطى بها في الدنيا ويجزى بها في الآخرة".','102','64','0');
INSERT INTO [t14299] VALUES (144,'١٢٧ - حديث "دخل رجل والنبي صلى الله عليه وسلم متكي بين ظهرانيهم".','102','64','0');
INSERT INTO [t14299] VALUES (145,'١٢٨ - حديث "عرضت على أجور أمتي حتى القذاة يخرجها الرجل من المسجد".','103','64','0');
INSERT INTO [t14299] VALUES (146,'١٢٩ - حديث "شهدت وليمتين من نساء رسول الله صلى الله عليه وسلم، فما أطعمنا خبزا ولا لحما. قلت: فمه؟ قال: الحيس.','104','64','0');
INSERT INTO [t14299] VALUES (147,'١٣٠ - حديث "لقد أخفت في الله وما يخاف أحد".','104','64','0');
INSERT INTO [t14299] VALUES (148,'١٣١ - حديث "أما إن كل بناء وبال على صاحبه إلا ما لا إلا ما لا".','105','64','0');
INSERT INTO [t14299] VALUES (149,'١٣٢ - حديث "الدجال، وإن بين عينيه مكتوب كافر" وفي نسخة "مكتوبا كافر".','105','64','0');
INSERT INTO [t14299] VALUES (150,'١٣٣ - حديث "هل من أحد يمشي على الماء إلا ابتلت قدماه".','106','64','0');
INSERT INTO [t14299] VALUES (151,'١٣٤ - حديث "من عال جاريتن حتى تبلغا جاء يوم القيامة أنا وهو كهاتين".','107','64','0');
INSERT INTO [t14299] VALUES (152,'١٣٥ - حديث "إن رجلا قال: يارسول الله كيف يحشر الكافر على وجهه يوم القيامة؟ قال: أليس الذي أمشاه على رجليه في الدنيا قادر على أن يمشيه على وجهه يوم القيامة".','107','64','0');
INSERT INTO [t14299] VALUES (153,'١٣٦ - حديث "لكل أمة أمين وإن أميننا أيتها الأمة أبو عبيدة".','107','64','0');
INSERT INTO [t14299] VALUES (154,'١٣٧ - حديث "قول سراقة: يانبي الله مرني بم شئت".','108','64','0');
INSERT INTO [t14299] VALUES (155,'١٣٨ - حديث "لا تشددوا على أنفسكم فيشدد الله عليكم".','108','64','0');
INSERT INTO [t14299] VALUES (156,'١٣٩ - حديث "أصبح رسول الله صلى الله عليه وسلم عروسا".','108','64','0');
INSERT INTO [t14299] VALUES (157,'١٤٠ - حديث "قال النبي صلى الله عليه وسلم يوم بدر: من ينظر ما فعل أبو جهل؟ فانطلق ابن مسعود فوجده قد ضربه ابنا عفراء حتى برد، فأخذ بلحيته فقال: أنت أبا جهل؟ ".','109','64','0');
INSERT INTO [t14299] VALUES (158,'١٤١ - حديث "من أجود جودا".','109','64','0');
INSERT INTO [t14299] VALUES (159,'١٤٢ - حديث أحد، قوله "لا تشرف يصيبك سهم".','110','64','0');
INSERT INTO [t14299] VALUES (160,'١٤٣ - حديث "أقرئ قومك السلام فإنهم ما علمت أعفة صبر".','110','64','0');
INSERT INTO [t14299] VALUES (161,'١٤٤ - حديث "هذا أول طعام أكله أبوك من ثلاثة أيام".','111','64','0');
INSERT INTO [t14299] VALUES (162,'١٤٥ - حديث نكاح زينب، قوله "فلما رأيتها عظمت في صدري حتى ما أستطيع أن أنظر إليها أن رسول الله صلى الله عليه وسلم ذكرها".','111','64','0');
INSERT INTO [t14299] VALUES (163,'١٤٦ - حديث "أنه صلى الله عليه وسلم رمى الجمرة ثم نحر البدن، والحجام جالس، ثم حلق أحد شقيه الأيمن".','112','64','0');
INSERT INTO [t14299] VALUES (164,'١٤٧ - حديث الجمل "فلما نظر الجمل إلى رسول الله صلى الله عليه وسلم أقبل نحوه حتى خر ساجدا بين يديه، فأخذ رسول الله صلى الله عليه وسلم بناصيته أذل ما كانت قط حتى أدخله في العمل".','112','64','0');
INSERT INTO [t14299] VALUES (165,'١٤٨ - حديث "أصابت الناس سنة على عهد رسول الله صلى الله عليه وسلم".','113','64','0');
INSERT INTO [t14299] VALUES (166,'١٤٩ - حديث "أن رجلا قال يارسول الله: متى الساعة قائمة؟ ".','115','64','0');
INSERT INTO [t14299] VALUES (167,'١٥٠ - حديث "لا يتمنين أحدكم الموت لضر نزل به، فإن كان لابد متمنيا .. ".','115','64','0');
INSERT INTO [t14299] VALUES (168,'١٥١ - حديث "إذا تقرب إلي العبد شبرا تقربت إليه ذراعا، وإذا تقرب إلي ذراعا تقربت منه باعا".','116','64','0');
INSERT INTO [t14299] VALUES (169,'١٥٢ - حديث "لا تقوم الساعة على أحد يقول الله الله".','116','64','0');
INSERT INTO [t14299] VALUES (170,'١٥٣ - حديث "نهى عن بيع الثمار حتى تزهي، قيل: وما تزهي؟ ".','116','64','0');
INSERT INTO [t14299] VALUES (171,'١٥٤ - حديث "نهى عن بيع الحب حتى يفرك".','117','64','0');
INSERT INTO [t14299] VALUES (172,'١٥٥ - حديث: "أرأيت إن منع الله الثمرة، بم يأخذ أحدكم مال أخيه؟ ".','117','64','0');
INSERT INTO [t14299] VALUES (173,'١٥٦ - حديث "إن رجلا جاء إلى الصلاة وقد حفزه النفس، فقال: الله أكبر، الحمد لله حمدا كثيرا طيبا".','118','64','0');
INSERT INTO [t14299] VALUES (174,'١٥٧ - حديث "مر على النبي صلى الله عليه وسلم بجنازة فأثنوا عليها خيرا، فقال: وجبت، ثم مر بأخرى فأثنوا عليها شرا، فقال: وجبت، فقيل: يا رسول الله، قلت لهذا وجبت ولهذا وجبت. قال: شهادة القوم، المؤمنون شهداء الله في الأرض".','119','64','0');
INSERT INTO [t14299] VALUES (175,'١٥٨ - حديث سؤال القبر، قوله: "إن العبد إذا وضع في قبره".','120','64','0');
INSERT INTO [t14299] VALUES (176,'١٥٩ - حديث (اعتدلوا في السجود ولا يبسط أحدكم ذراعيه انبساط الكلب).','137','64','0');
INSERT INTO [t14299] VALUES (177,'١٦٠ - حديث (جاء رجل فقال: يارسول الله إني أصبت حدا فأقمه علي).','138','64','0');
INSERT INTO [t14299] VALUES (178,'١٦١ - حديث (ليس في الخضراوات صدقة).','138','64','0');
INSERT INTO [t14299] VALUES (179,'١٦٢ - حديث: "أتي بقدر فيها خضرات" بكسر الضاد، أي بقول، واحدها خضرة".','139','64','0');
INSERT INTO [t14299] VALUES (180,'١٦٣ - حديث (اطلبوا العلم ولو بالصين).','139','64','0');
INSERT INTO [t14299] VALUES (181,'١٦٤ - حديث (اتخذ خاتما من فضة نقشه محمد رسول الله).','143','64','0');
INSERT INTO [t14299] VALUES (182,'١٦٥ - حديث (كان أبو طلحة أكثر أنصاري بالمدينة مالا).','143','64','0');
INSERT INTO [t14299] VALUES (183,'١٦٦ - حديث (أنه رأى عبد الرحمن بن عوف وعليه وضر من صفرة، فقال: مهيم).','144','64','0');
INSERT INTO [t14299] VALUES (184,'١٦٧ - حديث "فرأيت الماء ينبع من تحت أصابعه حتى توضؤوا من عند آخرهم".','145','64','0');
INSERT INTO [t14299] VALUES (185,'١٦٨ - حديث (نزلت على رسول الله صلى الله عليه وسلم {ليغفر لك الله ما تقدم من ذنبك وما تأخر} مرجعه من الحديبية).','146','64','0');
INSERT INTO [t14299] VALUES (186,'١٦٩ - حديث "إن الله تعالى وكل بالرحم ملكا يقول: أي رب نطفة، أي رب علقة، أي رب مضغة".','146','64','0');
INSERT INTO [t14299] VALUES (187,'١٧٠ - حديث "عجبت للمؤمن إن الله تعالى لم يقض له قضاء إلا كان خيرا له".','147','64','0');
INSERT INTO [t14299] VALUES (188,'١٧١ - حديث (مر على النبي صلى الله عليه وسلم ببدنة، فقال: اركبها، قالوا: إنها بدنة. قال: وإن).','147','64','0');
INSERT INTO [t14299] VALUES (189,'١٧٢ - حديث (قدم النبي صلى الله عليه وسلم المدينة وأنا ابن عشر، وكن أمهاتي يحثثنني على خدمته) الحديث.','147','64','0');
INSERT INTO [t14299] VALUES (190,'١٧٣ - حديث (دخل رسول الله صلى الله عليه وسلم المسجد وحبل ممدود بين ساريتين) الحديث.','149','64','0');
INSERT INTO [t14299] VALUES (191,'١٧٤ - حديث "ذهبت بعبد الله بن أبي طلحة إلى رسول الله صلى الله عليه وسلم حين ولد، ورسول الله صلى الله عليه وسلم في عباءة يهنا بعيرا له، فقال: هل معك تمر؟ فقلت: نعم. فناولته تمرات فألقاهن في فيه، فلاكهن ثم فغر فا الصبي فمجه في فيه، فجعل','150','64','0');
INSERT INTO [t14299] VALUES (192,'١٧٥ - حديث "لبيك حقا حقا، تعبدا ورقا".','151','64','0');
INSERT INTO [t14299] VALUES (193,'١٧٦ - حديث "أما إنكم في صلاة ما انتظرتموها".','151','64','0');
INSERT INTO [t14299] VALUES (194,'١٧٧ - حديث "حسبك من نساء العالمين مريم بنت عمران .. " الحديث.','151','64','0');
INSERT INTO [t14299] VALUES (195,'١٧٨ - حديث "وما رياض الجنة؟ قال: حلق الذكر".','152','64','0');
INSERT INTO [t14299] VALUES (196,'١٧٩ - حديث "يقول الله لأهون أهل النار عذابا يوم القيامة، لو أن لك ما في الأرض من شيء".','152','64','0');
INSERT INTO [t14299] VALUES (197,'١٨٠ - حديث (مر على امرأة وهى تبكي على قبر، فقال لها: اتقي الله واصبري، فقالت له: إليك عني، فإنك لاتبالي مصيبتي).','153','64','0');
INSERT INTO [t14299] VALUES (198,'مسند أوس الثقفي رضي الله عنه','155','0','0');
INSERT INTO [t14299] VALUES (199,'١٨١ - حديث: "كيف تعرض صلاتنا عليك وقد أرمت".','155','198','0');
INSERT INTO [t14299] VALUES (200,'١٨٢ - حديث: "قراءة القرآن ألف درجة".','156','198','0');
INSERT INTO [t14299] VALUES (201,'مسند أيمن بن خريم رضي الله عنه','157','0','0');
INSERT INTO [t14299] VALUES (202,'١٨٣ - حديث: "قام رسول الله صلى الله عليه وسلم خطيبا فقال: يا أيها الناس عدلت شهادة الزور إشراكا بالله".','157','201','0');
INSERT INTO [t14299] VALUES (203,'مسند البراء بن عازب رضي الله عنه','158','0','0');
INSERT INTO [t14299] VALUES (204,'١٨٤ - حديث: "إن الألى قد بغوا علينا".','158','203','0');
INSERT INTO [t14299] VALUES (205,'١٨٥ - حديث: "زينوا القرآن بأصواتكم".','158','203','0');
INSERT INTO [t14299] VALUES (206,'١٨٦ - حديث: "رأيت رسول الله صلى الله عليه وسلم على بغلة، وإن أبا سفيان آخذ بزمامها".','159','203','0');
INSERT INTO [t14299] VALUES (207,'١٨٧ - حديث: "حقا على المسلمين أن يغتسلوا يوم الجمعة".','159','203','0');
INSERT INTO [t14299] VALUES (208,'١٨٨ - حديث: "كان رسول الله صلى الله عليه وسلم إذا أقبل من سفر قال: آيبون تائبون عابدون لربنا حامدون".','160','203','0');
INSERT INTO [t14299] VALUES (209,'١٨٩ - حديث الصوم: "قالت: خيبة لك".','160','203','0');
INSERT INTO [t14299] VALUES (210,'١٩٠ - حديث: "المؤذن يغفر له مد صوته".','160','203','0');
INSERT INTO [t14299] VALUES (211,'١٩١ - حديث: "فما رأيت أهل المدينة فرحوا بشيء قط فرحهم به".','161','203','0');
INSERT INTO [t14299] VALUES (212,'١٩٢ - حديث سؤال القبر: "ولما يلحد".','161','203','0');
INSERT INTO [t14299] VALUES (213,'١٩٣ - حديث: "لما توفي إبراهيم قال رسول الله صلى الله عليه وسلم إن له مرضعا في الجنة".','163','203','0');
INSERT INTO [t14299] VALUES (214,'١٩٤ - حديث: "إذا رفع رأسه من الركوع قاموا قياما حتى يرونه قد سجد".','163','203','0');
INSERT INTO [t14299] VALUES (215,'١٩٥ - حديث: "من صلى صلاتنا ونسك نسكنا فقد أصاب النسك، ومن نسك قبل الصلاة فإنه قبل الصلاة ولا نسك له".','164','203','0');
INSERT INTO [t14299] VALUES (216,'١٩٦ - حديث: "كان أول ما قدم المدينة".','164','203','0');
INSERT INTO [t14299] VALUES (217,'١٩٧ - حديث: "دعاء القوم رغبة ورهبة إليك".','165','203','0');
INSERT INTO [t14299] VALUES (218,'١٩٨ - حديث قتل أبي رافع، قوله: "مالك".','166','203','0');
INSERT INTO [t14299] VALUES (219,'مسند بريدة بن الحصيب الأسلمي','167','0','0');
INSERT INTO [t14299] VALUES (220,'١٩٩ - حديث: "حرمة نساء المجاهدين على القاعدين كحرمة أمهاتهم".','167','219','0');
INSERT INTO [t14299] VALUES (221,'٢٠٠ - حديث: "جعل للجدة السدس إذا لم يكن دونها أم".','171','219','0');
INSERT INTO [t14299] VALUES (222,'٢٠١ - حديث: "من استعملناه على عمل فرزقناه فما أخذ بعد ذلك فهو غلول".','172','219','0');
INSERT INTO [t14299] VALUES (223,'٢٠٢ - حديث: "إني فعلته عمدا يا عمر".','172','219','0');
INSERT INTO [t14299] VALUES (224,'٢٠٣ - حديث: "أنه دخل المسجد فإذا صوت رجل يقرأ فقال: أتقوله مرائيا".','172','219','0');
INSERT INTO [t14299] VALUES (225,'٢٠٤ - حديث: "السلام عليكم دار قوم مؤمنين وإنا إن شاء الله بكم لاحقون".','173','219','0');
INSERT INTO [t14299] VALUES (226,'مسند بسر بن جحاش القرشي رضي الله عنه','174','0','0');
INSERT INTO [t14299] VALUES (227,'٢٠٥ - حديث: "قال الله تعالى: يا ابن آدم أنى تعجزني وقد خلقتك من مثل هذه حتى إذا سويتك وعدلتك مشيت بين بردين وللأرض منك وئيد فجمعت ومنعت حتى إذا بلغت التراقي قلت أتصدق وأنى أوان الصدقة".','174','226','0');
INSERT INTO [t14299] VALUES (228,'مسند بلال رضي الله عنه','176','0','0');
INSERT INTO [t14299] VALUES (229,'٢٠٦ - حديث: "ومن ابتدع بدعة ضلالة".','176','228','0');
INSERT INTO [t14299] VALUES (230,'مسند تميم بن أوس رضي الله عنه','177','0','0');
INSERT INTO [t14299] VALUES (231,'٢٠٧ - حديث: "من قال لا إله إلا الله واحدا أحدا صمدا لم يتخذ صاحبة ولا ولدا ولم يكن له كفوا أحد، عشر مرات، كتبت له أربعون حسنة".','177','230','0');
INSERT INTO [t14299] VALUES (232,'مسند ثابت بن الضحاك رضي الله عنه','184','0','0');
INSERT INTO [t14299] VALUES (233,'٢٠٨ - حديث:" من حلف على يمين بملة غير الإسلام كاذبا فهو كما قال".','184','232','0');
INSERT INTO [t14299] VALUES (234,'مسند ثوبان مولى رسول الله صلى الله عليه وسلم رضي الله عنه','185','0','0');
INSERT INTO [t14299] VALUES (235,'٢٠٩ - حديث: "استقيموا ولن تحصوا".','185','234','0');
INSERT INTO [t14299] VALUES (236,'٢١٠ - حديث: "إن الله زوى لي الأرض مشارقها ومغاربها، وسيبلغ ملك أمتي ما زوي لي منها".','186','234','0');
INSERT INTO [t14299] VALUES (237,'٢١١ - حديث: "إذا أصاب أحدكم الحمى فإن الحمى قطعة من النار، فليطفئها بالماء".','189','234','0');
INSERT INTO [t14299] VALUES (238,'٢١٢ - حديث: "من يتكفل لي أن لا يسأل الناس شيئا".','190','234','0');
INSERT INTO [t14299] VALUES (239,'مسند جابر بن سمرة رضي الله عنه','190','0','0');
INSERT INTO [t14299] VALUES (240,'٢١٣ - حديث: "أتى النبي صلى الله عليه وسلم بفرس معرورى فركبه .. ".','190','239','0');
INSERT INTO [t14299] VALUES (241,'٢١٤ - حديث: "لا يزال الدين قائما حتى تقوم الساعة أو يكون عليكم اثنا عشر خليفة".','190','239','0');
INSERT INTO [t14299] VALUES (242,'٢١٥ - حديث: "كان للنبي صلى الله عليه وسلم خطبتان يجلس بينهما يقرأ القرآن ويذكر الناس".','191','239','0');
INSERT INTO [t14299] VALUES (243,'مسند جابر بن عبد الله رضي الله عنه','191','0','0');
INSERT INTO [t14299] VALUES (244,'٢١٦ - حديث: "ذكاة الجنين ذكاة أمه".','191','243','0');
INSERT INTO [t14299] VALUES (245,'٢١٧ - حديث الغسل','195','243','0');
INSERT INTO [t14299] VALUES (246,'٢١٨ - حديث الاستخارة، قوله: (اللهم إني أستخيرك بعلمك وأستقدرك بقدرتك).','198','243','0');
INSERT INTO [t14299] VALUES (247,'٢١٩ - حديث: "ليس البر الصيام في السفر".','198','243','0');
INSERT INTO [t14299] VALUES (248,'٢٢٠ - حديث: "إن الموت فزع".','199','243','0');
INSERT INTO [t14299] VALUES (249,'٢٢١ - حديث: "قاتل الله اليهود، إن الله حرم شحومهما، جملوه ثم باعوه فأكلوا ثمنه".','199','243','0');
INSERT INTO [t14299] VALUES (250,'٢٢٢ - حديث: "رأيت رسول الله صلى الله عليه وسلم يرمي على راحلته يوم النحر ويقول: لتأخذوا','199','243','0');
INSERT INTO [t14299] VALUES (251,'٢٢٣ - حديث: "قلنا لا نكنيك أبا القاسم ولا كرامة".','200','243','0');
INSERT INTO [t14299] VALUES (252,'٢٢٤ - حديث: "إن أخوف ما أخاف على أمتي عمل قوم لوط".','200','243','0');
INSERT INTO [t14299] VALUES (253,'٢٢٥ - حديث: "يسألوني عن الساعة وإنما علمها عند الله".','201','243','0');
INSERT INTO [t14299] VALUES (254,'٢٢٦ - حديث: "لقد جئتكم بها بيضاء نقية".','201','243','0');
INSERT INTO [t14299] VALUES (255,'٢٢٧ - حديث: "لا يموتن أحدكم إلا وهو يحسن الظن بالله".','201','243','0');
INSERT INTO [t14299] VALUES (256,'٢٢٨ - حديث: "لا تدعوا على أنفسكم ولا على أولادكم لا توافقوا ساعة إجابة".','202','243','0');
INSERT INTO [t14299] VALUES (257,'٢٢٩ - حديث: "أن عمر بن الخطاب جاء يوم الخندق فقال يا رسول الله ما كدت أن أصلي العصر حتى كادت الشمس تغرب".','202','243','0');
INSERT INTO [t14299] VALUES (258,'٢٣٠ - حديث: "لو بعت من أخير تمرا، فأصابته جائحة فلا يحل لك أن تأخذ منه شيئا".','205','243','0');
INSERT INTO [t14299] VALUES (259,'٢٣١ - حديث: "بينا أنا أمشي إذ سمعت صوتا".','205','243','0');
INSERT INTO [t14299] VALUES (260,'٢٣٢ - حديث: "قال لي رسول الله صلى الله عليه وسلم: أتزوجت؟ قلت: نعم قال: أبكرا أم ثيبا؟ قلت: لا، بل ثيب".','208','243','0');
INSERT INTO [t14299] VALUES (261,'٢٣٣ - حديث: "إذا وضع الميت في قبره مثلت له الشمس عند غروبها فيجلس عند يمينه".','210','243','0');
INSERT INTO [t14299] VALUES (262,'٢٣٤ - حديث: "بينما نحن نصلي مع النبي صلى الله عليه وسلم، إذ أقبلت غير تحمل طعاما، فالتفتوا إليها حتى ما بقي مع النبي صلى الله عليه وسلم إلا اثني عشر رجلا".','210','243','0');
INSERT INTO [t14299] VALUES (263,'٢٣٥ - حديث: "اشتركنا مع النبي صلى الله عليه وسلم في الحج والعمرة كل سبعة في بدنة".','210','243','0');
INSERT INTO [t14299] VALUES (264,'٢٣٦ - حديث: "لا يؤمن عبد حتى يؤمن بالقدر خيره وشره، حتى يعلم أن ما أصابه لم يكن ليخطئه، وأن ما أخطأه لم يكن ليصيبه".','211','243','0');
INSERT INTO [t14299] VALUES (265,'٢٣٧ - حديث: "إذا سمعت الأذان فأجب ولو حبوا أو زحفا".','211','243','0');
INSERT INTO [t14299] VALUES (266,'٢٣٨ - حديث: "كلوا لحم الصيد وأنتم حرم ما لم تصيدوه أو يصاد لكم".','212','243','0');
INSERT INTO [t14299] VALUES (267,'٢٣٩ - حديث قتل كعب بن الأشرف، قوله: "ما رأيت كاليوم ريحا أطيب .. ".','212','243','0');
INSERT INTO [t14299] VALUES (268,'٢٤٠ - حديث: "أولوها له يفقهها".','213','243','0');
INSERT INTO [t14299] VALUES (269,'٢٤١ - حديث الرؤيا، قوله: "فقال: أبو بكر فلأعبرها".','213','243','0');
INSERT INTO [t14299] VALUES (270,'٢٤٢ - حديث: "إلي أيها الناس هلم إلي أنا رسول الله".','213','243','0');
INSERT INTO [t14299] VALUES (271,'٢٤٣ - حديث: "إن جابرا قد صنع لكم سؤرا فحي هلاكم".','216','243','0');
INSERT INTO [t14299] VALUES (272,'٢٤٤ - حديث: "قتل أبوهما معك يوم أحد".','217','243','0');
INSERT INTO [t14299] VALUES (273,'٢٤٥ - حديث: "نهى عن بيع الحيوان نسيئة اثنين بواحد، ولا بأس به يدا بيد".','218','243','0');
INSERT INTO [t14299] VALUES (274,'٢٤٦ - حديث: "فجعلن ينزعن حليهن وقلائدهن وقرطهن وخواتيمهن يقذفون في ثوب بلال يتصدقن به".','219','243','0');
INSERT INTO [t14299] VALUES (275,'٢٤٧ - حديث: "من كان يؤمن بالله واليوم الآخر فعليه الجمعة يوم الجمعة إلا مريض ... ".','219','243','0');
INSERT INTO [t14299] VALUES (276,'٢٤٨ - حديث: "من حلف أن لا يقوم من آخر الليل فليوتر أوله، ومن طمع أن يقوم في آخره فليوتر الليل".','221','243','0');
INSERT INTO [t14299] VALUES (277,'٢٤٩ - حديث: "ذكر رسول الله صلى الله عليه وسلم الفرش فقال: فراش للرجل، وفراش لامرأته، والثالث للضيف والرابع للشيطان".','222','243','0');
INSERT INTO [t14299] VALUES (278,'٢٥٠ - حديث: "أي حين توتر؟ قال: أول".','222','243','0');
INSERT INTO [t14299] VALUES (279,'٢٥١ - حديث: "قضى النبي صلى الله عليه وسلم بالعمرى أنها لمن وهبت له".','223','243','0');
INSERT INTO [t14299] VALUES (280,'٢٥٢ - حديث: "من صام رمضان وستا من شوال فكأنما صام السنة كلها".','223','243','0');
INSERT INTO [t14299] VALUES (281,'٢٥٣ - حديث: "معاذ الله أن يتحدث الناس أني أقتل أصحابي".','225','243','0');
INSERT INTO [t14299] VALUES (282,'٢٥٤ - حديث: "اللهم وليديه فاغفر".','225','243','0');
INSERT INTO [t14299] VALUES (283,'٢٥٥ - حديث: "من كن له ثلاث بنات يمونهن ويرحمهن ويكلفهن وجبت له الجنة ألبتة".','226','243','0');
INSERT INTO [t14299] VALUES (284,'٢٥٦ - حديث: "فأيما رجل من أمتي أدركته الصلاة فليصل".','227','243','0');
INSERT INTO [t14299] VALUES (285,'٢٥٧ - حديث: "يا معاذ أفتان أنت".','227','243','0');
INSERT INTO [t14299] VALUES (286,'٢٥٨ - حديث: "هو أي داء أدوى من البخل".','228','243','0');
INSERT INTO [t14299] VALUES (287,'٢٥٩ - حديث: "قول عمر: يا رسول الله أعليك أغار".','228','243','0');
INSERT INTO [t14299] VALUES (288,'٢٦٠ - حديث: "أما بعد فإن خير الحديث كتاب الله وخير الهدي هدي محمد صلى الله عليه وسلم، وشر الأمور محدثاتها".','228','243','0');
INSERT INTO [t14299] VALUES (289,'٢٦١ - حديث: "أنا وأبي وخالي من أصحاب العقبة".','229','243','0');
INSERT INTO [t14299] VALUES (290,'٢٦٢ - حديث: "إن رجلا قال يا رسول الله: إني نذرت ... إلى: ... فتح الله عليك مكة أن أصلي في بيت المقدس ركعتين قال: صل ههنا ... إلى قوله: شأنك إذن".','229','243','0');
INSERT INTO [t14299] VALUES (291,'٢٦٣ - حديث: "فقلت النجاء فقد قتل الله أبا رافع".','229','243','0');
INSERT INTO [t14299] VALUES (292,'٢٦٤ - حديث: "حي على أهل الوضوء".','230','243','0');
INSERT INTO [t14299] VALUES (293,'٢٦٥ - حديث: "بين العبد وبين الكفر ترك الصلاة".','230','243','0');
INSERT INTO [t14299] VALUES (294,'٢٦٦ - حديث: "إن أحسن ما دخل الرجل على أهله إذا قدم من سفر من أول الليل".','230','243','0');
INSERT INTO [t14299] VALUES (295,'٢٦٧ - حديث: "ما كان عند هذا ما يسكن به رأسه".','231','243','0');
INSERT INTO [t14299] VALUES (296,'٢٦٨ - حديث: "إبليس يجيء أحدهم فيقول فعلت كذا وكذا فيقول ما صنعت شيئا ويجيء أحدهم فيقول ما تركته فرقت بينه وبين أهله فيدنيه ويقول: نعم أنت".','231','243','0');
INSERT INTO [t14299] VALUES (297,'٢٦٩ - حديث: "إن الشيطان قد يئس أن يعبده المصلون ولكن في التحريش بينهم".','231','243','0');
INSERT INTO [t14299] VALUES (298,'٢٧٠ - حديث: "كان رسول الله صلى الله عليه وسلم في سفر فهبت ريح شديدة فقال: هذه لموت منافق، فما قدمنا المدينة إذا هو قد مات عظيم من عظماء المنافقين".','232','243','0');
INSERT INTO [t14299] VALUES (299,'٢٧١ - حديث: "ويل للأعقاب من النار".','232','243','0');
INSERT INTO [t14299] VALUES (300,'٢٧٢ - حديث: "من قال حين يسمع النداء: اللهم رب هذه الدعوة .. الحديث .. ".','234','243','0');
INSERT INTO [t14299] VALUES (301,'٢٧٣ - حديث: "أنه أخذ بيد مجذوم فوضعها في القصعة وقال: كل باسم الله ثقة بالله".','235','243','0');
INSERT INTO [t14299] VALUES (302,'٢٧٤ - حديث: الشجرتين: قوله: "فإذا شجرتين".','236','243','0');
INSERT INTO [t14299] VALUES (303,'٢٧٥ - حديث: "إن لكل نبي حواريا".','236','243','0');
INSERT INTO [t14299] VALUES (304,'٢٧٦ - حديث: "فينزل عيسى فيقول أميرهم: تعال صل لنا، فيقول لا إن بعضكم على بعض أمراء تكرمة الله هذه الأمة".','237','243','0');
INSERT INTO [t14299] VALUES (305,'٢٧٧ - حديث الأعرابي الذي اخترط سيفه: قوله: "فها هو ذا جالس".','237','243','0');
INSERT INTO [t14299] VALUES (306,'٢٧٨ - حديث الكسوف: قوله: "فانصرف وقد آضت الشمس".','237','243','0');
INSERT INTO [t14299] VALUES (307,'٢٧٩ - حديث: "كل مولود يولد على الفطرة حتى يعرب عنه لسانه فإذا عبر عنه لسانه إما شاكرا وإما كفورا".','238','243','0');
INSERT INTO [t14299] VALUES (308,'٢٨٠ - حديث: "الناس غاديان فمبتاع نفسه فمعتقها وبائع نفسه فموبقها".','238','243','0');
INSERT INTO [t14299] VALUES (309,'٢٨١ - حديث: "كل دم يفوح مسكا".','238','243','0');
INSERT INTO [t14299] VALUES (310,'٢٨٢ - حديث: "قضى رسول الله بالشفعة في كل شركة لم تقسم ربعة أو حائط".','239','243','0');
INSERT INTO [t14299] VALUES (311,'٢٨٣ - حديث: "اقتتل غلامان ... الحديث".','239','243','0');
INSERT INTO [t14299] VALUES (312,'٢٨٤ - حديث دين والد جابر، قوله: "فكلت ليس العجوة".','240','243','0');
INSERT INTO [t14299] VALUES (313,'٢٨٥ - حديث: "دياركم تكتب آثاركم".','240','243','0');
INSERT INTO [t14299] VALUES (314,'٢٨٦ - حديث: "ينزل عيسى ابن مريم فيقول أميركم: تعال صل بنا، فيقول: لا، إن بعضكم على بعض أميرا لتكرمة الله هذه الأمة".','241','243','0');
INSERT INTO [t14299] VALUES (315,'٢٨٧ - حديث سؤال القبر، قوله: "فيراهما كلاهما".','241','243','0');
INSERT INTO [t14299] VALUES (316,'٢٨٨ - حديث: قوله: "قم فصله".','242','243','0');
INSERT INTO [t14299] VALUES (317,'٢٨٩ - حديث: "من أكل من هذه الشجرة فلا يغشانا في مسجدنا".','242','243','0');
INSERT INTO [t14299] VALUES (318,'٢٩٠ - حديث: "أيما امرئ من المسلمين حلف عند منبري هذا على يمين كاذبة يستحق بها حق مسلم أدخله الله النار على سواك أخضر".','242','243','0');
INSERT INTO [t14299] VALUES (319,'٢٩١ - حديث: "إن رجلا من أسلم جاء إلى النبي صلى الله عليه وسلم فاعترف بالزنا فأعرض عنه حتى شهد على نفسه أربع مرات".','243','243','0');
INSERT INTO [t14299] VALUES (320,'٢٩٢ - حديث: "من ترك دينا أو ضياعا فإلي وعلي".','243','243','0');
INSERT INTO [t14299] VALUES (321,'مسند جابر بن عتيك رضي الله عنه','243','0','0');
INSERT INTO [t14299] VALUES (322,'٢٩٣ - حديث: "ما تعدون الشهادة؟ قالوا: قتل في سبيل الله".','243','321','0');
INSERT INTO [t14299] VALUES (323,'٢٩٤ - حديث: "كنت في الوفد من عبد القيس فنهاهم رسول الله صلى الله عليه وسلم عن الشرب في الأوعية التي سمعتم: الدباء والحنتم والنقير والمزفت".','244','321','0');
INSERT INTO [t14299] VALUES (324,'٢٩٥ - حديث: "دعا بأن يظهر عليهم من غيرهم ولا يهلكهم بالسنين فأعطيها، ودعا بأن لا يجعل بأسهم بينهم فمنعنيها".','245','321','0');
INSERT INTO [t14299] VALUES (325,'مسند جبير بن مطعم رضي الله عنه','245','0','0');
INSERT INTO [t14299] VALUES (326,'٢٩٦ - حديث: "أنا محمد وأنا أحمد وأنا الحاشر الذي يحشر الناس على قدمي وأنا الماحي الذي يمحى بي الكفر".','245','325','0');
INSERT INTO [t14299] VALUES (327,'٢٩٧ - حديث: "الله أكبر كبيرا والحمد لله كثيرا وسبحان الله بكرة وأصيلا".','245','325','0');
INSERT INTO [t14299] VALUES (328,'٢٩٨ - حديث: "وإنما بنو هاشم وبنو المطلب شيئا واحدا".','246','325','0');
INSERT INTO [t14299] VALUES (329,'٢٩٩ - حديث: "فعلقت الأعراب يسألونه حين اضطروه إلى سمرة".','246','325','0');
INSERT INTO [t14299] VALUES (330,'٣٠٠ - حديث: "إن كان لكم من الأمر شيء فلأعرفن ما منعتم أحدا أن يطوف بهذا البيت أي ساعة شاء من ليل أو نهار".','247','325','0');
INSERT INTO [t14299] VALUES (331,'٣٠١ - حديث: "من يكلؤنا الليلة لا نرقد عن صلاة الفجر".','247','325','0');
INSERT INTO [t14299] VALUES (332,'مسند جرير بن عبد الله البجلي رضي الله عنه','248','0','0');
INSERT INTO [t14299] VALUES (333,'٣٠٢ - حديث: "لا ترجعوا بعدي كفارا يضرب بعضكم رقاب بعض".','248','332','0');
INSERT INTO [t14299] VALUES (334,'٣٠٣ - حديث: "إنكم ترون ربكم عيانا كما ترون هذا القمر لا تضامون في رؤيته".','250','332','0');
INSERT INTO [t14299] VALUES (335,'٣٠٤ - حديث: "من يحرم الرفق يحرم الخير".','251','332','0');
INSERT INTO [t14299] VALUES (336,'٣٠٥ - حديث: "تصدق رجل من ديناره من درهمه من ثوبه من صاع بره من صاع تمره ... حتى قال ولو بشق تمرة".','251','332','0');
INSERT INTO [t14299] VALUES (337,'٣٠٦ - حديث: "بينا هو يخطب إذ عرض له في الخطبة فقال يدخل عليكم من هذا الباب أو من هذا الفج من خير ذي يمن".','253','332','0');
INSERT INTO [t14299] VALUES (338,'٣٠٧ - حديث: "صيام ثلاثة أيام من كل شهر صيام الدهر أيام البيض صبيحة ثلاثة عشرة وأربع عشرة وخمس عشرة".','254','332','0');
INSERT INTO [t14299] VALUES (339,'٣٠٨ - حديث: "الخيل معقود بنواصيها الخير الأجر والمغنم".','254','332','0');
INSERT INTO [t14299] VALUES (340,'٣٠٩ - حديث: "بني الإسلام على خمس: شهادة أن لا إله إلا الله ... الحديث .. ".','255','332','0');
INSERT INTO [t14299] VALUES (341,'٣١٠ - حديث: "قلت أبايعك على الإسلام، فشرط علي والنصح لكل مسلم".','255','332','0');
INSERT INTO [t14299] VALUES (342,'٣١١ - حديث: "بايعت النبي صلى الله عليه وسلم على السمع والطاعة فلقنني فيما استطعت والنصح لكل مسلم".','255','332','0');
INSERT INTO [t14299] VALUES (343,'مسند جعدة بن خالد بن الصمة الجشمي رضي الله عنه','256','0','0');
INSERT INTO [t14299] VALUES (344,'٣١٢ - حديث: "أتي النبي صلى الله عليه وسلم برجل، فقالوا هذا أراد أن يقتلك فقال له النبي صلى الله عليه وسلم: لم ترع ولو أردت ذلك لم يسلطك الله علي".','256','343','0');
INSERT INTO [t14299] VALUES (345,'مسند جندب بن عبد الله بن سفيان البجلي رضي الله عنه','256','0','0');
INSERT INTO [t14299] VALUES (346,'٣١٣ - حديث: "هل أنت إلا أصبع دميت وفي سبيل الله ما لقيت".','256','345','0');
INSERT INTO [t14299] VALUES (347,'٣١٤ - حديث: "من صلى الصبح فهو ذمة الله عز وجل فلا يطلبنكم الله من ذمته بشيء فإنه من يطلبه من ذمته بشيء يدركه ثم يكبه على وجهه في نار جهنم".','257','345','0');
INSERT INTO [t14299] VALUES (348,'مسند الحارث بن حسان البكري رضي الله عنه','258','0','0');
INSERT INTO [t14299] VALUES (349,'٣١٥ - حديث واقد، قوله: "فمرت به سحابتان سود فنودي منها".','258','348','0');
INSERT INTO [t14299] VALUES (350,'مسند الحارث بن عبد الله بن أوس الثقفي','258','0','0');
INSERT INTO [t14299] VALUES (351,'٣١٦ - حديث: أن عمر قال له: "أديت عن يديك سألتني عن شيء سألت عنه رسول','258','350','0');
INSERT INTO [t14299] VALUES (352,'مسند حبة بن خالد الخزاعي رضي الله عنه','259','0','0');
INSERT INTO [t14299] VALUES (353,'٣١٧ - حديث: "لا تيأسا من الرزق ما تهززت رؤوسكما".','259','352','0');
INSERT INTO [t14299] VALUES (354,'مسند حارثة بن وهب الخزاعي رضي الله عنه','260','0','0');
INSERT INTO [t14299] VALUES (355,'٣١٨ - حديث: "صلى بنا النبي صلى الله عليه وسلم ونحن أكثر ما كنا قط وآمنه بمنى ركعتين".','260','354','0');
INSERT INTO [t14299] VALUES (356,'٣١٩ - حديث: "صليت مع النبي صلى الله عليه وسلم الظهر والعصر بمنى أكثر ما كان الناس وآمنه ركعتين".','261','354','0');
INSERT INTO [t14299] VALUES (357,'٣٢٠ - حديث: "ألا أنبئكم بأهل الجنة كل ضعيف متضعف".','261','354','0');
INSERT INTO [t14299] VALUES (358,'مسند حبان بن بح الصدائي رضي الله عنه','262','0','0');
INSERT INTO [t14299] VALUES (359,'٣٢١ - حديث: "جعل أصابعه في الإناء فانفجر عيونا".','262','358','0');
INSERT INTO [t14299] VALUES (360,'مسند حجاج الأسلمي رضي الله عنه','262','0','0');
INSERT INTO [t14299] VALUES (361,'٣٢٢ - حديث: "قلت يا رسول الله: ما يذهب عني مذمة الرضاع قال: غرة عبد أو أمة".','262','360','0');
INSERT INTO [t14299] VALUES (362,'مسند حذيفة بن أسيد رضي الله عنه','263','0','0');
INSERT INTO [t14299] VALUES (363,'٣٢٣ - حديث: "لا تقوم الساعة حتى ترون عشر آيات: طلوع الشمس من مغربها ... إلى أنه قال: وثلاثة خسوف: خسف بالمغرب ... الحديث".','263','362','0');
INSERT INTO [t14299] VALUES (364,'مسند حذيفة بن اليمان رضي الله عنهما','263','0','0');
INSERT INTO [t14299] VALUES (365,'٣٢٤ - حديث الدجال: "إنما أدركن واحد منكم".','263','364','0');
INSERT INTO [t14299] VALUES (366,'٣٢٥ - حديث: "لا تقوم الساعة حتى يكون أزهد الناس بالدنيا لكع بن لكع".','264','364','0');
INSERT INTO [t14299] VALUES (367,'٣٢٦ - حديث: "ما صليت ولو مت مت على غير الفطرة التي فطر الله عليها محمدا صلى الله عليه وسلم".','264','364','0');
INSERT INTO [t14299] VALUES (368,'٣٢٧ - حديث: "لا تشربوا في آنية الذهب والفضة ولا تأكلوا في صحافها".','266','364','0');
INSERT INTO [t14299] VALUES (369,'٣٢٨ - حديث: "أن رجلا حضره الموت فأوصى أهله إذا أنا مت فاجمعوا حطبا ثم أوقدوا فيه نارا حتى إذا أكلت لحمي وخلص إلى عظمي".','266','364','0');
INSERT INTO [t14299] VALUES (370,'٣٢٩ - حديث: "إنه صلى الله عليه وسلم أشرك بين المسلمين: البقرة عن سبعة".','267','364','0');
INSERT INTO [t14299] VALUES (371,'٣٣٠ - حديث الشفاعة عنه وعن أبي هريرة رضي الله عنه: "إنما كنت خليلا من وراء وراء".','267','364','0');
INSERT INTO [t14299] VALUES (372,'٣٣١ - حديث: "لا تكونوا إمعة".','269','364','0');
INSERT INTO [t14299] VALUES (373,'٣٣٢ - حديث: "أخذ رسول الله صلى الله عليه وسلم بعضلة ساقي وقال هذا موضع الإزار فأسفل فإن أبيت فأسفل".','270','364','0');
INSERT INTO [t14299] VALUES (374,'٣٣٣ - حديث: "ضرب لنا رسول الله صلى الله عليه وسلم أمثالا واحد وثلاثة ... الحديث".','270','364','0');
INSERT INTO [t14299] VALUES (375,'٣٣٤ - حديث الساعة: "ولكن أخبركم بشرائطها".','271','364','0');
INSERT INTO [t14299] VALUES (376,'٣٣٥ - حديث الفتنة: "قلت يا رسول الله الهدنة على دخن ما هي؟ قال: لا ترجع قلوب الناس على الذي كانت عليه".','271','364','0');
INSERT INTO [t14299] VALUES (377,'٣٣٦ - حديث: "تعرض الفتن على القلوب عرض الحصير فأي قلب أنكرها نكتت في قلبه نكتة بيضاء وأي قلب أشربها نكتت فيه نكتة سوداء حتى يصير القلب على قلبين: أبيض مثل الصفا لا تضره فتنة ما دامت السموات والأرض والآخر أسود مربدا مخجيا".','271','364','0');
INSERT INTO [t14299] VALUES (378,'٣٣٧ - حديث: "احصوا لي كم يلفظ الإسلام".','276','364','0');
INSERT INTO [t14299] VALUES (379,'٣٣٨ - حديث الإسراء قوله: "ثم رجعا عودهما على بدئهما".','276','364','0');
INSERT INTO [t14299] VALUES (380,'٣٣٩ - حديث: "من سن خيرا فاستن به كان له أجره ومن أجور من يتبعه غير منتقص من أجورهم شيئا".','277','364','0');
INSERT INTO [t14299] VALUES (381,'٣٤٠ - حديث: "إن حوضي لأبعد من أيلة من عدن".','278','364','0');
INSERT INTO [t14299] VALUES (382,'٣٤١ - حديث: "من قال لا إله إلا الله ابتغاء وجه الله ختم له بها دخل الجنة، ومن صام يوما ابتغاء وجه الله ختم له بها".','278','364','0');
INSERT INTO [t14299] VALUES (383,'٣٤٢ - حديث: "أما رأيت العارض الذي عرض له قبيل".','278','364','0');
INSERT INTO [t14299] VALUES (384,'٣٤٣ - حديث: "فلما رآني قال: قم يا نومان".','279','364','0');
INSERT INTO [t14299] VALUES (385,'٣٤٤ - حديث: "فأقول رب أصيحابي أصيحابي".','279','364','0');
INSERT INTO [t14299] VALUES (386,'مسند حسان بن ثابت رضي الله عنه','279','0','0');
INSERT INTO [t14299] VALUES (387,'٣٤٥ - حديث: "لعن رسول الله صلى الله عليه وسلم زوارات القبور".','279','386','0');
INSERT INTO [t14299] VALUES (388,'مسند الحسن بن علي رضي الله عنه','280','0','0');
INSERT INTO [t14299] VALUES (389,'٣٤٦ - حديث: "إن كان رسول الله صلى الله عليه وسلم ليبعثه ويعطيه الراية فلا ينصرف حتى يفتح له".','280','388','0');
INSERT INTO [t14299] VALUES (390,'٣٤٧ - حديث القنوت قوله: "ولا يعز من عاديت" رواه البيهقي هو بكسر العين، وغلط بعض أعلام العصر فقال: إنه بضمها من باب نصر ينصر وهو غلط صريح.','280','388','0');
INSERT INTO [t14299] VALUES (391,'مسند الحكم بن حزن رضي الله عنه','281','0','0');
INSERT INTO [t14299] VALUES (392,'٣٤٨ - حديث: "قدمت على رسول الله صلى الله عليه وسلم سابع سبعة أو تاسع تسعة".','281','391','0');
INSERT INTO [t14299] VALUES (393,'مسند حكيم بن حزام رضي الله عنه','281','0','0');
INSERT INTO [t14299] VALUES (394,'٣٤٩ - حديث: "أسلمت على مات سلف لك من خير".','281','393','0');
INSERT INTO [t14299] VALUES (395,'مسند حنظلة بن الربيع الكاتب','281','0','0');
INSERT INTO [t14299] VALUES (396,'٣٥٠ - حديث: "يا حنظلة ساعة وساعة".','281','395','0');
INSERT INTO [t14299] VALUES (397,'مسند خارجة بن حذافة رضي الله عنه','282','0','0');
INSERT INTO [t14299] VALUES (398,'٣٥١ - حديث: "إن الله أمركم بصلاة هي خير لكم من حمر النعم الوتر".','282','397','0');
INSERT INTO [t14299] VALUES (399,'مسند خالد بن الوليد رضي الله عنه','282','0','0');
INSERT INTO [t14299] VALUES (400,'٣٥٢ - حديث الضب، قوله: "فقالت امرأة [من] الحضور".','282','399','0');
INSERT INTO [t14299] VALUES (401,'مسند خباب بن الأرت رضي الله عنه','283','0','0');
INSERT INTO [t14299] VALUES (402,'٣٥٣ - حديث: "شكونا إلى رسول الله صلى الله عليه وسلم حر الرمضاء فلم يشكنا".','283','401','0');
INSERT INTO [t14299] VALUES (403,'٣٥٤ - حديث: "ما أنفق المؤمن من نفقة إلا أجر فيها إلا نفقة في هذا الشراب".','284','401','0');
INSERT INTO [t14299] VALUES (404,'٣٥٥ - حديث: "قتل مصعب بن عمير يوم أحد، فلم يترك إلا نمرة، كنا إذا غطينا بها رأسه خرجت رجلاه، وإذا غطى رجليه بدا رأسه".','284','401','0');
INSERT INTO [t14299] VALUES (405,'٣٥٦ - حديث: "قد كان من قبلكم ليمشطن بمشاط الحديد ما دون عظامه".','284','401','0');
INSERT INTO [t14299] VALUES (406,'مسند دكين بن سعيد رضي الله عنه','285','0','0');
INSERT INTO [t14299] VALUES (407,'٣٥٧ - حديث: "أنه صلى الله عليه وسلم قال لعمر: قم فأعطهم، فقال عمر: يا رسول الله سمع وطاعة".','285','406','0');
INSERT INTO [t14299] VALUES (408,'مسند رافع بن خديج رضي الله عنه','285','0','0');
INSERT INTO [t14299] VALUES (409,'٣٥٨ - حديث: "ما أنهر الدم وذكر اسم الله عليه فكلوا ليس السن والظفر"،','285','408','0');
INSERT INTO [t14299] VALUES (410,'٣٥٩ - حديث: "نهى عن المزابنة التمر بالتمر".','286','408','0');
INSERT INTO [t14299] VALUES (411,'٣٦٠ - حديث: "الحمى من فور جهنم فأبردوها بالماء".','287','408','0');
INSERT INTO [t14299] VALUES (412,'٣٦١ - حديث القسامة. قوله: "استحقوا صاحبكم أو قتيلكم بأيمان خمسين منكم".','287','408','0');
INSERT INTO [t14299] VALUES (413,'٣٦٢ - حديث: "إن جبريل أو ملك جاء إلى النبي صلى الله عليه وسلم فقال: ما','287','408','0');
INSERT INTO [t14299] VALUES (414,'مسند ربيعة بن كعب الأسلمي رضي الله عنه','288','0','0');
INSERT INTO [t14299] VALUES (415,'٣٦٣ - حديث: "كنت أخدم رسول الله صلى الله عليه وسلم نهاري أجمع حتى يصلي العشاء الآخرة، فأجلس ببابه واقول: لعلها أن تحدث لرسول الله صلى الله عليه وسلم حاجة".','288','414','0');
INSERT INTO [t14299] VALUES (416,'مسند رفاعة بن رافع الزرقي رضي الله عنه','289','0','0');
INSERT INTO [t14299] VALUES (417,'٣٦٤ - حديث: "جمع رسول الله صلى الله عليه وسلم قريشا فقال: هل فيكم من غيركم؟ قالوا: لا، إلا ابن أختنا".','289','416','0');
INSERT INTO [t14299] VALUES (418,'٣٦٥ - حديث: "قال رجل وراءه: ربنا لك الحمد حمدا كثيرا طيبا".','289','416','0');
INSERT INTO [t14299] VALUES (419,'٣٦٦ - حديث: "ما تعدون أهل بدر فيكم".','291','416','0');
INSERT INTO [t14299] VALUES (420,'٣٦٧ - حديث": "اللهم لا قابض لما بسطت، ولا باسط لما قبضت، ولا هادي لمن أضللت، ولا مضل لمن هديت، ولا معطي لما منعت، ولا مانع لما أعطيت، ولا مقرب بما باعدت، ولا مباعد لما قربت".','292','416','0');
INSERT INTO [t14299] VALUES (421,'مسند رفاعة بن عرابة الجهني رضي الله عنه','293','0','0');
INSERT INTO [t14299] VALUES (422,'٣٦٨ - حديث: "من ذا الذي يستغفرني فأغفر له".','293','421','0');
INSERT INTO [t14299] VALUES (423,'مسند رويفع بن ثابت رضي الله عنه','294','0','0');
INSERT INTO [t14299] VALUES (424,'٣٦٩ - حديث: "لعل الحياة ستطول بك".','294','423','0');
INSERT INTO [t14299] VALUES (425,'مسند الزبير بن العوام رضي الله عنه','294','0','0');
INSERT INTO [t14299] VALUES (426,'٣٧٠ - حديث: "إنا لا نورث، ما تركنا صدقة".','294','425','0');
INSERT INTO [t14299] VALUES (427,'٣٧١ - حديث: قال ابن الأثير في "النهاية" في حديث الزبير: "فتلقاه رسول الله صلى الله عليه وسلم كفة كفة".','296','425','0');
INSERT INTO [t14299] VALUES (428,'٣٧٢ - حديث شراج الحرة قوله: "أن كان ابن عمتك".','296','425','0');
INSERT INTO [t14299] VALUES (429,'مسند زياد بن نعيم الحضرمي رضي الله عنه','297','0','0');
INSERT INTO [t14299] VALUES (430,'٣٧٣ - حديث: "أربعا فرضهن الله في الإسلام، فمن جاء بثلاث لم يغنين عنه شيئا حتى يأتي بهن جميعا: الصلاة والزكاة وصيام رمضان وحج البيت".','297','429','0');
INSERT INTO [t14299] VALUES (431,'مسند زيد بن خالد رضي الله عنه','297','0','0');
INSERT INTO [t14299] VALUES (432,'٣٧٤ - حديث: "صلى بنا رسول الله صلى الله عليه وسلم صلاة الصبح على أثر سماء','297','431','0');
INSERT INTO [t14299] VALUES (433,'مسند زيد بن أرقم رضي الله عنه','298','0','0');
INSERT INTO [t14299] VALUES (434,'٣٧٥ - حديث: "كيف أنعم وصاحب القرن قد التقم القرن" إلى أن قال: "قولوا: حسبنا الله ونعم الوكيل".','298','433','0');
INSERT INTO [t14299] VALUES (435,'٣٧٦ - حديث الأضاحي: "بكل شعرة حسنة".','299','433','0');
INSERT INTO [t14299] VALUES (436,'٣٧٧ - حديث: "تركت فيكم ما إن تمسكتم به ... ".','299','433','0');
INSERT INTO [t14299] VALUES (437,'٣٧٨ - حديث: "لولا أن لا تدافنوا لدعوت الله أن يسمعكم من عذاب القبر ما أسمع".','299','433','0');
INSERT INTO [t14299] VALUES (438,'مسند زيد بن ثابت رضي الله عنه','300','0','0');
INSERT INTO [t14299] VALUES (439,'٣٧٩ - حديث: "اللهم فاطر السموات والأرض، عالم الغيب والشهادة، ذا الجلال والإكرام".','300','438','0');
INSERT INTO [t14299] VALUES (440,'٣٨٠ - حديث: "أما بادئ بدء فإني أحمد الله".','301','438','0');
INSERT INTO [t14299] VALUES (441,'٣٨١ - حديث: "كان الناس في عهد النبي صلى الله عليه وسلم يتبايعون الثمار، فإذا جذ الناس، وحضر تقاضيهم، قال المبتاع إنه أصاب الثمر الدمان أصابه مرض، أصابه قشام".','302','438','0');
INSERT INTO [t14299] VALUES (442,'مسند السائب بن خلاد رضي الله عنه','303','0','0');
INSERT INTO [t14299] VALUES (443,'٣٨٢ - حديث: "ما من شيء يصيب المؤمن حتى الشوكة تصيبه إلا كتب له بها حسنة".','303','442','0');
INSERT INTO [t14299] VALUES (444,'مسند السائب بن يزيد رضي الله عنه','304','0','0');
INSERT INTO [t14299] VALUES (445,'٣٨٣ - حديث: "كان الصاع على عهد رسول الله صلى الله عليه وسلم مد وثلث".','304','444','0');
INSERT INTO [t14299] VALUES (446,'مسند سبرة بن معبد الجهني رضي الله عنه','304','0','0');
INSERT INTO [t14299] VALUES (447,'٣٨٤ - حديث: "علموا الصبي الصلاة ابن سبع، واضربوه عليها ابن عشر".','304','446','0');
INSERT INTO [t14299] VALUES (448,'مسند سراقة رضي الله عنه','305','0','0');
INSERT INTO [t14299] VALUES (449,'٣٨٥ - حديث: "ألا أدلك على أفضل الصدقة، ابنتك مردودة إليك، ليس لها كاسب غيرك".','305','448','0');
INSERT INTO [t14299] VALUES (450,'مسند سعد بن أبي وقاص رضي الله عنه','305','0','0');
INSERT INTO [t14299] VALUES (451,'٣٨٦ - حديث: " ... أتصدق بمالي؟ قال لا، الشطر؟ قال لا، قال الثلث والثلث كثير".','305','450','0');
INSERT INTO [t14299] VALUES (452,'٣٨٧ - حديث: "كان جدار المسجد عند المدينة ما كادت الشاة أن تجوزها".','306','450','0');
INSERT INTO [t14299] VALUES (453,'٣٨٨ - حديث: "ارموا وأنا معكم كلكم".','307','450','0');
INSERT INTO [t14299] VALUES (454,'٣٨٩ - حديث: "بايعت رسول الله صلى الله عليه وسلم بيدي هذه، وأخرج لنا كفه كف ضخمة".','307','450','0');
INSERT INTO [t14299] VALUES (455,'٣٩٠ - حديث: "ألا أخبركم بأشد حرا منه يوم القيامة؟ هذينك الرجلين".','307','450','0');
INSERT INTO [t14299] VALUES (456,'٣٩١ - حديث خيبر، قوله: "فقال رجل: يا رسول الله أو أهريقها ونغسلها قال: أو ذاك".','308','450','0');
INSERT INTO [t14299] VALUES (457,'٣٩٢ - حديث: "أيما رجل وامرأة توافقا فعشرة ما بينهما ثلاث ليال فإن أحبا أن يتزايدا أو يتتاركا تتاركا".','309','450','0');
INSERT INTO [t14299] VALUES (458,'مسند سلمة بن نفيل السكوني رضي الله عنه','309','0','0');
INSERT INTO [t14299] VALUES (459,'٣٩٣ - حديث: "ولستم لابثون بعدي إلا قليلا".','309','458','0');
INSERT INTO [t14299] VALUES (460,'٣٩٤ - حديث: " .. الآن جاء القتال" وحديث: "الآن نقروهم ولا يقرونا".','310','458','0');
INSERT INTO [t14299] VALUES (461,'مسند سلمان الفارسي رضي الله عنه','312','0','0');
INSERT INTO [t14299] VALUES (462,'٣٩٥ - حديث: "رباط يوم وليلة أفضل من صيام شهر وقيامه صائما لا يفطر، وقائما لا يفتر".','312','461','0');
INSERT INTO [t14299] VALUES (463,'٣٩٦ - حديث: "ولا نكتفي بدون ثلاثة أحجار ليس فيها رجيع ... ".','313','461','0');
INSERT INTO [t14299] VALUES (464,'مسند سمرة بن جندب رضي الله عنه','313','0','0');
INSERT INTO [t14299] VALUES (465,'٣٩٧ - حديث المنام قوله: "فانطلقت إلى ثقب مثل التنور أعلاه ضيق وأسفله واسع يتوقد تحته نارا".','313','464','0');
INSERT INTO [t14299] VALUES (466,'٣٩٨ - حديث: "لا يتعاطى أحدكم أسير أخيه فيقتله".','317','464','0');
INSERT INTO [t14299] VALUES (467,'٣٩٩ - حديث: "إذا توضأ يوم الجمعة فبها ونعمت ... ".','318','464','0');
INSERT INTO [t14299] VALUES (468,'٤٠٠ - حديث: "على اليد ما أخذت حتى تؤدي".','319','464','0');
INSERT INTO [t14299] VALUES (469,'٤٠١ - حديث: "إذا أتى أحدكم على ماشية".','319','464','0');
INSERT INTO [t14299] VALUES (470,'٤٠٢ - حديث: "أنه صلى الله عليه وسلم صلى على امرأة ماتت في نفاسها فقام وسطها".','319','464','0');
INSERT INTO [t14299] VALUES (471,'٤٠٣ - حديث: "من ملك ذا رحم محرم فهو حر".','320','464','0');
INSERT INTO [t14299] VALUES (472,'٤٠٤ - حديث: "من حدث عني بحديث وهو يرى أنه كذب فهو أحد الكاذبين".','321','464','0');
INSERT INTO [t14299] VALUES (473,'٤٠٥ - حديث: "كيف تقول في الضب؟ فقال: أمة مسخت من بني إسرائيل، فلا أدري أي الدواب مسخت".','322','464','0');
INSERT INTO [t14299] VALUES (474,'مسند سوادة بن الربيع','322','0','0');
INSERT INTO [t14299] VALUES (475,'٤٠٦ - حديث: "مر بنيك فليقلموا أظافيرهم لا يعبطوا بها ضروع مواشيهم".','322','474','0');
INSERT INTO [t14299] VALUES (476,'مسند سهل بن الحنظلية رضي الله عنه','323','0','0');
INSERT INTO [t14299] VALUES (477,'٤٠٧ - حديث هوازن قوله: (.. على بكرة أبيهم".','323','476','0');
INSERT INTO [t14299] VALUES (478,'مسند سهل بن سعد الساعدي رضي الله عنه','323','0','0');
INSERT INTO [t14299] VALUES (479,'٤٠٨ - حديث اللعان، قوله: "وإن جاءت به أحيمر".','323','478','0');
INSERT INTO [t14299] VALUES (480,'٤٠٩ - حديث: "نهى عن بيع التمر بالتمر، إلا أنه رخص في العرايا أن تباع بخرصها تمرا يأكلها أهلها رطبا".','323','478','0');
INSERT INTO [t14299] VALUES (481,'٤١٠ - حديث: "كنت أتسحر في أهلي ثم تكون سرعة بي أن أدرك صلاة الفجر مع رسول الله صلى الله عليه وسلم".','324','478','0');
INSERT INTO [t14299] VALUES (482,'٤١١ - حديث: "كان الناس يصلون مع النبي صلى الله عليه وسلم وهم عاقدي أزرهم من العفر على رقابهم".','324','478','0');
INSERT INTO [t14299] VALUES (483,'٤١٢ - حديث: "كانت فينا المرأة تجعل على أربعاء في مزرعة لها سلقا ... ".','325','478','0');
INSERT INTO [t14299] VALUES (484,'٤١٣ - حديث: "جاءت امرأة ببردة، فأخذها النبي صلى الله عليه وسلم محتاج إليها".','326','478','0');
INSERT INTO [t14299] VALUES (485,'٤١٤ - حديث: "يا أبا بكر ما منعك حين أشير إليك لم تصل".','326','478','0');
INSERT INTO [t14299] VALUES (486,'٤١٥ - حديث: "ليدخلن الجنة من أمتي سبعون ألفا في سكون آخذ بعضهم بعضا".','327','478','0');
INSERT INTO [t14299] VALUES (487,'٤١٦ - حديث: "أما ترضى أن تكون مني بمنزلة هارون من موسى".','327','478','0');
INSERT INTO [t14299] VALUES (488,'٤١٧ - حديث: "حديث الحوض: "ليرد علي أقوام أعرفهم ويعرفوني ثم يحال بيني وبينهم".','328','478','0');
INSERT INTO [t14299] VALUES (489,'٤١٨ - حديث: "وفي القوم رجل لا يدع شاذة ولا فاذة إلا اتبعها".','328','478','0');
INSERT INTO [t14299] VALUES (490,'٤١٩ - حديث: "التمس ولو خاتما من حديد".','328','478','0');
INSERT INTO [t14299] VALUES (491,'مسند شداد بن أسامة الهادي رضي الله عنه','329','0','0');
INSERT INTO [t14299] VALUES (492,'٤٢٠ - حديث: "خرج علينا رسول الله صلى الله عليه وسلم في إحدى صلاتي العشي الظهر أو العصر".','329','491','0');
INSERT INTO [t14299] VALUES (493,'مسند شداد بن أوس رضي الله عنه','329','0','0');
INSERT INTO [t14299] VALUES (494,'٤٢١ - حديث: "أنا خير قسيم لمن أشرك بي، من أشرك بي شيئا فإن عمله قليله وكثيره لشريكه الذي أشرك به".','329','493','0');
INSERT INTO [t14299] VALUES (495,'٤٢٢ - حديث: "وأسألك من خير ما تعلم".','330','493','0');
INSERT INTO [t14299] VALUES (496,'٤٢٣ - حديث: "إن أخوف ما أخاف عليكم الرياء والشهوة الخفية".','330','493','0');
INSERT INTO [t14299] VALUES (497,'مسند الشريد رضي الله عنه','330','0','0');
INSERT INTO [t14299] VALUES (498,'٤٢٤ - حديث أنه صلى الله عليه وسلم قال له: "هل معك من شعر أمية شيء؟ قال: نعم، قال: هيه".','330','497','0');
INSERT INTO [t14299] VALUES (499,'٤٢٥ - حديث: "يا نعايا العرب" وفي رواية: "يا نعيان العرب".','332','497','0');
INSERT INTO [t14299] VALUES (500,'مسند صفوان بن أمية رضي الله عنه','332','0','0');
INSERT INTO [t14299] VALUES (501,'٤٢٦ - حديث: "أنه صلى الله عليه وسلم استعار منه يوم حنين أدراعا، فقال: أغصبا يا محمد؟ قال: بل عارية مضمونة".','332','500','0');
INSERT INTO [t14299] VALUES (502,'مسند صفوان بن عسال رضي الله عنه','333','0','0');
INSERT INTO [t14299] VALUES (503,'٤٢٧ - حديث: "كان يأمرنا إذا كنا سفرا أن لا ننزع خفافنا ثلاثة أيام ولياليهن إلا من جنابة، ولكن من بول وغائط ونوم".','333','502','0');
INSERT INTO [t14299] VALUES (504,'٤٢٨ - حديث: "قال يهودي لصاحبه: اذهب بنا إلى هذا النبي".','334','502','0');
INSERT INTO [t14299] VALUES (505,'مسند الصعب بن جثامة الليثي رضي الله عنه','335','0','0');
INSERT INTO [t14299] VALUES (506,'٤٢٩ - حديث: "إنا لم نرده عليك إلا أنا حرم".','335','505','0');
INSERT INTO [t14299] VALUES (507,'٤٣٠ - حديث: "لا حمى إلا لله ولرسوله".','336','505','0');
INSERT INTO [t14299] VALUES (508,'مسند طلحة بن عبيد الله رضي الله عنه','336','0','0');
INSERT INTO [t14299] VALUES (509,'٤٣١ - حديث: "رأيت فيما يرى النائم كأني على باب الجنة إذا أنابهما".','336','508','0');
INSERT INTO [t14299] VALUES (510,'٤٣٢ - قال النووي: برفع (ثائر) صفة لرحل، وقيل يجوز نصبه على الحال.','337','508','0');
INSERT INTO [t14299] VALUES (511,'مسند طلق بن علي رضي الله عنه','337','0','0');
INSERT INTO [t14299] VALUES (512,'٤٣٣ - حديث: "لا وتران في ليلة".','337','511','0');
INSERT INTO [t14299] VALUES (513,'مسند عامر بن ربيعة رضي الله عنه','338','0','0');
INSERT INTO [t14299] VALUES (514,'٤٣٤ - حديث: "رأيت رسول الله صلى الله عليه وسلم ما لا أحصي يتسوك وهو صائم".','338','513','0');
INSERT INTO [t14299] VALUES (515,'٤٣٥ - حديث','339','513','0');
INSERT INTO [t14299] VALUES (516,'مسند عبادة بن الصامت رضي الله عنه','341','0','0');
INSERT INTO [t14299] VALUES (517,'٤٣٦ - حديث: "ما على الأرض من نفس تموت ولها عند الله خير، تحب أن ترجع إلا القتيل في سبيل الله عز وجل، فإنه يحب أن يرجع فيقتل مرة أخرى".','341','516','0');
INSERT INTO [t14299] VALUES (518,'٤٣٧ - حديث: "لقد أعطاني الله حتى لو أطعمت أهل الجنة ما نقص ما عندي شيئا".','342','516','0');
INSERT INTO [t14299] VALUES (519,'٤٣٨ - حديث: "من أحب لقاء الله أحب الله لقاءه".','342','516','0');
INSERT INTO [t14299] VALUES (520,'٤٣٩ - حديث: "لا صلاة لمن لم يقرأ بفاتحة الكتاب".','343','516','0');
INSERT INTO [t14299] VALUES (521,'٤٤٠ - وحديث أبي سعيد: "أمرنا نبينا صلى الله عليه وسلم أن نقرأ بفاتحة الكتاب وما تيسر".','343','516','0');
INSERT INTO [t14299] VALUES (522,'٤٤١ - وفي حديث تميم: "من قرأ بمائة آية كتب له قنوت ليلة".','343','516','0');
INSERT INTO [t14299] VALUES (523,'٤٤٢ - حديث: "أن رسول الله صلى الله عليه وسلم قال وحوله عصابة".','345','516','0');
INSERT INTO [t14299] VALUES (524,'٤٤٣ - حديث: "من قال أشهد أن لا إله إلا الله، وأن محمدا عبده ورسوله، وأن عيسى عبد الله ورسوله وكلمته ألقاها إلى مريم، وروح منه .. ".','345','516','0');
INSERT INTO [t14299] VALUES (525,'٤٤٤ - حديث:" الذهب بالذهب". الحديث.','346','516','0');
INSERT INTO [t14299] VALUES (526,'٤٤٥ - حديث: "البكر بالبكر جلد مائة".','347','516','0');
INSERT INTO [t14299] VALUES (527,'٤٤٦ - حديث: "بايعنا رسول الله صلى الله عليه وسلم على السمع والطاعة".','347','516','0');
INSERT INTO [t14299] VALUES (528,'٤٤٧ - حديث: "خمس صلوات افترضهن الله".','348','516','0');
INSERT INTO [t14299] VALUES (529,'مسند عبد الله بن جعفر رضي الله عنه','348','0','0');
INSERT INTO [t14299] VALUES (530,'٤٤٨ - حديث: "رأيت رسول الله صلى الله عليه وسلم يأكل القثاء بالرطب".','348','529','0');
INSERT INTO [t14299] VALUES (531,'مسند العباس عم النبي صلى الله عليه وسلم ورضي الله عنه','349','0','0');
INSERT INTO [t14299] VALUES (532,'٤٤٩ - حديث حنين، قوله: "هذا حين حمي الوطيس".','349','531','0');
INSERT INTO [t14299] VALUES (533,'مسند عبد الله بن الزبير رضي الله عنه','349','0','0');
INSERT INTO [t14299] VALUES (534,'٤٥٠ - حديث، قول رسول الله صلى الله عليه وسلم: "اسق يا زبير، ثم أرسل الماء. فقال الأنصاري: إنه ابن عمتك".','349','533','0');
INSERT INTO [t14299] VALUES (535,'مسند عبد الله بن زيد رضي الله عنه','350','0','0');
INSERT INTO [t14299] VALUES (536,'٤٥١ - حديث: "رأيت النبي صلى الله عليه وسلم في المسجد مستلقيا".','350','535','0');
INSERT INTO [t14299] VALUES (537,'٤٥٢ - حديث الوضوء، قوله: "فمسح برأسه".','350','535','0');
INSERT INTO [t14299] VALUES (538,'مسند عبد الله بن عباس رضي الله عنه','352','0','0');
INSERT INTO [t14299] VALUES (539,'٤٥٣ - حديث: "نزلت هذه الآية ورسول الله صلى الله عليه وسلم متواريا بمكة: ولا تجهر بصلاتك".','352','538','0');
INSERT INTO [t14299] VALUES (540,'٤٥٤ - حديث: "من هم بحسنة فلم يعملها كتبت له حسنة".','352','538','0');
INSERT INTO [t14299] VALUES (541,'٤٥٥ - حديث: "اللهم اجعل في قلبي نورا، وفي بصري نورا، وفي سمعي نورا، وعن يميني نورا، وخلفي نورا، وأعظم لي نورا".','353','538','0');
INSERT INTO [t14299] VALUES (542,'٤٥٦ - حديث التسبيح، قوله: "عشر خصال".','354','538','0');
INSERT INTO [t14299] VALUES (543,'٤٥٧ - حديث: "ماتت شاة لسودة فقالت: يا رسول الله، ماتت فلانة، تعني الشاة".','354','538','0');
INSERT INTO [t14299] VALUES (544,'٤٥٨ - حديث: "من لعن شيئا ليس له بأهل رجعت اللعنة عليه".','355','538','0');
INSERT INTO [t14299] VALUES (545,'٤٥٩ - حديث: "كان رسول الله صلى الله عليه وسلم أجود الناس بالخير، وكان أجود ... ".','355','538','0');
INSERT INTO [t14299] VALUES (546,'٤٦٠ - حديث: "يا بني لا ترموا الجمرة حتى تطلع الشمس".','359','538','0');
INSERT INTO [t14299] VALUES (547,'٤٦١ - حديث: "سبحان الله عدد خلقه، وسبحان الله رضى نفسه، وسبحان الله زنة','360','538','0');
INSERT INTO [t14299] VALUES (548,'٤٦٢ - حديث: "إنك يا سعد أن تدع".','368','538','0');
INSERT INTO [t14299] VALUES (549,'٤٦٣ - حديث: "مالك عن فلان".','370','538','0');
INSERT INTO [t14299] VALUES (550,'٤٦٤ - حديث: "من ادعى غير أبيه، وهو يعلم أنه غير أبيه فالجنة عليه حرام".','372','538','0');
INSERT INTO [t14299] VALUES (551,'٤٦٥ - حديث: "إنها أيام أكل وشرب".','372','538','0');
INSERT INTO [t14299] VALUES (552,'٤٦٦ - حديث: "رأيت عن يمين رسول الله صلى الله عليه وسلم وعن شماله يوم أحد رجلين يقاتلان كأشد القتال".','373','538','0');
INSERT INTO [t14299] VALUES (553,'٤٦٧ - حديث: "من تصبح كل يوم سبع تمرات عجوة، لم يضره في ذلك اليوم سم ولا سحر".','373','538','0');
INSERT INTO [t14299] VALUES (554,'٤٦٨ - حديث: "المدينة خير لهم لو كانوا يعلمون".','374','538','0');
INSERT INTO [t14299] VALUES (555,'٤٦٩ - حديث: "لو أن ما يقل ظفر مما في الجنة بدا لتزخرفت له ما بين خوافق السموات والأرض".','374','538','0');
INSERT INTO [t14299] VALUES (556,'٤٧٠ - حديث: "استأذن عمر بن الخطاب على رسول الله صلى الله عليه وسلم وعنده نسوة من قريش يكلمنه ويستكثرنه، عالية أصواتهن".','375','538','0');
INSERT INTO [t14299] VALUES (557,'مسند سعيد بن زيد بن عمرو بن نفيل رضي الله عنه','375','0','0');
INSERT INTO [t14299] VALUES (558,'٤٧١ - حديث: "اثبت حراء أو أحد، فإنما عليك نبي أو صديق أو شهيد".','375','557','0');
INSERT INTO [t14299] VALUES (559,'٤٧٢ - حديث: "أشهد لسمعت رسول الله صلى الله عليه وسلم يقول: من أخذ شبرا من الأرض ... ".','376','557','0');
INSERT INTO [t14299] VALUES (560,'٤٧٣ - حديث: "الكمأة من المن".','379','557','0');
INSERT INTO [t14299] VALUES (561,'مسند سفيان بن أسيد الحضرمي رضي الله عنه','379','0','0');
INSERT INTO [t14299] VALUES (562,'٤٧٤ - حديث: "كبرت خيانة أن تحدث أخاك حديثا هو لك مصدق وأنت له كاذب".','379','561','0');
INSERT INTO [t14299] VALUES (563,'مسند سلمة بن سلامة بن وقش الأنصاري رضي الله عنه','380','0','0');
INSERT INTO [t14299] VALUES (564,'٤٧٥ - حديث البعث، قوله: "لا يرون أن بعثا كائنا بعد الموت".','380','563','0');
INSERT INTO [t14299] VALUES (565,'مسند سلمة بن الأكوع رضي الله عنه','380','0','0');
INSERT INTO [t14299] VALUES (566,'٤٧٦ - حديث: "كنا نصلي مع رسول الله صلى الله عليه وسلم إذا توارت بالحجاب".','380','565','0');
INSERT INTO [t14299] VALUES (567,'٤٧٧ - حديث: "من حمل علينا السلاح فليس منا".','381','565','0');
INSERT INTO [t14299] VALUES (568,'٤٧٨ - حديث الحديبية، قوله: "فبايعته أول الناس".','381','565','0');
INSERT INTO [t14299] VALUES (569,'٤٧٩ - حديث: "فما اشتكيتها حتى الساعة".','384','565','0');
INSERT INTO [t14299] VALUES (570,'٤٨٠ - حديث أسلم: "يا لها الله وعنك عفو الله لها أما والله ما قلته ولكن الله قاله".','385','565','0');
INSERT INTO [t14299] VALUES (571,'٤٨١ - حديث: "من استمع إلى حديث قوم وهم له كارهون صب في أذنيه الآنك".','385','565','0');
INSERT INTO [t14299] VALUES (572,'٤٨٢ - حديث: "من رأى منكم رؤيا فليقصها أعبرها له".','386','565','0');
INSERT INTO [t14299] VALUES (573,'٤٨٣ - حديث: "قضى في امرأتين بغرة عبد أو أمة".','386','565','0');
INSERT INTO [t14299] VALUES (574,'٤٨٤ - حديث: "الحج مرة".','386','565','0');
INSERT INTO [t14299] VALUES (575,'٤٨٥ - حديث: "من تعلم كتاب الله ثم اتبع ما فيه هداه الله من الضلالة".','386','565','0');
INSERT INTO [t14299] VALUES (576,'٤٨٦ - حديث: "اتقوا الحديث إلا ما علمتكم".','387','565','0');
INSERT INTO [t14299] VALUES (577,'٤٨٧ - حديث وفد عبد القيس، قوله: "مرحبا بالوفد غير خزايا ولا ندامى".','387','565','0');
INSERT INTO [t14299] VALUES (578,'٤٨٨ - حديث: "الطواف بالبيت مثل الصلاة، إلا أن الله أباح فيه الكلام".','389','565','0');
INSERT INTO [t14299] VALUES (579,'٤٨٩ - حديث: حديث: "ائتوني بكتاب".','389','565','0');
INSERT INTO [t14299] VALUES (580,'٤٩٠ - حديث: مر رسول الله صلى الله عليه وسلم بحائط من حيطان المدينة، فسمع','389','565','0');
INSERT INTO [t14299] VALUES (581,'٤٩١ - حديث: "أعوذ بكلمات الله التامات من شر كل شيطان وهامة ومن كل عين لامة".','392','565','0');
INSERT INTO [t14299] VALUES (582,'٤٩٢ - حديث: "ما رأيت النبي صلى الله عليه وسلم يتحرى صيام يوم فضله على غيره إلا هذا اليوم".','392','565','0');
INSERT INTO [t14299] VALUES (583,'٤٩٣ - حديث: "رحم الله أم إسماعيل، لو تركت زمزم لكانت عينا معينا".','393','565','0');
INSERT INTO [t14299] VALUES (584,'٤٩٤ - حديث: "قدم النبي صلى الله عليه وسلم المدينة وهم يسلفون في الثمار السنة والسنتين".','393','565','0');
INSERT INTO [t14299] VALUES (585,'٤٩٥ - حديث: "صومي عن أمك".','393','565','0');
INSERT INTO [t14299] VALUES (586,'٤٩٦ - حديث: "قال لبريرة: لو راجعتيه".','393','565','0');
INSERT INTO [t14299] VALUES (587,'٤٩٧ - حديث: "لو كان لابن آدم واديان من مال، لابتغى لهما ثالثا".','394','565','0');
INSERT INTO [t14299] VALUES (588,'٤٩٨ - حديث: "لا يخلون رجل بامرأة إلا ومعها ذو محرم".','394','565','0');
INSERT INTO [t14299] VALUES (589,'٤٩٩ - حديث: "بعث النبي صلى الله عليه وسلم بسحر من جمع".','394','565','0');
INSERT INTO [t14299] VALUES (590,'٥٠٠ - حديث: "ما من عبد مؤمن إلا وله ذنب يقتاده الفينة بعد الفينة".','394','565','0');
INSERT INTO [t14299] VALUES (591,'٥٠١ - حديث: "كان رسول الله صلى الله عليه وسلم يجمع بين صلاتي الظهر والعصر إذا كان على ظهر سير".','395','565','0');
INSERT INTO [t14299] VALUES (592,'٥٠٢ - حديث: "كل ما شئت والبس ما شئت ما أخطأك اثنتان: سرف أو مخيلة".','395','565','0');
INSERT INTO [t14299] VALUES (593,'٥٠٣ - حديث: "أنه صلى الله عليه وسلم اتخذ خاتما فلبسه، فقال: شغلني هذا عنكم، منذ اليوم، إليه نظرة وإليكم نظرة، ثم ألقاه".','395','565','0');
INSERT INTO [t14299] VALUES (594,'٥٠٤ - حديث: "أنه قال في المعتكف، هو يعتكف الذنوب".','396','565','0');
INSERT INTO [t14299] VALUES (595,'٥٠٥ - حديث: "اغتسلوا يوم الجمعة، واغسلوا رؤوسكم وإن لم تكونوا جنبا".','396','565','0');
INSERT INTO [t14299] VALUES (596,'٥٠٦ - حديث: "ما العمل في أيام أفضل منها في هذه".','396','565','0');
INSERT INTO [t14299] VALUES (597,'٥٠٧ - حديث: "المسألة أن ترفع يديك حذو منكبيك".','398','565','0');
INSERT INTO [t14299] VALUES (598,'٥٠٨ - حديث: "خمس دعوات يستجاب لهن: دعوة المظلوم حتى ينتصر، ودعوة الحاج حتى يصدر، ودعوة المجاهد حتى يفرغ، ودعوة المريض حتى يصح".','398','565','0');
INSERT INTO [t14299] VALUES (599,'٥٠٩ - حديث: "أعوذ بعزتك الذي لا إله إلا أنت الذي لا تموت".','398','565','0');
INSERT INTO [t14299] VALUES (600,'٥١٠ - حديث: "من أصبح مطيعا لله في والديه، أصبح له بابان مفتوحان من الجنة".','399','565','0');
INSERT INTO [t14299] VALUES (601,'٥١١ - حديث: "إن الله خلق إسرافيل منذ يوم خلقه صافا قدميه".','399','565','0');
INSERT INTO [t14299] VALUES (602,'٥١٢ - حديث: "أمر النبي صلى الله عليه وسلم أن يسجد على سبعة أعضاء ولا يكفت شعرا ولا ثوبا".','399','565','0');
INSERT INTO [t14299] VALUES (603,'٥١٣ - حديث: "أمرت أن أسجد على سبعة أعظم: على الجبهة ... ".','400','565','0');
INSERT INTO [t14299] VALUES (604,'٥١٤ - حديث: "أنه عق عن الحسن والحسين كبشا كبشا".','400','565','0');
INSERT INTO [t14299] VALUES (605,'٥١٥ - حديث: "لا هجرة ولكن جهاد".','400','565','0');
INSERT INTO [t14299] VALUES (606,'٥١٦ - حديث: "أنا ممن قدم النبي صلى الله عليه وسلم المزدلفة في ضعفة أهله".','401','565','0');
INSERT INTO [t14299] VALUES (607,'٥١٧ - حديث: "إنك مقدم على أقوام أهل كتاب، فليكن أول ما تدعوهم إليه عبادة الله".','401','565','0');
INSERT INTO [t14299] VALUES (608,'٥١٨ - حديث: "ولم يمنعه أن يأمرهم أن يرملوا الأشواط كلها".','402','565','0');
INSERT INTO [t14299] VALUES (609,'٥١٩ - حديث: "العائد في هبته كالعائد في قيئه".','402','565','0');
INSERT INTO [t14299] VALUES (610,'٥٢٠ - حديث: "قول أبي بكر: حسبك كذاك مناشدتك ربك".','402','565','0');
INSERT INTO [t14299] VALUES (611,'٥٢١ - حديث: "ألحقوا الفرائض بأهلها، فما بقي فهو لأولى رجل ذكر".','403','565','0');
INSERT INTO [t14299] VALUES (612,'٥٢٢ - حديث: "لا يخلون رجل بامرأة، ولا تسافرن امرأة إلا ومعها محرم".','403','565','0');
INSERT INTO [t14299] VALUES (613,'٥٢٣ - حديث: "لا تمار أخاك ولا تمازحه ولا تعده موعدا فتخلفه".','404','565','0');
INSERT INTO [t14299] VALUES (614,'٥٢٤ - حديث: "البقرة سبعة".','404','565','0');
INSERT INTO [t14299] VALUES (615,'٥٢٥ - حديث: "من لم يجد النعلين فليلبس الخفين، ومن لم يجد إزارا فليلبس سروايل المحرم".','404','565','0');
INSERT INTO [t14299] VALUES (616,'٥٢٦ - حديث: "أن النبي صلى الله عليه وسلم لما قدم مكة، استقبله أغيلمة بني هاشم".','404','565','0');
INSERT INTO [t14299] VALUES (617,'٥٢٧ - حديث: "من سمع المنادي فلم يمنعه من اتباعه عذر - قالوا: وما العذر، قال: خوف أو مرض - لم تقبل منه الصلاة التي صلى".','405','565','0');
INSERT INTO [t14299] VALUES (618,'٥٢٨ - حديث: "التمسوها في العشر الأواخر من رمضان، ليلة القدر في تاسعة تبقى، في سابعة تبقى، في خامسة تبقى، ... ".','405','565','0');
INSERT INTO [t14299] VALUES (619,'٥٢٩ - حديث: "كان النبي صلى الله عليه وسلم إذا نزل جبريل بالوحي، فكان مما يحرك به لسانه وشفتيه، وفي لفظ كان يعالج من التنزيل شدة، وكان مما يحرك شفتيه".','406','565','0');
INSERT INTO [t14299] VALUES (620,'٥٣٠ - حديث: "رأيت النار فلم أر كاليوم منظرا، ورأيت أكثر أهلها النساء".','407','565','0');
INSERT INTO [t14299] VALUES (621,'٥٣١ - حديث: "أريت النار، أكثر أهلها النساء يكفرن ... ".','409','565','0');
INSERT INTO [t14299] VALUES (622,'٥٣٢ - حديث: "كأني به أسود أفحج يقلعها حجرا حجرا".','410','565','0');
INSERT INTO [t14299] VALUES (623,'٥٣٣ - حديث: "خطبنا ابن عباس في يوم زرع".','416','565','0');
INSERT INTO [t14299] VALUES (624,'٥٣٤ - حديث: "صليت مع النبي صلى الله عليه وسلم ذات ليلة".','417','565','0');
INSERT INTO [t14299] VALUES (625,'٥٣٥ - حديث: "أن هلال بن أمية قذف امرأته بشريط بن سحماء فقال النبي صلى الله عليه وسلم البينة أو حدا في ظهرك".','417','565','0');
INSERT INTO [t14299] VALUES (626,'٥٣٦ - حديث: "لينتهين أقوام عن ودعهم الجماعات".','418','565','0');
INSERT INTO [t14299] VALUES (627,'٥٣٧ - حديث: "دية أصابع اليدين والرجلين سواء عشرة من الإبل لكل أصبع".','419','565','0');
INSERT INTO [t14299] VALUES (628,'٥٣٨ - حديث: "إلا أن الله أعانني عليه فأسلم".','419','565','0');
INSERT INTO [t14299] VALUES (629,'٥٣٩ - حديث: "إن أول ما خلق الله القلم، فقال له: اكتب، قال ما أكتب؟ قال: اكتب القدر".','419','565','0');
INSERT INTO [t14299] VALUES (630,'٥٤٠ - حديث: "خمس كلهن فاسقة".','421','565','0');
INSERT INTO [t14299] VALUES (631,'٥٤١ - حديث غسل النبي صلى الله عليه وسلم: "نشدتك الله وحظنا من رسول الله".','421','565','0');
INSERT INTO [t14299] VALUES (632,'٥٤٢ - حديث: "صلى رسول الله صلى الله عليه وسلم صلاة الخوف بذي قرد صفا خلفه".','422','565','0');
INSERT INTO [t14299] VALUES (633,'٥٤٣ - حديث: "التحيات المباركات الصلوات الطيبات لله".','422','565','0');
INSERT INTO [t14299] VALUES (634,'٥٤٤ - حديث: "أمني جبريل عند البيت مرتين، فصلى بي الظهر حين زالت الشمس".','423','565','0');
INSERT INTO [t14299] VALUES (635,'٥٤٥ - حديث: "نعيت إلي نفسي".','423','565','0');
INSERT INTO [t14299] VALUES (636,'٥٤٦ - حديث: "ألم ألقاكم على تلك الحال".','424','565','0');
INSERT INTO [t14299] VALUES (637,'٥٤٧ - حديث: "قام رسول الله صلى الله عليه وسلم يوما يصلي فخطر خطرة".','424','565','0');
INSERT INTO [t14299] VALUES (638,'٥٤٨ - حديث: "وأنا أخشى أن يكون بي جنن".','424','565','0');
INSERT INTO [t14299] VALUES (639,'٥٤٩ - حديث: "من فارق الجماعة شبرا فمات إلا مات ميتة جاهلية".','424','565','0');
INSERT INTO [t14299] VALUES (640,'٥٥٠ - حديث "خير نساء ركبن الإبل صالح نساء قريش أحناه على ولد في صغر، وأرعاه على بعل ذات يد".','426','565','0');
INSERT INTO [t14299] VALUES (641,'٥٥١ - حديث "فرض صدقة رمضان نصف صاع من بر أو صاع من شعير أو صاع من تمر".','428','565','0');
INSERT INTO [t14299] VALUES (642,'[٥٥٢]- حديث "خير يوم تحتجمون فيه سبع عشرة وتسع عشرة وإحدى وعشرين".','428','565','0');
INSERT INTO [t14299] VALUES (643,'٥٥٣ - حديث "أحب الدين إلى الله الحنيفية السمحة".','429','565','0');
INSERT INTO [t14299] VALUES (644,'٥٥٤ - حديث "لما نزلت: "وأنذر عشيرتك الأقربين" صعد على الصفا فنادى: يا بني عبد مناف لبطون قريش".','430','565','0');
INSERT INTO [t14299] VALUES (645,'٥٥٥ - حديث "أن النبي صلى الله عليه وسلم صلى بالمدينة سبعا وثمانيا الظهر والعصر والمغرب والعشاء".','430','565','0');
INSERT INTO [t14299] VALUES (646,'٥٥٦ - حديث "أن يمنح أحدكم أخاه خير له من أن يأخذ عليه خرجا معلوما".','431','565','0');
INSERT INTO [t14299] VALUES (647,'٥٥٧ - حديثه "أنه صلى الله عليه وسلم قال لمسيلمة: لو سألتني هذه القطعة ما أعيطتكها ولن تعد أمر الله فيك".','431','565','0');
INSERT INTO [t14299] VALUES (648,'مسند عبد الله بن عتيك الأنصاري رضي الله عنه','431','0','0');
INSERT INTO [t14299] VALUES (649,'٥٥٨ - حديث "من خرج من بيته مجاهدا في سبيل الله فخر عن دابته فمات، أو مات حتف أنفه فقد وقع أجره على الله. والله إنها لكلمة ما سمعتها من أحد من العرب قبل رسول الله صلى الله عليه وسلم".','431','648','0');
INSERT INTO [t14299] VALUES (650,'مسند عبد الله بن عمر رضي الله عنه','432','0','0');
INSERT INTO [t14299] VALUES (651,'٥٥٩ - حديث "صلاة الليل مثنى مثنى".','432','650','0');
INSERT INTO [t14299] VALUES (652,'٥٦٠ - حديث "يصلي أحدكم مثنى مثنى".','432','650','0');
INSERT INTO [t14299] VALUES (653,'٥٦١ - حديث "كان رسول الله صلى الله عليه وسلم يصلي من الليل مثنى مثنى".','432','650','0');
INSERT INTO [t14299] VALUES (654,'٥٦٢ - حديث: "إن يكن هو فلن تسلط ... ".','435','650','0');
INSERT INTO [t14299] VALUES (655,'٥٦٣ - حديث: "الدجال إن يخرج من غضبة يغضبها".','435','650','0');
INSERT INTO [t14299] VALUES (656,'٥٦٤ - حديث: "باب أمتى الذي يدخلون منه الجنة عرضه مسيرة الراكب المجود ثلاثا".','436','650','0');
INSERT INTO [t14299] VALUES (657,'٥٦٥ - حديث: "الذي تفوته صلاة العصر فكأنما وتر أهله وماله".','436','650','0');
INSERT INTO [t14299] VALUES (658,'٥٦٦ - حديث: "مثل المنافق مثل الشاة العائرة بين الغنمين".','437','650','0');
INSERT INTO [t14299] VALUES (659,'٥٦٧ - حديث: "يا أهل الجنة خلود لا موت".','437','650','0');
INSERT INTO [t14299] VALUES (660,'٥٦٨ - حديث: "بني الإسلام على خمس: شهادة أن لا إله إلا الله".','438','650','0');
INSERT INTO [t14299] VALUES (661,'٥٦٩ - حديث: "إن شئت حبست أصلها".','439','650','0');
INSERT INTO [t14299] VALUES (662,'٥٧٠ - حديث: "وكان تمرهم دون".','439','650','0');
INSERT INTO [t14299] VALUES (663,'٥٧١ - حديث: "مثلكم ومثل اليهود والنصارى كرجل استعمل عمالا فقال: من يعمل لي من صلاة الصبح إلى نصف النهار على قيراط قيراط؟ فعملت اليهود، ثم قال: من يعمل لي من نصف النهار إلى صلاة العصر على قيراط قيراط؟ فعملت النصارى، ثم قال: من يعمل لي من صلاة العصر إلى مغرب الشمس على قيراطين قيراطين؟','439','650','0');
INSERT INTO [t14299] VALUES (664,'٥٧٢ - حديث: "إنما مثلكم واليهود والنصارى كرجل استعمل عمالا".','441','650','0');
INSERT INTO [t14299] VALUES (665,'٥٧٣ - حديث: "لا حسد إلا في اثنتين: رجل آتاه الله مالا ... ".','442','650','0');
INSERT INTO [t14299] VALUES (666,'٥٧٤ - حديث: "يكون في أمتي خسف ومسخ وقذف في أهل القدر".','442','650','0');
INSERT INTO [t14299] VALUES (667,'٥٧٥ - حديث: "أبعثها قياما مقيدة سنة محمد صلى الله عليه وسلم".','442','650','0');
INSERT INTO [t14299] VALUES (668,'٥٧٦ - حديث: "من مات وعليه صيام شهر فليطعم عنه مكان كل يوم مسكينا".','443','650','0');
INSERT INTO [t14299] VALUES (669,'٥٧٧ - حديث: "إن رسول الله صلى الله عليه وسلم مر على رجل من الأنصار وهو يعظ أخاه في الحياء ... " ولمسلم: "مر برجل ... ".','443','650','0');
INSERT INTO [t14299] VALUES (670,'٥٧٨ - حديث: "إنه سئل عن الماء وما ينوبه من السباع".','443','650','0');
INSERT INTO [t14299] VALUES (671,'٥٧٩ - حديث: "أمرت أن أقاتل الناس ... ".','444','650','0');
INSERT INTO [t14299] VALUES (672,'٥٨٠ - حديث: "اللهم ارحم المحلقين، قالوا: والمقصرين يا رسول الله ... ".','444','650','0');
INSERT INTO [t14299] VALUES (673,'٥٨١ - حديث: "يا معشر النساء تصدقن فإني رأيتكن أكثر أهل النار".','444','650','0');
INSERT INTO [t14299] VALUES (674,'٥٨٢ - حديث: "إن أبر البر أن يصل الرجل أهل ود أبيه".','445','650','0');
INSERT INTO [t14299] VALUES (675,'٥٨٣ - حديث: "كل شيء بقدر حتى العجز والكيس".','445','650','0');
INSERT INTO [t14299] VALUES (676,'٥٨٤ - حديث "خصلتان معلقتان في أعناق المؤذنين للمسلمين صلاتهم وصيامهم".','445','650','0');
INSERT INTO [t14299] VALUES (677,'٥٨٥ - حديث: "ما من أيام أعظم عند الله ولا أحب فيهن العمل من هذه الأيام العشر".','446','650','0');
INSERT INTO [t14299] VALUES (678,'٥٨٦ - وحديث: "ما من أيام أحب إلى الله فيها الصوم من أيام العشر".','446','650','0');
INSERT INTO [t14299] VALUES (679,'٥٨٧ - حديث: "فيما سقت السماء والعيون والبعل العشر".','447','650','0');
INSERT INTO [t14299] VALUES (680,'٥٨٨ - حديث المواقيت "هن لهن ولمن أتى عليهن من غير أهلهن".','451','650','0');
INSERT INTO [t14299] VALUES (681,'٥٨٩ - حديث: "نهى رسول الله صلى الله عليه وسلم أن يقيم الرجل من مقعده ويجلس فيه".','453','650','0');
INSERT INTO [t14299] VALUES (682,'٥٩٠ - حديث: "إن الله لا يعذب بدمع ولا بحزن القلب، ولكن يعذب بهذا – وأشار إلى لسانه – أو يرحم".','453','650','0');
INSERT INTO [t14299] VALUES (683,'٥٩١ - حديث "اجعلوا آخر صلاتكم بالليل وترا".','454','650','0');
INSERT INTO [t14299] VALUES (684,'٥٩٢ - حديث "قول الملك في النوم لعبد الله بن عمر: لن ترع لن ترع".','454','650','0');
INSERT INTO [t14299] VALUES (685,'٥٩٣ - حديث "كن في الدنيا كأنك غريب أو عابر سبيل".','455','650','0');
INSERT INTO [t14299] VALUES (686,'٥٩٤ - حديث "لا تدخلوا على هؤلاء المعذبين إلا أن تكونوا باكين فإن لم تكونوا باكين فلا تدخلوا عليهم لا يصيبكم ما أصابهم".','455','650','0');
INSERT INTO [t14299] VALUES (687,'٥٩٥ - حديث: "من استعاذ بالله فأعيذوه".','455','650','0');
INSERT INTO [t14299] VALUES (688,'٥٩٦ - حديث "إنما بقاؤكم فيمن سلف قبلكم من الأمم كما بين صلاة العصر إلى غروب الشمس".','455','650','0');
INSERT INTO [t14299] VALUES (689,'٥٩٧ - حديث "إن بلالا ينادي بليل".','456','650','0');
INSERT INTO [t14299] VALUES (690,'٥٩٨ - حديث "من حالت شفاعته دون حد".','456','650','0');
INSERT INTO [t14299] VALUES (691,'٥٩٩ - حديث "لا يتحرى أحدكم فيصلي عند طلوع الشمس ولا عند غروبها".','456','650','0');
INSERT INTO [t14299] VALUES (692,'٦٠٠ - حديث "إذا وضع عشاء أحدكم وأقيمت الصلاة فابدأوا بالعشاء، ولا يعجل حتى يفرغ منه".','457','650','0');
INSERT INTO [t14299] VALUES (693,'٦٠١ - حديث "لا يقبل صلاة بغير طهور".','457','650','0');
INSERT INTO [t14299] VALUES (694,'٦٠٢ - حديث "رأيت رسول الله صلى الله عليه وسلم ركب راحلته ثم يهل حين تستوي به راحلته، وروي حتى تستوي به راحلته".','458','650','0');
INSERT INTO [t14299] VALUES (695,'٦٠٣ - حديث: "إن ابن عبد الله بن عمر قال لأبيه: أقم فإني لا إيمنها أن ستصد عن البيت ... ".','459','650','0');
INSERT INTO [t14299] VALUES (696,'٦٠٤ - حديث "الوتر ركعة من آخر الليل".','459','650','0');
INSERT INTO [t14299] VALUES (697,'٦٠٥ - حديث "سبحان الله هي صلاة الملائكة".','459','650','0');
INSERT INTO [t14299] VALUES (698,'٦٠٦ - حديث: "إن أحدكم إذا مات عرض عليه مقعده بالغداة والعشي، إن كان من أهل','459','650','0');
INSERT INTO [t14299] VALUES (699,'٦٠٧ - حديث: "من كان في حاجة أخيه كان الله في حاجته".','461','650','0');
INSERT INTO [t14299] VALUES (700,'٦٠٨ - حديث "إن النبي صلى الله عليه وسلم أقطع الزبير بين العوام حضر فرسه".','462','650','0');
INSERT INTO [t14299] VALUES (701,'٦٠٩ - حديث: "كان النبي صلى الله عليه وسلم لا يصلي بعد الجمعة حتى ينصرف فيصلي ركعتين في بيته".','462','650','0');
INSERT INTO [t14299] VALUES (702,'٦١٠ - حديث: "إن العبد إذا نصح لسيده ... ".','462','650','0');
INSERT INTO [t14299] VALUES (703,'٦١١ - حديث "اللهم متعنا بأسماعنا وأبصارنا وقوتنا ما أحييتنا واجعله الوارث منا".','463','650','0');
INSERT INTO [t14299] VALUES (704,'٦١٢ - حديث "أرأيتكم ليلتكم هذه فإن على رأس مائة سنة منها لا يبقى ممن هو على ظهر الأرض أحد".','463','650','0');
INSERT INTO [t14299] VALUES (705,'٦١٣ - حديث: "أراني أتسوك بسواك".','465','650','0');
INSERT INTO [t14299] VALUES (706,'٦١٤ - حديث: "كان المسلمون حين قدموا المدينة يجتمعون فيتحينون الصلاة ليس ينادى لها".','465','650','0');
INSERT INTO [t14299] VALUES (707,'٦١٥ - حديث "مهل أهل المدينة".','466','650','0');
INSERT INTO [t14299] VALUES (708,'٦١٦ - حديث "لا تدخلوا على هؤلاء المعذبين إلا أن تكونوا باكين، أن يصيبكم مثل ما أصابهم".','466','650','0');
INSERT INTO [t14299] VALUES (709,'٦١٧ - حديث: "إن بين يدي الساعة ثلاثون دجالا كذابا".','466','650','0');
INSERT INTO [t14299] VALUES (710,'٦١٨ - حديث "إن المسيح الدجال أعور عين اليمنى".','467','650','0');
INSERT INTO [t14299] VALUES (711,'٦١٩ - حديث: "هن من الشجرة شجرة لا يسقط ورقها وإنما مثل المؤمن فحدثوني ما هي".','467','650','0');
INSERT INTO [t14299] VALUES (712,'٦٢٠ - حديث "اجعلوا في بيوتكم من صلاتكم".','467','650','0');
INSERT INTO [t14299] VALUES (713,'٦٢١ - حديث "لبيك إن الحمد لك".','468','650','0');
INSERT INTO [t14299] VALUES (714,'٦٢٢ - حديث "لبيك وسعديك".','468','650','0');
INSERT INTO [t14299] VALUES (715,'٦٢٣ - حديث: "البيعان بالخيار ما لم يتفرقا أو يختارا".','471','650','0');
INSERT INTO [t14299] VALUES (716,'٦٢٤ - حديث: "البيعان بالخيار ما لم يتفرقا أو يقول أحدهما للآخر اختر".','471','650','0');
INSERT INTO [t14299] VALUES (717,'٦٢٥ - حديث "من ضرب غلاما له، حدا لم يأته".','472','650','0');
INSERT INTO [t14299] VALUES (718,'٦٢٦ - حديث "أكثر ما كان النبي صلى الله عليه وسلم يحلف لا ومقلب القلوب".','472','650','0');
INSERT INTO [t14299] VALUES (719,'٦٢٧ - حديث "من كان قاضيا فقضى بالعدل فبالحري أن يتقلب كفافا".','472','650','0');
INSERT INTO [t14299] VALUES (720,'٦٢٨ - حديث "فرض رسول الله صلى الله عليه وسلم صدقة الفطر صاعا من تمر".','473','650','0');
INSERT INTO [t14299] VALUES (721,'٦٢٩ - حديث "أليس حسبكم سنة رسول الله صلى الله عليه وسلم – إن حبس أحدكم عن الحج طاف بالبيت".','473','650','0');
INSERT INTO [t14299] VALUES (722,'٦٣٠ - حديث "لا تلبسوا القمص ولا العمائم ولا السراويلات ولا البرانس ولا الخفاف إلا أحد لا يجد النعلين فليلبس الخفين".','474','650','0');
INSERT INTO [t14299] VALUES (723,'٦٣١ - حديث "كنت أنت تجيء به".','474','650','0');
INSERT INTO [t14299] VALUES (724,'٦٣٢ - حديث "لا ينقش أحد على نقش خاتمي".','474','650','0');
INSERT INTO [t14299] VALUES (725,'٦٣٣ - حديث "الأضحى يومان بعد يوم الأضحى".','475','650','0');
INSERT INTO [t14299] VALUES (726,'٦٣٤ - حديث "من أعتق شركا له في مملوك فعليه عتقه كله".','475','650','0');
INSERT INTO [t14299] VALUES (727,'٦٣٥ - حديث "ما حق امرئ مسلم له شيء يوصي فيه يبيت ليلتين إلا ووصيته مكتوبة عنده".','475','650','0');
INSERT INTO [t14299] VALUES (728,'٦٣٦ - حديث "بينما أنا أطوف بالكعبة فإذا رجل آدم سبط الشعر".','476','650','0');
INSERT INTO [t14299] VALUES (729,'٦٣٧ - حديث "أن رجلا قال: يا رسول الله، أي الليل أجوبه".','478','650','0');
INSERT INTO [t14299] VALUES (730,'٦٣٨ - حديث "فما خفي عليكم من شأنه فليس يخفى عليكم أن ربكم ليس بأعور".','479','650','0');
INSERT INTO [t14299] VALUES (731,'٦٣٩ - حديث "نهى النبي صلى الله عليه وسلم أن يبيع بعضكم على بيع بعض، ولا يخطب الرجل على خطبة أخيه".','479','650','0');
INSERT INTO [t14299] VALUES (732,'٦٤٠ - حديث "نهى عن الإقران".','479','650','0');
INSERT INTO [t14299] VALUES (733,'٦٤١ - حديث "الوقت الأول من الصلاة رضوان الله".','479','650','0');
INSERT INTO [t14299] VALUES (734,'٦٤٢ - حديث "من اقتضى كلبا ليس بكلب ماشية أو ضارية".','480','650','0');
INSERT INTO [t14299] VALUES (735,'٦٤٣ - حديث "اجتنبوا الموبقات: الشرك بالله"','481','650','0');
INSERT INTO [t14299] VALUES (736,'٦٤٤ - حديث "أيما امرئ قال لأخيه كافر، فقد باء بها أحدهما".','481','650','0');
INSERT INTO [t14299] VALUES (737,'٦٤٥ - حديث "أنه قال في حجة الوداع"','481','650','0');
INSERT INTO [t14299] VALUES (738,'٦٤٦ - حديث "من توضأ على طهر كتب له عشر حسنات".','482','650','0');
INSERT INTO [t14299] VALUES (739,'٦٤٧ - حديث "من صبر على لأوائها كنت له شفيعا أو شهيدا يوم القيامة".','482','650','0');
INSERT INTO [t14299] VALUES (740,'٦٤٨ - حديث "من استطاع أن يموت بالمدينة فليمت بها".','483','650','0');
INSERT INTO [t14299] VALUES (741,'٦٤٩ - حديث "الغار".','483','650','0');
INSERT INTO [t14299] VALUES (742,'٦٥٠ - حديث "إذا مشت أمتي المطيطاء".','484','650','0');
INSERT INTO [t14299] VALUES (743,'٦٥١ - حديث "الأيدي ثلاث: فيد الله هي العليا، ويد المعطي التي تليها، ويد السائل السفلى".','484','650','0');
INSERT INTO [t14299] VALUES (744,'مسند عبد الله بن عمرو بن العاص رضي الله عنه','485','0','0');
INSERT INTO [t14299] VALUES (745,'٦٥٢ - حديث "إنهم كانوا عبادا يعبدوني".','485','744','0');
INSERT INTO [t14299] VALUES (746,'٦٥٣ - حديث "لا صام من صام الأبد".','486','744','0');
INSERT INTO [t14299] VALUES (747,'٦٥٤ - حديث "أمرت بيوم الأضحى عيدا جعله الله لهذه الأمة".','487','744','0');
INSERT INTO [t14299] VALUES (748,'٦٥٥ - حديث "حوضي مسيرة شهر ماؤه أبيض من اللبن".','487','744','0');
INSERT INTO [t14299] VALUES (749,'٦٥٦ - حديث "أتى رجل رسول الله صلى الله عليه وسلم فقال: أقرني، قال: اقرأ ثلاثا من ذات (ألر). الحديث".','489','744','0');
INSERT INTO [t14299] VALUES (750,'٦٥٧ - حديث: "قالوا: يا رسول الله ما نقدر على شيء لا نفقة ولا دابة ولا متاع".','489','744','0');
INSERT INTO [t14299] VALUES (751,'٦٥٨ - حديث "إني أعطيت أمي حديقة حياتها".','489','744','0');
INSERT INTO [t14299] VALUES (752,'٦٥٩ - حديث "تعلوهم نار الأنيار".','489','744','0');
INSERT INTO [t14299] VALUES (753,'٦٦٠ - حديث "وإن لزورك عليك حقا".','490','744','0');
INSERT INTO [t14299] VALUES (754,'٦٦١ - حديث "لما كسفت الشمس على عهد رسول الله صلى الله عليه وسلم نودي أن الصلاة جامعة".','492','744','0');
INSERT INTO [t14299] VALUES (755,'٦٦٢ - حديث "كفى إثما أن تحبس عمن تملك قوته".','492','744','0');
INSERT INTO [t14299] VALUES (756,'٦٦٣ - حديث "أحي والداك؟ قال: نعم، قال: ففيهما جاهد".','492','744','0');
INSERT INTO [t14299] VALUES (757,'٦٦٤ - حديث "لتأتين على أمتي كما أتى على بني إسرائيل حذو النعل بالنعل".','493','744','0');
INSERT INTO [t14299] VALUES (758,'٦٦٥ - حديث "أفضل ما قلته أنا والنبيون من قبلي: لا إله إلا الله وحده لا شريك له".','493','744','0');
INSERT INTO [t14299] VALUES (759,'٦٦٦ - حديث "من قتل دون ماله فهو شهيد".','494','744','0');
INSERT INTO [t14299] VALUES (760,'٦٦٧ - حديث "إن امرأتين أتتا رسول الله صلى الله عليه وسلم، وفي أيديهما سواران".','494','744','0');
INSERT INTO [t14299] VALUES (761,'٦٦٨ - حديث "خير الدعاء دعاء يوم عرفة".','495','744','0');
INSERT INTO [t14299] VALUES (762,'٦٦٩ - حديث "لا يتوارث أهل ملتين شتى".','495','744','0');
INSERT INTO [t14299] VALUES (763,'٦٧٠ - حديث "قال رجل يا رسول الله إن لي ذوي أرحام، أصل ويقطعون، وأغفر ويظلمون، وأحسن ويسيئون، أفأكافؤهم؟ قال لا، إذن تتركون جميعا".','495','744','0');
INSERT INTO [t14299] VALUES (764,'٦٧١ - حديث: "إن رجلا سأل رسول الله صلى الله عليه وسلم: أي الإسلام خير؟ قال: تطعم الطعام، وتقرأ السلام على من عرفت ومن لم تعرف".','495','744','0');
INSERT INTO [t14299] VALUES (765,'٦٧٢ - حديث "أن رجلا سأل رسول الله صلى الله عليه وسلم عن أفضل الأعمال، فقال: الصلاة، قال: ثم مه؟ قال الصلاة".','496','744','0');
INSERT INTO [t14299] VALUES (766,'٦٧٣ - حديث "ألا إن دية الخطأ شبه العمد ما كان بالسوط والعصا مائة من الإبل".','497','744','0');
INSERT INTO [t14299] VALUES (767,'٦٧٤ - حديث "من نظر إلى أخيه نظرة تخيفه، أخافه الله يوم القيامة".','497','744','0');
INSERT INTO [t14299] VALUES (768,'٦٧٥ - حديث "ما أبالي ما أتيت إن أنا شربت ترياقا ... الحديث".','497','744','0');
INSERT INTO [t14299] VALUES (769,'٦٧٦ - حديث "إنه لموصوف في التوراة ببعض صفته في القرآن، يا أيها النبي إنا أرسلناك شاهدا".','497','744','0');
INSERT INTO [t14299] VALUES (770,'٦٧٧ - حديث "أربع من كن فيه كان منافقا خالصا".','498','744','0');
INSERT INTO [t14299] VALUES (771,'٦٧٨ - حديث "إن الله لا يقبض العلم انتزاعا".','498','744','0');
INSERT INTO [t14299] VALUES (772,'٦٧٩ - حديث "مروا أبناءكم بالصلاة لسبع سنين واضربوهم عليها لعشر سنين".','499','744','0');
INSERT INTO [t14299] VALUES (773,'٦٨٠ - حديث "إن أطيب ما أكلتم من كسبكم فكلوه هنيئا".','499','744','0');
INSERT INTO [t14299] VALUES (774,'٦٨١ - حديث "إذا مات الرجل بغير مولده قيس له من مولده إلى منقطع أثره في الجنة".','501','744','0');
INSERT INTO [t14299] VALUES (775,'٦٨٢ - حديث "من ولي يتيما له ماله فليتجر فيه".','501','744','0');
INSERT INTO [t14299] VALUES (776,'٦٨٣ - حديث "الوسيلة".','501','744','0');
INSERT INTO [t14299] VALUES (777,'٦٨٤ - حديث "اللهم اشف عبدك ينكا لك عدوا".','502','744','0');
INSERT INTO [t14299] VALUES (778,'٦٨٥ - حديث "أنه صلى الله عليه وسلم تلا قول الله في إبراهيم: (رب إنهن أضللن كثيرا من الناس) الآية، وقال عيسى: (إن تعذبهم فإنهم عبادك) ".','502','744','0');
INSERT INTO [t14299] VALUES (779,'٦٨٦ - حديث "إن المقسطين عند الله على منابر من نور عن يمين الرحمن – وكلتا يديه يمين – الذين يعدلون في حكمهم وأهليهم وما ولوا".','502','744','0');
INSERT INTO [t14299] VALUES (780,'٦٨٧ - حديث "إن أول الآيات خروجا طلوع الشمس من مغربها، وخروج الدابة على','503','744','0');
INSERT INTO [t14299] VALUES (781,'٦٨٨ - حديث "بلغوا عني ولو آية وحدثوا عن بني إسرائيل ولا حرج".','504','744','0');
INSERT INTO [t14299] VALUES (782,'مسند عبد الله بن مالك ابن بحينة (رضي الله عنه)','505','0','0');
INSERT INTO [t14299] VALUES (783,'٦٨٩ - حديث عبد الله ابن بحينة: "الصبح أربعا".','506','782','0');
INSERT INTO [t14299] VALUES (784,'مسند عبد الله بن مسعود رضي الله عنه','506','0','0');
INSERT INTO [t14299] VALUES (785,'٦٩٠ - حديث "حدثنا رسول الله صلى الله عليه وسلم، وهو الصادق المصدوق: أن خلق أحدكم ... ".','507','784','0');
INSERT INTO [t14299] VALUES (786,'٦٩١ - حديث "إياكم وهاتان الكعبتان الموسومتان اللتان تزجران".','508','784','0');
INSERT INTO [t14299] VALUES (787,'٦٩٢ - حديث "إن اليهود قالوا: سلوه في الروح، فقال بعضهم: لا تسألوه – لا يجيء فيه بشيء تكرهونه".','509','784','0');
INSERT INTO [t14299] VALUES (788,'٦٩٣ - حديث "قلت: يا أبا عبد الرحمن أية ساعة زيارة هذه".','510','784','0');
INSERT INTO [t14299] VALUES (789,'٦٩٤ - حديث "فقالت أجملهن امرأة".','510','784','0');
INSERT INTO [t14299] VALUES (790,'٦٩٥ - حديث اللعان: "فقال: فإن أحدنا رأى مع امرأته رجلا".','510','784','0');
INSERT INTO [t14299] VALUES (791,'٦٩٦ - حديث "قضى رسول الله صلى الله عليه وسلم في دية الخطأ عشرين بنت مخاص، وعشرين بني مخاض ذكور".','511','784','0');
INSERT INTO [t14299] VALUES (792,'٦٩٧ - حديث "فلو كنت برميلة مصر لأريتكم قبورهما".','511','784','0');
INSERT INTO [t14299] VALUES (793,'٦٩٨ - حديث "ما من نبي بعثه الله في أمة إلا كان له من أمته حواريون ثم إنها تخلف من بعدههم خلوف .. الحديث".','511','784','0');
INSERT INTO [t14299] VALUES (794,'٦٩٩ - حديث "حي على الطهور المبارك والبركة من الله تعالى".','512','784','0');
INSERT INTO [t14299] VALUES (795,'٧٠٠ - حديث "إن من شرار الناس من تدركه الساعة وهم أحياء".','512','784','0');
INSERT INTO [t14299] VALUES (796,'٧٠١ - حديث "ما من عبد يؤدي زكاة ماله إلا جعل له شجاع أقرع".','512','784','0');
INSERT INTO [t14299] VALUES (797,'٧٠٢ - حديث "سألت النبي صلى الله عليه وسلم: أي العمل أحب لله؟ قال: الصلاة على وقتها. قلت: ثم أي؟ قال: بر الوالدين. قلت: ثم أي؟ قال: الجهاد في سبيل الله. وفي لفظ، قال: الصلاة لوقتها".','513','784','0');
INSERT INTO [t14299] VALUES (798,'٧٠٣ - حديث "والذي لا إله غيره هذا مقام إبراهيم الذي أنزلت عليه سورة البقرة".','514','784','0');
INSERT INTO [t14299] VALUES (799,'٧٠٤ - حديث "أقرانيها النبي صلى الله عليه وسلم فاه إلى في".','515','784','0');
INSERT INTO [t14299] VALUES (800,'٧٠٥ - حديث "إذا كنتم ثلاثة فلا يتناجى رجلان دون الآخر، أجل أن يحزنه".','516','784','0');
INSERT INTO [t14299] VALUES (801,'٧٠٦ - حديث "أي الذنب أكبر؟ قال: أن تدعو لله ندا وهو خلقك".','516','784','0');
INSERT INTO [t14299] VALUES (802,'٧٠٧ - حديث "أتسخر بي وأنت الملك؟ ".','517','784','0');
INSERT INTO [t14299] VALUES (803,'٧٠٨ - حديث "إن في الصلاة شغلا".','517','784','0');
INSERT INTO [t14299] VALUES (804,'٧٠٩ - حديث "اجتمع عند البيت ثقفيان وقرشي، كثيرة شحم بطونهم، قليلة فقه قلوبهم".','517','784','0');
INSERT INTO [t14299] VALUES (805,'٧١٠ - حديث "ما منكم من أحد إلا وقد وكل به قرينه من الجن، قالوا: وإياك يا رسول الله. قال: وإياي".','517','784','0');
INSERT INTO [t14299] VALUES (806,'٧١١ - حديث "يا معشر الشباب من استطاع منكم الباءة فليتزوج، فإنه أغض للبصر، وأحصن للفرج. ومن لم يستطع فعليه بالصوم".','518','784','0');
INSERT INTO [t14299] VALUES (807,'٧١٢ - حديث "نضر الله امرءا سمع حديثا فبلغه كما سمعه".','521','784','0');
INSERT INTO [t14299] VALUES (808,'٧١٣ - حديث "أنزل القرآن على سبعة أحرف".','522','784','0');
INSERT INTO [t14299] VALUES (809,'٧١٤ - حديث "ليلني منكم أولو الأحلام والنهى".','522','784','0');
INSERT INTO [t14299] VALUES (810,'٧١٥ - حديث "أول ما يقضى بين الناس يوم القيامة في الدماء".','523','784','0');
INSERT INTO [t14299] VALUES (811,'٧١٦ - حديث: "أمسينا وأمسى الملك لله والحمد لله".','524','784','0');
INSERT INTO [t14299] VALUES (812,'٧١٧ - حديث "أنه صلى الله عليه وسلم لما رأى (من الناس) إدبارا قال: اللهم سبعا كسبع يوسف".','525','784','0');
INSERT INTO [t14299] VALUES (813,'٧١٨ - حديث "وما أحد أحب إليه المدح من الله".','526','784','0');
INSERT INTO [t14299] VALUES (814,'٧١٩ - حديث: "كانت لي بئر في أرض ابن عم لي فقال لي: شهودك".','526','784','0');
INSERT INTO [t14299] VALUES (815,'٧٢٠ - حديث: "ولابنة الابن السدس تكملة الثلثين".','527','784','0');
INSERT INTO [t14299] VALUES (816,'٧٢١ - حديث: "لا يمنعن أحدكم أذان بلال من سحوره، فإنه يؤذن بليل ليرجع قائمكم، ولينبه نائمكم".','527','784','0');
INSERT INTO [t14299] VALUES (817,'٧٢٢ - حديث "لا يكتسب عبد مالا حراما فيتصدق به فيقبل منه، ولا ينفق منه فيبارك له فيه".','528','784','0');
INSERT INTO [t14299] VALUES (818,'٧٢٣ - حديث: "جاء رجل إلى ابن مسعود فقال: قرأت المفصل الليلة في ركعة. فقال: هذا كهذ الشعر".','528','784','0');
INSERT INTO [t14299] VALUES (819,'٧٢٤ - حديث: "لا يجعل أحدكم للشيطان شيئا من صلاته يرى أن حقا عليه أن لا ينصرف إلا عن يمينه".','529','784','0');
INSERT INTO [t14299] VALUES (820,'٧٢٥ - حديث: "وغلظ القلوب في الفدادين عند أصول أذناب الإبل حيث يطلع قرنا الشيطان في ربيعة ومضر".','529','784','0');
INSERT INTO [t14299] VALUES (821,'٧٢٦ - حديث "سئل النبي صلى الله عليه وسلم عن الوسوسة".','529','784','0');
INSERT INTO [t14299] VALUES (822,'٧٢٧ - حديث: "مالي واللدنيا".','530','784','0');
INSERT INTO [t14299] VALUES (823,'٧٢٨ - حديث: "آخر من يدخل الجنة رجل فهو يمشي مرة ويكبو أخرى".','530','784','0');
INSERT INTO [t14299] VALUES (824,'٧٢٩ - حديث: "فقال سددناها عنك يا أمية فوالله لقد سمعت رسول الله صلى الله عليه وسلم يقول','530','784','0');
INSERT INTO [t14299] VALUES (825,'٧٣٠ - حديث: "بئسما لأحدكم أن يقول: نسيت آية كيت وكيت، بل نسي واستذكر القرآن فإنه أشد تفصيا على صدور الرجال من النعم من عقلها".','531','784','0');
INSERT INTO [t14299] VALUES (826,'٧٣١ - حديث "لا يحل دم امرئ مسلم شهد أن لا إله إلا الله".','532','784','0');
INSERT INTO [t14299] VALUES (827,'٧٣٢ - حديث: "ما من حاكم يحكم بين المسلمين – إلى قوله – في مهواة أربعين خريفا".','532','784','0');
INSERT INTO [t14299] VALUES (828,'٧٣٣ - حديث: "المؤمن مالف".','532','784','0');
INSERT INTO [t14299] VALUES (829,'٧٣٤ - حديث: "الجنازة متبوعة لا تتبع".','533','784','0');
INSERT INTO [t14299] VALUES (830,'مسند عبد الرحمن بن أبي بكر الصديق رضي الله عنهما','533','0','0');
INSERT INTO [t14299] VALUES (831,'٧٣٥ - حديث: "من كان عنده طعام اثنين فليذهب بثالث وإن أربع فخامس أو سادس".','533','830','0');
INSERT INTO [t14299] VALUES (832,'٧٣٦ - حديث: "جاء رجل مشعان بغنم يسوقها، قال له النبي صلى الله عليه وسلم: بيعا أم عطية".','535','830','0');
INSERT INTO [t14299] VALUES (833,'مسند عبد الرحمن بن أبي عميرة رضي الله عنه.','535','0','0');
INSERT INTO [t14299] VALUES (834,'٧٣٧ - حديث: "ما من نفس مسلمة يقبضها ربها تحب أن ترجع إليكم وأن لها الدنيا وما فيها غير الشهيد".','535','833','0');
INSERT INTO [t14299] VALUES (835,'مسند عبد الرحمن بن عوف رضي الله عنه','536','0','0');
INSERT INTO [t14299] VALUES (836,'٧٣٨ - حديث "ثلاثة تحت العرش يوم القيامة: القرآن يحاج العباد له ظهر وبطن".','536','835','0');
INSERT INTO [t14299] VALUES (837,'مسند عبد الرحمن بن سمرة رضي الله عنه','536','0','0');
INSERT INTO [t14299] VALUES (838,'٧٣٩ - حديث: "وإذا حلفت على يمين فرأيت غيرها خيرا منها".','537','837','0');
INSERT INTO [t14299] VALUES (839,'مسند عبد الرحمن بن يعمر رضي الله عنه','537','0','0');
INSERT INTO [t14299] VALUES (840,'٧٤٠ - حديث: "الحج عرفة".','537','839','0');
INSERT INTO [t14299] VALUES (841,'مسند عبد الله بن مغفل رضي الله عنه','538','0','0');
INSERT INTO [t14299] VALUES (842,'٧٤١ - حديث: "وأيما قوم اتخذوا كلبا ليس بكلب حرث أو صيد أو ماشية نقصوا من أجورهم كل يوم قيراط".','538','841','0');
INSERT INTO [t14299] VALUES (843,'٧٤٢ - حديث: "من أحبهم فبحبي أحبهم".','538','841','0');
INSERT INTO [t14299] VALUES (844,'مسند عبد المطلب بن ربيعة رضي الله عنه','539','0','0');
INSERT INTO [t14299] VALUES (845,'٧٤٣ - حديث: "إن هذه الصدقة إنما هي أوساخ الناس".','539','844','0');
INSERT INTO [t14299] VALUES (846,'مسند عتبة بن عبد السلمي رضي الله عنه','540','0','0');
INSERT INTO [t14299] VALUES (847,'٧٤٤ - حديث: "ما من عبد يخرج من بيته إلى غدو أو رواح إلى المسجد إلا كانت خطاه: خطوة كفارة وخطوة درجة".','540','846','0');
INSERT INTO [t14299] VALUES (848,'مسند عثمان بن أبي العاص الثقفي رضي الله عنه','541','0','0');
INSERT INTO [t14299] VALUES (849,'٧٤٥ - حديث: "هل من سائل فأعطيه، هل من داع فأستجيب له".','541','848','0');
INSERT INTO [t14299] VALUES (850,'٧٤٦ - حديث: "أنت إمامهم واقتد بأضعفهم".','541','848','0');
INSERT INTO [t14299] VALUES (851,'مسند عثمان بن حنيف رضي الله عنه','541','0','0');
INSERT INTO [t14299] VALUES (852,'٧٤٧ - حديث: "اللهم إني أسألك وأتوجه إليك بنبيك محمد نبي الرحمة".','542','851','0');
INSERT INTO [t14299] VALUES (853,'مسند عثمان بن عفان رضي الله عنه','543','0','0');
INSERT INTO [t14299] VALUES (854,'٧٤٨ - حديث: "من أسر سرية ألبسه الله رداء منها. إن خيرا فخير، وإن شرا فشر".','543','853','0');
INSERT INTO [t14299] VALUES (855,'٧٤٩ - حديث: "ما من مسلم تحضره صلاة مكتوبة فيحسن وضوءها وخشوعها، إلا كانت له كفارة لما قبلها من الذنوب، ما لم يأت كبيرة، وذلك الدهر كله".','544','853','0');
INSERT INTO [t14299] VALUES (856,'٧٥٠ - حديث: "ما رأيت منظرا قط إلا والقبر أفظع منه".','545','853','0');
INSERT INTO [t14299] VALUES (857,'٧٥١ - حديث: "لا يتوضأ رجل فيحسن وضوءه ويصلي الصلاة، إلا غفر له ما بينه وبين الصلاة حتى يصليها".','545','853','0');
INSERT INTO [t14299] VALUES (858,'مسند عرفجة الأشجعي رضي الله عنه','545','0','0');
INSERT INTO [t14299] VALUES (859,'٧٥٢ - حديث: "من أراد أن يفرق بين المسلمين وهم جميع، فاضربوه بالسيف كائنا من كان".','546','858','0');
INSERT INTO [t14299] VALUES (860,'مسند العداء بن خالد رضي الله عنه','546','0','0');
INSERT INTO [t14299] VALUES (861,'٧٥٣ - حديث: "كتب لي النبي صلى الله عليه وسلم: هذا ما اشتراه العداء بن خالد من محمد رسول الله صلى الله عليه وسلم -بيع المسلم المسلم".','547','860','0');
INSERT INTO [t14299] VALUES (862,'مسند عدي بن عميرة رضي الله عنه','547','0','0');
INSERT INTO [t14299] VALUES (863,'٧٥٤ - حديث: "من استعملناه منكم على عمل فكتمنا مخيطا فما فوقه صار غلولا".','547','862','0');
INSERT INTO [t14299] VALUES (864,'٧٥٥ - حديث "أيحسب أحدكم متكئا على أريكته يظن أن الله لم يحرم شيئا إلا ما في القرآن".','548','862','0');
INSERT INTO [t14299] VALUES (865,'٧٥٦ - حديث: "إني عبد الله مكتوب لخاتم النبيين وإن آدم لمنجدل في طينته".','549','862','0');
INSERT INTO [t14299] VALUES (866,'٧٥٧ - حديث: "لا يبلغ العبد أن يكون من المتقين".','550','862','0');
INSERT INTO [t14299] VALUES (867,'مسند عقبة بن الحارث النوفلي','550','0','0');
INSERT INTO [t14299] VALUES (868,'٧٥٨ - حديث: "كيف وقد قيل؟ ".','550','867','0');
INSERT INTO [t14299] VALUES (869,'مسند عقبة بن عامر رضي الله عنه','551','0','0');
INSERT INTO [t14299] VALUES (870,'٧٥٩ - حديث: "ليس من اللهو إلا ثلاث: تأديب الرجل فرسه وملاعبته أهله، ورميه بقوسه ونبله".','551','869','0');
INSERT INTO [t14299] VALUES (871,'٧٦٠ - حديث: "ما من مسلم يتوضأ فيحسن وضوءه، ثم يقوم فيصلي ركعتين مقبل عليهما بقلبه ووجهه إلا وجبت له الجنة".','551','869','0');
INSERT INTO [t14299] VALUES (872,'٧٦١ - حديث: "لهو أشد تفلتا من المخاض في العقل".','552','869','0');
INSERT INTO [t14299] VALUES (873,'٧٦٢ - حديث: "ما جاء بكم؟ قالوا: صحبتك رسول الله، أحببنا أن نسير معك".','553','869','0');
INSERT INTO [t14299] VALUES (874,'٧٦٣ - حديث "يوم عرفة، ويوم النحر وأيام التشريق عيدنا أهل الإسلام".','553','869','0');
INSERT INTO [t14299] VALUES (875,'٧٦٤ - حديث: "من توضأ فأحسن الوضوء ثم صلى (غير) ساه ولا لاه غفر له".','553','869','0');
INSERT INTO [t14299] VALUES (876,'٧٦٥ - حديث: "ألا أعلمك خير سورتين".','554','869','0');
INSERT INTO [t14299] VALUES (877,'٧٦٦ - حديث: "أحق الشروط أن توفوا به ما استحللتم به الفروج".','554','869','0');
INSERT INTO [t14299] VALUES (878,'٧٦٧ - حديث: "إياكم والدخول على النساء".','554','869','0');
INSERT INTO [t14299] VALUES (879,'٧٦٨ - حديث: "أنسابكم هذه ليست بمسبة على أحد، كلكم بنو آدم طف الصاع بالصاع".','554','869','0');
INSERT INTO [t14299] VALUES (880,'مسند علي بن أبي طالب رضي الله عنه','555','0','0');
INSERT INTO [t14299] VALUES (881,'٧٦٩ - حديث: "فجعل يعنق على بعيره والناس يضربون الإبل يمينا وشمالا".','555','880','0');
INSERT INTO [t14299] VALUES (882,'٧٧٠ - حديث: "اشف أنت الشافي لا شفاء إلا شفاؤك شفاء لا يغادر سقما".','556','880','0');
INSERT INTO [t14299] VALUES (883,'٧٧١ - حديث: "إن هذه أيام أكل وشرب فلا يصومها أحد".','556','880','0');
INSERT INTO [t14299] VALUES (884,'٧٧٢ - حديث: "أن تكبرا الله أربعا وثلاثين".','557','880','0');
INSERT INTO [t14299] VALUES (885,'٧٧٣ - حديث: "لا يحل للخليفة من مال الله إلا قصعتان، قصعة يأكلها ... ".','557','880','0');
INSERT INTO [t14299] VALUES (886,'٧٧٤ - حديث: "مات رجل من أهل الصفة وترك دينارين، فقال رسول الله صلى الله عليه وسلم: كيتان".','557','880','0');
INSERT INTO [t14299] VALUES (887,'٧٧٥ - حديث: "إني وإياك وهذان وهذا الراقد في مكان واحد يوم القيامة".','558','880','0');
INSERT INTO [t14299] VALUES (888,'٧٧٦ - حديث: "رفع القلم عن ثلاث: عن الصبي حتى يبلغ، وعن النائم حتى يستيقظ وعن المجنون حتى يفيق".','559','880','0');
INSERT INTO [t14299] VALUES (889,'٧٧٧ - حديث: "ما عندنا من كتاب نقرأه إلا كتاب الله غير هذه الصحيفة".','561','880','0');
INSERT INTO [t14299] VALUES (890,'٧٧٨ - حديث: "أوشك أن تستحل أمتي فروج النساء والحرير".','562','880','0');
INSERT INTO [t14299] VALUES (891,'٧٧٩ - حديث: "من حسن إسلام المرء تركه ما لا يعنيه".','563','880','0');
INSERT INTO [t14299] VALUES (892,'٧٨٠ - حديث: "إن في الجنة لسوقا ما فيها شراء ولا بيع إلا الصور".','563','880','0');
INSERT INTO [t14299] VALUES (893,'٧٨١ - حديث: "نعم الرجل الفقيه في الدين إن احتيج إليه نفع، وإن استغني عنه أغنى نفسه".','563','880','0');
INSERT INTO [t14299] VALUES (894,'٧٨٢ - حديث: "إنما مثل أمتي الغيث لا يدرى آخره خير أم أوله، أو كحديقة أطعم منها فوج عاما، ثم (أطعم) منها فوج عاما لعل آخرها فوجا أن يكون أحسنها".','564','880','0');
INSERT INTO [t14299] VALUES (895,'٧٨٣ - حديث: "ارجعن مأزورات غير مأجورات".','564','880','0');
INSERT INTO [t14299] VALUES (896,'٧٨٤ - حديث: "دية شبه العمد أثلاثا ثلاث وثلاثون".','565','880','0');
INSERT INTO [t14299] VALUES (897,'٧٨٥ - حديث الزكاة قوله: "وليس في تسعين ومائة شيء حتى تتم مائتي درهم".','565','880','0');
INSERT INTO [t14299] VALUES (898,'٧٨٦ - حديث: "ألا أعلمك كلمات لو كان عليك مثل جبل أحد دينا".','566','880','0');
INSERT INTO [t14299] VALUES (899,'٧٨٧ - حديث: "من ملك زادا وراحلة تبلغه إلى بيت الله ولم يحج".','566','880','0');
INSERT INTO [t14299] VALUES (900,'٧٨٨ - حديث: "سمعته أذناي، ووعاه قلبي من رسول الله صلى الله عليه وسلم: الناس تبع لقريش".','567','880','0');
INSERT INTO [t14299] VALUES (901,'٧٨٩ - حديث: "ما منكم من أحد، ما من نفس منفوسة إلا كتب مكانها من الجنة والنار، إلا وقد كتبت شقية أو سعيدة".','567','880','0');
INSERT INTO [t14299] VALUES (902,'٧٩٠ - حديث: "خير نسائها مريم، وخير نسائها خديجة".','568','880','0');
INSERT INTO [t14299] VALUES (903,'٧٩١ - حديث: "من فرق بين والدة وولدها فرق الله بينه وبين أحبته يوم القيامة".','569','880','0');
INSERT INTO [t14299] VALUES (904,'٧٩٢ - حديث: "إنه لعهد النبي الأمي إلي: أن لا يحبني إلا مؤمن ولا يبغضني إلا منافق".','569','880','0');
INSERT INTO [t14299] VALUES (905,'٧٩٣ - حديث: "لم يكن يحجزه عن القرآن شيء ليس الجنابة".','570','880','0');
INSERT INTO [t14299] VALUES (906,'٧٩٤ - حديث: "وضع عمر على سريره، فتكنفه الناس، فلم يرعني إلا رجل قد أخذ بمنكبي فقال: وأيم الله إن كنت لأظن ليجعلك الله مع صاحبيك، وذلك أني كنت أكثر أن أسمع رسول الله صلى الله عليه وسلم يقول: ذهبت أنا وأبو بكر وعمر، ودخلت أنا وأبو بكر وعمر، وخرجت أنا وأبو بكر وعمر، وفعلت أنا وأبو بكر وعمر، وانطلقت أنا وأبو بكر وعمر، [فإن كنت ليجعلنك الله معهما] ".','571','880','0');
INSERT INTO [t14299] VALUES (907,'٧٩٥ - حديث: "للمسلم على المسلم ست خصال بالمعروف".','571','880','0');
INSERT INTO [t14299] VALUES (908,'٧٩٦ - حديث: "ستر ما بين أعين الناس وعورات بني آدم إذا دخل أحدهم الخلاء يقول: باسم الله".','572','880','0');
INSERT INTO [t14299] VALUES (909,'٧٩٧ - حديث: "من ترك موضع شعرة من جنابة لم يصبها ماء فعل الله به كذا وكذا من النار".','572','880','0');
INSERT INTO [t14299] VALUES (910,'٧٩٨ - حديث: "لا يؤمن عبد حتى يؤمن بأربع".','572','880','0');
INSERT INTO [t14299] VALUES (911,'٧٩٩ - حديث "دعاء الافتتاح".','573','880','0');
INSERT INTO [t14299] VALUES (912,'٨٠٠ - حديث: "البخيل من ذكرت عنده فلم يصل علي".','573','880','0');
INSERT INTO [t14299] VALUES (913,'٨٠١ - حديث: "إن الله وتر يحب الوتر، فأوتروا يا أهل القرآن".','574','880','0');
INSERT INTO [t14299] VALUES (914,'٨٠٢ - حديث: "أما ناقتك فانحرها، وأما كيت وكيت، فمن الشيطان".','574','880','0');
INSERT INTO [t14299] VALUES (915,'٨٠٣ - حديث: "إن هذين حرام على ذكور أمتي".','575','880','0');
INSERT INTO [t14299] VALUES (916,'٨٠٤ - حديث الظعينة: "لتخرجن الكتاب، أول لتلقين الثياب".','575','880','0');
INSERT INTO [t14299] VALUES (917,'مسند عمار بن ياسر رضي الله عنه','576','0','0');
INSERT INTO [t14299] VALUES (918,'٨٠٥ - حديث: "ألا أحدثكم بأشقى الناس رجلين".','576','917','0');
INSERT INTO [t14299] VALUES (919,'٨٠٦ - حديث: "أصابتني جنابة ولا ماء".','576','917','0');
INSERT INTO [t14299] VALUES (920,'٨٠٧ - حديث: "إن طول صلاة الرجل، وقصر خطبته مئنة من فقهه".','577','917','0');
INSERT INTO [t14299] VALUES (921,'٨٠٨ - حديث: "تمعكت فأتيت النبي صلى الله عليه وسلم فقال: يكفيك الوجه والكفين".','577','917','0');
INSERT INTO [t14299] VALUES (922,'٨٠٩ - حديث: "اللهم بعلمك الغيب وقدرتك على الخلق أحيني ما علمت الحياة خيرا لي".','579','917','0');
INSERT INTO [t14299] VALUES (923,'مسند عمر بن الخطاب رضي الله عنه','579','0','0');
INSERT INTO [t14299] VALUES (924,'٨١٠ - حديث: "أيرقد أحدنا وهو جنب؟ قال: نعم".','579','923','0');
INSERT INTO [t14299] VALUES (925,'٨١١ - حديث: "من نام عن حزبه فقرأه فيما بين صلاة الفجر وصلاة الظهر، كتب له كأنما قرأه من الليل".','580','923','0');
INSERT INTO [t14299] VALUES (926,'٨١٢ - حديث: "إن أخوف ما أخاف على أمتي كل منافق عليم اللسان".','580','923','0');
INSERT INTO [t14299] VALUES (927,'٨١٣ - حديث: "قال: إني صائم، قال: وأي الصيام تصوم؟ قال: أول الشهر','580','923','0');
INSERT INTO [t14299] VALUES (928,'٨١٤ - حديث: "فإذا أنا برباح غلام رسول الله صلى الله عليه وسلم قاعدا على أسكفة المشربة".','581','923','0');
INSERT INTO [t14299] VALUES (929,'٨١٥ - حديث حمار: "لا تلعنوه فوالله ما علمت إنه يحب الله ورسوله".','582','923','0');
INSERT INTO [t14299] VALUES (930,'٨١٦ - حديث: "من كان ملتمسا ليلة القدر فليلتمسها في العشر الأواخر وترا".','583','923','0');
INSERT INTO [t14299] VALUES (931,'٨١٧ - حديث "الذهب بالورق ربا إلا ها وها".','583','923','0');
INSERT INTO [t14299] VALUES (932,'٨١٨ - حديث عمر: "ليس بي إياكم أيها الرهط".','585','923','0');
INSERT INTO [t14299] VALUES (933,'٨١٩ - حديث: "النية".','585','923','0');
INSERT INTO [t14299] VALUES (934,'٨٢٠ - حديث: "اللهم زدنا ولا تنقصنا وأكرمنا ولا تهنا، وأعطنا ولا تحرمنا".','593','923','0');
INSERT INTO [t14299] VALUES (935,'٨٢١ - حديث: "اللهم إني أسألك من صالح ما تؤتي الناس من المال والأهل والولد غير الضال ولا المضل".','594','923','0');
INSERT INTO [t14299] VALUES (936,'٨٢٢ - حديث: "تمعددوا واخشوشنوا".','594','923','0');
INSERT INTO [t14299] VALUES (937,'٨٢٣ - حديث: "إذا وسع الله عليكم فأوسعوا، صلى رجل في رداء وإزار وقميص في إزار وقباء".','595','923','0');
INSERT INTO [t14299] VALUES (938,'٨٢٤ - حديث: "لو غيرك قالها يا أبا عبيدة".','595','923','0');
INSERT INTO [t14299] VALUES (939,'٨٢٥ - حديث: "إني رأيته في النار في بردة غلها".','596','923','0');
INSERT INTO [t14299] VALUES (940,'٨٢٦ - حديث: "لوددت أن كل شيء عملناه بعده نجونا منه كفافا".','596','923','0');
INSERT INTO [t14299] VALUES (941,'٨٢٧ - حديث: "من يبكى عليه يعذب".','596','923','0');
INSERT INTO [t14299] VALUES (942,'٨٢٨ - حديث: "من ينح عليه يعذب بما ينح عليه".','597','923','0');
INSERT INTO [t14299] VALUES (943,'٨٢٩ - حديث: "ما منكم من أحد يتوضأ".','597','923','0');
INSERT INTO [t14299] VALUES (944,'٨٣٠ - حديث: "واعجبا لك يا ابن عباس".','597','923','0');
INSERT INTO [t14299] VALUES (945,'٨٣١ - حديث: "إن عمر بن الخطاب بينا هو يخطب إذ جاءه رجل فناداه عمر أية ساعة هذه".','599','923','0');
INSERT INTO [t14299] VALUES (946,'٨٣٢ - حديث: "لم يصبه البلاء كائنا ما كان".','602','923','0');
INSERT INTO [t14299] VALUES (947,'٨٣٣ - حديث: "كيف بك إذا خرجت".','603','923','0');
INSERT INTO [t14299] VALUES (948,'٨٣٤ - حديث: "أن عمر قال: مالنا وكرمك".','603','923','0');
INSERT INTO [t14299] VALUES (949,'٨٣٥ - حديث: "صل في هذا الوادي المبارك وقل: عمرة في حجة".','604','923','0');
INSERT INTO [t14299] VALUES (950,'٨٣٦ - حديث: "ما هو إلا أن رأيت الله شرح صدر أبي بكر للقتال".','605','923','0');
INSERT INTO [t14299] VALUES (951,'٨٣٧ - حديث: "مر بجنازة فأثني عليها خيرا".','605','923','0');
INSERT INTO [t14299] VALUES (952,'٨٣٨ - حديث خصومة علي والعباس، وقوله: "فانطلقت معه حتى أدخل على عمر".','606','923','0');
INSERT INTO [t14299] VALUES (953,'٨٣٩ - حديث: "حضرت أبي حين أصيب فأثنوا عليه وقالوا: جزاك الله خيرا فقال: راغب وراهب".','607','923','0');
INSERT INTO [t14299] VALUES (954,'٨٤٠ - حديث: "إنه لم يهلك أهل الكتاب (إلا أنه لم يكن بين صلاتهم فصل) ".','607','923','0');
INSERT INTO [t14299] VALUES (955,'٨٤١ - حديث: "أنه استأذن النبي صلى الله عليه وسلم في العمرة فأذن له وقال: يا أخي لا تنسنا من','607','923','0');
INSERT INTO [t14299] VALUES (956,'٨٤٢ - حديث: "لا تشتروا وإن بدرهم".','608','923','0');
INSERT INTO [t14299] VALUES (957,'٨٤٣ - حديث: "الأذان".','608','923','0');
INSERT INTO [t14299] VALUES (958,'مسند عمران بن حصين رضي الله عنه','610','0','0');
INSERT INTO [t14299] VALUES (959,'٨٤٤ - حديث: "فقال بشر بن كعب: مكتوب في الحكمة: إن منه وقارا".','611','958','0');
INSERT INTO [t14299] VALUES (960,'٨٤٥ - حديث: "إن فلانا لا يفطر نهارا الدهر".','611','958','0');
INSERT INTO [t14299] VALUES (961,'٨٤٦ - حديث: "إن رجلا أعتق ستة مملوكين له عند موته لم يكن له مال غيرهم، فدعاهم رسول الله صلى الله عليه وسلم فجزأهم ثلاثا".','611','958','0');
INSERT INTO [t14299] VALUES (962,'٨٤٧ - حديث: "أصابتني جنابة ولا ماء".','612','958','0');
INSERT INTO [t14299] VALUES (963,'٨٤٨ - حديث: "كان الله ولم يكن شيء قبله".','612','958','0');
INSERT INTO [t14299] VALUES (964,'٨٤٩ - حديث (المرأة والمزادتين) – قوله: "وقعنا تلك الوقعة".','613','958','0');
INSERT INTO [t14299] VALUES (965,'مسند عمرو بن العاص رضي الله عنه','616','0','0');
INSERT INTO [t14299] VALUES (966,'٨٥٠ - حديث: "ألا كان مفزعكم إلى الله ورسوله".','616','965','0');
INSERT INTO [t14299] VALUES (967,'٨٥١ - حديث: "إن هذا القرآن أنزل على سبعة أحرف، فأي ذلك قرأتم فقد أصبتم".','616','965','0');
INSERT INTO [t14299] VALUES (968,'٨٥٢ - حديث: "إن أفضل ما نعد شهادة أن لا إله إلا الله".','616','965','0');
INSERT INTO [t14299] VALUES (969,'٨٥٣ - حديث: "فوالله ما أدري أحبا ذلك أم تألفا".','618','965','0');
INSERT INTO [t14299] VALUES (970,'٨٥٤ - حديث: "نعما بالمال الصالح للمرء الصالح".','618','965','0');
INSERT INTO [t14299] VALUES (971,'٨٥٥ - حديث: "خرج علينا رسول الله صلى الله عليه وسلم وفي يده كتابان فقال: أتدرون ما هذان الكتابان؟ قلنا: لا يا رسول الله إلا أن تخبرنا".','620','965','0');
INSERT INTO [t14299] VALUES (972,'مسند عمرو بن يثربي رضي الله عنه','621','0','0');
INSERT INTO [t14299] VALUES (973,'٨٥٦ - حديث: "إن لقيتها نعجة تحمل شفرة وزنادا بخبت الجميش فلا تمسها".','621','972','0');
INSERT INTO [t14299] VALUES (974,'مسند عمرو بن عبسة السلمي رضي الله عنه','622','0','0');
INSERT INTO [t14299] VALUES (975,'٨٥٧ - حديث: "من قاتل في سبيل الله فواق ناقة".','622','974','0');
INSERT INTO [t14299] VALUES (976,'٨٥٨ - حديث أوقات الكراهة، قوله: "ثم أقصر عن الصلاة، فإن حينئذ تسجر جهنم".','623','974','0');
INSERT INTO [t14299] VALUES (977,'٨٥٩ - حديث: "أقرب ما يكون الرب من العبد في جوف الليل الآخر".','624','974','0');
INSERT INTO [t14299] VALUES (978,'مسند عمرو بن عوف رضي الله عنه','624','0','0');
INSERT INTO [t14299] VALUES (979,'٨٦٠ - حديث: "فوالله ما الفقر أخشى عليكم".','625','978','0');
INSERT INTO [t14299] VALUES (980,'٨٦١ - حديث: "ليس لعرق ظالم حق".','625','978','0');
INSERT INTO [t14299] VALUES (981,'٨٦٢ - حديث: "إن الدين ليأرز إلى الحجاز كما تأرز الحية إلى جحرها، وليعقلن الدين من الحجاز معقل الأروية من رأس الجبل".','625','978','0');
INSERT INTO [t14299] VALUES (982,'مسند عوف بن مالك رضي الله عنه','626','0','0');
INSERT INTO [t14299] VALUES (983,'٨٦٣ - حديث: "لا يقص إلا أمير أو مأمور أو مختال أو مرائي".','626','982','0');
INSERT INTO [t14299] VALUES (984,'مسند عياض بن حمار رضي الله عنه','626','0','0');
INSERT INTO [t14299] VALUES (985,'٨٦٤ - حديث: "الخطبة الطويلة".','626','984','0');
INSERT INTO [t14299] VALUES (986,'مسند فضالة الليثي رضي الله عنه','627','0','0');
INSERT INTO [t14299] VALUES (987,'٨٦٥ - حديث: "حافظ على العصرين – ولم تكن من لغتنا – قلت: وما العصران؟ قال: صلاة قبل طلوع الشمس وصلاة قبل غروبها".','628','986','0');
INSERT INTO [t14299] VALUES (988,'مسند الفضل بن العباس رضي الله عنه','628','0','0');
INSERT INTO [t14299] VALUES (989,'٨٦٦ - حديث: "الصلاة مثنى مثنى، تشهد في كل ركعتين وتضرع وتخشع وتمسكن".','628','988','0');
INSERT INTO [t14299] VALUES (990,'مسند قبيصة بن المخارق رضي الله عنه','629','0','0');
INSERT INTO [t14299] VALUES (991,'٨٦٧ - حديث: "يأكله صاحبه سحتا".','630','990','0');
INSERT INTO [t14299] VALUES (992,'مسند قتادة بن ملحان رضي الله عنه','630','0','0');
INSERT INTO [t14299] VALUES (993,'٨٦٨ - حديث: "كان يأمر بصيام أيام البيض".','630','992','0');
INSERT INTO [t14299] VALUES (994,'مسند قدامة بن عبد الله رضي الله عنه','631','0','0');
INSERT INTO [t14299] VALUES (995,'٨٦٩ - حديث: "لا ضرب ولا طرد ولا إليك إليك".','631','994','0');
INSERT INTO [t14299] VALUES (996,'مسند قرة بن إياس المزني رضي الله عنه','631','0','0');
INSERT INTO [t14299] VALUES (997,'٨٧٠ - حديث: "إن كنتم لا بد آكليهما".','631','996','0');
INSERT INTO [t14299] VALUES (998,'مسند قيس بن عمرو الأنصاري رضي الله عنه','632','0','0');
INSERT INTO [t14299] VALUES (999,'٨٧١ - حديث: "رأى رسول الله صلى الله عليه وسلم رجلا يصلي بعد صلاة الصبح ركعتين، فقال: أصلاة الصبح مرتين".','632','998','0');
INSERT INTO [t14299] VALUES (1000,'مسند كعب بن مالك رضي الله عنه','632','0','0');
INSERT INTO [t14299] VALUES (1001,'٨٧٢ - حديث تخلفه عن غزوة تبوك.','632','1000','0');
INSERT INTO [t14299] VALUES (1002,'٨٧٣ - حديث: "بل الدم الدم والهدم الهدم".','633','1000','0');
INSERT INTO [t14299] VALUES (1003,'٨٧٤ - حديث: "قم فاقضه".','634','1000','0');
INSERT INTO [t14299] VALUES (1004,'مسند كعب بن عاصم الأشعري رضي الله عنه','635','0','0');
INSERT INTO [t14299] VALUES (1005,'٨٧٥ - حديث: "ليس من امبر امصيام في السفر".','635','1004','0');
INSERT INTO [t14299] VALUES (1006,'مسند كعب بن عجرة رضي الله عنه','635','0','0');
INSERT INTO [t14299] VALUES (1007,'٨٧٦ - حديث: "أطعم ثلاثة أصوع".','635','1006','0');
INSERT INTO [t14299] VALUES (1008,'٨٧٧ - حديث: "معقبات لا يخيب قائلهن دبر كل صلاة مكتوبة ثلاث وثلاثون تسبيحة".','636','1006','0');
INSERT INTO [t14299] VALUES (1009,'مسند لقيط بن عامر رضي الله عنه','637','0','0');
INSERT INTO [t14299] VALUES (1010,'٨٧٨ - حديث لقوله: "لعمر إلهك".','637','1009','0');
INSERT INTO [t14299] VALUES (1011,'مسند مالك بن الحويرث رضي الله عنه','638','0','0');
INSERT INTO [t14299] VALUES (1012,'٨٧٩ - حديث: "صلوا ما رأيتموني أصلي".','638','1011','0');
INSERT INTO [t14299] VALUES (1013,'مسند مالك بن يسار رضي الله عنه','638','0','0');
INSERT INTO [t14299] VALUES (1014,'٨٨٠ - حديث: "إذا سألتم الله تعالى فاسألوه ببطون أكفكم".','638','1013','0');
INSERT INTO [t14299] VALUES (1015,'مسند محمود بن الربيع الأنصاري رضي الله عنه','639','0','0');
INSERT INTO [t14299] VALUES (1016,'٨٨١ - حديث: "إن عتبان بن مالك قال: يا رسول الله إنها تكون الظلمة والسيل".','639','1015','0');
INSERT INTO [t14299] VALUES (1017,'مسند محمود بن لبيد الأشهلي رضي الله عنه','640','0','0');
INSERT INTO [t14299] VALUES (1018,'٨٨٢ - حديث: "ما جاء بك يا عمرو أحدبا على قومك أو رغبة في الإسلام".','640','1017','0');
INSERT INTO [t14299] VALUES (1019,'مسند مرداس الأسلمي رضي الله عنه','640','0','0');
INSERT INTO [t14299] VALUES (1020,'٨٨٣ - حديث: "يذهب الصالحون الأول فالأول".','640','1019','0');
INSERT INTO [t14299] VALUES (1021,'مسند المسور بن مخرمة ومروان بن الحكم رضي الله عنهما','641','0','0');
INSERT INTO [t14299] VALUES (1022,'٨٨٤ - حديث صلح الحديبية','641','1021','0');
INSERT INTO [t14299] VALUES (1023,'مسند المسيب بن حزن رضي الله عنه','643','0','0');
INSERT INTO [t14299] VALUES (1024,'٨٨٥ - حديث: "أي عم، قل: لا إله إلا الله، كلمة الشهادة، أشهد لك بها عند الله".','643','1023','0');
INSERT INTO [t14299] VALUES (1025,'مسند مطيع بن الأسود رضي الله عنه','644','0','0');
INSERT INTO [t14299] VALUES (1026,'٨٨٦ - حديث: "لا يقتل رجل من قريش بعد العام صبرا أبدا".','644','1025','0');
INSERT INTO [t14299] VALUES (1027,'مسند معاذ بن أنس الجهني رضي الله عنه','644','0','0');
INSERT INTO [t14299] VALUES (1028,'٨٨٧ - حديث: "أي المجاهدين أعظم أجرا؟ قال: أكثرهم لله ذكرا".','644','1027','0');
INSERT INTO [t14299] VALUES (1029,'٨٨٨ - حديث: "من ترك أن يلبس صالح الثياب، وهو يقدر عليه تواضعا لله تعالى".','645','1027','0');
INSERT INTO [t14299] VALUES (1030,'٨٨٩ - حديث: "الجفاء كل الجفاء، والكفر والنفاق من سمع منادي الله ينادي بالصلاة يدعو إلى الفلاح ولا يجيبه".','645','1027','0');
INSERT INTO [t14299] VALUES (1031,'٨٩٠ - حديث: "من تخطى رقاب الناس يوم الجمعة اتخذ جسرا من جهنم".','645','1027','0');
INSERT INTO [t14299] VALUES (1032,'مسند معاذ بن جبل رضي الله عنه','646','0','0');
INSERT INTO [t14299] VALUES (1033,'٨٩١ - حديث: "وأن يعطى الرجل ألف دينار فيتسخطها".','646','1032','0');
INSERT INTO [t14299] VALUES (1034,'٨٩٢ - حديث: "من مات هو يشهد أن لا إله إلا الله صادقا من قلبه".','646','1032','0');
INSERT INTO [t14299] VALUES (1035,'٨٩٣ - حديث: "من لقي الله لا يشرك به شيئا".','646','1032','0');
INSERT INTO [t14299] VALUES (1036,'٨٩٤ - حديث: "مفاتيح الجنة شهادة أن لا إله إلا الله".','647','1032','0');
INSERT INTO [t14299] VALUES (1037,'٨٩٥ - حديث: "انظروا فستجدونه راعيا معزى أو مكلبا".','647','1032','0');
INSERT INTO [t14299] VALUES (1038,'٨٩٦ - حديث: "من كان آخر كلامه لا إله إلا الله".','647','1032','0');
INSERT INTO [t14299] VALUES (1039,'٨٩٧ - حديث: "من غزا فخرا ورياء".','648','1032','0');
INSERT INTO [t14299] VALUES (1040,'٨٩٨ - حديث: "ملئ جنانا".','648','1032','0');
INSERT INTO [t14299] VALUES (1041,'٨٩٩ - حديث: "أنه سأل رسول الله صلى الله عليه وسلم عن أفضل الإيمان فقال: أن تحب لله وتعمل لسانك في ذكر الله، قال: وماذا يا رسول الله؟ قال: وأن تحب للناس ما تحب لنفسك".','648','1032','0');
INSERT INTO [t14299] VALUES (1042,'٩٠٠ - حديث: "ولا تتركن صلاة مكتوبة متعمدا".','649','1032','0');
INSERT INTO [t14299] VALUES (1043,'٩٠١ - حديث: "ولا ترفع عصاك عنهم أدبا".','649','1032','0');
INSERT INTO [t14299] VALUES (1044,'٩٠٢ - حديث: "سألته أن لا يهلك أمتي غرقا".','649','1032','0');
INSERT INTO [t14299] VALUES (1045,'٩٠٣ - حديث: "فقلت: حمى أو طاعونا".','649','1032','0');
INSERT INTO [t14299] VALUES (1046,'٩٠٤ - حديث: "فأخبر بها معاذ عند موته تأثما".','649','1032','0');
INSERT INTO [t14299] VALUES (1047,'٩٠٥ - حديث: "يا معاذ أتدري ما حق الله على العباد؟ قال: الله ورسوله أعلم. قال: يعبد الله ولا يشرك به شيء".','650','1032','0');
INSERT INTO [t14299] VALUES (1048,'٩٠٦ - حديث: "لا تجلس حتى يقبل قضاء الله ورسوله".','651','1032','0');
INSERT INTO [t14299] VALUES (1049,'٩٠٧ - حديث: "أخبرني بعمل يدخلني الجنة ويباعد من النار".','651','1032','0');
INSERT INTO [t14299] VALUES (1050,'٩٠٨ - حديث: "لا تشرك بالله شيئا وإن قتلت أو حرقت".','654','1032','0');
INSERT INTO [t14299] VALUES (1051,'٩٠٩ - حديث: "ما من مسلمين يتوفى لهما ثلاثة إلا أدخلهما الله الجنة بفضل رحمته إياهما".','655','1032','0');
INSERT INTO [t14299] VALUES (1052,'٩١٠ - حديث: "كأغزر ما كنت".','655','1032','0');
INSERT INTO [t14299] VALUES (1053,'٩١١ - حديث: "الغزو غزوان ... إلى قوله: فإن نومه ونبهه أجر كله".','656','1032','0');
INSERT INTO [t14299] VALUES (1054,'٩١٢ - حديث: "من عادى لله وليا فقد بارزه بالمحاربة".','656','1032','0');
INSERT INTO [t14299] VALUES (1055,'٩١٣ - حديث: "إن الشيطان ذئب الإنسان كذئب الغنم".','656','1032','0');
INSERT INTO [t14299] VALUES (1056,'مسند معاوية بن أبي سفيان رضي الله عنه','656','0','0');
INSERT INTO [t14299] VALUES (1057,'٩١٤ - حديث: "من يرد الله به خيرا يفقه في الدين".','657','1056','0');
INSERT INTO [t14299] VALUES (1058,'٩١٥ - حديث: "لا تحلفوا في المسألة، فوالله لا يسألني أحد منكم شيئا فتخرج له مسألته مني شيئا وأنا له كاره فيبارك (له فيما أعطيته) ".','658','1056','0');
INSERT INTO [t14299] VALUES (1059,'٩١٦ - حديث: "من أحب أن يتمثل له الرجال قياما".','659','1056','0');
INSERT INTO [t14299] VALUES (1060,'٩١٧ - حديث: "أما إني لم أستحلفكم تهمة لكم".','659','1056','0');
INSERT INTO [t14299] VALUES (1061,'مسند معقل بن يسار رضي الله عنه','660','0','0');
INSERT INTO [t14299] VALUES (1062,'٩١٨ - حديث: "ما من عبد يسترعيه الله رعية فلم يحطها بنصيحة لم يجد رائحة الجنة".','660','1061','0');
INSERT INTO [t14299] VALUES (1063,'٩١٩ - حديث: "من قرأ (يس) ابتغاء وجه الله غفر له ما تقدم من ذنبه فاقرءوها عند موتاكم".','660','1061','0');
INSERT INTO [t14299] VALUES (1064,'مسند معيقب رضي الله عنه','660','0','0');
INSERT INTO [t14299] VALUES (1065,'٩٢٠ - حديث: "إن كنت فاعلا فواحدة".','661','1064','0');
INSERT INTO [t14299] VALUES (1066,'مسند المغيرة بن شعبة رضي الله عنه','661','0','0');
INSERT INTO [t14299] VALUES (1067,'٩٢١ - حديث: "فمكث طويلا".','661','1066','0');
INSERT INTO [t14299] VALUES (1068,'٩٢٢ - حديث: "من نيح عليه (بكسر النون ماض مبني للمفعول) يعذب".','662','1066','0');
INSERT INTO [t14299] VALUES (1069,'٩٢٣ - حديث: "كان إذا ذهب المذهب أبعد".','662','1066','0');
INSERT INTO [t14299] VALUES (1070,'مسند المقداد بن الأسود رضي الله عنه','663','0','0');
INSERT INTO [t14299] VALUES (1071,'٩٢٤ - حديث: "لا يبقى على ظهر الأرض بيت مدر ولا وبر إلا أدخله كلمة الإسلام بعز عزيل أو ذل ذليل".','663','1070','0');
INSERT INTO [t14299] VALUES (1072,'مسند المقدام بن معد يكرب رضي الله عنه','663','0','0');
INSERT INTO [t14299] VALUES (1073,'٩٢٥ - حديث: "ما أكل أحد طعاما قط خيرا من أن يأكل من عمل يده".','663','1072','0');
INSERT INTO [t14299] VALUES (1074,'٩٢٦ - حديث: "ألا إني أوتيت الكتاب ومثله معه".','664','1072','0');
INSERT INTO [t14299] VALUES (1075,'٩٢٧ - حديث: "فثلث للطعام".','665','1072','0');
INSERT INTO [t14299] VALUES (1076,'مسند النعمان بن بشير رضي الله عنه','665','0','0');
INSERT INTO [t14299] VALUES (1077,'٩٢٨ - حديث: "قال: كلهم أعطيتهم كما أعطيته".','665','1076','0');
INSERT INTO [t14299] VALUES (1078,'٩٢٩ - حديث: "ألا وإن لكل ملك حمى".','665','1076','0');
INSERT INTO [t14299] VALUES (1079,'٩٣٠ - حديث: "ألستم في طعام وشراب ما شئتم".','666','1076','0');
INSERT INTO [t14299] VALUES (1080,'مسند نقادة الأسدي رضي الله عنه','666','0','0');
INSERT INTO [t14299] VALUES (1081,'٩٣١ - حديث: "اللهم اجعل قوت فلان يوم يوم".','666','1080','0');
INSERT INTO [t14299] VALUES (1082,'مسند النواس بن سمعان رضي الله عنه','667','0','0');
INSERT INTO [t14299] VALUES (1083,'٩٣٢ - حديث: "غير الدجال أخوفني عليكم".','667','1082','0');
INSERT INTO [t14299] VALUES (1084,'٩٣٣ - حديث: "كأنهما غمامتان أو ظلتان أو كأنهما فرقان".','675','1082','0');
INSERT INTO [t14299] VALUES (1085,'مسند نعيم بن هزال رضي الله عنه','675','0','0');
INSERT INTO [t14299] VALUES (1086,'٩٣٤ - حديث: "كان ماعز بن مالك في حجر أبي فأصاب جارية من الحي، فقال له أبي: ائت رسول الله صلى الله عليه وسلم فأخبره بما صنعت لعله يستغفر لك – يريد بذلك – رجاء أن يكون له مخرجا".','676','1085','0');
INSERT INTO [t14299] VALUES (1087,'مسند واثلة رضي الله عنه','677','0','0');
INSERT INTO [t14299] VALUES (1088,'٩٣٥ - حديث: "إن للمسلم حقا إذا رآه أخوه أن يتزحزح له".','677','1087','0');
INSERT INTO [t14299] VALUES (1089,'مسند يوسف بن عبد الله بن سلام رضي الله عنه','677','0','0');
INSERT INTO [t14299] VALUES (1090,'٩٣٦ - حديث: "ما على أحدكم إن وجد أن يتخذ ثوبين ليوم الجمعة".','677','1089','0');
INSERT INTO [t14299] VALUES (1091,'مسند يزيد بن الأسود رضي الله عنه','678','0','0');
INSERT INTO [t14299] VALUES (1092,'٩٣٧ - حديث: "أنه صلى فرأى رجلين لم يصليا مع القوم فقال: علي بهما".','678','1091','0');
INSERT INTO [t14299] VALUES (1093,'مسند يزيد بن الأخنس رضي الله عنه','678','0','0');
INSERT INTO [t14299] VALUES (1094,'٩٣٨ - حديث: "فيقول رجل: لو أن الله أعطاني مثل ما أعطى فلانا فأقوم به".','678','1093','0');
INSERT INTO [t14299] VALUES (1095,'مسند يعلى بن مرة رضي الله عنه','678','0','0');
INSERT INTO [t14299] VALUES (1096,'٩٣٩ - حديث: "ما أحسسنا منه شيئا حتى الساعة".','679','1095','0');
INSERT INTO [t14299] VALUES (1097,'مسند يزيد بن ركانة رضي الله عنه','679','0','0');
INSERT INTO [t14299] VALUES (1098,'٩٤٠ - حديث: "طلقت امرأتي البتة".','679','1097','0');
INSERT INTO [t14299] VALUES (1099,'مسند أبي أمامة الباهلي رضي الله عنه','680','0','0');
INSERT INTO [t14299] VALUES (1100,'٩٤١ - حديث: "ما أذن الله لعبد في شيء أفضل من ركعتين يصليهما".','681','1099','0');
INSERT INTO [t14299] VALUES (1101,'٩٤٢ - حديث: "قلت: يا نبي الله أرأيت الصيام ماذا هو؟ قال: فرض مجزى".','681','1099','0');
INSERT INTO [t14299] VALUES (1102,'٩٤٣ - حديث: "إن الله قد أعطى كل ذي حق حقه، فلا وصية لوارث، الولد للفراش، وللعاهر الحجر، وحسابهم على الله".','682','1099','0');
INSERT INTO [t14299] VALUES (1103,'٩٤٤ - حديث: "من أوى إلى فراشه طاهرا".','683','1099','0');
INSERT INTO [t14299] VALUES (1104,'٩٤٥ - حديث: "يا سعد إن كنت خلقت للجنة فما طال من عمرك وحسن من عملك فهو خير".','683','1099','0');
INSERT INTO [t14299] VALUES (1105,'٩٤٦ - حديث: "إنك إن تبذل الفضل خير لك".','684','1099','0');
INSERT INTO [t14299] VALUES (1106,'٩٤٧ - حديث: "ما من رجل يلي عشرة فما فوق ذلك إلا أتى الله مغلولا يده إلى عنقه يوم القيامة".','684','1099','0');
INSERT INTO [t14299] VALUES (1107,'٩٤٨ - حديث: "من اقتطع حق امرئ مسلم إلى قوله: وإن قضيب من أراك".','684','1099','0');
INSERT INTO [t14299] VALUES (1108,'٩٤٩ - حديث: "ثلاثة كلهم (ضامن) على الله".','685','1099','0');
INSERT INTO [t14299] VALUES (1109,'٩٥٠ - حديث: "من خرج من بيته متطهرا إلى صلاة مكتوبة فأجره كأجر الحاج".','685','1099','0');
INSERT INTO [t14299] VALUES (1110,'٩٥١ - حديث: "أي الدعاء أسمع؟ قال: جوف الليل الأخير".','686','1099','0');
INSERT INTO [t14299] VALUES (1111,'٩٥٢ - حديث: "لتنقضن عرى الإسلام عروة عروة".','687','1099','0');
INSERT INTO [t14299] VALUES (1112,'٩٥٣ - حديث: "ما ضل قوم بعد هدى كانوا عليه إلا أوتوا الجدل".','687','1099','0');
INSERT INTO [t14299] VALUES (1113,'٩٥٤ - حديث: "ما من أمتي أحد إلا وأنا أعرفه يوم القيامة، قالوا: يا رسول الله من رأيت ومن لم تر؟ قال: من رأيت ومن لم أر غرا محجلين من آثار الوضوء".','687','1099','0');
INSERT INTO [t14299] VALUES (1114,'٩٥٥ - حديث: "إن بلالا أخذ في الإقامة فلما أن قال: قد قامت الصلاة، قال النبي صلى الله عليه وسلم: أقامها الله وأدامها".','687','1099','0');
INSERT INTO [t14299] VALUES (1115,'٩٥٦ - حديث: "كان إذا فرغ من طعامه قال: الحمد لله كثيرا مباركا فيه غير مكفي ولا مودع ولا مستغنى عنه ربنا".','688','1099','0');
INSERT INTO [t14299] VALUES (1116,'٩٥٧ - حديث: "خمس بخ بخ سبحان الله .. الحديث".','688','1099','0');
INSERT INTO [t14299] VALUES (1117,'٩٥٨ - حديث: "عرض علي ربي ليجعل لي بطحاء مكة ذهبا".','689','1099','0');
INSERT INTO [t14299] VALUES (1118,'٩٥٩ - حديث: "إن أغبط أوليائي عندي".','689','1099','0');
INSERT INTO [t14299] VALUES (1119,'٩٦٠ - حديث: "وعدني ربي أن يدخل الجنة من أمتي سبعين ألفا لا حساب عليهم ولا عذاب، مع كل ألف سبعون ألفا وثلاث حثيات".','689','1099','0');
INSERT INTO [t14299] VALUES (1120,'٩٦١ - حديث: "أنه سئل هل يتناكح أهل الجنة؟ فقال: نعم دحما دحما".','690','1099','0');
INSERT INTO [t14299] VALUES (1121,'مسند أبي أيوب الأنصاري رضي الله عنه','690','0','0');
INSERT INTO [t14299] VALUES (1122,'٩٦٢ - حديث: "إن رجلا قال: يا رسول الله أخبرني بعمل يدخلني الجنة".','690','1121','0');
INSERT INTO [t14299] VALUES (1123,'مسند أبي بردة هانئ بن نيار رضي الله عنه','692','0','0');
INSERT INTO [t14299] VALUES (1124,'٩٦٣ - حديث: "لا تذهب الدنيا حتى تكون للكع بن لكع".','693','1123','0');
INSERT INTO [t14299] VALUES (1125,'مسند أبي برزة رضي الله عنه','693','0','0');
INSERT INTO [t14299] VALUES (1126,'٩٦٤ - حديث: "فقالت أمها: أجليبيب إنيه".','693','1125','0');
INSERT INTO [t14299] VALUES (1127,'٩٦٥ - حديث: "اعزل الأذى عن طريق المسلمين".','696','1125','0');
INSERT INTO [t14299] VALUES (1128,'٩٦٦ - حديث: "وكان لا يبالي بتأخير العشاء".','696','1125','0');
INSERT INTO [t14299] VALUES (1129,'٩٦٧ - حديث: "غزوت مع رسول الله صلى الله عليه وسلم ست غزوات، أو سبع غزوات أو ثماني".','696','1125','0');
INSERT INTO [t14299] VALUES (1130,'مسند أبي بصرة الغفاري رضي الله عنه','696','0','0');
INSERT INTO [t14299] VALUES (1131,'٩٦٨ - حديث: "إن الله زادكم صلاة فصلوها فيما بين صلاة العشاء إلى الصبح الوتر الوتر".','697','1130','0');
INSERT INTO [t14299] VALUES (1132,'مسند أبي بكر الصديق رضي الله عنه','697','0','0');
INSERT INTO [t14299] VALUES (1133,'٩٦٩ - حديث: "أنه حمل الحسن وقال: وا بأبي شبه النبي صلى الله عليه وسلم، ليس شبيه بعلي".','697','1132','0');
INSERT INTO [t14299] VALUES (1134,'٩٧٠ - حديث الصدقة','698','1132','0');
INSERT INTO [t14299] VALUES (1135,'٩٧١ - حديث: "إن أبا بكر قال لعمر: وما عسيتهم أن يفعلوا بي".','700','1132','0');
INSERT INTO [t14299] VALUES (1136,'مسند أبي بكرة رضي الله عنه','701','0','0');
INSERT INTO [t14299] VALUES (1137,'٩٧٢ - حديث: "إذا التقى المسلمان حمل أحدهما على أخيه السلاح فهما في جوف جهنم".','701','1136','0');
INSERT INTO [t14299] VALUES (1138,'٩٧٣ - حديث: "رب مبلغ أوعى من سامع".','701','1136','0');
INSERT INTO [t14299] VALUES (1139,'٩٧٤ - حديث: "كما تكونوا يولى عليكم".','702','1136','0');
INSERT INTO [t14299] VALUES (1140,'٩٧٥ - حديث: "ويخرجون من كان في قلبه ما يزن ذرة من إيمان".','703','1136','0');
INSERT INTO [t14299] VALUES (1141,'٩٧٦ - حديث: "إن كان أحدكم مادحا لا محالة فليقل: أحسب كذا وكذا، إن كان يرى أنه كذلك، وحسيبه الله".','703','1136','0');
INSERT INTO [t14299] VALUES (1142,'٩٧٧ - حديث: "كل الذنوب يغفر الله منها ما شاء إلا عقوق الوالدين".','703','1136','0');
INSERT INTO [t14299] VALUES (1143,'مسند أبي بهيسة رضي الله عنه','703','0','0');
INSERT INTO [t14299] VALUES (1144,'٩٧٨ - حديث: "يا نبي الله ما الشيء الذي لا يحل منعه قال: أن تفعل الخير خير لك".','704','1143','0');
INSERT INTO [t14299] VALUES (1145,'مسند أبي ثعلبة الخشني رضي الله عنه','704','0','0');
INSERT INTO [t14299] VALUES (1146,'٩٧٩ - حديث: "إن أحبكم إلي وأقربكم مني في الآخرة محاسنكم أخلاقا".','704','1145','0');
INSERT INTO [t14299] VALUES (1147,'مسند أبي جحيفة رضي الله عنه','708','0','0');
INSERT INTO [t14299] VALUES (1148,'٩٨٠ - حديث: "خرج علينا رسول الله صلى الله عليه وسلم بالهاجرة فأتى بوضوء فتوضأ فصلى بنا الظهر والعصر وبين يديه عنزة والمرأة والحمار يمرون من ورائها".','708','1147','0');
INSERT INTO [t14299] VALUES (1149,'مسند أبي الجهيم بن الحارث بن الصمة رضي الله عنه','709','0','0');
INSERT INTO [t14299] VALUES (1150,'٩٨١ - حديث: "لو يعلم المار بين يدي المصلي ماذا عليه لكان أن يقف أربعين خريفا خيرا له من أن يمر بين يديه".','709','1149','0');
INSERT INTO [t14299] VALUES (1151,'مسند أبي جمعة رضي الله عنه','710','0','0');
INSERT INTO [t14299] VALUES (1152,'٩٨٢ - حديث: "تغدينا مع رسول الله صلى الله عليه وسلم ومعنا أبو عبيدة بن الجراح فقال: يا رسول الله: أحد خير منا، أكلنا معك، وجاهدنا معك، قال: نعم، قوم بعدكم يؤمنون بي ولم يروني".','710','1151','0');
INSERT INTO [t14299] VALUES (1153,'مسند أبي الجعد رضي الله عنه','710','0','0');
INSERT INTO [t14299] VALUES (1154,'٩٨٣ - حديث: "من ترك ثلاث جمع تهاونا".','711','1153','0');
INSERT INTO [t14299] VALUES (1155,'مسند أبي حميد الساعدي رضي الله عنه','711','0','0');
INSERT INTO [t14299] VALUES (1156,'٩٨٤ - حديث: "لأعرفن ما جاء الله رجل ببقرة لها خوار".','711','1155','0');
INSERT INTO [t14299] VALUES (1157,'مسند أبي الدرداء رضي الله عنه','712','0','0');
INSERT INTO [t14299] VALUES (1158,'٩٨٥ - حديث: "هل أنتم تاركوا لي صاحبي".','712','1157','0');
INSERT INTO [t14299] VALUES (1159,'٩٨٦ - حديث: "فرغ الله إلى كل عبد من خلقه من خمس: من أجله ورزقه وأثره وشقي أم سعيد".','713','1157','0');
INSERT INTO [t14299] VALUES (1160,'٩٨٧ - حديث: "كيف يستخدمه وهو لا يحل له، أم كيف تورثه وهو لا يحل له".','714','1157','0');
INSERT INTO [t14299] VALUES (1161,'٩٨٨ - حديث: "وجدت الناس أخبر تقله".','714','1157','0');
INSERT INTO [t14299] VALUES (1162,'٩٨٩ - حديث: "إن أحسن ما زرتم به الله في مساجدكم وقبوركم البياض".','715','1157','0');
INSERT INTO [t14299] VALUES (1163,'٩٩٠ - حديث: "من سلك طريقا يطلب فيه علما، سلك الله به طريقا إلى الجنة".','715','1157','0');
INSERT INTO [t14299] VALUES (1164,'٩٩١ - حديث: "يقول الله: أنا الله لا إله إلا إنا".','716','1157','0');
INSERT INTO [t14299] VALUES (1165,'٩٩٢ - حديث: "أوصاني خليلي صلى الله عليه وسلم أن لا تشرك بالله شيئا".','716','1157','0');
INSERT INTO [t14299] VALUES (1166,'٩٩٣ - حديث: "ربنا الله الذي في السماء".','716','1157','0');
INSERT INTO [t14299] VALUES (1167,'٩٩٤ - حديث: "دعوة المرء المسلم لأخيه بظهر الغيب مستجابة".','717','1157','0');
INSERT INTO [t14299] VALUES (1168,'٩٩٥ - حديث: "ما طلعت شمس ولا غربت إلا وبجنبها ملكان".','717','1157','0');
INSERT INTO [t14299] VALUES (1169,'٩٩٦ - حديث: "من حفظ على أمتي أربعين حديثا".','717','1157','0');
INSERT INTO [t14299] VALUES (1170,'٩٩٧ - حديث: "ما من ثلاثة من قرية لا يؤذن ولا تقام فيها الصلاة إلا استحوذ عليهم الشيطان".','718','1157','0');
INSERT INTO [t14299] VALUES (1171,'مسند أبي ذر رضي الله عنه','718','0','0');
INSERT INTO [t14299] VALUES (1172,'٩٩٨ - حديث: "قلت: يا رسول الله: الصلاة؟ قال: خير موضوع".','718','1171','0');
INSERT INTO [t14299] VALUES (1173,'٩٩٩ - حديث إسلام أبي ذر.','720','1171','0');
INSERT INTO [t14299] VALUES (1174,'١٠٠٠ - حديث: "الصعيد الطيب وضوء المسلم".','720','1171','0');
INSERT INTO [t14299] VALUES (1175,'١٠٠١ - حديث: "ليت شعري متى تخرج نار من اليمن، من جبل الوراق تضيء منها','720','1171','0');
INSERT INTO [t14299] VALUES (1176,'١٠٠٢ - حديث: "غفرا يا أبا ذر".','722','1171','0');
INSERT INTO [t14299] VALUES (1177,'١٠٠٣ - حديث: "الحب في الله".','722','1171','0');
INSERT INTO [t14299] VALUES (1178,'١٠٠٤ - حديث: "بل تنقاد معهم حيث قادوك، وتنساق معهم حيث ساقوك، ولو عبد أسود".','723','1171','0');
INSERT INTO [t14299] VALUES (1179,'١٠٠٥ - حديث: "يا أبا ذر كيف تصنع إذا أخرجت من المدينة، قلت: السعة والدعة".','723','1171','0');
INSERT INTO [t14299] VALUES (1180,'١٠٠٦ - حديث: "كيف أنت وأئمة من بعدي يستأثرون بهذا الفيء".','723','1171','0');
INSERT INTO [t14299] VALUES (1181,'١٠٠٧ - حديث: "انتهيت إلى النبي صلى الله عليه وسلم وهو جالس في ظل الكعبة، فلما رآني قال: هم الأخسرون ورب الكعبة".','724','1171','0');
INSERT INTO [t14299] VALUES (1182,'١٠٠٨ - حديث: "إن العبد المسلم ليصلي الصلاة يريد بها وجه الله".','725','1171','0');
INSERT INTO [t14299] VALUES (1183,'١٠٠٩ - حديث: "كيف بك إذا كانت عليك أمراء يميتون الصلاة".','725','1171','0');
INSERT INTO [t14299] VALUES (1184,'١٠١٠ - حديث: "دعاني رسول الله صلى الله عليه وسلم وهو يشترط علي أن لا تسأل الناس شيئا".','725','1171','0');
INSERT INTO [t14299] VALUES (1185,'١٠١١ - حديث: "من تقرب إلي شبرا تقربت منه ذراعا، ومن تقرب إلي ذراعا تقربت منه باعا".','726','1171','0');
INSERT INTO [t14299] VALUES (1186,'١٠١٢ - حديث: "كانوا على أتقى قلب رجل واحد".','726','1171','0');
INSERT INTO [t14299] VALUES (1187,'١٠١٣ - حديث: "قلت: يا رسول الله ألا تستعملني؟ فضرب بيده على منكبي ثم قال: يا أبا ذر إنك ضعيف، وإنها أمانة".','727','1171','0');
INSERT INTO [t14299] VALUES (1188,'١٠١٤ - حديث: "كنت مخاصرا النبي صلى الله عليه وسلم يوما إلى منزلة فسمعته يقول: غير الدجال أخوف على أمتي من الدجال، قلت: أي شيء أخوف على أمتك من الدجال، قال: الأئمة المضلين".','727','1171','0');
INSERT INTO [t14299] VALUES (1189,'١٠١٥ - حديث: "من كان منكم صائما من الشهر ثلاثة أيام فليصم الثلاث البيض".','728','1171','0');
INSERT INTO [t14299] VALUES (1190,'١٠١٦ - حديث: "قلت: يا رسول الله ما آنية الحوض؟ قال: والذي نفسي بيده لآنيته أكثر من نجوم السماء".','728','1171','0');
INSERT INTO [t14299] VALUES (1191,'١٠١٧ - حديث: "ألا أدلك على كنز من كنوز الجنة: لا حول ولا قوة إلا بالله".','730','1171','0');
INSERT INTO [t14299] VALUES (1192,'١٠١٨ - حديث: "ونصرت بالرعب، فيرعب العدو، وهو مني مسيرة شهر".','732','1171','0');
INSERT INTO [t14299] VALUES (1193,'١٠١٩ - حديث: "سألت النبي صلى الله عليه وسلم عن كل شيء حتى سألته عن مسح الحصى، قال: واحدة أو دع".','732','1171','0');
INSERT INTO [t14299] VALUES (1194,'١٠٢٠ - حديث: "أي مسجد وضع في الأرض أول".','733','1171','0');
INSERT INTO [t14299] VALUES (1195,'١٠٢١ - حديث: "فإن عاد كان حقا على الله أن يسقيه من طينة الخبال".','734','1171','0');
INSERT INTO [t14299] VALUES (1196,'١٠٢٢ - حديث: "عرضت علي أمتي بأعمالها حسنة وسيئة".','735','1171','0');
INSERT INTO [t14299] VALUES (1197,'١٠٢٣ - حديث: "من فارق الجماعة شبرا".','735','1171','0');
INSERT INTO [t14299] VALUES (1198,'١٠٢٤ - حديث: "يصبح على كل سلامى من أحدكم صدقة".','735','1171','0');
INSERT INTO [t14299] VALUES (1199,'١٠٢٥ - حديث: "سألته: هل رأيت ربك؟ فقال: قد رأيته نورا أنى أراه".','736','1171','0');
INSERT INTO [t14299] VALUES (1200,'١٠٢٦ - حديث: "ما من مسلم ينفق من كل مال له زوجين إلى أن قال: إن كانت','736','1171','0');
INSERT INTO [t14299] VALUES (1201,'١٠٢٧ - حديث: "إن المكثرين هم المقلون يوم القيامة إلا من أعطاه الله خيرا فنفخ فيه يمينه وشماله و (بين) يديه ووراءه".','737','1171','0');
INSERT INTO [t14299] VALUES (1202,'١٠٢٨ - حديث: "رأيت أبا ذر وعليه حلة وعلى غلامه مثله".','740','1171','0');
INSERT INTO [t14299] VALUES (1203,'١٠٢٩ - حديث: "قلت يا رسول الله: سبقنا أصحاب الدثور سبقا بينا".','741','1171','0');
INSERT INTO [t14299] VALUES (1204,'١٠٣٠ - حديث: "بايعني خمسا وأوثقني سبعا، وأشهد الله علي تسعا".','741','1171','0');
INSERT INTO [t14299] VALUES (1205,'١٠٣١ - حديث: "إن رسول الله صلى الله عليه وسلم قال: ستة أيام ثم اعقل يا أبا ذر ما يقال لك بعد".','742','1171','0');
INSERT INTO [t14299] VALUES (1206,'١٠٣٢ - حديث: "يا أبا ذر هل تدري فيما تنتطحان".','742','1171','0');
INSERT INTO [t14299] VALUES (1207,'١٠٣٣ - حديث: "ومن دعا رجلا بالكفر أو قال: عدو الله، وليس ذلك إلا حار عليه".','743','1171','0');
INSERT INTO [t14299] VALUES (1208,'١٠٣٤ - حديث: "والمنفق سلعته بالحلف الكاذب".','743','1171','0');
INSERT INTO [t14299] VALUES (1209,'١٠٣٥ - حديث: "ما أظلت الخضراء ولا أقلت الغبراء من ذي لهجة أصدق ولا أوفى من أبي ذر".','744','1171','0');
INSERT INTO [t14299] VALUES (1210,'١٠٣٦ - حديث: "إن الناس يحشرون على ثلاثة أفواج راكبين طاعمين كاسين ... فوج يمشون ويسعون، وفوج تسحبهم الملائكة".','744','1171','0');
INSERT INTO [t14299] VALUES (1211,'١٠٣٧ - حديث: "ما للشياطين من سلاح أبلغ في الصالحين من النساء إلا المتزوجون".','744','1171','0');
INSERT INTO [t14299] VALUES (1212,'١٠٣٨ - حديث: "ما أحب أن سكون لي مثل أحد ذهبا".','747','1171','0');
INSERT INTO [t14299] VALUES (1213,'١٠٣٩ - حديث: "ما أحب أنه يحول لي ذهبا".','747','1171','0');
INSERT INTO [t14299] VALUES (1214,'١٠٤٠ - حديث: "ما يسرني أن عندي مثل أحد ذهبا تمضي علي ثالثة وعندي منه دينار إلا شيء أرصده لدين إلا أن أقول به في عباد الله هكذا وهكذا".','749','1171','0');
INSERT INTO [t14299] VALUES (1215,'١٠٤١ - حديث: "ما أحب أن لي ذاك عندي ذهبا".','749','1171','0');
INSERT INTO [t14299] VALUES (1216,'١٠٤٢ - حديث: "ما من رجل يموت فيترك غنما أو إبلا أو بقرا لم يؤد زكاتها إلا جاءته يوم القيامة أعظم ما تكون وأسمنه".','749','1171','0');
INSERT INTO [t14299] VALUES (1217,'١٠٤٣ - حديث: "إن خليلي عهد إلي أن أيما ذهب أو فضة أوكي عليه فهو جمر على صاحبه".','749','1171','0');
INSERT INTO [t14299] VALUES (1218,'١٠٤٤ - حديث: "لهذا عند الله أخير يوم القيامة من ملء الأرض مثل هذا".','750','1171','0');
INSERT INTO [t14299] VALUES (1219,'١٠٤٥ - حديث: "ألا أخبرك يا عبد الله بن جابر بأخير سورة في القرآن؟ قال: بلى. قال: الحمد لله رب العالمين".','750','1171','0');
INSERT INTO [t14299] VALUES (1220,'١٠٤٦ - حديث قول اليهود في عبد الله بن سلام: "أعلمنا وابن أعلمنا وأخيرنا وابن أخيرنا".','750','1171','0');
INSERT INTO [t14299] VALUES (1221,'١٠٤٧ - حديث ابن عباس: "أتى رسول الله صلى الله عليه وسلم وقد حمل قثم بين يديه، والفضل خلفه، فأيهم أشر وأيهم أخير".','750','1171','0');
INSERT INTO [t14299] VALUES (1222,'١٠٤٨ - حديث أنس: "لا يأتي عليكم زمان إلا الذي بعده أشر منه".','751','1171','0');
INSERT INTO [t14299] VALUES (1223,'١٠٤٩ - حديث أبي سعيد: "إن من أشر الناس عند الله منزلة يوم القيامة الرجل يفضي إلى امرأته وتفضي إليه ثم ينشر سرها".','751','1171','0');
INSERT INTO [t14299] VALUES (1224,'١٠٥٠ - حديث أنس: "أن النبي صلى الله عليه وسلم نهى أن يشرب الرجل قائما، قيل: فالأكل، قال: ذاك أشر وأخبث".','751','1171','0');
INSERT INTO [t14299] VALUES (1225,'مسند أبي رمثة رضي الله عنه','752','0','0');
INSERT INTO [t14299] VALUES (1226,'١٠٥١ - حديث: "أتيت النبي صلى الله عليه وسلم وهو يخطب يقول: يد المعطي العليا أمك وأباك وأختك وأخاك، وأدناك أدناك".','752','1225','0');
INSERT INTO [t14299] VALUES (1227,'مسند أبي زيد عمرو بن أخطب رضي الله عنه','752','0','0');
INSERT INTO [t14299] VALUES (1228,'١٠٥٢ - حديث: "كان هذا يوما الطعام فيه كريه".','752','1227','0');
INSERT INTO [t14299] VALUES (1229,'مسند أبي سعيد الخدري رضي الله عنه','753','0','0');
INSERT INTO [t14299] VALUES (1230,'١٠٥٣ - حديث: "ما من رجل يخرج من بيته متطهرا فيصلي مع المسلمين الصلاة ثم يجلس في المجلس ينتظر الصلاة الأخرى ألا إن الملائكة تقول: اللهم اغفر له".','753','1229','0');
INSERT INTO [t14299] VALUES (1231,'١٠٥٤ - حديث: "ما منكن امرأة تقدم ثلاثة من ولدها إلا كان لها حجاب من النار".','754','1229','0');
INSERT INTO [t14299] VALUES (1232,'١٠٥٥ - حديث: "أم والله إن أحدكم ليخرج بمسألته من عندي يتأبطها وما هي له إلا نار".','754','1229','0');
INSERT INTO [t14299] VALUES (1233,'١٠٥٦ - حديث: "أم والله لو شئتم لقلتم فصدقتم: أتيتنا مكذبا فصدقناك".','754','1229','0');
INSERT INTO [t14299] VALUES (1234,'١٠٥٧ - حديث: "ليس فيما دون خمس ذود من الإبل صدقة".','755','1229','0');
INSERT INTO [t14299] VALUES (1235,'١٠٥٨ - حديث: "إن الناس لكم تبع".','757','1229','0');
INSERT INTO [t14299] VALUES (1236,'١٠٥٩ - حديث: "من رأى منكرا إن استطاع أن يغيره بيده فليفعل، فإن لم يستطع فبلسانه فإن لم يستطع فبقلبه وذلك أضعف الإيمان".','757','1229','0');
INSERT INTO [t14299] VALUES (1237,'١٠٦٠ - حديث: "إذا أتى أحدكم أهله ثم بدا له أن يعود فليتوضأ بينهما وضوءا".','758','1229','0');
INSERT INTO [t14299] VALUES (1238,'١٠٦١ - حديث: "إن أهل الجنة لينزلون الغرف – إلى أن قال: قالوا: يا رسول الله تلك منازل الأنبياء لا يبلغها غيرهم قال: بلى والذي نفسي بيده رجال آمنوا بالله وصدقوا المرسلين".','758','1229','0');
INSERT INTO [t14299] VALUES (1239,'١٠٦٢ - حديث: "إذا أسلم العبد فحسن إسلامه".','758','1229','0');
INSERT INTO [t14299] VALUES (1240,'١٠٦٣ - حديث: "فما أنتم بأشد لي مناشدة في الحق، قد تبين لكم من المؤمن يومئذ للجبار، إذا رأوا أنهم قد نجوا في إخوانهم، يقولون ربنا إخواننا كانوا يصلون معنا".','759','1229','0');
INSERT INTO [t14299] VALUES (1241,'١٠٦٤ - حديث: "ألا رجل يتصدق على هذا فيصلي معه".','760','1229','0');
INSERT INTO [t14299] VALUES (1242,'١٠٦٥ - حديث: "الذهب بالذهب، والفضة بالفضة، والتمر بالتمر، والبر بالبر، والشعير بالشعير، والملح بالملح، سواء بسواء، مثلا بمثل".','760','1229','0');
INSERT INTO [t14299] VALUES (1243,'١٠٦٦ - حديث: "من رآني فقد رأى الحق فإن الشيطان لا يتكونني".','761','1229','0');
INSERT INTO [t14299] VALUES (1244,'١٠٦٧ - حديث: "إذا أيقط أهله من الليل فصليا جميعا".','761','1229','0');
INSERT INTO [t14299] VALUES (1245,'١٠٦٨ - حديث: "بعث علي إلى النبي صلى الله عليه وسلم بذهبية".','761','1229','0');
INSERT INTO [t14299] VALUES (1246,'١٠٦٩ - حديث: "كان النبي صلى الله عليه وسلم يخرج يوم الفطر والأضحى إلى المصلى، فأول شيء يبدأ به الصلاة".','762','1229','0');
INSERT INTO [t14299] VALUES (1247,'١٠٧٠ - حديث: "اطلبوا ليلة القدر في العشر الأواخر".','762','1229','0');
INSERT INTO [t14299] VALUES (1248,'١٠٧١ - حديث: "يلقى في النار وتقول: هل من مزيد، حتى يأتيها تبارك وتعالى فيضع قدمه عليها فتنزوي، وتقول: قدي قدي".','764','1229','0');
INSERT INTO [t14299] VALUES (1249,'١٠٧٢ - حديث: "إذا دخلتم على المريض فنفسوا له في أجله".','764','1229','0');
INSERT INTO [t14299] VALUES (1250,'١٠٧٣ - حديث: "لما كان عند مغيربان الشمس".','765','1229','0');
INSERT INTO [t14299] VALUES (1251,'١٠٧٤ - حديث: "اعتكف رسول الله صلى الله عليه وسلم عشر الأول".','765','1229','0');
INSERT INTO [t14299] VALUES (1252,'١٠٧٥ - حديث: "أنت مني بمنزلة هارون من موسى".','766','1229','0');
INSERT INTO [t14299] VALUES (1253,'١٠٧٦ - حديث: "خصلتان لا يجتمعان في مؤمن البخل وسوء الخلق".','766','1229','0');
INSERT INTO [t14299] VALUES (1254,'١٠٧٧ - حديث: "أيما مسلم كسا مسلما ثوبا على عري، كساه الله من خضر الجنة".','767','1229','0');
INSERT INTO [t14299] VALUES (1255,'١٠٧٨ - حديث: "لا يخرج الرجلان يضربان الغائط كاشفان عورتهما، يتحدثان فإن الله يمقت على ذلك".','767','1229','0');
INSERT INTO [t14299] VALUES (1256,'١٠٧٩ - حديث: "لتتبعن سنن الذين من قبلكم شبرا بشبر وذراعا فذراعا".','768','1229','0');
INSERT INTO [t14299] VALUES (1257,'١٠٨٠ - حديث: "تصدقن فإني أريتكن أكثر أهل النار".','769','1229','0');
INSERT INTO [t14299] VALUES (1258,'١٠٨١ - حديث: "إن أكثر ما أخاف عليكم ما يخرج الله لكم من بركات الأرض".','769','1229','0');
INSERT INTO [t14299] VALUES (1259,'١٠٨٢ - حديث: "ما يكون عندي فلن أدخره عنكم".','771','1229','0');
INSERT INTO [t14299] VALUES (1260,'١٠٨٣ - حديث: "ليس فيما أقل من خمسة أوسق صدقة".','771','1229','0');
INSERT INTO [t14299] VALUES (1261,'١٠٨٤ - حديث: "ثلاثة يضحك الله إليهم: الرجل إذا قام بالليل يصلي".','771','1229','0');
INSERT INTO [t14299] VALUES (1262,'١٠٨٥ - حديث: "إن رجلا رغسه الله مالا وولدا فلما حضره الموت دعا بنيه فقال: أي أب كنت لكم؟ قالوا: خير أب".','772','1229','0');
INSERT INTO [t14299] VALUES (1263,'١٠٨٦ - حديث: "بينما أنا نائم رأيت الناس يعرضون وعليهم قمص منها ما يبلغ الثدي، ومنها ما يبلغ دون ذلك ومر علي عمر بن الخطاب وعليه قميص يجره فقالوا: ما أولت ذلك يا رسول الله قال: الدين".','772','1229','0');
INSERT INTO [t14299] VALUES (1264,'١٠٨٧ - حديث: إن في الجنة لشجرة يسير الراكب الجواد أو المضمر السريع مائة عام لا يقطعها".','773','1229','0');
INSERT INTO [t14299] VALUES (1265,'١٠٨٨ - حديث: "رأيت أن في ذراعي سوارين من ذهب فكرهتهما فنفختهما فطارا فأولتهما: هذان الكذابان".','773','1229','0');
INSERT INTO [t14299] VALUES (1266,'١٠٨٩ - حديث: "سمع الله لمن حمده اللهم ربنا الحمد ملء السموات، وملء الأرض، وملء ما شئت من شيء بعد، أهل الثناء والمجد، أحق ما قال العبد، وكلنا لك عبد، لا مانع لما أعطيت، ولا معطي لما منعت، ولا ينفع ذا الجد منك الجد".','773','1229','0');
INSERT INTO [t14299] VALUES (1267,'١٠٩٠ - حديث: "ألا تأمنوني وأنا أمين من في السماء يأتيني خبر السماء صباح مساء".','776','1229','0');
INSERT INTO [t14299] VALUES (1268,'١٠٩١ - حديث: "من قرأ سورة الكهف في يوم الجمعة أضاء له النور ما بين الجمعتين".','776','1229','0');
INSERT INTO [t14299] VALUES (1269,'١٠٩٢ - حديث: "قال موسى: يا رب علمني شيئا أذكرك به".','777','1229','0');
INSERT INTO [t14299] VALUES (1270,'١٠٩٣ - حديث: "عدد رمل عالج".','777','1229','0');
INSERT INTO [t14299] VALUES (1271,'١٠٩٤ - حديث: "الله أكبر كبيرا".','777','1229','0');
INSERT INTO [t14299] VALUES (1272,'١٠٩٥ - حديث: "أربع سمعتهن من النبي صلى الله عليه وسلم فأعجبنني وآنقنني: أن لا تسافر المرأة ... ".','778','1229','0');
INSERT INTO [t14299] VALUES (1273,'١٠٩٦ - حديث: "أبشروا فإن من يأجوج ومأجوج ألف ومنكم رجل".','778','1229','0');
INSERT INTO [t14299] VALUES (1274,'١٠٩٧ - حديث: "أنه سئل العزل فقال: لا عليكم أن لا تفعلوا".','778','1229','0');
INSERT INTO [t14299] VALUES (1275,'١٠٩٨ - حديث: "أنكم ترون ربكم كما ترون القمر ليلة البدر".','779','1229','0');
INSERT INTO [t14299] VALUES (1276,'١٠٩٩ - حديث: "إذا شك أحدكم في صلاته فلم يدر".','779','1229','0');
INSERT INTO [t14299] VALUES (1277,'١١٠٠ - حديث: "أعوذ بك من غلبة الدين وقهر الرجال".','780','1229','0');
INSERT INTO [t14299] VALUES (1278,'١١٠١ - حديث: "من لا يرحم الناس لا يرحمه الله".','780','1229','0');
INSERT INTO [t14299] VALUES (1279,'١١٠٢ - حديث: "يخلص المؤمنون من النار فيحبسون على قنطرة بين الجنة والنار حتى إذا هذبوا ونقوا".','780','1229','0');
INSERT INTO [t14299] VALUES (1280,'١١٠٣ - حديث: "أبردوا بالظهر في الحر فإن شدة الحر من فوح جهنم".','781','1229','0');
INSERT INTO [t14299] VALUES (1281,'١١٠٤ - حديث: "خرج رسول الله صلى الله عليه وسلم في أضحى".','781','1229','0');
INSERT INTO [t14299] VALUES (1282,'١١٠٥ - حديث: "في قوله تعالى: (وفرش مرفوعة) [الواقعة: ٣٤]، قال: ارتفاعها لكما بين السماء والأرض".','781','1229','0');
INSERT INTO [t14299] VALUES (1283,'١١٠٦ - حديث: "أوه عين الربا فلا تقربنه".','782','1229','0');
INSERT INTO [t14299] VALUES (1284,'١١٠٧ - حديث: "من يتجر على هذا فيصلي معه".','782','1229','0');
INSERT INTO [t14299] VALUES (1285,'١١٠٨ - حديث: "يوشك أن يكون خير مال المسلم غنم يتبع بها شعف الجبال ومواقع القطر يفر بدينه من الفتن".','783','1229','0');
INSERT INTO [t14299] VALUES (1286,'١١٠٩ - حديث: "إن أول زمرة تدخل الجنة يوم القيامة صورة وجوههم مثل صورة القمر ليلة البدر، والزمرة الثانية على لون أحسن كوكب دري في السماء، لكل رجل منهم زوجتان على كل زوجة سبعون حلة، يرى مخ ساقها من وراء لحومهم أو دمائهم أو حللهم".','785','1229','0');
INSERT INTO [t14299] VALUES (1287,'١١١٠ - حديث: "حتى يتمنى الأحياء الأموات".','785','1229','0');
INSERT INTO [t14299] VALUES (1288,'١١١١ - حديث: "فينظر وجهه في خدها أصفى من المرآة".','786','1229','0');
INSERT INTO [t14299] VALUES (1289,'١١١٢ - حديث: "الصعود جبل في النار يتصعد فيه خمسين خريفا، ويهوي به كذلك فيه أبدا".','786','1229','0');
INSERT INTO [t14299] VALUES (1290,'١١١٣ - حديث: "إن الأعضاء تكفر اللسان تقول: نشدك الله فينا".','786','1229','0');
INSERT INTO [t14299] VALUES (1291,'١١١٤ - حديث: "خطب النبي صلى الله عليه وسلم فقال: إن الله عز وجل خير عبدا بين الدنيا وبين ما عنده، فاختار ما عند الله، فبكى أبو بكر الصديق، فقلت في نفسي: ما يبكي هذا إن يكن الله خير عبدا".','787','1229','0');
INSERT INTO [t14299] VALUES (1292,'١١١٥ - حديث: "إن أعظم الأمانة عند الله يوم القيامة يفضي الرجل إلى امرأته وتفضي إليه ثم يفشي سرها".','788','1229','0');
INSERT INTO [t14299] VALUES (1293,'١١١٦ - حديث: "يقول الله تعالى: "أخرجوا من كان في قلبه مثقال حبة من خردل من إيمان".','790','1229','0');
INSERT INTO [t14299] VALUES (1294,'١١١٧ - حديث: "أنه صلى الله عليه وسلم أرسل إلى رجل، فجاء ورأسه يقطر، فقال: لعلنا أعجلناك".','790','1229','0');
INSERT INTO [t14299] VALUES (1295,'١١١٨ - حديث: "ويح عمار".','791','1229','0');
INSERT INTO [t14299] VALUES (1296,'١١١٩ - حديث:" من رضي بالله ربا ... إلى أن قال: وأخرى يرفع الله بها العبد".','791','1229','0');
INSERT INTO [t14299] VALUES (1297,'١١٢٠ - حديث: "أنا سيد ولد آدم يوم القيامة ... إلى قوله: وما من نبي يومئذ آدم فمن سواه".','791','1229','0');
INSERT INTO [t14299] VALUES (1298,'١١٢١ - حديث: "إني أوشك أن أدعى فأجيب، وإني تارك فيكم الثقلين: كتاب الله','791','1229','0');
INSERT INTO [t14299] VALUES (1299,'١١٢٢ - حديث: "وتذيفون فيه من القطيعاء".','792','1229','0');
INSERT INTO [t14299] VALUES (1300,'١١٢٣ - حديث: "فأقول لهم: أما النسب فقد عرفت، ولكنكم أحدثتم بعدي، وارتددتم القهقرى".','792','1229','0');
INSERT INTO [t14299] VALUES (1301,'١١٢٤ - حديث: "أرأيت هذه الأمراض التي تصيبنا ما لنا بها؟ قال: كفارات".','793','1229','0');
INSERT INTO [t14299] VALUES (1302,'١١٢٥ - حديث: "أي شيء تركتم عبادي يصنعون".','793','1229','0');
INSERT INTO [t14299] VALUES (1303,'١١٢٦ - حديث:" الماء طهور".','793','1229','0');
INSERT INTO [t14299] VALUES (1304,'مسند أبي سعيد الزرقي رضي الله عنه','794','0','0');
INSERT INTO [t14299] VALUES (1305,'١١٢٧ - حديث: "إن ما يقدر في الرحم فسيكن".','794','1304','0');
INSERT INTO [t14299] VALUES (1306,'مسند أبي سفيان بن حرب رضي الله عنه','795','0','0');
INSERT INTO [t14299] VALUES (1307,'١١٢٨ - حديث هرقل','795','1306','0');
INSERT INTO [t14299] VALUES (1308,'١١٢٩ - حديث: "إنه صلى الله عليه وسلم قال: "والله لا يؤمن أحدكم، والله لا يؤمن أحدكم، قيل: يا رسول الله، ومن؟ ".','802','1306','0');
INSERT INTO [t14299] VALUES (1309,'مسند أبي شريح رضي الله عنه','802','0','0');
INSERT INTO [t14299] VALUES (1310,'١١٣٠ - حديث أبي شريح الخزاعي: "سمعت أذناي وأبصرت عيناي رسول الله صلى الله عليه وسلم حين تكلم".','802','1309','0');
INSERT INTO [t14299] VALUES (1311,'١١٣١ - حديث: "ومن قتل له قتيل فهو بخير النظرين".','804','1309','0');
INSERT INTO [t14299] VALUES (1312,'مسند أبي سعيد بن المعلى رضي الله عنه','805','0','0');
INSERT INTO [t14299] VALUES (1313,'١١٣٢ - حديث: "ما من الناس (أحد) أمن علينا في صحبته وذات يده من أبي بكر".','805','1312','0');
INSERT INTO [t14299] VALUES (1314,'مسند أبي شريح الكعبي رضي الله عنه','805','0','0');
INSERT INTO [t14299] VALUES (1315,'١١٣٣ - حديث: "إياكم والجلوس على الصعدات، فمن جلس منكم على الصعيد، فليعطه حقه، قلنا: وما حقه؟ قال: غضوض البصر".','805','1314','0');
INSERT INTO [t14299] VALUES (1316,'مسند أبي عسيب رضي الله عنه','806','0','0');
INSERT INTO [t14299] VALUES (1317,'١١٣٤ - حديث: "أن عمر قال: يا رسول الله إنا لمسئولون عن هذا يوم القيامة قال: نعم إلا من ثلاثة: خرقة كف بها الرجل عورته، أو كسرة سد بها جوعته، أو حجرا يتدخل فيه من الحر والقر".','806','1316','0');
INSERT INTO [t14299] VALUES (1318,'مسند أبي طلحة رضي الله عنه','806','0','0');
INSERT INTO [t14299] VALUES (1319,'١١٣٥ - حديث: "لا يدخل الملائكة بيتا فيه كلب ولا تصاوير".','806','1318','0');
INSERT INTO [t14299] VALUES (1320,'١١٣٦ - حديث: "أقرئ السلام فإنهم ما علمت أعفة صبر".','807','1318','0');
INSERT INTO [t14299] VALUES (1321,'مسند أبي عياش الزرقي رضي الله عنه','807','0','0');
INSERT INTO [t14299] VALUES (1322,'١١٣٧ - حديث: "من قال إذا أصبح: لا إله إلا الله وحده لا شريك له، له الملك وله الحمد وهو على كل شيء قدير. كعدل رقبة .. الحديث".','807','1321','0');
INSERT INTO [t14299] VALUES (1323,'مسند أبي قتادة رضي الله عنه','810','0','0');
INSERT INTO [t14299] VALUES (1324,'١١٣٨ - حديث: "لاها الله إذن يعمد إلى أسد من أسد الله يقاتل عن الله وعن رسوله فيعطيك سلبه".','811','1323','0');
INSERT INTO [t14299] VALUES (1325,'١١٣٩ - حديث: "إذا أتيتم الصلاة فعليكم السكينة".','823','1323','0');
INSERT INTO [t14299] VALUES (1326,'١١٤٠ - حديث: "إنا أصدنا حمار وحش".','823','1323','0');
INSERT INTO [t14299] VALUES (1327,'١١٤١ - حديث: "إنما هي طعمة أطعمكموها الله".','824','1323','0');
INSERT INTO [t14299] VALUES (1328,'١١٤٢ - حديث: "مرت جنازة فقال رسول الله صلى الله عليه وسلم: مستريح ومستراح منه".','824','1323','0');
INSERT INTO [t14299] VALUES (1329,'١١٤٣ - حديث: "سرنا مع النبي صلى الله عليه وسلم فقال بعض القوم: يا رسول الله لو عرست بنا".','824','1323','0');
INSERT INTO [t14299] VALUES (1330,'١١٤٤ - حديث: "الآيات بعد المائتين".','825','1323','0');
INSERT INTO [t14299] VALUES (1331,'١١٤٥ - حديث: "أسوأ الناس سرقة الذي يسرق في صلاته".','825','1323','0');
INSERT INTO [t14299] VALUES (1332,'١١٤٦ - حديث: "ثلاث من كل شهر ورمضان إلى رمضان فهذا صيام الدهر كله".','825','1323','0');
INSERT INTO [t14299] VALUES (1333,'١١٤٧ - حديث: "أرأيت إن قتلت في سبيل الله مقبلا غير مدبر".','825','1323','0');
INSERT INTO [t14299] VALUES (1334,'١١٤٨ - حديث: "خير الخيل الأدهم الأقرح الأرثم المحجل ثلاث".','826','1323','0');
INSERT INTO [t14299] VALUES (1335,'مسند أبي مالك الأشعري رضي الله عنه','827','0','0');
INSERT INTO [t14299] VALUES (1336,'١١٤٩ - حديث: "أربع في أمتي من أمر الجاهلية لا يتركونهن".','827','1335','0');
INSERT INTO [t14299] VALUES (1337,'١١٥٠ - حديث: "ولينزلن أقوام إلى جنب علم يروح عليهم بسارحة لهم يأتيهم لحاجة، فيقولون: ارجع إلينا غدا".','827','1335','0');
INSERT INTO [t14299] VALUES (1338,'١١٥١ - حديث: "وسبحان الله والحمد لله تملآن أو تملأ ما بين السموات والأرض".','828','1335','0');
INSERT INTO [t14299] VALUES (1339,'مسند أبي مسعود عقبة بن عمرو الأنصاري رضي الله عنه','828','0','0');
INSERT INTO [t14299] VALUES (1340,'١١٥٢ - حديث: "أدعو رسول الله صلى الله عليه وسلم خامس خمسة".','828','1339','0');
INSERT INTO [t14299] VALUES (1341,'١١٥٣ - حديث: "ومن قرأ بالآيتين من سورة البقرة في ليلة كفتاه".','829','1339','0');
INSERT INTO [t14299] VALUES (1342,'١١٥٤ - حديث: "فإن كانوا في القراءة سواء".','829','1339','0');
INSERT INTO [t14299] VALUES (1343,'١١٥٥ - حديث: "من صلى بالناس فليخفف فإن فيهم المريض والضعيف وذا الحاجة".','829','1339','0');
INSERT INTO [t14299] VALUES (1344,'١١٥٦ - حديث: "إن منكم منفرين، فأيكم ما صلى بالناس فليتجوز".','829','1339','0');
INSERT INTO [t14299] VALUES (1345,'١١٥٧ - حديث: "إن مما أدرك الناس من كلام النبوة: إذا لم تستح فاصنع ما شئت".','830','1339','0');
INSERT INTO [t14299] VALUES (1346,'١١٥٨ - حديث: "من ههنا جاءت الفتن نحو المشرق".','830','1339','0');
INSERT INTO [t14299] VALUES (1347,'١١٥٩ - حديث: "لله أقدر عليك منك عليه".','830','1339','0');
INSERT INTO [t14299] VALUES (1348,'مسند أبي موسى الأشعري رضي الله عنه','831','0','0');
INSERT INTO [t14299] VALUES (1349,'١١٦٠ - حديث: "إذا مرت بك جنازة يهودي أو نصراني أو مسلم فقوموا لها".','831','1348','0');
INSERT INTO [t14299] VALUES (1350,'١١٦١ - حديث: "ثم أمر لنا بثلاث ذود".','831','1348','0');
INSERT INTO [t14299] VALUES (1351,'١١٦٢ - حديث: "أعظم الناس أجرا في الصلاة أبعدهم فأبعدهم ممشى".','832','1348','0');
INSERT INTO [t14299] VALUES (1352,'١١٦٣ - حديث: "قال: والله إن قلتها".','832','1348','0');
INSERT INTO [t14299] VALUES (1353,'١١٦٤ - حديث: "بعث رسول الله صلى الله عليه وسلم أبا موسى ومعاذا إلى اليمن، فقال لهما: يسروا ولا تعسروا ... الحديث".','832','1348','0');
INSERT INTO [t14299] VALUES (1354,'١١٦٥ - حديث: "أي الإسلام أفضل؟ قال: من سلم المسلمون من لسانه ويده".','833','1348','0');
INSERT INTO [t14299] VALUES (1355,'١١٦٦ - حديث: "ثلاثة لهم أجران: رجل من أهل الكتاب آمن بنبيه".','833','1348','0');
INSERT INTO [t14299] VALUES (1356,'١١٦٧ - حديث: "لا أحد أصبر على أذى يسمعه من الله، يدعون له ندا وولدا وهو يرزقهم ويعافيهم".','834','1348','0');
INSERT INTO [t14299] VALUES (1357,'١١٦٨ - حديث: "من مر في شيء من مساجدنا أو أسواقنا بنبل فيأخذ على نصالها".','835','1348','0');
INSERT INTO [t14299] VALUES (1358,'١١٦٩ - حديث المواقيت.','835','1348','0');
INSERT INTO [t14299] VALUES (1359,'١١٧٠ - حديث: "من أحب دنياه أضر بآخرته".','836','1348','0');
INSERT INTO [t14299] VALUES (1360,'١١٧١ - حديث: "كان كثيرا مما يرفع رأسه".','836','1348','0');
INSERT INTO [t14299] VALUES (1361,'١١٧٢ - حديث: "خسفت الشمس، فقام النبي صلى الله عليه وسلم فزعا، فأتى المسجد فصلى بأطول قيام وركوع وسجود رأيته قط يفعله". ولفظ النسائي: "يفعله في صلاة قط".','837','1348','0');
INSERT INTO [t14299] VALUES (1362,'١١٧٣ - حديث: "الخازن الأمين يعطي ما أمر به كاملا موفورا طيبة به نفسه".','837','1348','0');
INSERT INTO [t14299] VALUES (1363,'١١٧٤ - حديث: "مثل المسلمين واليهود والنصارى ... إلى قوله: حتى إذا كان حين صلاة العصر".','838','1348','0');
INSERT INTO [t14299] VALUES (1364,'١١٧٥ - حديث: "ولكم أنتم أهل السفينة هجرتان".','838','1348','0');
INSERT INTO [t14299] VALUES (1365,'١١٧٦ - حديث: "مثل القلب كريشة ملقاة بأرض فلاة تقلبها الرياح ظهرا لبطن".','838','1348','0');
INSERT INTO [t14299] VALUES (1366,'١١٧٨ - حديث: "على كل مسلم صدقة، قالوا: فإن لم يجد؟ قال: فيعمل بيديه','839','1348','0');
INSERT INTO [t14299] VALUES (1367,'١١٧٩ - حديث: "اشفعوا فلتؤجروا".','840','1348','0');
INSERT INTO [t14299] VALUES (1368,'١١٨٠ - حديث: "من صام الدهر ضيقت عليه جهنم".','841','1348','0');
INSERT INTO [t14299] VALUES (1369,'١١٨١ - حديث: "وإني والنذير العريان فالنجاء".','841','1348','0');
INSERT INTO [t14299] VALUES (1370,'١١٨٢ - حديث: "اثنان فما فوقهما جماعة".','841','1348','0');
INSERT INTO [t14299] VALUES (1371,'١١٨٣ - حديث: "جنتان من فضة آنيتهما وما فيهما".','842','1348','0');
INSERT INTO [t14299] VALUES (1372,'١١٨٤ - حديث: "إن أعظم الذنوب عند الله أن يلقاه بها عبد بعد الكبائر أن يموت رجل وعليه دين لا يدع قضاء".','842','1348','0');
INSERT INTO [t14299] VALUES (1373,'١١٨٥ - حديث: "قام فينا رسول الله صلى الله عليه وسلم بخمس كلمات".','843','1348','0');
INSERT INTO [t14299] VALUES (1374,'١١٨٦ - حديث: "إن الله خلق آدم من قبضة قبضها".','843','1348','0');
INSERT INTO [t14299] VALUES (1375,'١١٨٧ - حديث: "في ساعة الجمعة وهي ما بين أن يجلس الإمام إلى أن تنقضي الصلاة".','844','1348','0');
INSERT INTO [t14299] VALUES (1376,'مسند أبي هريرة رضي الله عنه','845','0','0');
INSERT INTO [t14299] VALUES (1377,'١١٨٨ - حديث: "والذي نفس محمد بيده لا يسمع بي أحد من هذه الأمة يهودي ولا نصراني ثم يموت ولم يؤمن بالذي أرسلت به إلا كان من أصحاب النار".','845','1376','0');
INSERT INTO [t14299] VALUES (1378,'١١٨٩ - وحديث: "ما من أمير عشرة إلا يؤتى به يوم القيامة مغلولا لا يفكه إلا العدل، وما من رجل قرأ القرآن ثم نسيه إلا لقي الله يوم القيامة وهو أجذم".','856','1376','0');
INSERT INTO [t14299] VALUES (1379,'١١٩٠ - وحديث: "لا يكيد أهل المدينة أحد إلا انماع كما ينماع الملح في الماء".','856','1376','0');
INSERT INTO [t14299] VALUES (1380,'١١٩١ - وحديث: "ما لقيك الشيطان قط سالكا فجا إلا سلك فجا غير فجك".','857','1376','0');
INSERT INTO [t14299] VALUES (1381,'١١٩٢ - وحديث: "لا يبقى باب في المسجد إلا سد إلا باب أبي بكر".','857','1376','0');
INSERT INTO [t14299] VALUES (1382,'١١٩٣ - وحديث: "أنه لم يكن نبي إلا وقد وصف الدجال لأمته".','857','1376','0');
INSERT INTO [t14299] VALUES (1383,'١١٩٤ - وحديث: "ما منكن امرأة لها ثلاثة من الولد إلا كانوا لها حجابا من النار".','857','1376','0');
INSERT INTO [t14299] VALUES (1384,'١١٩٥ - وحديث: "ما يصيب المؤمن من وصب ولا نصب ولا هم ولا حزن، ولا أذى ولا غم، حتى الشوكة يشاكها إلا كفر الله من خطاياه".','857','1376','0');
INSERT INTO [t14299] VALUES (1385,'١١٩٦ - وحديث: "ما من مسلم يدعو الله بدعوة ليس فيها إثم، ولا قطيعة رحم، إلا أعطاه الله بها إحدى ثلاث: إما أن يعجل له دعوته وإما أن يذخرها له في الآخرة، وإما أن يصرف عنه من السوء مثلها".','857','1376','0');
INSERT INTO [t14299] VALUES (1386,'١١٩٧ - وحديث: "لا يصوم عبد يوما في سبيل الله إلا باعد الله بذلك اليوم النار عن وجهه سبعين خريفا".','857','1376','0');
INSERT INTO [t14299] VALUES (1387,'١١٩٨ - وحديث: "لا يسمع صوت المؤذن جن ولا إنس ولا شيء إلا شهد له يوم القيامة".','857','1376','0');
INSERT INTO [t14299] VALUES (1388,'١١٩٩ - وحديث: "ما بعث الله بنبي ولا استخلف خليفة إلا كانت له','857','1376','0');
INSERT INTO [t14299] VALUES (1389,'١٢٠٠ - وحديث: "ما جاءني جبريل قط إلا أمرني بالسواك".','858','1376','0');
INSERT INTO [t14299] VALUES (1390,'١٢٠١ - وحديث: "إن الله لم يضع داء إلا وضع له شفاء".','858','1376','0');
INSERT INTO [t14299] VALUES (1391,'١٢٠٢ - وحديث: "ليس منكم أحد إلا وقد وكل به قرين من الشيطان".','858','1376','0');
INSERT INTO [t14299] VALUES (1392,'١٢٠٣ - وحديث عبد الله بن بسر المازني مرفوعا: " ما من أمتي أحد إلا وأنا أعرفه يوم القيامة".','858','1376','0');
INSERT INTO [t14299] VALUES (1393,'١٢٠٤ - وحديث: "ما من عبد مؤمن تصدق بصدقة من طيب إلا وهو يضعها في كف الرحمن".','858','1376','0');
INSERT INTO [t14299] VALUES (1394,'١٢٠٥ - حديث: "ما من مسلم سلم علي إلا رد الله عليه روحي، حتى أرد عليه السلام".','858','1376','0');
INSERT INTO [t14299] VALUES (1395,'١٢٠٦ - حديث: "تفتح أبواب الجنة يوم الاثنين ويوم الخميس، فيغفر لكل عبد لا يشرك بالله شيئا، إلا رجل كانت بينه وبين أخيه شحناء".','859','1376','0');
INSERT INTO [t14299] VALUES (1396,'١٢٠٧ - حديث: "لتؤدن الحقوق إلى أهلها يوم القيامة".','860','1376','0');
INSERT INTO [t14299] VALUES (1397,'١٢٠٨ - حديث: "يقول العبد: مالي، وإن ما له من ماله إلا ثلاث".','860','1376','0');
INSERT INTO [t14299] VALUES (1398,'١٢٠٩ - حديث: "إن الدنيا ملعون ما فيها، إلا ذكر الله، وما والاه، وعالم ومتعلم".','860','1376','0');
INSERT INTO [t14299] VALUES (1399,'١٢١٠ - حديث: "كلمتان خفيفتان على اللسان، ثقيلتان في الميزان، حبيبتان إلى الرحمن، سبحان الله وبحمده، سبحان الله العظيم".','861','1376','0');
INSERT INTO [t14299] VALUES (1400,'١٢١١ - حديث: "خلق الله التربة يوم السبت، وخلق الجبال يوم الأحد .. الحديث".','872','1376','0');
INSERT INTO [t14299] VALUES (1401,'١٢١٢ - حديث: "إذا أوى أحدكم إلى فراشه فلينفضه بداخلة إزاره، فإنه لا يدري ما خلفه عليه".','874','1376','0');
INSERT INTO [t14299] VALUES (1402,'١٢١٣ - حديث: "لا يشير أحدكم إلى أخيه بالسلاح فإنه لا يدري أحدكم لعل الشيطان ينزع في يده فيقع في حفرة من النار".','876','1376','0');
INSERT INTO [t14299] VALUES (1403,'١٢١٤ - حديث: "صنفان من أهل النار لم أرهما: قوم معهم سياط كأذناب البقر، ونساء كاسيات عاريات".','877','1376','0');
INSERT INTO [t14299] VALUES (1404,'١٢١٥ - حديث: "رغم أنف من أدرك أبويه عند الكبر أحدهما أو كلاهما، فلم يدخل الجنة".','877','1376','0');
INSERT INTO [t14299] VALUES (1405,'١٢١٦ - حديث: "أقرب ما يكون العبد من ربه وهو ساجد".','878','1376','0');
INSERT INTO [t14299] VALUES (1406,'١٢١٧ - حديث: "إياك واللو فإن اللو تفتح عمل الشيطان".','881','1376','0');
INSERT INTO [t14299] VALUES (1407,'١٢١٨ - حديث: "لما خلق الله آدم ونفخ فيه الروح عطس ... الحديث".','884','1376','0');
INSERT INTO [t14299] VALUES (1408,'١٢١٩ - حديث: "كيف أنتم إذا نزل فيكم ابن مريم، وأمكم منكم".','885','1376','0');
INSERT INTO [t14299] VALUES (1409,'١٢٢٠ - حديث: "ليس من الإنسان شيء لا يبلى إلا عظما واحدا".','885','1376','0');
INSERT INTO [t14299] VALUES (1410,'١٢٢١ - حديث: "إن حوضي لأبعد من عدن من أيلة".','885','1376','0');
INSERT INTO [t14299] VALUES (1411,'١٢٢٢ - حديث: "إن أول زمرة يدخلون الجنة على صورة القمر ... الحديث".','886','1376','0');
INSERT INTO [t14299] VALUES (1412,'١٢٢٣ - حديث: "إن أدنى مقعد أحدكم من الجنة أن يقول له: تمن".','886','1376','0');
INSERT INTO [t14299] VALUES (1413,'١٢٢٤ - حديث: "سيحان وجيحان والفرات والنيل كل من أنهار الجنة".','886','1376','0');
INSERT INTO [t14299] VALUES (1414,'١٢٢٥ - حديث: "سوق الجنة".','886','1376','0');
INSERT INTO [t14299] VALUES (1415,'١٢٢٦ - حديث وفاة موسى','887','1376','0');
INSERT INTO [t14299] VALUES (1416,'١٢٢٧ - حديث: "بعثت من خير قرون بني آدم قرنا حتى كنت من القرن الذي كنت فيه".','888','1376','0');
INSERT INTO [t14299] VALUES (1417,'١٢٢٨ - حديث: "لما خلق الله آدم مسح ظهره".','889','1376','0');
INSERT INTO [t14299] VALUES (1418,'١٢٢٩ - حديث: "يوشك أن يضرب الناس أكباد الإبل".','889','1376','0');
INSERT INTO [t14299] VALUES (1419,'١٢٣٠ - حديث: "إن سورة في القرآن ثلاثون آية شفعت لرجل".','889','1376','0');
INSERT INTO [t14299] VALUES (1420,'١٢٣١ - حديث: "قالوا يا رسول الله: متى وجبت لك النبوة؟ قال: وآدم بين الروح والجسد".','890','1376','0');
INSERT INTO [t14299] VALUES (1421,'١٢٣٢ - حديث: "بعثت لأتم مكارم الأخلاق".','890','1376','0');
INSERT INTO [t14299] VALUES (1422,'١٢٣٣ - حديث: "صوموا لرؤيته وأفطروا لرؤيته فإن غم عليكم فأكملوا شعبان ثلاثين".','890','1376','0');
INSERT INTO [t14299] VALUES (1423,'١٢٣٤ - حديث: "إياكم أن تتخذوا قبور أنبيائكم منابر".','890','1376','0');
INSERT INTO [t14299] VALUES (1424,'١٢٣٥ - حديث: "حديث المجامع".','893','1376','0');
INSERT INTO [t14299] VALUES (1425,'١٢٣٦ - حديث: "يأتي يوم القيامة كأغذ ما كانت وأكبره وأسمنه".','894','1376','0');
INSERT INTO [t14299] VALUES (1426,'١٢٣٧ - " كل عمل ابن آدم يضاعف الحسنة عشر أمثالها ".','894','1376','0');
INSERT INTO [t14299] VALUES (1427,'١٢٣٨ - حديث: "ويضرب جسر على جهنم فأكون أول من يجيز، وفيها كلاليب".','895','1376','0');
INSERT INTO [t14299] VALUES (1428,'١٢٣٩ - حديث: "يسترق السمع فيلقيها إلى ما تحته ثم يلقيها إلى الآخر إلى ما تحته".','895','1376','0');
INSERT INTO [t14299] VALUES (1429,'١٢٤٠ - حديث مجالس الذكر: "قالوا لا، أي رب".','895','1376','0');
INSERT INTO [t14299] VALUES (1430,'١٢٤١ - حديث: "خير يوم طلعت عليه الشمس يوم الجمعة".','896','1376','0');
INSERT INTO [t14299] VALUES (1431,'١٢٤٢ - حديث: "مثلي كمثل رجل استوقد نارا".','897','1376','0');
INSERT INTO [t14299] VALUES (1432,'١٢٤٣ - حديث: "أحد أحد".','898','1376','0');
INSERT INTO [t14299] VALUES (1433,'١٢٤٤ - حديث: "ولكن الكبر من بطر الحق وغمط الناس".','898','1376','0');
INSERT INTO [t14299] VALUES (1434,'١٢٤٥ - حديث قصة إبراهيم والكافر','899','1376','0');
INSERT INTO [t14299] VALUES (1435,'١٢٤٦ - حديث: (قوله: بينا نحن عند رسول الله صلى الله عليه وسلم إذ طلع علينا رجل).','899','1376','0');
INSERT INTO [t14299] VALUES (1436,'١٢٤٧ - حديث: "آية المنافق ثلاث: إذا حدث كذب وإذا وعد أخلف، وإذا ائتمن خان".','908','1376','0');
INSERT INTO [t14299] VALUES (1437,'١٢٤٨ - حديث: "خياركم محاسنكم قضاء".','909','1376','0');
INSERT INTO [t14299] VALUES (1438,'١٢٤٩ - حديث: "جاءكم رمضان شهر مبارك".','909','1376','0');
INSERT INTO [t14299] VALUES (1439,'١٢٥٠ - حديث: "إن الناس معادن، خيارهم في الجاهلية خيارهم في الإسلام إذا فقهوا".','910','1376','0');
INSERT INTO [t14299] VALUES (1440,'١٢٥١ - حديث: "إن لله تسعة وتسعين اسما مائة إلا واحدا ".','910','1376','0');
INSERT INTO [t14299] VALUES (1441,'١٢٥٢ - حديث: "استوصوا بالنساء خيرا".','911','1376','0');
INSERT INTO [t14299] VALUES (1442,'١٢٥٣ - حديث: "منزلنا إذا فتح الله الخيف إن شاء الله".','912','1376','0');
INSERT INTO [t14299] VALUES (1443,'١٢٥٤ - حديث: "إذا أقيمت الصلاة فلا تأتوها وأنتم تسعون، ولكن ائتوها وأنتم تمشون وعليكم السكينة".','913','1376','0');
INSERT INTO [t14299] VALUES (1444,'١٢٥٥ - حديث: "إذا قال الرجل هلك الناس فهو أهلكهم".','913','1376','0');
INSERT INTO [t14299] VALUES (1445,'١٢٥٦ - حديث: "والله في عون العبد ما كان العبد في عون أخيه".','914','1376','0');
INSERT INTO [t14299] VALUES (1446,'١٢٥٧ - حديث: "نعم المنيحة اللقحة منيحة"','914','1376','0');
INSERT INTO [t14299] VALUES (1447,'١٢٥٨ - حديث: "ما لقيت من عقرب لدغتني البارحة".','917','1376','0');
INSERT INTO [t14299] VALUES (1448,'١٢٥٩ - حديث: "لا تقوم الساعة حتى تخرج نار من أرض الحجاز تضيء أعناق','917','1376','0');
INSERT INTO [t14299] VALUES (1449,'١٢٦٠ - حديث: "يعقد الشيطان على قافية رأس أحدكم ثلاث عقد بكل عقدة يضرب عليك ليلا طويلا".','918','1376','0');
INSERT INTO [t14299] VALUES (1450,'١٢٦١ - حديث: "من أفطر يوما من رمضان في غير رخصة رخصها الله له، فلن يقبل منه الدهر كله".','919','1376','0');
INSERT INTO [t14299] VALUES (1451,'١٢٦٢ - حديث: "سمع سامع بحمد الله وحسن بلائه علينا. ربنا صاحبنا، وأفضل علينا. عائذا بالله من النار ربنا".','920','1376','0');
INSERT INTO [t14299] VALUES (1452,'١٢٦٣ - حديث: "سبعة يظلهم الله في ظله – قوله: وشاب نشا في عبادة الله".','921','1376','0');
INSERT INTO [t14299] VALUES (1453,'١٢٦٤ - حديث: "من أصبح منكم اليوم صائما؟ قال أبو بكر أنا".','922','1376','0');
INSERT INTO [t14299] VALUES (1454,'١٢٦٥ - حديث: "إن العبد ليتكلم بالكلمة من رضوان الله لا يلقي لها بالا".','922','1376','0');
INSERT INTO [t14299] VALUES (1455,'١٢٦٦ - حديث: "نحن الآخرون السابقون يوم القيامة بيد أنهم أوتوا الكتاب من قبلنا".','923','1376','0');
INSERT INTO [t14299] VALUES (1456,'١٢٦٧ - حديث: "لا تدخلوا الجنة حتى تؤمنوا ولا تؤمنوا حتى تحابوا".','927','1376','0');
INSERT INTO [t14299] VALUES (1457,'١٢٦٨ - حديث: "إذا قرأ ابن آدم السجدة فسجد اعتزل الشيطان يبكي يقول: يا ويلتا".','928','1376','0');
INSERT INTO [t14299] VALUES (1458,'١٢٦٩ - حديث: "إن أول الناس يقضى عليه يوم القيامة ثلاثة: رجل استشهد فأتي به".','929','1376','0');
INSERT INTO [t14299] VALUES (1459,'١٢٧٠ - حديث: "طهور إناء أحدكم إذا ولغ فيه الكلب أن يغسله سبعا".','929','1376','0');
INSERT INTO [t14299] VALUES (1460,'١٢٧١ - حديث: "ورجل بايع رجلا بسلعة بعد العصر".','930','1376','0');
INSERT INTO [t14299] VALUES (1461,'١٢٧٢ - حديث: "صلاة الرجل في الجماعة تضعف على صلاته في بيته وفي سوقه خمس وعشرين ضعفا".','931','1376','0');
INSERT INTO [t14299] VALUES (1462,'١٢٧٣ - حديث: "تفضل صلاة الجمع صلاة أحدكم وحده بخمس وعشرين جزءا".','932','1376','0');
INSERT INTO [t14299] VALUES (1463,'١٢٧٤ - حديث: "الشهداء خمس".','932','1376','0');
INSERT INTO [t14299] VALUES (1464,'١٢٧٥ - حديث: "إذا وسد الأمر إلى غير أهله فانتظر الساعة".','933','1376','0');
INSERT INTO [t14299] VALUES (1465,'١٢٧٦ - حديث: "لا يموت لمسلم ثلاثة من الولد فيلج النار" وفي لفظ','933','1376','0');
INSERT INTO [t14299] VALUES (1466,'١٢٧٧ - حديث: "لا يموت لإحداكن ثلاثة من الولد فتحتسبه إلا دخلت الجنة".','937','1376','0');
INSERT INTO [t14299] VALUES (1467,'١٢٧٨ - حديث: "للصائم فرحتان يفرحهما".','938','1376','0');
INSERT INTO [t14299] VALUES (1468,'١٢٧٩ - حديث: "جف القلم بما هو كائن".','938','1376','0');
INSERT INTO [t14299] VALUES (1469,'١٢٨٠ - حديث: "أنه صلى الله عليه وسلم قال لبلال حدثني بأرجى عمل عملته في الإسلام".','939','1376','0');
INSERT INTO [t14299] VALUES (1470,'١٢٨١ - حديث: "أوصاني خليلي صلى الله عليه وسلم بثلاث، لا أدعهن حتى أموت: صوم ثلاثة أيام من كل شهر".','940','1376','0');
INSERT INTO [t14299] VALUES (1471,'١٢٨٢ - حديث جريج قوله: "في وجوه الميامين".','940','1376','0');
INSERT INTO [t14299] VALUES (1472,'١٢٨٣ - حديث: "لا تشد الرحال".','941','1376','0');
INSERT INTO [t14299] VALUES (1473,'١٢٨٤ - حديث: "سؤال القبر".','942','1376','0');
INSERT INTO [t14299] VALUES (1474,'١٢٨٥ - حديث: "إذا صلى أحدكم فلا يضع نعليه عن يمينه ولا عن يساره فيكون عن يمين صاحبه".','942','1376','0');
INSERT INTO [t14299] VALUES (1475,'١٢٨٦ - حديث: "من سره أن يكتال بالمكيال الأوفى إذا صلى علينا أهل البيت فليقل ... ".','943','1376','0');
INSERT INTO [t14299] VALUES (1476,'١٢٨٧ - حديث: "وأومأ أن كما أنتم".','943','1376','0');
INSERT INTO [t14299] VALUES (1477,'١٢٨٨ - حديث: "أسرعوا بالجنازة فإن تك صالحة فخير تقدمونها إليها".','943','1376','0');
INSERT INTO [t14299] VALUES (1478,'١٢٨٩ - حديث: "لا تصروا الإبل".','944','1376','0');
INSERT INTO [t14299] VALUES (1479,'١٢٩٠ - حديث الجمعة: " ... وفي آخر ثلاث ساعات منها ساعة من دعا الله فيها استجيب له".','945','1376','0');
INSERT INTO [t14299] VALUES (1480,'١٢٩١ - حديث: "في كل كبد رطبة أجر".','946','1376','0');
INSERT INTO [t14299] VALUES (1481,'١٢٩٢ - حديث: "نهى عن الصلاة نصف النهار".','946','1376','0');
INSERT INTO [t14299] VALUES (1482,'١٢٩٣ - حديث: "نبني صومعتك من ذهب، قال: لا، إلا من طين".','946','1376','0');
INSERT INTO [t14299] VALUES (1483,'١٢٩٤ - حديث: "إن أول ما يحاسب به العبد صلاته إلى قوله: انظروا هل لعبدي من تطوع فيكمل بها".','947','1376','0');
INSERT INTO [t14299] VALUES (1484,'١٢٩٥ - حديث: "لو كان لي مثل أحد ذهبا ما يسرني أن لا يمر علي ثلاث وعندي منه شيء".','947','1376','0');
INSERT INTO [t14299] VALUES (1485,'١٢٩٦ - حديث: "من قال حين يصبح وحين يمسي: سبحان الله وبحمده مائة مرة لم يأت أحد يوم القيامة بأفضل مما جاء به، إلا أحد قال مثل ما قال أو زاد عليه".','949','1376','0');
INSERT INTO [t14299] VALUES (1486,'١٢٩٧ - حديث: "لا يزني الزاني حين يزني وهو مؤمن، ولا يشرب الخمر حين يشربها وهو مؤمن".','950','1376','0');
INSERT INTO [t14299] VALUES (1487,'١٢٩٨ - حديث: "ما من أيام العمل أحب إلى الله أن يتعبد له فيها من عشر ذي الحجة".','950','1376','0');
INSERT INTO [t14299] VALUES (1488,'١٢٩٩ - حديث: "ما يصيب المسلم من نصب ولا وصب إلى قوله: حتى الشوكة يشاكها".','951','1376','0');
INSERT INTO [t14299] VALUES (1489,'١٣٠٠ - حديث: "أكثروا ذكر هادم اللذات".','951','1376','0');
INSERT INTO [t14299] VALUES (1490,'١٣٠١ - حديث: "لا يبولن أحدكم في الماء الدائم الذي لا يجري ثم يغتسل منه".','953','1376','0');
INSERT INTO [t14299] VALUES (1491,'١٣٠٢ - حديث: "كان رسول الله صلى الله عليه وسلم يسكت بين التكبير والقراءة إسكاتة – أحسبه هنئة".','956','1376','0');
INSERT INTO [t14299] VALUES (1492,'١٣٠٣ - حديث: "لا يقبل الله صلاة أحدكم إذا أحدث حتى يتوضأ".','957','1376','0');
INSERT INTO [t14299] VALUES (1493,'١٣٠٤ - حديث: "اللهم اشدد وطأتك على مضر اللهم اجعلها عليهم سنين كسني يوسف".','957','1376','0');
INSERT INTO [t14299] VALUES (1494,'١٣٠٥ - حديث: "لولا أن أشق على أمتي لأمرتهم بالسواك".','959','1376','0');
INSERT INTO [t14299] VALUES (1495,'١٣٠٦ - حديث: "من حج هذا البيت – فلم يرفث ولم يفسق رجع كيوم ولدته أمه".','959','1376','0');
INSERT INTO [t14299] VALUES (1496,'١٣٠٧ - حديث: "شر ما في الرجل شح هالع وجبن خالع".','959','1376','0');
INSERT INTO [t14299] VALUES (1497,'مسند إلى صاحبه مبالغة.','960','0','0');
INSERT INTO [t14299] VALUES (1498,'١٣٠٨ - حديث: "أي الصدقة أعظم".','960','1497','0');
INSERT INTO [t14299] VALUES (1499,'١٣٠٩ - حديث: "لا تقوم الساعة حتى يكثر فيكم المال فيفيض، حتى يهم رب المال من يقبل صدقته".','960','1497','0');
INSERT INTO [t14299] VALUES (1500,'١٣١٠ - حديث: "العمرة إلى العمرة كفارة لما بينهما".','961','1497','0');
INSERT INTO [t14299] VALUES (1501,'١٣١١ - حديث: "الصلوات الخمس والجمعة إلى الجمعة ورمضان إلى رمضان مكفرات لما بينهن إذا اجتنب الكبائر".','961','1497','0');
INSERT INTO [t14299] VALUES (1502,'١٣١٢ - حديث: "لا يصوم أحدكم يوم الجمعة إلا يوما قبله أو بعده".','961','1497','0');
INSERT INTO [t14299] VALUES (1503,'١٣١٣ - حديث: "لا صلاة لجار المسجد إلا في المسجد".','962','1497','0');
INSERT INTO [t14299] VALUES (1504,'١٣١٤ - حديث: "ما من أصحاب النبي - صلى الله عليه وسلم - أحد أكثر حديثا عنه مني إلا ما كان من عبد الله بن عمرو فإنه كان يكتب ولا أكتب".','962','1497','0');
INSERT INTO [t14299] VALUES (1505,'١٣١٥ - حديث: "الإيمان بضع وسبعون شعبة فأفضلها قول لا إله إلا الله".','962','1497','0');
INSERT INTO [t14299] VALUES (1506,'١٣١٦ - حديث: "فلما قدم جاءه بالألف دينار".','963','1497','0');
INSERT INTO [t14299] VALUES (1507,'١٣١٧ - حديث: "لا يلدغ المؤمن من الجحر مرتين".','965','1497','0');
INSERT INTO [t14299] VALUES (1508,'١٣١٨ - حديث: "إنكم في زمان من ترك عشر ما أمر به هلك".','965','1497','0');
INSERT INTO [t14299] VALUES (1509,'١٣١٩ - حديث: "الساعي على الأرملة والمسكين كالقائم لا يفتر".','965','1497','0');
INSERT INTO [t14299] VALUES (1510,'١٣٢٠ - " حديث: "من تعلم علما مما يبتغي به وجه الله لا يتعلمه إلا ليصيب به عرضا من الدنيا".','966','1497','0');
INSERT INTO [t14299] VALUES (1511,'١٣٢١ - حديث: "إذا نودي للصلاة أدبر الشيطان له ضراط".','966','1497','0');
INSERT INTO [t14299] VALUES (1512,'١٣٢٢ - حديث: "يكون في آخر الزمان دجالون كذابون يأتونكم من الأحاديث بما لا تسمعوا فإياكم وإياهم لا يضلونكم ولا يفترونكم".','967','1497','0');
INSERT INTO [t14299] VALUES (1513,'١٣٢٣ - حديث: "الصوم لي وأنا أجزي به، يذر طعامه وشرابه وشهوته من أجلي".','968','1497','0');
INSERT INTO [t14299] VALUES (1514,'١٣٢٤ - حديث: "يتعاقبون فيكم ملائكة بالليل وملائكة بالنهار".','968','1497','0');
INSERT INTO [t14299] VALUES (1515,'١٣٢٥ - حديث: "كل مولود يولد على الفطرة حتى يكون أبواه هما اللذان يهودانه وينصرانه".','969','1497','0');
INSERT INTO [t14299] VALUES (1516,'١٣٢٦ - حديث: "ما من مولود إلا يولد على الفطرة، فأبواه يهودانه أو ينصرانه أو يمجسانه كما تنتج البهيمة بهيمة جمعاء هل تحسون فيها من جدعاء".','971','1497','0');
INSERT INTO [t14299] VALUES (1517,'١٣٢٧ - حديث: "خير الصدقة ما كان عن ظهر غنى".','972','1497','0');
INSERT INTO [t14299] VALUES (1518,'١٣٢٨ - حديث: "ما من يوم يصبح العباد إلا ملكان ينزلان، فيقول أحدهما: اللهم أعط منفقا خلفا، ويقول الآخر: اللهم أعط ممسكا تلفا".','972','1497','0');
INSERT INTO [t14299] VALUES (1519,'١٣٢٩ - حديث: "إن خياركم أحسنكم قضاء".','973','1497','0');
INSERT INTO [t14299] VALUES (1520,'١٣٣٠ - حديث: "إن العبد ليتكلم بالكلمة ينزل بها في النار أبعد ما بين المشرق والمغرب".','974','1497','0');
INSERT INTO [t14299] VALUES (1521,'١٣٣١ - حديث: "إنما ورث هذا المال كابرا عن كابر".','974','1497','0');
INSERT INTO [t14299] VALUES (1522,'١٣٣٢ - حديث: "إنهم قالوا: إنا نجد في أنفسنا ما يتعاظم أحدنا أن يتكلم به، قال: وقد وجدتموه؟ قالوا: نعم".','975','1497','0');
INSERT INTO [t14299] VALUES (1523,'١٣٣٣ - حديث: "لا يزال الناس يتساءلون، حتى يقولون: هذا الله خلق كل شيء فمن خلقه".','975','1497','0');
INSERT INTO [t14299] VALUES (1524,'١٣٣٤ - حديث: "من يقم ليلة القدر إيمانا واحتسابا غفر له ما تقدم من ذنبه".','976','1497','0');
INSERT INTO [t14299] VALUES (1525,'١٣٣٥ - حديث: "نعم الرجل".','980','1497','0');
INSERT INTO [t14299] VALUES (1526,'١٣٣٦ - حديث: "شر الطعام طعام الوليمة يدعى لها الأغنياء ويترك الفقراء".','980','1497','0');
INSERT INTO [t14299] VALUES (1527,'١٣٣٧ - حديث: "إن الله تجاوز عن أمتي عما حدثت به أنفسها".','981','1497','0');
INSERT INTO [t14299] VALUES (1528,'١٣٣٨ - حديث: "كخ كخ أما شعرت أنا لا نأكل الصدقة".','982','1497','0');
INSERT INTO [t14299] VALUES (1529,'١٣٣٩ - حديث: "ما أسفل الكعبين من الإزار ففي النار".','982','1497','0');
INSERT INTO [t14299] VALUES (1530,'١٣٤٠ - حديث: "إذا شرب الكلب في إناء أحدكم".','983','1497','0');
INSERT INTO [t14299] VALUES (1531,'١٣٤١ - حديث: "إن رجلا رأى كلبا يأكل الثرى من العطش".','983','1497','0');
INSERT INTO [t14299] VALUES (1532,'١٣٤٢ - حديث: "كل كلم يكلمه المسلم في سبيل الله يكون يوم القيامة كهيئتها إذا طعنت".','983','1497','0');
INSERT INTO [t14299] VALUES (1533,'١٣٤٣ - حديث: "من آتاه الله مالا فلم يؤد كاته مثل له ماله يوم القيامة شجاعا أقرع".','984','1497','0');
INSERT INTO [t14299] VALUES (1534,'١٣٤٤ - حديث: "تبلغ الحلية من المؤمن حيث يبلغ الوضوء".','985','1497','0');
INSERT INTO [t14299] VALUES (1535,'١٣٤٥ - حديث: "أقيمت الصلاة وعدلت الصفوف قياما، فقال لنا رسول الله صلى الله عليه وسلم مكانكم".','986','1497','0');
INSERT INTO [t14299] VALUES (1536,'١٣٤٦ - حديث: "من هم بحسنة فلم يعملها كتبت له حسنة".','986','1497','0');
INSERT INTO [t14299] VALUES (1537,'١٣٤٧ - حديث: "فإن لم يكن له مال استسعى العبد في ثمن رقبته غير مشقوق عليه".','986','1497','0');
INSERT INTO [t14299] VALUES (1538,'١٣٤٨ - حديث: "الفضة بالفضة وزنا بوزن" الحديث.','987','1497','0');
INSERT INTO [t14299] VALUES (1539,'١٣٤٩ - حديث: "من قال سبحان الله كتبت له عشرين حسنة، ومن قال: الله أكبر فمثل ذلك".','987','1497','0');
INSERT INTO [t14299] VALUES (1540,'١٣٥٠ - حديث: "مر رجل بجذل شوك، فقال: لأميطن هذا أن لا يعقر".','987','1497','0');
INSERT INTO [t14299] VALUES (1541,'١٣٥١ - حديث: "إذا اكتحل أحدكم فليكتحل وترا".','988','1497','0');
INSERT INTO [t14299] VALUES (1542,'١٣٥٢ - حديث: "لا يؤمن العبد الإيمان كله حتى يترك الكذب في المزاحة".','988','1497','0');
INSERT INTO [t14299] VALUES (1543,'١٣٥٣ - حديث: "إنه نهى عن القيل والقال".','988','1497','0');
INSERT INTO [t14299] VALUES (1544,'١٣٥٤ - حديث: "جاء ناس إلى النبي صلى الله عليه وسلم فسألوه: إنا نجد في أنفسنا ما يتعاظم أحدنا أن يتكلم به".','989','1497','0');
INSERT INTO [t14299] VALUES (1545,'١٣٥٥ - حديث: "من جاء مسجدي هذا لم يأت إلا لخير يتعلمه".','990','1497','0');
INSERT INTO [t14299] VALUES (1546,'١٣٥٦ - حديث: "لا صلاة بعد الإقامة إلا المكتوبة".','990','1497','0');
INSERT INTO [t14299] VALUES (1547,'١٣٥٧ - حديث: "عليك السمع والطاعة في عسرك ويسرك ومنشطك ومكرهك".','990','1497','0');
INSERT INTO [t14299] VALUES (1548,'١٣٥٨ - حديث: "كفى بالمرء كذبا أن يحدث بكل ما سمع".','990','1497','0');
INSERT INTO [t14299] VALUES (1549,'١٣٥٩ - حديث: "يحشر الناس يوم القيامة ثلاثة أصناف".','990','1497','0');
INSERT INTO [t14299] VALUES (1550,'١٣٦٠ - حديث: "ليختصمن كل شيء يوم القيامة حتى الشاتين فيما انتطحتا".','991','1497','0');
INSERT INTO [t14299] VALUES (1551,'١٣٦١ - حديث: "ما لعبدي المؤمن عندي جزاء إذا قبضت صفيه من أهل الدنيا ثم احتسبه إلا الجنة".','991','1497','0');
INSERT INTO [t14299] VALUES (1552,'١٣٦٢ - حديث: "أرأيت أنه وضع في يدي سواران من ذهب فقطعتهما".','992','1497','0');
INSERT INTO [t14299] VALUES (1553,'١٣٦٣ - حديث: "ما من بني آدم مولود إلا يمسه الشيطان".','992','1497','0');
INSERT INTO [t14299] VALUES (1554,'١٣٦٤ - حديث: "غزوت مع رسول الله صلى الله عليه وسلم سبع غزوات أو ثماني".','992','1497','0');
INSERT INTO [t14299] VALUES (1555,'١٣٦٥ - حديث: "وليبصق عن يساره أو تحت قدمه فيدفنها".','994','1497','0');
INSERT INTO [t14299] VALUES (1556,'١٣٦٦ - حديث: "إذا كان يوم الجمعة وقفت الملائكة على باب المسجد يكتبون الأول فالأول".','994','1497','0');
INSERT INTO [t14299] VALUES (1557,'١٣٦٧ - حديث: "ابغني أحجارا أستنقض بها".','995','1497','0');
INSERT INTO [t14299] VALUES (1558,'١٣٦٨ - حديث: "أنا سيد الناس يوم القيامة – فلما رأى أصحابه لا يسألونه قال: ألا تقولون كيفه؟ قالوا: كيفه؟ ".','995','1497','0');
INSERT INTO [t14299] VALUES (1559,'١٣٦٩ - حديث: "فقال الذئب: هذا استنقذتها مني، فمن لهذا يوم السبع، يوم لا راعي لها غيري".','996','1497','0');
INSERT INTO [t14299] VALUES (1560,'١٣٧٠ - حديث: "دينار تنفقه في سبيل الله، ودينار تنفقه في رقبة، ودينار تصدق على مسكين، ودينار تنفقه على أهلك، فأعظمها أجرا الذي أنفقته على أهلك".','998','1497','0');
INSERT INTO [t14299] VALUES (1561,'١٣٧١ - حديث: "لا تختصوا ليلة الجمعة بقيام ولا يوم الجمعة بصيام".','998','1497','0');
INSERT INTO [t14299] VALUES (1562,'١٣٧٢ - حديث: "ليس صلاة أثقل على المنافقين من الفجر والعشاء".','998','1497','0');
INSERT INTO [t14299] VALUES (1563,'١٣٧٣ - حديث: "كل سلامى من الناس عليه صدقة".','999','1497','0');
INSERT INTO [t14299] VALUES (1564,'١٣٧٤ - حديث: "والذي نفسي بيده وددت أني أقاتل في سبيل الله فأقتل، ثم أحيا ثم أقتل".','1001','1497','0');
INSERT INTO [t14299] VALUES (1565,'١٣٧٥ - حديث: "يضحك الله إلى رجلين قتل أحدهما الآخر يدخلان الجنة".','1001','1497','0');
INSERT INTO [t14299] VALUES (1566,'١٣٧٦ - حديث: "من أنفق زوجين في سبيل الله".','1002','1497','0');
INSERT INTO [t14299] VALUES (1567,'١٣٧٧ - حديث: "من صام يوما ابتغاء وجه الله، بعده الله من جهنم كبعد غراب طار وهو فرخ حتى مات هرما".','1003','1497','0');
INSERT INTO [t14299] VALUES (1568,'١٣٧٨ - حديث: "اختبأت دعوتي شفاعة لأمتي، فهي نائلة منهم من مات لا يشرك','1003','1497','0');
INSERT INTO [t14299] VALUES (1569,'١٣٧٩ - حديث: قوله صلى الله عليه وسلم لليهود: "إني سائلكم عن شيء فهل أنتم صادقوني".','1004','1497','0');
INSERT INTO [t14299] VALUES (1570,'١٣٨٠ - حديث الموقف، قوله: "وهو آخر أهل الجنة دخولا الجنة".','1005','1497','0');
INSERT INTO [t14299] VALUES (1571,'١٣٨١ - حديث: "ذهب أهل الدثور من الأموال بالدرجات العلى".','1006','1497','0');
INSERT INTO [t14299] VALUES (1572,'١٣٨٢ - حديث: "من اغتسل يوم الجمعة غسل الجنابة".','1007','1497','0');
INSERT INTO [t14299] VALUES (1573,'١٣٨٣ - حديث: "العجماء جبار".','1007','1497','0');
INSERT INTO [t14299] VALUES (1574,'١٣٨٤ - حديث: "لا يتمنى أحدكم الموت إما محسنا فلعله يزداد، وإما مسيئا فلعله يستعتب".','1008','1497','0');
INSERT INTO [t14299] VALUES (1575,'١٣٨٥ - حديث: "طوبى لعبد آخذ بعنان فرسه في سبيل الله أشعث رأسه مغبرة قدماه".','1008','1497','0');
INSERT INTO [t14299] VALUES (1576,'١٣٨٦ - حديث: "هلك كسرى ثم لا يكون كسرى بعده، وقيصر ليهلكن ثم لا يكون قيصر بعده".','1009','1497','0');
INSERT INTO [t14299] VALUES (1577,'١٣٨٧ - حديث: "أنفق بلال، ولا تخش من ذي العرش إقلالا".','1010','1497','0');
INSERT INTO [t14299] VALUES (1578,'١٣٨٨ - حديث: "ما نقصت صدقة من مال".','1010','1497','0');
INSERT INTO [t14299] VALUES (1579,'١٣٨٩ - حديث: "ليس شيء أكرم على الله من الدعاء".','1010','1497','0');
INSERT INTO [t14299] VALUES (1580,'١٣٩٠ - حديث: "ثلاثة دعوتهم لا ترد: الصائم حين يفطر".','1010','1497','0');
INSERT INTO [t14299] VALUES (1581,'١٣٩١ - حديث: "رأس الكفر نحو المشرق".','1011','1497','0');
INSERT INTO [t14299] VALUES (1582,'١٣٩٢ - حديث: "إذا وقع الذباب في شراب أحدكم فليغمسه ثم لينزعه، فإن في إحدى جناحيه داء والآخر شفاء".','1011','1497','0');
INSERT INTO [t14299] VALUES (1583,'١٣٩٣ - حديث: "ألا أدلك على كلمة من تحت العرش من كنوز الجنة".','1011','1497','0');
INSERT INTO [t14299] VALUES (1584,'١٣٩٤ - حديث الشفاعة، قوله: "فرفع إليه الذراع".','1012','1497','0');
INSERT INTO [t14299] VALUES (1585,'١٣٩٥ - حديث: "هل عسيت إن فعلت ذلك بك أن تسأل غيره".','1012','1497','0');
INSERT INTO [t14299] VALUES (1586,'١٣٩٦ - حديث: "لو أخذت الخمر غوت أمتك".','1013','1497','0');
INSERT INTO [t14299] VALUES (1587,'١٣٩٧ - حديث: "اغتسل موسى عليه السلام (- قوله: ثوبي حجر) ".','1013','1497','0');
INSERT INTO [t14299] VALUES (1588,'١٣٩٨ - حديث: "اللهم بك أصبحنا".','1014','1497','0');
INSERT INTO [t14299] VALUES (1589,'١٣٩٩ - حديث: "إن النبي صلى الله عليه وسلم كان إذا الإنسان تزوج قال: بارك الله لك".','1014','1497','0');
INSERT INTO [t14299] VALUES (1590,'١٤٠٠ - حديث: "ولا أقول إن أحدا أفضل من يونس بن متى".','1015','1497','0');
INSERT INTO [t14299] VALUES (1591,'١٤٠١ - حديث: "أي العمل أفضل عند الله قال: إيمان بالله والجهاد وحج مبرور".','1016','1497','0');
INSERT INTO [t14299] VALUES (1592,'١٤٠٢ - حديث: "يأتي على الناس زمان لا يبالي المرء ما أخذ من الحلال أم من الحرام".','1016','1497','0');
INSERT INTO [t14299] VALUES (1593,'١٤٠٣ - حديث: "يؤذيني ابن آدم يسب الدهر وأنا الدهر، بيدي الأمر أقلب الليل والنهار".','1017','1497','0');
INSERT INTO [t14299] VALUES (1594,'١٤٠٤ - حديث: "ليأتين على الناس زمان لا يبقى أحد إلا أكل الربا".','1018','1497','0');
INSERT INTO [t14299] VALUES (1595,'١٤٠٥ - حديث: "لا يبع بعضكم على بيع بعض".','1018','1497','0');
INSERT INTO [t14299] VALUES (1596,'١٤٠٦ - حديث: "ثلاثة أنا خصمهم".','1018','1497','0');
INSERT INTO [t14299] VALUES (1597,'١٤٠٧ - حديث: "من اغتسل ثم أتى الجمعة فصلى ما قدر له، ثم أنصت حتى يفرغ','1018','1497','0');
INSERT INTO [t14299] VALUES (1598,'١٤٠٨ - حديث: "من تصدق بعدل تمرة من كسب طيب فإن الله يقبلها بيمينه".','1019','1497','0');
INSERT INTO [t14299] VALUES (1599,'١٤٠٩ - حديث: "أيما رجل جحد ولده وهو ينظر إليه، احتجب الله وفضحه في الأولين والآخرين على رؤوس الخلائق".','1019','1497','0');
INSERT INTO [t14299] VALUES (1600,'١٤١٠ - حديث: "إخوانكم جعلهم الله تحت أيديكم".','1020','1497','0');
INSERT INTO [t14299] VALUES (1601,'١٤١١ - حديث: "نعما للمملوك أن يتوفاه الله بحسن عبادة ربه وطاعة سيده".','1020','1497','0');
INSERT INTO [t14299] VALUES (1602,'١٤١٢ - حديث: "بعث النبي صلى الله عليه وسلم أبان على سرية".','1020','1497','0');
INSERT INTO [t14299] VALUES (1603,'١٤١٣ - حديث: "من تطهر في بيته ثم مضى إلى بيت من بيوت الله ليقضي فريضة من فرائض الله، كانت خطوتاه إحداها تحط خطيئة والأخرى ترفع درجة".','1021','1497','0');
INSERT INTO [t14299] VALUES (1604,'١٤١٤ - حديث: "أعددت لعبادي الصالحين ما لا عين رأت ولا أذن سمعت ولا خطر على قلب بشر ذخرا بله ما أطلعتم عليه".','1021','1497','0');
INSERT INTO [t14299] VALUES (1605,'١٤١٥ - حديث: "لا يفترق البيعان إلا عن تراض".','1023','1497','0');
INSERT INTO [t14299] VALUES (1606,'١٤١٦ - حديث: "من قذف مملوكه وهو بريء لما قال، جلد يوم القيامة إلا أن يكون كما قال".','1023','1497','0');
INSERT INTO [t14299] VALUES (1607,'١٤١٧ - حديث: "لأن يلج أحدكم بيمينه في أهله آثم له عند الله من أن يعطى كفارته".','1023','1497','0');
INSERT INTO [t14299] VALUES (1608,'١٤١٨ - حديث: "يمينك على ما يصدقك على صاحبك".','1024','1497','0');
INSERT INTO [t14299] VALUES (1609,'١٤١٩ - حديث: "من لا يرحم لا يرحم".','1024','1497','0');
INSERT INTO [t14299] VALUES (1610,'١٤٢٠ - حديث: "لأنا بهم أو ببعضهم أوثق مني بكم أو ببعضكم".','1025','1497','0');
INSERT INTO [t14299] VALUES (1611,'١٤٢١ - حديث: "لا تصوم المرأة وبعلها شاهد إلا بإذنه".','1025','1497','0');
INSERT INTO [t14299] VALUES (1612,'١٤٢٢ - حديث: "قسم النبي صلى الله عليه وسلم بيننا تمرا أصابني منه خمس، أربع تمرة وحشفة".','1026','1497','0');
INSERT INTO [t14299] VALUES (1613,'١٤٢٣ - حديث: "إذا زنت أمة أحدكم فليجلدها الحد".','1026','1497','0');
INSERT INTO [t14299] VALUES (1614,'١٤٢٤ - حديث: "من يرد الله به خيرا يصب منه".','1026','1497','0');
INSERT INTO [t14299] VALUES (1615,'١٤٢٥ - حديث: "أرأيتم لو أن نهرا بباب أحدكم يغتسل منه كل يوم خمس مرات، هل يبقى من درنه قالوا لا".','1026','1497','0');
INSERT INTO [t14299] VALUES (1616,'١٤٢٦ - حديث: "من أشد أمتي لي حبا ناس يكونون بعدي يود أحدهم لو رآني بأهله وماله".','1027','1497','0');
INSERT INTO [t14299] VALUES (1617,'١٤٢٧ - حديث: "خلق الله الخلق فلما فرغ منه قامت الرحم فأخذت فقال: مه".','1027','1497','0');
INSERT INTO [t14299] VALUES (1618,'١٤٢٨ - حديث: "يد الله ملأى لا تغيضها نفقة سحاء الليل والنهار".','1028','1497','0');
INSERT INTO [t14299] VALUES (1619,'١٤٢٩ - حديث: "ما من نبي من الأنبياء إلا أعطي ما مثله آمن عليه البشر".','1030','1497','0');
INSERT INTO [t14299] VALUES (1620,'١٤٣٠ - حديث: "خلق الله الرحمة في مائة جزء".','1031','1497','0');
INSERT INTO [t14299] VALUES (1621,'١٤٣١ - حديث: "وكونوا عباد الله إخوانا".','1031','1497','0');
INSERT INTO [t14299] VALUES (1622,'١٤٣٢ - حديث: "إن الله يغار، وغيرة الله أن لا يأتي المؤمن ما حرم الله".','1032','1497','0');
INSERT INTO [t14299] VALUES (1623,'١٤٣٣ - حديث:"من سأل الناس أموالهم تكثرا فإنما هي جمر فليستقل منه أو ليستكثر".','1032','1497','0');
INSERT INTO [t14299] VALUES (1624,'١٤٣٤ - حديث: "من سلك طريقا يلتمس فيه علما سهل الله له به طريقا إلى الجنة".','1033','1497','0');
INSERT INTO [t14299] VALUES (1625,'١٤٣٥ - حديث: "وأفضل الصيام بعد شهر رمضان شهر الله المحرم".','1033','1497','0');
INSERT INTO [t14299] VALUES (1626,'١٤٣٦ - حديث: "أرأيتم لو أن نهرا بباب أحدكم يغتسل منه كل يوم خمس مرات ما تقول ذلك يبقي من درنه".','1035','1497','0');
INSERT INTO [t14299] VALUES (1627,'١٤٣٧ - حديث: "آية المنافق ثلاث: وإن صلى وصام وزعم أنه مسلم".','1038','1497','0');
INSERT INTO [t14299] VALUES (1628,'١٤٣٨ - حديث:" المسلم من سلم الناس من لسانه ويده، والمؤمن من أمنه الناس على دمائهم وأموالهم".','1038','1497','0');
INSERT INTO [t14299] VALUES (1629,'١٤٣٩ - حديث: "فقضى رسول الله صلى الله عليه وسلم بديتها على العاقلة، وفي جنينها غرة عبد أو أمة".','1038','1497','0');
INSERT INTO [t14299] VALUES (1630,'١٤٤٠ - حديث: "لقد ظننت أن لا يسألني عن هذا الحديث أحد أول منك".','1039','1497','0');
INSERT INTO [t14299] VALUES (1631,'١٤٤١ - حديث: "ما أخرجكما من بيوتكما هذه الساعة، قالا: الجوع الجوع، قال: والذي نفسي بيده لأخرجني الذي أخرجكما".','1040','1497','0');
INSERT INTO [t14299] VALUES (1632,'١٤٤٢ - حديث: "من صام رمضان إيمانا واحتسابا".','1040','1497','0');
INSERT INTO [t14299] VALUES (1633,'١٤٤٣ - حديث: "إن الله جعل الحق على لسان عمر".','1042','1497','0');
INSERT INTO [t14299] VALUES (1634,'١٤٤٤ - حديث ماعز قوله: "فما نلتما من عرض أخيكما آنفا أشد".','1043','1497','0');
INSERT INTO [t14299] VALUES (1635,'١٤٤٥ - حديث: "فنعم المرضعة وبئست الفاطمة".','1043','1497','0');
INSERT INTO [t14299] VALUES (1636,'١٤٤٦ - حديث: "إن قعر جهنم لسبعين خريفا".','1043','1497','0');
INSERT INTO [t14299] VALUES (1637,'١٤٤٧ - حديث قتل خبيب: قوله: "حتى أجمعوا قتله".','1046','1497','0');
INSERT INTO [t14299] VALUES (1638,'١٤٤٨ - حديث: "قصة أبي طالب: (لولا تعيرني قريش) ".','1047','1497','0');
INSERT INTO [t14299] VALUES (1639,'١٤٤٩ - حديث: " من أطاعني فقد أطاع الله، ومن يعصيني فقد عصى الله ".','1047','1497','0');
INSERT INTO [t14299] VALUES (1640,'١٤٥٠ - حديث: " كل أهل الجنة يرى مقعده من النار فيقول: لولا أن الله','1047','1497','0');
INSERT INTO [t14299] VALUES (1641,'١٤٥١ - حديث: "إذا كان النصف من شعبان فأمسكوا عن الصوم حتى يكون رمضان".','1048','1497','0');
INSERT INTO [t14299] VALUES (1642,'١٤٥٢ - حديث: "ما من صاحب ذهب ولا فضة لا يؤدي منها حقها".','1048','1497','0');
INSERT INTO [t14299] VALUES (1643,'١٤٥٣ - حديث: "ألا أخبركم بخيركم من شركم".','1050','1497','0');
INSERT INTO [t14299] VALUES (1644,'١٤٥٤ - حديث: "تجدون من خير الناس أشدهم كراهية لهذا الأمر حتى يقع فيه".','1051','1497','0');
INSERT INTO [t14299] VALUES (1645,'١٤٥٥ - حديث: "ويل للأمراء".','1051','1497','0');
INSERT INTO [t14299] VALUES (1646,'١٤٥٦ - حديث: "من خير معاش الناس لهم رجل ممسك عنان فرسه في سبيل الله يطير على متنه".','1051','1497','0');
INSERT INTO [t14299] VALUES (1647,'١٤٥٧ - حديث: "إذا اشتد الحر فأبردوا عن الصلاة".','1052','1497','0');
INSERT INTO [t14299] VALUES (1648,'١٤٥٨ - حديث: "من منح منحة بصدقة راحت صبوحها وغبوقها".','1054','1497','0');
INSERT INTO [t14299] VALUES (1649,'١٤٥٩ - حديث: "لو يعلم الناس ما في النداء والصف الأول، ثم لا يجدوا إلا أن','1054','1497','0');
INSERT INTO [t14299] VALUES (1650,'١٤٦٠ - حديث: "كان إذا دخل في الصلاة رفع يديه مدا".','1055','1497','0');
INSERT INTO [t14299] VALUES (1651,'١٤٦١ - حديث: "إن الله حبس عن مكة الفيل، وسلط عليهم رسوله والمؤمنين، ألا وإنها لم تحل لأحد قبلي".','1056','1497','0');
INSERT INTO [t14299] VALUES (1652,'١٤٦٢ - حديث: "لأن يمتلئ جوف رجل قيحا حتى يريه".','1057','1497','0');
INSERT INTO [t14299] VALUES (1653,'١٤٦٣ - حديث الذئب، قوله: "إن رأيت كاليوم".','1057','1497','0');
INSERT INTO [t14299] VALUES (1654,'١٤٦٤ - حديث: "يقولون الكرم، وإنما الكرم قلب المؤمن".','1057','1497','0');
INSERT INTO [t14299] VALUES (1655,'١٤٦٥ - حديث: "لا تصحب الملائكة رفقة فيها كلب ولا جرس".','1058','1497','0');
INSERT INTO [t14299] VALUES (1656,'١٤٦٦ - حديث: "قال لي النبي صلى الله عليه وسلم يا أبا هر".','1058','1497','0');
INSERT INTO [t14299] VALUES (1657,'١٤٦٧ - حديث: "إذا هلك كسرى فلا كسرى بعده وإذا هلك قيصر فلا قيصر بعده".','1058','1497','0');
INSERT INTO [t14299] VALUES (1658,'١٤٦٨ - حديث: "كان إبراهيم أول الناس ضيف ... الحديث".','1058','1497','0');
INSERT INTO [t14299] VALUES (1659,'١٤٦٩ - حديث: "من لعق العسل ثلاث غدوات في كل شهر".','1059','1497','0');
INSERT INTO [t14299] VALUES (1660,'١٤٧٠ - حديث: "من رآني في المنام فقد رآني حقا".','1059','1497','0');
INSERT INTO [t14299] VALUES (1661,'١٤٧١ - حديث: "من اضطجع مضجعا لم يذكر الله فيه كان عليه ترة يوم القيامة .. الحديث".','1060','1497','0');
INSERT INTO [t14299] VALUES (1662,'١٤٧٢ - حديث: "إن لله ملائكة يطوفون في الطرق يلتمسون أهل الذكر، فإذا وجدوا قوما يذكرون الله تنادوا: هلموا إلى حاجتكم".','1060','1497','0');
INSERT INTO [t14299] VALUES (1663,'١٤٧٣ - حديث:" الله الذي لا إله إلا هو إن كنت لأعتمد بكبدي على الأرض من الجوع".','1061','1497','0');
INSERT INTO [t14299] VALUES (1664,'١٤٧٤ - حديث: "ما أنزل الله من السماء من بركة إلا أصبح فريق من الناس بها كافرين ينزل الله الغيث فيقولون بكوكب كذا وكذا".','1061','1497','0');
INSERT INTO [t14299] VALUES (1665,'١٤٧٥ - حديث: "إن الله خلق الرحمة يوم خلقها مائة رحمة فأمسك عنده تسعا وتسعين وأرسل في خلقه كلهم رحمة واحدة فلو يعلم الكافر بكل الذي عند الله من الرحمة لم ييأس من الجنة، ولو يعلم المؤمن بالذي عند الله من العذاب لم يأمن من النار".','1061','1497','0');
INSERT INTO [t14299] VALUES (1666,'١٤٧٦ - حديث: "كنا مع النبي صلى الله عليه وسلم – في مسير فنفدت أزواد القوم – إلى أن قال: (حتى ملأ القوم أزودتهم) ".','1062','1497','0');
INSERT INTO [t14299] VALUES (1667,'١٤٧٧ - حديث: "كنا قعودا حول النبي صلى الله علىه وسلم".','1062','1497','0');
INSERT INTO [t14299] VALUES (1668,'١٤٧٨ - حديث: "من كان يؤمن بالله واليوم الآخر فلا يؤذي جاره".','1065','1497','0');
INSERT INTO [t14299] VALUES (1669,'١٤٧٩ - حديث: "لكل نبي دعوة مستجابة".','1065','1497','0');
INSERT INTO [t14299] VALUES (1670,'١٤٨٠ - حديث: "يدخل الجنة من أمتي سبعون ألفا زمرة واحدة".','1065','1497','0');
INSERT INTO [t14299] VALUES (1671,'١٤٨١ - حديث: "الإيمان يمان والفقه يمان والحكمة يمانية".','1066','1497','0');
INSERT INTO [t14299] VALUES (1672,'١٤٨٢ - حديث: "أنه قال للحسن حزقة حزقة ترق عين بقة".','1066','1497','0');
INSERT INTO [t14299] VALUES (1673,'١٤٨٣ - حديث: "إذا قضى الله الأمر في السماء ضربت الملائكة بأجنحتها خضعانا لقوله كأنه سلسلة على صفوان".','1066','1497','0');
INSERT INTO [t14299] VALUES (1674,'١٤٨٤ - حديث: "المستبان ما قالا فعلى البادئ".','1067','1497','0');
INSERT INTO [t14299] VALUES (1675,'١٤٨٥ - حديث: "بحسب امرئ من الشر أن يحقر أخاه المسلم".','1067','1497','0');
INSERT INTO [t14299] VALUES (1676,'١٤٨٦ - حديث: "بدأ الإسلام غريبا وسيعود غريبا كما بدأ".','1067','1497','0');
INSERT INTO [t14299] VALUES (1677,'١٤٨٧ - حديث: "لقد رأيتني في الحجر وقريش تسألني عن مسراي، فسألتني عن أشياء من بيت المقدس لم أثبتها فكربت كربة ما كربت مثله".','1068','1497','0');
INSERT INTO [t14299] VALUES (1678,'١٤٨٨ - حديث: "إذا أتى أحدكم الغائط فلا يستقبل القبلة ولا يستدبرها ولا يستطيب بيمينه".','1068','1497','0');
INSERT INTO [t14299] VALUES (1679,'١٤٨٩ - حديث: "إن أول ما يسأل عنه العبد يوم القيامة من النعيم أن يقال: ألم أصح جسمك".','1069','1497','0');
INSERT INTO [t14299] VALUES (1680,'١٤٩٠ - حديث: "هو الطهور ماؤه الحل ميتته".','1069','1497','0');
INSERT INTO [t14299] VALUES (1681,'١٤٩١ - حديث: "فإن أحدكم لا يدري أين باتت يده".','1070','1497','0');
INSERT INTO [t14299] VALUES (1682,'١٤٩٢ - حديث: "احتج آدم وموسى فقال موسى: أنت آدم الذي خلقك الله بيده".','1071','1497','0');
INSERT INTO [t14299] VALUES (1683,'١٤٩٣ - حديث: "إن الدين يسر".','1071','1497','0');
INSERT INTO [t14299] VALUES (1684,'١٤٩٤ - حديث: "يقولون: إن أبا هريرة يكثر والله الموعد".','1073','1497','0');
INSERT INTO [t14299] VALUES (1685,'١٤٩٥ - حديث: "المرء على دين خليله فلينظر أحدكم من يخالل".','1073','1497','0');
INSERT INTO [t14299] VALUES (1686,'١٤٩٦ - حديث: "إذا سألتم الله فاسألوه الفردوس فإنه أوسط الجنة وأعلى الجنة وفوقه عرش الرحمن".','1073','1497','0');
INSERT INTO [t14299] VALUES (1687,'١٤٩٧ - حديث: "ما رأيت مثل النار نام لها ربها".','1073','1497','0');
INSERT INTO [t14299] VALUES (1688,'١٤٩٨ - حديث: "قال رجل: يا رسول الله إن فلانة تذكر من كثرة صلاتها وصيامها وصدقتها، غير أنها تؤذي جيرانها".','1074','1497','0');
INSERT INTO [t14299] VALUES (1689,'١٤٩٩ - حديث: "يا نساء المسلمات لا تحقرن جارة لجارته ولو فرسن شاة".','1074','1497','0');
INSERT INTO [t14299] VALUES (1690,'١٥٠٠ - حديث السهو: قوله: "صلى بنا رسول الله صلى الله عليه وسلم".','1077','1497','0');
INSERT INTO [t14299] VALUES (1691,'١٥٠١ - حديث: "لن ينجي أحدكم عمله، قالوا: ولا أنت يا رسول الله، قال: ولا أنا إلا أن يتغمدني الله برحمته".','1078','1497','0');
INSERT INTO [t14299] VALUES (1692,'١٥٠٢ - حديث: "إذا انتعل أحدكم فليبدأ باليمنى، وإذا نزع فليبدأ باليسرى، لتكون اليمنى أولهما تنعل وآخرهما تنزع".','1078','1497','0');
INSERT INTO [t14299] VALUES (1693,'١٥٠٣ - حديث: "لا يمشي أحدكم في نعل واحد، لينعلهما جميعا أو ليحفهما جميعا".','1078','1497','0');
INSERT INTO [t14299] VALUES (1694,'١٥٠٤ - حديث: "من أعتق رقبة مؤمنة أعتق الله بكل عضو منه عضوا من النار حتى يعتق فرجه بفرجه".','1079','1497','0');
INSERT INTO [t14299] VALUES (1695,'١٥٠٥ - حديث: "انتدب الله لمن خرج في سبيله لا يخرجه إلا إيمان بي وتصديق برسلي".','1079','1497','0');
INSERT INTO [t14299] VALUES (1696,'مسند أبي واقد الليثي رضي الله عنه','1081','0','0');
INSERT INTO [t14299] VALUES (1697,'١٥٠٦ - حديث: "يعمدون إلى أليات الغنم".','1081','1696','0');
INSERT INTO [t14299] VALUES (1698,'١٥٠٧ - حديث: "أنه صلى الله عليه وسلم قال لنسائه في حجته: هذه ثم ظهور الحصر".','1081','1696','0');
INSERT INTO [t14299] VALUES (1699,'مسند رجال لم يسموا رضي الله عنهم','1082','0','0');
INSERT INTO [t14299] VALUES (1700,'١٥٠٨ - حديث: "إن تبتم فليكن شعاركم حم لا ينصرون".','1082','1699','0');
INSERT INTO [t14299] VALUES (1701,'١٥٠٩ - حديث: "فإنه رب مبلغ أوعى من سامع".','1082','1699','0');
INSERT INTO [t14299] VALUES (1702,'١٥١٠ - حديث: "كيف رأيتم كرامة إخوانكم لكم، وضيافتهم إياكم قالوا: خير إخوان".','1083','1699','0');
INSERT INTO [t14299] VALUES (1703,'١٥١١ - حديث: " إنما هلك أهل الكتاب أنه لم يكن لصلاتهم فصل".','1083','1699','0');
INSERT INTO [t14299] VALUES (1704,'١٥١٢ - حديث " إذا كان أحدكم في صلاة، فلا يرفع بصره إلى السماء أن يلتمع بصره ".','1083','1699','0');
INSERT INTO [t14299] VALUES (1705,'١٥١٣ - حديث: "أسوأ السرقة الذي يسرق صلاته".','1084','1699','0');
INSERT INTO [t14299] VALUES (1706,'١٥١٤ - حديث: "إن العبد إذا سبقت له منزلة من الله لم يبلغها بعمله، ابتلاه الله حتى يبلغه المنزلة".','1084','1699','0');
INSERT INTO [t14299] VALUES (1707,'١٥١٥ - حديث: "إن الفراسي قال: يا رسول الله أسأل؟ قال: لا وإن كنت لا بد فاسأل الصالحين".','1084','1699','0');
INSERT INTO [t14299] VALUES (1708,'١٥١٦ - حديث: "ما رئي الشيطان يوما هو فيه أصغر ولا أحقر ولا أغيظ منه يوم عرفة".','1084','1699','0');
INSERT INTO [t14299] VALUES (1709,'١٥١٧ - حديث: "إما لا فأعني على نفسك بكثرة السجود".','1085','1699','0');
INSERT INTO [t14299] VALUES (1710,'١٥١٨ - حديث الرقية بالفاتحة، قوله: "فلعمري لمن أكل برقية باطل لقد أكلت برقية حق".','1086','1699','0');
INSERT INTO [t14299] VALUES (1711,'١٥١٩ - حديث: "دعوا الحبشة ما ودعوكم واتركوا الترك ما تركوكم".','1086','1699','0');
INSERT INTO [t14299] VALUES (1712,'مسند أسماء بنت أبي بكر الصديق رضي الله عنها','1087','0','0');
INSERT INTO [t14299] VALUES (1713,'١٥٢٠ - حديث: "ما من شيء لم أكن أريته إلا رأيته في منامي".','1087','1712','0');
INSERT INTO [t14299] VALUES (1714,'١٥٢١ - حديث: "جئت يوما والنوى على رأسي، فلقيت رسول الله صلى الله عليه وسلم ومعه نفر من الأنصار، فدعاني ثم قال: إخ إخ ليحملني خلفه".','1093','1712','0');
INSERT INTO [t14299] VALUES (1715,'١٥٢٢ - حديث: "ألا أخبركم بشراركم المشاؤون بالنميمة المفرقون بين الأحبة الباغين البرآء العنت".','1094','1712','0');
INSERT INTO [t14299] VALUES (1716,'١٥٢٣ - حديث: "أوحى الله إلي أنكم تفتنون في القبور قريبا أو مثل فتنة الدجال".','1094','1712','0');
INSERT INTO [t14299] VALUES (1717,'١٥٢٤ - حديث: "لا تحصي فيحصي الله عليك، ولا توعي فيوعي الله عليك".','1095','1712','0');
INSERT INTO [t14299] VALUES (1718,'مسند أسماء بنت عميس رضي الله عنها','1095','0','0');
INSERT INTO [t14299] VALUES (1719,'١٥٢٥ - حديث: "بئس العبد عبد طمع يقوده".','1095','1718','0');
INSERT INTO [t14299] VALUES (1720,'مسند حمنة بنت جحش رضي الله عنها','1095','0','0');
INSERT INTO [t14299] VALUES (1721,'١٥٢٦ - حديث المستحاضة: " فقال لها: سآمرك بأمرين أيهما فعلت ... ".','1096','1720','0');
INSERT INTO [t14299] VALUES (1722,'مسند خولة الأنصارية رضي الله عنها','1096','0','0');
INSERT INTO [t14299] VALUES (1723,'١٥٢٧ - حديث: "إن رجالا يتخوضون في مال الله بغير حق، فلهم النار يوم القيامة".','1096','1722','0');
INSERT INTO [t14299] VALUES (1724,'مسند الربيع بنت معوذ بن عفراء رضي الله عنها','1096','0','0');
INSERT INTO [t14299] VALUES (1725,'١٥٢٨ - حديث: "أتيت النبي صلى الله عليه وسلم بقناع به رطب وأجر زغب".','1096','1724','0');
INSERT INTO [t14299] VALUES (1726,'١٥٢٩ - حديث: "من كان أصبح صائما فليتم صومه".','1097','1724','0');
INSERT INTO [t14299] VALUES (1727,'مسند عائشة رضي الله عنها','1097','0','0');
INSERT INTO [t14299] VALUES (1728,'١٥٣٠ - حديث: "لا أحصي ثناء عليك أنت كما أثنيت على نفسك".','1097','1727','0');
INSERT INTO [t14299] VALUES (1729,'١٥٣١ - حديث:" إن امرأة قالت يا رسول الله، إن أمي افتلتت نفسها ".','1098','1727','0');
INSERT INTO [t14299] VALUES (1730,'١٥٣٢ - حديث: "الخراج بالضمان".','1099','1727','0');
INSERT INTO [t14299] VALUES (1731,'١٥٣٣ - حديث: "لم أعقل أبوي إلا وهما يدينان الدين".','1099','1727','0');
INSERT INTO [t14299] VALUES (1732,'١٥٣٤ - حديث: " نعم المرء كان عامر ".','1099','1727','0');
INSERT INTO [t14299] VALUES (1733,'١٥٣٥ - حديث: " نهى عن قتل جنان البيوت إلا الأبتر وذو الطفيتين".','1100','1727','0');
INSERT INTO [t14299] VALUES (1734,'١٥٣٦ - حديث: " إن أبا بكر رجل أسيف وإنه متى يقوم مقامك لا يسمع الناس ".','1101','1727','0');
INSERT INTO [t14299] VALUES (1735,'١٥٣٧ - حديث موت النبي صلى الله عليه وسلم: "فقال عمر: أو إنها لفي كتاب الله".','1101','1727','0');
INSERT INTO [t14299] VALUES (1736,'١٥٣٨ - حديث: " ما أكثر ما تذكرها حمراء الشدقين ".','1101','1727','0');
INSERT INTO [t14299] VALUES (1737,'١٥٣٩ - حديث: "فأسوة ما لك بنا ".','1102','1727','0');
INSERT INTO [t14299] VALUES (1738,'١٥٤٠ - حديث: "إني من أتقاهم وأداهم للأمانة".','1102','1727','0');
INSERT INTO [t14299] VALUES (1739,'١٥٤١ - حديث: "ستة لعنهم الله، وكل نبي مجاب الدعوة".','1103','1727','0');
INSERT INTO [t14299] VALUES (1740,'١٥٤٢ - حديث: "أنه صلى الله عليه وسلم خرج ليلة من جوف الليل، فصلى في المسجد رجال بصلاته فأصبح الناس فتحدثوا".','1104','1727','0');
INSERT INTO [t14299] VALUES (1741,'١٥٤٣ - حديث: "إن بعض أزواج النبي صلى الله عليه وسلم قلن: أينا أسرع بك لحوقا قال أطولكن يدا".','1104','1727','0');
INSERT INTO [t14299] VALUES (1742,'١٥٤٤ - حديث: "كان يعجبه التيمن في تنعله وترجله في شأنه كله".','1105','1727','0');
INSERT INTO [t14299] VALUES (1743,'١٥٤٥ - حديث: "إذا أنفقت المرأة من طعام بيتها غير مفسدة، كان لها أجرها بما أنفقت، ولزوجها والخازن مثل ذلك ولا ينقص بعضهم أجر بعض شيئا".','1109','1727','0');
INSERT INTO [t14299] VALUES (1744,'١٥٤٦ - حديث:" إذا نعس أحدكم وهو يصلي فليرقد حتى يذهب عنه النوم فإن أحدكم إذا صلى وهو ناعس لا يدري لعله يستغفر فيسب نفسه".','1110','1727','0');
INSERT INTO [t14299] VALUES (1745,'١٥٤٧ - حديث: "لولا قومك حديث عهدهم بكفر لزدت البيت على قواعد إبراهيم".','1111','1727','0');
INSERT INTO [t14299] VALUES (1746,'١٥٤٨ - حديث: " كان [؟ صلى] رسول الله صلى الله عليه وسلم وهو شاكي".','1117','1727','0');
INSERT INTO [t14299] VALUES (1747,'١٥٤٩ - حديث: "السواك مطهرة للفم".','1117','1727','0');
INSERT INTO [t14299] VALUES (1748,'١٥٥٠ - حديث: "إن أبا سفيان رجل شحيح، وإنه لا يعطيني ما يكفيني وبني، قال: خذي ما يكفيك وبنيك بالمعروف".','1118','1727','0');
INSERT INTO [t14299] VALUES (1749,'١٥٥١ - حديث: " ما من يوم أكثر من أن يعتق الله فيه عبدا من النار من يوم عرفة ".','1119','1727','0');
INSERT INTO [t14299] VALUES (1750,'١٥٥٢ - حديث: "كانت ضجعة رسول الله صلى الله عليه وسلم أدما حشوها ليف".','1119','1727','0');
INSERT INTO [t14299] VALUES (1751,'١٥٥٣ - حديث: "سبحانك اللهم وبحمدك".','1120','1727','0');
INSERT INTO [t14299] VALUES (1752,'١٥٥٤ - حديث: "وإنا وإياه في لحاف واحد".','1121','1727','0');
INSERT INTO [t14299] VALUES (1753,'١٥٥٥ - حديث: "استأذنته في دخول أبي القعيس، فقال: ائذني له فإنه عمج".','1121','1727','0');
INSERT INTO [t14299] VALUES (1754,'١٥٥٦ - حديث: "وما رأيته أكثر صياما منه في شعبان".','1122','1727','0');
INSERT INTO [t14299] VALUES (1755,'١٥٥٧ - حديث: "خلق الله كل إنسان من بني آدم على ستين وثلثمائة مفصل فمن كبر الله، وحمد الله، وهلل الله وسبح الله، عدد تلك الستين والثلاثمائة سلامى".','1122','1727','0');
INSERT INTO [t14299] VALUES (1756,'١٥٥٨ - حديث: "دعي رسول الله صلى الله عليه وسلم إلى جنازة صبي من الأنصار فقلت: طوبى له عصفور من عصافير الجنة، فقال رسول الله صلى الله عليه وسلم: أو غير ذلك".','1123','1727','0');
INSERT INTO [t14299] VALUES (1757,'١٥٥٩ - حديث: "لو أخذتم إهابها فدبغتموه".','1123','1727','0');
INSERT INTO [t14299] VALUES (1758,'١٥٦٠ - حديث: "كانوا يصلون العتمة فيما بين أن يغيب الشفق إلى ثلث الليل".','1123','1727','0');
INSERT INTO [t14299] VALUES (1759,'١٥٦١ - حديث: "أي الصلوات كانت أحب إلى رسول الله صلى الله عليه وسلم أن يواظب عليها".','1124','1727','0');
INSERT INTO [t14299] VALUES (1760,'١٥٦٢ - حديث: "فرضت علي الصلاة ركعتين ركعتين".','1124','1727','0');
INSERT INTO [t14299] VALUES (1761,'١٥٦٣ - حديث: "لا يصيب المؤمن شوكة فما فوقها".','1125','1727','0');
INSERT INTO [t14299] VALUES (1762,'١٥٦٤ - حديث: "إن رسول الله صلى الله عليه وسلم كان يصلي وهي بينه وبين القبلة على فراش أهله اعتراض الجنازة".','1125','1727','0');
INSERT INTO [t14299] VALUES (1763,'١٥٦٥ - حديث: "إن لي جارين، قالت أيهما أهدي، قال: أقربهما منك بابا".','1125','1727','0');
INSERT INTO [t14299] VALUES (1764,'١٥٦٦ - حديث: "فناداني ملك الجبال يا محمد: ذلك فيم شئت".','1126','1727','0');
INSERT INTO [t14299] VALUES (1765,'١٥٦٧ - حديث: " إن الله ليؤيد حسانا".','1126','1727','0');
INSERT INTO [t14299] VALUES (1766,'١٥٦٨ - حديث: "من اشترى من يهودي طعاما فأعطى درعا له رهنا".','1127','1727','0');
INSERT INTO [t14299] VALUES (1767,'١٥٦٩ - حديث: "فضل الصلاة بالسواك على الصلاة بغير سواك، سبعين صلاة".','1127','1727','0');
INSERT INTO [t14299] VALUES (1768,'١٥٧٠ - حديث: "دخلت العشر".','1127','1727','0');
INSERT INTO [t14299] VALUES (1769,'١٥٧١ - حديث بدء الوحي: قوله: "أول ما بدئ به رسول الله صلى الله عليه وسلم من الوحي الرؤيا الصالحة".','1127','1727','0');
INSERT INTO [t14299] VALUES (1770,'١٥٧٢ - حديث: "كنت أغتسل أنا والنبي صلى الله عليه وسلم من إناء واحد من جنابة".','1142','1727','0');
INSERT INTO [t14299] VALUES (1771,'١٥٧٣ - حديث: "إن هذا أمر كتبه الله على بنات آدم فاقضي ما يقضي الحاج غير أن لا تطوفي بالبيت".','1143','1727','0');
INSERT INTO [t14299] VALUES (1772,'١٥٧٤ - حديث: "كان يأمرني فأتزر".','1143','1727','0');
INSERT INTO [t14299] VALUES (1773,'١٥٧٥ - حديث: "ما يبكيك يا هنتاه".','1144','1727','0');
INSERT INTO [t14299] VALUES (1774,'١٥٧٦ - حديث: "كيف يأتيك الوحي".','1145','1727','0');
INSERT INTO [t14299] VALUES (1775,'١٥٧٧ - حديث: "كنت أغتسل أنا والنبي صلى الله عليه وسلم من إناء واحد، من قدح يقال له الفرق".','1146','1727','0');
INSERT INTO [t14299] VALUES (1776,'١٥٧٨ - حديث: "أهريقوا علي الماء من سبع قرب".','1147','1727','0');
INSERT INTO [t14299] VALUES (1777,'١٥٧٩ - حديث: "عقرى حلقى".','1148','1727','0');
INSERT INTO [t14299] VALUES (1778,'١٥٨٠ - حديث: "بئسما عدلتمونا بالكلب والحمار، لقد رأيتني ورسول الله صلى الله عليه وسلم يصلي وأنا مضطجعة بينه وبين القبلة".','1149','1727','0');
INSERT INTO [t14299] VALUES (1779,'١٥٨١ - حديث: "شبهتمونا بالحمر والكلاب".','1150','1727','0');
INSERT INTO [t14299] VALUES (1780,'١٥٨٢ - حديث: "إني لأعلم إذا كنت علي راضية وإذا كنت علي غضبى".','1151','1727','0');
INSERT INTO [t14299] VALUES (1781,'١٥٨٣ - حديث: "لا صلاة بحضرة طعام ولا وهو يدافعه الأخبثان".','1151','1727','0');
INSERT INTO [t14299] VALUES (1782,'١٥٨٤ - حديث: "كن نساء المؤمنات يشهدن مع رسول الله صلى الله عليه وسلم صلاة الفجر".','1152','1727','0');
INSERT INTO [t14299] VALUES (1783,'١٥٨٥ - حديث: "أنه صلى الله عليه وسلم أراد أن يعتكف، فلما انصرف فإذا أخبية خباء عائشة وخباء حفصة وخباء زينب، فقال: ألبر تقولون بهن".','1153','1727','0');
INSERT INTO [t14299] VALUES (1784,'١٥٨٦ - حديث: "أنها سئلت: ما كان النبي صلى الله عليه وسلم يصنع في بيته، قالت: كان يكون','1153','1727','0');
INSERT INTO [t14299] VALUES (1785,'١٥٨٧ - حديث: "صنع رسول الله صلى الله عليه وسلم شيئا فترخص فيه فتنزه عنه قوم فقال: ما بال قوم يتنزهون عن الشيء أصنعه".','1154','1727','0');
INSERT INTO [t14299] VALUES (1786,'١٥٨٨ - حديث: "لم يكن النبي صلى الله عليه وسلم على شيء من النوافل أشد تعهدا من ركعتي الفجر".','1155','1727','0');
INSERT INTO [t14299] VALUES (1787,'١٥٨٩ - حديث: "أنه قال: ضعوني ماء في المخضب".','1155','1727','0');
INSERT INTO [t14299] VALUES (1788,'١٥٩٠ - حديث: "كان إذا سمع المؤذن يتشهد قال: وأنا وأنا".','1156','1727','0');
INSERT INTO [t14299] VALUES (1789,'١٥٩١ - حديث: "كان الناس ينتابون الجمعة من منازلهم والعوالي، فقال النبي صلى الله عليه وسلم: لو أنكم تطهرتم ليومكم هذا".','1156','1727','0');
INSERT INTO [t14299] VALUES (1790,'١٥٩٢ - حديث: "كان يصلي جالسا فيقرأ وهو جالس، فإذا بقي من قراءته نحوا كذا".','1156','1727','0');
INSERT INTO [t14299] VALUES (1791,'١٥٩٣ - حديث: "أنه صلى الله عليه وسلم دخل إلى المسجد فأوجعه البرد، فقال: ادن مني، قلت: إني حائض، فقال: وإن".','1158','1727','0');
INSERT INTO [t14299] VALUES (1792,'١٥٩٤ - حديث: "دخل علي النبي صلى الله عليه وسلم وعندي جاريتان تغنيان بغناء بعاث، ودخل أبو بكر فانتهرني، وقال: مزمارة الشيطان عند النبي صلى الله عليه وسلم".','1159','1727','0');
INSERT INTO [t14299] VALUES (1793,'١٥٩٥ - حديث: "إني امرأة أستحاض".','1160','1727','0');
INSERT INTO [t14299] VALUES (1794,'١٥٩٦ - حديث: "اللهم صيبا نافعا".','1160','1727','0');
INSERT INTO [t14299] VALUES (1795,'١٥٩٧ - حديث: "والله ما من أحد أغير من الله أن يزني عبده".','1161','1727','0');
INSERT INTO [t14299] VALUES (1796,'١٥٩٨ - حديث: "أنها سألت النبي صلى الله عليه وسلم أيعذب الناس في قبورهم فقال: عائذا بالله من ذلك".','1161','1727','0');
INSERT INTO [t14299] VALUES (1797,'١٥٩٩ - حديث: "الصلاة أول ما فرضت ركعتان".','1162','1727','0');
INSERT INTO [t14299] VALUES (1798,'١٦٠٠ - حديث: "أنه صلى ركعتين بين النداءين ولم يكن يدعهما أبدا".','1162','1727','0');
INSERT INTO [t14299] VALUES (1799,'١٦٠١ - حديث: "هو لها صدقة".','1163','1727','0');
INSERT INTO [t14299] VALUES (1800,'١٦٠٢ - حديث: "من كل الليل أوتر رسول الله صلى الله عليه وسلم من أوله وأوسطه وآخره".','1163','1727','0');
INSERT INTO [t14299] VALUES (1801,'١٦٠٣ - حديث: "فقال هذا مكان عمرتك".','1164','1727','0');
INSERT INTO [t14299] VALUES (1802,'١٦٠٤ - حديث: "أمر بكبش أقرن يطأ في سواد وينظر في سواد".','1164','1727','0');
INSERT INTO [t14299] VALUES (1803,'١٦٠٥ - حديث: "أن النبي صلى الله عليه وسلم دخل عام الفتح من كدا وخرج من كدا".','1164','1727','0');
INSERT INTO [t14299] VALUES (1804,'١٦٠٦ - حديث: "إنها قالت يا رسول الله: وأفضل العمل أفلا نجاهد؟ قال: لكن أفضل الجهاد حج مبرور".','1165','1727','0');
INSERT INTO [t14299] VALUES (1805,'١٦٠٧ - حديث: "إني ذاكر لك أمرا ولا عليك أن لا تعجلي حتى تستأمري أبويك".','1166','1727','0');
INSERT INTO [t14299] VALUES (1806,'١٦٠٨ - حديث: "الصلاة جامعة".','1166','1727','0');
INSERT INTO [t14299] VALUES (1807,'١٦٠٩ - حديث: "أما بعد، ما بال أناس يشترطون شروطا ليست في كتاب الله".','1166','1727','0');
INSERT INTO [t14299] VALUES (1808,'١٦١٠ - حديث الإفك: "قوله: يا رسول الله أهلك ولا نعلم إلا خيرا".','1168','1727','0');
INSERT INTO [t14299] VALUES (1809,'١٦١١ - حديث: "فإن الله لا يمل حتى تملوا".','1169','1727','0');
INSERT INTO [t14299] VALUES (1810,'١٦١٢ - حديث الهجرة: "قولها: لقل يوم كان يأتي على النبي صلى الله عليه وسلم لا يأتي فيه بيت أبي بكر".','1169','1727','0');
INSERT INTO [t14299] VALUES (1811,'١٦١٣ - حديث: "لقد رأيتنا مع رسول الله صلى الله عليه وسلم وما لنا طعام إلا الأسودان".','1170','1727','0');
INSERT INTO [t14299] VALUES (1812,'١٦١٤ - حديث: "ما رأيت أحدا الوجع عليه أشد من رسول الله صلى الله عليه وسلم".','1173','1727','0');
INSERT INTO [t14299] VALUES (1813,'١٦١٥ - حديث: "ليس من أحد يقع الطاعون فيمكث في بلده صابرا محتسبا يعلم أنه لا يصيبه إلا ما كتب له إلا ما كان".','1173','1727','0');
INSERT INTO [t14299] VALUES (1814,'١٦١٦ - حديث: "سئل النبي صلى الله عليه وسلم بما أفضلت الحمر، فقال: وبما أفضلت السباع". رواه الدارقطني.','1174','1727','0');
INSERT INTO [t14299] VALUES (1815,'١٦١٧ - حديث: "إن أم حبيبة وأم سلمة ذكرتا كنيسة رأينها".','1174','1727','0');
INSERT INTO [t14299] VALUES (1816,'١٦١٨ - حديث: "فإن خلق رسول الله صلى الله عليه وسلم كان القرآن".','1174','1727','0');
INSERT INTO [t14299] VALUES (1817,'١٦١٩ - حديث: "لخلوف فم الصائم"','1175','1727','0');
INSERT INTO [t14299] VALUES (1818,'١٦٢٠ - حديث: "تقطع اليد في ربع دينار فصاعدا".','1175','1727','0');
INSERT INTO [t14299] VALUES (1819,'١٦٢١ - حديث: "كان رسول الله صلى الله عليه وسلم كلما كان ليلتها يخرج من آخر الليل إلى البقيع، فيقول: السلام عليكم دار قوم مؤمنين وأتاكم ما توعدون غدا مؤجلون".','1176','1727','0');
INSERT INTO [t14299] VALUES (1820,'١٦٢٢ - حديث: "كنت أدخل البيت فأضع ثوبي وأقول إنما هو زوجي".','1176','1727','0');
INSERT INTO [t14299] VALUES (1821,'١٦٢٣ - حديث: "أنها رأت أرادت أن تعتق مملوكين لها زوج".','1176','1727','0');
INSERT INTO [t14299] VALUES (1822,'١٦٢٤ - حديث: "كان صداقه صلى الله عليه وسلم لأزواجه ثنتي عشرة أوقية ونش".','1177','1727','0');
INSERT INTO [t14299] VALUES (1823,'١٦٢٥ - حديث: "أقيلوا ذوي الهيئات عثراتهم إلا الحدود".','1177','1727','0');
INSERT INTO [t14299] VALUES (1824,'١٦٢٦ - حديث: "ادرءوا الحدود عن المسلمين ما استطعتم، فإن الإمام إن يخطئ في العفو خير من أن يخطئ في العقوبة".','1177','1727','0');
INSERT INTO [t14299] VALUES (1825,'١٦٢٧ - حديث: "أنهم قالوا لابن الدغنة، مر أبا بكر فليعبد ربه في بيته".','1177','1727','0');
INSERT INTO [t14299] VALUES (1826,'١٦٢٨ - حديث: "اللهم من ولي من أمر أمتي شيئا فشق عليهم".','1178','1727','0');
INSERT INTO [t14299] VALUES (1827,'١٦٢٩ - حديث: "إذا أراد الله بالأمير خيرا جعل له وزير صدق".','1178','1727','0');
INSERT INTO [t14299] VALUES (1828,'١٦٣٠ - حديث: "من أعمر أرضا ليست لأحد فهو أحق".','1178','1727','0');
INSERT INTO [t14299] VALUES (1829,'١٦٣١ - حديث: "كان يكون علي الصوم من رمضان، فما أستطيع أن أقضيه إلا في شعبان، من الشغل برسول الله صلى الله عليه وسلم".','1179','1727','0');
INSERT INTO [t14299] VALUES (1830,'١٦٣٢ - حديث أم زرع: "قولها: زوجي لحم جمل غث".','1179','1727','0');
INSERT INTO [t14299] VALUES (1831,'١٦٣٣ - حديث: "ما من مصيبة تصيب المسلم إلا كفر الله بها عنه حتى الشوكة يشاكها".','1181','1727','0');
INSERT INTO [t14299] VALUES (1832,'١٦٣٤ - حديث: "ليأتين على القاضي العدل يوم القيامة يتمنى أنه لم يقض بين اثنين في تمرة".','1182','1727','0');
INSERT INTO [t14299] VALUES (1833,'١٦٣٥ - حديث: "كيف تجدك".','1182','1727','0');
INSERT INTO [t14299] VALUES (1834,'١٦٣٦ - حديث: "بل أنا وارأساه".','1183','1727','0');
INSERT INTO [t14299] VALUES (1835,'١٦٣٧ - حديث: "كان إذا اشتكى نفث على نفسه بالمعوذات".','1183','1727','0');
INSERT INTO [t14299] VALUES (1836,'١٦٣٨ - حديث: "أذهب الباس رب الناس".','1183','1727','0');
INSERT INTO [t14299] VALUES (1837,'١٦٣٩ - حديث: "من يلي من هذه البنات شيئا فأحسن إليهن كن له سترا من النار".','1184','1727','0');
INSERT INTO [t14299] VALUES (1838,'١٦٤٠ - حديث: "إذا أكل أحدكم فنسي أن يذكر الله في طعامه، فليقل باسم الله أوله وآخره".','1184','1727','0');
INSERT INTO [t14299] VALUES (1839,'١٦٤١ - حديث: "جاء أعرابي إلى النبي صلى الله عليه وسلم فقال: تقبلون الصبيان فما نقبلهم، فقال: أو أملك لك أن نزع الله من قلبك الرحمة".','1184','1727','0');
INSERT INTO [t14299] VALUES (1840,'١٦٤٢ - حديث: "الملائكة تحدث في العنان الأمر يكون في الأرض، فتستمع','1184','1727','0');
INSERT INTO [t14299] VALUES (1841,'١٦٤٣ - حديث: "كان يأتي علينا الشهر ما توقد فيه نار إلا أن نؤتى".','1185','1727','0');
INSERT INTO [t14299] VALUES (1842,'١٦٤٤ - حديث: "شخص بصر النبي صلى الله عليه وسلم ثم قال: في الرفيق الأعلى".','1186','1727','0');
INSERT INTO [t14299] VALUES (1843,'١٦٤٥ - حديث: "فهذا أوان وجدت انقطاع أبهري".','1186','1727','0');
INSERT INTO [t14299] VALUES (1844,'١٦٤٦ - حديث: "ما رأيت النبي صلى الله عليه وسلم مستجمعا ضاحكا".','1186','1727','0');
INSERT INTO [t14299] VALUES (1845,'١٦٤٧ - حديث: "كنت أغتسل أنا والنبي صلى الله عليه وسلم من إناء واحد ونحن جنبان".','1187','1727','0');
INSERT INTO [t14299] VALUES (1846,'١٦٤٨ - حديث:" الدنيا دار من لا دار له، ولها يجمع من لا عقل له".','1187','1727','0');
INSERT INTO [t14299] VALUES (1847,'١٦٤٩ - حديث: "إياك ومحقرات الذنوب فإن لها من الله طالبا".','1187','1727','0');
INSERT INTO [t14299] VALUES (1848,'١٦٥٠ - حديث: "كان يذكر الله على كل حياته".','1187','1727','0');
INSERT INTO [t14299] VALUES (1849,'١٦٥١ - حديث: "إن أول ما يكفأ كما يكفأ الإناء الخمر".','1188','1727','0');
INSERT INTO [t14299] VALUES (1850,'١٦٥٢ - حديث: "لا يجوز شهادة خائن ولا خائنة ولا القانع لأهل البيت".','1188','1727','0');
INSERT INTO [t14299] VALUES (1851,'١٦٥٣ - حديث: "مثل الذي يقرأ القرآن وهو حافظ له مع السفرة الكرام".','1188','1727','0');
INSERT INTO [t14299] VALUES (1852,'١٦٥٤ - حديث: "كان إذا اشتكى إنسان الشيء منه أو كانت به قرحة قال بأصبعه باسم الله بتربة أرضنا بريقة بعضنا ليشفي سقيمنا".','1188','1727','0');
INSERT INTO [t14299] VALUES (1853,'١٦٥٥ - حديث: "عشر من الفطرة: قص الشارب ... " الحديث.','1189','1727','0');
INSERT INTO [t14299] VALUES (1854,'١٦٥٦ - حديث: "سبوح قدوس".','1189','1727','0');
INSERT INTO [t14299] VALUES (1855,'١٦٥٧ - حديث: "كان إذا خرج من الخلاء قال: غفرانك".','1190','1727','0');
INSERT INTO [t14299] VALUES (1856,'١٦٥٨ - حديث: "فأشار أن لا تلدوني قلنا: كراهية المريض الدواء".','1190','1727','0');
INSERT INTO [t14299] VALUES (1857,'١٦٥٩ - حديث: "زوجي كليل تهامة، لا حر ولا قر ولا مخافة ولا سآمة".','1190','1727','0');
INSERT INTO [t14299] VALUES (1858,'١٦٦٠ - حديث عاشوراء: "فلما نزل رمضان، كان هو الفريضة".','1191','1727','0');
INSERT INTO [t14299] VALUES (1859,'١٦٦١ - حديث: "إن في العجوة العالية شفاء وإنها ترياق أول البكرة".','1191','1727','0');
INSERT INTO [t14299] VALUES (1860,'١٦٦٢ - حديث: "ادعوا لي بعض رفقائي قلت: أبو بكر قال: لا، قلت: ابن عمك عليا قال: لا".','1192','1727','0');
INSERT INTO [t14299] VALUES (1861,'١٦٦٣ - حديث: "فلما سمع أبو بكر حسه ذهب ليتأخر فأومأ إليه رسول الله صلى الله عليه وسلم أن قم كما أنت".','1194','1727','0');
INSERT INTO [t14299] VALUES (1862,'١٦٦٤ - حديث: "كان رجل يدخل على أزواج النبي صلى الله عليه وسلم مخنث".','1195','1727','0');
INSERT INTO [t14299] VALUES (1863,'١٦٦٥ - حديث: "ذاك جبريل".','1195','1727','0');
INSERT INTO [t14299] VALUES (1864,'١٦٦٦ - حديث: "لقد أذكرني كذا وكذا آية كنت أسقطتها".','1195','1727','0');
INSERT INTO [t14299] VALUES (1865,'١٦٦٧ - حديث: "يحشر الناس يوم القيامة حفاة عراة غرلا، قلت: يا رسول الله','1195','1727','0');
INSERT INTO [t14299] VALUES (1866,'١٦٦٨ - حديث: "إذا ذهب أحدكم إلى الغائط فليذهب معه بثلاثة أحجار يستطيب بها".','1196','1727','0');
INSERT INTO [t14299] VALUES (1867,'١٦٦٩ - حديث: "كان لا يرقد فيستيقظ إلا تسوك".','1196','1727','0');
INSERT INTO [t14299] VALUES (1868,'١٦٧٠ - حديث: "إنما كان يجزئك أن رأيته أن تغسل مكانه".','1196','1727','0');
INSERT INTO [t14299] VALUES (1869,'١٦٧١ - حديث: "كان يغتسل من أربع".','1197','1727','0');
INSERT INTO [t14299] VALUES (1870,'١٦٧٢ - حديث: "ما غرت على امرأة ما غرت على خديجة".','1197','1727','0');
INSERT INTO [t14299] VALUES (1871,'مسند ميمونة رضي الله عنها','1197','0','0');
INSERT INTO [t14299] VALUES (1872,'١٦٧٣ - حديث: "ما لك شعثا رأسك".','1197','1871','0');
INSERT INTO [t14299] VALUES (1873,'مسند أم جندب الأزدية رضي الله عنها','1198','0','0');
INSERT INTO [t14299] VALUES (1874,'١٦٧٤ - حديث: "قلت يا رسول الله إن ابني هذا ذاهب العقل فادع له، قال ائتني بماء".','1198','1873','0');
INSERT INTO [t14299] VALUES (1875,'مسند أم حبيبة رضي الله عنها','1199','0','0');
INSERT INTO [t14299] VALUES (1876,'١٦٧٥ - حديث: "إني كنت عن هذا لغنية".','1199','1875','0');
INSERT INTO [t14299] VALUES (1877,'١٦٧٦ - حديث: "فلا تعرضن علي بناتكن ولا أخواتكن".','1200','1875','0');
INSERT INTO [t14299] VALUES (1878,'مسند أم سلمة رضي الله عنها','1200','0','0');
INSERT INTO [t14299] VALUES (1879,'١٦٧٧ - حديث: "أن امرأة كانت تهراق الدماء على عهد رسول الله صلى الله عليه وسلم".','1200','1878','0');
INSERT INTO [t14299] VALUES (1880,'١٦٧٨ - حديث: "فإذا هي قراءة مفسرة حرفا حرفا".','1202','1878','0');
INSERT INTO [t14299] VALUES (1881,'١٦٧٩ - حديث: "فازدادت عليهم كرامة".','1202','1878','0');
INSERT INTO [t14299] VALUES (1882,'١٦٨٠ - حديث: "ذيول النساء شبر. قلت: إذن تبدو أقدامهن، قال: فذراعا".','1202','1878','0');
INSERT INTO [t14299] VALUES (1883,'١٦٨١ - حديث: "إنما يكفيك أن تحثين على رأسك ثلاث حثيات من ماء ثم تفيضي على جسدك".','1203','1878','0');
INSERT INTO [t14299] VALUES (1884,'١٦٨٢ - حديث: "فبما يشبه الولد".','1203','1878','0');
INSERT INTO [t14299] VALUES (1885,'١٦٨٣ - حديث: "يا رب كاسيات في الدنيا عاريات في الآخرة".','1203','1878','0');
INSERT INTO [t14299] VALUES (1886,'١٦٨٤ - حديث: "أن امرأة قالت: يا رسول الله، إن ابنتي توفي عنها زوجها وقد اشتكت عينها".','1208','1878','0');
INSERT INTO [t14299] VALUES (1887,'١٦٨٥ - حديث: "الذي يشرب في آنية الفضة إنما يجرجر في جهنم بطنه نارا".','1208','1878','0');
INSERT INTO [t14299] VALUES (1888,'١٦٨٦ - حديث: "كان يأمرني أن أصوم ثلاثة أيام من كل شهر أولها الاثنين والخميس".','1209','1878','0');
INSERT INTO [t14299] VALUES (1889,'١٦٨٧ - حديث: "شكوت إلى رسول الله صلى الله عليه وسلم أني أشتكي".','1210','1878','0');
INSERT INTO [t14299] VALUES (1890,'١٦٨٨ - حديث: "لا يحرم من الرضاع إلا ما فتق الأمعاء من الثدي".','1210','1878','0');
INSERT INTO [t14299] VALUES (1891,'مسند أم شريك رضي الله عنها','1210','0','0');
INSERT INTO [t14299] VALUES (1892,'١٦٨٩ - حديث: "أمرنا أن نخرج الحيض يوم العيدين".','1210','1891','0');
INSERT INTO [t14299] VALUES (1893,'١٧٠٠ - حديث: "أنه صلى الله عليه وسلم دخل على عائشة فقال: أعندكم شيء؟ قالت: لا، إلا شيء بعثت به إلينا نسيبة".','1212','1891','0');
INSERT INTO [t14299] VALUES (1894,'١٧٠١ - حديث: "نهى النبي صلى الله عليه وسلم ولا تمس طيبا".','1212','1891','0');
INSERT INTO [t14299] VALUES (1895,'مسند أم عطية رضي الله عنها','1212','0','0');
INSERT INTO [t14299] VALUES (1896,'١٧٠٢ - حديث: "أن حفصة قالت لها: أسمعت رسول الله صلى الله عليه وسلم قالت: بأبي نعم".','1212','1895','0');
INSERT INTO [t14299] VALUES (1897,'١٧٠٣ - حديث: "لا تحد امرأة على ميت فوق ثلاث، إلا على زوج أربعة أشهر وعشرا".','1213','1895','0');
INSERT INTO [t14299] VALUES (1898,'مسند أم فروة رضي الله عنها','1213','0','0');
INSERT INTO [t14299] VALUES (1899,'١٧٠٤ - حديث: "أفضل الأعمال الصلاة لوقتها".','1213','1898','0');
INSERT INTO [t14299] VALUES (1900,'مسند أم قيس بنت محصن الأسدية رضي الله عنها','1214','0','0');
INSERT INTO [t14299] VALUES (1901,'١٧٠٥ - حديث: "أنها أتت النبي صلى الله عليه وسلم بابن لها قد أعلقت عليه من العذرة".','1214','1900','0');
INSERT INTO [t14299] VALUES (1902,'مسند أم كلثوم القرشية رضي الله عنها','1214','0','0');
INSERT INTO [t14299] VALUES (1903,'١٧٠٦ - حديث: "إني قد أهديت إلى النجاشي حلة وأواق من مسك".','1215','1902','0');
INSERT INTO [t14299] VALUES (1904,'١٧٠٧ - حديث: "ولم أسمعه يرخص في شيء مما يقول الناس كذب".','1215','1902','0');
INSERT INTO [t14299] VALUES (1905,'مسند أم معبد رضي الله عنها','1215','0','0');
INSERT INTO [t14299] VALUES (1906,'١٧٠٨ - حديث: "فإنك تعلم خائنة الأعين".','1215','1905','0');
INSERT INTO [t14299] VALUES (1907,'مسند أم هانئ رضي الله عنها','1216','0','0');
INSERT INTO [t14299] VALUES (1908,'١٧٠٩ - حديث: "دخل علينا رسول الله صلى الله عليه وسلم فقال: عندك شيء؟ فقلت: لا إلا كسر يابسة وخل".','1216','1907','0');
INSERT INTO [t14299] VALUES (1909,'مسند امرأة من غفار رضي الله عنها','1217','0','0');
INSERT INTO [t14299] VALUES (1910,'١٧١٠ - حديث: "فوالله لنزل رسول الله صلى الله عليه وسلم إلى الصبح".','1217','1909','0');
INSERT INTO [t14299] VALUES (1911,'أحاديث مرسلة لم يقف على صحابتها، ولا على أسانيدها، وآثار','1217','0','0');
INSERT INTO [t14299] VALUES (1912,'١٧١١ - حديث: "أنا أفصح العرب بيد أني من قريش".','1217','1911','0');
INSERT INTO [t14299] VALUES (1913,'١٧١٢ - حديث: "يحمل هذا العلم من كل خلف عدوله، ينفون عنه تحريف الغالين".','1217','1911','0');
INSERT INTO [t14299] VALUES (1914,'١٧١٣ - حديث: "الفكاهة مقودة إلى الأذى".','1218','1911','0');
INSERT INTO [t14299] VALUES (1915,'١٧١٤ - حديث: "قال ابن هشام في تذكرته: من ندبة المضاف ما رواه المبرد في كتابه متشابه القرآن: (أن النبي صلى الله عليه وسلم استسقى ربه عز وجل على المنبر وقال: يا أبا طالباه، لو رأيت ابن أخيك على المنبر إذ تقول','1218','1911','0');
INSERT INTO [t14299] VALUES (1916,'١٧١٥ - حديث: "قال ابن الشجري في أماليه: قال أبو عبيدة في حديث النبي صلى الله عليه وسلم: " إن المهاجرين قالوا: يا رسول الله، إن الأنصار قد فضلونا، إنهم آوونا وفعلوا بنا وفعلوا، فقال: ألستم تعرفون ذلك لهم؟ قالوا: بلى، قال: فإن ذلك".','1219','1911','0');
INSERT INTO [t14299] VALUES (1917,'١٧١٦ - حديث: "ظلم دون ظلم".','1220','1911','0');
INSERT INTO [t14299] VALUES (1918,'١٧١٧ - حديث: "نعم العبد صهيب لو لم يخف الله لم يعصه".','1220','1911','0');
INSERT INTO [t14299] VALUES (1919,'١٧١٨ - حديث: "قال ابن فلاح في المغني: "وأما قوله عليه الصلاة والسلام في صفة المهدي: إنه قرشي يمان ليس من ذوي ولا ذوو".','1221','1911','0');
INSERT INTO [t14299] VALUES (1920,'١٧١٩ - حديث: "المؤمنون هينون لينون، كالجمل الأنف".','1221','1911','0');
INSERT INTO [t14299] VALUES (1921,'١٧٢٠ - حديث: "وقال الزمخشري في المفصل: تقلب ألف ما الاستفهامية هاء، جاء في حديث أبي ذؤيب: قدمت المدينة ولأهلها ضجيج بالبكاء كضجيج الحجيج، أهلوا بالإحرام، فقلت: مه، فقيل: قد قبض رسول الله صلى الله عليه وسلم".','1221','1911','0');
INSERT INTO [t14299] VALUES (1922,'١٧٢١ - حديث: "قال ابن القواس في شرح الدرة: قد جاء بدل المضمر الغائب مثل قوله عليه السلام، لعمر رضي الله عنه: (لا ولكن انحرها إياها) ".','1222','1911','0');
INSERT INTO [t14299] VALUES (1923,'١٧٢٢ - حديث: "حزقة حزقة، ترق عين بقة".','1222','1911','0');
INSERT INTO [t14299] VALUES (1924,'١٧٢٣ - حديث: "اغزوا قزوين فإنه من أعلى أبواب الجنة".','1222','1911','0');
INSERT INTO [t14299] VALUES (1925,'١٧٢٤ - حديث: "ومن زنى مم بكر فاصفعوه".','1222','1911','0');
INSERT INTO [t14299] VALUES (1926,'١٧٢٥ - حديث: "قول عمر: أيما رجل بايع آخر فإنه لا يؤمر واحد منهما تغرة أن يقتلا".','1223','1911','0');
INSERT INTO [t14299] VALUES (1927,'١٧٢٦ - حديث: "إذا نشأت بحرية ثم تشاءمت فتلك عين غديقة".','1223','1911','0');
INSERT INTO [t14299] VALUES (1928,'١٧٢٧ - حديث: "قول عروة: أما إن جبريل نزل فصلى أمام رسول الله صلى الله عليه وسلم".','1223','1911','0');
INSERT INTO [t14299] VALUES (1929,'١٧٢٨ - حديث ابن أبي مليكة: "كاد الخيران يهلكا".','1224','1911','0');
INSERT INTO [t14299] VALUES (1930,'١٧٢٩ - حديث القاسم بن محمد: "كان في بريرة ثلاث سنن، أرادت عائشة أن تشتريها، فتعتقها، فقال أهلها: ولنا الولاء".','1224','1911','0');
INSERT INTO [t14299] VALUES (1931,'١٧٣٠ - حديث: قال ابن الأثير في النهاية في الحديث: "الحجامة على الريق فيها شفاء وبركة فمن احتجم فيوم الأحد والخميس كذباك".','1224','1911','0');
