INSERT INTO [t142] VALUES (13,'التعبير بـ (عن) قد يكون من الراوي نفسه، إلا أنه في الأعم الأغلب هو من التلميذ أو من دونه','13','12','0');
INSERT INTO [t142] VALUES (14,'(عن) قد تكون مبدلة من صيغة صريحة في السماع أو صريحة في الانقطاع أو محتملة للأمرين، وقد تكون هي أصل الرواية، وذكر ما يدل على ذلك كله من نصوص الأئمة','15','12','0');
INSERT INTO [t142] VALUES (15,'ذكر بعض من حكي عنهم اشتراط التصريح بالتحديث للحكم بالاتصال ومناقشة قولهم','20','12','0');
INSERT INTO [t142] VALUES (16,'المبحث الثاني: الرواية عن الشخص والرواية لقصته','27','7','0');
INSERT INTO [t142] VALUES (17,'ذكر بعض الصيغ الدالة على قصد الرواية لقصة الشخص','27','16','0');
INSERT INTO [t142] VALUES (18,'بيان وجه تفريق أحمد ويعقوب بن شيبة والبرديجي بين (عن) و (أن) وغلط من غلط عليهم في ذلك','28','16','0');
INSERT INTO [t142] VALUES (19,'استخدام (عن) في حكاية قصة الشخص لا الرواية عنه، وسبل معرفة ذلك مع ذكر الأمثلة عليه','32','16','0');
INSERT INTO [t142] VALUES (20,'القاعدة في هذا المبحث: أن كل ما تبين به من الصيغ قصد الرواية عن الشخص فإنه يبحث في اتصال الإسناد وانقطاعه بين الراويين، وكل ما تبين به قصد حكاية قصة الشخص فإنه لا يبحث في الاتصال والانقطاع بينهما','37','16','0');
INSERT INTO [t142] VALUES (21,'الإشارة إلى بعض أخطاء الباحثين في هذه المسألة من جهة عدم مراعاة القاعدة السابقة','39','16','0');
INSERT INTO [t142] VALUES (22,'الفصل الثاني: سماع الراوي ممن روى عنه','44','0','0');
INSERT INTO [t142] VALUES (23,'المبحث الأول: الطريق إلى معرفة سماع الراوي ممن روى عنه','45','22','0');
INSERT INTO [t142] VALUES (24,'الطريقة الأولى: النظر في ترجمة الراويين في كتب الجرح والتعديل للوقوف على أنه يروي عنه','45','23','0');
INSERT INTO [t142] VALUES (25,'مناقشة الاستدلال بعمل المزي في تهذيبه على إثبات السماع بذكره شيوخ الراوي ومن روى عنه، وذكر القدر الأسلم الذي يفيده عمل المزي هذا','45','24','0');
INSERT INTO [t142] VALUES (26,'جرت عادة الأئمة التعبير بالرواية عن الشخص ولا يريدون بذلك إثبات السماع، وكذا قولهم " حدث عن فلان " و " حدث عنه فلان "','48','24','0');
INSERT INTO [t142] VALUES (27,'الطريقة الثانية: كلام أئمة النقد في سماع بعض الرواة ممن رووا عنه نفيا وإثباتا','53','23','0');
INSERT INTO [t142] VALUES (28,'استفادة الباحث من الكلمات المجملة التي تأتي عن الأئمة حين يفقد النص عنهم في سماع راو من آخر أو نفيه','55','27','0');
INSERT INTO [t142] VALUES (29,'سلوك الباحث طريق التخريج على أقوال النقاد حين يعدم عنهم نصا مباشرا في سماع راو من آخر أو نفيه، ولهذه الطريقة صورتان','57','27','0');
INSERT INTO [t142] VALUES (30,'الصورة الأولى: أن يعرف أن راويا لم يدرك آخر ولم يسمع منه فمن باب أولى أن لا يدرك من مات قبله','57','29','0');
INSERT INTO [t142] VALUES (31,'الصورة الثانية: أن يعلم أن شخصا لم يدرك آخر فيستدل بذلك على أن من هو أصغر منه لم يدركه من باب أولى (عكس الصورة الأولى)','60','29','0');
INSERT INTO [t142] VALUES (32,'موقف الباحث في حالة إجماع النقاد أو اختلافهم في سماع راو من آخر أو نفيه','60','27','0');
