INSERT INTO [t142] VALUES (35,'بعض أهم القرائن المستخدمة في ترجيح أو نفي السماع بعد ترجيح الإدراك والمعاصرة','72','33','0');
INSERT INTO [t142] VALUES (36,'١ - اختلاف مكان الراويين ولا رحلة لأحدهما إلى مكان الآخر حال وجوده فيه','72','35','0');
INSERT INTO [t142] VALUES (37,'٢ - إدخال الواسطة','75','35','0');
INSERT INTO [t142] VALUES (38,'٣ - أن يكون الراوي كثير الإرسال، ويلتحق به من كان من بلد عرف أهله بكثرة الإرسال','79','35','0');
INSERT INTO [t142] VALUES (39,'٤ - أن يروي الراوي عن شخص أحاديث كثيرة ولا يذكر في واحد منها سماعا أو ما يدل عليه','80','35','0');
INSERT INTO [t142] VALUES (40,'ليس الغرض من جمع دلائل إثبات السماع أو نفيه وإبرازها للباحث إعادة النظر في أحكام الأئمة، وإنما هو الموازنة بين أقوالهم عند الاختلاف أو في حال عدم الوقوف على قول لهم','81','33','0');
INSERT INTO [t142] VALUES (41,'مثالان تطبيقيان لكيفية استخدام المتأخر لدلائل إثبات السماع ونفيه في الموازنة بين أقوال النقاد: (سماع محمد بن المنكدر من عائشة) و (سماع أبان بن عثمان بن عفان من أبيه)','82','40','0');
INSERT INTO [t142] VALUES (42,'مثالان آخران لاستخدام المتأخر تلك الدلائل في إعادة النظر في كلام النقاد: (سماع الحسن البصري من أبي هريرة) و (سماع الحسن البصري من الأسود بن سريع)','84','40','0');
INSERT INTO [t142] VALUES (43,'المبحث الثاني: اشتراط العلم بالسماع في الإسناد المعنعن','88','22','0');
INSERT INTO [t142] VALUES (44,'ما كتب بعد مسلم حول مسألة حكم عنعنة المعاصر يتلخص في أربعة أقسام','91','43','0');
INSERT INTO [t142] VALUES (45,'نصوص الأئمة الدالة على اشتراط العلم بالسماع، وهي في الجملة على أربعة أقسام','95','43','0');
INSERT INTO [t142] VALUES (46,'القسم الأول: التفتيش عن السماع مع غير المدلس حتى مع ثبوت سماعه ممن روى عنه','96','45','0');
INSERT INTO [t142] VALUES (47,'القسم الثاني: ما فيه إثبات السماع أو اللقي لوجود التصريح به، أو نفي ذلك لعدم وجوده','99','45','0');
INSERT INTO [t142] VALUES (48,'القسم الثالث: ما فيه إثبات إدراك الراوي من روى عنه مع نفي سماعه منه، وفوق ذلك أن يثبتوا رؤيته له أو دخوله عليه','101','45','0');
INSERT INTO [t142] VALUES (49,'القسم الرابع: ما جاء عنهم من نفي السماع دون النص على الإدراك لكن يعرف ذلك وأن اللقاء بينهما ممكن من ترجمتي الراويين','103','45','0');
INSERT INTO [t142] VALUES (50,'يمكن الاعتراض على النصوص السابقة من ثلاثة أوجه','105','43','0');
INSERT INTO [t142] VALUES (51,'الوجه الأول: أن ما يتعلق منها بنفي السماع فليس مرجعه أن السماع لم يرد بل لورود نفي السماع، والجواب عنه','105','50','0');
INSERT INTO [t142] VALUES (52,'الوجه الثاني: إقامة الأئمة الدليل على أن فلانا لم يسمع من فلان وعدم اكتفائهم بالقول: إن فلانا لم يثبت له السماع من فلان، والجواب عنه من وجهين','106','50','0');
INSERT INTO [t142] VALUES (53,'الوجه الثالث: إثبات النقاد في نصوص كثيرة عنهم السماع بالقرائن مع عدم وجود للتصريح، والجواب عنه من وجهين، مع ذكر أمثلة كثيرة يستدل بها على هذا الوجه الثالث ومعالجتها','114','50','0');
INSERT INTO [t142] VALUES (54,'استخدام الأئمة النقاد القرائن مع المدلسين حين لا يذكرون التصريح بالتحديث من شيوخهم دال على أن اكتفاؤهم بالقرائن مع غير المدلس في نصوص كثيرة عنهم لا يدل على عدم اشتراطهم العلم بالسماع، مع التنبيه على أن كثيرا من الباحثين يحشدون نصوصا قالها النقاد في المدلسين بغرض تأييد ما ذهبوا إليه من اكتفاء الأئمة بالقرائن في إثبات السماع','131','43','0');
INSERT INTO [t142] VALUES (55,'تنبيهات مهمة على مسائل أربعة','134','43','0');
INSERT INTO [t142] VALUES (56,'المسألة الأولى: مناقشة ابن القطان ومن تابعه في أن ابن المديني والبخاري إذا لم يعلما لقاء الراوي للآخر لا يقولان إنه منقطع، إنما يقولان: لم يثبت سماع فلان من فلان','134','55','0');
