INSERT INTO [t142] VALUES (63,'المبحث الأول: التدليس والإرسال','167','60','0');
INSERT INTO [t142] VALUES (64,'إذا أرسل الراوي عن شخص وروى عنه ما لم يسمعه منه فلا يخلو الأمر من ثلاث حالات','167','63','0');
INSERT INTO [t142] VALUES (65,'رواية الراوي عن من لم يدرك عصره ليست تدليسا، والإشارة إلى من أطلق عليها تدليسا','167','63','0');
INSERT INTO [t142] VALUES (66,'بحث مسألة دخول رواية الراوي عمن عاصره ولم يسمع منه في التدليس، ومناقشة قول ابن حجر في ذلك، والوقوف مع نسبة القول بعدم دخول رواية الراوي عمن عاصره ولم يسمع منه في التدليس لبعض النقاد في هذه المسألة (الشافعي والبزار والخطيب وابن القطان الفاسي)','170','63','0');
