INSERT INTO [t142] VALUES (68,'من وصفه الأئمة بالتدليس عمن لم يسمع منه فلا يبحث عن تدليسه في ذلك الحديث بعينه','182','63','0');
INSERT INTO [t142] VALUES (69,'تحقيق القول في مسألة من ثبت ارتكابه لإحدى صورتي التدليس (رواية الراوي عمن عاصره ولم يسمع منه، ورواية الراوي عمن سمع منه) هل يعطى حكم المدلس في الصورة الأخرى؟','185','63','0');
INSERT INTO [t142] VALUES (70,'مناقشة بعض أدلة من فرق فذكر أن من كان يدلس عمن لم يسمع منه لم يؤثر تدليسه في روايته عمن سمع منه إذا لم يصرح','189','63','0');
INSERT INTO [t142] VALUES (71,'المبحث الثاني: التدليس وصورة التدليس','193','60','0');
INSERT INTO [t142] VALUES (72,'هل يصح للمتأخر إذا وقف على راو ورد عنه ارتكاب صورة التدليس وصفه بذلك وإن لم يفعله المتقدمون؟','193','71','0');
INSERT INTO [t142] VALUES (73,'اعتبار قد الإيهام في صنيع الراوي حتى يعد مدلسا مع ذكر الأمر الذي يفهم به قصد الإيهام','205','71','0');
INSERT INTO [t142] VALUES (74,'ألفاظ استعملها النقاد في وصف الراوي بالتدليس ليست بلفظ " التدليس " مع ذكر ما أداه جمود عدد من الباحثين على لفظ " التدليس " واشتراطه لوصف الراوي بذلك','211','71','0');
