INSERT INTO [t142] VALUES (79,'أمثلة لنصوص الرواة أنفسهم فيما سمعوا من شيوخهم وما لم يسمعوا بأنواعها المتعددة','225','78','0');
INSERT INTO [t142] VALUES (80,'أمثلة لنصوص النقاد في بيان ما سمعه المدلسون من شيوخهم وما لم يسمعوه بأنواعها المتعددة','232','78','0');
INSERT INTO [t142] VALUES (81,'سبب اختلاف الأئمة في عد ما سمعه الراوي المدلس من شيخه الذي لقيه','238','78','0');
INSERT INTO [t142] VALUES (82,'طريقة استفادة الباحث من كلام الأئمة في حالة النص على أحاديث سمعها المدلس من شيخه، أو في حالة ذكر عددها من غير نص','245','78','0');
INSERT INTO [t142] VALUES (83,'المبحث الرابع: التدليس والتصريح بالتحديث','251','60','0');
INSERT INTO [t142] VALUES (84,'اتفاق أئمة الحديث على قبول رواية المدلس إذا صرح بالتحديث، وما يذكر من خلاف في هذه المسألة فهو عن غيرهم','251','83','0');
INSERT INTO [t142] VALUES (85,'أثر التدليس على حديث الروي وإن صرح بالتحديث','252','83','0');
INSERT INTO [t142] VALUES (86,'قد يرد في الرواية تصريح المدلس بالتحديث ومع ذلك لا يقبل لأسباب ترجع في جملتها إلى أربعة','255','83','0');
INSERT INTO [t142] VALUES (87,'السبب الأول: تعمد بعض المدلسين إخفاء تدليسهم، وأمثلة ذلك','255','86','0');
INSERT INTO [t142] VALUES (88,'السبب الثاني: أخطاء المدلسين أنفسهم، وأمثلة ذلك','258','86','0');
INSERT INTO [t142] VALUES (89,'السبب الثالث: تدليس التسوية، وأمثلة ذلك','260','86','0');
INSERT INTO [t142] VALUES (90,'السبب الرابع: أخطاء الرواة، وما يلتحق به من أخطاء النساخ والطباعة، وأمثلة ذلك','266','86','0');
INSERT INTO [t142] VALUES (91,'ذكر جماعة من الرواة نسب إليهم الخطأ على شيوخهم في ذكر التصريح','287','83','0');
INSERT INTO [t142] VALUES (92,'يقوم مقام التصريح بالتحديث رواية من عرف أنه لا يأخذ من شيوخه إلا ما كان مسموعا لهم عمن رووا عنه ومن هؤلاء: شعبة، والقطان، ومحمد بن فضيل، وعبدالرحمن بن مهدي، ويزيد بن زريع، وأبي نعيم الفضل بن دكين، وعفان بن مسلم','289','83','0');
INSERT INTO [t142] VALUES (93,'المبحث الخامس: رواية المدلس بصيغة محتملة للسماع','296','60','0');
INSERT INTO [t142] VALUES (94,'جملة ما يحكى من أقوال في حكم رواية المدلس إذا لم يصرح بالتحديث ثلاثة، مع مناقشتها، وتحقيق نسبة الأقوال إلى قائليها وبيان المراد منها','297','93','0');
INSERT INTO [t142] VALUES (95,'القول الأول: أن رواية المدلس الثقة محمولة على الاتصال ما لم يتبين أنه دلس في حديث بعينه','297','94','0');
INSERT INTO [t142] VALUES (96,'القول الثاني: أن رواية المدلس بصيغة محتملة محمولة على الانقطاع أبدا، ولا يحكم لروايته بالاتصال إلا إذا صرح، يستوي في ذلك المقل من التدليس والمكثر منه، ومن دلس مرة واحدة','301','94','0');
INSERT INTO [t142] VALUES (97,'القول الثالث: التفريق بين المقل من التدليس والمكثر منه، فالأول روايته محمولة على الاتصال، والثاني روايته محمولة على الانقطاع، وهو القول الراجح','307','94','0');
INSERT INTO [t142] VALUES (98,'يمكن رد ما خالف القول الثالث من أقوال نسبت لبعض الأئمة إليه فيكون إجماعا، وهو ما نقله غير واحد صراحة أو إشارة، كما أنه هو الموافق لما يظهر من عمل الأئمة مع المدلسين','308','93','0');
INSERT INTO [t142] VALUES (99,'لا فرق بين المتقدم والمتأخر في الحكم الذي تعامل به رواية المدلس إذا لم يصرح','315','93','0');
INSERT INTO [t142] VALUES (100,'تنبيهات وضوابط خمسة للنظر في التعامل مع المدلسين ورواياتهم إذا لم يصرحوا','320','93','0');
