INSERT INTO [t143157] VALUES (24,'المطلب الثاني: منهج الصيدلاني في اختياراته','35','22','0');
INSERT INTO [t143157] VALUES (25,'المطلب الثالث: مصادر اختيارات الصيدلاني','36','22','0');
INSERT INTO [t143157] VALUES (26,'المطلب الرابع: الألفاظ ذات العلاقة بالاختيار','37','22','0');
INSERT INTO [t143157] VALUES (27,'المطلب الخامس: كيفية الترجيح بين هذه الألفاظ','39','22','0');
INSERT INTO [t143157] VALUES (28,'الفصل الأول','40','0','0');
INSERT INTO [t143157] VALUES (29,'المبحث الأول: شروط عقد النكاح، وادعاء كل زوج سبقه بالنكاح','41','28','0');
INSERT INTO [t143157] VALUES (30,'المطلب الأول: شروط عقد النكاح','42','29','0');
INSERT INTO [t143157] VALUES (31,'المسألة الأولى: حكم اشتراط إذن السلطان في تولي الأب تزويج المجنونة البالغة الثيب.','43','30','0');
INSERT INTO [t143157] VALUES (32,'المسألة الثانية: إذا كان ولي المرأة السلطان أو من ينوبه؛ فإذا دعت المرأة إلى تزويجها ممن لا يكافئها، فهل يزوجها السلطان؟','47','30','0');
INSERT INTO [t143157] VALUES (33,'المسألة الثالثة: الوليان في نكاح المرأة إذا زوجاها باثنين، ولم يعلم السابق منهما.','48','30','0');
INSERT INTO [t143157] VALUES (34,'المسألة الرابعة: إذا قلنا بوجوب إنشاء الفسخ فيما إذا زوج الوليان المرأة باثنين، ولم يعلم السابق منهما، فهل يشترط أن يفسخ القاضي النكاح؟','48','30','0');
INSERT INTO [t143157] VALUES (35,'المطلب الثاني: اختلاف الزوجين','60','29','0');
INSERT INTO [t143157] VALUES (36,'المسألة الأولى: إذا تنازع زوجان، وادعى كل زوج سبقه، وأنها زوجته.','61','35','0');
INSERT INTO [t143157] VALUES (37,'المسألة الثانية: حكم النكاح فيما إذا اعترف الزوج بعلمه بفسق شاهدي النكاح حالة العقد، وأنكرت المرأة.','65','35','0');
INSERT INTO [t143157] VALUES (38,'المبحث الثاني: ما يحرم من النكاح، ونكاح العبيد والإماء، والصداق والوليمة، والقسم.','81','28','0');
INSERT INTO [t143157] VALUES (39,'المطلب الأول: ما يحرم من النكاح، ونكاح العبيد والإماء','82','38','0');
INSERT INTO [t143157] VALUES (40,'المسألة الأولى: أثر الملامسة في حرمة المصاهرة.','83','39','0');
INSERT INTO [t143157] VALUES (41,'المسألة الثانية: إذا نكح العبد بإذن سيده نكاحا صحيحا، ولكن فسد المهر، فهل يتعلق المهر بذمته، أم برقبته، أم بكسبه؟','94','39','0');
INSERT INTO [t143157] VALUES (42,'المسألة الثالثة: حكم نكاح الأمة فيما لو لم يجد طول حرة، ولكنه صادف حرة تسمح ببعض المهر، أو ترضى بمقدار من المهر؛ وثقلت عليه المنة.','98','39','0');
INSERT INTO [t143157] VALUES (43,'المطلب الثاني: الصداق والوليمة','102','38','0');
INSERT INTO [t143157] VALUES (44,'المسألة الأولى: حكم الصداق فيما لو أصدقها بلفظ فاسد كقوله "أصدقتها هذا الحر"، أو قال: "أصدقتك هذه الخمر".','103','43','0');
INSERT INTO [t143157] VALUES (45,'المسألة الثانية الحكم فيما إذا قال: "أصدقتك هذا العصير"، وكانت خمرا مشوبة بالماء.','108','43','0');
INSERT INTO [t143157] VALUES (46,'المسألة الثالثة: إذا طلق زوجته قبل الدخول والمسيس، وكان المهر عينا، وتلف الصداق في يد الزوجة بعد الطلاق، ولم تعتد.','108','43','0');
INSERT INTO [t143157] VALUES (47,'المسألة الرابعة: حكم دخول البيت الذي فيه الصور الممنوعة.','109','43','0');
INSERT INTO [t143157] VALUES (48,'المطلب الثالث: القسم','114','38','0');
INSERT INTO [t143157] VALUES (49,'المسألة الأولى: إذا اجتمعت الرقيقة والحرة تحت حر، وكان القسم ليلتين للحرة وليلة للأمة، فجرى العتق للأمة في الليلة الثانية، فأراد الزوج أن يفارق الحرة في بقية تلك الليلة ويبيت عند صاحب له، ثم يبيت عند العتيقة ليلة ويدور إلى الحرة بليلة ويدير النوبتين كذلك.','118','48','0');
INSERT INTO [t143157] VALUES (50,'المسألة الثانية: إذا قالت للزوج: وهبت نوبتي منك، فضعها حيث تشاء، وخصص بها من تشاء من زوجاتك الأخريات. فهل له تخصيص واحدة بنوبة الواهبة؟','121','48','0');
INSERT INTO [t143157] VALUES (51,'المسألة الثالثة: من سافر وأخذ إحدى زوجاته بالقرعة ثم بلغ البلد الذي يقصده، فلو نوى الإقامة أربعة أيام، فهل عليه قضاء الليالي لزوجاته الباقيات؟','125','48','0');
INSERT INTO [t143157] VALUES (52,'الفصل الثاني','129','0','0');
INSERT INTO [t143157] VALUES (53,'المبحث الأول: الخلع والطلاق والإيلاء، والظهار واللعان، والعدة','130','52','0');
INSERT INTO [t143157] VALUES (54,'المطلب الأول: الخلع','131','53','0');
INSERT INTO [t143157] VALUES (55,'المسألة الأولى: حكم انتظام الخلع فيما لو قال: "أنت طالق على ألف إن شئت".','132','54','0');
INSERT INTO [t143157] VALUES (56,'المسألة الثانية: طريقة توزيع المال إذا قال الرجل لامرأتيه -وكان يملك على كل واحدة ثلاث طلقات-: طلقتك يا زينب واحدة، وطلقتك يا عمرة اثنتين بألف درهم.','134','54','0');
INSERT INTO [t143157] VALUES (57,'المسألة الثالثة: لو قالت: طلقني ثلاثا بألف وهو يملك الثلاث فقال: أنت طالق واحدة بألف وثنتين مجانا.','137','54','0');
INSERT INTO [t143157] VALUES (58,'المسألة الرابعة: حكم الخلع فيما لو اختلعت نفسها عن زوجها بغير إذن مولاها، بمال في الذمة.','137','54','0');
INSERT INTO [t143157] VALUES (59,'المطلب الثاني: الطلاق، والإيلاء','138','53','0');
INSERT INTO [t143157] VALUES (60,'المسألة الأولى: لو قيل لرجل: طلقت امرأتك؟ فقال: نعم، طلقتها، ثم قال: إنما قلت ذلك على ظن أن اللفظ الذي جرى طلاق، وقد سألت المفتين فقالوا: لا يقع به شيء. وقالت المرأة: بل أردت إنشاء الطلاق أو الإقرار به.','139','59','0');
INSERT INTO [t143157] VALUES (61,'المسألة الثانية: الحكم فيما إذا قال لامرأته: متى وقع عليك طلاقي، أو إذا وقع عليك طلاقي فأنت طالق قبله ثلاثا، ثم قال لها: أنت طالق.','141','59','0');
INSERT INTO [t143157] VALUES (62,'المسألة الثالثة: إذا قال: أنت طالق لقدوم فلان، هل يقع الطلاق ناجزا، أو معلقا بقدومه؟','146','59','0');
INSERT INTO [t143157] VALUES (63,'المسألة الرابعة: لو قال: أنت طالق لقدوم زيد، وكان زيد غير قادم، وإنما ينتظر قدومه؛ ونحوها من الصيغ التي يظهر فيه قصد التأقيت ولا يكر كرور الزمان.','146','59','0');
INSERT INTO [t143157] VALUES (64,'المسألة الخامسة: لو قالت المرأة لزوجها: "أسقطت حقي من طلب الفيئة سنة"، وانقضت المدة، ورضيت بالمقام تحت الزوج، ثم عادت إلى الطلب بالاستمتاع، هل نضرب مدة أخرى أربعة أشهر؟','148','59','0');
INSERT INTO [t143157] VALUES (65,'المطلب الثالث: الظهار واللعان','151','53','0');
INSERT INTO [t143157] VALUES (66,'المسألة الأولى: المراد بالعود في الظهار إذا كان مؤقتا، وجعلنا العود بمعنى الوطء والوقاع.','152','65','0');
INSERT INTO [t143157] VALUES (67,'المسألة الثانية: المعتبر في قدر الكفارة (الكيل أو الوزن).','156','65','0');
INSERT INTO [t143157] VALUES (68,'المسألة الثالثة: حكم انتفاء الولد عمن لحقه ولد، وكان ممسوحا (فاقد الذكر والأنثيين).','158','65','0');
INSERT INTO [t143157] VALUES (69,'المطلب الرابع: العدة','163','53','0');
INSERT INTO [t143157] VALUES (70,'المسألة الأولى: حكم النكاح لكافر نكح في الشرك، ثم إن المرأة وطئت بشبهة بعد جريان النكاح، وجرت في العدة، فلحق الإسلام النكاح والمرأة في عدة الشبهة.','164','69','0');
INSERT INTO [t143157] VALUES (71,'المسألة الثانية: حكم النكاح فيمن نكح معتدة غيره، وأسلم أحدهما والعدة أو المدة باقية، ثم أسلم الآخر وقد انقضت العدة.','170','69','0');
INSERT INTO [t143157] VALUES (72,'المبحث الثاني: الحضانة، والنفقات','176','52','0');
INSERT INTO [t143157] VALUES (73,'المطلب الأول: الرضاع والحضانة','177','72','0');
INSERT INTO [t143157] VALUES (74,'المسألة الأولى: حكم تصريح الشاهد في الرضاع بوصول اللبن إلى الجوف.','178','73','0');
INSERT INTO [t143157] VALUES (75,'المسألة الثانية: حق الحضانة فيما لو امتنعت الأم عن الحضانة.','178','73','0');
INSERT INTO [t143157] VALUES (76,'المسألة: فيمن يلي الأم مباشرة في الحضانة فيما لو امتنعت عن الحضانة.','178','75','0');
INSERT INTO [t143157] VALUES (77,'المسألة الثالثة: من أحق بالولد في الحضانة، الخالة من الأم أم الخالة من الأب؟','180','73','0');
INSERT INTO [t143157] VALUES (78,'المطلب الثاني: النفقات','182','72','0');
INSERT INTO [t143157] VALUES (79,'المسألة الأولى: إذا احتاجت الخادمة إلى إزالة الوسخ عن نفسها، ولو لم تفعل ذلك، لتأذت بالهوام والوسخ، ثم تأذت المخدومة بها، فهل يجب على الزوج أن يكفي الخادمة؟','183','78','0');
INSERT INTO [t143157] VALUES (80,'المسألة الثانية: حكم سقوط حق المرأة لو أبرأت زوجها عن نفقة سنة مثلا، وانقضت المدة، ورضيت بالمقام تحت الزوج، ثم عادت إلى طلب النفقة.','185','78','0');
INSERT INTO [t143157] VALUES (81,'الفصل الثالث','186','0','0');
INSERT INTO [t143157] VALUES (82,'المبحث الأول: الجراح، ودعوى الدم والقسامة والديات','187','81','0');
INSERT INTO [t143157] VALUES (83,'المطلب الأول: الجراح، ودعوى الدم والقسامة','188','82','0');
INSERT INTO [t143157] VALUES (84,'المسألة الأولى: حكم القصاص فيما لو ألقى زيد رجلا في النار وكان من الممكن للرجل أن يتخطاها ويتعداها، فلبث حتى أحاط به الوهج واللفح.','189','83','0');
INSERT INTO [t143157] VALUES (85,'المسألة الثانية: نوع القتل فيما إذا رمى جماعة حجرا بالمنجنيق، وقصدوا شخصا أو جماعة بأعيانهم فأصابوا من قصدوه.','189','83','0');
INSERT INTO [t143157] VALUES (86,'المسألة الثالثة: لو سلمنا السيف إلى ولي القصاص في القتل، فضرب غير الرقبة؛ فلم يصبها السيف.','194','83','0');
INSERT INTO [t143157] VALUES (87,'المسألة الرابعة: الضمان فيما إذا قتل الجلاد رجلا بالجلد، وقد أخبر الإمام الجلاد بأنه ظالم، فتابعه الجلاد.','197','83','0');
INSERT INTO [t143157] VALUES (88,'المسألة الخامسة: حكم القسامة مع عدم وجود أثر القتل.','205','83','0');
INSERT INTO [t143157] VALUES (89,'المطلب الثاني: الديات','211','82','0');
INSERT INTO [t143157] VALUES (90,'المسألة الأولى: إن قال ولي الدم: "عفوت عن الدم على أن لا مال لي".','212','89','0');
INSERT INTO [t143157] VALUES (91,'المسألة الثانية: إن عفا المبذر عن الدية.','214','89','0');
INSERT INTO [t143157] VALUES (92,'المسألة الثالثة: إن قصر الزمان، ولم يمكن فيه الاندمال، فقال الوارث: مات بسبب آخر، ولم يمت بالجرح، ولم يذكر سببا مفصلا من سقطة وغيرها، فهل يقبل قوله؟','225','89','0');
INSERT INTO [t143157] VALUES (93,'المسألة الرابعة: إذا اختلف الجاني ومستحق الدم؛ فقال الجاني: مات بالسراية، أو قتلته أنا قبل الاندمال، وقال الولي: بل مات بسبب آخر، بأن قال: قتل نفسه، أو قتله آخر، أو شرب سما قاتلا، فإذا قلنا: يصدق الولي، فهل يلتفت إلى قوله إن قصر الزمان ولم يمكن فيه الاندمال؟','225','89','0');
INSERT INTO [t143157] VALUES (94,'المسألة الخامسة: إذا شج رأس إنسان موضحتين، فارتفع الحاجز بينهما، وقد طال الزمان، فقال المشجوج: رفعت الحاجز بعد الاندمال، واستقرار الأرشين، [وألزمك رفع الحاجز] أرشا ثالثا، وقال الشاج: رفعت الحاجز قبل الاندمال، وقد طال الزمان، هل يثبت الأرش الثالث الذي يتعلق برفع الحاجز بعد الاندمال؟','226','89','0');
INSERT INTO [t143157] VALUES (95,'المسألة السادسة: لو طعن رجلا في بطنه، فنفذ السنان من الظهر، على القول بأن من ضرب بطن إنسان بمشقص وأجافه في موضعين بينهما حائل في الاتصال، وجعلنا الطعنة النافذة جائفة واحدة، فهل في الزيادة حكومة؟ أم تجب الدية فقط؟','227','89','0');
INSERT INTO [t143157] VALUES (96,'المسألة السابعة: إذا اندملت الجراحة، وبقي في موضعها أثر وحواليها شين.','231','89','0');
INSERT INTO [t143157] VALUES (97,'المسألة الثامنة: وجوب الغرة فيما لو جني على كافرة حامل بجناية أفضت إلى قتلها، وكان الجنين محكوما له بالكفر والحرية.','231','89','0');
INSERT INTO [t143157] VALUES (98,'المبحث الثاني: الحدود','238','81','0');
INSERT INTO [t143157] VALUES (99,'المطلب الأول: حد الزنا، والقذف','239','98','0');
INSERT INTO [t143157] VALUES (100,'المسألة الأولى: لو حكم على زان بالجلد، وكان مريضا مرضا لا يرجى برؤه، أو كان ضعيفا بالخلقة لا يحتمل السياط، هل يجلد؟ وإن جلدها كيف يجلد؟','240','99','0');
INSERT INTO [t143157] VALUES (101,'المسألة الثانية: هل يشترط أن يكون السيد عالما عند إقامة الحد على مملوكه؟','250','99','0');
INSERT INTO [t143157] VALUES (102,'المسألة الثالثة: إقامة مالك الرق الحد على من نصفه حر ونصفه مملوك.','253','99','0');
INSERT INTO [t143157] VALUES (103,'المسألة الرابعة: إذا قال الرجل لزوجته: زنيت بك، أو قالت المرأة لزوجها: زنيت بك، فهل يكون قذفا؟','254','99','0');
INSERT INTO [t143157] VALUES (104,'المطلب الثاني: حد السرقة','255','98','0');
INSERT INTO [t143157] VALUES (105,'المسألة الأولى: حكم إقامة حد السرقة فيما لو اشترك رجلان في نقب جدار، ثم دخل أحدهما الحرز ووضع المتاع في وسط النقب، وأخذه الآخر من حيث وضعه الداخل.','256','104','0');
INSERT INTO [t143157] VALUES (106,'المسألة الثانية: إذا أقر بالسرقة الموجبة للقطع، ثم رجع عن إقراره.','260','104','0');
INSERT INTO [t143157] VALUES (107,'المسألة الثالثة: لو سرق عبد من مال ولد سيده أو والده، هل تقطع يده؟','269','104','0');
INSERT INTO [t143157] VALUES (108,'المطلب الثالث: حد قطاع الطريق','273','98','0');
INSERT INTO [t143157] VALUES (109,'المسألة الأولى: مدة الصلب فيما لو أخذ قاطع الطريق المال على وجه يوجب القطع، والقتل، فإذا صلب قتيلا، أو قتل مصلوبا، فكم يترك على الصليب؟','274','108','0');
INSERT INTO [t143157] VALUES (110,'المسألة الثانية: وقت غسله وكفنه والصلاة عليه إن قلنا: يترك على الصليب حتى يسيل ودكه.','283','108','0');
INSERT INTO [t143157] VALUES (111,'المسألة الثالثة: حكم قتل السيد المحارب عبده، إذا كان العبد في الرفقة.','284','108','0');
INSERT INTO [t143157] VALUES (112,'الفصل الرابع','288','0','0');
INSERT INTO [t143157] VALUES (113,'المبحث الأول: الجهاد والسير','289','112','0');
INSERT INTO [t143157] VALUES (114,'المطلب الأول: استئجار المجاهد','290','113','0');
INSERT INTO [t143157] VALUES (115,'المسألة الأولى: حكم استئجار السلطان من أراد استئجاره من المسلمين في الجهاد، وإعطاءهم الأجرة من سهم المصالح.','291','114','0');
INSERT INTO [t143157] VALUES (116,'المسألة الثانية: لو قهر الإمام طائفة من المسلمين، وأخرجهم إلى الجهاد، هل يستحقون أجر المثل؟','301','114','0');
INSERT INTO [t143157] VALUES (117,'المطلب الثاني: الغنائم، والتولي يوم الزحف، وحكم تحكيم الإمام مسلما على العدو','303','113','0');
INSERT INTO [t143157] VALUES (118,'المسألة الأولى: لو أخذ بعض الغانمين شيئا من الطعام، وأقرضه غانما آخر، فهل الإقراض يتعلق به حكم؟','304','117','0');
INSERT INTO [t143157] VALUES (119,'المسألة الثانية: لو أخذ بعض الغانمين شيئا من الطعام، وباعه غانما آخر، فهل البيع يتعلق به حكم؟','311','117','0');
INSERT INTO [t143157] VALUES (120,'المسألة الثالثة: الحكم فيما إذا أخذ الإمام من المقترض ما استقرضه من غنيمة غانم، وكانت العين باقية، وتفرق الجند وعسر فض وتوزيع مد من طعام على مائة ألف.','314','117','0');
INSERT INTO [t143157] VALUES (121,'المسألة الرابعة: طرق التحيز الجائزة في قوله تعالى: {ومن يولهم يومئذ دبره إلا متحرفا لقتال أو متحيزا إلى فئة فقد باء بغضب من الله ومأواه جهنم وبئس المصير}','317','117','0');
INSERT INTO [t143157] VALUES (122,'المسألة الخامسة: إذا حاصر صاحب الراية قلعة، فرضوا بأن ينزلهم على حكم رجل عينوه، فأنزلهم الإمام على حكم أمين عدل، وحكم المحكم بالقتل، فأراد الإمام أن يسترق.','321','117','0');
INSERT INTO [t143157] VALUES (123,'المبحث الثاني: الجزية، وما يجب على أهل الذمة وما يجب لهم، والأمان','323','112','0');
INSERT INTO [t143157] VALUES (124,'المطلب الأول: الجزية','324','123','0');
INSERT INTO [t143157] VALUES (125,'المسألة الأولى: الفرق بين منع الجزية والامتناع عنها.','325','124','0');
INSERT INTO [t143157] VALUES (126,'المسألة الثانية: نقض العهد بمنع الجزية.','325','124','0');
INSERT INTO [t143157] VALUES (127,'المطلب الثاني: فيما يجب على أهل الذمة وما يجب لهم','332','123','0');
INSERT INTO [t143157] VALUES (128,'المسألة الأولى: الحكم عند امتناع أهل الذمة عن إجراء أحكام الإسلام عليهم.','333','127','0');
INSERT INTO [t143157] VALUES (129,'المسألة الثانية: الحكم عند نصبهم القتال من غير شبهة.','335','127','0');
INSERT INTO [t143157] VALUES (130,'المسألة الثالثة: الحكم فيما لو زنى ذمي بمسلمة أو أصابها بنكاح، أو دل أهل الحرب على عورة للمسلمين، أو فتن مسلما عن دينه لم يجر شرط بانتقاض العهد بها.','341','127','0');
INSERT INTO [t143157] VALUES (131,'المسألة الأولى: لو زنى ذمي بمسلمة أو أصابها بنكاح.','341','130','0');
INSERT INTO [t143157] VALUES (132,'المسألة الثانية: الحكم فيما لو دل الذمي أهل الحرب على عورة للمسلمين.','344','130','0');
INSERT INTO [t143157] VALUES (133,'المسألة الثالثة: الحكم فيما لو فتن الذمي مسلما عن دينه، ولم يجر شرط بانتقاض العهد بها.','349','130','0');
INSERT INTO [t143157] VALUES (134,'المسألة الرابعة: حكم الذمة إذا قال عاقد الذمة: إن أظهرتم خموركم، فلا عهد، أو انتقض العهد.','351','127','0');
INSERT INTO [t143157] VALUES (135,'المسألة الخامسة: لو تعرض أهل الذمة لرسولنا - صلى الله عليه وسلم - بسوء، وقدحوا في الإسلام، وأظهروا ذلك.','351','127','0');
INSERT INTO [t143157] VALUES (136,'المسألة السادسة: حكم من سب رسول الله - صلى الله عليه وسلم - بما هو قذف صريح، ولم يتب.','352','127','0');
INSERT INTO [t143157] VALUES (137,'المسألة السابعة: حكم من سب رسول الله - صلى الله عليه وسلم - بما هو قذف صريح، ثم تاب.','362','127','0');
INSERT INTO [t143157] VALUES (138,'المسألة الثامنة: حكم منع الذمي من إطالة ارتفاع الدار إذا كان داره في طرف البلد لا تجاور أبنية المسلمين.','374','127','0');
INSERT INTO [t143157] VALUES (139,'المطلب الثالث: الأمان','376','123','0');
INSERT INTO [t143157] VALUES (140,'المسألة الأولى: إقامة الكافر في موضع من جزيرة العرب (الحجاز) أكثر من ثلاثة أيام.','377','139','0');
INSERT INTO [t143157] VALUES (141,'المسألة الثانية: إن أقام الكافر في موضع من جزيرة العرب ثلاثة أيام، ثم انتقل منه إلى موضع آخر، وأقام فيه ثلاثة أيام، ثم كذلك يقيم في كل موضع ثلاثا فما دون.','386','139','0');
INSERT INTO [t143157] VALUES (142,'المسألة الثالثة: بذل الإمام مالا للكفار إذا خاف استئصال المسلمين وإبادتهم.','387','139','0');
INSERT INTO [t143157] VALUES (143,'الفصل الخامس','388','0','0');
INSERT INTO [t143157] VALUES (144,'المبحث الأول: الصيد والذبائح، والعقيقة، والأطعمة','389','143','0');
INSERT INTO [t143157] VALUES (145,'المطلب الأول: الصيد والذبائح','390','144','0');
INSERT INTO [t143157] VALUES (146,'المسألة الأولى: إذا رمى صيدا، أو أرسل كلبه المعلم، ولم يبلغ سلاحه أو معلمه ما يبلغ الذبح من الصيد، ويمكن ذبحه، فهل يلزمه أن يعدو، أم يكفيه أن يمشي، حتى تحل؟','391','145','0');
INSERT INTO [t143157] VALUES (147,'المسألة الثانية: حكم ملك صاحب الأرض الصيد فيما لو سقى الرجل أرضا له، أو وقع الماء على أرضه من غير قصده، فتخطى فيها صيد وتوحل، وصار مقدورا عليه.','395','145','0');
INSERT INTO [t143157] VALUES (148,'المطلب الثاني: العقيقة','399','144','0');
INSERT INTO [t143157] VALUES (149,'المسألة الأولى: كسر عظم العقيقة.','400','148','0');
INSERT INTO [t143157] VALUES (150,'المسألة الثانية: طبخ لحم العقيقة','405','148','0');
INSERT INTO [t143157] VALUES (151,'المطلب الثالث: الأطعمة','407','144','0');
INSERT INTO [t143157] VALUES (152,'المسألة الأولى: على القول بتحريم الجلالة، إذا منعت من تعاطي القاذورات، وردت إلى العلف النقي، وزال ما بلحمها من أثر النجاسة، وطاب لحمها.','408','151','0');
INSERT INTO [t143157] VALUES (153,'المسألة الثانية: على القول بتحريم الجلالة، هل جلدها نجس؟','410','151','0');
INSERT INTO [t143157] VALUES (154,'المسألة الثالثة: على القول بتحريم الجلالة، هل يطهر جلدها بالدباغ إذا ذبحت؟','411','151','0');
INSERT INTO [t143157] VALUES (155,'المبحث الثاني: الأيمان','413','143','0');
INSERT INTO [t143157] VALUES (156,'المطلب الأول: اليمين المنعقدة','414','155','0');
INSERT INTO [t143157] VALUES (157,'المسألة الأولى: من حلف لا يأكل اللحم، وأكل لحم الحيتان.','415','156','0');
INSERT INTO [t143157] VALUES (158,'المسألة الثانية: من حلف لا يأكل اللحم، وأكل القلب.','419','156','0');
INSERT INTO [t143157] VALUES (159,'المسألة الثالثة: لو حلف لا يأكل الخبز، هل يحنث بأي خبز كان؟','422','156','0');
INSERT INTO [t143157] VALUES (160,'المسألة الرابعة: إذا حلف لا يأكل طعاما اشتراه زيد، أو من طعام اشتراه زيد، ثم ملكه زيد بالصلح.','426','156','0');
INSERT INTO [t143157] VALUES (161,'المسألة الخامسة: لو قال: "والله لا أكلم امرأة تزوجها زيد"، فوكل زيد من يقبل له نكاح امرأة، فكلمها الحالف.','432','156','0');
INSERT INTO [t143157] VALUES (162,'المسألة السادسة: لو حلف: لا يتزوج، أو لا ينكح، فوكل من قبل له نكاح امرأة.','434','156','0');
INSERT INTO [t143157] VALUES (163,'المسألة السابعة: لو حلف لا يبيع بيعا فاسدا، فباع فاسدا.','436','156','0');
INSERT INTO [t143157] VALUES (164,'المسألة الثامنة: لو حلف عمرو لا يدخل دار زيد، فباعها زيد، واشترى دارا أخرى، ثم دخلها عمرو.','438','156','0');
INSERT INTO [t143157] VALUES (165,'المسألة التاسعة: لو حلف لا يفارق غريمه حتى يستوفي حقه منه، ففارقه الغريم وفر منه.','441','156','0');
INSERT INTO [t143157] VALUES (166,'المسألة العاشرة: لو قال: لأقضين حقك إلى أيام.','446','156','0');
INSERT INTO [t143157] VALUES (167,'المطلب الثاني: كفارة اليمين','448','155','0');
INSERT INTO [t143157] VALUES (168,'المسألة الأولى: من عقد يمينه على مباح، فهل الأولى أن يبر بيمينه، أو يحنث ويفعل المباح ويكفر؟','449','167','0');
INSERT INTO [t143157] VALUES (169,'المسألة الثانية: لو ملك عبده عبدا، وأذن له في أن يعتقه عن كفارته، وقلنا: أن ولاء ذلك المعتق موقوف: فإن عتق هذا العبد الذي أعتقه، فيكون ولاء ذلك المعتق له تبينا، وإن مات رقيقا، فيكون ولاؤه لسيده. فهل الكفارة موقوفة أيضا؟','452','167','0');
INSERT INTO [t143157] VALUES (170,'المسألة الثالثة: ولو أعتق المكاتب عن كفارته بإذن سيده، وصححنا تبرعاته بإذن سيده، هل تبرأ ذمته عن الكفارة؟','454','167','0');
INSERT INTO [t143157] VALUES (171,'الفصل السادس','456','0','0');
INSERT INTO [t143157] VALUES (172,'المبحث الأول: القضاء، والشهادات، والدعوى والبينات','457','171','0');
INSERT INTO [t143157] VALUES (173,'المطلب الأول: القضاء','458','172','0');
INSERT INTO [t143157] VALUES (174,'المسألة الأولى: حكم سماع الدعوى والبينة على المدعى عليه إذا كان في البلد، ولم يحضر مجلس القضاء مع القدرة على إحضاره.','459','173','0');
INSERT INTO [t143157] VALUES (175,'المسألة الثانية: لو قال الإمام للقاضي: "إذا قرأت كتابي، فأنت معزول"، فلم يقرأه بنفسه، وقرئ عليه.','461','173','0');
INSERT INTO [t143157] VALUES (176,'المطلب الثاني: الشهادات','462','172','0');
INSERT INTO [t143157] VALUES (177,'المسألة الأولى: حكم خروج الشاعر إلى حد الكذب في وصف، أو مدح وإطراء (بدون أن يهجو، ولا يتعرض لعرض، ولا يشبب بامرأة معينة).','463','176','0');
INSERT INTO [t143157] VALUES (178,'المسألة الثانية: هل تقبل شهادة الحسبة في الوقف إن كان على معينين، وقلنا: الملك في الرقبة لله تعالى؟','469','176','0');
INSERT INTO [t143157] VALUES (179,'المسألة الثالثة: حكم قبول البينة حسبة إذا شهد شاهدان أن المرأة ولدت الولد على فراش زوجها لستة أشهر فصاعدا، والزوج يقول: أتت به لأقل من ستة أشهر.','473','176','0');
INSERT INTO [t143157] VALUES (180,'المطلب الثالث: الدعوى والبينات','478','172','0');
INSERT INTO [t143157] VALUES (181,'المسألة الأولى: لو أقام المدعي بينة على الملك المطلق، وأقام صاحب اليد بينة على أن الدار ملكه اشتراها من المدعي.','479','180','0');
INSERT INTO [t143157] VALUES (182,'المسألة الثانية: إذا ادعى الرجل جهة في الاستحقاق على شخص، فلم يتعرض المدعى عليه لها، واقتصر على قوله: لا يلزمني تسليم شيء إليك.','485','180','0');
INSERT INTO [t143157] VALUES (183,'المبحث الثاني: العتق وكتاب المكاتب وأمهات الأولاد','487','171','0');
INSERT INTO [t143157] VALUES (184,'المطلب الأول: العتق','488','183','0');
INSERT INTO [t143157] VALUES (185,'المسألة الأولى: عبد يملكه اثنان بالسوية، فأعتق أحدهما نصيبه من العبد، فطالب الشريك المعتق بالقيمة، فتعذرت القيمة بإفلاس أو هرب، فهل يثبت العتق؟','489','184','0');
INSERT INTO [t143157] VALUES (186,'المسألة الثانية: إن أعتق في مرض موته عبدا لا مال له سواه، ومات العبد قبل موت السيد، فهل يموت كله رقيقا، أم كله حرا، أم ثلثه حرا وباقيه رقيقا؟','493','184','0');
INSERT INTO [t143157] VALUES (187,'المسألة الثالثة: الحكم فيما لو أوصى بعتق عبيد، وكان الثلث لا يفي بهم، ولم يوص بتقديم بعضهم.','497','184','0');
INSERT INTO [t143157] VALUES (188,'المطلب الثاني: كتاب المكاتب','506','183','0');
INSERT INTO [t143157] VALUES (189,'المسألة الأولى: حكم الكتابة على دم أو ميتة.','508','188','0');
INSERT INTO [t143157] VALUES (190,'المسألة الثانية: إذا كاتب السيد المكاتب كتابة فاسدة، ثم أوصى برقبته، وكان عالما بفساد الكتابة، فهل تصح الوصية فتفسخ الكتابة.','512','188','0');
INSERT INTO [t143157] VALUES (191,'المسألة الثالثة: حكم الكتابة فيما إذا مات رجل وخلف ابنين وعبدا، فادعى العبد عليهما أن أباهما كاتبه، فصدقه أحدهما وكذبه الثاني، فشهد المصدق بالكتابة، وانضم إليه شاهد آخر.','513','188','0');
INSERT INTO [t143157] VALUES (192,'المسألة الرابعة: حكم العتق فيما إذا قبض السيد النجم الأخير للمكاتبة، وتلف ما قبضه، ثم اطلع على ما كان به من نقص.','514','188','0');
INSERT INTO [t143157] VALUES (193,'المسألة الخامسة: إذا أتى المكاتب بالنجوم، فقال السيد: هذا حرام، أو مغصوب، وأقام بينة بذلك.','517','188','0');
INSERT INTO [t143157] VALUES (194,'المسألة السادسة: إذا حل النجم، والمكاتب غائب، أو غاب بعد حلوله بغير إذن السيد، فإذا قلنا: إن للسيد الفسخ إن شاء بنفسه، وإن شاء بالحاكم، وإن رفع إلى الحاكم، فلا بد أن يثبت عنده حلول النجم، وتعذر التحصيل، ويحلفه الحاكم مع ذلك، فكيف يحلفه الحاكم؟','519','188','0');
INSERT INTO [t143157] VALUES (195,'المسألة السابعة: حكم الأروش لو مات المكاتب قبل قسمة ما في يده، وانفسخت الكتابة، وسقطت النجوم.','520','188','0');
INSERT INTO [t143157] VALUES (196,'المسألة الثامنة: إذا كاتب السيد عبدين من عبيده في صفقتين أو في صفقة، وقلنا بجواز الصفقة، فإذا أقر السيد أنه استوفى نجوم أحدهما، أو أنه أبرأ أحدهما، ثم مات، فلو ادعى أحد المكاتبين على الوارث الأداء أو الإبراء، فأنكر وقال: لست المؤدي، فهل يحصل بإنكاره الإقرار للآخر؟','522','188','0');
INSERT INTO [t143157] VALUES (197,'المسألة التاسعة: إذا اجتمع للسيد على مكاتبه نجوم الكتابة، وأرش جناية كانت صدرت منه على السيد، أو على ماله، وأدى المكاتب ما في يده إلى سيده مطلقا، ولم يتعرض واحد منهما للجهة، فإذا قال المكاتب: نويت به أداء النجوم، وأنكر السيد ذلك، وقال: بل قبضته عن الأرش.','523','188','0');
INSERT INTO [t143157] VALUES (198,'المسألة العاشرة: إذا ثبت دين المعاملة لأجنبي على مكاتب، وضاق ما في يده، فهل للأجنبي حق تعجيزه؟','525','188','0');
INSERT INTO [t143157] VALUES (199,'المسألة الحادية عشرة: إذا جنى العبد المرهون على طرف من يرثه السيد كأبيه وابنه خطأ، ومات المجني عليه قبل الاستيفاء وورثه السيد.','527','188','0');
INSERT INTO [t143157] VALUES (200,'المسألة الثانية عشرة: المكاتبة المشتركة بين اثنين، إذا وطئها أحدهما، وقد كان موسرا، ثم أحبلها، وانفصل الولد حيا، فما حكم ولد المكاتبة وقيمته؟','529','188','0');
INSERT INTO [t143157] VALUES (201,'المسألة الثالثة عشر: لو قال لورثته: ضعوا عن هذا المكاتب أكثر ما بقي عليه، ووفى الثلث واتسع، فإن زاد الورثة ووضعوا أكثر من الوصية، فهل كل ما يضعونه محمول على الوصية، أو هو تبرع منهم؟','533','188','0');
INSERT INTO [t143157] VALUES (202,'المطلب الثالث: أمهات الأولاد','535','183','0');
INSERT INTO [t143157] VALUES (203,'المسألة الأولى: حكم بيع أم الولد في حياة المولى.','540','202','0');
INSERT INTO [t143157] VALUES (204,'المسألة الثانية: حكم تزويج الكافر المستولدة إذا جوزنا تزويجها.','569','202','0');
INSERT INTO [t143157] VALUES (205,'المسألة الثالثة: عتق أم الولد بموت المولى.','569','202','0');
INSERT INTO [t143157] VALUES (206,'المسألة الرابعة: على القول بجواز بيع المستولدة (أم الولد)، فهل تعتق بموت السيد؟','570','202','0');
INSERT INTO [t143157] VALUES (207,'قائمة المراجع والمصادر','595','0','0');
