INSERT INTO [t14381] VALUES (314,'بيان المؤلف أن إطلاق كلمة (إله) على الله تعالى، وكلمة (العبادة) على طاعته والتقرب إليه، أمر لا يحتاج إلى إيضاح وبيان، وأما غير الله: فقد اتخذ المشركون آلهة من دونه، وعبدوا أشياء كثيرة غير الله','865','313','0');
INSERT INTO [t14381] VALUES (315,'بيان المؤلف للمخلوقات التي اتخذها المشركون آلهة منذ قوم نوح عليه السلام، وحتى مشركي العرب، وبيان ما كانوا يفعلونه مع هذه المعبودات. ومنها: الأصنام، والعجل، والهوى، والشياطين، والأحبار والرهبان، والمسيح وأمه عليهما السلام، وفرعون، وأشخاص متوهمة لا وجود لها، والملائكة','865','313','0');
INSERT INTO [t14381] VALUES (316,'فصل ذكر ما أخبر الله به من عبادة قوم إبراهيم الأصنام، وأن سبأ عبدوا الشمس، وأن قوما آخرين عبدوا الشيطان، وأن اليهود والنصارى عبدوا الأحبار والرهبان، وأن النصارى عبدوا المسيح، وأن قوم هود وبني إسرائيل في عهد يوسف عليه السلام عبدوا أشخاصا متخيلة، وأن المشركين زعموا أنهم يعبدون الملائكة','889','313','0');
INSERT INTO [t14381] VALUES (317,'يلزم النظر في اعتقاد قوم نوح وقوم هود وقوم صالح وقوم إبراهيم في تلك الأشياء، وما كانوا يعظمونها به ليتبين معنى الإله والعبادة','900','313','0');
INSERT INTO [t14381] VALUES (318,'إقامة المؤلف البرهان على أن المشركين مع اتخاذهم آلهة من دون الله، إلا أنهم لم يكونوا ينكرون وجود الله تعالى، بل كانوا مقرين بربوبيته','901','313','0');
INSERT INTO [t14381] VALUES (319,'اعتقاد المشركين أن الله أمر بتعظيم الأصنام لتقربهم إلى الله','903','313','0');
INSERT INTO [t14381] VALUES (320,'ما كانت تعبده عاد وثمود','905','313','0');
INSERT INTO [t14381] VALUES (321,'اعتقاد عاد وثمود وجود أشخاص علوية تتصرف في الكون بقدرة ممنوحة لها من الله','907','313','0');
INSERT INTO [t14381] VALUES (322,'الكلام على قوم إبراهيم وتأليههم الأصنام وعبادتهم إياها وإيراد الآيات الدالة على ما كان ينكره عليهم إبراهيم عليه السلام','908','313','0');
INSERT INTO [t14381] VALUES (323,'اختلاف أهل العلم في قول إبراهيم: {هذا ربي}','909','313','0');
INSERT INTO [t14381] VALUES (324,'الظاهر أن قوم إبراهيم لم يعتقدوا في الأصنام ذواتها القدرة على النفع والضر','910','313','0');
INSERT INTO [t14381] VALUES (325,'تقليد الآباء هو الحامل لقوم إبراهيم على التشبث بعبادة الأصنام','911','313','0');
INSERT INTO [t14381] VALUES (326,'ما قيل من أن قوم إبراهيم كانوا يعبدون التماثيل على أنها رموز للكواكب، وذكر الأدلة عليه','912','313','0');
INSERT INTO [t14381] VALUES (327,'ما نقل عن السلف في تفسير اسم (إيل)','916','313','0');
INSERT INTO [t14381] VALUES (328,'بيان حقيقة قول الذي حاج إبراهيم: {أنا أحيي وأميت}','921','313','0');
INSERT INTO [t14381] VALUES (329,'توجيه قول إبراهيم عليه السلام لأبيه: {لا تعبد الشيطان}','929','313','0');
INSERT INTO [t14381] VALUES (330,'فصل (إيراد ألفاظ أخرى بمعنى التأليه والعبادة نسبها الله إلى المشركين في حق من اتخذوه من دون الله. منها الدعاء، وا تخاذهم أربابا وشركاء وأندادا، وذكر ما يبين ذلك)','931','313','0');
INSERT INTO [t14381] VALUES (331,'تفسير قوله تعالى: {هو الذي خلقكم من نفس واحدة وجعل منها زوجها} الآيات، وبيان نوع الشرك المذكور فيها','933','313','0');
INSERT INTO [t14381] VALUES (332,'هل يطلق على من لم يعبد الله واقتصر على عبادة غيره أنه مشرك؟','941','313','0');
INSERT INTO [t14381] VALUES (333,'المؤمن يريد ــ والله أعلم ــ بقوله: (لا إله إلا الله وحده لا شريك له) لا شريك له في الألوهية أي: في المعبودية بحق','942','313','0');
INSERT INTO [t14381] VALUES (334,'تفسير قوله تعالى: {وجعلوا لله شركاء قل سموهم أم تنبئونه بما لا يعلم في الأرض}','942','313','0');
INSERT INTO [t14381] VALUES (335,'قصد المشركين بعبادتهم الإناث الخياليات التي زعموا أنها بنات الله، وأنها الملائكة','951','313','0');
INSERT INTO [t14381] VALUES (336,'بيان وجه عبادة المشركين للملائكة، وتعظيمهم للأصنام','951','313','0');
INSERT INTO [t14381] VALUES (337,'تفسير قوله تعالى: {وكم من ملك في السماوات لا تغني شفاعتهم}','952','313','0');
INSERT INTO [t14381] VALUES (338,'تفسير قوله تعالى: {ومن لم يحكم بما أنزل الله فأولئك هم الكافرون} وتفصيل أحكام من حكم بغير ما أنزل الله','957','313','0');
INSERT INTO [t14381] VALUES (339,'بيان اعتقاد المشركين في الأصنام','959','313','0');
INSERT INTO [t14381] VALUES (340,'كلام المؤلف على قوله تعالى: {أم اتخذوا من دون الله شفعاء قل أولو كانوا لا يملكون شيئا ولا يعقلون}، وإطالته في ذلك','963','339','0');
INSERT INTO [t14381] VALUES (341,'سؤالان من المؤلف وجوابهما حول بحث الأصنام','963','339','0');
INSERT INTO [t14381] VALUES (342,'الكلام على اللات والعزى ومناة وبيان اشتقاق كل منها','965','339','0');
INSERT INTO [t14381] VALUES (343,'ذكر صنيع المشركين عند الأصنام: من التمسح بها، والعكوف عليها، والاستقسام بالأزلام عندها','970','339','0');
INSERT INTO [t14381] VALUES (344,'هل يوجد نص صريح على أن المشركين كانوا يدعون الأصنام ويسجدون لها؟ ووجه دلالة قوله تعالى: {ياأيها الناس ضرب مثل فاستمعوا له} الآية، على ذلك','970','339','0');
INSERT INTO [t14381] VALUES (345,'تفسير الأزلام بأنها قداح معدة للقرعة والاستخارة','974','339','0');
INSERT INTO [t14381] VALUES (346,'بيان اعتقاد المشركين في الملائكة','975','313','0');
INSERT INTO [t14381] VALUES (347,'جميع مشركي العرب أو أغلبهم كانوا يعبدون الملائكة','975','313','0');
INSERT INTO [t14381] VALUES (348,'إنكار القرآن على المشركين في شأن الملائكة يتعلق بأربعة أمور','975','313','0');
INSERT INTO [t14381] VALUES (349,'لم يبقوا للملائكة إلا الشفاعة','976','313','0');
INSERT INTO [t14381] VALUES (350,'مناقشتهم في شفاعة الملائكة','976','313','0');
INSERT INTO [t14381] VALUES (351,'ذكر كيفية تأليه المشركين للملائكة، وتلبيتهم في الحج بالإناث التي هي الملائكة في زعمهم','976','350','0');
INSERT INTO [t14381] VALUES (352,'بيان طاعة المشركين لأهوائهم ورؤسائهم في شرع الدين، ومن ثم تأليه الشياطين','978','350','0');
INSERT INTO [t14381] VALUES (353,'مدار محاجة الله للمشركين على الشفاعة','979','350','0');
INSERT INTO [t14381] VALUES (354,'أغلب آيات الشفاعة في تقرير أن الملائكة لا يشفعون إلا لمن ارتضى','981','350','0');
INSERT INTO [t14381] VALUES (355,'لو فرض شفاعة الملائكة لما نفعت لأن الأمور بيد الله','984','350','0');
INSERT INTO [t14381] VALUES (356,'اعتماد المشركين على شبهتي التشبث بالقدر والتقليد بعد إفحامهم','987','350','0');
INSERT INTO [t14381] VALUES (357,'إبطال شبهة تشبثهم بالقدر بأمرين','989','350','0');
INSERT INTO [t14381] VALUES (358,'الأول بإقامة الحجة على صدق محمد - صلى الله عليه وسلم -','989','357','0');
INSERT INTO [t14381] VALUES (359,'الأمر الثاني: أن يقال لهم: ترك اليقين لمجرد التخرص والتخمين جهل واضح، فدعوا ذلك وأخبروني: هل عندكم من دليل علمي بأن ما أنتم عليه من الشرك وتحريم بعض الأشياء حق يحبه الله ويرضاه؟','992','357','0');
INSERT INTO [t14381] VALUES (360,'إبطال شبهة التقليد بثلاثة أمور ثالثها: لا تحصروا نظركم في حسن الظن بآبائكم، بل مع ذلك انظروا فيما وجدتموهم عليه وفيما جئتكم به، ووازنوا بينهما؛ فإنكم إذا فعلتم ذلك بإخلاص تبين لكم أن ما جئتكم به الحق المبين، فحينئذ ينبغي لكم أن تتبعوا اليقين وتتركوا التوهم والتخمين','993','357','0');
INSERT INTO [t14381] VALUES (361,'لم يكن مناط تأليه المشركين للملائكة دعوى أنهم بنات الله وبيان المناط الحقيقي لذلك التأليه والعبادة؟','995','350','0');
INSERT INTO [t14381] VALUES (362,'بيان اعتقاد المشركين في أهوائهم','1002','313','0');
INSERT INTO [t14381] VALUES (363,'بيان اعتقاد المشركين في الشياطين','1003','362','0');
INSERT INTO [t14381] VALUES (364,'بيان أن المشركين كانوا يطيعون الشياطين في ما يوسوسون به إليهم في شرع الدين، وأن عباداتهم في حقيقة الأمر ترجع إلى الشيطان الذي أمرهم بها','1003','362','0');
INSERT INTO [t14381] VALUES (365,'فصل (بيان حقيقة عكوف المشركين عند الأصنام وكيفيته، وزعمهم أن ذلك عبادة لله عز وجل)','1005','362','0');
INSERT INTO [t14381] VALUES (366,'حاصل ما تقدم في هذا الباب','1006','313','0');
INSERT INTO [t14381] VALUES (367,'خلاصة ما كانت تفعله الأقوام تجاه معبوداتها، ودعوى كل منها استحقاق معبودها أن يخضع له طلبا للنفع الغيبي','1006','313','0');
INSERT INTO [t14381] VALUES (368,'زعم الهنود أن لكل جنس من المخلوقات الحسية مدبرا من الملائكة، ويدعونهم ويخضعون لتماثيل ينصبونها لهم، ويخضعون للمخلوقات بنية الخضوع لمدبرها','1016','367','0');
INSERT INTO [t14381] VALUES (369,'وقد يكون للمعبود الواحد ألوف من التماثيل يطلقون على كل تمثال منها اسم ذلك المعبود','1017','367','0');
INSERT INTO [t14381] VALUES (370,'ذكر ما نقله أهل التاريخ من أن أول من وضع عبادة المفضلين من الأموات هو سروج بن رعو جد والد إبراهيم عليه السلام','1018','367','0');
INSERT INTO [t14381] VALUES (371,'التماثيل كانت للذكرى أولا ثم صارت للعبادة في أيام طهمورث','1018','367','0');
INSERT INTO [t14381] VALUES (372,'ما ذكره أبو الريحان البيروني عن الأمم السابقة من نزوعهم إلى التصوير في الكتب والهياكل وأن ذلك هو السبب الباعث على اتخاذ الأصنام','1019','367','0');
INSERT INTO [t14381] VALUES (373,'حكاية خرافات أهل الهند في باب اتخاذ الأصنام نقلا عن البيروني','1021','367','0');
INSERT INTO [t14381] VALUES (374,'عمرو بن لحي وما جلبه للعرب من الأصنام، وتغييره دين إسماعيل','1026','367','0');
INSERT INTO [t14381] VALUES (375,'عبادة الملائكة أصل الشرك ومبدؤه','1026','367','0');
INSERT INTO [t14381] VALUES (376,'اليونان والمصريون القدماء ووثنيو الهند وغيرهم يعبدون الأوثان تعظيما وتكريما للغائبين','1027','367','0');
INSERT INTO [t14381] VALUES (377,'الوثنيون صنفان: فلاسفة كالصابئة، وسذج كالعرب في جاهليتهم','1028','367','0');
INSERT INTO [t14381] VALUES (378,'انتشار صنع الأمم المسيحية في هذا العصر تماثيل لعظماء رجالها ونصبها في الشوارع العامة','1029','367','0');
INSERT INTO [t14381] VALUES (379,'شاع بين الشيعة في هذا الزمان اختلاق صورة لأمير المؤمنين علي وابنه الحسين وفرسه، وعوامهم يعظمون تلك الصور','1031','367','0');
INSERT INTO [t14381] VALUES (380,'العرب إنما عظموا اللات والعزى ومناة تعظيما لأشخاص معظمين','1033','367','0');
INSERT INTO [t14381] VALUES (381,'الخلاصة أن عبادتهم للشياطين كانت من وجهين: طاعتهم لهم، واعتراض الشياطين للعبادات لتكون في الصورة لهم','1038','367','0');
INSERT INTO [t14381] VALUES (382,'تفسير المؤلف لآيات سورة النجم {أفرأيتم اللات والعزى} إلى قوله تعالى: {تلك إذا قسمة ضيزى}','1041','367','0');
INSERT INTO [t14381] VALUES (383,'كلام المؤلف عن قصة الغرانيق، وبيان حقيقة الكلمات التي ألقاها الشيطان','1047','367','0');
INSERT INTO [t14381] VALUES (384,'عبادة الشياطين','1054','313','0');
INSERT INTO [t14381] VALUES (385,'تفسير قوله تعالى: {وما نرى معكم شفعاءكم الذين زعمتم أنهم فيكم شركاء} والآثار الواردة في ذلك','1055','384','0');
INSERT INTO [t14381] VALUES (386,'تفسير قوله تعالى: {وجعلوا لله شركاء الجن} الآية','1055','384','0');
INSERT INTO [t14381] VALUES (387,'المراد بالمدعوين من دون الله في قوله تعالى: {ولا تسبوا الذين يدعون من دون الله. . .} الإناث الخياليات أو الشياطين لا الملائكة؛ لأن سب الملائكة ممنوع مطلقا','1056','384','0');
INSERT INTO [t14381] VALUES (388,'الآثار الواردة في تفسير قوله تعالى: {ولا تأكلوا مما لم يذكر اسم الله عليه وإنه لفسق} الآية','1056','384','0');
INSERT INTO [t14381] VALUES (389,'ذكر إحدى الوجوه في معنى (لا) النافية في قوله تعالى: {قل تعالوا أتل ما حرم ربكم عليكم ألا تشركوا به شيئا}','1057','384','0');
INSERT INTO [t14381] VALUES (390,'ذكر الآثار الواردة في تفسير قوله تعالى: {إنما سلطانه على الذين يتولونه والذين هم به مشركون}','1058','384','0');
INSERT INTO [t14381] VALUES (391,'ما رجحه المؤلف في معنى الإشراك بالشيطان، ومناقشته ما ذهب إليه ابن جرير','1059','384','0');
INSERT INTO [t14381] VALUES (392,'ما ذهب إليه العز بن عبد السلام من تضمين {لا تشرك بالله} معنى: لا تعدل، أي: لا تسو بالله شيئا في العبادة والمحبة','1061','384','0');
INSERT INTO [t14381] VALUES (393,'تفسير قوله تعالى: {ويوم يقول نادوا شركائي الذين زعمتم}','1063','384','0');
INSERT INTO [t14381] VALUES (394,'تفسير قوله تعالى: {ياأبت لا تعبد الشيطان}','1063','384','0');
INSERT INTO [t14381] VALUES (395,'تفسير قوله تعالى: {إنكم وما تعبدون من دون الله حصب جهنم} وبيان أنهم إنما يعبدون الشيطان ومن أمرهم بعبادتهم','1064','384','0');
INSERT INTO [t14381] VALUES (396,'بيان أن عبادة المشركين للإناث الغيبيات هي في الحقيقة عبادة للشياطين، وإيضاح وجه ذلك','1065','384','0');
INSERT INTO [t14381] VALUES (397,'عبادة الهوى','1074','313','0');
INSERT INTO [t14381] VALUES (398,'النظر فيما كان يعتقده المشركون في آلهتهم ويعملونه','1076','313','0');
INSERT INTO [t14381] VALUES (399,'تفسير عبادة الأصنام','1076','398','0');
INSERT INTO [t14381] VALUES (400,'ود وسواع ويغوث ويعوق كانوا رجالا صالحين فلما ماتوا جعلت لهم تماثيل','1077','399','0');
INSERT INTO [t14381] VALUES (401,'لم يعتقد متأخرو المشركين أن هذه الأصنام تخلق وترزق','1078','399','0');
INSERT INTO [t14381] VALUES (402,'قول المشركين: {ولو شاء الله لأنزل ملائكة} صريح في اعترافهم بوجود الله وقدرته وبوجود الملائكة','1078','399','0');
INSERT INTO [t14381] VALUES (403,'ذكر أهل التاريخ أن قوم هود وقوم صالح كانت لهم أصنام لكن لم يرد في القرآن ما يدل على ذلك','1079','399','0');
INSERT INTO [t14381] VALUES (404,'جاء في القرآن التصريح بعبادة قوم إبراهيم الأصنام وبيان ما كانوا يتأولون في ذلك','1079','399','0');
INSERT INTO [t14381] VALUES (405,'آثار المصريين الذين كانوا في عهد يوسف عليه السلام تدل على أنهم كانوا يعبدون الأصنام، وفي القرآن أنهم كانوا يعبدون الروحانيين','1084','399','0');
INSERT INTO [t14381] VALUES (406,'توهم بني إسرائيل الذين مروا على قوم يعكفون على أصنام لهم أن عبادة الجماد إذا كان رمزا لله لا تنافي التوحيد','1086','399','0');
INSERT INTO [t14381] VALUES (407,'العرب كانوا يعبدون الأصنام على أنها تماثيل للإناث الخياليات التي هي الملائكة في زعمهم','1086','399','0');
INSERT INTO [t14381] VALUES (408,'تفصيل ما كان عباد الأصنام يعظمون به أصنامهم من العكوف عليها والتمسح بها، والذبح عندها، وتقريب الزاد لها','1089','399','0');
INSERT INTO [t14381] VALUES (409,'الوثنيون إلى وقتنا هذا ينحنون للأصنام ويسجدون لها، لكن لم يثبت عن العرب أنهم كانوا يسجدون للأصنام','1092','399','0');
INSERT INTO [t14381] VALUES (410,'عباد النار','1094','398','0');
INSERT INTO [t14381] VALUES (411,'عجل السامري','1094','398','0');
INSERT INTO [t14381] VALUES (412,'الأناسي الأحياء وأرواح الموتى','1095','398','0');
INSERT INTO [t14381] VALUES (413,'لم يكن قوم نوح يرفعون أرواح الموتى إلى درجة الملائكة','1095','412','0');
INSERT INTO [t14381] VALUES (414,'بيان حقيقة ما كان يدعيه محاج إبراهيم','1096','412','0');
INSERT INTO [t14381] VALUES (415,'تفسير تأليه المسيح وأمه عليهما السلام','1101','398','0');
INSERT INTO [t14381] VALUES (416,'بيان معنى قوله تعالى: {ما كان لبشر أن يؤتيه الله الكتاب والحكم والنبوة} الآية','1107','415','0');
INSERT INTO [t14381] VALUES (417,'تأليه الأحبار والرهبان','1113','398','0');
INSERT INTO [t14381] VALUES (418,'شرع الدين خاص بالرب','1117','417','0');
