INSERT INTO [t14381] VALUES (932,'اشتقاق التأويل، وإطلاقاته، ومعنى تأويل الرؤيا، وتأويل الفعل، وتأويل اللفظ.','2503','930','0');
INSERT INTO [t14381] VALUES (933,'معنى قوله - صلى الله عليه وسلم - لابن عباس: «علمه التأويل».','2505','930','0');
INSERT INTO [t14381] VALUES (934,'الباب الثاني: مقدمة في الصدق والكذب.','2506','928','0');
INSERT INTO [t14381] VALUES (935,'نعمة الكلام على الناس وفضيلة الصدق.','2507','934','0');
INSERT INTO [t14381] VALUES (936,'تشديد الشارع في الكذب.','2508','934','0');
INSERT INTO [t14381] VALUES (937,'علامات المنافق المذكورة في الحديث تدور كلها على الكذب.','2509','934','0');
INSERT INTO [t14381] VALUES (938,'الترخيص في بعض ما يسمى كذبا.','2510','934','0');
INSERT INTO [t14381] VALUES (939,'المقصود بالكذب المرخص به عند الضرورة.','2511','934','0');
INSERT INTO [t14381] VALUES (940,'عدم خلو الكذب من المفاسد ولو كان لضرورة.','2511','934','0');
INSERT INTO [t14381] VALUES (941,'كذبات إبراهيم عليه الصلاة والسلام كانت من باب التورية، مع تسميتها كذبات.','2513','934','0');
INSERT INTO [t14381] VALUES (942,'ثلاثة أنواع أخرى من الكلام داخل تحت التورية، وأمثلة عليها.','2514','934','0');
INSERT INTO [t14381] VALUES (943,'مؤدى ما سبق استحالة كذب الله تعالى أو رسوله - صلى الله عليه وسلم - في كلامهما.','2517','934','0');
INSERT INTO [t14381] VALUES (944,'الباب الثالث: في حكم التأويل.','2518','928','0');
INSERT INTO [t14381] VALUES (945,'اللفظ المراد تأويله لا يخلو عن أن يكون: في العقائد، أو الأخبار، أو الأحكام.','2518','944','0');
INSERT INTO [t14381] VALUES (946,'الفصل الأول: تأويل النصوص الواردة في العقيدة.','2519','944','0');
INSERT INTO [t14381] VALUES (947,'نصوص العقيدة على ضربين: ما ورد في عقيدة كلف الناس باعتقاده، أوبخلافه.','2519','946','0');
INSERT INTO [t14381] VALUES (948,'العقيدة الواجبة هي الإيمان بالله وملائكته وكتبه ورسله والبعث بعد الموت والقدر','2519','946','0');
INSERT INTO [t14381] VALUES (949,'تفاصيل العقيدة راجعة إلى أركان الإيمان الستة الآنف ذكرها.','2520','946','0');
INSERT INTO [t14381] VALUES (950,'عامة ما ذكر من العقيدة الواجبة يمكن إدراكه بالعقل.','2521','946','0');
INSERT INTO [t14381] VALUES (951,'محدودية عقل المخلوق، والجهل بحكمة بعض الأمور لا يخدش في موافقتها للعقل.','2522','946','0');
INSERT INTO [t14381] VALUES (952,'الضروريات في الإيمان معلومة من الدين بالضرورة وتأويلها بما ينافي ذلك كفر.','2524','946','0');
INSERT INTO [t14381] VALUES (953,'الاختلاف في نصوص الصفات بين مجريها على الظاهر وبين مؤوليها بقرائن متوهمة.','2525','946','0');
INSERT INTO [t14381] VALUES (954,'الجواب من وجهين عن احتجاج المؤولين للصفات بتأويل السلفيين لمعية الله.','2525','946','0');
INSERT INTO [t14381] VALUES (955,'قام البرهان على وجوب حمل النصوص على ظواهرها','2525','946','0');
INSERT INTO [t14381] VALUES (956,'من تفصيلات ضروريات الإيمان ما لا يتوقف الإيمان بها أو بالعلم بها.','2525','946','0');
INSERT INTO [t14381] VALUES (957,'اللفظ الظاهر قد يكون ظاهرا في نفسه ولكن اقترن به ما يصير له معنى آخر، ومثاله','2526','946','0');
INSERT INTO [t14381] VALUES (958,'العقل لا يصح أن يكون قرينة لصرف اللفظ عن ظاهره إلا إذا كان بديهيا للمخاطبين','2526','946','0');
INSERT INTO [t14381] VALUES (959,'عدم إرادة المتكلم للظاهر مع انتفاء القرينة المدركة لصرفه عنه = يجعل كلامه كذبا.','2526','946','0');
INSERT INTO [t14381] VALUES (960,'وجوه في الجواب عن شبهة من جعل آية {ليس كمثله شيء} ونحوها قرينة للتأويل.','2527','946','0');
INSERT INTO [t14381] VALUES (961,'الفرق بين آيات الصفات وآيات التحليل والتحريم.','2528','946','0');
INSERT INTO [t14381] VALUES (962,'كثير من القوانين لا تطابق الحكمة في كل فرد، وإنما روعي مطابقتها في الأغلب.','2528','946','0');
INSERT INTO [t14381] VALUES (963,'إبطال قول من زعم أن الشرع استحسن الكذب بما يوافق اعتقاد الناس لإصلاحهم.','2530','946','0');
INSERT INTO [t14381] VALUES (964,'عند هؤلاء أن عامة الصحابة والتابعين وغالب الأمة مخطئون في اعتقادهم.','2530','946','0');
INSERT INTO [t14381] VALUES (965,'لو سلم أن الكذب قد يكون حسنا، فإنما ذلك من الإنسان العاجز المحتاج.','2531','946','0');
INSERT INTO [t14381] VALUES (966,'كان في الصحابة جماعة من أهل الذكاء يلازمون النبي - صلى الله عليه وسلم - فكان ينبغي أن يبوح لهم بالحقيقة.','2531','946','0');
INSERT INTO [t14381] VALUES (967,'إبطال قول من زعم أن الصحابة أمروا بكتمان حقيقة ما يعتقده العامة من الكذب.','2532','946','0');
INSERT INTO [t14381] VALUES (968,'مذهب بعض نفاة الصفات أن ما ورد في هذا الباب من الأمور المحيرة.','2533','946','0');
INSERT INTO [t14381] VALUES (969,'مذاهب من أثبت الصفات على ظاهرها، بين مؤول ومثبت لها على الظاهر دون تأويل','2533','946','0');
INSERT INTO [t14381] VALUES (970,'إبطال قول من جعل مذهب مثبتي الصفات على ظاهرها كمذهب المشبهة والمجسمة.','2534','946','0');
INSERT INTO [t14381] VALUES (971,'غالب صفات الأشياء يختلف تصورها تبعا لاختلاف تصور الموصوف بها.','2534','946','0');
INSERT INTO [t14381] VALUES (972,'لا ندرك من صفات الله تعالى إلا بما يشبه ما يتصف به المخلوق بقدر مشترك في الجملة.','2534','946','0');
INSERT INTO [t14381] VALUES (973,'باتفاق العقلاء لا يدرك شيء إلا إن تناوله الإحساس أوبفرد مماثل له مع قدر مشترك.','2534','946','0');
INSERT INTO [t14381] VALUES (974,'اليأس من إيجاد صورة ذهنية لصفة تليق بالله ينتج اعترافا بالعجز أوإنكارا للصفة.','2536','946','0');
INSERT INTO [t14381] VALUES (975,'ضرب أمثلة واقعية لبيان أن الإنسان يجحد بما لا يحس به من الأمور وبما لا يشبهه.','2537','946','0');
INSERT INTO [t14381] VALUES (976,'ضلال النفاة لأحد أمور: قلة معرفة بالشرع، أوتقديس للفلاسفة، أوتطلعهم لما لا يدرك.','2539','946','0');
INSERT INTO [t14381] VALUES (977,'مدى العقل كضعف البصر، فلكليهما حد ينتهي إليه، ثم يتوهم بعد ذلك خطأ.','2539','946','0');
INSERT INTO [t14381] VALUES (978,'كل شبهة عقلية زعم أنها برهان قاطع وجد من ينقضها ثم ثالث يدفع النقض وهكذا.','2540','946','0');
INSERT INTO [t14381] VALUES (979,'من شبه العقلانيين الاستقراء، وهو استقراء مبني على ما تطاله حواسهم، وأمثلة عليه.','2541','946','0');
INSERT INTO [t14381] VALUES (980,'العقل ينفي بعض الأشياء لأنه لم يدركها، أو لم يدرك صحتها، أو مطابقتها للحكمة.','2542','946','0');
INSERT INTO [t14381] VALUES (981,'من صفات الله ما لا شبهة فيه للمنكر، ومنها ما شبهتها من الفلاسفة فيجب ردها.','2544','946','0');
INSERT INTO [t14381] VALUES (982,'من صفات الله ما تعرض فيه الشبهة لكل أحد، وهذه يجب صرف الذهن عنها.','2544','946','0');
INSERT INTO [t14381] VALUES (983,'الاختلاف بين أوائل الفلاسفة وأواخرهم وتخطئة بعضهم بعضا يدل أن عقولهم قاصرة.','2544','946','0');
INSERT INTO [t14381] VALUES (984,'تفصيل القول في آية: {هو الذي أنزل عليك الكتاب منه آيات محكمات هن أم الكتاب .. }.','2545','946','0');
INSERT INTO [t14381] VALUES (985,'{هو الذي أنزل عليك الكتاب منه آيات محكمات .. } كادت تصير متشابهة للاختلاف فيها!','2545','946','0');
INSERT INTO [t14381] VALUES (986,'القرآن كله محكم وكله متشابه، ومنه محكم ومنه متشابه، ومعنى ذلك بالتفصيل.','2546','946','0');
INSERT INTO [t14381] VALUES (987,'معنى كون الآيات متشابهات، ومعاني المتشابه فيها.','2546','946','0');
INSERT INTO [t14381] VALUES (988,'متى يكون ابتغاء الآيات المتشابهة من الزيغ المذموم؟ ومعنى قوله: {والراسخون في العلم}؟','2548','946','0');
INSERT INTO [t14381] VALUES (989,'الرسوخ في العلم حال قلبية، وليس عن كثرة العلم، والناس متفاوتون فيه.','2552','946','0');
INSERT INTO [t14381] VALUES (990,'علامة الراسخ في العلم والزائغ فيه، وما دلت عليه الدلائل من علامتهما.','2554','946','0');
INSERT INTO [t14381] VALUES (991,'ذكر كلام الراغب الأصبهاني، ومناقشته في أضرب مشتبه المعنى واللفظ في القرآن.','2556','946','0');
INSERT INTO [t14381] VALUES (992,'من شبه أو أؤل صفات الله التي لا يعلم حقيقتها إلا هو فقد زعم أنه أدرك حقيقة معناها.','2559','946','0');
INSERT INTO [t14381] VALUES (993,'مثبت صفة الله على ظاهرها ومحيل علمه بكنهها إليه هو أحق بقوله: {آمنا به كل من عند ربنا}.','2560','946','0');
INSERT INTO [t14381] VALUES (994,'بيان الفائدة من إنزال المتشابه في القرآن مع أنه نزل هدى للعالمين وأمرنا بتدبره مطلقا.','2560','946','0');
INSERT INTO [t14381] VALUES (995,'من فوائد إنزال المتشابه في القرآن الإيمان به وإن لم يدرك كنهه، ومن فوائده الابتلاء به.','2561','946','0');
INSERT INTO [t14381] VALUES (996,'الفصل الثاني: تأويل النصوص الشرعية الواردة في الإخبار عن الوقائع.','2563','944','0');
INSERT INTO [t14381] VALUES (997,'الإخبار بما لا نحس به وبما ليس من جنسه حكمه كالعقائد في وجوب الإيمان وترك التأويل.','2563','996','0');
INSERT INTO [t14381] VALUES (998,'رد حجة المؤولين للأخبار عن بعض المحسوسات بحجة مخالفتها للعقل أوالحس أوالتواتر.','2563','996','0');
INSERT INTO [t14381] VALUES (999,'حل شبهة أن ترك بعض الأخبار على ظاهرها دون تأويل يسلط أعداء الإسلام عليه.','2565','996','0');
INSERT INTO [t14381] VALUES (1000,'اليقين الحاصل بالبراهين على صحة خبر الله ورسوله - صلى الله عليه وسلم - يوجب رد الشبهات المخالفة له.','2566','996','0');
INSERT INTO [t14381] VALUES (1001,'حل كل الشبهات حلا يقنع الخصم لا يمكن إلا بتحرى الخصم لطلب الحق وتركه التقليد.','2566','996','0');
INSERT INTO [t14381] VALUES (1002,'رد المؤلف على من كذب أهل الفلك والطبيعة فيما ظنه مخالفا من قولهم لظاهر الوحي.','2567','996','0');
INSERT INTO [t14381] VALUES (1003,'الشرع لبيان أحكام الدين، ولم يأت لتعليم العلوم الكونية، وما يقع منه يكون عرضا.','2567','996','0');
INSERT INTO [t14381] VALUES (1004,'من العلوم الكونية ما لا فائدة في علمه، ومنها ما فيه فائدة، ولكنه لا يتوقف على الوحي.','2568','996','0');
INSERT INTO [t14381] VALUES (1005,'العلوم الكونية متسعة جدا وقد قضى الله تعالى أن يكون ظهورها في أوقات متراخية.','2568','996','0');
INSERT INTO [t14381] VALUES (1006,'من الأحكام الشرعية ما لا يدرك بالنظر، وما يدرك به فهو مظنة الاختلاف وجور الحكام.','2568','996','0');
INSERT INTO [t14381] VALUES (1007,'ليس كل حاكم كاملا في العقل والفهم والنظر حتى يدرك بنظره جميع الأحكام بعدل.','2568','996','0');
INSERT INTO [t14381] VALUES (1008,'اجتماع جماعة العقلاء لوضع القوانين لا يكفي؛ لقصر نظرهم، واحتمال ميلهم وتعصبهم.','2568','996','0');
INSERT INTO [t14381] VALUES (1009,'غالب القوانين الوضعية تختل الحكمة المقصودة منها في كثير من الجزئيات الداخلة فيها.','2568','996','0');
INSERT INTO [t14381] VALUES (1010,'القوانين الشرعية يؤمن فيها الغلط والميل والعصبية ويقبلها الناس طيبة بها نفوسهم.','2569','996','0');
INSERT INTO [t14381] VALUES (1011,'أمثلة على حصول الظن والخطأ من النبي - صلى الله عليه وسلم - عند الكلام على بعض أمور الدنيا.','2569','996','0');
INSERT INTO [t14381] VALUES (1012,'مناقشة المؤلف للطحاوي في حمل همه - صلى الله عليه وسلم - النهي عن الغيل على الظن من ثلاثة وجوه.','2570','996','0');
INSERT INTO [t14381] VALUES (1013,'أمثلة على إتيان الشرع بما يشير لمسائل طبيعية بمعرض ديني إجمالا إن دعت ضرورة إليه.','2572','996','0');
INSERT INTO [t14381] VALUES (1014,'ليس المقصود مما جاء في الشرع من علوم الطبيعة التعريف بكنهها وحقيقتها وكيفيتها، وإنما ورد تنبيها على الآيات والمثلات، وليس من مقتضى هذا جواز أن يكون الواقع ظاهرها.','2573','996','0');
INSERT INTO [t14381] VALUES (1015,'المتكلم يعتني بالمعنى المقصود بالذات، وما ذكر عرضا لا يعتني به، فيوكل تحقيقه إلى موضعه.','2573','996','0');
INSERT INTO [t14381] VALUES (1016,'المسألة إذا ذكرت في غير بابها استطرادا، ثم ذكرت في بابها مع مخالفة فالمعتمد فيها ما في بابها.','2573','996','0');
INSERT INTO [t14381] VALUES (1017,'المتكلم في علم قد يذكر أثنائه قاعدة من علم آخر يكون ظاهر كلامه أنها كلية ولكن لا يعتد بها.','2573','996','0');
INSERT INTO [t14381] VALUES (1018,'قاعدتا: قلب الياء ألفا لتحركها وانفتاح ما قبلها، وحذف أحد الساكنين إذا التقيا= ليستا مطلقتين.','2574','996','0');
INSERT INTO [t14381] VALUES (1019,'الكتب الموضوعة يذكر فيها ما يكون أقرب لفهم المبتدئين، وإن لم يكن صحيحا في نفسه.','2574','996','0');
INSERT INTO [t14381] VALUES (1020,'على المعلم تجنب ما يشغل أذهان الطلبة عما لا يفيدهم في علمهم، وكان النبي - صلى الله عليه وسلم - يفعل ذلك.','2575','996','0');
INSERT INTO [t14381] VALUES (1021,'قد يخبر الوحي عن علوم الطبيعة بشيء يكون ظاهره مرادا مخالفا للحقيقة وقد لا يكون مرادا.','2575','996','0');
INSERT INTO [t14381] VALUES (1022,'أجاز الجمهور تأخير البيان إلى وقت الحاجة، كورود نص عاما ثم تخصيصه عند العمل.','2575','996','0');
INSERT INTO [t14381] VALUES (1023,'جرى من عادة خطاب الناس بينهم أن مجمل كلامهم أوعمومه يحصل بيانه وقت الحاجة إليه.','2576','996','0');
INSERT INTO [t14381] VALUES (1024,'إذا ورد نص ظاهر على حكم كان ظاهرا لفظا لا معنى، فإن جاء وقت العمل به ولم يبين أنه خلاف الظاهر علم أنه مراد من جهة المعنى أيضا، ولا يعد تأخير بيانه كذبا.','2577','996','0');
INSERT INTO [t14381] VALUES (1025,'أمثلة على أن معرفة الصفة الطبيعية للشيء والاطلاع عليه يحصل به مراد الشارع من كلامه.','2577','996','0');
INSERT INTO [t14381] VALUES (1026,'الرسالة الثانية: حقيقة البدعة','2581','912','0');
INSERT INTO [t14381] VALUES (1027,'مقدمة المؤلف، بذكر سبب تأليف الرسالة، وما عابه على المؤلفات التي سبقته.','2582','1026','0');
INSERT INTO [t14381] VALUES (1028,'زعم صاحب البدعة أن بدعته من الدين يبطله أن الدين وضع إلهي، وضعه الله تعالى، وبلغه النبي - صلى الله عليه وسلم -.','2583','1026','0');
INSERT INTO [t14381] VALUES (1029,'إما أن يعترف المنافح عن بدعة بأنها ليست من الدين، أو يصر فيطلب منه إبراز دليله عليه.','2583','1026','0');
INSERT INTO [t14381] VALUES (1030,'ما يراه صاحب البدعة دليلا على مشروعية بدعته لا يخرج عن أحد أربعة أضرب، وهي','2583','1026','0');
INSERT INTO [t14381] VALUES (1031,'الضرب الأول: ما ليس شبهة دليل عند العلماء، كالاستحسان، والرؤيا، ونحوهما.','2583','1030','0');
INSERT INTO [t14381] VALUES (1032,'الضرب الثاني: ما فيه شبهة دليل عند العامي، وهو تقليده لأهل العلم أوالفضل.','2584','1030','0');
INSERT INTO [t14381] VALUES (1033,'الضرب الثالث: ما يجوز التمسك به، لكنه لم يثبت، أوعارضه قول المجتهد، وهو أولى منه.','2584','1030','0');
INSERT INTO [t14381] VALUES (1034,'الضرب الرابع: ما هو دليل، لكنه لم يثبت أوعارضه أولى منه، كتاب أوسنة أوإجماع أوقياس.','2584','1030','0');
INSERT INTO [t14381] VALUES (1035,'دفع الضرب الأول إجمالا: أن الاستدلال به ليس من الدين الذي بلغه محمد - صلى الله عليه وسلم -.','2584','1026','0');
INSERT INTO [t14381] VALUES (1036,'البرهان على مشروعية البدعة لا بد من أن يكون قطعيا؛ لأن الله قال: {إن الظن لا يغني من الحق شيئا}.','2584','1026','0');
INSERT INTO [t14381] VALUES (1037,'العمل في الفروع بخبر الواحد لا يفيد إلا الظن لكن وجوب العمل بخبر الواحد ثابت قطعا.','2584','1026','0');
INSERT INTO [t14381] VALUES (1038,'ما يستحسنه الإنسان لا يكون من الدين، وتفصيل القول في الاستحسان عند أبي حنيفة ومالك.','2585','1026','0');
INSERT INTO [t14381] VALUES (1039,'رد الاحتجاج بالرؤيا المنامية للتشريع بأنها قد تكون من الشيطان أولها تأويل، وأمثلة عليها.','2586','1026','0');
INSERT INTO [t14381] VALUES (1040,'يرد الاحتجاج للتشريع بالتجربة بأن صحة التجربة ابتلاء أواستدراج، وأمثلة عديدة على ذلك.','2588','1026','0');
INSERT INTO [t14381] VALUES (1041,'بعض الناس يعتمد في أمور دنياه على القرعة والفأل، وقد يغلو فيعتمد مثله للتشريع، وهو باطل.','2593','1026','0');
INSERT INTO [t14381] VALUES (1042,'الاعتماد في التشريع على الفأل من الاستقسام بالأزلام، والبديل الشرعي له صلاة الاستخارة.','2595','1026','0');
INSERT INTO [t14381] VALUES (1043,'من الاحتجاج الباطل بالتجربة العوذ المشتملة على تعظيم الجن ونحوهم، والذبح لهم ونحو ذلك.','2596','1026','0');
INSERT INTO [t14381] VALUES (1044,'نشوء رقاة لا دين لهم أويقين ولا نفع لرقيتهم بالآيات والدعاء = دفع الناس لاسترضاء الشياطين.','2596','1026','0');
INSERT INTO [t14381] VALUES (1045,'قصة مرض ولد المؤلف وزوجه، وموقفه الحازم من عمل شيء محظور شرعا طلبا للعلاج.','2597','1026','0');
INSERT INTO [t14381] VALUES (1046,'الانتفاع حاصل بدعاء الصالحين ورقيتهم، وبالأعمال المحظورة شرعا، وذكر الفرق بينهما في التأثير.','2598','1026','0');
INSERT INTO [t14381] VALUES (1047,'الصرع ليس من فعل الشيطان، بل يعرض لمن يعتريه ما يضعف عقله فتتضاعف عليه عوارضه.','2598','1026','0');
INSERT INTO [t14381] VALUES (1048,'دفع الاستدلال بالضرب الثاني: وهو تقليد أهل العلم أوالفضل، وفيه شبهة دليل عند العامي.','2600','1026','0');
INSERT INTO [t14381] VALUES (1049,'لا يجوز احتجاج العامي بعالم أوصالح إن كان مقلدا لا مجتهدا، بنص العلماء على حرمة الافتاء له.','2600','1026','0');
INSERT INTO [t14381] VALUES (1050,'عدم نقل استحباب أمر عن الأئمة وسكوتهم عنه كاف في الحجة؛ لأن ما لا يستحب لا يتناهى.','2600','1026','0');
INSERT INTO [t14381] VALUES (1051,'استحسان إمام أمرا قد يكون معذورا فيه، ولكن مقلده لا يعذر بعد بيان بدعيته، لأمور.','2601','1026','0');
INSERT INTO [t14381] VALUES (1052,'اشتهار عمل في جهة لا يصلح حجة على استحسان البدع بها، حتى عمل أهل المدينة ليس بمسلم.','2603','1026','0');
INSERT INTO [t14381] VALUES (1053,'المبتدعة أربعة أقسام','2604','1026','0');
INSERT INTO [t14381] VALUES (1054,'الأول: من يعلم بدعية فعله ويزعم أن الشارع يحبه، فقد جمع كذبا وتشريعا.','2604','1053','0');
INSERT INTO [t14381] VALUES (1055,'القسم الثاني: من يشك في بدعية فعله ولكنه يجزم أنها من دين الإسلام، فهو كالقسم الأول.','2606','1053','0');
INSERT INTO [t14381] VALUES (1056,'القسم الثالث: من يجزم أن بدعته من دين الإسلام، وليس عنده برهان عليه، وهذا ثلاثة أضرب','2606','1053','0');
INSERT INTO [t14381] VALUES (1057,'الأول: مجتهد بشبهة دليل، فهو معذور إن خفي عليه اختلال شرط أوقيام معارض دون علمه.','2606','1056','0');
INSERT INTO [t14381] VALUES (1058,'متى تبين للمجتهد ـ أو لمقلده ـ خطأ اجتهاده المعذور فيه ابتداء = فأصر على رأيه فهو هالك.','2606','1057','0');
INSERT INTO [t14381] VALUES (1059,'الثاني: من لم يبلغ درجة الاجتهاد وينظر في الأدلة ويحكم دون موافقة مجتهد، فهذا ضال مضل.','2607','1056','0');
INSERT INTO [t14381] VALUES (1060,'أكثر البدع من اختراع العباد، الذين لا يعتد بأقوالهم، إذ لم يكونوا في العلوم بدرجة الاجتهاد.','2608','1059','0');
INSERT INTO [t14381] VALUES (1061,'الثالث: من يقيس على نصوص المجتهدين ويستنبط منها، وهو الذي يسمى «مجتهد المذهب».','2608','1056','0');
INSERT INTO [t14381] VALUES (1062,'الاستنباط من نص المجتهد تحصيل لدلالات ظنية قد تضعف جدا، كعموم فاته فيه بعض أفراده.','2609','1061','0');
INSERT INTO [t14381] VALUES (1063,'عموم نصوص الله ورسوله - صلى الله عليه وسلم - يشمل الصور النادرة؛ لأنه مبني على علم معصوم بخلاف غيرهما.','2609','1061','0');
INSERT INTO [t14381] VALUES (1064,'مناقشة ومنع الاستنباط من نص المجتهد بدلالة ظنية، كالإشارة ومفهوم الموافقة والمخالفة.','2609','1061','0');
INSERT INTO [t14381] VALUES (1065,'مناقشة ورد استحسان البدع والمحدثات بناء على القياس على نصوص مجتهدي المذاهب.','2611','1061','0');
INSERT INTO [t14381] VALUES (1066,'قياس مجتهد المذهب على نص إمامه المستند في اجتهاده على قياس= باطل؛ لأنه قياس على قياس.','2612','1061','0');
INSERT INTO [t14381] VALUES (1067,'أكثر مسائل الفروع لا من نص إمام المذهب ولا مستنبطة من كلامه، بل كل متأخر يستنبط ممن قبله!','2612','1061','0');
INSERT INTO [t14381] VALUES (1068,'من أسباب استحسان البدع: ميل العالم لأهل البدع، أو لأهل الدنيا، أو منافسة علماء عصره.','2613','1061','0');
INSERT INTO [t14381] VALUES (1069,'الشريعة كجداول نبعت من جبل، فيها ما صفي وفيها ما كدر، فمن لم يقدر على المنبع فليحتط.','2613','1061','0');
INSERT INTO [t14381] VALUES (1070,'استنباط المتأهل للاستنباط من المذاهب جائز إن اضطر إليه ولم يقدر على تحصيل ما هو أوثق منه.','2614','1061','0');
INSERT INTO [t14381] VALUES (1071,'قد يكون المستحسن لبدعة خيرا في نفسه صالحا وليا لله، ولكن لا يلزم من ذلك عصمته عن الخطأ.','2615','1061','0');
INSERT INTO [t14381] VALUES (1072,'لا يلزم من كون المجتهد معذورا مأجورا في اجتهاده أن يكون كل من وافقه على ذلك كذلك.','2615','1061','0');
INSERT INTO [t14381] VALUES (1073,'الرسالة الثالثة: صدع الدجنة في فصل البدعة عن السنة','2617','912','0');
INSERT INTO [t14381] VALUES (1074,'المقدمة، فضل رسالة نبينا - صلى الله عليه وسلم - وإكمال الدين وحفظه، وفضل السلف، ثم وقوع البدع والفساد.','2618','1073','0');
INSERT INTO [t14381] VALUES (1075,'نشوء الفساد برؤساء جهال، وإماتة السنن وإقامة البدع، وعلاجه بالعلم والصبر والحكمة.','2619','1073','0');
INSERT INTO [t14381] VALUES (1076,'المؤلفات في البدع، والاعتصام للشاطبي كبير لا يناسب العامة، ومراد المؤلف البيان والإيجاز.','2621','1073','0');
INSERT INTO [t14381] VALUES (1077,'تعريف السنة لغة واصطلاحا، السنة: كل أمر ثبت بالكتاب أوالسنة طلبه فرضا أوندبا.','2622','1073','0');
INSERT INTO [t14381] VALUES (1078,'تعريف المحدثة لغة واصطلاحا، والمشهور أنها: «ما أحدث في الدين وليس له أصل شرعي».','2623','1073','0');
INSERT INTO [t14381] VALUES (1079,'الاعتراضات على تعريف «المحدثة»: تناوله للمعاصي، وللمباحات، وإجمال معنى «الأصل».','2624','1073','0');
INSERT INTO [t14381] VALUES (1080,'الوصف بالبدعة لا يقع إلا بزعم أنها من الدين، ولا يقال لمسلم عاص كتارك الصلاة مبتدع.','2624','1073','0');
INSERT INTO [t14381] VALUES (1081,'يجاب عن عدم التصريح بإخراج المعاصي المحدثة بشهرة إخراجها، وذمها على الدوام.','2624','1073','0');
INSERT INTO [t14381] VALUES (1082,'إن أريد بـ «الأصل»: مستند لا يصلح للاستناد فلا يصح؛ إذ كل البدع يتشبث فيها أهلها بمستند.','2625','1073','0');
INSERT INTO [t14381] VALUES (1083,'وإن أريد بـ «الأصل»: مستند يصلح للاستناد فلا يصح؛ إذ المحدث لم يكن موجودا في عهده - صلى الله عليه وسلم -.','2625','1073','0');
INSERT INTO [t14381] VALUES (1084,'ما ترك النبي - صلى الله عليه وسلم - استعماله قبل وجوده كالبواخر والمدافع لا يسمى محدثة لكونه لم يوجد ليترك.','2625','1073','0');
INSERT INTO [t14381] VALUES (1085,'ذكر تعريف ابن حجر المكي «ما لم يقم دليل شرعي على أنه وجوب أواستحباب»، ثم نقده.','2626','1073','0');
INSERT INTO [t14381] VALUES (1086,'تقسيم العلماء للبدعة إلى حسنة وغير حسنة أرادوا بها البدعة اللغوية، لا الشرعية.','2627','1073','0');
INSERT INTO [t14381] VALUES (1087,'تعريف البدعة عند المؤلف «كل أمر ألصق بالدين ولم يكن من هدي النبي - صلى الله عليه وسلم -، لا بالفعل ولا بالقوة».','2627','1073','0');
INSERT INTO [t14381] VALUES (1088,'لفظ «بدعة» الوارد في النصوص باق على معناه اللغوي، وليس المراد به صورة الفعل ولكن حكمه.','2628','1073','0');
INSERT INTO [t14381] VALUES (1089,'توجيه ما نقل عن السلف في إطلاقهم لفظ «بدعة» على الأفعال نفسها، كالقنوت في الفجر.','2628','1073','0');
INSERT INTO [t14381] VALUES (1090,'الرسالة الرابعة: الحنيفية والعرب','2631','912','0');
INSERT INTO [t14381] VALUES (1091,'الحنيفية ملة إبراهيم عليه السلام، وبقيت بعده في ابنيه إسماعيل وإسحاق وذريتهما، بتباين أمرهما.','2632','1090','0');
INSERT INTO [t14381] VALUES (1092,'أخبار بني إسرائيل مع موسى تدل على ضعف دينهم جدا بعد وفاة يوسف مع قرب العهد بينهما.','2632','1090','0');
INSERT INTO [t14381] VALUES (1093,'أرسل الله موسى لبني إسرائيل بالتوراة وبشريعة وبأنبياء بعده، ومع ذلك لم يستقر الدين فيهم!','2632','1090','0');
INSERT INTO [t14381] VALUES (1094,'بعد سكنى إسماعيل بمكة وبناء الكعبة ومصاهرته للعرب ذاعت الحنيفية ورسخت فيهم.','2633','1090','0');
INSERT INTO [t14381] VALUES (1095,'بقي الدين الحق في العرب فوق عشرين قرنا بعد إبراهيم، ثم غيروا أشياء حتى بعث نبينا - صلى الله عليه وسلم -.','2633','1090','0');
INSERT INTO [t14381] VALUES (1096,'سرد جملة من النصوص من كتب أهل الكتاب تؤكد على ثبات العرب على دينهم، ثم مناقشها.','2633','1090','0');
INSERT INTO [t14381] VALUES (1097,'أمثلة لمدح بني «قيدار» أبناء إسماعيل في كتب أهل الكتاب بعدم التبديل والثبات على الدين.','2634','1090','0');
INSERT INTO [t14381] VALUES (1098,'الطابعون للكتاب المقدس يحاولون إخفاء دلالة ثبات بني قيدار، بإغفال التنبيه على المراد بهم.','2636','1090','0');
INSERT INTO [t14381] VALUES (1099,'«قيذر» هو نفسه «قيدار»، و «النبيت» أولاد ابن آخر لإسماعيل، ويسمى «نبت» أو «نبايوت».','2637','1090','0');
INSERT INTO [t14381] VALUES (1100,'بحث تاريخي في أصل قوم «النبط» أو «النبيط» أو «الأنباط» الذين كانوا بالعراق والشام.','2637','1090','0');
INSERT INTO [t14381] VALUES (1101,'قد يسمى «ابن قيذر» باسم عمه، فـ «عدنان» من ولد «قيذر»، ونبيط الشام والعراق من «نبايوت».','2639','1090','0');
INSERT INTO [t14381] VALUES (1102,'ثلاثة أدلة للشيخ رحمة الله على أن المقصود بالعاقر في كتب أهل الكتاب مكة لا القدس.','2640','1090','0');
INSERT INTO [t14381] VALUES (1103,'رد القول بأن لفظة المتوحشة وذات البعل هما «هاجر وسارة»، وتصويب أن المقصود «مكة والقدس».','2641','1090','0');
INSERT INTO [t14381] VALUES (1104,'ثبت بنو قيدار على الدين الخالص بعد أرميا بضعة قرون؛ وأول من غير دينهم عمرو بن لحي.','2641','1090','0');
INSERT INTO [t14381] VALUES (1105,'التحقيق في نسب عمرو بن لحي، وزمانه، والمدة التي كانت بينه وبين بعثة النبي - صلى الله عليه وسلم -.','2642','1090','0');
INSERT INTO [t14381] VALUES (1106,'عبد بنو إسرائيل العجل بعد إبراهيم بنحو ستمائة سنة، وموسى وهارون بين أظهرهم!','2644','1090','0');
INSERT INTO [t14381] VALUES (1107,'تخصيص بني إسرائيل دون بني إسماعيل بكثرة الأنبياء إنما كان لتمردهم لا لفضيلة فيهم','2644','1090','0');
INSERT INTO [t14381] VALUES (1108,'الرسالة الخامسة: عقيدة العرب في وثنيتهم','2645','912','0');
INSERT INTO [t14381] VALUES (1109,'استغراب المؤلف من جهل حقيقة عقيدة العرب في وثنيتها، مع ذكرها في آيات كثيرة في القرآن.','2646','1108','0');
INSERT INTO [t14381] VALUES (1110,'أهمية معرفتها أن آيات كثيرة جاءت لنقض هذه العقيدة، فمن لم يعرفها صعب عليه فهم الآيات.','2646','1108','0');
INSERT INTO [t14381] VALUES (1111,'١ - كانت العرب تعتقد بوجود الله وربوبيته، وأنه الذي يدبر ويخلق، وذكر الأدلة على ذلك.','2646','1110','0');
INSERT INTO [t14381] VALUES (1112,'يرى الطبري أن مجاهدا كان يرى أن العرب جحدت وحدانية الله، وأنه خالقها ورازقها!','2648','1111','0');
INSERT INTO [t14381] VALUES (1113,'٢ - كانت العرب تجمع بين الإيمان والشرك، وذكر الأدلة والأمثلة على ذلك.','2649','1110','0');
INSERT INTO [t14381] VALUES (1114,'٣ - وقعت العرب في أنواع من الكفر، مآلها أمران: القول بأن الملائكة بنات الله، وعبادتهم غيره.','2651','1110','0');
INSERT INTO [t14381] VALUES (1115,'قرع القرآن العرب بنسبتهم الولد إلى الله، وبجعل ذلك الولد إناثا، وتارة بقولهم: الملائكة إناث.','2651','1114','0');
INSERT INTO [t14381] VALUES (1116,'السبب الباعث على قول العرب: «الملائكة بنات الله»، أحد أربعة أمور، وذكرها.','2651','1114','0');
INSERT INTO [t14381] VALUES (1117,'يطلق أهل الكتاب عبارة «أبناء الله» على بعض الموجودات، وتعني بها: المختارين لله.','2652','1114','0');
INSERT INTO [t14381] VALUES (1118,'تنزيه الله عن العقر ومشاركة ذكر ينازعه في الملك وذم الجن = جعلهم يرون أن الملائكة بنات الله.','2652','1114','0');
INSERT INTO [t14381] VALUES (1119,'تفسير الجنة في قوله: {وجعلوا بينه وبين الجنة نسبا} أنها الملائكة هو قول جماعة من السلف.','2654','1114','0');
INSERT INTO [t14381] VALUES (1120,'ذم الله في كتابه العرب على عبادتهم الملائكة، أوعبادتهم إناثا، أوما لا وجود له ألبتة، أوالشياطين.','2654','1114','0');
INSERT INTO [t14381] VALUES (1121,'من عادة الشيطان التعرض للعبادات الباطلة؛ ليكون معبودا ولو كان ذلك في الصورة.','2656','1114','0');
INSERT INTO [t14381] VALUES (1122,'ذم الله العرب في عبادتهم: عبادتهم الجن، وعبادة رؤساءهم، وعبادة أهواءهم، وعبادة الأوثان.','2657','1114','0');
INSERT INTO [t14381] VALUES (1123,'أكثر أهل العلم على أن عبادة الشياطين طاعتهم لهم، والتحقيق أنها طاعة خاصة في شرع الدين.','2657','1114','0');
INSERT INTO [t14381] VALUES (1124,'٤ - كيفية دخول الأوثان للحجاز، وأن أول من أدخلها عمرو بن لحي، وبعض خبره في ذلك.','2660','1110','0');
INSERT INTO [t14381] VALUES (1125,'٥ - المنشأ في نصب الأصنام عند عبادها جعلهم إياها صورا للملائكة ولمن يعظمونه في الحقيقة.','2661','1110','0');
INSERT INTO [t14381] VALUES (1126,'٦ - ما «اللات والعزى ومناة» التي عبدتها العرب؟ واشتقاق المشركين أسماء آلهتهم من أسماء الله.','2662','1110','0');
INSERT INTO [t14381] VALUES (1127,'٧ - الذي رجته العرب من عبادة الملائكة أنها تشفع لهم عند الله، مع أنهم لا يثبتون لها تصرفا.','2666','1110','0');
INSERT INTO [t14381] VALUES (1128,'الرسالة السادسة: الرد على حسن الضالعي','2668','912','0');
INSERT INTO [t14381] VALUES (1129,'مقدمة الرسالة، وسبب تأليفها طلب بعض إخوانه، ونقده لمن سبقه ممن رد بأسلوب حاد.','2669','1128','0');
INSERT INTO [t14381] VALUES (1130,'ذكر بداية أمر المردود عليه (حسن الضالعي)، وبيان حقيقة أمره بكلام من عرفه من الثقات.','2671','1128','0');
INSERT INTO [t14381] VALUES (1131,'استدلال الضالعي بالآيات والأحاديث بتحريف معانيها، ودعواه العلم والنسب والتصوف.','2673','1128','0');
INSERT INTO [t14381] VALUES (1132,'قول الضالعي بعقيدة وحدة الوجود، وذكر بعض كلامه الذي نقله من رآه ورد عليه.','2681','1128','0');
INSERT INTO [t14381] VALUES (1133,'ذكر مقتطفات من كتاب «كشف الغطا» وذيله للشيخ سالم باصهي الذي رد به على الضالعي.','2682','1128','0');
INSERT INTO [t14381] VALUES (1134,'اعتراف الضالعي باعتقاده معتقد محيي الدين ابن عربي وعبد الكريم الجيلي في وحدة الوجود.','2683','1128','0');
INSERT INTO [t14381] VALUES (1135,'من اعتقد عقيدة وحدة الوجود فإنه كافر بإجماع المسلمين؛ لأنه مكذب بآيات القرآن.','2683','1128','0');
INSERT INTO [t14381] VALUES (1136,'مقتطفات من رسالة الشيخ عبدالله بن طاهر إلى الشيخ عبد الله الفوري في حقيقة الضالعي.','2686','1128','0');
INSERT INTO [t14381] VALUES (1137,'مقتطفات من رسالة الشيخ باشيخ في حقيقة الضالعي، وفيها قوله برفع التكاليف عن الناس.','2686','1128','0');
INSERT INTO [t14381] VALUES (1138,'إنكار الضالعي لنبوة محمد - صلى الله عليه وسلم -، ومناقشة لطيفة في طريق إثبات صحة القرآن بإثبات التوراة.','2688','1128','0');
INSERT INTO [t14381] VALUES (1139,'الرد على أسئلته المشككة، كتسمية المسيح، وولادته المعجزة، وبعض ما خصه الله من الآيات.','2691','1128','0');
INSERT INTO [t14381] VALUES (1140,'مناقشة نفيه التحريف عن التوراة بحجة بشارته بالمسيح، ومراد اليهود الخبيث من ترك ذكره.','2694','1128','0');
INSERT INTO [t14381] VALUES (1141,'مناقشته في دعواه ثبوت صحة التوراة والإنجيل بالتواريخ ونفي ذلك عن القرآن الكريم.','2697','1128','0');
INSERT INTO [t14381] VALUES (1142,'الرد عليه في نفيه الإعجاز عن القرآن، بأن العرب عجزت عن التحدي، وذكر أنواع إعجازه.','2697','1128','0');
INSERT INTO [t14381] VALUES (1143,'من وجوه الإعجاز: ما في القرآن من أخبار الأمم السابقة واللاحقة، مع أن نبينا - صلى الله عليه وسلم - كان أميا.','2698','1128','0');
INSERT INTO [t14381] VALUES (1144,'معارضة الشعراء والخطباء حصلت سابقا ولاحقا، بخلاف القرآن فلم ينجح أحد بمعارضته.','2699','1128','0');
INSERT INTO [t14381] VALUES (1145,'مناقشته فيما ذكره من تشكيكه بصحة القرآن؛ بحجة ذكره لمعاصي الأنبياء المنافية لعصمتهم.','2701','1128','0');
INSERT INTO [t14381] VALUES (1146,'مناقشته فيما احتج به للنصارى في مسألة التثليث، بحجة تركب الإنسان من ثلاث حقائق.','2706','1128','0');
INSERT INTO [t14381] VALUES (1147,'مناقشة نفيه وجود آيات تدل على صحة نبوة محمد - صلى الله عليه وسلم - إلا القرآن، وذكر بعض الآيات العقلية.','2707','1128','0');
INSERT INTO [t14381] VALUES (1148,'مقارنة لطيفة بين حال بعثة نبينا محمد - صلى الله عليه وسلم - إلى أهل مكة مع بعثة موسى وعيسى إلى قوميهما.','2709','1128','0');
INSERT INTO [t14381] VALUES (1149,'من أعظم آيات صدق نبوته - صلى الله عليه وسلم - صدق قوله، وتجرده عن الهوى وعن طلب ملاذ الدنيا.','2712','1128','0');
INSERT INTO [t14381] VALUES (1150,'ذكر بعض آياته - صلى الله عليه وسلم - المشهودة؛ كبركة الطعام، ونبع الماء بين كفيه، وانشقاق القمر، وغيرها.','2714','1128','0');
INSERT INTO [t14381] VALUES (1151,'نقل مسهب عن ابن حزم ذكر فيه أمثلة مستفيضة في التحريف الواقع في التوراة والإنجيل.','2715','1128','0');
INSERT INTO [t14381] VALUES (1152,'اختلاف يهود السامرية وغيرهم في نسخ التوراة التي عندهم، ودعوى كل منهما تحريف الآخر.','2715','1128','0');
INSERT INTO [t14381] VALUES (1153,'من تناقض التوراة اختلافها في مكان ولادة بنيامين بن يعقوب، ومدة بقاء بني إسرائيل بمصر.','2716','1128','0');
INSERT INTO [t14381] VALUES (1154,'من تحريف التوراة المبالغة بذكر عدد المدن التي عمرها بنوإسرائيل وعدد أبنائه بعد موته.','2718','1128','0');
INSERT INTO [t14381] VALUES (1155,'من إقحام ما ليس في التوراة ما فيها من ذكر موضع وفاة موسى وسنه وخفاء قبره بعد موته.','2719','1128','0');
INSERT INTO [t14381] VALUES (1156,'نقل ابن حزم في الفصل اتفاق كل النصارى على أن ما بين أيديهم ليس هو كتاب الله المنزل.','2720','1128','0');
INSERT INTO [t14381] VALUES (1157,'إلزام النصارى في زعمهم التصديق بالتوراة مع ما فيها من مناقضة لما في كتبهم، كعمر الدنيا.','2721','1128','0');
INSERT INTO [t14381] VALUES (1158,'التناقض البين في عدة مواضع من نسخ التوراة التي عندهم، والنصارى يقرون بالنسختين!','2722','1128','0');
INSERT INTO [t14381] VALUES (1159,'ذكر أمثلة عديدة يظهر بها التحريف والتناقض بين أناجيل النصارى التي بين أيديهم.','2723','1128','0');
INSERT INTO [t14381] VALUES (1160,'الجواب عن الشبه التي استدل بها من الكتاب والسنة بزعم إقرارها بصحة كتب بني إسرائيل.','2725','1128','0');
INSERT INTO [t14381] VALUES (1161,'ذكر بعض الشبه التي استدل بها من الكتاب والسنة بزعم إقرارها بصحة كتب بني إسرائيل.','2725','1128','0');
INSERT INTO [t14381] VALUES (1162,'إقرار المسلمين بصحة نزول التوراة والإنجيل لا يتعارض مع اعتقادهم بتحريفها وتبديلها.','2725','1128','0');
INSERT INTO [t14381] VALUES (1163,'الاستشهاد من التوراة والإنجيل ليس إقرارا بصحتها كلها بل من باب إلزامهم بما يؤمنون.','2728','1128','0');
INSERT INTO [t14381] VALUES (1164,'بطلان حديث: «آمنت بما فيك»، ومعنى قوله: {لستم على شيء حتى تقيموا التوراة والإنجيل}','2729','1128','0');
INSERT INTO [t14381] VALUES (1165,'معنى قوله: {قل فأتوا بالتوراة فاتلوها إن كنتم صادقين} أنها من باب إلزامهم بما يؤمنون.','2730','1128','0');
INSERT INTO [t14381] VALUES (1166,'معنى قوله: {يحكم بها النبيون} يحكم أنبيائهم بها قبل تحريفها، وليس فيه نفي للتحريف.','2730','1128','0');
INSERT INTO [t14381] VALUES (1167,'معنى قوله: {وليحكم أهل الإنجيل بما أنزل الله فيه} أمر بالحكم بما أنزل في الإنجيل الصحيح.','2732','1128','0');
INSERT INTO [t14381] VALUES (1168,'قوله: {ياأيها الذين أوتوا الكتاب آمنوا بما نزلنا مصدقا لما معكم} عموم أريد به الخصوص.','2732','1128','0');
INSERT INTO [t14381] VALUES (1169,'ذكر أمثلة مستفيضة من صور التحريف والتناقض الموجود في نسخ التوراة التي بين أيديهم','2732','1128','0');
INSERT INTO [t14381] VALUES (1170,'المقصود بأنصار الله الممدوحين من النصارى في القرآن هم المتبعون لعيسى، وليس المحرفين.','2734','1128','0');
INSERT INTO [t14381] VALUES (1171,'الرسالة السابعة: ما وقع لبعض المسلمين من الرياضة الصوفية والغلو فيها','2736','912','0');
INSERT INTO [t14381] VALUES (1172,'من قوى النفس البشرية «العين»، وذكر ما ورد في إثبات صحتها من الكتاب والسنة.','2737','1171','0');
INSERT INTO [t14381] VALUES (1173,'الكلام عن «التنويم المغناطيسي» والتسليم بها عند فلاسفة العصر، وبيان معناها وتأثيرها.','2738','1171','0');
INSERT INTO [t14381] VALUES (1174,'التفريق في حكم الإصابة بالعين بين ما يحصل اكتسابا، وما يحصل دون اكتساب اختياري.','2739','1171','0');
INSERT INTO [t14381] VALUES (1175,'من قوى النفس ما للرقاة من الحية والعقرب من الأثر على الشفاء بتلاوتهم بألفاظ لا معنى لها.','2740','1171','0');
INSERT INTO [t14381] VALUES (1176,'بعض أنواع قوى النفس البشرية ما يمكن حصوله اكتسابا وارتياضا، كارتياض البدن.','2740','1171','0');
INSERT INTO [t14381] VALUES (1177,'بعض الوسائل التي يحصل بها الارتياض لكسب قوى النفس، كالتجويع والتأمل ونحوهما.','2741','1171','0');
INSERT INTO [t14381] VALUES (1178,'القوى النفسية المكتسبة بالرياضة معروفة عند قدماء اليونان والهنود وغيرهم.','2741','1171','0');
INSERT INTO [t14381] VALUES (1179,'لم تلق الرياضات الوثنية معارضة كما تلقت الفلسفة ذلك؛ لأن في الإسلام ما يشبهها جملة.','2741','1171','0');
INSERT INTO [t14381] VALUES (1180,'من الرياضات الإسلامية التي شابهت غيرها الصيام والقيام، واعتزال الناس خشية الفتنة.','2741','1171','0');
INSERT INTO [t14381] VALUES (1181,'وجود زهاد التابعين وغيرهم ممن بالغ في العبادات الإسلامية سبب فشو الرياضات الوثنية.','2741','1171','0');
INSERT INTO [t14381] VALUES (1182,'من أسباب فشو الرياضات الوثنية أن الناقلين لها تلطفوا في إدراجها في العبادات الشرعية.','2742','1171','0');
INSERT INTO [t14381] VALUES (1183,'أغراض المرتاضين مختلفة إما لإضعاف شهوة أومحاولة كشف أولغرض سياسي أولغير ذلك.','2742','1171','0');
INSERT INTO [t14381] VALUES (1184,'لا يشترط متأخروا العارفين بحقيقة تلك الرياضات دينا أو مذهبا خاصا في طلبها أو تعليمها!','2743','1171','0');
INSERT INTO [t14381] VALUES (1185,'نهى الشرع عما يقرب من الغلو في العبادة كصيام الدهر وقيام جميع الليل، وعن تعاطي السحر.','2744','1171','0');
INSERT INTO [t14381] VALUES (1186,'يعترف المتصوفة بولع الشياطين بمن ارتاض بطريقتهم فتخدمه وتحصل له قوة السحر.','2744','1171','0');
INSERT INTO [t14381] VALUES (1187,'من تأثير النفوس سحر الأبصار الذي ذكره الله، وذكر جملة من القصص في ذلك.','2745','1171','0');
INSERT INTO [t14381] VALUES (1188,'سحر الأبصار إما أن يكون لها حقيقة فترى ما لم يكن، أو سحر للأدمغة فتتخيل ما لم يكن.','2747','1171','0');
INSERT INTO [t14381] VALUES (1189,'التنويم المغناطيسي هو من جنس سحر الأدمغة، فيخيل للمنوم ما لا وجود له في الحقيقية.','2748','1171','0');
INSERT INTO [t14381] VALUES (1190,'الجواب عما يقال من أن القول بسحر الأدمغة يفقد الثقة بالمحسوسات وعذر منكري المعجزات.','2748','1171','0');
INSERT INTO [t14381] VALUES (1191,'ذكر مجاورة النبي - صلى الله عليه وسلم - وخلوته بحراء للتعبد شهرا كل عام، ونزول الوحي عليه برمضان فيه.','2749','1171','0');
INSERT INTO [t14381] VALUES (1192,'المتقرر في الشريعة صيام رمضان واعتكاف العشر الأواخر منه في أي مسجد، وأحكامه معروفة.','2750','1171','0');
INSERT INTO [t14381] VALUES (1193,'خلوة الصوفية الأربعينية لا حجة شرعية لها، وقد يحتج لها بالرؤى أوالإلهام أوالكشف أونحوها.','2751','1171','0');
INSERT INTO [t14381] VALUES (1194,'احتجاج هؤلاء بما ليس نصا شرعيا على بدعهم يرد بأمور','2751','1171','0');
INSERT INTO [t14381] VALUES (1195,'الأول: أن ما ليس نصا في الاحتجاج فمنه ما نفته الشريعة، ومنه ما لا يعلم إثباته أو نفيه، ومنه ما ثبت جملة، ثم تفصيل أحكامها.','2751','1194','0');
INSERT INTO [t14381] VALUES (1196,'الثاني: ما يصح جملة مما ادعي دليلا قد يشتبه بتضليل الشيطان والهوى والتخيل ونحو ذلك.','2752','1194','0');
INSERT INTO [t14381] VALUES (1197,'الثالث: ما أوضح الله لعباده من طريق يعرف بها الحق معصوم جملة، وغير ذلك فليس بمعصوم.','2753','1194','0');
INSERT INTO [t14381] VALUES (1198,'مناقشة القول بحرمة قتال الكفار وإيذائهم بما لم تجر العادة في قتالهم به، كالقوى النفسية فيه!','2754','1171','0');
INSERT INTO [t14381] VALUES (1199,'مناقشة القول بحرمة التصرف بالقوى النفسية إلا بإذن أهل الديوان، لأنهم المقررون لقضاء الله!','2756','1171','0');
INSERT INTO [t14381] VALUES (1200,'تغليط القول بأن تلك القوى من كرامة الله لمكتسبها، والخلط بينها وبين المعجزات والكرامات.','2757','1171','0');
INSERT INTO [t14381] VALUES (1201,'القوى النفسية المكتسبة بالشياطين لا يمكن استخدامها إلا بإذن قدري إلهي، وله في ذلك حكم.','2758','1171','0');
INSERT INTO [t14381] VALUES (1202,'آيات الأنبياء غير مكتسبة، وإنما هي إمداد من الله لهم لنصرة دعوتهم، وقد يبذلون سببا لحصولها.','2760','1171','0');
INSERT INTO [t14381] VALUES (1203,'مناقشة دعوى أن ترك السلف لهذه الرياضات لصفاء نفوسهم، وذكر سبب وقوعها للمسلمين.','2762','1171','0');
INSERT INTO [t14381] VALUES (1204,'سرد تاريخي لكيفية تسلل هذه الرياضات إلى عباد المسلمين من طريق الزهد والتقلل من الدنيا.','2763','1171','0');
INSERT INTO [t14381] VALUES (1205,'التطور في القرنين الثاني والثالث بتقصد الجوع ونحوه، وخلط تصوفهم بما عند الأمم الكافرة.','2768','1171','0');
INSERT INTO [t14381] VALUES (1206,'مناقشة جعل (الجوع) من أركان الرياضة، وإلصاقه بالدين، والاحتجاج له بما لا حجة فيه.','2770','1171','0');
INSERT INTO [t14381] VALUES (1207,'الكلام عن إسناد وفقه حديث: «بحسب ابن آدم لقيمات» وتحقيق سماع يحيى بن جابر من المقدام.','2771','1171','0');
INSERT INTO [t14381] VALUES (1208,'زيادة: «ثلاث» عند الحاكم منكرة في الحديث، والضبط الصحيح لـ «أكلات» بضم الألف والكاف.','2772','1171','0');
INSERT INTO [t14381] VALUES (1209,'يمكن للإنسان ضبط ثلث الطعام بأمرين: ترك استيفاء شهوته من طعامه، تقدير ما يثقل به منه.','2773','1171','0');
INSERT INTO [t14381] VALUES (1210,'مناقشة جعل (السهر) من أركان الرياضة، وإلصاقه بالدين، والاحتجاج له بما لا حجة فيه.','2775','1171','0');
INSERT INTO [t14381] VALUES (1211,'قيام الليل ليس مقصودا من السهر، والمقصود العبادة بالصلاة والذكر، والسهر المجرد لا فضل فيه.','2775','1171','0');
INSERT INTO [t14381] VALUES (1212,'أفضل القيام حدده - صلى الله عليه وسلم - بقوله: «أفضل القيام قيام داود كان ينام نصف الليل ويقوم ثلثه وينام سدسه».','2776','1171','0');
INSERT INTO [t14381] VALUES (1213,'ليس المقصود بالليل من غروب الشمس، بل هو وقت القيام، ما بعد الفراغ من العشاء للفجر.','2776','1171','0');
INSERT INTO [t14381] VALUES (1214,'مناقشة جعلهم ترك أكل ذي الروح وما يخرج منه من الرياضة، واحتجاجهم له بما لا حجة فيه.','2777','1171','0');
INSERT INTO [t14381] VALUES (1215,'إبطال احتجاجهم بأثر عمر رضي الله عنه قال: «إن لهذا اللحم ضراوة كضرواة الخمر» من ثلاثة وجوه.','2778','1171','0');
INSERT INTO [t14381] VALUES (1216,'إبطال قولهم إن المرتاض إذا حصل له الفتح تحصل له القوة النفسية المذكورة، وهي غير شرعية.','2778','1171','0');
INSERT INTO [t14381] VALUES (1217,'الرسالة الثامنة: الشفاعة','2781','912','0');
INSERT INTO [t14381] VALUES (1218,'مقدمة الرسالة، بيان تفاوت الخلق في مسائل الحق ما بين مشرق ومغرب، ومنها مسألة الشفاعة.','2782','1217','0');
INSERT INTO [t14381] VALUES (1219,'المشايخ والقصاص أشد ترخيصا للشفاعة الباطلة، وأحوال المنتسبين منهم للعلم وحظه منه.','2784','1217','0');
INSERT INTO [t14381] VALUES (1220,'من المرخصين للشفاعة الباطلة من أخلد إلى ما شاع؛ خشية أن يكون خلافه هلاكا في دينه ودنياه.','2784','1217','0');
INSERT INTO [t14381] VALUES (1221,'سبب تصديه للكتابة فيها عدم معرفته لمن حققها كلها، وترابطها بـ «العبادة» التي كتب فيها.','2786','1217','0');
INSERT INTO [t14381] VALUES (1222,'معنى الشفاعة واشتقاقها لغة، وأكثر استعمالها ممن هو أعلى حرمة ومرتبة إلى من هو أدنى.','2787','1217','0');
INSERT INTO [t14381] VALUES (1223,'ليس للشافع أن يغضب على المشفوع إليه إذا أبى قبول الشفاعة، وإلا لم يكن شافعا بل كان آمرا.','2787','1217','0');
INSERT INTO [t14381] VALUES (1224,'لا يشترط في الشفاعة كونها من أدنى لأعلى، ولكن يشترط لها أن لا يكون الشافع مالكا للحاجة.','2787','1217','0');
INSERT INTO [t14381] VALUES (1225,'والشفاعة عند الله تعالى أقسام، منها شفاعة لإنسان في الدنيا لحي أو ميت، وهي المسماة (دعاء).','2788','1217','0');
INSERT INTO [t14381] VALUES (1226,'ذكر الاتفاق على جواز طلب الدعاء من الحي في الدنيا، بمخاطبته أوالكتابة إليه أو نحو ذلك.','2788','1217','0');
INSERT INTO [t14381] VALUES (1227,'كره بعض العلماء طلب الدعاء، والأصل عند المؤلف الجواز، ويكره أو يخالف الأولى لعارض.','2788','1217','0');
INSERT INTO [t14381] VALUES (1228,'من عوارض الأصل ووجوه كراهة طلب الدعاء كون الحاجة دنيوية غير ضرورية، وبيان ذلك.','2789','1217','0');
INSERT INTO [t14381] VALUES (1229,'فضائل دعاء العبد لنفسه، ككون دعائه عبادة يؤجر عليها، وأنه موعود بإجابة طلبه أو خير منه.','2790','1217','0');
INSERT INTO [t14381] VALUES (1230,'من أسباب عدم وثوق العبد بدعاء نفسه إصراره على الذنوب، وعدم تركها مع رجاء الإجابة!','2791','1217','0');
INSERT INTO [t14381] VALUES (1231,'أكثر من يطلب منه الدعاء يفرح به ولا يعظ السائل؛ طمعا في شيء من الدنيا يأتيه من الطالب.','2791','1217','0');
INSERT INTO [t14381] VALUES (1232,'جواز طلب الدعاء لحاجة دنيوية ضرورية، أو حاجة عامة للمسلمين، وأمثلة على ذلك.','2792','1217','0');
INSERT INTO [t14381] VALUES (1233,'من عوارض الجواز، كراهة طلب الدعاء من الغير لحصول مشقة على المسؤول أو إساءة ظن به.','2792','1217','0');
INSERT INTO [t14381] VALUES (1234,'يكره طلب الدعاء خشية العجب على المسؤول، أوخشية الغلو فيه، أو اتكال السائل عليه.','2793','1217','0');
INSERT INTO [t14381] VALUES (1235,'قد يفيد طلب الدعاء الاستحباب، وبيان سبب طلب النبي - صلى الله عليه وسلم - الدعاء من عمر.','2794','1217','0');
INSERT INTO [t14381] VALUES (1236,'المبحث الثاني: أمور ينبغي للمطلوب منه الدعاء، بعضها متعلقة بالسائل وبعضها متعلقة به.','2796','1217','0');
INSERT INTO [t14381] VALUES (1237,'الرسالة التاسعة: التفضيل بين الخلفاء الأربعة','2798','912','0');
INSERT INTO [t14381] VALUES (1238,'التفضيل بين الخلفاء إن كان بتشييد الدين ونفع المسلمين وورود الأدلة فهم مشتركون فيه في الجملة.','2799','1237','0');
INSERT INTO [t14381] VALUES (1239,'التفضيل بين الخلفاء الأربعة إن كان بحسب منزلتهم عند الله فهو من الغيب الذي لا يعلمه إلا هو.','2799','1237','0');
INSERT INTO [t14381] VALUES (1240,'الفضائل عرض لها التعصبات، بدفن بعضها واختلاق بعضها الآخر.','2799','1237','0');
INSERT INTO [t14381] VALUES (1241,'لم يكن الخلفاء الأربعة مشتغلين بالتفاضل، بل كانوا يغمطون أنفسهم ويفضل كل غيره عليه.','2800','1237','0');
INSERT INTO [t14381] VALUES (1242,'كان السلف لا يهمهم إلا تعظيم الجميع، وإن قال به بالظن لم يعنف مخالفه فيه.','2800','1237','0');
INSERT INTO [t14381] VALUES (1243,'حصول بعض الوحشة بين الخلفاء ليس بمحدث فما زال جاريا بين الأخيار ولم يسلم منه الأنبياء.','2801','1237','0');
INSERT INTO [t14381] VALUES (1244,'ومع الوحشة التي حصلت بين الأخيار فقد كانوا سليمي الصدور تجاه بعضهم.','2801','1237','0');
INSERT INTO [t14381] VALUES (1245,'تفضيل الخلفاء بحسب ترتيبهم في الخلافة ليس قطعيا؛ إذ الإمامة كالأمارة يولاها الأصلح لها.','2802','1237','0');
INSERT INTO [t14381] VALUES (1246,'ذكر أربعة وجوه في تأخر خلافة علي مع فضائله عن الثلاثة الآخرين.','2802','1237','0');
INSERT INTO [t14381] VALUES (1247,'جرت حكمة الله أن ينبأ الأنبياء ببلوغ الأربعين، وعلي حين وفاته - صلى الله عليه وسلم - ووفاة أبي بكر لم يكن بلغها.','2802','1237','0');
INSERT INTO [t14381] VALUES (1248,'من حكمة الله في تأخر خلافة علي ما قد يترتب من مفاسد ترجح على المصالح المرجوة من توليه لها.','2802','1237','0');
INSERT INTO [t14381] VALUES (1249,'من مفاسد تولي علي للخلافة بعد وفاته - صلى الله عليه وسلم - ظن أهل الكتاب والمنافقين أن نبوته - صلى الله عليه وسلم - ملك يورث.','2803','1237','0');
INSERT INTO [t14381] VALUES (1250,'حصل في تولي الخلفاء الثلاثة قبل علي مصالح تعود إلى ما خص الله كل واحد منهم من الخصال.','2804','1237','0');
INSERT INTO [t14381] VALUES (1251,'الرسالة العاشرة: تعلق العقائد بالزمان والمكان','2805','912','0');
INSERT INTO [t14381] VALUES (1252,'مما يتعلق بالعقائد تعلقا متينا حال المكان والزمان، فالضرورة داعية إلى النظر فيهما.','2806','1251','0');
INSERT INTO [t14381] VALUES (1253,'المكان قد يطلق على موضع التمكن، وقد يطلق على محل الكون.','2806','1251','0');
INSERT INTO [t14381] VALUES (1254,'ما بين السماء والأرض فضاء، وليس هذا الفضاء هو الهواء، بل بإطباق المتكلمين أنه عدم.','2806','1251','0');
INSERT INTO [t14381] VALUES (1255,'الضرورة كثيرا ما تشتبه بالوهمي، والمتكلم يتحكم أحيانا، فيجعل الوهمي ضرورة، أوالعكس.','2807','1251','0');
INSERT INTO [t14381] VALUES (1256,'مناقشة المتكلمين في دعواهم أن خارج العالم فضاء بمعنى البعد الموهوم.','2807','1251','0');
