INSERT INTO [t14381] VALUES (4890,'«ما زال بكم صنيعكم حتى ظننت أنه سيكتب عليكم. . .»','10104','4877','0');
INSERT INTO [t14381] VALUES (4891,'«لا يلدغ المؤمن من جحر واحد مرتين»','10105','4877','0');
