INSERT INTO [t14381] VALUES (4893,'«إذا سلم عليكم أهل الكتاب فقولوا: وعليكم»','10106','4877','0');
INSERT INTO [t14381] VALUES (4894,'«أقيمت الصلاة ورجل يناجي رسول الله - صلى الله عليه وآله وسلم -، فما زال يناجيه. . .»','10107','4877','0');
INSERT INTO [t14381] VALUES (4895,'باب الختان بعد الكبر (حديث: «الفطرة خمس. . .»)','10108','4877','0');
INSERT INTO [t14381] VALUES (4896,'«اللهم صل على آل أبي أوفى»','10109','4877','0');
INSERT INTO [t14381] VALUES (4897,'«أيها الناس اربعوا على أنفسكم، فإنكم لا تدعون أصم ولا غائبا. . .»','10110','4877','0');
INSERT INTO [t14381] VALUES (4898,'«يا رب لا تجعلني أشقى خلقك»','10110','4877','0');
INSERT INTO [t14381] VALUES (4899,'«والله لا أحملكم، وما عندي ما أحملكم»','10111','4877','0');
INSERT INTO [t14381] VALUES (4900,'قول عائشة: «فوالله ما زالت في حذيفة منها بقية حتى لقي الله»','10113','4877','0');
INSERT INTO [t14381] VALUES (4901,'«لعن الله السارق يسرق البيضة، فتقطع يده، ويسرق الحبل، فتقطع يده»','10114','4877','0');
INSERT INTO [t14381] VALUES (4902,'«أليس قد صليت معنا؟» قال: نعم، قال: «فإن الله قد غفر لك ذنبك»','10115','4877','0');
INSERT INTO [t14381] VALUES (4903,'«لا يجلد فوق عشر جلدات إلا في حد من حدود الله»','10116','4877','0');
INSERT INTO [t14381] VALUES (4904,'«باب إذا أكره حتى وهب عبدا أو باعه لم يجز، وقال بعض الناس. . .»','10117','4877','0');
INSERT INTO [t14381] VALUES (4905,'«يجاء بنوح يوم القيامة فيقال له: هل بلغت؟ فيقول: نعم يا رب. . .»','10118','4877','0');
INSERT INTO [t14381] VALUES (4906,'قول مجاهد: «{والذي جاء بالصدق} القرآن. . .»','10119','4877','0');
INSERT INTO [t14381] VALUES (4907,'«لخلوف فم الصائم أطيب عند الله من ريح المسك»','10120','4877','0');
INSERT INTO [t14381] VALUES (4908,'«لا ينبغي لعبد أن يقول: إنه خير من يونس بن متى»','10121','4877','0');
INSERT INTO [t14381] VALUES (4909,'تعليق على أحاديث من «صحيح مسلم»','10123','4877','0');
INSERT INTO [t14381] VALUES (4910,'«الحياء لا يأتي إلا بخير»','10123','4877','0');
