INSERT INTO [t14381] VALUES (4914,'قول النبي - صلى الله عليه وسلم - لأشعث بن قيس: «شاهداك أو يمينه»','10129','4877','0');
INSERT INTO [t14381] VALUES (4915,'«كأني أنظر إلى موسى - صلى الله عليه وسلم - واضعا أصبعيه في أذنيه له جؤار إلى الله بالتلبية»','10131','4877','0');
INSERT INTO [t14381] VALUES (4916,'«تأخذ إحداكن ماءها وسدرتها فتطهر فتحسن الطهور، ثم تصب على رأسها. . .»','10132','4877','0');
INSERT INTO [t14381] VALUES (4917,'«كان رسول الله - صلى الله عليه وسلم - إذا قعد اطمأن على فخذه اليسرى»','10134','4877','0');
INSERT INTO [t14381] VALUES (4918,'«خرج رسول الله - صلى الله عليه وآله وسلم - بالهاجرة إلى البطحاء فتوضأ فصلى الظهر ركعتين .. »','10134','4877','0');
INSERT INTO [t14381] VALUES (4919,'قول علقمة: «فجاء رجل فعرفت فيه تحوش القوم وهيئتهم .. »','10135','4877','0');
INSERT INTO [t14381] VALUES (4920,'حديث الركعتين بعد العصر','10135','4877','0');
INSERT INTO [t14381] VALUES (4921,'«ولا صاحب كنز لا يفعل فيه حقه، إلا جاء كنزه يوم القيامة شجاعا أقرع .. »','10136','4877','0');
INSERT INTO [t14381] VALUES (4922,'«لا يتصدق أحد بتمرة من كسب طيب إلا أخذها الله بيمينه فيربيها»','10137','4877','0');
INSERT INTO [t14381] VALUES (4923,'«إن الله طيب لا يقبل إلا طيبا وإن الله أمر المؤمنين بما أمر به المرسلين»','10138','4877','0');
INSERT INTO [t14381] VALUES (4924,'«قال رجل: لأتصدقن الليلة بصدقة فخرج بصدقته فوضعها في يد زانية»','10139','4877','0');
INSERT INTO [t14381] VALUES (4925,'«أفضل الصدقة ــ أو خير الصدقة ــ عن ظهر غنى»','10139','4877','0');
INSERT INTO [t14381] VALUES (4926,'«إن الله مده للرؤية»','10141','4877','0');
INSERT INTO [t14381] VALUES (4927,'«كان رسول الله - صلى الله عليه وآله وسلم - يصلي في رمضان فجئت فقمت إلى جنبه .. »','10142','4877','0');
INSERT INTO [t14381] VALUES (4928,'«ما علمت أن رسول الله - صلى الله عليه وآله وسلم - صام يوما يطلب فضله على الأيام إلا هذا اليوم»','10144','4877','0');
INSERT INTO [t14381] VALUES (4929,'حديث تلبية النبي - صلى الله عليه وسلم - وزيادة عمر وابنه عبد الله فيها','10144','4877','0');
INSERT INTO [t14381] VALUES (4930,'«فرماها بسبع حصيات، يكبر مع كل حصاة منها، مثل حصى الخذف»','10145','4877','0');
INSERT INTO [t14381] VALUES (4931,'حديث جبير بن مطعم أنه رأى النبي - صلى الله عليه وآله وسلم - يوم عرفة بعرفة','10146','4877','0');
INSERT INTO [t14381] VALUES (4932,'«ما رأيت رسول الله - صلى الله عليه وآله وسلم - صلى صلاة إلا لميقاتها، إلا صلاتين:. . .»','10146','4877','0');
INSERT INTO [t14381] VALUES (4933,'«لا يصبر أحد على لأوائها فيموت، إلا كنت له شفيعا أو شهيدا يوم القيامة»','10147','4877','0');
INSERT INTO [t14381] VALUES (4934,'«الطعام بالطعام مثلا بمثل»','10147','4877','0');
INSERT INTO [t14381] VALUES (4935,'«يضحك الله إلى رجلين، يقتل أحدهما الآخر، كلاهما يدخل الجنة»','10148','4877','0');
INSERT INTO [t14381] VALUES (4936,'«لا تبدءوا اليهود ولا النصارى بالسلام، فإذا لقيتم أحدهم في طريق. . .»','10149','4877','0');
INSERT INTO [t14381] VALUES (4937,'«ارم فداك أبي وأمي»','10150','4877','0');
INSERT INTO [t14381] VALUES (4938,'«خير نسائها مريم بنت عمران، وخير نسائها خديجة»','10151','4877','0');
INSERT INTO [t14381] VALUES (4939,'«ولو دعت عليه أن يفتن لفتن»','10152','4877','0');
INSERT INTO [t14381] VALUES (4940,'قول نواس بن سمعان: أقمت مع رسول الله - صلى الله عليه وآله وسلم - بالمدينة سنة، ما يمنعني من الهجرة إلا المسألة','10153','4877','0');
INSERT INTO [t14381] VALUES (4941,'«لا يدخل الجنة قاطع»','10154','4877','0');
INSERT INTO [t14381] VALUES (4942,'«لا تحاسدوا، ولا تباغضوا، ولا تقاطعوا، وكونوا عباد الله إخوانا»','10155','4877','0');
INSERT INTO [t14381] VALUES (4943,'قول سراقة: «فالله لكما أن أرد عنكما الطلب»','10155','4877','0');
INSERT INTO [t14381] VALUES (4944,'تعليق على أحاديث من سنن أبي داود','10156','4877','0');
INSERT INTO [t14381] VALUES (4945,'تعليق على حديث: «إن الله أعطى كل ذي حق حقه. . .»','10157','4877','0');
INSERT INTO [t14381] VALUES (4946,'تعليق على مواضع من «الموطأ»','10161','4877','0');
INSERT INTO [t14381] VALUES (4947,'«إذا جاء أحدكم الجمعة فليغتسل»','10161','4877','0');
INSERT INTO [t14381] VALUES (4948,'باب الزكاة في الدين','10162','4877','0');
INSERT INTO [t14381] VALUES (4949,'صدقة الخلطاء','10162','4877','0');
INSERT INTO [t14381] VALUES (4950,'مسألة ضم الماشية المستفادة بنحو ميراث إلى ما قبلها من جنسها','10163','4877','0');
INSERT INTO [t14381] VALUES (4951,'«قد أنكحتكها بما معك من القرآن»','10165','4877','0');
INSERT INTO [t14381] VALUES (4952,'تعليقات على أحاديث من «مجمع الزوائد»','10167','4877','0');
INSERT INTO [t14381] VALUES (4953,'«إن أبي وأباك وأنت في النار»','10167','4877','0');
INSERT INTO [t14381] VALUES (4954,'«من مس فرجه فليتوضأ، وأيما امرأة مست فرجها فلتتوضأ»','10168','4877','0');
INSERT INTO [t14381] VALUES (4955,'كان عبد الله يقول: «تجوزوا في الصلاة، فإن خلفكم الكبير والضعيف .. »','10168','4877','0');
INSERT INTO [t14381] VALUES (4956,'«خذ غيرها يا أبا هريرة فإنه لا عيش إلا عيش الآخرة»','10169','4877','0');
INSERT INTO [t14381] VALUES (4957,'«يا معاذ بن جبل لا تكن فتانا، إما أن تصلي معي وإما أن تخفف على قومك»','10170','4877','0');
INSERT INTO [t14381] VALUES (4958,'الرجل يؤم النساء','10173','4877','0');
INSERT INTO [t14381] VALUES (4959,'قول عمار: «أتيت النبي - صلى الله عليه وآله وسلم - وهو يصلي، فسلمت عليه فلم يرد علي»','10174','4877','0');
INSERT INTO [t14381] VALUES (4960,'التعليق على أحاديث متفرقة','10175','4877','0');
