INSERT INTO [t14381] VALUES (4962,'«الصوم لي وأنا أجزي به»','10176','4877','0');
INSERT INTO [t14381] VALUES (4963,'«إن الله حرم عليكم عقوق الأمهات. . .»','10178','4877','0');
INSERT INTO [t14381] VALUES (4964,'«الأنصار لا يحبهم إلا مؤمن ولا يبغضهم إلا منافق»','10179','4877','0');
INSERT INTO [t14381] VALUES (4965,'الجمع بين أحاديث ذم المبادرة بالشهادة وأحاديث الحث على ذلك','10180','4877','0');
INSERT INTO [t14381] VALUES (4966,'«إنما الولاء لمن أعتق»','10181','4877','0');
INSERT INTO [t14381] VALUES (4967,'«تلك امرأة يغشاها أصحابي، اعتدي عند عبد الله بن أم مكتوم. . .»','10182','4877','0');
INSERT INTO [t14381] VALUES (4968,'«هي رخصة من الله عز وجل، فمن أخذ بها فحسن، ومن أحب أن يصوم فلا جناح عليه»','10182','4877','0');
INSERT INTO [t14381] VALUES (4969,'«إن كنت عبد الله فارفع إزارك»','10182','4877','0');
INSERT INTO [t14381] VALUES (4970,'حديث البراء أن آخر آية نزلت آية الكلالة آخر سورة النساء','10183','4877','0');
INSERT INTO [t14381] VALUES (4971,'«إذا رأى أحدكم البرق أو الودق فلا يشر إليه»','10184','4877','0');
INSERT INTO [t14381] VALUES (4972,'قوله في دعاء الاستسقاء: «دائما إلى يوم الدين»','10184','4877','0');
