INSERT INTO [t143] VALUES (496,'١ - كلام الناقد قد يكون متضمنا لذكر الاختلاف، وذكر أمثلته','944','495','0');
INSERT INTO [t143] VALUES (497,'٢ - بعض الأحاديث لا يظهر للناظر فيها لأول وهلة وجود اختلاف على أحد رواته، فيقف على كلام ناقد ينص أو يشير فيه إلى هذا الاختلاف','949','495','0');
INSERT INTO [t143] VALUES (498,'سعة حفظ الإمام النسائي وقدرته على استحضار الارتباط بين الأسانيد','950','497','0');
INSERT INTO [t143] VALUES (499,'٣ - كلام النقاد على الاختلافات هو في نفسه مورد معين للطرق نفسها','950','495','0');
INSERT INTO [t143] VALUES (500,'٤ - الناظر في الاختلاف قد يبعد النجعة في معالجة الاختلاف، وكان يغنيه عن ذلك أن يقف على قول ناقد متقدم يحسم الأمر','951','495','0');
INSERT INTO [t143] VALUES (501,'٥ - قد يكون الناقد الذي وقفنا على كلامه هو أحد المختلفين، تراجع عن روايته، فيحسم الاختلاف','954','495','0');
INSERT INTO [t143] VALUES (502,'٦ - أن الباحث قد يعتمد على أقوال النقاد في الترجيح، في حال إجماعهم على رأي فيه، فإجماعهم حجة','956','495','0');
INSERT INTO [t143] VALUES (503,'ما المقصود بإجماع أهل الحديث؟','956','502','0');
INSERT INTO [t143] VALUES (504,'يلتحق بإجماع المحدثين ما يوجد في كلام النقاد من حكاية نقد لم ينسب لأحد، ويفهم منه أنه قول مشهور في النقد، قال به جماعة','957','502','0');
INSERT INTO [t143] VALUES (505,'يقرب من الإجماع دلالة أن يكون الجمهور على قول، ويخالفهم ناقد واحد، فإن الأقرب إلى الصواب في الغالب مع الجمهور','958','502','0');
INSERT INTO [t143] VALUES (506,'٧ - الاجتهاد في جمع كلام النقاد يقرب الباحث من الإنصاف، ويبعده عن التعجل في رد كلام النقاد، والاستهانة به','959','495','0');
INSERT INTO [t143] VALUES (507,'إذا لم يختلف النقاد في الترجيح فهل يكتفى بذكر بعضهم؟','962','506','0');
INSERT INTO [t143] VALUES (508,'٨ - أن أئمة النقد يذكرون في كلامهم ما استندوا إليه في الترجيح، فيستفيد الباحث تحرير قرائن الترجيح في حديثه الذي ينظر فيه','965','495','0');
INSERT INTO [t143] VALUES (509,'٩ - أن الباحث حين نظره في الطرق، ورواة الأوجه، وصفة رواية كل منهم، يظهر لديه رأي في الاختلاف، لكنه محتاج إلى رأي ناقد يتكئ عليه','966','495','0');
INSERT INTO [t143] VALUES (510,'١٠ - أن الباحث يتعود على تحليل النصوص، والتمعن فيها، وفحصها، ويتربى على تقبل الرأي المخالف','966','495','0');
INSERT INTO [t143] VALUES (511,'تنبيهات في مسألة كلام النقاد في الاختلاف','967','510','0');
INSERT INTO [t143] VALUES (512,'الأمر الأول: كلام النقاد قد يكون صريحا في الاختلاف الذي ينظر فيه الباحث','967','511','0');
INSERT INTO [t143] VALUES (513,'الثاني: يصح للباحث أن يذكر إجماعا للنقاد، أو للجمهور، أو لاثنين منهم، وإن لم يكن بين من نسب إليهم اتفاق في جميع التفاصيل، إذا كان بينهم قدر مشترك','969','511','0');
INSERT INTO [t143] VALUES (514,'الاستفادة من القدر المشترك في كلام النقاد','974','513','0');
INSERT INTO [t143] VALUES (515,'الثالث: التأني في نقل نص الناقد في الاختلاف، والحذر من نسبة قول لغير قائله','975','511','0');
INSERT INTO [t143] VALUES (516,'إتقان الإسناد إلى صاحب النص','980','515','0');
INSERT INTO [t143] VALUES (517,'الرابع: مصادر كلام النقاد','982','511','0');
INSERT INTO [t143] VALUES (518,'إشكالات تواجه الباحثين أثناء جمعهم لكلام النقاد','984','517','0');
INSERT INTO [t143] VALUES (519,'- فوات كلام بعض النقاد','984','518','0');
INSERT INTO [t143] VALUES (520,'- الاضطراب في النص، وذكر نماذج له','985','518','0');
INSERT INTO [t143] VALUES (521,'الخامس: طبقات الأئمة في علم العلل','991','511','0');
INSERT INTO [t143] VALUES (522,'لم أقف على تصنيف لأئمة النقد بين التشدد، والاعتدال، والتساهل في علم العلل','994','521','0');
INSERT INTO [t143] VALUES (523,'المبحث الثالث: كلام النقاد ومصطلحاتهم','995','484','0');
INSERT INTO [t143] VALUES (524,'أولا: عبارات النقاد وألفاظهم وطرقهم في الترجيح','995','523','0');
INSERT INTO [t143] VALUES (525,'يدخل في التصريح: ما ينقله التلميذ عن شيخه، أو من فوقه، بعبارته هو','996','524','0');
INSERT INTO [t143] VALUES (526,'ثانيا: إشارات النقاد، وهي تقوم مقام التصريح، ولها صور منها','996','523','0');
INSERT INTO [t143] VALUES (527,'١ - أن يسأل الناقد فيذكر قرينة أو أكثر للترجيح','996','526','0');
INSERT INTO [t143] VALUES (528,'٢ - أن يسأل الناقد عن اختلاف فينقل عن غيره رأيا، ولا يتعقبه','997','526','0');
INSERT INTO [t143] VALUES (529,'إشكال: هل نقل تلامذة النقاد عن النقاد هو رأي لهم أيضا؟','998','528','0');
INSERT INTO [t143] VALUES (530,'يغتفر للباحث أن يدرج الناقد الذي لم يصرح برأيه مع من صرحوا إذا كثروا','999','528','0');
INSERT INTO [t143] VALUES (531,'ثالثا: تصرفات النقاد في مصنفاتهم','999','523','0');
INSERT INTO [t143] VALUES (532,'إذا أورد الناقد وجهين فإنه لا يؤخذ من مجرد ذلك رأي له، ما لم تظهر منه إشارة لاختياره، أو يعرف رأيه من تصرفه، ونماذج لذلك من كلام ابن رجب والسخاوي','1000','531','0');
INSERT INTO [t143] VALUES (533,'صحيح البخاري ومسلم من أهم كتب نقد المرويات','1002','531','0');
INSERT INTO [t143] VALUES (534,'من دقائق النقد لما تركه البخاري ومسلم، أن يخرج أحدهما حديثا بإسناده، ثم يدع من متنه جملة طويلة أو قصيرة، لعلة فيها، حسب اجتهاده','1002','531','0');
INSERT INTO [t143] VALUES (535,'غرض الشيخين مما يوردانه من علل على أصل حديث عندهما، أن تلك العلة غير مؤثرة، فكأن مرادهما الإشارة إلى اطلاعهما على الاختلاف وأنه غير مؤثر أصلا، أو هو مؤثر على معنى نزول الحديث عن المرتبة العليا من الصحة','1003','531','0');
INSERT INTO [t143] VALUES (536,'ليس في ذكر وجود أحاديث معللة في الصحيحين تجن عليهما، بل هو دفاع عنهما','1004','531','0');
INSERT INTO [t143] VALUES (537,'يمكن معرفة أن صاحبي الصحيح يعلان ما أورداه في كتابيهما بأحد أمرين','1006','531','0');
INSERT INTO [t143] VALUES (538,'صلة نقد الشيخين لبعض ما يخرجانه بالرواة الذين يخرجان لهم','1012','531','0');
INSERT INTO [t143] VALUES (539,'ضرورة اعتناء الباحث بمصطلحات النقاد في الترجيح والاختيار، وإشاراتهم، وتصرفاتهم، ونماذج من الخطأ في ذلك','1012','531','0');
INSERT INTO [t143] VALUES (540,'النقاد قد يستخدمون مصطلح «المضطرب» في الإسناد والمتن على معنى وجود خلل في أحدهما، وإن لم يكن مرجعه إلى الاختلاف','1023','531','0');
INSERT INTO [t143] VALUES (541,'التدقيق في سياق المصطلح الذي ذكره الناقد','1023','531','0');
INSERT INTO [t143] VALUES (542,'على الباحث ألا يعترض على مصطلحات النقاد لورودها على غير ما اشتهر في كتب المصطلح المتأخرة','1027','531','0');
INSERT INTO [t143] VALUES (543,'المبحث الرابع: عبارات الترجيح والتصحيح','1031','484','0');
INSERT INTO [t143] VALUES (544,'أهمية التمعن في عبارات النقاد وعدم الاستدلال بها على ما لا تدل عليه','1033','543','0');
INSERT INTO [t143] VALUES (545,'مما يعين على فهم عبارة الناقد: السياق، كلام الناقد في موضع آخر، كلام النقاد، ونماذج لذلك','1033','543','0');
INSERT INTO [t143] VALUES (546,'إذا لم يتمعن الباحث في كلام الناقد، وسياقه، وما يحتف به، فربما فهمه على غير مراد الناقد، بل ربما اعترض عليه، ونماذج لذلك','1041','543','0');
INSERT INTO [t143] VALUES (547,'قضايا أخرى للاستفادة من كلام النقاد على الوجه الأمثل','1057','543','0');
INSERT INTO [t143] VALUES (548,'الباحث كما هو ملزم بالتدقيق في عبارات النقاد، فهو ملزم أيضا باعتنائه بالألفاظ التي يستخدمها','1057','543','0');
INSERT INTO [t143] VALUES (549,'أشد خطورة من العبارات الموهمة أن يخطئ الباحث في التعبير عن مراده','1058','543','0');
INSERT INTO [t143] VALUES (550,'فهرس المصادر والمراجع','1061','0','0');
