INSERT INTO [t144114] VALUES (388,'* كان - صلى الله عليه وسلم - يقرأ بها وبـ "قل يا أيها الكافرون" في ركعتي الفجر والمغرب ويقول: نعم السورتان هما يقرأ بهما في ركعتي الفجر','683','375','0');
INSERT INTO [t144114] VALUES (389,'* من قرأها خمسين مرة غفر الله له ذنوب خمسين سنة ومن قرأها مائتي مرة غفر الله له ذنوب مائتي سنة','685','375','0');
