INSERT INTO [t1446] VALUES (1,'كتاب الوضوء " مختصر المختصر من المسند الصحيح، عن النبي صلى الله عليه وسلم بنقل العدل عن العدل موصولا إليه صلى الله عليه وسلم من غير قطع في أثناء الإسناد ولا جرح في ناقلي الأخبار التي نذكرها بمشيئة الله تعالى "','2','0','0');
INSERT INTO [t1446] VALUES (2,'باب ذكر الخبر الثابت، عن النبي صلى الله عليه وسلم بأن إتمام الوضوء من الإسلام','3','1','0');
INSERT INTO [t1446] VALUES (3,'باب ذكر فضائل الوضوء يكون بعده صلاة مكتوبة','5','1','0');
INSERT INTO [t1446] VALUES (4,'باب ذكر فضل الوضوء ثلاثا ثلاثا يكون بعده صلاة تطوع، لا يحدث المصلي فيها نفسه','7','1','0');
INSERT INTO [t1446] VALUES (5,'باب ذكر حط الخطايا بالوضوء من غير ذكر صلاة تكون بعده','9','1','0');
INSERT INTO [t1446] VALUES (6,'باب ذكر حط الخطايا، ورفع الدرجات في الجنة بإسباغ الوضوء على المكاره، وإعطاء منتظر الصلاة بعد الصلاة أجر المرابط في سبيل الله','11','1','0');
INSERT INTO [t1446] VALUES (7,'باب ذكر علامة أمة النبي صلى الله عليه وسلم الذين جعلهم الله خير أمة أخرجت للناس بآثار الوضوء يوم القيامة علامة يعرفون بها في ذلك اليوم','13','1','0');
INSERT INTO [t1446] VALUES (8,'باب استحباب تطويل التحجيل بغسل العضدين في الوضوء " إذ الحلية تبلغ مواضع الوضوء يوم القيامة بحكم النبي المصطفى صلى الله عليه وسلم "','15','1','0');
INSERT INTO [t1446] VALUES (9,'باب نفي قبول الصلاة بغير وضوء بذكر خبر مجمل غير مفسر','17','1','0');
INSERT INTO [t1446] VALUES (10,'باب ذكر الخبر المفسر للفظة المجملة التي ذكرتها، والدليل على أن النبي صلى الله عليه وسلم إنما نفى قبول الصلاة لغير المتوضئ المحدث الذي قد أحدث حدثا يوجب الوضوء، لا كل قائم إلى الصلاة، وإن كان غير محدث حدثا يوجب الوضوء','21','1','0');
INSERT INTO [t1446] VALUES (11,'باب ذكر الدليل على أن الله عز وجل إنما أوجب الوضوء على بعض القائمين إلى الصلاة لا على كل قائم إلى الصلاة " في قوله: يا أيها الذين آمنوا إذا قمتم إلى الصلاة فاغسلوا وجوهكم الآية، إذ الله جل وعلا ولى نبيه صلى الله عليه وسلم بيان ما أنزل عليه خاصا وعاما،','23','1','0');
INSERT INTO [t1446] VALUES (12,'باب الدليل على أن الوضوء لا يجب إلا من حدث','27','1','0');
INSERT INTO [t1446] VALUES (13,'باب صفة وضوء النبي صلى الله عليه وسلم على طهر من غير حدث كان مما يوجب الوضوء','29','1','0');
INSERT INTO [t1446] VALUES (14,'جماع أبواب الأحداث الموجبة للوضوء','31','1','0');
INSERT INTO [t1446] VALUES (15,'باب ذكر وجوب الوضوء من الغائط والبول والنوم " والدليل على أن الله عز وجل قد يوجب الفرض في كتابه بمعنى، ويوجب ذلك الفرض بغير ذلك المعنى على لسان نبيه صلى الله عليه وسلم، إذ الله عز وجل إنما دل في كتابه على أن الوضوء يوجبه الغائط وملامسة النساء؛ لأنه','32','1','0');
INSERT INTO [t1446] VALUES (16,'باب ذكر وجوب الوضوء من المذي " وهو من الجنس الذي قد أعلمت أن الله قد يوجب الحكم في كتابه بشرط، ويوجبه على لسان نبيه صلى الله عليه وسلم بغير ذلك الشرط، إذ الله عز وجل لم يذكر في آية الوضوء المذي، والنبي صلى الله عليه وسلم قد أوجب الوضوء من المذي،','34','1','0');
INSERT INTO [t1446] VALUES (17,'باب الأمر بغسل الفرج من المذي مع الوضوء','37','1','0');
INSERT INTO [t1446] VALUES (18,'باب الأمر بنضح الفرج من المذي','39','1','0');
INSERT INTO [t1446] VALUES (19,'باب ذكر الدليل على أن الأمر بغسل الفرج ونضحه من المذي أمر ندب وإرشاد لا أمر فريضة وإيجاب','42','1','0');
INSERT INTO [t1446] VALUES (20,'باب ذكر وجوب الوضوء من الريح الذي يسمع صوتها بالأذن أو يوجد رائحتها بالأنف','44','1','0');
INSERT INTO [t1446] VALUES (21,'باب ذكر الدليل على أن الوضوء لا يجب إلا بيقين حدث " إذ الطهارة بيقين لا تزول بشك وارتياب، وإنما يزول اليقين باليقين، فإذا كانت الطهارة قد تقدمت بيقين لم تبطل الطهارة إلا بيقين حدث "','46','1','0');
INSERT INTO [t1446] VALUES (22,'باب ذكر الدليل على أن الاسم باسم المعرفة بالألف واللام قد لا يحوي جميع المعاني التي تدخل في ذلك الاسم " خلاف قول من يزعم ممن شاهدنا من أهل عصرنا ممن كان يدعي اللغة من غير معرفة بها، ويدعي العلم من غير معرفة به، أن الاسم باسم المعرفة يحوي جميع معاني','48','1','0');
INSERT INTO [t1446] VALUES (23,'باب ذكر خبر روي مختصرا عن رسول الله صلى الله عليه وسلم " أوهم عالما ممن لم يميز بين الخبر المختصر، والخبر المتقصى أن الوضوء لا يجب إلا من الحدث الذي له صوت أو رائحة "','50','1','0');
INSERT INTO [t1446] VALUES (24,'باب ذكر الخبر المتقصي للفظة المختصرة التي ذكرتها، والدليل على أن النبي صلى الله عليه وسلم إنما أعلم أن لا وضوء إلا من صوت أو ريح عند مسألة سئل عنها في الرجل يخيل إليه أنه قد خرجت منه ريح فيشك في خروج الريح، وكانت هذه المقالة عنه صلى الله عليه وسلم: "','52','1','0');
INSERT INTO [t1446] VALUES (25,'باب ذكر الدليل على أن اللمس قد يكون باليد " ضد قول من زعم أن اللمس لا يكون إلا بجماع بالفرج في الفرج','55','1','0');
INSERT INTO [t1446] VALUES (26,'باب الأمر بالوضوء من أكل لحوم الإبل','57','1','0');
INSERT INTO [t1446] VALUES (27,'باب استحباب الوضوء من مس الذكر','60','1','0');
INSERT INTO [t1446] VALUES (28,'باب ذكر الدليل على أن المحدث لا يجب عليه الوضوء قبل وقت الصلاة','62','1','0');
INSERT INTO [t1446] VALUES (29,'جماع أبواب الأفعال اللواتي لا توجب الوضوء','64','1','0');
INSERT INTO [t1446] VALUES (30,'باب ذكر الخبر الدال على أن خروج الدم من غير مخرج الحدث لا يوجب الوضوء','65','1','0');
INSERT INTO [t1446] VALUES (31,'باب ذكر الدليل على أن وطء الأنجاس لا يوجب الوضوء','67','1','0');
INSERT INTO [t1446] VALUES (32,'باب إسقاط إيجاب الوضوء من أكل ما مسته النار أو غيرته','70','1','0');
INSERT INTO [t1446] VALUES (33,'باب ذكر الدليل على أن اللحم الذي ترك النبي صلى الله عليه وسلم الوضوء من أكله كان لحم غنم لا لحم إبل','74','1','0');
INSERT INTO [t1446] VALUES (34,'باب ذكر الدليل على أن ترك النبي صلى الله عليه وسلم الوضوء مما مست النار أو غيرت ناسخ لوضوئه كان مما مست النار أو غيرت','76','1','0');
INSERT INTO [t1446] VALUES (35,'باب الرخصة في ترك غسل اليدين، والمضمضة من أكل اللحم " إذ العرب قد تسمي غسل اليدين وضوءا "','79','1','0');
INSERT INTO [t1446] VALUES (36,'باب ذكر الدليل على أن الكلام السيئ والفحش في المنطق لا يوجب وضوءا','81','1','0');
INSERT INTO [t1446] VALUES (37,'باب استحباب المضمضة من شرب اللبن','83','1','0');
INSERT INTO [t1446] VALUES (38,'باب ذكر الدليل على أن المضمضة من شرب اللبن استحباب لإزالة الدسم من الفم وإذهابه، لا لإيجاب المضمضة من شربه','85','1','0');
INSERT INTO [t1446] VALUES (39,'باب ذكر ما كان الله عز وجل فرق به بين نبيه صلى الله عليه وسلم، وبين أمته في النوم " من أن عينيه إذا نامتا لم يكن قلبه ينام، ففرق بينه وبينهم في إيجاب الوضوء من النوم على أمته دونه عليه السلام "','87','1','0');
INSERT INTO [t1446] VALUES (40,'جماع أبواب الآداب المحتاج إليها في إتيان الغائط والبول إلى الفراغ منها','90','1','0');
INSERT INTO [t1446] VALUES (41,'باب التباعد للغائط في الصحاري عن الناس','91','1','0');
INSERT INTO [t1446] VALUES (42,'باب الرخصة في ترك التباعد عن الناس عند البول','94','1','0');
INSERT INTO [t1446] VALUES (43,'باب استحباب الاستتار عند الغائط','96','1','0');
INSERT INTO [t1446] VALUES (44,'باب الرخصة للنساء في الخروج للبراز بالليل إلى الصحاري','98','1','0');
INSERT INTO [t1446] VALUES (45,'باب التحفظ من البول كي لا يصيب البدن والثياب، والتغليظ في ترك غسله إذا أصاب البدن أو الثياب','100','1','0');
INSERT INTO [t1446] VALUES (46,'باب ذكر خبر روي عن النبي صلى الله عليه وسلم في النهي عن استقبال القبلة واستدبارها عند الغائط والبول بلفظ عام مراده خاص','102','1','0');
INSERT INTO [t1446] VALUES (47,'باب ذكر خبر روي عن النبي صلى الله عليه وسلم في الرخصة في البول مستقبل القبلة " بعد نهي النبي صلى الله عليه وسلم عنه مجملا غير مفسر، قد يحسب من لم يتبحر في العلم أن البول مستقبل القبلة جائز لكل بائل، وفي أي موضع كان، ويتوهم من لا يفهم العلم، ولا يميز','104','1','0');
INSERT INTO [t1446] VALUES (48,'باب ذكر الخبر المفسر للخبرين اللذين ذكرتهما في البابين المتقدمين " والدليل على أن النبي صلى الله عليه وسلم إنما نهى عن استقبال القبلة واستدبارها عند الغائط والبول في الصحاري، والمواضع اللواتي لا سترة فيها، وأن الرخصة في ذلك في الكنف والمواضع التي فيها','106','1','0');
INSERT INTO [t1446] VALUES (49,'باب الرخصة في البول قائما','109','1','0');
INSERT INTO [t1446] VALUES (50,'باب استحباب تفريج الرجلين عند البول قائما، إذ هو أحرى أن لا ينشر البول على الفخذين والساقين','112','1','0');
INSERT INTO [t1446] VALUES (51,'باب كراهية تسمية البائل مهريقا للماء','114','1','0');
INSERT INTO [t1446] VALUES (52,'باب الرخصة في البول في الطساس','116','1','0');
INSERT INTO [t1446] VALUES (53,'باب النهي عن البول في الماء الراكد الذي لا يجري، وفي نهيه عن ذلك دلالة على إباحة البول في الماء الجاري','118','1','0');
INSERT INTO [t1446] VALUES (54,'باب النهي عن التغوط على طريق المسلمين وظلهم الذي هو مجالسهم','120','1','0');
INSERT INTO [t1446] VALUES (55,'باب النهي عن مس الذكر باليمين','122','1','0');
INSERT INTO [t1446] VALUES (56,'باب الاستعاذة من الشيطان الرجيم عند دخول المتوضأ','124','1','0');
INSERT INTO [t1446] VALUES (57,'باب إعداد الأحجار للاستنجاء عند إتيان الغائط','126','1','0');
INSERT INTO [t1446] VALUES (58,'باب النهي عن المحادثة على الغائط','128','1','0');
INSERT INTO [t1446] VALUES (59,'باب النهي عن نظر المسلم إلى عورة أخيه المسلم','130','1','0');
INSERT INTO [t1446] VALUES (60,'باب كراهية رد السلام يسلم على البائل','132','1','0');
INSERT INTO [t1446] VALUES (61,'جماع أبواب الاستنجاء بالأحجار','134','1','0');
INSERT INTO [t1446] VALUES (62,'باب الأمر بالاستطابة بالأحجار، والدليل على أن الاستطابة بالأحجار يجزي دون الماء','135','1','0');
INSERT INTO [t1446] VALUES (63,'باب الأمر بالاستطابة بالأحجار وترا لا شفعا','137','1','0');
INSERT INTO [t1446] VALUES (64,'باب ذكر الدليل على أن الأمر بالاستطابة وترا " هو الوتر الذي يزيد على الواحد، الثلاث فما فوقه من الوتر، إذ الواحد قد يقع عليه اسم الوتر، والاستطابة بحجر واحد غير مجزية، إذ النبي صلى الله عليه وسلم قد أمر أن لا يكتفى بدون ثلاثة أحجار في الاستطابة "','139','1','0');
INSERT INTO [t1446] VALUES (65,'باب الدليل على أن الأمر بالوتر في الاستطابة أمر استحباب لا أمر إيجاب " وأن من استطاب بأكثر من ثلاثة بشفع لا بوتر غير عاص في فعله، إذ تارك الاستحباب غير الإيجاب تارك فضيلة لا فريضة "','141','1','0');
INSERT INTO [t1446] VALUES (66,'باب النهي عن الاستطابة باليمين','143','1','0');
INSERT INTO [t1446] VALUES (67,'باب النهي عن الاستطابة بدون ثلاثة أحجار','146','1','0');
INSERT INTO [t1446] VALUES (68,'باب الدليل على النهي عن الاستطابة بدون ثلاثة أحجار " وأن الاستطابة بدون ثلاثة أحجار لا يكفي دون الاستنجاء بالماء؛ لأن المستطيب بدون ثلاثة أحجار عاص في فعله وإن استنجى بعده بالماء، والنهي عن الاستنجاء بالعظام والرجيع "','148','1','0');
INSERT INTO [t1446] VALUES (69,'باب ذكر العلة التي من أجلها زجر عن الاستنجاء بالعظام والروث','150','1','0');
INSERT INTO [t1446] VALUES (70,'جماع أبواب الاستنجاء بالماء','152','1','0');
INSERT INTO [t1446] VALUES (71,'باب ذكر ثناء الله عز وجل على المتطهرين بالماء','153','1','0');
INSERT INTO [t1446] VALUES (72,'باب ذكر استنجاء النبي صلى الله عليه وسلم بالماء','155','1','0');
INSERT INTO [t1446] VALUES (73,'باب تسمية الاستنجاء بالماء فطرة','160','1','0');
INSERT INTO [t1446] VALUES (74,'باب دلك اليد بالأرض وغسلهما بعد الفراغ من الاستنجاء بالماء','162','1','0');
INSERT INTO [t1446] VALUES (75,'باب القول عند الخروج من المتوضأ','164','1','0');
INSERT INTO [t1446] VALUES (76,'جماع أبواب ذكر الماء الذي لا ينجس، والذي ينجس إذا خالطته نجاسة','166','1','0');
INSERT INTO [t1446] VALUES (77,'باب ذكر خبر روي عن النبي صلى الله عليه وسلم في نفي تنجيس الماء بلفظ مجمل غير مفسر بلفظ عام مراده خاص','167','1','0');
INSERT INTO [t1446] VALUES (78,'باب ذكر الخبر المفسر للفظة المجملة التي ذكرتها، والدليل على أن النبي صلى الله عليه وسلم إنما أراد بقوله: " الماء لا ينجسه شيء " بعض المياه لا كلها، وإنما أراد الماء الذي هو قلتان فأكثر لا ما دون القلتين منه','169','1','0');
INSERT INTO [t1446] VALUES (79,'باب النهي عن اغتسال الجنب في الماء الدائم بلفظ عام مراده خاص " وفيه دليل على أن قوله صلى الله عليه وسلم: " الماء لا ينجسه شيء " لفظ عام مراده خاص على ما بينت قبل أراد الماء الذي يكون قلتين فصاعدا "','171','1','0');
INSERT INTO [t1446] VALUES (80,'باب النهي عن الوضوء من الماء الدائم الذي قد بيل فيه، والنهي عن الشرب منه بذكر لفظ عام مراده خاص','173','1','0');
INSERT INTO [t1446] VALUES (81,'باب الأمر بغسل الإناء من ولوغ الكلب " والدليل على أن النبي صلى الله عليه وسلم إنما أمر بغسل الإناء من ولوغ الكلب تطهيرا للإناء، لا على ما ادعى بعض أهل العلم أن الأمر بغسله أمر تعبد، وأن الإناء طاهر والوضوء والاغتسال بذلك الماء جائز، وشرب ذلك الماء طلق','175','1','0');
INSERT INTO [t1446] VALUES (82,'باب الأمر بإهراق الماء الذي ولغ فيه الكلب، وغسل الإناء من ولوغ الكلب " وفيه دليل على نقض قول من زعم أن الماء طاهر، والأمر بغسل الإناء تعبد، إذ غير جائز أن يأمر النبي صلى الله عليه وسلم بهراقة ماء طاهر غير نجس "','179','1','0');
INSERT INTO [t1446] VALUES (83,'باب النهي عن غمس المستيقظ من النوم يده في الإناء قبل غسلها','182','1','0');
INSERT INTO [t1446] VALUES (84,'باب ذكر الدليل على أن النبي صلى الله عليه وسلم إنما أراد بقوله: " فإنه لا يدري أين باتت يده منه " أي أنه لا يدري أين أتت يده من جسده','184','1','0');
INSERT INTO [t1446] VALUES (85,'باب ذكر الدليل على أن الماء إذا خالطه فرث ما يؤكل لحمه لم ينجس','186','1','0');
INSERT INTO [t1446] VALUES (86,'باب الرخصة في الوضوء بسؤر الهرة " والدليل على أن خراطيم ما يأكل الميتة من السباع، ومما لا يجوز أكل لحمه من الدواب والطيور إذا ماس الماء الذي دون القلتين ولا نجاسة مرئية بخراطيمها ومناخيرها إن ذلك لا ينجس الماء، إذ العلم محيط أن الهرة تأكل الفأر، وقد','188','1','0');
INSERT INTO [t1446] VALUES (87,'باب ذكر الدليل على أن سقوط الذباب في الماء لا ينجسه " وفيه ما دل على أن لا نجاسة في الأحياء، وإن كان لا يجوز أكل لحمه، إلا ما خص به النبي صلى الله عليه وسلم الكلب، وكل ما يقع عليه اسم الكلب من السباع إذ الذباب لا يؤكل، وهو من الخبائث التي أعلم الله','193','1','0');
INSERT INTO [t1446] VALUES (88,'باب إباحة الوضوء بالماء المستعمل " والدليل على أن الماء إذا غسل به بعض أعضاء البدن أو جميعه لم ينجس الماء، وكان الماء طاهرا لا نجاسة عليه "','195','1','0');
INSERT INTO [t1446] VALUES (89,'باب إباحة الوضوء من فضل وضوء المتوضئ','197','1','0');
INSERT INTO [t1446] VALUES (90,'باب إباحة الوضوء من فضل وضوء المرأة','199','1','0');
INSERT INTO [t1446] VALUES (91,'باب إباحة الوضوء بفضل غسل المرأة من الجنابة','201','1','0');
INSERT INTO [t1446] VALUES (92,'باب الدليل على أن سؤر الحائض ليس بنجس، وإباحة الوضوء والغسل به " إذ هو طاهر غير نجس، إذ لو كان سؤر حائض نجسا لما شرب النبي صلى الله عليه وسلم ماء نجسا غير مضطر إلى شربه "','203','1','0');
INSERT INTO [t1446] VALUES (93,'باب الرخصة في الغسل والوضوء من ماء البحر " إذ ماؤه طهور ميتته حل ضد قول من كره الوضوء، والغسل من ماء البحر، وزعم أن تحت البحر نارا، وتحت النار بحرا حتى عد سبعة أبحر، وسبعة نيران، وكره الوضوء والغسل من مائه لهذه العلة زعم "','205','1','0');
INSERT INTO [t1446] VALUES (94,'باب الرخصة في الوضوء والغسل من الماء الذي يكون في أواني أهل الشرك وأسقيتهم " والدليل على أن الإهاب يطهر بدباغ المشركين إياه "','208','1','0');
INSERT INTO [t1446] VALUES (95,'باب الرخصة في الوضوء من الماء يكون في جلود الميتة إذا دبغت','210','1','0');
INSERT INTO [t1446] VALUES (96,'باب الدليل على أن أبوال ما يؤكل لحمه ليس بنجس، ولا ينجس الماء إذا خالطه " إذ النبي صلى الله عليه وسلم قد أمر بشرب أبوال الإبل مع ألبانها، ولو كان نجسا لم يأمر بشربه، وقد أعلم أن لا شفاء في المحرم، وقد أمر بالاستشفاء بأبوال الإبل، ولو كان نجسا كان','212','1','0');
INSERT INTO [t1446] VALUES (97,'باب ذكر خبر روي عن النبي صلى الله عليه وسلم في إجازة الوضوء بالمد من الماء " أوهم بعض العلماء أن توقيت المد من الماء للوضوء توقيت لا يجوز الوضوء بأقل منه "','214','1','0');
INSERT INTO [t1446] VALUES (98,'باب ذكر الدليل على أن توقيت المد من الماء للوضوء " أن الوضوء بالمد يجزئ، لا أنه لا يسع المتوضئ أن يزيد على المد أو ينقص منه إذ لو لم يجزئ الزيادة على ذلك ولا النقصان منه، كان على المرء إذا أراد الوضوء أن يكيل مدا من ماء فيتوضأ به لا يبقي منه شيئا، وقد','216','1','0');
INSERT INTO [t1446] VALUES (99,'باب الرخصة في الوضوء بأقل من قدر المد من الماء','218','1','0');
INSERT INTO [t1446] VALUES (100,'باب ذكر الدليل على أن لا توقيت في قدر الماء الذي يتوضأ به المرء " فيضيق على المتوضئ أن يزيد عليه أو ينقص منه، إذ لو كان لقدر الماء الذي يتوضأ به المرء مقدار لا يجوز أن يزيد عليه ولا ينقص منه شيئا، لما جاز أن يجتمع اثنان ولا جماعة على إناء واحد،','220','1','0');
INSERT INTO [t1446] VALUES (101,'باب استحباب القصد في صب الماء، وكراهة التعدي فيه، والأمر باتقاء وسوسة الماء','224','1','0');
INSERT INTO [t1446] VALUES (102,'جماع أبواب الأواني اللواتي يتوضأ فيهن أو يغتسل','226','1','0');
INSERT INTO [t1446] VALUES (103,'باب إباحة الوضوء والغسل في أواني النحاس','227','1','0');
INSERT INTO [t1446] VALUES (104,'باب إباحة الوضوء من أواني الزجاج " ضد قول بعض المتصوفة الذي يتوهم أن اتخاذ أواني الزجاج من الإسراف، إذ الخزف أصلب وأبقى من الزجاج "','229','1','0');
INSERT INTO [t1446] VALUES (105,'باب إباحة الوضوء من الركوة والقعب','231','1','0');
INSERT INTO [t1446] VALUES (106,'باب إباحة الوضوء من الجفان والقصاع','234','1','0');
INSERT INTO [t1446] VALUES (107,'باب الأمر بتغطية الأواني التي يكون فيها الماء للوضوء بلفظ مجمل غير مفسر، ولفظ عام مراده خاص','236','1','0');
INSERT INTO [t1446] VALUES (108,'باب ذكر الخبر المفسر للفظة المجملة التي ذكرتها، والدليل على أن النبي صلى الله عليه وسلم إنما أمر بتغطية الأواني بالليل لا بالنهار جميعا','238','1','0');
INSERT INTO [t1446] VALUES (109,'باب الأمر بتسمية الله عز وجل عند تخمير الأواني، والعلة التي من أجلها أمر النبي صلى الله عليه وسلم بتخمير الإناء','241','1','0');
INSERT INTO [t1446] VALUES (110,'باب بدء النبي صلى الله عليه وسلم بالسواك عند دخول منزله','245','1','0');
INSERT INTO [t1446] VALUES (111,'باب فضل السواك وتطهير الفم به','247','1','0');
INSERT INTO [t1446] VALUES (112,'باب استحباب التسوك عند القيام من النوم للتهجد','249','1','0');
INSERT INTO [t1446] VALUES (113,'باب فضل الصلاة التي يستاك لها على الصلاة التي لا يستاك لها إن صح الخبر','251','1','0');
INSERT INTO [t1446] VALUES (114,'باب الأمر بالسواك عند كل صلاة أمر ندب وفضيلة لا أمر وجوب وفريضة','253','1','0');
INSERT INTO [t1446] VALUES (115,'باب ذكر الدليل على أن الأمر بالسواك أمر فضيلة لا أمر فريضة " إذ لو كان السواك فرضا أمر النبي صلى الله عليه وسلم أمته شق ذلك عليهم أو لم يشق، وقد أعلم صلى الله عليه وسلم أنه كان يأمر به أمته عند كل صلاة، لولا أن ذلك يشق عليهم، فدل هذا القول منه صلى','255','1','0');
INSERT INTO [t1446] VALUES (116,'باب صفة استياك النبي صلى الله عليه وسلم','258','1','0');
INSERT INTO [t1446] VALUES (117,'جماع أبواب الوضوء وسننه','260','1','0');
INSERT INTO [t1446] VALUES (118,'باب إيجاب إحداث النية للوضوء والغسل','261','1','0');
INSERT INTO [t1446] VALUES (119,'باب ذكر تسمية الله عز وجل عند الوضوء','264','1','0');
INSERT INTO [t1446] VALUES (120,'باب الأمر بغسل اليدين ثلاثا عند الاستيقاظ من النوم قبل إدخالهما الإناء','266','1','0');
INSERT INTO [t1446] VALUES (121,'باب كراهة معارضة خبر النبي صلى الله عليه وسلم بالقياس والرأي " والدليل على أن أمر النبي صلى الله عليه وسلم يجب قبوله إذا علم المرء به، وإن لم يدرك ذلك عقله ورأيه قال الله عز وجل: وما كان لمؤمن ولا مؤمنة إذا قضى الله ورسوله أمرا أن يكون لهم الخيرة من','268','1','0');
INSERT INTO [t1446] VALUES (122,'باب صفة غسل اليدين قبل إدخالهما الإناء، وصفة وضوء النبي صلى الله عليه وسلم','270','1','0');
INSERT INTO [t1446] VALUES (123,'باب إباحة المضمضة والاستنشاق من غرفة واحدة، والوضوء مرة مرة','272','1','0');
INSERT INTO [t1446] VALUES (124,'باب الأمر بالاستنشاق عند الاستيقاظ من النوم، وذكر العلة التي من أجلها أمر به','274','1','0');
INSERT INTO [t1446] VALUES (125,'باب الأمر بالمبالغة في الاستنشاق إذا كان المتوضئ مفطرا غير صائم','276','1','0');
INSERT INTO [t1446] VALUES (126,'باب تخليل اللحية في الوضوء عند غسل الوجه','278','1','0');
INSERT INTO [t1446] VALUES (127,'باب استحباب صك الوجه بالماء عند غسل الوجه','281','1','0');
INSERT INTO [t1446] VALUES (128,'باب استحباب تجديد حمل الماء لمسح الرأس غير فضل بلل اليدين','283','1','0');
INSERT INTO [t1446] VALUES (129,'باب استحباب مسح الرأس باليدين جميعا ليكون أوعب لمسح جميع الرأس، وصفة المسح، والبدء بمقدم الرأس قبل المؤخر في المسح','285','1','0');
INSERT INTO [t1446] VALUES (130,'باب ذكر الدليل على أن المسح على الرأس إنما يكون بما يبقى من بلل الماء على اليدين، لا بنفس الماء كما يكون الغسل بالماء قال أبو بكر: خبر عبد خير، عن علي: " ثم أدخل يده اليمنى في الإناء حتى غمرها الماء، ثم رفعها بما حملت من الماء، ثم مسحها بيده اليسرى','288','1','0');
INSERT INTO [t1446] VALUES (131,'باب مسح جميع الرأس في الوضوء','289','1','0');
INSERT INTO [t1446] VALUES (132,'باب مسح باطن الأذنين وظاهرهما قال أبو بكر: " قد أمليت حديث عثمان بن عفان، وخبر ابن عباس في مسح الأذنين ظاهرهما وباطنهما "','291','1','0');
INSERT INTO [t1446] VALUES (133,'باب ذكر الدليل على أن الكعبين اللذين أمر المتوضئ بغسل الرجلين إليهما العظمان الناتئان في جانبي القدم " لا العظم الصغير الناتئ على ظهر القدم، على ما يتوهمه من يتحذلق ممن لا يفهم العلم، ولا لغة العرب "','292','1','0');
INSERT INTO [t1446] VALUES (134,'باب التغليظ في ترك غسل العقبين في الوضوء " والدليل على أن الفرض غسل القدمين لا مسحهما، إذا كانتا باديتين غير مغطيتين بالخف أو ما يقوم مقام الخف، لا على ما زعمت الروافض أن الفرض مسح القدمين لا غسلهما، إذ لو كان الماسح على القدمين مؤديا للفرض، لما جاز','296','1','0');
INSERT INTO [t1446] VALUES (135,'باب التغليظ في ترك غسل بطون الأقدام في الوضوء " فيه أيضا دلالة على أن الماسح على ظهر القدمين غير مؤد للفرض، لا كما زعمت الروافض أن الفرض مسح ظهورهما لا غسل جميع القدمين "','299','1','0');
INSERT INTO [t1446] VALUES (136,'باب ذكر الدليل على أن المسح على القدمين غير جائز، لا كما زعمت الروافض، والخوارج','301','1','0');
INSERT INTO [t1446] VALUES (137,'باب ذكر البيان أن الله عز وجل وعلا أمر بغسل القدمين في قوله: وأرجلكم إلى الكعبين الآية، لا بمسحهما " على ما زعمت الروافض، والخوارج، والدليل على صحة تأويل المطلبي رحمه الله أن معنى الآية على التقديم والتأخير على معنى: اغسلوا وجوهكم وأيديكم وأرجلكم','303','1','0');
INSERT INTO [t1446] VALUES (138,'باب التغليظ في المسح على الرجلين، وترك غسلهما في الوضوء " والدليل على أن الماسح للقدمين التارك لغسلهما مستوجب للعقاب بالنار، إلا أن يعفو الله ويصفح، نعوذ بالله من عقابه "','305','1','0');
INSERT INTO [t1446] VALUES (139,'باب غسل أنامل القدمين في الوضوء " وفيه ما دل على أن الفرض غسلهما لا مسحهما "','307','1','0');
INSERT INTO [t1446] VALUES (140,'باب تخليل أصابع القدمين في الوضوء قال أبو بكر: " قد ذكرنا خبر عثمان بن عفان، عن النبي صلى الله عليه وسلم في تخليل أصابع القدمين ثلاثا "','309','1','0');
INSERT INTO [t1446] VALUES (141,'باب صفة وضوء النبي صلى الله عليه وسلم ثلاثا ثلاثا قال أبو بكر: خبر عثمان بن عفان وعلي بن أبي طالب في صفة وضوء النبي صلى الله عليه وسلم ثلاثا ثلاثا "','311','1','0');
INSERT INTO [t1446] VALUES (142,'باب إباحة الوضوء مرتين مرتين','312','1','0');
INSERT INTO [t1446] VALUES (143,'باب إباحة الوضوء مرة مرة " والدليل على أن غاسل أعضاء الوضوء مرة مرة مؤد لفرض الوضوء، إذ غاسل أعضاء الوضوء مرة مرة واقع عليه اسم غاسل، والله عز وجل أمر بغسل أعضاء الوضوء بلا ذكر توقيت، وفي وضوء النبي صلى الله عليه وسلم مرة مرة، ومرتين مرتين، وثلاثا','314','1','0');
INSERT INTO [t1446] VALUES (144,'باب إباحة غسل بعض أعضاء الوضوء شفعا، وبعضه وترا','316','1','0');
INSERT INTO [t1446] VALUES (145,'باب التغليظ في غسل أعضاء الوضوء أكثر من ثلاث " والدليل على أن فاعله مسيء ظالم أو متعد ظالم "','319','1','0');
INSERT INTO [t1446] VALUES (146,'باب الأمر بإسباغ الوضوء','321','1','0');
INSERT INTO [t1446] VALUES (147,'باب ذكر تكفير الخطايا، والزيادة في الحسنات بإسباغ الوضوء على المكاره','324','1','0');
INSERT INTO [t1446] VALUES (148,'باب الأمر بالتيامن في الوضوء أمر استحباب لا أمر إيجاب','326','1','0');
INSERT INTO [t1446] VALUES (149,'باب ذكر الدليل على أن الأمر بالبدء بالتيامن في الوضوء أمر استحباب واختيار، ولا أمر فرض وإيجاب','328','1','0');
INSERT INTO [t1446] VALUES (150,'باب الرخصة في المسح على العمامة','330','1','0');
INSERT INTO [t1446] VALUES (151,'جماع أبواب المسح على الخفين','333','1','0');
INSERT INTO [t1446] VALUES (152,'باب ذكر المسح على الخفين من غير ذكر توقيت للمسافر وللمقيم بذكر أخبار مجملة غير مفسرة','334','1','0');
INSERT INTO [t1446] VALUES (153,'باب ذكر مسح النبي صلى الله عليه وسلم على الخفين في الحضر','338','1','0');
INSERT INTO [t1446] VALUES (154,'باب ذكر مسح النبي صلى الله عليه وسلم على الخفين بعد نزول سورة المائدة " ضد قول من زعم أن النبي صلى الله عليه وسلم إنما مسح على الخفين قبل نزول المائدة "','340','1','0');
INSERT INTO [t1446] VALUES (155,'باب الرخصة في المسح على الموقين','344','1','0');
INSERT INTO [t1446] VALUES (156,'باب ذكر الخبر المفسر للألفاظ المجملة التي ذكرتها، والدليل على أن الرخصة في المسح على الخفين للابسها على طهارة، دون لابسها محدثا غير متطهر','346','1','0');
INSERT INTO [t1446] VALUES (157,'باب الدليل على أن لابس أحد الخفين قبل غسل كلا الرجلين، إذا لبس الخف الآخر بعد غسل الرجل الأخرى غير جائز له المسح على الخفين إذا أحدث " إذ هو لابس أحد الخفين قبل كمال الطهارة، والنبي صلى الله عليه وسلم إنما رخص في المسح على الخفين إذا لبسهما على طهارة،','350','1','0');
INSERT INTO [t1446] VALUES (158,'باب ذكر توقيت المسح على الخفين للمقيم والمسافر','352','1','0');
INSERT INTO [t1446] VALUES (159,'باب ذكر الدليل على أن الأمر بالمسح على الخفين أمر إباحة " أن المسح يقوم مقام غسل القدمين، إذا كان القدم باديا غير مغطى بالخف، وإن خالع الخف وإن كان لبسه على طهارة، إذا غسل قدميه كان مؤديا للفرض غير عاص، إلا أن يكون تاركا للمسح رغبة عن سنة النبي صلى','354','1','0');
INSERT INTO [t1446] VALUES (160,'باب ذكر الدليل على أن الرخصة في المسح على الخفين إنما هي من الحدث الذي يوجب الوضوء دون الجنابة التي توجب الغسل','356','1','0');
INSERT INTO [t1446] VALUES (161,'باب التغليظ في ترك المسح على الخفين رغبة عن السنة','358','1','0');
INSERT INTO [t1446] VALUES (162,'باب الرخصة في المسح على الجوربين والنعلين','360','1','0');
INSERT INTO [t1446] VALUES (163,'باب ذكر أخبار رويت عن النبي صلى الله عليه وسلم في المسح على النعلين مجملة، غلط في الاحتجاج بها بعض من أجاز المسح على النعلين في الوضوء الواجب من الحدث "','362','1','0');
INSERT INTO [t1446] VALUES (164,'باب ذكر الدليل على أن مسح النبي صلى الله عليه وسلم على النعلين كان في وضوء متطوع به، لا في وضوء واجب عليه من حدث يوجب الوضوء','364','1','0');
INSERT INTO [t1446] VALUES (165,'باب ذكر أخبار رويت عن النبي صلى الله عليه وسلم في المسح على الرجلين مجملة، غلط في الاحتجاج بها بعض من لم ينعم الروية في الأخبار، وأباح للمحدث المسح على الرجلين','366','1','0');
INSERT INTO [t1446] VALUES (166,'باب ذكر الدليل على أن مسح النبي صلى الله عليه وسلم على القدمين كان وهو طاهر لا محدث','368','1','0');
INSERT INTO [t1446] VALUES (167,'باب الرخصة في استعانة المتوضئ بمن يصب عليه الماء ليطهر " خلاف مذهب من يتوهم من المتصوفة أن هذا من الكبر "','370','1','0');
INSERT INTO [t1446] VALUES (168,'باب الرخصة في وضوء الجماعة من الإناء الواحد','372','1','0');
INSERT INTO [t1446] VALUES (169,'باب الرخصة في وضوء الرجال والنساء من الإناء الواحد','374','1','0');
INSERT INTO [t1446] VALUES (170,'جماع أبواب فضول التطهير والاستحباب من غير إيجاب','376','1','0');
INSERT INTO [t1446] VALUES (171,'باب استحباب الوضوء لذكر الله، وإن كان الذكر على غير وضوء مباحا','377','1','0');
INSERT INTO [t1446] VALUES (172,'باب ذكر الدليل على أن كراهية النبي صلى الله عليه وسلم لذكر الله على غير طهر كانت إذ الذكر على طهارة أفضل لا أنه غير جائز أن يذكر الله على غير طهر، إذ النبي صلى الله عليه وسلم قد كان يذكر الله على كل أحيانه "','379','1','0');
INSERT INTO [t1446] VALUES (173,'باب الرخصة في قراءة القرآن، وهو أفضل الذكر على غير وضوء','381','1','0');
INSERT INTO [t1446] VALUES (174,'باب استحباب الوضوء للدعاء، ومسألة الله ليكون المرء طاهرا عند الدعاء والمسألة','383','1','0');
INSERT INTO [t1446] VALUES (175,'باب استحباب وضوء الجنب إذا أراد النوم','385','1','0');
INSERT INTO [t1446] VALUES (176,'باب ذكر الدليل على أن الوضوء الذي أمر به الجنب للنوم كوضوء الصلاة، إذ العرب قد تسمي غسل اليدين وضوءا','388','1','0');
INSERT INTO [t1446] VALUES (177,'باب استحباب غسل الذكر مع الوضوء إذا أراد الجنب النوم','390','1','0');
INSERT INTO [t1446] VALUES (178,'باب استحباب الوضوء للجنب إذا أراد الأكل','392','1','0');
INSERT INTO [t1446] VALUES (179,'باب استحباب الوضوء عند النوم، وإن لم يكن المرء جنبا ليكون مبيته على طهارة','394','1','0');
INSERT INTO [t1446] VALUES (180,'باب ذكر الدليل على أن الوضوء الذي أمر به الجنب للأكل كوضوء الصلاة سواء','396','1','0');
INSERT INTO [t1446] VALUES (181,'باب ذكر الدليل على أن الأمر بالوضوء للجنب عند إرادة الأكل أمر ندب وإرشاد وفضيلة وإباحة','398','1','0');
INSERT INTO [t1446] VALUES (182,'باب ذكر الدليل على أن جميع ما ذكرت من الأبواب من وضوء الاستحباب على ما ذكرت، أن الأمر بالوضوء من ذلك كله أمر ندب وإرشاد وفضيلة، لا أمر فرض وإيجاب قال أبو بكر، خبر ابن عباس، أن النبي صلى الله عليه وسلم قال: " إنما أمرت بالوضوء إذا قمت إلى الصلاة "','400','1','0');
INSERT INTO [t1446] VALUES (183,'باب استحباب الوضوء عند معاودة الجماع بلفظ مجمل غير مفسر','401','1','0');
INSERT INTO [t1446] VALUES (184,'باب ذكر الدليل على أن الوضوء للمعاودة للجماع كوضوء الصلاة','403','1','0');
INSERT INTO [t1446] VALUES (185,'باب ذكر الدليل على أن الأمر بالوضوء عند إرادة الجماع أمر ندب وإرشاد " إذ المتوضئ بعد الجماع يكون أنشط للعودة إلى الجماع، لا أن الوضوء بين الجماعين واجب، ولا أن الجماع قبل الوضوء وبعد الجماع الأول محظور "','405','1','0');
INSERT INTO [t1446] VALUES (186,'باب فضل التهليل والشهادة للنبي صلى الله عليه وسلم بالرسالة والعبودية " وأن لا يطرى كما أطرت النصارى عيسى ابن مريم، إذا شهد له بالعبودية مع الشهادة له بالرسالة عند الفراغ من الوضوء "','407','1','0');
INSERT INTO [t1446] VALUES (187,'جماع أبواب غسل الجنابة','410','1','0');
INSERT INTO [t1446] VALUES (188,'باب ذكر أخبار رويت عن النبي صلى الله عليه وسلم في الرخصة في ترك الغسل في الجماع من غير إمناء " قد نسخ بعض أحكامها "','411','1','0');
INSERT INTO [t1446] VALUES (189,'باب ذكر نسخ إسقاط الغسل في الجماع من غير إمناء','413','1','0');
INSERT INTO [t1446] VALUES (190,'باب ذكر إيجاب الغسل بمماسة الختانين أو التقائهما، وإن لم يكن أمنى','417','1','0');
INSERT INTO [t1446] VALUES (191,'باب إيجاب إحداث النية للاغتسال من الجنابة " والدليل على ضد قول من زعم أن الجنب إذا دخل نهرا ناويا للسباحة فماس الماء جميع بدنه، ولم ينو غسلا ولا أراده إذا فرض الغسل، ولا تقربا إلى الله عز وجل أو صب عليه ماء وهو مكره، فماس الماء جميع جسده أن فرض الغسل','419','1','0');
INSERT INTO [t1446] VALUES (192,'باب ذكر الدليل على أن جماع نسوة لا يوجب أكثر من غسل واحد','421','1','0');
INSERT INTO [t1446] VALUES (193,'باب صفة ماء الرجل الذي يوجب الغسل، وصفة ماء المرأة الذي يوجب عليها الغسل إذا لم يكن جماع يكون فيه التقاء الختانين','425','1','0');
INSERT INTO [t1446] VALUES (194,'باب إيجاب الغسل من الإمناء، وإن كان الإمناء من غير جماع " يلتقي فيه الختانان أو يتماسان، كان الإمناء من مباشرة أو جماع دون الفرج، أو من قبلة أو من احتلام، كان الإمناء في اليقظة بعد الغسل من الجنابة قبل تبول الجنب قبل الاغتسال أو بعده، أو بعد ما يبول','428','1','0');
INSERT INTO [t1446] VALUES (195,'باب ذكر إيجاب الغسل على المرأة في الاحتلام إذا أنزلت الماء','431','1','0');
INSERT INTO [t1446] VALUES (196,'باب ذكر الدليل على أن لا وقت فيما يغتسل به المرء من الماء، فيضيق الزيادة فيه أو النقصان منه " والدليل على أن الواجب على المغتسل إمساس الماء جميع البدن قل الماء أو كثر " قال أبو بكر: خبر عائشة " كنت أغتسل أنا ورسول الله صلى الله عليه وسلم من إناء واحد "','433','1','0');
INSERT INTO [t1446] VALUES (197,'باب الاستتار للاغتسال من الجنابة','435','1','0');
INSERT INTO [t1446] VALUES (198,'باب إباحة الاغتسال من القصاع، والمراكن، والطاس','437','1','0');
INSERT INTO [t1446] VALUES (199,'باب صفة الغسل من الجنابة','441','1','0');
INSERT INTO [t1446] VALUES (200,'باب تخليل أصول شعر الرأس بالماء قبل إفراغ الماء على الرأس، وحثي الماء على الرأس بعد التخليل حثيات ثلاث','443','1','0');
INSERT INTO [t1446] VALUES (201,'باب اكتفاء صاحب الجمة والشعر الكثير بإفراغ ثلاث حثيات من الماء على الرأس في غسل الجنابة','445','1','0');
INSERT INTO [t1446] VALUES (202,'باب استحباب بدء المغتسل بإفاضة الماء على الميامن قبل المياسر','447','1','0');
INSERT INTO [t1446] VALUES (203,'باب الرخصة في ترك المرأة نقض ضفائر رأسها في الغسل من الجنابة','450','1','0');
INSERT INTO [t1446] VALUES (204,'باب غسل المرأة من الجنابة، والدليل على أن غسلها كغسل الرجل سواء','453','1','0');
INSERT INTO [t1446] VALUES (205,'باب الزجر عن دخول الماء بغير مئزر للغسل','455','1','0');
INSERT INTO [t1446] VALUES (206,'باب اغتسال الرجل والمرأة وهما جنبان من إناء واحد','457','1','0');
INSERT INTO [t1446] VALUES (207,'باب إفراغ المرأة الماء على يد زوجها ليغسل يديه قبل إدخالهما الإناء إذا أراد الاغتسال من الجنابة','459','1','0');
INSERT INTO [t1446] VALUES (208,'باب الأمر بالاغتسال إذا أسلم الكافر','461','1','0');
INSERT INTO [t1446] VALUES (209,'باب استحباب غسل الكافر إذا أسلم بالماء والسدر','464','1','0');
INSERT INTO [t1446] VALUES (210,'جماع أبواب غسل التطهير والاستحباب من غير فرض ولا إيجاب','467','1','0');
INSERT INTO [t1446] VALUES (211,'باب استحباب الاغتسال من الحجامة، ومن غسل الميت','468','1','0');
INSERT INTO [t1446] VALUES (212,'باب استحباب اغتسال المغمى عليه بعد الإفاقة من الإغماء','470','1','0');
INSERT INTO [t1446] VALUES (213,'باب ذكر الدليل على أن اغتسال النبي صلى الله عليه وسلم من الإغماء لم يكن اغتسال فرض ووجوب، وإنما اغتسل استراحة من الغم الذي أصابه في الإغماء ليخفف بدنه ويستريح "','472','1','0');
INSERT INTO [t1446] VALUES (214,'باب استحباب اغتسال الجنب للنوم','474','1','0');
INSERT INTO [t1446] VALUES (215,'باب ذكر دليل أن النبي صلى الله عليه وسلم قد كان يأمر بالوضوء قبل نزول سورة المائدة','476','1','0');
INSERT INTO [t1446] VALUES (216,'جماع أبواب التيمم عند الإعواز من الماء في السفر، وعند المرض الذي يخاف في إمساس الماء مواضع الوضوء والبدن في غسل الجنابة للمريض المخوف أو الألم الموجع أو التلف','478','1','0');
INSERT INTO [t1446] VALUES (217,'باب ذكر ما كان من إباحة الصلاة بلا تيمم عند عدم الماء قبل نزول آية التيمم','479','1','0');
INSERT INTO [t1446] VALUES (218,'باب الرخصة في النزول في السفر على غير ماء للحاجة تبدو من منافع الدنيا','481','1','0');
INSERT INTO [t1446] VALUES (219,'باب ذكر ما كان الله عز وجل فضل به رسول الله صلى الله عليه وسلم على الأنبياء قبله، وفضل أمته على الأمم السالفة قبلهم بإباحته لهم التيمم بالتراب عند الإعواز من الماء','483','1','0');
INSERT INTO [t1446] VALUES (220,'باب ذكر الدليل على أن ما وقع عليه اسم التراب فالتيمم به جائز عند الإعواز من الماء " وإن كان التراب على بساط أو ثوب، وإن لم يكن على الأرض مع الدليل على أن خبر أبي معاوية الذي ذكرناه مختصر " جعلت لنا الأرض طهورا " أي عند الإعواز من الماء إذا كان المحدث','485','1','0');
INSERT INTO [t1446] VALUES (221,'باب إباحة التيمم بتراب السباخ " ضد قول من زعم من أهل عصرنا أن التيمم بالسبخة غير جائز، وقول هذه المقالة يقود إلى أن التيمم بالمدينة غير جائز، إذ أرضها سبخة، وقد خبر النبي صلى الله عليه وسلم أنها طيبة أو طابة "','487','1','0');
INSERT INTO [t1446] VALUES (222,'باب ذكر الدليل على أن التيمم ضربة واحدة للوجه والكفين لا ضربتان، مع الدليل على أن مسح الذراعين في التيمم غير واجب','489','1','0');
INSERT INTO [t1446] VALUES (223,'باب النفخ في اليدين بعد ضربهما على التراب للتيمم','492','1','0');
INSERT INTO [t1446] VALUES (224,'باب نفض اليدين من التراب بعد ضربهما على الأرض قبل النفخ فيهما، وقبل مسح الوجه واليدين للتيمم','494','1','0');
INSERT INTO [t1446] VALUES (225,'باب ذكر الدليل على أن الجنب يجزيه التيمم عند الإعواز من الماء في السفر " والدليل على أن التيمم ليس كالغسل في جميع أحكامه، إذ المغتسل من الجنابة لا يجب عليه غسل ثان إلا بجنابة حادثة، والتيمم في الجنابة عند الإعواز من الماء يجب عليه غسل عند وجود الماء "','497','1','0');
INSERT INTO [t1446] VALUES (226,'باب الرخصة في التيمم للمجدور والمجروح، وإن كان الماء موجودا إذا خاف إن ماس الماء البدن التلف أو المرض أو الوجع المؤلم','499','1','0');
INSERT INTO [t1446] VALUES (227,'باب استحباب التيمم في الحضر لرد السلام، وإن كان الماء موجودا','502','1','0');
INSERT INTO [t1446] VALUES (228,'جماع أبواب تطهير الثياب بالغسل من الأنجاس','504','1','0');
INSERT INTO [t1446] VALUES (229,'باب حت دم الحيضة من الثوب، وقرصه بالماء ورش الثوب بعده','505','1','0');
INSERT INTO [t1446] VALUES (230,'باب ذكر الدليل على أن النضح المأمور به هو نضح ما لم يصب الدم من الثوب','507','1','0');
INSERT INTO [t1446] VALUES (231,'باب استحباب غسل دم الحيض من الثوب بالماء والسدر، وحكه بالأضلاع " إذ هو أحرى أن يذهب أثره من الثوب إذا حك بالضلع، وغسل بالسدر مع الماء، من أن يغسل بالماء بحتا "','509','1','0');
INSERT INTO [t1446] VALUES (232,'باب ذكر الدليل على أن الاقتصار من غسل الثوب الملبوس في المحيض على غسل أثر الدم منه جائز " وإن لم يحك موضع الدم بضلع، ولا قرص موضعه بالأظفار، وإن لم يغسل بسدر أيضا، ولا رش ما لم يصب الدم من الثوب، وأن جميع ما أمر به من قرص بالأظفار، وحك بالأضلاع،','511','1','0');
INSERT INTO [t1446] VALUES (233,'باب الرخصة في غسل الثوب من عرق الجنب " والدليل على أن عرق الجنب طاهر غير نجس "','513','1','0');
INSERT INTO [t1446] VALUES (234,'باب ذكر الدليل على أن عرق الإنسان طاهر غير نجس','516','1','0');
INSERT INTO [t1446] VALUES (235,'باب غسل بول الصبية من الثوب','518','1','0');
INSERT INTO [t1446] VALUES (236,'باب غسل بول الصبية، وإن كانت مرضعة " والفرق بين بولها، وبين بول الصبي المرضع "','521','1','0');
INSERT INTO [t1446] VALUES (237,'باب نضح بول الغلام، ورشه قبل أن يطعم','523','1','0');
INSERT INTO [t1446] VALUES (238,'باب استحباب غسل المني من الثوب','526','1','0');
INSERT INTO [t1446] VALUES (239,'باب ذكر الدليل على أن المني ليس بنجس " والرخصة في فركه إذا كان يابسا من الثوب، إذ النجس لا يزيله عن الثوب الفرك دون الغسل، وفي صلاة النبي صلى الله عليه وسلم في الثوب الذي قد أصابه مني بعد فركه يابسا ما بان، وثبت أن المني ليس بنجس "','528','1','0');
INSERT INTO [t1446] VALUES (240,'باب نضح الثوب من المذي إذا خفي موضعه في الثوب','532','1','0');
INSERT INTO [t1446] VALUES (241,'باب ذكر وطء الأذى اليابس بالخف والنعل " والدليل على أن ذلك لا يوجب غسل الخف ولا النعل، وأن تطهيرهما يكون بالمشي على الأرض الطاهرة بعدها "','534','1','0');
INSERT INTO [t1446] VALUES (242,'باب النهي عن البول في المساجد وتقذيرها','536','1','0');
INSERT INTO [t1446] VALUES (243,'باب سلت المني من الثوب بالإذخر إذا كان رطبا','538','1','0');
INSERT INTO [t1446] VALUES (244,'باب الزجر عن قطع البول على البائل في المسجد قبل الفراغ منه " والدليل على أن صب دلو من ماء يطهر الأرض، وإن لم يحفر موضع البول فينقل ترابه من المسجد على ما زعم بعض العراقيين، إذ الله عز وجل أنعم على عباده المؤمنين بأن بعث فيهم نبيه صلى الله عليه وسلم','541','1','0');
INSERT INTO [t1446] VALUES (245,'باب استحباب نضح الأرض من ربض الكلاب عليها','544','1','0');
INSERT INTO [t1446] VALUES (246,'باب الدليل على أن مرور الكلاب في المساجد لا يوجب نضحا ولا غسلا','546','1','0');
INSERT INTO [t1446] VALUES (247,'كتاب الصلاة " المختصر من المختصر من المسند الصحيح عن النبي صلى الله عليه وسلم على الشرط الذي اشترطنا في كتاب الطهارة "','548','0','0');
INSERT INTO [t1446] VALUES (248,'باب بدء فرض الصلوات الخمس','549','247','0');
INSERT INTO [t1446] VALUES (249,'باب ذكر فرض الصلوات الخمس من عدد الركعة بلفظ خبر مجمل غير مفسر، بلفظ عام مراده خاص','551','247','0');
INSERT INTO [t1446] VALUES (250,'باب ذكر الخبر المفسر للفظة المجملة التي ذكرتها، والدليل على أن قولها: إن الصلاة أول ما افترضت ركعتان أرادت بعض الصلاة دون جميعها " أرادت الصلوات الأربعة دون المغرب، وكذلك أرادت، ثم زيد في صلاة الحضر ثلاث صلوات خلا الفجر والمغرب، والدليل على أن قول','554','247','0');
INSERT INTO [t1446] VALUES (251,'باب فرض الصلوات الخمس " والدليل على أن لا فرض من الصلاة إلا الخمس، وأن كل ما سوى الخمس من الصلاة فتطوع ليس شيء منها فرض إلا الخمس فقط "','556','247','0');
INSERT INTO [t1446] VALUES (252,'باب الدليل على أن إقام الصلاة من الإيمان','558','247','0');
INSERT INTO [t1446] VALUES (253,'باب ذكر الدليل على أن إقام الصلاة من الإسلام " إذ الإيمان والإسلام إسمان بمعنى واحد " خبر عمر بن الخطاب في مسألة جبريل النبي صلى الله عليه وسلم عن الإسلام قد أمليته في كتاب الطهارة.','560','247','0');
INSERT INTO [t1446] VALUES (254,'باب في فضائل الصلوات الخمس','563','247','0');
INSERT INTO [t1446] VALUES (255,'باب ذكر الدليل على أن الحد الذي أصابه هذا السائل فأعلمه صلى الله عليه وسلم أن الله قد عفا عنه بوضوئه وصلاته كان معصية ارتكبها دون الزنا الذي يوجب الحد " إذ كل ما زجر الله عنه قد يقع عليه اسم حد، وليس اسم الحد إنما يقع على ما يوجب جلدا أو رجما أو قطعا قط','566','247','0');
INSERT INTO [t1446] VALUES (256,'باب ذكر الدليل على أن الصلوات الخمس إنما تكفر صغائر الذنوب دون كبائرها','569','247','0');
INSERT INTO [t1446] VALUES (257,'باب فضيلة السجود في الصلاة وحط الخطايا بها مع رفع الدرجات في الجنة','572','247','0');
INSERT INTO [t1446] VALUES (258,'باب فضل صلاة الصبح وصلاة العصر','574','247','0');
INSERT INTO [t1446] VALUES (259,'باب ذكر اجتماع ملائكة الليل وملائكة النهار في صلاة الفجر وصلاة العصر جميعا، ودعاء الملائكة لمن شهد الصلاتين جميعا','579','247','0');
INSERT INTO [t1446] VALUES (260,'باب ذكر مواقيت الصلوات الخمس','582','247','0');
INSERT INTO [t1446] VALUES (261,'باب ذكر الدليل على أن فرض الصلاة كان على الأنبياء قبل محمد صلى الله عليه وسلم كانت خمس صلوات، كما هي على النبي صلى الله عليه وسلم وأمته، وأن أوقات صلواتهم كانت أوقات النبي محمد صلى الله عليه وسلم وأمته','584','247','0');
INSERT INTO [t1446] VALUES (262,'باب ذكر وقت الصلاة للمعذور','586','247','0');
INSERT INTO [t1446] VALUES (263,'باب اختيار الصلاة في أول وقتها، بذكر خبر لفظه لفظ عام مراده خاص','588','247','0');
INSERT INTO [t1446] VALUES (264,'باب ذكر الدليل على أن النبي صلى الله عليه وسلم إنما أراد بقوله: " الصلاة في أول وقتها " بعض الصلاة دون جميعها، وبعض الأوقات دون جميع الأوقات، إذ قد أخبر النبي صلى الله عليه وسلم بتبريد الظهر في شدة الحر، وقد أعلم أن لولا ضعف الضعيف، وسقم السقيم لأخر','590','247','0');
INSERT INTO [t1446] VALUES (265,'باب استحباب تعجيل صلاة العصر','595','247','0');
INSERT INTO [t1446] VALUES (266,'باب ذكر التغليظ في تأخير صلاة العصر إلى اصفرار الشمس، والدليل على أن قوله صلى الله عليه وسلم في خبر عبد الله بن عمرو: " فإذا صليتم العصر فهو وقت إلى أن تصفر الشمس " إنما أراد وقت العذر والضرورة والناسي لصلاة العصر، فيذكرها قبل اصفرار الشمس أو عنده.','597','247','0');
INSERT INTO [t1446] VALUES (267,'باب التغليظ في تأخير صلاة العصر من غير ضرورة','600','247','0');
INSERT INTO [t1446] VALUES (268,'باب الأمر بتبكير صلاة العصر في يوم الغيم، والتغليظ في ترك صلاة العصر','602','247','0');
INSERT INTO [t1446] VALUES (269,'باب استحباب تعجيل صلاة المغرب','604','247','0');
INSERT INTO [t1446] VALUES (270,'باب التغليظ في تأخير صلاة المغرب، وإعلام النبي صلى الله عليه وسلم أمته أنهم لا يزالون بخير ثابتين على الفطرة، ما لم يؤخروها إلى اشتباك النجوم','607','247','0');
INSERT INTO [t1446] VALUES (271,'باب النهي عن تسمية صلاة المغرب عشاء، إذ العامة أو كثير منهم يسمونها عشاء','610','247','0');
INSERT INTO [t1446] VALUES (272,'باب استحباب تأخير صلاة العشاء إذا لم يخف المرء الرقاد قبلها، ولم يخف الإمام ضعف الضعيف وسقم السقيم فتفوتهم الجماعة لتأخير الإمام الصلاة، أو يشق عليهم حضور الجماعة إذا أخر صلاة العشاء','612','247','0');
INSERT INTO [t1446] VALUES (273,'باب كراهية النوم قبل صلاة العشاء والحديث بعدها بذكر خبر مجمل غير مفسر','617','247','0');
INSERT INTO [t1446] VALUES (274,'باب ذكر الخبر الدال على الرخصة في النوم قبل العشاء إذا أخرت الصلاة، وفيه ما دل على أن كراهة النبي صلى الله عليه وسلم النوم قبلها إذا لم تؤخر','620','247','0');
INSERT INTO [t1446] VALUES (275,'باب كراهة تسمية صلاة العشاء عتمة','623','247','0');
INSERT INTO [t1446] VALUES (276,'باب ذكر بيان الفجر الذي يجوز صلاة الصبح بعد طلوعه، إذ الفجر هنا فجران، طلوع أحدهما بالليل، وطلوع الثاني يكون بطلوع النهار','630','247','0');
INSERT INTO [t1446] VALUES (277,'باب فضل انتظار الصلاة والجلوس في المسجد وذكر دعاء الملائكة لمنتظر الصلاة الجالس في المسجد','632','247','0');
INSERT INTO [t1446] VALUES (278,'باب ذكر الدليل على أن الشيء قد يشبه بالشيء إذا اشتبه في بعض المعاني لا في جميعها، إذ النبي صلى الله عليه وسلم قد أعلم أن العبد لا يزال في صلاة ما دام في مصلاه ينتظرها، وإنما أراد النبي صلى الله عليه وسلم أنه لا يزال في صلاة، أي أن له أجر المصلي، لا','636','247','0');
INSERT INTO [t1446] VALUES (279,'جماع أبواب الأذان والإقامة','638','247','0');
INSERT INTO [t1446] VALUES (280,'باب في بدء الأذان والإقامة','639','247','0');
INSERT INTO [t1446] VALUES (281,'باب ذكر الدليل على أن من كان أرفع صوتا وأجهر، كان أحق بالأذان ممن كان أخفض صوتا، إذ الأذان إنما ينادى به لاجتماع الناس للصلاة','642','247','0');
INSERT INTO [t1446] VALUES (282,'باب الأمر بالأذان للصلاة قائما لا قاعدا، إذ الأذان قائما أحرى أن يسمعه من بعد عن المؤذن من أن يؤذن وهو قاعد','644','247','0');
INSERT INTO [t1446] VALUES (283,'باب ذكر الدليل على أن بدء الأذان إنما كان بعد هجرة النبي صلى الله عليه وسلم إلى المدينة، وأن صلاته بمكة إنما كانت من غير نداء لها ولا إقامة','646','247','0');
INSERT INTO [t1446] VALUES (284,'باب تثنية الأذان وإفراد الإقامة بذكر خبر مجمل غير مفسر بلفظ عام مراده خاص','648','247','0');
INSERT INTO [t1446] VALUES (285,'باب ذكر الدليل على أن الآمر بلالا أن يشفع الأذان ويوتر الإقامة كان النبي صلى الله عليه وسلم لا بعده أبو بكر ولا عمر، كما ادعى بعض الجهلة أنه جائز أن يكون الصديق أو الفاروق أمر بلالا بذلك','650','247','0');
INSERT INTO [t1446] VALUES (286,'باب ذكر الخبر المفسر للفظة المجملة التي ذكرتها، والدليل على أن النبي صلى الله عليه وسلم إنما أمر بأن يشفع بعض الأذان لا كلها، وأنه إنما أمر بأن يوتر بعض الإقامة لا كلها، وأن اللفظة التي في خبر أنس إنما هي من أخبار ألفاظ العام التي يراد بها الخاص، إذ','654','247','0');
INSERT INTO [t1446] VALUES (287,'باب تثنية قد قامت الصلاة في الإقامة، ضد قول بعض من لا يفهم العلم ولا يميز بين ما يكون لفظه عاما مراده خاص، وبين ما لفظه عام مراده عام، فتوهم بجهله أن قوله: ويوتر الإقامة كل الإقامة لا بعضها من أولها إلى آخرها، يعني الحسن بن الفضل','659','247','0');
INSERT INTO [t1446] VALUES (288,'باب الترجيع في الأذان مع تثنية الإقامة، وهذا من جنس اختلاف المباح، فمباح أن يؤذن المؤذن فيرجع في الأذان ويثني الإقامة، ومباح أن يثني الأذان ويفرد الإقامة، إذ قد صح كلا الأمرين من النبي صلى الله عليه وسلم، فأما تثنية الأذان والإقامة فلم يثبت عن النبي','662','247','0');
INSERT INTO [t1446] VALUES (289,'باب التثويب في أذان الصبح','670','247','0');
INSERT INTO [t1446] VALUES (290,'باب الانحراف في الأذان عند قول المؤذن حي على الصلاة، حي على الفلاح والدليل على أنه إنما ينحرف بفيه لا ببدنه كله وإنما يمكن الانحراف بالفم بانحراف الوجه','673','247','0');
INSERT INTO [t1446] VALUES (291,'باب إدخال الإصبعين في الأذنين عند الأذان، إن صح الخبر؛ فإن هذه اللفظة لست أحفظها إلا عن حجاج بن أرطاة، ولست أفهم أسمع الحجاج هذا الخبر من عون بن أبي جحيفة أم لا، فأشك في صحة هذا الخبر لهذه العلة','675','247','0');
INSERT INTO [t1446] VALUES (292,'باب فضل الأذان ورفع الصوت به وشهادة من يسمعه من حجر ومدر وشجر وجن وإنس للمؤذن','677','247','0');
INSERT INTO [t1446] VALUES (293,'باب الاستهام على الأذان إذا تشاجر الناس عليه','680','247','0');
INSERT INTO [t1446] VALUES (294,'باب ذكر تباعد الشيطان عن المؤذن عند أذانه وهربه كي لا يسمع الأذان','682','247','0');
INSERT INTO [t1446] VALUES (295,'باب الأمر بالأذان والإقامة في السفر للصلاة كلها ضد قول من زعم أنه لا يؤذن في السفر للصلاة إلا للفجر خاصة قال أبو بكر: خبر أبي ذر: كنا مع النبي صلى الله عليه وسلم في سفر فأراد المؤذن أن يؤذن فقال النبي صلى الله عليه وسلم: " أبرد "','685','247','0');
INSERT INTO [t1446] VALUES (296,'باب الأمر بالأذان والإقامة في السفر، وإن كانا اثنين لا أكثر بذكر خبر لفظه عام مراده خاص','687','247','0');
INSERT INTO [t1446] VALUES (297,'باب ذكر الخبر المفسر للفظة المجملة التي ذكرت أنها لفظة عام مرادها خاص، والدليل على أن النبي صلى الله عليه وسلم إنما أمر أن يؤذن أحدهما لا كليهما','690','247','0');
INSERT INTO [t1446] VALUES (298,'باب الأذان في السفر وإن كان المرء وحده ليس معه جماعة ولا واحد طلبا لفضيلة الأذان ضد قول من سئل عن الأذان في السفر فقال: لمن يؤذن؟ فتوهم أن الأذان لا يؤذن إلا لاجتماع الناس إلى الصلاة جماعة، والأذان وإن كان الأعم أنه يؤذن لاجتماع الناس إلى الصلاة جماعة','692','247','0');
INSERT INTO [t1446] VALUES (299,'باب إباحة الأذان للصبح قبل طلوع الفجر إذا كان للمسجد مؤذنان لا مؤذن واحد، فيؤذن أحدهما قبل طلوع الفجر، والآخر بعد طلوعه بذكر خبر مجمل غير مفسر','695','247','0');
INSERT INTO [t1446] VALUES (300,'باب ذكر العلة التي كان لها بلال يؤذن بليل','697','247','0');
INSERT INTO [t1446] VALUES (301,'باب ذكر قدر ما كان بين أذان بلال وأذان ابن أم مكتوم','699','247','0');
INSERT INTO [t1446] VALUES (302,'باب ذكر خبر روي عن النبي صلى الله عليه وسلم رأى بعض أهل الجهل أنه يضاد هذا الخبر الذي ذكرنا أن النبي صلى الله عليه وسلم قال: " إن بلالا يؤذن بليل "','701','247','0');
INSERT INTO [t1446] VALUES (303,'باب الأذان للصلوات بعد ذهاب الوقت','707','247','0');
INSERT INTO [t1446] VALUES (304,'باب الأمر بأن يقال ما يقوله المؤذن إذا سمعه ينادي بالصلاة، بلفظ عام مراده خاص','710','247','0');
INSERT INTO [t1446] VALUES (305,'باب ذكر الأخبار المفسرة للفظتين اللتين ذكرتهما في خبر أبي سعيد وأم حبيبة والدليل على أن النبي صلى الله عليه وسلم إنما أمر في خبر أبي سعيد أن يقال كما يقول المؤذن حتى يفرغ، وكذاك كان يقول كما يقول المؤذن حتى يسكت، خلا قوله حي على الصلاة، حي على الفلاح','714','247','0');
INSERT INTO [t1446] VALUES (306,'باب ذكر فضيلة هذا القول عند سماع الأذان إذا قاله المرء صدقا من قلبه','718','247','0');
INSERT INTO [t1446] VALUES (307,'باب فضل الصلاة على النبي صلى الله عليه وسلم بعد فراغ سماع الأذان','720','247','0');
INSERT INTO [t1446] VALUES (308,'باب استحباب الدعاء عند الأذان ورجاء إجابة الدعوة عنده','722','247','0');
INSERT INTO [t1446] VALUES (309,'باب صفة الدعاء عند مسألة الله عز وجل للنبي صلى الله عليه وسلم محمد الوسيلة، واستحقاق الداعي بتلك الدعوة الشفاعة يوم القيامة','724','247','0');
INSERT INTO [t1446] VALUES (310,'باب فضيلة الشهادة لله عز وجل بوحدانيته وللنبي صلى الله عليه وسلم برسالته وعبوديته وبالرضا بالله ربا وبمحمد رسولا وبالإسلام دينا عند سماع الأذان وما يرجى من مغفرة الذنوب بذلك','726','247','0');
INSERT INTO [t1446] VALUES (311,'باب الزجر عن أخذ الأجر على الأذان','729','247','0');
INSERT INTO [t1446] VALUES (312,'باب الرخصة في أذان الأعمى إذا كان له من يعلمه الوقت','731','247','0');
INSERT INTO [t1446] VALUES (313,'باب استحباب الدعاء بين الأذان والإقامة رجاء أن تكون الدعوة غير مردودة بينهما','733','247','0');
INSERT INTO [t1446] VALUES (314,'باب ذكر الصلاة كانت إلى بيت المقدس قبل هجرة النبي صلى الله عليه وسلم إلى المدينة، إذ القبلة في ذلك الوقت بيت المقدس لا الكعبة','737','247','0');
INSERT INTO [t1446] VALUES (315,'باب بدء الأمر باستقبال الكعبة للصلاة ونسخ الأمر بالصلوات إلى بيت المقدس قال أبو بكر: خبر البراء بن عازب من هذا الباب','740','247','0');
INSERT INTO [t1446] VALUES (316,'باب ذكر الدليل على أن القبلة إنما هي الكعبة لا جميع المسجد الحرام، وأن الله عز وجل إنما أراده بقوله: فول وجهك شطر المسجد الحرام؛ لأن الكعبة في المسجد الحرام، وإنما أمر النبي صلى الله عليه وسلم والمسلمين أن يصلوا إلى الكعبة إذ القبلة إنما هي الكعبة لا','742','247','0');
INSERT INTO [t1446] VALUES (317,'باب ذكر الدليل على أن الشطر في هذا الموضع القبل لا النصف، وهذا من الجنس الذي نقول إن العرب قد يوقع الاسم الواحد على الشيئين المختلفين، قد يوقع اسم الشطر على النصف وعلى القبل أي الجهة','747','247','0');
INSERT INTO [t1446] VALUES (318,'باب النهي عن التشبيك بين الأصابع عند الخروج إلى الصلاة','750','247','0');
INSERT INTO [t1446] VALUES (319,'باب الدعاء عند الخروج إلى الصلاة','757','247','0');
INSERT INTO [t1446] VALUES (320,'باب فضل المشي إلى المساجد للصلاة','759','247','0');
INSERT INTO [t1446] VALUES (321,'باب السلام على النبي صلى الله عليه وسلم ومسألة الله فتح أبواب الرحمة عند دخول المسجد','762','247','0');
INSERT INTO [t1446] VALUES (322,'باب القول عند الانتهاء إلى الصف قبل تكبيرة الافتتاح','764','247','0');
INSERT INTO [t1446] VALUES (323,'باب إيجاب استقبال القبلة للصلاة','766','247','0');
INSERT INTO [t1446] VALUES (324,'باب إحداث النية عند دخول كل صلاة يريدها المرء فينويها بعينها فريضة كانت أو نافلة، إذ الأعمال إنما تكون بالنية، وإنما يكون للمرء ما ينوي بحكم النبي المصطفى','768','247','0');
INSERT INTO [t1446] VALUES (325,'باب البدء برفع اليدين عند افتتاح الصلاة قبل التكبير','770','247','0');
INSERT INTO [t1446] VALUES (326,'باب الرخصة في رفع اليدين تحت الثياب في البرد وترك إخراجهما من الثياب عند رفعهما','772','247','0');
INSERT INTO [t1446] VALUES (327,'باب نشر الأصابع عند رفع اليدين في الصلاة','774','247','0');
INSERT INTO [t1446] VALUES (328,'باب التكبير لافتتاح الصلاة','778','247','0');
INSERT INTO [t1446] VALUES (329,'باب ذكر الدعاء بين تكبيرة الافتتاح وبين القراءة','780','247','0');
INSERT INTO [t1446] VALUES (330,'باب ذكر بيان إغفال من زعم أن الدعاء بما ليس في القرآن غير جائز في الصلاة المكتوبة، وهذا القول خلاف سنن النبي صلى الله عليه وسلم الثابتة، قد دعا النبي صلى الله عليه وسلم في أول صلاته ووسطها وآخرها بما ليس في القرآن','782','247','0');
INSERT INTO [t1446] VALUES (331,'باب إباحة الدعاء بعد التكبير وقبل القراءة بغير ما ذكرنا في خبر علي بن أبي طالب، والدليل على أن هذا الاختلاف في الافتتاح من جهة اختلاف المباح، جائز للمصلي أن يفتتح بكل ما ثبت عن النبي صلى الله عليه وسلم أنه افتتح الصلاة به بعد التكبير من حمد وثناء على','784','247','0');
INSERT INTO [t1446] VALUES (332,'باب الاستعاذة في الصلاة قبل القراءة قال الله عز وجل: فإذا قرأت القرآن فاستعذ بالله من الشيطان الرجيم','790','247','0');
INSERT INTO [t1446] VALUES (333,'باب ذكر سؤال العبد ربه عز وجل من فضله بين التكبير والقراءة في صلاة الفريضة ضد قول من زعم أن الدعاء بما ليس في القرآن يفسد صلاة الفريضة','792','247','0');
INSERT INTO [t1446] VALUES (334,'باب الأمر بالخشوع في الصلاة، إذ المصلي يناجي ربه، والمناجي ربه يجب عليه أن يفرغ قلبه لمناجاة خالقه عز وجل ولا يشغل قلبه التعلق بشيء من أمور الدنيا يشغله عن مناجاة خالقه','794','247','0');
INSERT INTO [t1446] VALUES (335,'باب التغليظ في النظر إلى السماء في الصلاة','796','247','0');
INSERT INTO [t1446] VALUES (336,'باب وضع اليمين على الشمال في الصلاة قبل افتتاح القراءة','799','247','0');
INSERT INTO [t1446] VALUES (337,'باب وضع بطن الكف اليمنى على كف اليسرى والرسغ والساعد جميعا','803','247','0');
INSERT INTO [t1446] VALUES (338,'باب في الخشوع في الصلاة أيضا، والزجر عن الالتفات في الصلاة إذ الله عز وجل يصرف وجهه عن وجه المصلي إذا التفت في صلاته','805','247','0');
INSERT INTO [t1446] VALUES (339,'باب ذكر الدليل على أن الالتفات في الصلاة ينقص الصلاة لا أنه يفسدها فسادا يجب عليه إعادتها','808','247','0');
INSERT INTO [t1446] VALUES (340,'باب ذكر الدليل على أن الالتفات المنهي عنه في الصلاة التي تكون صلاة المرء به ناقصة هو أن يلوي الملتفت عنقه، لا أن يلحظ بعينه يمينا وشمالا من غير أن يلوي عنقه، إذ النبي صلى الله عليه وسلم قد كان يلتفت في صلاته من غير أن يلوي عنقه خلف ظهره','810','247','0');
INSERT INTO [t1446] VALUES (341,'باب ذكر الدليل على أن الالتفات المنهي عنه في الصلاة هو الالتفات في الصلاة في غير الوقت الذي يحتاج المصلي أن يعرف فعل المأمومين أو بعضهم ليأمرهم بفعل أو يزجرهم عن فعل بإشارة أو إيماء يفهمهم ما يأتون وما يذرون في صلواتهم','812','247','0');
INSERT INTO [t1446] VALUES (342,'باب إيجاب القراءة في الصلاة بفاتحة الكتاب ونفي الصلاة بغير قراءتها','814','247','0');
INSERT INTO [t1446] VALUES (343,'باب ذكر لفظة رويت عن النبي صلى الله عليه وسلم في ترك قراءة فاتحة الكتاب بلفظ ادعت فرقة أنها دالة على أن ترك قراءة فاتحة الكتاب ينقص صلاة المصلي لا تبطل صلاته ولا يجب عليه إعادتها','816','247','0');
INSERT INTO [t1446] VALUES (344,'باب ذكر الدليل على أن الخداج الذي أعلم النبي صلى الله عليه وسلم في هذا الخبر هو النقص الذي لا تجزئ الصلاة معه، إذ النقص في الصلاة يكون نقصين، أحدهما لا تجزئ الصلاة مع ذلك النقص، والآخر تكون الصلاة جائزة مع ذلك النقص لا يجب إعادتها، وليس هذا النقص مما','818','247','0');
INSERT INTO [t1446] VALUES (345,'باب افتتاح القراءة بالحمد لله رب العالمين','820','247','0');
INSERT INTO [t1446] VALUES (346,'باب ذكر الدليل على أن بسم الله الرحمن الرحيم آية من فاتحة الكتاب','823','247','0');
INSERT INTO [t1446] VALUES (347,'باب ذكر خبر غلط في الاحتجاج به من لم يتبحر بالعلم فتوهم أن النبي صلى الله عليه وسلم لم يكن يقرأ ببسم الله الرحمن الرحيم في الصلاة في فاتحة الكتاب ولا في غيرها من السور','825','247','0');
INSERT INTO [t1446] VALUES (348,'باب ذكر الدليل على أن أنسا إنما أراد بقوله: لم أسمع أحدا منهم يقرأ: بسم الله الرحمن الرحيم. أي لم أسمع أحدا منهم يقرأ جهرا بسم الله الرحمن الرحيم، وأنهم كانوا يسرون بسم الله الرحمن الرحيم في الصلاة، لا كما توهم من لم يشتغل بطلب العلم من مظانه وطلب','827','247','0');
INSERT INTO [t1446] VALUES (349,'باب ذكر الدليل على أن الجهر ببسم الله الرحمن الرحيم والمخافتة به جميعا مباح، ليس واحد منهما محظورا، وهذا من اختلاف المباح','832','247','0');
INSERT INTO [t1446] VALUES (350,'باب فضل قراءة فاتحة الكتاب مع البيان أنها السبع المثاني، وأن الله لم ينزل في التوراة ولا في الإنجيل ولا في القرآن مثلها','834','247','0');
INSERT INTO [t1446] VALUES (351,'باب القراءة في الظهر والعصر في الأوليين منهما بفاتحة الكتاب وسورة، وفي الأخريين بفاتحة الكتاب ضد قول من زعم أن المصلي ظهرا أو عصرا مخير بين أن يقرأ في الأخريين منهما بفاتحة الكتاب وبين أن يسبح في الأخريين منهما، وخلاف قول من زعم أنه يسبح في الأخريين','838','247','0');
INSERT INTO [t1446] VALUES (352,'باب المخافتة بالقراءة في الظهر والعصر وترك الجهر فيهما بالقراءة','841','247','0');
INSERT INTO [t1446] VALUES (353,'باب إباحة الجهر ببعض الآي في صلاة الظهر والعصر','843','247','0');
INSERT INTO [t1446] VALUES (354,'باب تطويل الركعتين الأوليين من الظهر والعصر وحذف الأخريين منهما','845','247','0');
INSERT INTO [t1446] VALUES (355,'باب إباحة القراءة في الأخريين من الظهر والعصر بأكثر من فاتحة الكتاب، وهذا من اختلاف المباح لا من اختلاف الذي يكون أحدهما محظورا والآخر مباحا، فجائز أن يقرأ في الأخريين في كل ركعة بفاتحة الكتاب، فيقصر من القراءة عليها، ومباح أن يزاد في الأخريين على','847','247','0');
INSERT INTO [t1446] VALUES (356,'باب ذكر قراءة القرآن في الركعتين الأوليين من الظهر والعصر','849','247','0');
INSERT INTO [t1446] VALUES (357,'باب ذكر الدليل على أن الصلاة بقراءة فاتحة الكتاب جائزة دون غيرها من القراءة، وأن ما زاد على فاتحة الكتاب من القراءة في الصلاة فضيلة لا فريضة في خبر عبادة بن الصامت: " لا صلاة لمن لم يقرأ بفاتحة الكتاب " دلالة على أن من قرأ بها له صلاة. وفي خبر أبي','853','247','0');
INSERT INTO [t1446] VALUES (358,'باب القراءة في صلاة المغرب','855','247','0');
