INSERT INTO [t1446] VALUES (365,'باب إباحة ترداد المصلي قراءة السورة الواحدة في كل ركعتين من المكتوبة','885','247','0');
INSERT INTO [t1446] VALUES (366,'باب إباحة قراءة السورتين في الركعة الواحدة','887','247','0');
INSERT INTO [t1446] VALUES (367,'باب إباحة جمع السور في الركعة الواحدة من المفصل','889','247','0');
INSERT INTO [t1446] VALUES (368,'باب إباحة ترديد الآية الواحدة في الصلاة مرارا عند التدبر والتفكر في القرآن إن صح الخبر؛ فإن جسرة بنت دجاجة قالت: سمعت أبا ذر يقول: قام النبي صلى الله عليه وسلم بآية حتى أصبح يرددها والآية إن تعذبهم فإنهم عبادك وإن تغفر لهم فإنك أنت العزيز الحكيم','892','247','0');
INSERT INTO [t1446] VALUES (369,'باب إباحة قراءة السورة الواحدة في ركعتين من المكتوبة نا محمد بن العلاء بن كريب الهمداني أبو أسامة، عن هشام، عن أبيه، أن أبا أيوب، أو زيد بن ثابت، فذكر الحديث','893','247','0');
INSERT INTO [t1446] VALUES (370,'باب الدعاء في الصلاة بالمسألة عند قراءة آية الرحمة، والاستعاذة عند قراءة آية العذاب، والتسبيح عند قراءة آية التنزيه','895','247','0');
INSERT INTO [t1446] VALUES (371,'باب إجازة الصلاة بالتسبيح والتكبير والتحميد والتهليل لمن لا يحسن القرآن','898','247','0');
INSERT INTO [t1446] VALUES (372,'باب إباحة قراءة بعض السورة في الركعة الواحدة للعلة تعرض للمصلي','901','247','0');
