INSERT INTO [t1446] VALUES (415,'باب ذكر البيان أن النبي صلى الله عليه وسلم لم يكن يقنت دهره كله وإنه إنما كان يقنت إذا دعا لأحد، أو يدعو على أحد','1013','247','0');
