INSERT INTO [t1446] VALUES (417,'باب ذكر أخبار غلط في الاحتجاج بها بعض من لم ينعم النظر في ألفاظ الأخبار، ولم يستوعب أخبار النبي صلى الله عليه وسلم في القنوت، فاحتج بها وزعم أن القنوت في الصلاة منسوخ منهي عنه','1018','247','0');
