INSERT INTO [t1446] VALUES (563,'باب الأمر بالتسبيح للرجال والتصفيق للنساء عند النائبة تنوبهم في الصلاة','1385','247','0');
INSERT INTO [t1446] VALUES (564,'باب نسخ الكلام في الصلاة وحظره بعدما كان مباحا','1387','247','0');
