INSERT INTO [t1446] VALUES (566,'باب ذكر الكلام في الصلاة والمصلي غير عالم أنه قد بقي عليه بعض صلاته، والدليل على أن الكلام والمصلي هذه صفته غير مفسد للصلاة','1392','247','0');
INSERT INTO [t1446] VALUES (567,'باب ذكر ما خص الله عز وجل به نبيه صلى الله عليه وسلم وأبان به بينه وبين أمته من أن أوجب على الناس إجابته وإن كانوا في الصلاة، إذا دعاهم لما يحييهم','1394','247','0');
