INSERT INTO [t1446] VALUES (604,'باب الزجر عن غرز الضفائر في القفا في الصلاة، إذ هو مقعد للشيطان','1475','247','0');
INSERT INTO [t1446] VALUES (605,'باب الدليل على كراهة تشبيك الأصابع في الصلاة إذ النبي صلى الله عليه وسلم لما زجر عن تشبيك الأصابع عند الخروج إلى المسجد وفي المسجد، وأعلم أن الخارج إلى الصلاة في صلاة، كان المصلي أولى أن يشبك بين أصابعه ممن قد خرج إليها أو هو في المسجد ينتظرها قال أبو','1477','247','0');
INSERT INTO [t1446] VALUES (606,'باب الزجر عن تحريك الحصا بلفظ خبر مجمل غير مفسر','1478','247','0');
