INSERT INTO [t1446] VALUES (649,'باب صفة الصلاة جالسا إذا لم يقدر على القيام','1576','247','0');
INSERT INTO [t1446] VALUES (650,'باب صفة صلاة المريض مضطجعا إذا لم يقدر على القيام ولا على الجلوس','1578','247','0');
INSERT INTO [t1446] VALUES (651,'باب إباحة الصلاة راكبا وماشيا مستقبلي القبلة وغير مستقبليها عند الخوف قال الله جل وعلا فرجالا أو ركبانا','1580','247','0');
INSERT INTO [t1446] VALUES (652,'باب الرخصة في الصلاة ماشيا عند طلب العدو','1582','247','0');
INSERT INTO [t1446] VALUES (653,'باب الناسي للصلاة والنائم عنها يدرك ركعة منها قبل ذهاب وقتها','1584','247','0');
INSERT INTO [t1446] VALUES (654,'باب ذكر البيان ضد قول من زعم أن المدرك ركعة من صلاة الصبح قبل طلوع الشمس غير مدرك الصبح زعم أنه خرج من وقت الصلاة إلى غير وقت الصلاة، ففرق بين ما جمع النبي صلى الله عليه وسلم بينهما، وخالف النبي صلى الله عليه وسلم المصطفى بجهله، والنبي المصطفى الذي','1586','247','0');
INSERT INTO [t1446] VALUES (655,'باب الدليل على أن المدرك هذه الركعة مدرك لوقت الصلاة والواجب عليه إتمام صلاته','1588','247','0');
INSERT INTO [t1446] VALUES (656,'باب النائم عن الصلاة والناسي لها، لا يستيقظ ولا يدركها إلا بعد ذهاب الوقت','1590','247','0');
INSERT INTO [t1446] VALUES (657,'باب ذكر العلة التي لها أمر النبي صلى الله عليه وسلم أصحابه بالارتحال وترك الصلاة في ذلك المكان','1592','247','0');
INSERT INTO [t1446] VALUES (658,'باب النائم عن الصلاة والناسي لها يستيقظ أو يذكرها في غير وقت الصلاة','1594','247','0');
INSERT INTO [t1446] VALUES (659,'باب ذكر الدليل على أن أمر النبي صلى الله عليه وسلم بإعادة تلك الصلاة التي قد نام عنها أو نسيها، من الغد لوقتها بعد قضائها عند الاستيقاظ أو عند ذكرها، أمر فضيلة لا أمر عزيمة وفريضة، إذ النبي صلى الله عليه وسلم قد أعلم أن كفارة نسيان الصلاة أو النوم','1597','247','0');
INSERT INTO [t1446] VALUES (660,'باب ذكر الدليل على أن النبي صلى الله عليه وسلم إنما أمر بإعادة تلك الصلاة التي قد ينام عنها أو ذكرها بعد النسيان من الغد لوقتها قبل نهي الله عز وجل عن الربا، إذ النبي صلى الله عليه وسلم قد زجر عن إعادة تلك الصلاة من الغد بعد أمره كان بها، وأعلم أصحابه','1601','247','0');
INSERT INTO [t1446] VALUES (661,'باب ذكر الناسي للصلاة يذكرها في وقت صلاة الثانية والبدء بالأولى ثم بالثانية','1603','247','0');
INSERT INTO [t1446] VALUES (662,'باب ذكر فوت الصلوات والسنة في قضائها، إذا قضيت في وقت صلاة الأخيرة منها والاكتفاء بكل صلاة منها بإقامة واحدة والدليل على ضد قول من زعم أن الصلوات إذا فات وقتها لم تصل جماعة وإنما تصلى فرادى','1605','247','0');
INSERT INTO [t1446] VALUES (663,'باب الأذان للصلاة بعد ذهاب الوقت وإن كانت الإقامة تجزئ','1607','247','0');
INSERT INTO [t1446] VALUES (664,'باب الناسي لصلاة الفريضة يذكرها بعد ذهاب وقتها، والرخصة له في التطوع قبل الفريضة وفيه ما دل على أن النبي صلى الله عليه وسلم لم يرد بقوله: " من نام عن صلاة فليصلها إذا استيقظ " أن وقتها حين يستيقظ لا وقت لها غير ذلك، وإنما أراد أن فرض الصلاة غير ساقط','1610','247','0');
INSERT INTO [t1446] VALUES (665,'باب إسقاط فرض الصلاة عن الحائض أيام حيضها والدليل على أن الله عز وجل إنما فرض الصلاة في قوله قل لعبادي الذين آمنوا يقيموا الصلاة وفي قوله وأقيموا الصلاة على بعض المؤمنين لا على جميعهم، إذ لو كان فرض الصلاة على جميع المؤمنين كان فرض الصلاة على الحائض كما','1612','247','0');
INSERT INTO [t1446] VALUES (666,'باب ذكر نفي إيجاب قضاء الصلاة عن الحائض بعد طهرها من حيضها','1614','247','0');
INSERT INTO [t1446] VALUES (667,'باب أمر الصبيان بالصلاة وضربهم على تركها قبل البلوغ كي يعتادوا بها','1616','247','0');
INSERT INTO [t1446] VALUES (668,'باب ذكر الخبر الدال على أن أمر الصبيان بالصلاة قبل البلوغ على غير الإيجاب','1618','247','0');
INSERT INTO [t1446] VALUES (669,'جماع أبواب الصلاة على البسط','1620','247','0');
INSERT INTO [t1446] VALUES (670,'باب الصلاة على الحصير','1621','247','0');
INSERT INTO [t1446] VALUES (671,'باب الصلاة على البساط إن كان زمعة يجوز الاحتجاج بخبره','1623','247','0');
INSERT INTO [t1446] VALUES (672,'باب الصلاة على الفراء المدبوغة','1625','247','0');
INSERT INTO [t1446] VALUES (673,'باب الصلاة على الخمرة','1627','247','0');
INSERT INTO [t1446] VALUES (674,'باب الصلاة في النعلين والخيار للمصلي بين الصلاة فيهما وبين خلعهما ووضعهما بين رجليه، كي لا يؤذي بهما غيره','1630','247','0');
INSERT INTO [t1446] VALUES (675,'باب وضع المصلي نعليه عن يساره إذا خلعهما، إذا لم يكن عن يساره مصل، فيكون نعلاه عن يمين المصلي عن يساره','1636','247','0');
INSERT INTO [t1446] VALUES (676,'باب ذكر الزجر عن وضع المصلي نعليه عن يساره إذا كان عن يساره مصل، يكون النعلان عن يمين المصلي عن يساره','1639','247','0');
INSERT INTO [t1446] VALUES (677,'باب المصلي يصلي في نعليه وقد أصابهما قذر لا يعلم به والدليل على أن المصلي إذا صلى في نعل وثوب طاهر عنده، ثم بان عنده أن النعل أو الثوب كان غير طاهر، أن ما مضى من صلاته جائز عنه لا يجب عليه إعادته، إذ المرء إنما أمر أن يصلي في ثوب طاهر عنده، لا في','1641','247','0');
INSERT INTO [t1446] VALUES (678,'باب المصلي يشك في الحدث، والأمر بالمضي في صلاته وترك الانصراف عن الصلاة إذا خيل إليه أنه قد أحدث فيها، والدليل على أن يقين الطهارة لا يزول إلا بيقين حدث وأن الصلاة لا تفسد بالشك في الحدث حتى يستيقن المصلي بالحدث','1643','247','0');
INSERT INTO [t1446] VALUES (679,'باب الأمر بالانصراف من الصلاة إذا أحدث المصلي فيها ووضع اليد على الأنف كي يتوهم الناس أنه راعف لا محدث حدثا من دبر','1645','247','0');
INSERT INTO [t1446] VALUES (680,'السهو في الصلاة','1647','247','0');
INSERT INTO [t1446] VALUES (681,'باب ذكر المصلي يشك في صلاته والأمر بأن يسجد سجدتي السهو بذكر خبر مختصر غير متقصى، قد يحسب كثير ممن لا يميز بين المفسر والمجمل، ولا يفهم المختصر والمتقصى من الأخبار، أن الشاك في صلاته جائز له أن ينصرف من صلاته على الشك بعد أن يسجد سجدتي السهو','1648','247','0');
INSERT INTO [t1446] VALUES (682,'باب ذكر الخبر المتقصى في المصلي شك في صلاته والأمر بالبناء على الأقل مما يشك فيه المصلي، والدليل على أن النبي صلى الله عليه وسلم إنما أمر الشاك في صلاته بسجدتي السهو بعدما يبني على الأقل، فيتمم صلاته على يقين إذا لم يكن له تحر','1650','247','0');
INSERT INTO [t1446] VALUES (683,'باب ذكر البيان أن هاتين السجدتين اللتين يسجدهما الشاك في صلاته، إذا بنى على اليقين فيسجدهما قبل السلام لا بعد السلام ضد قول من زعم أن سجدتي السهو في جميع الأحوال تكونان بعد السلام','1652','247','0');
INSERT INTO [t1446] VALUES (684,'باب الأمر بتحسين ركوع هذه الركعة وسجودها التي يصليها لتمام صلاته أو نافلته','1654','247','0');
INSERT INTO [t1446] VALUES (685,'باب ذكر المصلي يشك في صلاته وله تحر، والأمر بالبناء على التحري إذا كان قلبه إلى أحد العددين أميل، وكان أكثر ظنه أنه قد صلى ما القلب إليه أميل','1657','247','0');
INSERT INTO [t1446] VALUES (686,'باب ذكر القيام من الركعتين قبل الجلوس ساهيا، والمضي في الصلاة إذا استوى المصلي قائما، وإيجاب سجدتي السهو على فاعله','1659','247','0');
INSERT INTO [t1446] VALUES (687,'باب ذكر البيان أن المصلي إذا قام من الثنتين فاستوى قائما، ثم ذكر بتسبيح أنه ناس للجلوس، أن عليه المضي في صلاته، ترك الركوع إلى الجلوس، وعليه سجدتا السهو قبل السلام','1662','247','0');
INSERT INTO [t1446] VALUES (688,'باب الأمر بسجدتي السهو إذا نسي المصلي شيئا من صلاته','1665','247','0');
INSERT INTO [t1446] VALUES (689,'باب التسليم من الركعتين ساهيا في الظهر أو العصر أو العشاء وإباحة البناء على ما قد صلى المصلي قبل تسليمه في الركعتين ساهيا، والدليل على أن السلام ساهيا قبل الفراغ من الصلاة لا تفسد الصلاة','1667','247','0');
INSERT INTO [t1446] VALUES (690,'باب إيجاب سجدتي السهو على المسلم قبل الفراغ من الصلاة ساهيا، والدليل أن هاتين السجدتين إنما يسجدهما المصلي بعد السلام لا قبل','1669','247','0');
INSERT INTO [t1446] VALUES (691,'باب ذكر خبر روي في قصة ذي اليدين، أدرج لفظه الزهري في متن الحديث فتوهم من لم يتبحر العلم ولم يكتب من الحديث إلا نتفا أن أبا هريرة قال تلك اللفظة التي قالها الزهري في آخر الخبر، وتوهم أيضا أن هذا الخبر الذي زاد فيه الزهري هذه اللفظة خلاف الأخبار الثابتة','1673','247','0');
INSERT INTO [t1446] VALUES (692,'باب ذكر التسليم من الركعتين من المغرب ساهيا والدليل على الفرق بين الكلام في الصلاة ساهيا وبين الكلام في الصلاة عامدا، إذ مخالفونا من العراقيين يتابعونا على الفرق بين السلام قبل الفراغ من الصلاة عامدا وبين السلام ساهيا، فيوجبون على المسلم عامدا إعادة','1678','247','0');
INSERT INTO [t1446] VALUES (693,'باب ذكر الجلوس في الثالثة، والتسليم منها ساهيا في الظهر أو العصر أو العشاء، والدليل على إغفال من زعم أن المسلم ساهيا في الثالثة إذا تكلم بعد السلام وهو غير ذاكر أنه قد بقي عليه بعض صلاته أن عليه إعادة الصلاة، وهذا القول خلاف سنة النبي صلى الله عليه','1681','247','0');
INSERT INTO [t1446] VALUES (694,'باب ذكر المصلي يصلي خمس ركعات ساهيا والأمر بسجدتي السهو إذا صلى خمسا من غير أن يضيف إليها سادسة، والدليل على ضد قول من زعم من العراقيين أنه إن كان جلس في الرابعة مقدار التشهد أضاف إلى الخامسة سادسة، ثم سجد سجدتي السهو، وإن لم يكن جلس في الرابعة مقدار','1683','247','0');
INSERT INTO [t1446] VALUES (695,'باب ذكر السنة في سجدتي السهو بعد الكلام ساهيا ضد قول من زعم أن المسلم من الصلاة إذا كان قد سها في صلاته فتكلم بعد السلام ساهيا، أنه لا يسجد سجدتي السهو، وهذا القول خلاف الثابت من سنة النبي صلى الله عليه وسلم.','1687','247','0');
INSERT INTO [t1446] VALUES (696,'باب السلام بعد سجدتي السهو إذا سجدهما المصلي بعد السلام','1690','247','0');
INSERT INTO [t1446] VALUES (697,'باب التشهد بعد سجدتي السهو إذا سجدهما المصلي بعد السلام','1693','247','0');
INSERT INTO [t1446] VALUES (698,'باب ذكر تسمية سجدتي السهو المرغمتين؛ إذ هما ترغمان الشيطان','1695','247','0');
INSERT INTO [t1446] VALUES (699,'باب ذكر الدليل على أن المسبوق بركعة أو ثلاث لا تجب عليه سجدتا السهو بجلوسه في الأولى والثالثة اقتداء بإمامه، ضد قول من زعم أن المدرك وترا من صلاة الإمام تجب عليه سجدتا السهو، وهاتان السجدتان لو يسجدهما المصلي كانتا سجدتي العمد لا السهو؛ لأن المدرك','1697','247','0');
INSERT INTO [t1446] VALUES (700,'جماع أبواب ذكر الوتر وما فيه من السنن','1700','247','0');
INSERT INTO [t1446] VALUES (701,'باب ذكر الأخبار المنصوصة والدالة على أن الوتر ليس بفرض لا على ما زعم من لم يفهم العدد، ولا فرق بين الفرض وبين الفضيلة، فزعم أن الوتر فريضة، فلما سئل عن عدد الفرض من الصلاة زعم أن الفرض من الصلاة خمس، فقيل له: والوتر، فقال: فريضة، فقال السائل:','1701','247','0');
INSERT INTO [t1446] VALUES (702,'باب الترغيب في الوتر واستحبابه إذ الله يحبه','1705','247','0');
INSERT INTO [t1446] VALUES (703,'باب ذكر الأخبار المنصوصة عن النبي صلى الله عليه وسلم أن الوتر ركعة','1707','247','0');
INSERT INTO [t1446] VALUES (704,'باب إباحة الوتر بخمس ركعات، وصفة الجلوس في الوتر إذا أوتر بخمس ركعات، وهذا من اختلاف المباح','1712','247','0');
INSERT INTO [t1446] VALUES (705,'باب ذكر الخبر المفسر أن النبي صلى الله عليه وسلم لم يكن يجلس إلا في الخامسة إذا أوتر بخمس','1714','247','0');
INSERT INTO [t1446] VALUES (706,'باب إباحة الوتر بسبع ركعات أو بتسع وصفة الجلوس إذا أوتر بسبع أو بتسع','1716','247','0');
INSERT INTO [t1446] VALUES (707,'باب إباحة الوتر أول الليل إن أحب المصلي أو وسطه أو آخره، إذ الليل بعد العشاء الآخرة إلى طلوع الفجر كله وقت الوتر','1719','247','0');
INSERT INTO [t1446] VALUES (708,'باب الأمر بالوتر من آخر الليل بذكر خبر مختصر غير متقصى ومجمل غير مفسر','1722','247','0');
INSERT INTO [t1446] VALUES (709,'باب ذكر الوصية بالوتر قبل النوم بلفظ مجمل غير مفسر، قد يسبق علمي إلى وهم من لا يميز بين الخبر المختصر والخبر المتقصى، ولا يستدل بالمفسر من الأخبار على المجمل منها، أن أمر النبي صلى الله عليه وسلم بأن يجعل آخر صلاة الليل وترا يضاد أمره ووصيته بالوتر','1724','247','0');
INSERT INTO [t1446] VALUES (710,'باب ذكر الخبر المفسر للفظتين المجملتين اللتين ذكرتهما في البابين المقدمين، والدليل على أن النبي صلى الله عليه وسلم أمر بالوتر قبل النوم أخذا بالوثيقة والحزم، تخوفا أن لا يستيقظ المرء آخر الليل فيوتر آخره. وأنه إنما أمر بالوتر آخر الليل من قوي على قيام','1726','247','0');
INSERT INTO [t1446] VALUES (711,'باب الأمر بمبادرة طلوع الفجر بالوتر إذ الوتر وقته الليل، لا الليل والنهار ولا بعض النهار أيضا.','1730','247','0');
INSERT INTO [t1446] VALUES (712,'باب الرخصة في الوتر راكبا في السفر وفيه ما دل على أن الوتر ليست بفريضة، إذ النبي صلى الله عليه وسلم لم يكن يصلي المكتوبة على راحلته في الحالة التي كان يوتر عليها','1735','247','0');
INSERT INTO [t1446] VALUES (713,'باب النائم عن الوتر أو الناسي له يصبح قبل أن يوتر','1737','247','0');
INSERT INTO [t1446] VALUES (714,'باب ذكر خبر روي في وتر النبي صلى الله عليه وسلم بعد الفجر مجمل غير مفسر أوهم بعض من لم يتبحر العلم ولم يكتب من العلم ما يستدل بالخبر المفسر على الخبر المجمل أن النبي صلى الله عليه وسلم أوتر بعد طلوع الفجر الثاني','1740','247','0');
INSERT INTO [t1446] VALUES (715,'باب ذكر الدليل على أن النبي صلى الله عليه وسلم إنما أوتر هذه الليلة التي بات ابن عباس فيها عنده بعد طلوع الفجر الذي يكون بعد طلوعه ليل لا نهار، لا بعد طلوع الفجر الثاني الذي يكون بعد طلوعه نهار، مع الدليل على أن النبي صلى الله عليه وسلم لم يركع ركعتي','1742','247','0');
INSERT INTO [t1446] VALUES (716,'باب الزجر أن يوتر المصلي في الليلة الواحدة مرتين إذ الموتر مرتين تصير صلاته بالليل شفعا لا وترا','1750','247','0');
INSERT INTO [t1446] VALUES (717,'باب الرخصة في الصلاة بعد الوتر','1752','247','0');
INSERT INTO [t1446] VALUES (718,'باب ذكر القراءة في الركعتين اللتين كان النبي صلى الله عليه وسلم يصليهما بعد الوتر','1755','247','0');
INSERT INTO [t1446] VALUES (719,'باب ذكر الدليل على أن الصلاة بعد الوتر مباحة لجميع من يريد الصلاة بعده، وأن الركعتين اللتين كان النبي صلى الله عليه وسلم يصليهما بعد الوتر لم يكونا خاصة للنبي صلى الله عليه وسلم دون أمته، إذ النبي صلى الله عليه وسلم قد أمرنا بالركعتين بعد الوتر، أمر','1758','247','0');
INSERT INTO [t1446] VALUES (720,'جماع أبواب الركعتين قبل الفجر وما فيها من السنن','1760','247','0');
INSERT INTO [t1446] VALUES (721,'باب فضل ركعتي الفجر إذ هما خير من الدنيا جميعا','1761','247','0');
INSERT INTO [t1446] VALUES (722,'باب المسارعة إلى الركعتين قبل الفجر اقتداء بالنبي المصطفى صلى الله عليه وسلم','1763','247','0');
INSERT INTO [t1446] VALUES (723,'باب ذكر الدليل على أن عائشة إنما أرادت بقولها: الخير النوافل دون خير الفريضة، إذ اسم الخير قد يقع على الفريضة والنافلة جميعا','1765','247','0');
INSERT INTO [t1446] VALUES (724,'باب الأمر بالركعتين قبل الفجر أمر ندب واستحباب لا أمر فريضة وإيجاب','1767','247','0');
INSERT INTO [t1446] VALUES (725,'باب وقت ركعتي الفجر','1769','247','0');
