INSERT INTO [t1446] VALUES (771,'باب ذكر الخبر الدال على أن هذه الأخبار الثلاثة التي ذكرتها ليست بمتضادة ولا متهاترة " والدليل على أن النبي صلى الله عليه وسلم قد كان يصلي من الليل ثلاث عشرة ركعة على ما أخبر ابن عباس، ثم نقص ركعتين فكان يصلي إحدى عشرة ركعة من الليل على ما أخبر أبو سلمة','1874','247','0');
INSERT INTO [t1446] VALUES (772,'باب قضاء صلاة الليل بالنهار إذا فاتت لمرض أو شغل أو نوم','1876','247','0');
