INSERT INTO [t1446] VALUES (780,'باب ذكر الخبر المفسر للفظة المجملة التي ذكرتها " والدليل على أن النبي صلى الله عليه وسلم إنما أراد بقوله: " في كل يوم " أي في كل يوم وليلة مع بيان عدد هذه الركعات قبل الفرائض وبعدهن، قد كنت أعلمت في كتاب معاني القرآن أن العرب قد تقول: يوما تريد بليلته','1902','247','0');
