INSERT INTO [t1446] VALUES (787,'باب ذكر الخبر المفسر لأمر النبي صلى الله عليه وسلم بأن تصلى الركعتان بعد المغرب في البيوت " والدليل على أن الأمر بذلك أمر استحباب لا أمر إيجاب، إذ صلاة النوافل في البيوت أفضل من النوافل في المساجد "','1922','247','0');
