INSERT INTO [t1446] VALUES (791,'باب ذكر الدليل على أن النبي صلى الله عليه وسلم إنما أمر بأن يجعل بعض صلاة التطوع في البيوت لا كلها " إذ الله جل وعلا يجعل في بيت المصلي من صلاته خيرا " " خبر ابن عمر: " اجعلوا من صلاتكم في بيوتكم " دال على أنه إنما أمر بأن يجعل بعض الصلاة في البيوت لا','1930','247','0');
