INSERT INTO [t1446] VALUES (844,'باب ذكر الخبر المفسر لبعض اللفظة المجملة التي ذكرتها والدليل على أن النبي صلى الله عليه وسلم إنما نهى عن الصلاة بعد العصر حتى تغرب الشمس إذا كانت الشمس غير مرتفعة، فدانت للغروب','2061','247','0');
