INSERT INTO [t1446] VALUES (889,'جماع أبواب صلاة الخوف','2165','247','0');
INSERT INTO [t1446] VALUES (890,'باب صلاة الإمام في شدة الخوف بكل طائفة من المأمومين ركعة واحدة لتكون للإمام ركعتان ولكل طائفة ركعة " وترك الطائفتين قضاء الركعة الثانية، وفي هذا ما دل على جواز فريضة للمأموم خلف الإمام المصلي نافلة "','2166','247','0');
INSERT INTO [t1446] VALUES (891,'باب ذكر البيان أن النبي صلى الله عليه وسلم صلى هذه الصلاة بكل طائفة ركعة ولم يقض الطائفتان شيئا، والعدو بينه وبين القبلة، وأن الطائفة التي حرست من العدو كانت أمام النبي صلى الله عليه وسلم لا خلفه','2169','247','0');
INSERT INTO [t1446] VALUES (892,'باب صفة صلاة الخوف " والخوف أقل مما ذكرنا، إذا كان العدو بين المسلمين وبين القبلة، وافتتاح كلتا الطائفتين الصلاة مع الإمام وركوعهما مع الإمام معا "','2171','247','0');
INSERT INTO [t1446] VALUES (893,'باب في صفة الخوف أيضا " والخوف أشد مما تقدم ذكرنا له في الباب قبل هذا، وإباحة افتتاح الصف الثاني صلواتهم مع الإمام وهم قعود وافتتاح الصف الأول صلواتهم مع الإمام وهم قيام "','2173','247','0');
INSERT INTO [t1446] VALUES (894,'باب في صفة صلاة الخوف والعدو خلف القبلة " وصلاة الإمام بكل طائفة ركعتين، وهذا أيضا الجنس الذي أعلمت من جواز صلاة المأموم فريضة خلف الإمام المصلي نافلة، إذ إحدى الركعتين كانت للنبي صلى الله عليه وسلم تطوعا وللمأمومين فريضة "','2175','247','0');
INSERT INTO [t1446] VALUES (895,'باب في صلاة الخوف أيضا إذا كان العدو خلف القبلة " والرخصة للطائفة الأولى في ترك استقبالها القبلة بعد فراغها من الركعة الأولى لتحرس الطائفة الثانية من العدو وقضاء الطائفتين الركعة الثانية بعد تسليم الإمام "','2178','247','0');
INSERT INTO [t1446] VALUES (896,'باب في صلاة الخوف أيضا إذا كان العدو خلف القبلة وإتمام الطائفة الأولى الركعة الثانية قبل الإمام','2180','247','0');
INSERT INTO [t1446] VALUES (897,'باب انتظار الإمام الطائفة الأولى جالسا لتقضي الركعة الثانية، وانتظاره الطائفة الثانية جالسا قبل التسليم ليقضي الركعة الثانية','2182','247','0');
INSERT INTO [t1446] VALUES (898,'باب في صلاة الخوف أيضا، والرخصة لإحدى الطائفتين أن تكبر مع الإمام وهي غير مستقبلة القبلة إذا كان العدو خلف القبلة " وانتظار الإمام قائما بعد فراغه من الركعة الأولى للطائفة التي كبرت غير مستقبلي القبلة فيصلي الركعة التي سبقهم بها الإمام وانتظار الطائفة','2184','247','0');
INSERT INTO [t1446] VALUES (899,'باب في صلاة الخوف أيضا وانتظار الإمام الطائفة الأولى بعد سجدة من الركعة الأولى ليسجد السجدة الثانية " وانتظار الثانية حتى تركع ركعة لتلحق بالإمام فتسجد معه السجدة الثانية، ثم ينتظرهم الإمام قائما لتسجد السجدة الثانية، وجمع الإمام الطائفتين جميعا','2186','247','0');
INSERT INTO [t1446] VALUES (900,'باب الإقامة لصلاة الخوف " وقد كنت بينت في كتاب معاني القرآن، أن قوله تعالى: فأقمت لهم الصلاة تحمل معنيين: أي صليت لهم، والمعنى الثاني أي أمرت بإقامة الصلاة لاجتماع الناس للصلاة، وأعلمت أن هذا على هذا المعنى من الجنس الذي أعلمنا في غير موضع من كتبنا','2188','247','0');
INSERT INTO [t1446] VALUES (901,'باب الرخصة في القتال والكلام في صلاة الخوف، قبل إتمام الصلاة، إذا خافوا غلبة العدو','2190','247','0');
INSERT INTO [t1446] VALUES (902,'باب إباحة صلاة الخوف ركبانا ومشاة في شدة الخوف " قال الله عز وجل: فإن خفتم فرجالا أو ركبانا "','2192','247','0');
