INSERT INTO [t1446] VALUES (905,'جماع أبواب صلاة الكسوف','2198','247','0');
INSERT INTO [t1446] VALUES (906,'باب الأمر بالصلاة عند كسوف الشمس والقمر " والدليل على أنهما لا ينكسفان لموت أحد وأنهما آيتان من آيات الله "','2199','247','0');
INSERT INTO [t1446] VALUES (907,'باب ذكر الخبر الدال على أن كسوفهما تخويف من الله لعباده " قال الله عز وجل وما نرسل بالآيات إلا تخويفا "','2201','247','0');
