INSERT INTO [t1446] VALUES (1024,'باب إمامة المولى القرشي إذا كان المولى أكثر جمعا للقرآن خبر النبي صلى الله عليه وسلم: " يؤمهم أقرؤهم لكتاب الله " دلالة على أن المولى إذا كان أقرأ من القرشي فهو أحق بالإمامة','2451','991','0');
INSERT INTO [t1446] VALUES (1025,'باب إباحة إمامة غير المدرك البالغين إذا كان غير المدرك أكثر جمعا للقرآن من البالغين','2453','991','0');
INSERT INTO [t1446] VALUES (1026,'باب ذكر الدليل على ضد قول من كره للابن إمامة أبيه قال أبو بكر: خبر النبي صلى الله عليه وسلم: " يؤم القوم أقرؤهم "','2455','991','0');
INSERT INTO [t1446] VALUES (1027,'باب التغليط على الأئمة في تركهم إتمام الصلاة، وتأخيرهم الصلاة " والدليل على أن صلاة الإمام قد تكون ناقصة، وصلاة المأموم تامة، ضد قول من زعم أن صلاة المأموم متصلة بصلاة الإمام، إذا فسدت صلاة الإمام، فسدت صلاة المأموم، زعم "','2456','991','0');
INSERT INTO [t1446] VALUES (1028,'باب الرخصة في ترك انتظار الإمام إذا أبطأ، وأمر المأمومين أحدهم بالإمامة','2458','991','0');
INSERT INTO [t1446] VALUES (1029,'باب الرخصة في صلاة الإمام الأعظم خلف من أم الناس من رعيته، وإن كان الإمام من الرعية يؤم الناس بغير إذن الإمام الأعظم قال أبو بكر: خبر المغيرة بن شعبة في إمامة عبد الرحمن بن عوف','2460','991','0');
INSERT INTO [t1446] VALUES (1030,'باب إمامة المرء السلطان بأمره، واستخلاف الإمام رجلا من الرعية إذا غاب عن حضرة المسجد الذي يؤم الناس فيه فتكون الإمامة بأمره قال أبو بكر: خبر أبي حازم، عن سهل بن سعد " في أمر النبي صلى الله عليه وسلم بلالا إذا حضرت العصر ولم يأت أن يأمر أبا بكر يصلي','2463','991','0');
