INSERT INTO [t1446] VALUES (1041,'باب قيام المأموم الواحد عن يمين الإمام إذا لم يكن معهما أحد','2485','991','0');
INSERT INTO [t1446] VALUES (1042,'باب ذكر الدليل على ضد قول من زعم أن المأموم يقوم خلف الإمام " ينتظر مجيء غيره فإن فرغ الإمام من القراءة، وأراد الركوع قبل مجيء غيره، تقدم فقام عن يمين الإمام "','2487','991','0');
