INSERT INTO [t1446] VALUES (1066,'باب فضل تليين المناكب في القيام في الصفوف','2544','991','0');
INSERT INTO [t1446] VALUES (1067,'باب طرد المصطفين بين السواري عنها','2546','991','0');
INSERT INTO [t1446] VALUES (1068,'باب النهي عن الاصطفاف بين السواري','2548','991','0');
INSERT INTO [t1446] VALUES (1069,'باب الزجر عن صلاة المأموم خلف الصف وحده " والبيان أن صلاته خلف الصف وحده غير جائزة، يجب عليه استقبالها، وأن قوله: لا صلاة له من الجنس الذي نقول: إن العرب تنفي الاسم عن الشيء لنقصه عن الكمال "','2550','991','0');
INSERT INTO [t1446] VALUES (1070,'باب الرخصة في ركوع المأموم قبل اتصاله بالصف، ودبيبه راكعا حتى يتصل بالصف في ركوعه','2552','991','0');
INSERT INTO [t1446] VALUES (1071,'باب ذكر البيان أن أولي الأحلام والنهى أحق بالصف الأول إذ النبي صلى الله عليه وسلم أمر بأن يلوه','2554','991','0');
INSERT INTO [t1446] VALUES (1072,'باب الرخصة في شق أولي الأحلام، والنهى للصفوف إذا كانوا قد اصطفوا عند حضورهم ليقوموا في الصف الأول','2557','991','0');
INSERT INTO [t1446] VALUES (1073,'باب أمر المؤمنين بالاقتداء بالإمام، والنهي عن مخالفتهم إياه','2559','991','0');
INSERT INTO [t1446] VALUES (1074,'باب الزجر عن مبادرة المأموم الإمام بالتكبير، والركوع، والسجود','2561','991','0');
INSERT INTO [t1446] VALUES (1075,'باب ذكر البيان أن المأموم إنما يكبر بعد فراغ الإمام من التكبير لا يكون مكبرا حتى يفرغ من التكبير، ويتم الراء التي هي آخر التكبير، والفرق بين قوله: " إذا كبر فكبروا "، وبين قوله: " وإذا ركع فاركعوا، وإذا سجد فاسجدوا "، إذ اسم المكبر لا يقع على','2563','991','0');
INSERT INTO [t1446] VALUES (1076,'باب سكوت الإمام قبل القراءة، وبعد تكبيرة الافتتاح','2565','991','0');
INSERT INTO [t1446] VALUES (1077,'باب ذكر البيان أن اسم الساكت قد يقع على الناطق سرا إذا كان ساكتا عن الجهر بالقول " إذ النبي صلى الله عليه وسلم قد كان داعيا خفيا في سكته عن الجهر بين التكبيرة الأولى، وبين القراءة "','2567','991','0');
INSERT INTO [t1446] VALUES (1078,'باب تطويل الإمام الركعة الأولى من الصلوات ليتلاحق المأمومون','2569','991','0');
INSERT INTO [t1446] VALUES (1079,'باب القراءة خلف الإمام، وإن جهر الإمام بالقراءة " والزجر عن أن يزيد المأموم على قراءة فاتحة الكتاب إذا جهر الإمام بالقراءة "','2571','991','0');
INSERT INTO [t1446] VALUES (1080,'باب تأمين المأموم عند فراغ الإمام من قراءة فاتحة الكتاب في الصلاة التي يجهر فيها الإمام بالقراءة، وإن نسي إمام وجهل، ولم يؤمن','2573','991','0');
INSERT INTO [t1446] VALUES (1081,'باب فضل تأمين المأموم إذا أمن إمامه " رجاء مغفرة ما تقدم من ذنب المؤمن إذا وافق تأمينه الملائكة مع الدليل على أن على الإمام الجهر بالتأمين إذا جهر بالقراءة ليسمع المأموم تأمينه، إذ غير جائز أن يأمر النبي صلى الله عليه وسلم المأموم بالتأمين إذا أمن إمامه','2575','991','0');
INSERT INTO [t1446] VALUES (1082,'باب ذكر إجابة الرب عز وجل عند فراغ قراءة فاتحة الكتاب','2577','991','0');
INSERT INTO [t1446] VALUES (1083,'باب ذكر حسد اليهود المؤمنين على تأمينهم','2579','991','0');
INSERT INTO [t1446] VALUES (1084,'باب ذكر ما كان الله عز وجل خص نبيه صلى الله عليه وسلم بالتأمين " فلم يعطه أحدا من النبيين قبله، خلا هارون حين دعا موسى، فأمن هارون، إن ثبت الخبر "','2581','991','0');
INSERT INTO [t1446] VALUES (1085,'باب السنة في جهر الإمام بالقراءة، واستحباب الجهر بالقراءة جهرا بين المخافتة، وبين الجهر الرفيع','2583','991','0');
INSERT INTO [t1446] VALUES (1086,'باب ذكر مخافتة الإمام القراءة في الظهر والعصر، وإباحة الجهر ببعض الآي أحيانا فيما يخافت بالقراءة في الصلاة','2585','991','0');
INSERT INTO [t1446] VALUES (1087,'باب جهر الإمام بالقراءة في صلاة المغرب','2587','991','0');
INSERT INTO [t1446] VALUES (1088,'باب جهر الإمام بالقراءة في صلاة العشاء','2589','991','0');
INSERT INTO [t1446] VALUES (1089,'باب جهر الإمام بالقراءة في صلاة الغداة','2591','991','0');
INSERT INTO [t1446] VALUES (1090,'باب ذكر الخبر المفسر أن النبي صلى الله عليه وسلم إنما كان يجهر في الأوليين من المغرب، والأوليين من العشاء " لا في جميع الركعات كلها، من المغرب والعشاء إن ثبت الخبر مسندا، ولا أخال، وإنما خرجت هذا الخبر في هذا الكتاب إذ لا خلاف بين أهل القبلة في صحة','2593','991','0');
INSERT INTO [t1446] VALUES (1091,'باب الأمر بمبادرة الإمام المأموم بالركوع والسجود','2595','991','0');
INSERT INTO [t1446] VALUES (1092,'باب النهي عن مبادرة الإمام المأموم بالركوع، والإخبار بأن الإمام ما سبق المأموم في الركوع، أدركه المأموم بعد رفع الإمام رأسه من الركوع','2597','991','0');
INSERT INTO [t1446] VALUES (1093,'باب ذكر الوقت الذي فيه المأموم مدركا للركعة إذا ركع إمامه قبل','2599','991','0');
INSERT INTO [t1446] VALUES (1094,'باب رفع الإمام رأسه من الركوع قبل المأموم قال أبو بكر: في خبر أبي موسى: " فإن الإمام يركع قبلكم، ويرفع قبلكم " قال نبي الله صلى الله عليه وسلم: " فتلك بتلك "','2601','991','0');
INSERT INTO [t1446] VALUES (1095,'باب الأمر بتحميد المأموم ربه عز وجل عند رفع الرأس من الركوع، ورجاء مغفرة ذنوبه إذا وافق تحميده تحميد الملائكة','2602','991','0');
INSERT INTO [t1446] VALUES (1096,'باب مبادرة الإمام المأموم بالسجود، وثبوت المأموم قائما، وتركه الانحناء للسجود حتى يسجد إمامه','2604','991','0');
INSERT INTO [t1446] VALUES (1097,'باب التغليظ في مبادرة المأموم الإمام برفع الرأس من السجود','2607','991','0');
INSERT INTO [t1446] VALUES (1098,'باب ذكر إدراك المأموم ما فاته من سجود الإمام بعد رفع الإمام رأسه قال أبو بكر: في خبر أبي موسى: " فإن الإمام يسجد قبلكم، ويرفع قبلكم، فتلك بتلك "، وفي خبر معاوية: " ومهما أسبقكم به إذا سجدت تدركوني به إذا رفعت "','2609','991','0');
INSERT INTO [t1446] VALUES (1099,'باب النهي عن مبادرة المأموم الإمام بالقيام والقعود','2610','991','0');
INSERT INTO [t1446] VALUES (1100,'باب افتتاح الإمام القراءة في الركعة الثانية في الصلاة التي يجهر فيها من غير سكت فيها','2612','991','0');
INSERT INTO [t1446] VALUES (1101,'باب تخفيف الإمام الصلاة مع الإتمام','2614','991','0');
INSERT INTO [t1446] VALUES (1102,'باب النهي عن تطويل الإمام الصلاة مخافة تنفير المأمومين، وقنوتهم','2616','991','0');
INSERT INTO [t1446] VALUES (1103,'باب قدر قراءة الإمام الذي لا يكون تطويلا','2618','991','0');
INSERT INTO [t1446] VALUES (1104,'باب تقدير الإمام الصلاة بضعفاء المأمومين، وكبارهم، وذوي الحوائج منهم','2621','991','0');
INSERT INTO [t1446] VALUES (1105,'باب تخفيف الإمام القراءة للحاجة تبدو لبعض المأمومين','2623','991','0');
INSERT INTO [t1446] VALUES (1106,'باب الرخصة في تخفيف الإمام الصلاة للحاجة تبدو لبعض المأمومين بعد ما قد نوى إطالتها','2625','991','0');
INSERT INTO [t1446] VALUES (1107,'باب الرخصة في خروج المأموم من صلاة الإمام للحاجة تبدو له من أمور الدنيا إذا طول الصلاة','2627','991','0');
INSERT INTO [t1446] VALUES (1108,'باب الأمر بائتمام أهل الصفوف الأواخر بأهل الصفوف الأول','2629','991','0');
INSERT INTO [t1446] VALUES (1109,'باب أمر المأموم بالصلاة جالسا إذا صلى إمامه جالسا','2631','991','0');
INSERT INTO [t1446] VALUES (1110,'باب أمر المأموم بالجلوس بعد افتتاحه الصلاة قائما إذا صلى الإمام قاعدا','2633','991','0');
INSERT INTO [t1446] VALUES (1111,'باب النهي عن صلاة المأموم قائما خلف الإمام قاعدا','2635','991','0');
INSERT INTO [t1446] VALUES (1112,'باب ذكر أخبار تأولها بعض العلماء ناسخة لأمر رسول الله صلى الله عليه وسلم المأموم بالصلاة جالسا إذا صلى إمامه جالسا','2637','991','0');
INSERT INTO [t1446] VALUES (1113,'باب إدراك المأموم الإمام ساجدا، والأمر بالاقتداء به في السجود، وأن لا يعتد به إذ المدرك للسجدة إنما يكون بإدراك الركوع قبلها','2642','991','0');
INSERT INTO [t1446] VALUES (1114,'باب إجازة الصلاة الواحدة بإمامين أحدهما بعد الآخر من غير حدث الأول، إذا ترك الأول الإمامة بعد ما قد دخل فيها، فيتقدم الثاني فيتم الصلاة من الموضع الذي كان انتهى إليه الأول، وإجازة صلاة المصلي يكون إماما في بعض الصلاة مأموما في بعضها، وإجازة ائتمام','2644','991','0');
INSERT INTO [t1446] VALUES (1115,'باب استخلاف الإمام الأعظم في المرض بعض رعيته ليتولى الإمامة بالناس','2646','991','0');
INSERT INTO [t1446] VALUES (1116,'باب ذكر استخلاف الإمام عند الغيبة عن حضرة المسجد الذي هو إمامه عند الحاجة تبدو له قال أبو بكر: في خبر سهل بن سعد وخروجه إلى بني عمرو ليصلح بينهم قال لبلال: " إذا حضرت الصلاة ولم آت فمر أبا بكر فليصل بالناس ".','2648','991','0');
INSERT INTO [t1446] VALUES (1117,'باب الرخصة في الاقتداء بالمصلي الذي ينوي الصلاة منفردا، ولا ينوي إمامة المقتدي به','2649','991','0');
INSERT INTO [t1446] VALUES (1118,'باب افتتاح غير الطاهر الصلاة ناويا الإمامة وذكره أنه غير طاهر بعد الافتتاح، وتركه الاستخلاف عند ذلك لينتظر المأمومون رجوعه بعد الطهارة فيؤمهم','2652','991','0');
INSERT INTO [t1446] VALUES (1119,'باب الرخصة في خصوصية الإمام نفسه بالدعاء دون المأمومين خلاف الخبر غير الثابت المروي عن النبي صلى الله عليه وسلم: أنه قد خانهم إذا خص نفسه بالدعاء دونهم','2654','991','0');
INSERT INTO [t1446] VALUES (1120,'باب الرخصة في الصلاة جماعة في المسجد الذي قد جمع فيه ضد قول من زعم أنهم يصلون فرادى إذا صلى في المسجد جماعة مرة','2656','991','0');
INSERT INTO [t1446] VALUES (1121,'باب إباحة ائتمام المصلي فريضة بالمصلي نافلة، ضد قول من زعم من العراقيين أنه غير جائز أن يأتم المصلي فريضة بالمصلي نافلة','2658','991','0');
INSERT INTO [t1446] VALUES (1122,'باب ذكر البيان أن معاذا كان يصلي مع النبي صلى الله عليه وسلم فريضة لا تطوعا كما ادعى بعض العراقيين قال أبو بكر: في خبر عبيد الله بن مقسم، عن جابر، كان معاذ يصلي مع رسول الله صلى الله عليه وسلم العشاء، ثم يرجع فيصلي بأصحابه. قال أبو بكر: قد أمليت','2661','991','0');
INSERT INTO [t1446] VALUES (1123,'باب الأمر بالصلاة منفردا عند تأخير الإمام الصلاة جماعة','2662','991','0');
INSERT INTO [t1446] VALUES (1124,'باب الأمر بالصلاة جماعة بعد أداء الفرض منفردا عند تأخير الإمام الصلاة، والبيان أن الأولى تكون فرضا منفردا، والثانية نافلة في جماعة، ضد قول من زعم أن الصلاة جماعة هي الفريضة لا الصلاة منفردا، والزجر عن ترك الصلاة نافلة خلف الإمام المصلي فريضة، وإن','2664','991','0');
INSERT INTO [t1446] VALUES (1125,'باب الصلاة جماعة بعد صلاة الصبح منفردا فتكون الصلاة جماعة للمأموم نافلة، وصلاة المنفرد قبلها فريضة، والدليل على أن قول النبي صلى الله عليه وسلم: " لا صلاة بعد الصبح حتى تطلع الشمس " نهي خاص لا نهي عام','2666','991','0');
INSERT INTO [t1446] VALUES (1126,'باب النهي عن ترك الصلاة جماعة نافلة بعد الصلاة منفردا فريضة','2668','991','0');
INSERT INTO [t1446] VALUES (1127,'باب ذكر الدليل على أن الصلاة الأولى التي يصليها المرء في وقتها تكون فريضة، والثانية التي يصليها جماعة مع الإمام تكون تطوعا، ضد قول من زعم أن الثانية تكون فريضة، والأولى نافلة، مع الدليل على أن الإمام إذا أخر العصر فعلى المرء أن يصلي العصر في وقتها،','2670','991','0');
INSERT INTO [t1446] VALUES (1128,'باب النهي عن إعادة الصلاة على نية الفرض','2672','991','0');
INSERT INTO [t1446] VALUES (1129,'باب المدرك وترا من صلاة الإمام، وجلوسه في الوتر من صلاته اقتداء بالإمام','2674','991','0');
INSERT INTO [t1446] VALUES (1130,'باب إمامة المسافر المقيمين، وإتمام المقيمين صلاتهم بعد فراغ الإمام إن ثبت الخبر؛ فإن في القلب من علي بن زيد بن جدعان، وإنما خرجت هذا الخبر في هذا الكتاب؛ لأن هذه مسألة لا يختلف العلماء فيها','2676','991','0');
INSERT INTO [t1446] VALUES (1131,'باب المسبوق ببعض الصلاة، والأمر باقتدائه بالإمام فيما يدرك، وإتمامه ما سبق به بعد فراغ الإمام من الصلاة','2678','991','0');
INSERT INTO [t1446] VALUES (1132,'باب المسبوق بوتر من صلاة الإمام، والدليل على أن لا سجدتي السهو عليه، ضد قول من زعم أنه عليه سجدتا السهو، على مذهبهم في هذه المسألة تكون سجدتا العمد، لا سجدتا السهو، إذ المأموم إنما يتعمد الجلوس في الوتر من صلاته اقتداء بإمامه إذ كان للإمام شفع، وله','2680','991','0');
INSERT INTO [t1446] VALUES (1133,'باب تلقين الإمام إذا تعايا، أو ترك شيئا من القرآن','2683','991','0');
INSERT INTO [t1446] VALUES (1134,'باب وضع الإمام نعليه عن يساره','2686','991','0');
INSERT INTO [t1446] VALUES (1135,'جماع أبواب العذر الذي يجوز فيه ترك إتيان الجماعة','2688','991','0');
INSERT INTO [t1446] VALUES (1136,'باب الرخصة للمريض في ترك إتيان الجماعة','2689','991','0');
INSERT INTO [t1446] VALUES (1137,'باب الرخصة في ترك الجماعة عند حضور العشاء','2691','991','0');
INSERT INTO [t1446] VALUES (1138,'باب الرخصة في ترك الجماعة إذا كان المرء حاقنا','2693','991','0');
INSERT INTO [t1446] VALUES (1139,'باب الرخصة في ترك العميان الجماعة في الأمطار، والسيول','2695','991','0');
INSERT INTO [t1446] VALUES (1140,'باب إباحة ترك الجماعة في السفر، والأمر بالصلاة في الرحال في الليلة المطيرة والباردة، بذكر خبر مختصر غير متقصى لو حمل الخبر على ظاهره كان شهود الجماعة في الليلة المطيرة والباردة معصية، إذ النبي صلى الله عليه وسلم قد أمر بالصلاة في الرحال','2697','991','0');
INSERT INTO [t1446] VALUES (1141,'باب إباحة ترك الجماعة في السفر في الليلة المظلمة، وإن لم تكن باردة، ولا مطيرة بمثل اللفظ الذي ذكرت في الباب قبل','2699','991','0');
INSERT INTO [t1446] VALUES (1142,'باب إباحة ترك الجماعة في السفر، والأمر بالصلاة في الرحال في المطر القليل غير المؤذي بمثل اللفظ الذي ذكرت قبل','2701','991','0');
INSERT INTO [t1446] VALUES (1143,'باب إباحة الصلاة في الرحال، وترك الجماعة في اليوم المطير في السفر مثل اللفظة التي ذكرت قبل، والدليل على أن حكم النهار في إباحة ترك الصلاة في الجماعة في المطر كحكم الليل سواء','2703','991','0');
INSERT INTO [t1446] VALUES (1144,'باب ذكر الخبر المتقصي للفظة المختصرة التي ذكرتها من أمر النبي صلى الله عليه وسلم بالصلاة في الرحال، والدليل على أن أمر النبي صلى الله عليه وسلم بذلك أمر إباحة لا أمر عزم، يكون متعديه عاصيا إن شهد الصلاة جماعة في المطر','2705','991','0');
INSERT INTO [t1446] VALUES (1145,'باب إتيان المساجد في الليلة المطيرة المظلمة، والدليل على أن الأمر بالصلاة في الرحال في مثل تلك الليلة أمر إباحة له لا حتم','2707','991','0');
INSERT INTO [t1446] VALUES (1146,'باب النهي عن إتيان الجماعة لآكل الثوم','2709','991','0');
INSERT INTO [t1446] VALUES (1147,'باب توقيت النهي عن إتيان الجماعة لآكل الثوم','2712','991','0');
INSERT INTO [t1446] VALUES (1148,'باب النهي عن إتيان المساجد لآكل الثوم','2714','991','0');
INSERT INTO [t1446] VALUES (1149,'باب النهي عن إتيان الجماعة لآكل الكراث','2716','991','0');
INSERT INTO [t1446] VALUES (1150,'باب الدليل على أن النهي عن إتيان المساجد لآكلهن نيئا غير مطبوخ','2718','991','0');
INSERT INTO [t1446] VALUES (1151,'باب الدليل على أن النهي عن ذلك لتأذي الناس بريحه لا تحريما لأكله','2720','991','0');
INSERT INTO [t1446] VALUES (1152,'باب ذكر الدليل على أن النهي عن ذلك لتأذي الملائكة بريحه إذ الناس يتأذون به','2722','991','0');
INSERT INTO [t1446] VALUES (1153,'باب النهي عن إتيان المسجد لآكل الثوم، والبصل، والكراث إلى أن يذهب ريحه','2724','991','0');
INSERT INTO [t1446] VALUES (1154,'باب ذكر ما خص الله به نبيه صلى الله عليه وسلم من ترك أكل الثوم، والبصل، والكراث مطبوخا','2726','991','0');
INSERT INTO [t1446] VALUES (1155,'باب الدليل على أن النبي صلى الله عليه وسلم خص بترك أكلهن لمناجاة الملائكة','2728','991','0');
INSERT INTO [t1446] VALUES (1156,'باب الرخصة في أكله عند الضرورة والحاجة إليه','2730','991','0');
INSERT INTO [t1446] VALUES (1157,'باب صلاة التطوع بالنهار في الجماعة ضد مذهب من كره ذلك','2732','991','0');
INSERT INTO [t1446] VALUES (1158,'باب صلاة التطوع بالليل في الجماعة في غير رمضان ضد مذهب من كره ذلك','2734','991','0');
INSERT INTO [t1446] VALUES (1159,'باب الوتر جماعة في غير رمضان','2736','991','0');
INSERT INTO [t1446] VALUES (1160,'جماع أبواب صلاة النساء في الجماعة','2738','991','0');
INSERT INTO [t1446] VALUES (1161,'باب إمامة المرأة النساء في الفريضة','2739','991','0');
INSERT INTO [t1446] VALUES (1162,'باب الإذن للنساء في إتيان المساجد','2741','991','0');
INSERT INTO [t1446] VALUES (1163,'باب النهي عن منع النساء الخروج إلى المساجد بالليل','2743','991','0');
INSERT INTO [t1446] VALUES (1164,'باب الأمر بخروج النساء إلى المساجد تفلات','2745','991','0');
INSERT INTO [t1446] VALUES (1165,'باب الزجر عن شهود المرأة المسجد متعطرة','2747','991','0');
INSERT INTO [t1446] VALUES (1166,'باب التغليظ في تعطر المرأة عند الخروج ليوجد ريحها وتسمية فاعلها زانية، والدليل على أن اسم الزاني قد يقع على من يفعل فعلا لا يوجب ذلك الفعل جلدا ولا رجما، مع الدليل على أن التشبيه الذي يوجب ذلك الفعل إنما يكون إذا اشتبهت العلتان لا لاجتماع الاسم، إذ','2749','991','0');
INSERT INTO [t1446] VALUES (1167,'باب إيجاب الغسل على المتطيبة للخروج إلى المسجد، ونفي قبول صلاتها إن صلت قبل أن تغتسل','2751','991','0');
INSERT INTO [t1446] VALUES (1168,'باب اختيار صلاة المرأة في بيتها على صلاتها في المسجد، إن ثبت الخبر، فإني لا أعرف السائب مولى أم سلمة بعدالة ولا جرح، ولا أقف على سماع حبيب بن أبي ثابت هذا الخبر من ابن عمر، ولا هل سمع قتادة خبره من مورق، عن أبي الأحوص أم لا؛ بل كأني لا أشك أن قتادة','2753','991','0');
INSERT INTO [t1446] VALUES (1169,'باب اختيار صلاة المرأة في بيتها على صلاتها في حجرتها، إن كان قتادة سمع هذا الخبر من مورق','2758','991','0');
INSERT INTO [t1446] VALUES (1170,'باب اختيار صلاة المرأة في حجرتها على صلاتها في دارها، وصلاتها في مسجد قومها على صلاتها في مسجد النبي صلى الله عليه وسلم، وإن كانت صلاة في مسجد النبي صلى الله عليه وسلم تعدل ألف صلاة في غيرها من المساجد، والدليل على أن قول النبي صلى الله عليه وسلم: "','2760','991','0');
INSERT INTO [t1446] VALUES (1171,'باب اختيار صلاة المرأة في مخدعها على صلاتها في بيتها','2762','991','0');
INSERT INTO [t1446] VALUES (1172,'باب اختيار صلاة المرأة في أشد مكان من بيتها ظلمة','2764','991','0');
INSERT INTO [t1446] VALUES (1173,'باب فضل صفوف النساء المؤخرة على الصفوف المقدمة، والدليل على أن صفوفهن إذا كانت متباعدة عن صفوف الرجال كانت أفضل','2767','991','0');
INSERT INTO [t1446] VALUES (1174,'باب أمر النساء بخفض أبصارهن إذا صلين مع الرجال إذا خفن رؤية عورات الرجال إذا سجد الرجال أمامهن','2769','991','0');
INSERT INTO [t1446] VALUES (1175,'باب الزجر عن رفع النساء رءوسهن من السجود، إذا صلين مع الرجال قبل استواء الرجال جلوسا، إذا ضاقت أزرهم، فخيف أن يرى النساء عوراتهم','2771','991','0');
INSERT INTO [t1446] VALUES (1176,'باب التغليظ في قيام المأموم في الصف المؤخر إذا كان خلفه نساء، إذا أراد النظر إليهن، أو إلى بعضهن، والدليل على أن المصلي إذا نظر إلى من خلفه من النساء لم يفسد ذلك الفعل صلاته','2773','991','0');
INSERT INTO [t1446] VALUES (1177,'باب ذكر الدليل على أن النهي عن منع النساء المساجد كان إذ كن لا يخاف فسادهن في الخروج إلى المساجد، وظن لا بيقين','2775','991','0');
INSERT INTO [t1446] VALUES (1178,'باب ذكر بعض أحداث نساء بني إسرائيل الذي من أجله منعن المساجد','2777','991','0');
INSERT INTO [t1446] VALUES (1179,'باب الرخصة في إمامة المماليك الأحرار إذا كان المماليك أقرأ من الأحرار','2780','991','0');
INSERT INTO [t1446] VALUES (1180,'باب الصلاة جماعة في الأسفار','2782','991','0');
INSERT INTO [t1446] VALUES (1181,'باب الصلاة جماعة بعد ذهاب وقتها','2784','991','0');
INSERT INTO [t1446] VALUES (1182,'باب الجمع بين الصلاتين في الجماعة في السفر','2786','991','0');
INSERT INTO [t1446] VALUES (1183,'باب الأمر بالفصل بين الفريضة والتطوع بالكلام أو الخروج','2788','991','0');
INSERT INTO [t1446] VALUES (1184,'باب رفع الصوت بالتكبير والذكر عند قضاء الإمام الصلاة','2790','991','0');
INSERT INTO [t1446] VALUES (1185,'باب نية المصلي بالسلام من عن يمينه إذا سلم عن يمينه، ومن عن شماله إذا سلم عن يساره','2793','991','0');
INSERT INTO [t1446] VALUES (1186,'باب سلام المأموم من الصلاة عند سلام الإمام','2795','991','0');
INSERT INTO [t1446] VALUES (1187,'باب رد المأموم على الإمام إذا سلم الإمام عند انقضاء الصلاة','2797','991','0');
INSERT INTO [t1446] VALUES (1188,'باب إقبال الإمام بوجهه يمنة إذا سلم عن يمينه، ويسرة إذا سلم عن شماله، وفيه دليل أيضا أن الإمام إذا سلم عن يمينه، والمأمومين الذين عن يساره إذا سلم عن يساره','2800','991','0');
INSERT INTO [t1446] VALUES (1189,'باب انحراف الإمام من الصلاة التي لا يتطوع بعدها','2802','991','0');
INSERT INTO [t1446] VALUES (1190,'باب تخيير الإمام في الانصراف من الصلاة أن ينصرف يمنة، أو ينصرف يسرة','2804','991','0');
INSERT INTO [t1446] VALUES (1191,'باب إباحة استقبال الإمام بوجهه بعد السلام إذا لم يكن مقابله من قد فاته بعض صلاة الإمام فيكون مقابل الإمام إذا قام يقضي','2806','991','0');
INSERT INTO [t1446] VALUES (1192,'باب الزجر عن مبادرة الإمام بالانصراف من الصلاة','2808','991','0');
INSERT INTO [t1446] VALUES (1193,'باب نهوض الإمام عند الفراغ من الصلاة التي يتطوع بعدها ساعة يسلم من غير لبث، إذا لم يكن خلفه نساء','2810','991','0');
INSERT INTO [t1446] VALUES (1194,'باب ذكر الدليل على أن النبي صلى الله عليه وسلم إنما كان يقوم ساعة يسلم إذا لم يكن خلفه نساء، واستحباب ثبوت الإمام جالسا إذا كان خلفه نساء ليرجع النساء قبل أن يلحقهم الرجال','2812','991','0');
INSERT INTO [t1446] VALUES (1195,'باب تخفيف ثبوت الإمام بعد السلام لينصرف النساء قبل الرجال، وترك تطويله الجلوس بعد السلام','2814','991','0');
INSERT INTO [t1446] VALUES (1196,'كتاب الجمعة المختصر من المختصر من المسند على الشرط الذي ذكرنا في أول الكتاب','2816','0','0');
INSERT INTO [t1446] VALUES (1197,'باب ذكر فرض الجمعة والبيان أن الله عز وجل فرضها على من قبلنا من الأمم واختلفوا فيها فهدى الله أمة محمد صلى الله عليه وسلم خير أمة أخرجت للناس لها قال الله عز وجل: يا أيها الذين آمنوا إذا نودي للصلاة من يوم الجمعة فاسعوا إلى ذكر الله وذروا البيع، وهذا','2817','1196','0');
INSERT INTO [t1446] VALUES (1198,'باب الدليل أن فرض الجمعة على البالغين دون الأطفال، وهذا من الجنس الذي نقول: إنه من الأخبار المعللة الذي يجوز القياس عليه، قد بينته في عقب الخبر','2819','1196','0');
INSERT INTO [t1446] VALUES (1199,'باب ذكر إسقاط فرض الجمعة عن النساء، والدليل على أن الله عز وجل خاطب بالأمر بالسعي إلى الجمعة عند النداء بها في قوله: يا أيها الذين آمنوا إذا نودي للصلاة من يوم الجمعة الآية، الرجال دون النساء إن ثبت هذا الخبر من جهة النقل، وإن لم يثبت فاتفاق العلماء','2821','1196','0');
INSERT INTO [t1446] VALUES (1200,'باب ذكر أول جمعة جمعت بمدينة النبي صلى الله عليه وسلم، وذكر عدد من جمع بها أولا','2823','1196','0');
INSERT INTO [t1446] VALUES (1201,'باب ذكر الجمعة التي جمعت بعد الجمعة التي جمعت بالمدينة، وذكر الموضع الذي جمع به','2825','1196','0');
INSERT INTO [t1446] VALUES (1202,'باب ذكر من الله عز وجل على أمة محمد صلى الله عليه وسلم خير أمة أخرجت للناس بهدايته إياهم ليوم الجمعة، فله الحمد كثيرا على ذلك، إذ قد ضل عنه أهل الكتاب قبلهم بعد فرض الله ذلك عليهم، والدليل على أن الهداية هدايتان على ما بينته في كتاب " أحكام القرآن "','2827','1196','0');
INSERT INTO [t1446] VALUES (1203,'جماع أبواب فضل الجمعة','2829','1196','0');
INSERT INTO [t1446] VALUES (1204,'باب في ذكر فضل يوم الجمعة، وأنها أفضل الأيام، وفزع الخلق غير الثقلين الجن والإنس بذكر خبر مختصر غير متقصى','2830','1196','0');
INSERT INTO [t1446] VALUES (1205,'باب ذكر الخبر المتقصى للفظة المختصرة التي ذكرتها، والدليل على أن العلة التي تفزع الخلق لها من يوم الجمعة هي خوفهم من قيام الساعة فيها إذ الساعة تقوم يوم الجمعة','2832','1196','0');
INSERT INTO [t1446] VALUES (1206,'باب صفة يوم الجمعة، وأهلها إذا بعثوا يوم القيامة، إن صح الخبر فإن في النفس من هذا الإسناد','2835','1196','0');
INSERT INTO [t1446] VALUES (1207,'باب ذكر الساعة التي فيها خلق الله آدم من يوم الجمعة','2837','1196','0');
INSERT INTO [t1446] VALUES (1208,'باب ذكر العلة التي أحسب لها سميت الجمعة جمعة','2839','1196','0');
INSERT INTO [t1446] VALUES (1209,'باب فضل الصلاة على النبي صلى الله عليه وسلم يوم الجمعة','2841','1196','0');
INSERT INTO [t1446] VALUES (1210,'باب ذكر بعض ما خص به يوم الجمعة من الفضيلة، بأن جعل الله فيه ساعة يستجيب فيها دعاء المصلي، بذكر خبر مجمل غير مفسر مختصر غير متقصى','2843','1196','0');
INSERT INTO [t1446] VALUES (1211,'باب ذكر الخبر المتقصى لبعض هذه اللفظة المجملة التي ذكرتها، والدليل على أن النبي صلى الله عليه وسلم إنما أعلم أن هذه الساعة التي في الجمعة إنما يستجاب فيها دعاء المصلي دون غيره، وفيه اختصار أيضا، ليست هذه اللفظة التي أذكرها بمتقصاة لكلها','2845','1196','0');
INSERT INTO [t1446] VALUES (1212,'باب ذكر الخبر المتقصي للفظتين المجملتين اللتين ذكرتهما في البابين قبل، والبيان أن النبي صلى الله عليه وسلم إنما أعلم أن دعاء المصلي القائم يستجاب في تلك الساعة من يوم الجمعة، دون دعاء غير المصلي، ودون دعاء المصلي غير القائم، وذكر قصر تلك الساعة التي','2847','1196','0');
INSERT INTO [t1446] VALUES (1213,'باب ذكر البيان أن الساعة التي ذكرناها هي في كل جمعة من الجمعات لا في بعضها دون بعض','2849','1196','0');
INSERT INTO [t1446] VALUES (1214,'باب ذكر الدليل أن الدعاء بالخير مستجاب في تلك الساعة من يوم الجمعة دون الدعاء بالمأثم قال أبو بكر: " في خبر ابن سيرين، عن أبي هريرة يسأل الله فيها خيرا إلا أعطاه إياه "','2851','1196','0');
INSERT INTO [t1446] VALUES (1215,'باب ذكر وقت تلك الساعة التي يستجاب فيها الدعاء من يوم الجمعة','2852','1196','0');
INSERT INTO [t1446] VALUES (1216,'باب ذكر الدليل أن الدعاء في تلك الساعة يستجاب في الصلاة لانتظار الصلاة كما تأوله عبد الله بن سلام أن منتظر الصلاة في صلاة، مع الدليل على أن الدعاء بالخير في صلاة الفريضة جائز، إذ النبي صلى الله عليه وسلم قد أعلم في خبر أبي موسى، أن تلك الساعة هي ما','2854','1196','0');
INSERT INTO [t1446] VALUES (1217,'باب ذكر إنساء النبي صلى الله عليه وسلم وقت تلك الساعة بعد علمه إياها، والدليل على أن العالم قد يخبر بالشيء، ثم ينساه ويحفظه عنه بعض من سمعه منه؛ لأن أبا موسى الأشعري، وعمرو بن عوف المزني، قد أخبرا عن النبي صلى الله عليه وسلم تلك الساعة، والنبي صلى','2856','1196','0');
INSERT INTO [t1446] VALUES (1218,'جماع أبواب الغسل للجمعة','2858','1196','0');
INSERT INTO [t1446] VALUES (1219,'باب إيجاب الغسل للجمعة مثل اللفظة التي ذكرت قبل، أن الأمر إذا كان لعلة فمتى كانت العلة قائمة كان الأمر واجبا إذ النبي صلى الله عليه وسلم، إنما قال: " غسل يوم الجمعة واجب على كل محتلم "، لعلة: أي أن الاحتلام بلوغ، فمتى كان البلوغ - وإن كان بغير','2859','1196','0');
INSERT INTO [t1446] VALUES (1220,'باب ذكر الدليل على أن النبي صلى الله عليه وسلم إنما أراد بقوله: واجب أي واجب على البطلان لا وجوب فرض لا يجزئ غيره، على أن في الخبر أيضا اختصار كلام سأبينه بعد إن شاء الله تعالى','2862','1196','0');
INSERT INTO [t1446] VALUES (1221,'باب ذكر الخبر المفسر للفظة المجملة التي ذكرتها، والدليل أن النبي صلى الله عليه وسلم إنما أمر بغسل يوم الجمعة من أتاها دون من لم يأت الجمعة','2867','1196','0');
INSERT INTO [t1446] VALUES (1222,'باب أمر الخاطب بالغسل يوم الجمعة في خطبة الجمعة، والدليل على أن الخطبة ليست بصلاة كما توهم بعض الناس، إذ الخطبة لو كانت صلاة ما جاز أن يتكلم فيها ما لا يجوز من الكلام في الصلاة','2869','1196','0');
INSERT INTO [t1446] VALUES (1223,'باب أمر النساء بالغسل لشهود الجمعة، وهذه اللفظة أيضا من الجنس الذي ذكرت أنه مفسر للفظة المجملة التي في خبر أبي سعيد، وبيان أن النبي صلى الله عليه وسلم أمر بالغسل من أتى الجمعة دون من حبس عنها','2873','1196','0');
INSERT INTO [t1446] VALUES (1224,'باب ذكر علة ابتداء الأمر بالغسل للجمعة','2875','1196','0');
INSERT INTO [t1446] VALUES (1225,'باب ذكر دليل أن الغسل يوم الجمعة فضيلة لا فريضة','2879','1196','0');
INSERT INTO [t1446] VALUES (1226,'باب ذكر فضيلة الغسل يوم الجمعة إذا ابتكر المغتسل إلى الجمعة فدنا، وأنصت، ولم يلغ','2882','1196','0');
INSERT INTO [t1446] VALUES (1227,'باب ذكر بعض فضائل الغسل يوم الجمعة، وأن المغتسل لا يزال طاهرا إلى الجمعة الأخرى، وإن كان يحيى بن أبي كثير سمع هذا الخبر من عبد الله بن أبي قتادة','2885','1196','0');
INSERT INTO [t1446] VALUES (1228,'جماع أبواب الطيب، والتسوك، واللبس للجمعة','2887','1196','0');
INSERT INTO [t1446] VALUES (1229,'باب الأمر بالتطيب يوم الجمعة، إذ من الحقوق على المسلم التطيب إذا كان واجدا له','2888','1196','0');
INSERT INTO [t1446] VALUES (1230,'باب فضيلة التطيب، والتسوك، ولبس أحسن ما يجد المرء من الثياب بعد الاغتسال يوم الجمعة، وترك تخطي رقاب الناس، والتطوع بالصلاة بما قضى الله للمرء أن يتطوع بها قبل الجمعة، والإنصات عند خروج الإمام حتى تقضى الصلاة','2890','1196','0');
INSERT INTO [t1446] VALUES (1231,'باب فضيلة الادهان يوم الجمعة، والتجميع بين الادهان وبين التطيب يوم الجمعة','2892','1196','0');
INSERT INTO [t1446] VALUES (1232,'باب استحباب اتخاذ المرء في الجمعة ثيابا سوى ثوبي المهنة','2894','1196','0');
INSERT INTO [t1446] VALUES (1233,'باب استحباب لبس الجبة في الجمعة إن كان الحجاج بن أرطأة سمع هذا الخبر من أبي جعفر محمد بن علي','2896','1196','0');
INSERT INTO [t1446] VALUES (1234,'جماع أبواب التهجير إلى الجمعة، والمشي إليها','2898','1196','0');
INSERT INTO [t1446] VALUES (1235,'باب فضل التبكير إلى الجمعة مغتسلا، والدنو من الإمام، والاستماع، والإنصات','2899','1196','0');
INSERT INTO [t1446] VALUES (1236,'باب تمثيل المهجرين إلى الجمعة في الفضل بالمهدين، والدليل على أن من سبق بالتهجير كان أفضل من إبطائه','2901','1196','0');
INSERT INTO [t1446] VALUES (1237,'باب ذكر جلوس الملائكة على أبواب المسجد يوم الجمعة لكتابة المهجرين إليها على منازلهم، ووقت طيهم للصحف لاستماع الخطبة','2903','1196','0');
INSERT INTO [t1446] VALUES (1238,'باب ذكر عدد من يقعد على كل باب من أبواب المسجد يوم الجمعة من الملائكة لكتابة المهجرين إليها، والدليل على أن الاثنين قد يقع عليهما اسم جماعة، إذ النبي صلى الله عليه وسلم قد أوقع على الملكين اسم الملائكة','2905','1196','0');
INSERT INTO [t1446] VALUES (1239,'باب ذكر دعاء الملائكة للمتخلفين عن الجمعة بعد طيهم الصحف','2907','1196','0');
INSERT INTO [t1446] VALUES (1240,'باب فضل المشي إلى الجمعة، وترك الركوب واستحباب مقاربة الخطا لتكثر الخطا فيكثر الأجر قال أبو بكر: في خبر أوس بن أوس، عن النبي صلى الله عليه وسلم: " كان له بكل خطوة أجر سنة صيامها وقيامها ". قد أمليته قبل','2909','1196','0');
INSERT INTO [t1446] VALUES (1241,'باب الأمر بالسكينة في المشي إلى الجمعة، والنهي عن السعي إليها، والدليل على أن الاسم الواحد يقع على فعلين يؤمر بأحدهما، ويزجر عن الآخر بالاسم الواحد، فمن لا يفهم العلم، ولا يميز بين المعنيين، قد يخطر بباله أنهما مختلفان، قد أمر الله عز وجل في نص','2910','1196','0');
INSERT INTO [t1446] VALUES (1242,'جماع أبواب الأذان والخطبة في الجمعة، وما يجب على المأمومين في ذلك الوقت من الاستماع للخطبة، والإنصات لها، وما أبيح لهم من الأفعال، وما نهوا عنه','2912','1196','0');
INSERT INTO [t1446] VALUES (1243,'باب ذكر الأذان الذي كان على عهد رسول الله صلى الله عليه وسلم الذي أمر الله جل وعلا بالسعي إلى الجمعة إذا نودي به، والوقت الذي كان ينادى به، وذكر من أحدث النداء الأول قبل خروج الإمام','2913','1196','0');
INSERT INTO [t1446] VALUES (1244,'باب فضل إنصات المأموم عند خروج الإمام قبل الابتداء في الخطبة، ضد قول من زعم أن كلام الإمام يقطع الكلام قال أبو بكر: في خبر أبي سعيد، وأبي هريرة، عن النبي صلى الله عليه وسلم: " وأنصت إذا خرج إمامه "، وكذلك في خبر سليمان أيضا، وأبي أيوب الأنصاري،','2916','1196','0');
INSERT INTO [t1446] VALUES (1245,'باب ذكر أن موضع قيام النبي صلى الله عليه وسلم في الخطبة، كان قبل اتخاذه المنبر، والدليل على أن الخطبة على الأرض جائزة من غير صعود المنبر يوم الجمعة، والعلة التي لها أمر النبي صلى الله عليه وسلم باتخاذ المنبر إذ هو أحرى أن يسمع الناس خطبة الإمام إذا','2918','1196','0');
INSERT INTO [t1446] VALUES (1246,'باب ذكر العلة التي لها حن الجذع عند قيام النبي صلى الله عليه وسلم على المنبر، وصفة منبر النبي صلى الله عليه وسلم، وعدد درجه، والاستناد إلى شيء إذا خطب على الأرض','2920','1196','0');
INSERT INTO [t1446] VALUES (1247,'باب استحباب الاعتماد في الخطبة على القسي، أو العصا، استنانا بالنبي صلى الله عليه وسلم','2922','1196','0');
INSERT INTO [t1446] VALUES (1248,'باب ذكر العود الذي منه اتخذ منبر رسول الله صلى الله عليه وسلم','2924','1196','0');
INSERT INTO [t1446] VALUES (1249,'باب أمر الإمام الناس بالجلوس عند الاستواء على المنبر يوم الجمعة إن كان الوليد بن مسلم، ومن دونه حفظ ابن عباس في هذا الإسناد؛ فإن أصحاب ابن جريج أرسلوا هذا الخبر عن عطاء، عن النبي صلى الله عليه وسلم','2926','1196','0');
INSERT INTO [t1446] VALUES (1250,'باب ذكر عدد الخطبة يوم الجمعة، والجلسة بين الخطبتين، ضد قول من جهل السنة، فزعم أن السنة بدعة، وقال: الجلوس بين الخطبتين بدعة','2928','1196','0');
INSERT INTO [t1446] VALUES (1251,'باب استحباب تقصير الخطبة وترك تطويلها','2930','1196','0');
INSERT INTO [t1446] VALUES (1252,'باب صفة خطبة النبي صلى الله عليه وسلم، وبدئه فيها بحمد الله، والثناء عليه','2932','1196','0');
INSERT INTO [t1446] VALUES (1253,'باب قراءة القرآن في الخطبة يوم الجمعة','2934','1196','0');
INSERT INTO [t1446] VALUES (1254,'باب الرخصة في الاستسقاء في خطبة الجمعة إذا قحط الناس، وخيف من القحط هلاك الأموال، وانقطاع السبل إن لم يغث الله بمنه وطوله','2937','1196','0');
INSERT INTO [t1446] VALUES (1255,'باب الدعاء بحبس المطر عن البيوت والمنازل إذا خيف الضرر من كثرة الأمطار وهدم المنازل، ومسألة الله عز وجل تحويل الأمطار إلى الجبال والأودية حيث لا يخاف الضرر في خطبة الجمعة','2939','1196','0');
INSERT INTO [t1446] VALUES (1256,'باب الرخصة في تبسم الإمام في الخطبة قال أبو بكر: في خبر حميد عن أنس: " فتبسم رسول الله صلى الله عليه وسلم "','2941','1196','0');
INSERT INTO [t1446] VALUES (1257,'باب صفة رفع اليدين في الاستسقاء في خطبة الجمعة','2942','1196','0');
INSERT INTO [t1446] VALUES (1258,'باب الإشارة بالسبابة على المنبر في خطبة الجمعة، وكراهة رفع اليدين على المنبر في غير الاستسقاء','2944','1196','0');
INSERT INTO [t1446] VALUES (1259,'باب تحريك السبابة عند الإشارة بها في الخطبة قال أبو بكر: " قد أمليت خبر سهل بن سعد في كتاب العيدين "','2946','1196','0');
INSERT INTO [t1446] VALUES (1260,'باب النزول عن المنبر للسجود عند قراءة السجدة في الخطبة إن صح الخبر','2947','1196','0');
INSERT INTO [t1446] VALUES (1261,'باب الرخصة في العلم إذا سئل الإمام وقت خطبته على المنبر يوم الجمعة، ضد مذهب من توهم أن الخطبة صلاة، ولا يجوز الكلام فيها بما لا يجوز في الصلاة','2949','1196','0');
INSERT INTO [t1446] VALUES (1262,'باب الرخصة في تعليم الإمام الناس ما يجهلون في الخطبة من غير سؤال يسأل الإمام','2951','1196','0');
INSERT INTO [t1446] VALUES (1263,'باب الرخصة في سلام الإمام في الخطبة على القادم من السفر إذا دخل المسجد','2953','1196','0');
INSERT INTO [t1446] VALUES (1264,'باب أمر الإمام الناس في خطبة يوم الجمعة بالصدقة، إذا رأى حاجة، وفقرا','2955','1196','0');
INSERT INTO [t1446] VALUES (1265,'باب الرخصة في قطع الإمام الخطبة لتعليم السائل العلم','2957','1196','0');
INSERT INTO [t1446] VALUES (1266,'باب نزول الإمام عن المنبر، وقطعه الخطبة للحاجة تبدو له','2959','1196','0');
INSERT INTO [t1446] VALUES (1267,'باب فضل الإنصات، والاستماع للخطبة','2961','1196','0');
INSERT INTO [t1446] VALUES (1268,'باب الزجر عن الكلام يوم الجمعة عند خطبة الإمام','2963','1196','0');
INSERT INTO [t1446] VALUES (1269,'باب الزجر عن إنصات الناس بالكلام يوم الجمعة، والإمام يخطب','2965','1196','0');
INSERT INTO [t1446] VALUES (1270,'باب الزجر عن إنصات الناس بالكلام، وإن لم يسمع خطبة الإمام','2967','1196','0');
INSERT INTO [t1446] VALUES (1271,'باب النهي عن السؤال عن العلم غير الإمام، والإمام يخطب','2969','1196','0');
INSERT INTO [t1446] VALUES (1272,'باب ذكر إبطال فضيلة الجمعة بالكلام، والإمام يخطب، بلفظ مجمل غير مفسر، وزجر المتكلم عن الكلام بالتسبيح','2971','1196','0');
INSERT INTO [t1446] VALUES (1273,'باب ذكر الخبر المفسر للفظة المجملة التي ذكرتها، والدليل على أن اللغو والإمام يخطب إنما يبطل فضيلة الجمعة لا أنه يبطل الصلاة نفسها إبطالا يجب إعادتها، وهذا من الجنس الذي أعلمت في كتاب الإيمان، أن العرب تنفي الاسم عن الشيء لنقصه عن الكمال والتمام،','2973','1196','0');
INSERT INTO [t1446] VALUES (1274,'باب الأمر بإنصات المتكلم، والإمام يخطب بالإشارة إليه بالزجر قال أبو بكر: " في خبر شريك بن عبد الله بن أبي نمر، عن أنس، في قصة السائل عن الساعة، فأشار إليه الناس أن اسكت "','2975','1196','0');
INSERT INTO [t1446] VALUES (1275,'باب النهي عن تخطي الناس يوم الجمعة والإمام يخطب، وإباحة زجر الإمام عن ذلك في خطبته','2976','1196','0');
INSERT INTO [t1446] VALUES (1276,'باب النهي عن التفريق بين الناس في الجمعة، وفضيلة اجتناب ذلك','2978','1196','0');
INSERT INTO [t1446] VALUES (1277,'باب طبقات من يحضر الجمعة','2980','1196','0');
INSERT INTO [t1446] VALUES (1278,'باب ذكر الخبر المفسر للأخبار المجملة التي ذكرتها في الأبواب المتقدمة، والدليل على أن جميع ما تقدم من الأخبار في ذكر الجمعة أنها كفارة للذنوب والخطايا، إنما هي ألفاظ عام، مرادها خاص، أراد النبي المصطفى صلى الله عليه وسلم أنها كفارة لصغائر الذنوب دون','2982','1196','0');
INSERT INTO [t1446] VALUES (1279,'باب النهي عن الحبوة يوم الجمعة والإمام يخطب','2984','1196','0');
INSERT INTO [t1446] VALUES (1280,'باب الزجر عن الحلق يوم الجمعة قبل الصلاة','2986','1196','0');
INSERT INTO [t1446] VALUES (1281,'باب فضل ترك الجهل يوم الجمعة من حين يأتي المرء الجمعة إلى انقضاء الصلاة','2988','1196','0');
INSERT INTO [t1446] VALUES (1282,'باب الزجر عن مس الحصى والإمام يخطب يوم الجمعة، والإعلام بأن مس الحصى في ذلك الوقت لغو','2990','1196','0');
INSERT INTO [t1446] VALUES (1283,'باب استحباب تحول الناعس يوم الجمعة عن موضعه إلى غيره، والدليل على أن النعاس ليس باستحقاق نوم، ولا موجب وضوءا','2992','1196','0');
INSERT INTO [t1446] VALUES (1284,'باب الزجر عن إقامة الرجل أخاه يوم الجمعة من مجلسه ليخلفه فيه','2994','1196','0');
INSERT INTO [t1446] VALUES (1285,'باب ذكر قيام الرجل من مجلسه يوم الجمعة ثم يرجع، وقد خلفه فيه غيره، والبيان أنه أحق بمجلسه ممن خلفه فيه','2996','1196','0');
INSERT INTO [t1446] VALUES (1286,'باب الأمر بالتوسع والتفسح إذا ضاق الموضع قال الله عز وجل: يا أيها الذين آمنوا إذا قيل لكم تفسحوا في المجالس فافسحوا يفسح الله لكم','2998','1196','0');
INSERT INTO [t1446] VALUES (1287,'باب ذكر كراهة انفضاض الناس عن الإمام وقت خطبته للنظر إلى لهو أو تجارة قال الله عز وجل لنبيه المصطفى صلى الله عليه وسلم: وإذا رأوا تجارة أو لهوا انفضوا إليها وتركوك قائما الآية','3000','1196','0');
INSERT INTO [t1446] VALUES (1288,'أبواب الصلاة قبل الجمعة','3002','1196','0');
INSERT INTO [t1446] VALUES (1289,'باب الأمر بإعطاء المساجد حقها من الصلاة عند دخولها','3003','1196','0');
INSERT INTO [t1446] VALUES (1290,'باب الأمر بالتطوع بركعتين عند دخول المسجد قبل الجلوس','3005','1196','0');
INSERT INTO [t1446] VALUES (1291,'باب الزجر عن الجلوس عند دخول المسجد قبل أن يصلي ركعتين','3007','1196','0');
INSERT INTO [t1446] VALUES (1292,'باب الأمر بالرجوع إلى المسجد ليصلي الركعتين إذا دخله فخرج منه قبل أن يصليهما','3009','1196','0');
INSERT INTO [t1446] VALUES (1293,'باب الدليل على أن الأمر بركعتين عند دخول المسجد أمر ندب، وإرشاد، وفضيلة، والدليل على أن الزجر عن الجلوس قبل صلاة ركعتين عند دخول المسجد نهي تأديب لا نهي تحريم، بل حض على الخير والفضيلة قال أبو بكر: " خبر طلحة بن عبيد الله: جاء أعرابي إلى النبي صلى','3011','1196','0');
INSERT INTO [t1446] VALUES (1294,'باب الدليل على أن الجالس عند دخول المسجد قبل أن يصلي الركعتين لا يجب إعادتهما، إذ الركعتان عند دخول المسجد فضيلة لا فريضة','3012','1196','0');
INSERT INTO [t1446] VALUES (1295,'باب الأمر بتطوع ركعتين عند دخول المسجد، وإن كان الإمام يخطب خطبة الجمعة، ضد قول من زعم أنه غير جائز أن يصلي داخل المسجد والإمام يخطب','3014','1196','0');
INSERT INTO [t1446] VALUES (1296,'باب سؤال الإمام في خطبة الجمعة داخل المسجد وقت الخطبة أصلي ركعتين أم لا؟ وأمر الإمام الداخل بأن يصلي ركعتين إن لم يكن صلاهما قبل سؤال الإمام إياه، والدليل على الخطبة ليست بصلاة','3017','1196','0');
INSERT INTO [t1446] VALUES (1297,'باب أمر الإمام في خطبة الجمعة داخل المسجد بركعتين يصليهما، والدليل على أن النبي صلى الله عليه وسلم لم يقطع خطبتة ليصلي الداخل الذي أمره أن يصلي ركعتين إلى أن يفرغ المصلي من الركعتين كما زعم بعض من لم ينعم النظر في الأخبار قال أبو بكر: في خبر ابن عجلان','3021','1196','0');
INSERT INTO [t1446] VALUES (1298,'باب أمر الإمام في خطبته الجالس قبل أن يصليهما بالقيام ليصليهما أمر اختيار واستحباب، والتجوز فيهما، والدليل على ضد قول من زعم أن هذا كان خاصا لسليك الغطفاني','3022','1196','0');
INSERT INTO [t1446] VALUES (1299,'باب إباحة ما أراد المصلي من الصلاة قبل الجمعة من غير حظر أن يصلي ما شاء، وأراد من عدد الركعات والدليل على أن كل ما صلى قبل الجمعة فتطوع لا فرض منها قال أبو بكر في خبر أبي سعيد، وأبي هريرة، عن النبي صلى الله عليه وسلم: " وصلى ما كتب له "، وفي خبر','3024','1196','0');
INSERT INTO [t1446] VALUES (1300,'باب استحباب تطويل الصلاة قبل صلاة الجمعة','3025','1196','0');
INSERT INTO [t1446] VALUES (1301,'باب وقت الإقامة لصلاة الجمعة','3027','1196','0');
INSERT INTO [t1446] VALUES (1302,'باب الرخصة في الكلام للمأموم والإمام بعد الخطبة، وقبل افتتاح الصلاة','3029','1196','0');
INSERT INTO [t1446] VALUES (1303,'باب وقت صلاة الجمعة','3031','1196','0');
INSERT INTO [t1446] VALUES (1304,'باب استحباب التبكير بالجمعة','3033','1196','0');
INSERT INTO [t1446] VALUES (1305,'باب التبريد بصلاة الجمعة في شدة الحر والتبكير بها " والدليل على أن اسم التبكير يقع على التعجيل بالظهر والجمعة بعد زوال الشمس؛ لأن التبكير لا يقع إلا على أول النهار قبل زوال الشمس "','3036','1196','0');
INSERT INTO [t1446] VALUES (1306,'باب ذكر عدد صلاة الجمعة قال أبو بكر: " خبر عمر بن الخطاب: " صلاة الجمعة ركعتان " قد أمليته قبل في كتاب العيدين "','3038','1196','0');
INSERT INTO [t1446] VALUES (1307,'باب القراءة في صلاة الجمعة','3039','1196','0');
INSERT INTO [t1446] VALUES (1308,'باب إباحة قراءة غير سورة المنافقين في الركعة الثانية من صلاة الجمعة، وإن قرأ في الأولى بسورة الجمعة','3041','1196','0');
INSERT INTO [t1446] VALUES (1309,'باب إباحة القراءة في صلاة الجمعة بسبح اسم ربك الأعلى، وهل أتاك حديث الغاشية " وهذا الاختلاف في القراءة من اختلاف المباح "','3044','1196','0');
INSERT INTO [t1446] VALUES (1310,'باب المدرك ركعة من صلاة الجمعة مع الإمام " والدليل على أن المدرك منها ركعة يكون مدركا للجمعة، يجب عليه أن يضيف إليها أخرى، لا كما قال بعض من زعم: أن من فاتته الخطبة فعليه أن يصلي ظهرا أربعا، مع الدليل أن من لم يدرك منها ركعة فعليه أن يصلي ظهرا أربعا','3046','1196','0');
INSERT INTO [t1446] VALUES (1311,'باب الدليل على تجويز صلاة الجمعة بأقل من أربعين رجلا " ضد قول من زعم أن الجمعة لا تجزئ بأقل من أربعين رجلا خبرا بالغا "','3051','1196','0');
INSERT INTO [t1446] VALUES (1312,'باب التغليظ في التخلف عن شهود الجمعة','3053','1196','0');
INSERT INTO [t1446] VALUES (1313,'باب ذكر الختم على قلوب التاركين للجمعات، وكونهم من الغافلين بالتخلف عن الجمعة','3055','1196','0');
INSERT INTO [t1446] VALUES (1314,'باب ذكر الدليل على أن الوعيد لتارك الجمعة هو لتاركها من غير عذر','3057','1196','0');
INSERT INTO [t1446] VALUES (1315,'باب ذكر الدليل على أن الطبع على القلب بترك الجمعات الثلاث إنما يكون إذا تركها تهاونا بها','3060','1196','0');
INSERT INTO [t1446] VALUES (1316,'باب التغليظ في الغيبة عن المدن لمنافع الدنيا إذا آلت الغيبة إلى ترك شهود الجمعات','3062','1196','0');
INSERT INTO [t1446] VALUES (1317,'باب ذكر شهود من كان خارج المدن الجمعة مع الإمام إذا جمع في المدن " إن صح الخبر؛ فإن في القلب من سوء حفظ عبد الله بن عمر العمري رحمه الله "','3064','1196','0');
INSERT INTO [t1446] VALUES (1318,'باب الأمر بصدقة دينار إن وجده، أو بنصف دينار إن أعوزه دينار لترك جمعة من غير عذر " إن صح الخبر، فإني لا أقف على سماع قتادة، عن قدامة بن وبرة، ولست أعرف قدامة بعدالة ولا جرح "','3066','1196','0');
INSERT INTO [t1446] VALUES (1319,'باب الرخصة في التخلف عن الجمعة في الأمطار إذا كان المطر وابلا كبيرا','3068','1196','0');
INSERT INTO [t1446] VALUES (1320,'باب الرخصة في التخلف عن الجمعة في المطر وإن لم يكن المطر مؤذيا " وهذا من الجنس الذي أعلمت في غير موضع من كتابنا في كتاب معاني القرآن، وفي الكتب المصنفة من المسند، أن الله جل وعلا، ورسوله المصطفى قد يبيحان الشيء لعلة من غير حظر ذلك الشيء، وإن كانت تلك','3070','1196','0');
INSERT INTO [t1446] VALUES (1321,'باب أمر الإمام المؤذن في أذان الجمعة بالنداء " أن الصلاة في البيوت ليعلم السامع أن التخلف عن الجمعة في المطر طلق مباح "','3072','1196','0');
INSERT INTO [t1446] VALUES (1322,'باب أمر الإمام المؤذن بحذف حي على الصلاة، والأمر بالصلاة في البيوت بدله','3074','1196','0');
INSERT INTO [t1446] VALUES (1323,'باب الدليل على أن الأمر بالنداء يوم الجمعة بالصلاة في الرحال " الذي خبر ابن عباس أنه فعله من هو خير مني، النبي صلى الله عليه وسلم، إن كان عباد بن منصور حفظ هذا الخبر الذي أذكره "','3076','1196','0');
INSERT INTO [t1446] VALUES (1324,'باب الأمر بالفصل بين صلاة الجمعة، وبين صلاة التطوع بعدها بكلام، أو خروج','3078','1196','0');
INSERT INTO [t1446] VALUES (1325,'باب الاكتفاء من الخروج للفصل بين الجمعة، والتطوع بعدها بالتقدم أمام المصلى الذي صلى فيه الجمعة','3080','1196','0');
INSERT INTO [t1446] VALUES (1326,'باب استحباب تطوع الإمام بعد الجمعة في منزله','3082','1196','0');
INSERT INTO [t1446] VALUES (1327,'باب إباحة صلاة التطوع بعد الجمعة للإمام في المسجد قبل خروجه منه " إن صح الخبر، فإني لا أقف على سماع موسى بن الحارث في جابر بن عبد الله "','3086','1196','0');
INSERT INTO [t1446] VALUES (1328,'باب أمر المأموم بأن يتطوع بعد الجمعة بأربع ركعات بلفظ مختصر غير متقصى','3088','1196','0');
INSERT INTO [t1446] VALUES (1329,'باب ذكر الخبر المتقصى للفظة المختصرة التي ذكرتها " والدليل على أن النبي صلى الله عليه وسلم إنما أمر المرء بأن يتطوع بأربع ركعات إذا أراد أن يصلي بعدها، مع الدليل على أن ما صلى بعدها فتطوع غير فريضة "','3090','1196','0');
INSERT INTO [t1446] VALUES (1330,'باب الرجوع إلى المنازل بعد قضاء الجمعة للغداء، والقيلولة','3092','1196','0');
INSERT INTO [t1446] VALUES (1331,'باب استحباب الانتشار بعد صلاة الجمعة " والابتغاء من فضل الله قال الله عز وجل: فإذا قضيت الصلاة فانتشروا في الأرض وابتغوا من فضل الله إلا أن في القلب من هذا الخبر، فإني لا أعرف سعيد بن عنبسة القطان هذا، ولا عبد الله بن بشر الذي روى عنه سعيد هذا بعدالة','3096','1196','0');
INSERT INTO [t1446] VALUES (1332,'كتاب الصيام " المختصر من المختصر من المسند عن النبي صلى الله عليه وسلم على الشرط الذي ذكرنا بنقل العدل عن العدل موصولا إليه صلى الله عليه وسلم، من غير قطع في الإسناد، ولا جرح في ناقلي الأخبار إلا ما نذكر أن في القلب من بعض الأخبار شيء، إما لشك في سماع','3098','0','0');
INSERT INTO [t1446] VALUES (1333,'باب ذكر البيان أن صوم شهر رمضان من الإيمان قال أبو بكر: قد أمليت خبر حماد بن زيد وعباد بن عباد المهلبي وشعبة بن الحجاج جميعا عن أبي جمرة عن ابن عباس في كتاب الإيمان','3099','1332','0');
INSERT INTO [t1446] VALUES (1334,'باب ذكر البيان أن صوم شهر رمضان من الإسلام إذ الإيمان والإسلام اسمان لمسمى واحد قال أبو بكر: " خبر جبريل في مسألته النبي صلى الله عليه وسلم عن الإسلام قد أمليته في كتاب الإيمان "','3101','1332','0');
INSERT INTO [t1446] VALUES (1335,'جماع أبواب فضائل شهر رمضان وصيامه','3103','1332','0');
INSERT INTO [t1446] VALUES (1336,'باب ذكر فتح أبواب الجنان - نسأل الله دخولها - وإغلاق أبواب النار " باعدنا الله منها وتصفيد الشياطين بالله نتعوذ من شرهم في شهر رمضان بذكر لفظ عام مراده خاص في تصفيد الشياطين "','3104','1332','0');
INSERT INTO [t1446] VALUES (1337,'باب ذكر البيان أن النبي صلى الله عليه وسلم إنما أراد بقوله: " وصفدت الشياطين " مردة الجن منهم، لا جميع الشياطين " إذ اسم الشياطين قد يقع على بعضهم، وذكر دعاء الملك في رمضان إلى الخيرات، والتقصير عن السيئات، مع الدليل على أن أبواب الجنان إذا فتحت لم','3106','1332','0');
INSERT INTO [t1446] VALUES (1338,'باب في فضل شهر رمضان " وأنه خير الشهور للمسلمين، وذكر إعداد المؤمن القوة من النفقة للعبادة قبل دخوله "','3108','1332','0');
INSERT INTO [t1446] VALUES (1339,'باب ذكر تفضل الله عز وجل على عبادة المؤمنين في أول ليلة من شهر رمضان بمغفرته إياهم كرما وجودا " إن صح الخبر فإني لا أعرف خلفا أبا الربيع هذا بعدالة ولا جرح، ولا عمرو بن حمزة القيسي الذي هو دونه "','3110','1332','0');
INSERT INTO [t1446] VALUES (1340,'باب ذكر تزيين الجنة لشهر رمضان " وذكر بعض ما أعد الله للصائمين في الجنة غير ممكن لآدمي صفته، إذ فيها ما لا عين رأت، ولا أذن سمعت، ولا خطر على قلب بشر، إن صح الخبر؛ فإن في القلب من جرير بن أيوب البجلي "','3112','1332','0');
INSERT INTO [t1446] VALUES (1341,'باب فضائل شهر رمضان إن صح الخبر','3114','1332','0');
INSERT INTO [t1446] VALUES (1342,'باب استحباب الاجتهاد في العبادة في رمضان " لعل الرب عز وجل برأفته ورحمته، يغفر للمجتهد قبل أن ينقضي الشهر ولا يرغم بأنف العبد بمضي رمضان قبل الغفران "','3116','1332','0');
INSERT INTO [t1446] VALUES (1343,'باب استحباب الجود بالخير والعطايا في شهر رمضان إلى انسلاخه استنانا بالنبي صلى الله عليه وسلم','3118','1332','0');
INSERT INTO [t1446] VALUES (1344,'باب الاجتنان بالصوم من النار " إذ الله عز وجل جعل الصوم جنة من النار، نعوذ بالله من النار "','3120','1332','0');
INSERT INTO [t1446] VALUES (1345,'باب الدليل على أن الصوم إنما يكون جنة باجتناب ما نهي الصائم عنه وإن كان ما نهي عنه مما لا يفطره، ولكن ينقص صومه عن الكمال والتمام "','3124','1332','0');
INSERT INTO [t1446] VALUES (1346,'باب فضل الصيام وأنه لا عدل له من الأعمال','3126','1332','0');
INSERT INTO [t1446] VALUES (1347,'باب ذكر مغفرة الذنوب السالفة بصوم رمضان إيمانا واحتسابا','3128','1332','0');
INSERT INTO [t1446] VALUES (1348,'باب ذكر تمثيل الصائم في طيب ريحه بطيب ريح المسك إذ هو أطيب الطيب','3130','1332','0');
INSERT INTO [t1446] VALUES (1349,'باب ذكر طيب خلفة الصائم عند الله يوم القيامة','3132','1332','0');
INSERT INTO [t1446] VALUES (1350,'باب ذكر إعطاء الرب عز وجل الصائم أجره بغير حساب إذ الصيام من الصبر " قال الله عز وجل: إنما يوفى الصابرون أجرهم بغير حساب "','3134','1332','0');
INSERT INTO [t1446] VALUES (1351,'باب ذكر البيان أن الصيام من الصبر على ما تأولت خبر النبي صلى الله عليه وسلم','3136','1332','0');
INSERT INTO [t1446] VALUES (1352,'باب ذكر فرح الصائم يوم القيامة بإعطاء الرب إياه ثواب صومه بلا حساب " جعلنا الله منهم "','3139','1332','0');
INSERT INTO [t1446] VALUES (1353,'باب ذكر استجابة الله عز وجل دعاء الصوام إلى فطرهم من صيامهم، جعلنا الله منهم','3141','1332','0');
INSERT INTO [t1446] VALUES (1354,'باب ذكر باب الجنة الذي يخص بدخوله الصوام دون غيرهم " ونفي الظمأ عمن يدخل الجنة، ويشرب من شرابها، جعلنا الله منهم "','3143','1332','0');
INSERT INTO [t1446] VALUES (1355,'باب صفة بدء الصوم " كان في تخيير الله عز وجل عبادة المؤمنين بين الصوم والإطعام، ونسخ ذلك بإيجاب الصوم عليهم من غير تخيير "','3145','1332','0');
INSERT INTO [t1446] VALUES (1356,'باب ذكر ما كان الصائم عنه ممنوعا بعد النوم " في ليل الصوم من الأكل، والشرب، والجماع عند ابتداء فرض الصيام، ونسخ الله جل وعلا ذلك بإباحته لهم ذلك أجمع إلى طلوع الفجر، تفضلا منه عز وجل على عباده المؤمنين، وعفوا منه عنهم، وتخفيفا عليهم "','3147','1332','0');
INSERT INTO [t1446] VALUES (1357,'جماع أبواب الأهلة ووقت ابتداء صوم شهر رمضان','3149','1332','0');
INSERT INTO [t1446] VALUES (1358,'باب الأمر بالصيام لرؤية الهلال إذا لم يغم على الناس','3150','1332','0');
INSERT INTO [t1446] VALUES (1359,'باب ذكر البيان أن الله جل وعلا جعل الأهلة مواقيت للناس لصومهم وفطرهم " إذ قد أمر الله على لسان نبيه عليه السلام بصوم شهر رمضان لرؤيته، والفطر لرؤيته ما لم يغم قال الله عز وجل: يسألونك عن الأهلة قل هي مواقيت للناس، الآية "','3152','1332','0');
INSERT INTO [t1446] VALUES (1360,'باب الأمر بالتقدير للشهر إذا غم على الناس','3154','1332','0');
INSERT INTO [t1446] VALUES (1361,'باب ذكر الدليل على أن الأمر بالتقدير للشهر إذا غم، أن يعد شعبان ثلاثين يوما، ثم يصام','3156','1332','0');
INSERT INTO [t1446] VALUES (1362,'باب ذكر الدليل على ضد قول من زعم أن النبي صلى الله عليه وسلم إنما أمر بإكمال ثلاثين يوما لصوم شهر رمضان دون إكمال ثلاثين يوما لشعبان','3159','1332','0');
INSERT INTO [t1446] VALUES (1363,'باب الزجر عن الصيام لرمضان قبل مضي ثلاثين يوما لشعبان إذا لم ير الهلال','3161','1332','0');
INSERT INTO [t1446] VALUES (1364,'باب التسوية بين الزجر عن صيام رمضان قبل رؤية هلال رمضان إذا لم يغم الهلال، وبين الزجر عن إفطار رمضان قبل رؤية هلال شوال إذا لم يغم الهلال " والدليل على أن الصائم لرمضان إذا غم الهلال قبل مضي ثلاثين يوما لشعبان عاص كالمفطر قبل مضي ثلاثين يوما لرمضان إذا','3164','1332','0');
INSERT INTO [t1446] VALUES (1365,'باب الزجر عن صوم اليوم الذي يشك فيه، أمن رمضان أم من شعبان " بلفظ مجمل غير مفسر "','3166','1332','0');
INSERT INTO [t1446] VALUES (1366,'باب ذكر الدليل على أن الهلال يكون لليلة التي يرى صغر أو كبر ما لم تمض ثلاثون يوما للشهر، ثم لا يرى الهلال لغيم أو سحاب','3168','1332','0');
INSERT INTO [t1446] VALUES (1367,'باب الدليل على أن الواجب على أهل كل بلدة صيام رمضان لرؤيتهم لا رؤية غيرهم','3170','1332','0');
INSERT INTO [t1446] VALUES (1368,'باب ذكر أخبار رويت عن النبي صلى الله عليه وسلم في أن الشهر تسع وعشرون بلفظ عام مراده خاص','3172','1332','0');
INSERT INTO [t1446] VALUES (1369,'باب ذكر الدليل على خلاف ما توهمه العامة، والجهال أن الهلال إذا كان كبيرا مضيئا أنه لليلة الماضية لا لليلة المستقبلة','3175','1332','0');
INSERT INTO [t1446] VALUES (1370,'باب ذكر إعلام النبي صلى الله عليه وسلم أمته أن الشهر تسع وعشرون " بإشارة لا بنطق، مع إعلامه إياهم أنه أمي لا يكتب، ولا يحسب، صلى الله عليه وسلم، مع الدليل على أن الإشارة المفهومة من الناطق تقوم مقام النطق في الحكم كهي من الأخرس "','3177','1332','0');
INSERT INTO [t1446] VALUES (1371,'باب ذكر الخبر المفسر للفظة المجملة التي ذكرتها والدليل على أن النبي صلى الله عليه وسلم أراد بقوله: " الشهر تسع وعشرون " بعض الشهور لا كلها، والدليل على أن قوله: " الشهر تسع وعشرون " أراد: أي قد يكون تسعا وعشرين','3179','1332','0');
INSERT INTO [t1446] VALUES (1372,'باب الدليل على أن صيام تسع وعشرين لرمضان كان على عهد النبي صلى الله عليه وسلم أكثر من صيام ثلاثين خلاف ما يتوهم بعض الجهال، والرعاع، أن الواجب أن يصام لكل رمضان ثلاثون يوما كوامل "','3181','1332','0');
INSERT INTO [t1446] VALUES (1373,'باب إجازة شهادة الشاهد الواحد على رؤية الهلال','3183','1332','0');
INSERT INTO [t1446] VALUES (1374,'باب ذكر البيان أن الله عز وجل أراد بقوله: حتى يتبين لكم الخيط الأبيض من الخيط الأسود من الفجر " بيان بياض النهار من الليل، فوقع اسم الخيط على بياض النهار، وعلى سواد الليل، وهذا من الجنس الذي كنت أعلم أن العرب لم تكن تعرفها في معناها، وأن الله عز وجل','3185','1332','0');
INSERT INTO [t1446] VALUES (1375,'باب الدليل على أن الفجر هما فجران " وأن طلوع الثاني منهما هو المحرم على الصائم الأكل والشرب والجماع، لا الأول، وهذا من الجنس الذي أعلمت أن الله عز وجل ولى نبيه عليه السلام البيان عنه عز وجل "','3188','1332','0');
INSERT INTO [t1446] VALUES (1376,'باب صفة الفجر الذي ذكرناه وهو المعترض لا المستطيل','3190','1332','0');
INSERT INTO [t1446] VALUES (1377,'باب الدليل على أن الفجر الثاني الذي ذكرناه هو البياض المعترض الذي لونه الحمرة إن صح الخبر " فإني لا أعرف عبد الله بن النعمان هذا بعدالة ولا جرح، ولا أعرف له عنه راويا غير ملازم بن عمرو','3193','1332','0');
INSERT INTO [t1446] VALUES (1378,'باب الدليل على أن الأذان قبل الفجر لا يمنع الصائم طعامه، ولا شرابه، ولا جماعا ضد ما يتوهم العامة','3195','1332','0');
INSERT INTO [t1446] VALUES (1379,'باب ذكر قدر ما كان بين أذان بلال، وأذان ابن أم مكتوم','3197','1332','0');
INSERT INTO [t1446] VALUES (1380,'باب إيجاب الإجماع على الصوم الواجب قبل طلوع الفجر بلفظ عام، مراده خاص','3199','1332','0');
INSERT INTO [t1446] VALUES (1381,'باب إيجاب النية لصوم كل يوم قبل طلوع فجر ذلك اليوم، خلاف قول من زعم أن نية واحدة في وقت واحد لجميع الشهر جائز','3201','1332','0');
INSERT INTO [t1446] VALUES (1382,'باب الدليل على أن النبي صلى الله عليه وسلم أراد بقوله: " لا صيام لمن لم يجمع الصيام من الليل، الواجب من الصيام دون التطوع منه "','3203','1332','0');
INSERT INTO [t1446] VALUES (1383,'باب الأمر بالسحور أمر ندب وإرشاد، إذ السحور بركة، لا أمر فرض وإيجاب، يكون تاركه عاصيا بتركه','3205','1332','0');
INSERT INTO [t1446] VALUES (1384,'باب ذكر الدليل أن السحور قد يقع عليه اسم الغداء','3208','1332','0');
INSERT INTO [t1446] VALUES (1385,'باب الأمر بالاستعانة على الصوم بالسحور إن جاز الاحتجاج بخبر زمعة بن صالح فإن في القلب منه؛ لسوء حفظه','3210','1332','0');
INSERT INTO [t1446] VALUES (1386,'باب استحباب السحور فصلا من صيام النهار، وصيام أهل الكتاب، والأمر بمخالفتهم إذ هم لا يتسحرون','3212','1332','0');
INSERT INTO [t1446] VALUES (1387,'باب تأخير السحور','3214','1332','0');
INSERT INTO [t1446] VALUES (1388,'جماع أبواب الأفعال اللواتي تفطر الصائم','3217','1332','0');
INSERT INTO [t1446] VALUES (1389,'باب ذكر المفطر بالجماع في نهار الصيام','3218','1332','0');
INSERT INTO [t1446] VALUES (1390,'باب إيجاب الكفارة على المجامع في الصوم في رمضان بالعتق إذا وجده، أو الصيام إذا لم يجد العتق، أو الإطعام إذا لم يستطع الصوم، والدليل على أن خبر ابن جريج ومالك مختصر غير متقصى مع الدليل على أن اللفظ الذي ذكرناه في خبرهما كان فطرا بجماع لا بأكل، ولا','3220','1332','0');
INSERT INTO [t1446] VALUES (1391,'باب إعطاء الإمام المجامع في رمضان نهارا ما يكفر به إذا لم يكن واجدا للكفارة " مع الدليل على أن المجامع في رمضان نهارا إذا كان غير واجد للكفارة وقت الجماع، ثم استفاد ما به يكفر، كانت الكفارة واجبة عليه "','3222','1332','0');
INSERT INTO [t1446] VALUES (1392,'باب ذكر خبر روي مختصرا وهم بعض العلماء من الحجازيين " أن المجامع في رمضان نهارا جائز له أن يكفر بالإطعام، وإن كان واجدا لعتق رقبة، مستطيعا لصوم شهرين متتابعين "','3224','1332','0');
INSERT INTO [t1446] VALUES (1393,'باب ذكر الدليل على أن النبي صلى الله عليه وسلم، إنما أمر هذا المجامع بالصدقة بعد أن أخبره أنه لا يجد عتق رقبة، ويشبه أن يكون قد أعلم أيضا أنه غير مستطيع لصوم شهرين متتابعين كإخبار أبي هريرة، فاختصر الخبر','3226','1332','0');
INSERT INTO [t1446] VALUES (1394,'باب الدليل على أن المجامع في رمضان إذا ملك ما يطعم ستين مسكينا، ولم يملك معه قوت نفسه وعياله، لم تجب عليه الكفارة قال أبو بكر: " في خبر عائشة قال: " إنا لجياع ما لنا شيء ". هذا في خبر عمرو بن الحارث , وفي خبر عبد الرحمن بن الحارث: " ما لنا عشاء','3228','1332','0');
INSERT INTO [t1446] VALUES (1395,'باب الأمر بالاستغفار للمعصية التي ارتكبها المجامع في صوم رمضان إذا لم يجد الكفارة بعتق، ولا بإطعام، ولا يستطيع صوم شهرين متتابعين، والأمر بإطعام التمر في كفارة الجماع في رمضان','3229','1332','0');
INSERT INTO [t1446] VALUES (1396,'باب ذكر قدر مكيل التمر لإطعام ستين مسكينا في كفارة الجماع في صوم رمضان','3231','1332','0');
INSERT INTO [t1446] VALUES (1397,'باب الدليل على خلاف قول من زعم أن إطعام مسكين واحد طعام ستين مسكينا في ستين يوما " كل يوم طعام مسكين جائز في كفارة الجماع في صوم رمضان، فلم يميز بين إطعام ستين مسكينا، وبين طعام ستين مسكينا، ومن فهم لغة العرب علم أن إطعام ستين مسكينا لا يكون إلا وكل','3233','1332','0');
INSERT INTO [t1446] VALUES (1398,'باب الدليل على أن صيام الشهرين في كفارة الجماع لا يجوز متفرقا إنما يجب صيام شهرين متتابعين قال أبو بكر: " في خبر الزهري، عن حميد، عن أبي هريرة: فصم شهرين متتابعين "','3234','1332','0');
INSERT INTO [t1446] VALUES (1399,'باب الدليل على أن المجامع إذا وجب عليه صيام شهرين متتابعين ففرط في الصيام، حتى تنزل به المنية، قضي الصوم عنه، كالدين يكون عليه مع الدليل على أن دين الله أحق بالقضاء من ديون العباد','3235','1332','0');
INSERT INTO [t1446] VALUES (1400,'باب أمر المجامع بقضاء صوم يوم مكان اليوم الذي جامع فيه إذا لم يكن واجدا للكفارة التي ذكرتها قبل إن صح الخبر؛ فإن في القلب من هذه اللفظة','3237','1332','0');
INSERT INTO [t1446] VALUES (1401,'باب ذكر البيان أن الاستقاء على العمد يفطر الصائم','3239','1332','0');
INSERT INTO [t1446] VALUES (1402,'باب ذكر إيجاب قضاء الصوم عن المستقيء عمدا، وإسقاط القضاء عمن يذرعه القيء " والدليل على أن إيجاب الكفارة على المجامع لا لعلة الفطر فقط، إذ لو كان لعلة الفطر فقط لا للجماع خاصة، كان على كل مفطر الكفارة، والمستقيء عمدا مفطر بحكم النبي صلى الله عليه وسلم','3241','1332','0');
INSERT INTO [t1446] VALUES (1403,'باب ذكر البيان أن الحجامة تفطر الحاجم والمحجوم جميعا','3243','1332','0');
INSERT INTO [t1446] VALUES (1404,'باب ذكر الدليل على أن السعوط، وما يصل إلى الأنوف من المنخرين يفطر الصائم خبر عاصم بن لقيط بن صبرة، عن أبيه، عن النبي صلى الله عليه وسلم: " وإذا استنشقت فبالغ، إلا أن تكون صائما "','3261','1332','0');
INSERT INTO [t1446] VALUES (1405,'باب ذكر تعليق المفطرين قبل وقت الإفطار بعراقيبهم، وتعذيبهم في الآخرة بفطرهم قبل تحلة صومهم','3262','1332','0');
INSERT INTO [t1446] VALUES (1406,'باب التغليط في إفطار يوم من رمضان متعمدا من غير رخصة " إن صح الخبر فإني لا أعرف ابن المطوس، ولا أباه غير أن حبيب ابن أبي ثابت قد ذكر أنه لقي أبا المطوس "','3264','1332','0');
INSERT INTO [t1446] VALUES (1407,'باب ذكر البيان أن الآكل والشارب ناسيا لصيامه غير مفطر بالأكل والشرب','3266','1332','0');
INSERT INTO [t1446] VALUES (1408,'باب ذكر إسقاط القضاء والكفارة عن الآكل والشارب في الصيام إذا كان ناسيا لصيامه وقت الأكل والشرب','3268','1332','0');
INSERT INTO [t1446] VALUES (1409,'باب ذكر الفطر قبل غروب الشمس إذا حسب الصائم أنها قد غربت','3270','1332','0');
INSERT INTO [t1446] VALUES (1410,'جماع أبواب الأقوال والأفعال المنهية عنها في الصوم من غير إيجاب فطر','3272','1332','0');
INSERT INTO [t1446] VALUES (1411,'باب النهي عن الجهل في الصيام','3273','1332','0');
INSERT INTO [t1446] VALUES (1412,'باب الزجر عن السباب والإقتتال في الصيام " وإن سب الصائم أو قوتل، وإعلام الصائم مقاتله وسابه أنه صائم لعله ينزجر عن قتاله وسبابه إذا علم أنه لا ينتصر منه لعلة صومه "','3275','1332','0');
INSERT INTO [t1446] VALUES (1413,'باب الأمر بالجلوس إذا شتم الصائم وهو قائم، لتسكين الغضب على المشتوم فلا ينتصر بالجواب','3277','1332','0');
INSERT INTO [t1446] VALUES (1414,'باب النهي عن قول الزور، والعمل به، والجهل في الصوم، والتغليظ فيه','3279','1332','0');
INSERT INTO [t1446] VALUES (1415,'باب النهي عن اللغو في الصيام " والدليل على أن الإمساك عن اللغو والرفث من تمام الصوم، مع الدليل على أن الاسم باسم المعرفة بالألف واللام قد يقع على بعض أجزاء العمل ذي الشعب والأجزاء، على ما بينته في كتاب الإيمان "','3281','1332','0');
INSERT INTO [t1446] VALUES (1416,'باب نفي ثواب الصوم عن الممسك عن الطعام والشراب مع ارتكابه ما زجر عنه غير الأكل والشرب','3283','1332','0');
INSERT INTO [t1446] VALUES (1417,'جماع أبواب الأفعال المباحة في الصيام مما قد اختلف العلماء في إباحتها','3285','1332','0');
INSERT INTO [t1446] VALUES (1418,'باب الرخصة في المباشرة التي هي دون الجماع للصائم والدليل على أن اسم الواحد قد يقع على فعلين، أحدهما مباح، والآخر محظور، إذ اسم المباشرة قد أوقعه الله في نص كتابه على الجماع، ودل الكتاب على أن الجماع في الصوم محظور، قال المصطفى صلى الله عليه وسلم: "','3286','1332','0');
INSERT INTO [t1446] VALUES (1419,'باب تمثيل النبي صلى الله عليه وسلم قبلة الصائم بالمضمضة منه بالماء','3288','1332','0');
INSERT INTO [t1446] VALUES (1420,'باب الرخصة في قبلة الصائم','3290','1332','0');
INSERT INTO [t1446] VALUES (1421,'باب الرخصة في قبلة الصائم رءوس النساء ووجوههن خلاف مذهب من كان يكره ذلك','3292','1332','0');
INSERT INTO [t1446] VALUES (1422,'باب الرخصة في مص الصائم لسان المرأة " خلاف مذهب من كره القبلة للصائم على الفم إن جاز الاحتجاج بمصدع أبي يحيى، فإني لا أعرفه بعدالة ولا جرح "','3295','1332','0');
INSERT INTO [t1446] VALUES (1423,'باب الرخصة في قبلة الصائم المرأة الصائمة','3297','1332','0');
INSERT INTO [t1446] VALUES (1424,'باب ذكر الدليل على أن القبلة للصائم مباحة لجميع الصوام، ولم تكن خاصة للنبي صلى الله عليه وسلم قال أبو بكر: خبر جابر عن عمر من هذا الباب','3299','1332','0');
INSERT INTO [t1446] VALUES (1425,'باب الرخصة في السواك للصائم قال أبو بكر: إخبار النبي صلى الله عليه وسلم: " لولا أن أشق على أمتي لأمرتهم بالسواك عند كل صلاة "، ولم يستثن مفطرا دون صائم، ففيها دلالة على أن السواك للصائم عند كل صلاة فضيلة كهو للمفطر "','3301','1332','0');
INSERT INTO [t1446] VALUES (1426,'باب الرخصة في اكتحال الصائم " إن صح الخبر، وإن لم يصح الخبر من جهة النقل، فالقرآن دال على إباحته وهو قول الله عز وجل: فالآن باشروهن الآية، دال على إباحة الكحل للصائم "','3303','1332','0');
INSERT INTO [t1446] VALUES (1427,'باب إباحة ترك الجنب الاغتسال من الجنابة إلى طلوع الفجر إذا كان مريدا للصوم','3305','1332','0');
INSERT INTO [t1446] VALUES (1428,'باب ذكر خبر روي في الزجر عن الصوم إذا أدرك الجنب الصبح قبل أن يغتسل " لم يفهم معناه بعض العلماء، فأنكر الخبر، وتوهم أن أبا هريرة مع جلالته ومكانه من العلم غلط في روايته، والخبر ثابت صحيح من جهة النقل إلا أنه منسوخ لا أن أبا هريرة غلط في رواية هذا','3307','1332','0');
INSERT INTO [t1446] VALUES (1429,'باب الدليل على أن جنابة النبي صلى الله عليه وسلم التي أخر الغسل بعدها إلى طلوع الفجر فصام، كان من جماع لا من احتلام','3310','1332','0');
INSERT INTO [t1446] VALUES (1430,'باب الدليل على أن الصوم جائز لكل من أصبح جنبا واغتسل بعد طلوع الفجر " والزجر عن أن يقال: كان هذا خاصا للنبي صلى الله عليه وسلم، مع الدليل على أن كل ما فعله النبي صلى الله عليه وسلم مما لم يجز أنه خاص له، فعلى الناس التأسي به واتباعه صلى الله عليه وسلم','3312','1332','0');
INSERT INTO [t1446] VALUES (1431,'جماع أبواب الصوم في السفر، من أبيح له الفطر في رمضان عند المسافر','3315','1332','0');
INSERT INTO [t1446] VALUES (1432,'باب ذكر خبر روي عن النبي صلى الله عليه وسلم في الصوم في السفر " بلفظة مختصرة من غير ذكر السبب الذي قال له تلك المقالة، توهم بعض العلماء من لم يفهم السبب أن الصوم في السفر غير جائز حتى أمر بعضهم الصائم في السفر بإعادة الصوم بعد في الحضر','3316','1332','0');
INSERT INTO [t1446] VALUES (1433,'باب ذكر السبب الذي قال النبي صلى الله عليه وسلم: " ليس من البر الصيام في السفر "','3318','1332','0');
INSERT INTO [t1446] VALUES (1434,'باب ذكر خبر روي عن النبي صلى الله عليه وسلم في تسمية الصوم في السفر عصاة " من غير ذكر العلة التي أسماهم بهذا الاسم توهم بعض العلماء أن الصوم في السفر غير جائز لهذا الخبر "','3320','1332','0');
INSERT INTO [t1446] VALUES (1435,'باب الدليل على أن النبي صلى الله عليه وسلم إنما سماهم عصاة إذ أمرهم بالإفطار وصاموا " ومن أمر بفعل وإن كان الفعل مباحا فرضا واجبا فترك ما أمر به من المباح جاز أن يسمى عاصيا "','3322','1332','0');
INSERT INTO [t1446] VALUES (1436,'باب الدليل على أن النبي صلى الله عليه وسلم إنما أمر أصحابه بالفطر عام فتح مكة إذ الفطر أقوى لهم على الحرب، لا أن الصوم في السفر غير جائز','3325','1332','0');
INSERT INTO [t1446] VALUES (1437,'باب التغليظ في ترك سنة النبي صلى الله عليه وسلم رغبة عنها " وجائز أن يسمى تارك السنة عاصيا إذا تركها رغبة عنها لا بتركها، إذ الترك غير معصية، وفعلها فضيلة "','3327','1332','0');
INSERT INTO [t1446] VALUES (1438,'باب ذكر إسقاط فرض الصوم عن المسافر " إذ هو مباح له الفطر في السفر على أن يصوم في الحضر من أيام أخر، لا أن الفرض ساقط عنه لا تجب عليه إعادته " " قال الله عز وجل: فمن كان منكم مريضا أو على سفر فعدة من أيام أخر " قال أبو بكر: " خبر أنس بن مالك القشيري','3329','1332','0');
INSERT INTO [t1446] VALUES (1439,'باب ذكر البيان أن الفطر في السفر رخصة لا أن حتما عليه أن يفطر','3330','1332','0');
INSERT INTO [t1446] VALUES (1440,'باب استحباب الفطر في السفر في رمضان لقبول رخصة الله التي رخص لعباده المؤمنين، إذ الله يحب قابل رخصته','3332','1332','0');
INSERT INTO [t1446] VALUES (1441,'باب ذكر تخيير المسافر بين الصوم والفطر، إذ الفطر رخصة، والصوم جائز " مع الدليل على أن قوله: " ليس البر "، و " وليس من البر الصوم في السفر " على ما تأولت؛ لأن الصوم في السفر ليس من البر، إذ ما ليس من البر، فمعصية، ولو كان الصوم في السفر معصية،','3334','1332','0');
INSERT INTO [t1446] VALUES (1442,'باب استحباب الصوم في السفر لمن قوي عليه والفطر لمن ضعف عنه','3337','1332','0');
INSERT INTO [t1446] VALUES (1443,'باب استحباب الفطر في السفر إذا عجز عن خدمة نفسه إذا صام','3339','1332','0');
INSERT INTO [t1446] VALUES (1444,'باب ذكر الدليل على أن المفطر الخادم في السفر أفضل من الصائم المخدوم في السفر','3341','1332','0');
INSERT INTO [t1446] VALUES (1445,'باب الرخصة في صوم بعض رمضان، وفطر بعض في السفر قال أبو بكر: خبر ابن عباس: صام رسول الله صلى الله عليه وسلم عام الفتح في رمضان حتى بلغ الكديد ثم أفطر','3344','1332','0');
INSERT INTO [t1446] VALUES (1446,'باب ذكر خبر توهم بعض العلماء أن الفطر في السفر ناسخ لإباحة الصوم في السفر','3345','1332','0');
INSERT INTO [t1446] VALUES (1447,'باب ذكر البيان على أن هذه الكلمة: " وإنما يؤخذ بالآخر " ليس من قول ابن عباس','3347','1332','0');
INSERT INTO [t1446] VALUES (1448,'باب ذكر دليل ثان على أن أمر النبي صلى الله عليه وسلم بالفطر عام الفتح لم يكن بناسخ لإباحته الصوم في السفر " خبر قزعة بن يحيى , عن أبي سعيد قال: ولقد رأيتنا نصوم بعد ذلك في السفر مع رسول الله صلى الله عليه وسلم: أمليته قبل "','3349','1332','0');
INSERT INTO [t1446] VALUES (1449,'باب الرخصة في الفطر في رمضان في السفر " لمن قد صام بعضه في الحضر، خلاف مذهب من أوجب عليه الصوم في السفر إذا كان قد صام بعضه في الحضر، توهم أن قوله: فمن شهد منكم الشهر فليصمه أن من شهد بعض الشهر وهو حاضر غير مسافر فوجب عليه صوم جميع الشهر، وإن سافر في','3350','1332','0');
INSERT INTO [t1446] VALUES (1450,'باب إباحة الفطر في رمضان في السفر يوما قد مضى بعضه والمرء ناو للصوم فيه قال أبو بكر: " قد أمليت خبر أبي سعيد الخدري "','3352','1332','0');
INSERT INTO [t1446] VALUES (1451,'باب إباحة الفطر في اليوم الذي يخرج المرء فيه مسافرا من بلده " إن ثبت الخبر، ضد مذهب من زعم أنه إذا دخل في الصوم مقيما، ثم سافر لم يجز له الفطر، وإباحة الفطر إذا جاوز المرء بيوت البلدة التي يخرج منها وإن كان قريبا يرى بيوتها "','3354','1332','0');
INSERT INTO [t1446] VALUES (1452,'باب الرخصة في الفطر في رمضان في مسيرة أقل من يوم وليلة " إن ثبت الخبر، فإني لا أعرف منصور بن زيد الكلبي هذا بعدالة ولا جرح "','3356','1332','0');
INSERT INTO [t1446] VALUES (1453,'باب الرخصة للحامل والمرضع في الإفطار في رمضان " والبيان أن فرض الصوم ساقط عنهما في رمضان على أن يقضيا من أيام أخر، إذ النبي صلى الله عليه وسلم قرنهما، أو إحديهما إلى المسافر، فجعل حكمهما أو حكم إحديهما حكم المسافر "','3358','1332','0');
INSERT INTO [t1446] VALUES (1454,'باب ذكر إسقاط فرض الصوم عن النساء أيام حيضهن','3361','1332','0');
INSERT INTO [t1446] VALUES (1455,'باب ذكر الدليل على أن الحائض يجب عليها قضاء الصوم في أيام طهرها " والرخصة لها في تأخير قضاء الصوم الذي أسقط الفرض عنها في أيام حيضها إلى شعبان "','3363','1332','0');
INSERT INTO [t1446] VALUES (1456,'باب قضاء ولي الميت صوم رمضان عن الميت إذا مات وأمكنه القضاء ففرط في قضائه','3368','1332','0');
INSERT INTO [t1446] VALUES (1457,'باب قضاء الصيام عن المرأة تموت وعليها صيام , والدليل على أن الصائم إذا قضى الحي عن الميت يكون ساقطا عن الميت , كالدين يقضى عنه بعد الموت إذ النبي صلى الله عليه وسلم شبه قضاء الصوم عن الميت بقضاء الدين عنها','3370','1332','0');
INSERT INTO [t1446] VALUES (1458,'باب الأمر بقضاء الصوم بالنذر عن الناذرة إذا ماتت قبل الوفاء بنذرها','3372','1332','0');
INSERT INTO [t1446] VALUES (1459,'باب ذكر البيان أن من قضى الصوم عن الناذر والناذرة من ولي , أو قريب , أو بعيد , أو ذكر , أو أنثى , أو حر , أو عبد , أو حرة , أو أمة , فالقضاء جائز عن الميت , إذ النبي صلى الله عليه وسلم شبه قضاء صوم النذر عن الميتة بقضاء الدين عنها , والدين إذا قضي عن','3374','1332','0');
INSERT INTO [t1446] VALUES (1460,'باب الإطعام عن الميت يموت وعليه صوم لكل يوم مسكينا إن صح الخبر , فإن في القلب من أشعث بن سوار رحمه الله لسوء حفظه','3376','1332','0');
INSERT INTO [t1446] VALUES (1461,'باب قدر مكيلة ما يطعم كل مسكين في كفارة الصوم إن ثبت الخبر , فإن في القلب من هذا الإسناد','3378','1332','0');
INSERT INTO [t1446] VALUES (1462,'جماع أبواب وقت الإفطار , وما يستحب أن يفطر عليه','3380','1332','0');
INSERT INTO [t1446] VALUES (1463,'باب ذكر خبر روي عن النبي صلى الله عليه وسلم في وقت الفطر بلفظ خبر معناه عندي معنى الأمر','3381','1332','0');
INSERT INTO [t1446] VALUES (1464,'باب ذكر دوام الناس على الخير ما عجلوا الفطر وفيه كالدلالة على أنهم إذا أخروا الفطر وقعوا في الشر','3383','1332','0');
INSERT INTO [t1446] VALUES (1465,'باب ذكر ظهور الدين ما عجل الناس فطرهم , والدليل على أن اسم الدين قد يقع على بعض شعب الإسلام','3385','1332','0');
INSERT INTO [t1446] VALUES (1466,'باب ذكر استحسان سنة المصطفى محمد صلى الله عليه وسلم ما لم ينتظر بالفطر قبل طلوع النجوم','3387','1332','0');
INSERT INTO [t1446] VALUES (1467,'باب ذكر حب الله عز وجل المعجلين للإفطار والدليل على ضد قول بعض أهل عصرنا ممن زعم أنه غير جائز أن يقال: أحب العباد إلى الله أعجلهم فطرا , إلا أن يكون الله يحب جميع عباده , وخالفنا في باب أفعل , فادعى ما لا يحسنه , فقد بينت باب أفعل في غير موضع من كتبنا','3389','1332','0');
INSERT INTO [t1446] VALUES (1468,'باب استحباب الفطر قبل صلاة المغرب','3391','1332','0');
INSERT INTO [t1446] VALUES (1469,'باب إعطاء مفطر الصائم مثل أجر الصائم من غير أن ينتقص الصائم من أجره شيئا','3393','1332','0');
INSERT INTO [t1446] VALUES (1470,'باب استحباب الفطر على الرطب إذا وجد وعلى التمر إذا لم يوجد الرطب','3395','1332','0');
INSERT INTO [t1446] VALUES (1471,'باب استحباب الفطر على الماء إذا أعوز الصائم الرطب , والتمر جميعا','3397','1332','0');
INSERT INTO [t1446] VALUES (1472,'باب الدليل على أن الأمر بالفطر على التمر إذا كان موجودا , أمر اختيار واستحباب طالبا للبركة إذ التمر بركة , وأن الأمر بالفطر على الماء إذا أعوز التمر أمر استحباب واختيار إذ الماء طهور , لا أن الأمر بذلك أمر فرض وإيجاب','3399','1332','0');
INSERT INTO [t1446] VALUES (1473,'باب الزجر عن الوصال في الصوم وذكر ما خص الله به نبيه صلى الله عليه وسلم من إباحة الوصال إذ الله تبارك وتعالى فرق بينه وبين أمته في ذلك أن كان الله يطعمه ويسقيه بالليل دونهم مكرمة له صلى الله عليه وسلم','3403','1332','0');
INSERT INTO [t1446] VALUES (1474,'باب تسمية الوصال بتعمق في الدين','3406','1332','0');
INSERT INTO [t1446] VALUES (1475,'باب الدليل على أن الوصال منهي عنه إذ ذلك يشق على المرء , خلاف ما يتأوله بعض المتصوفة ممن يفطر على اللقمة أو الجرعة من الماء فيعذب نفسه ليالي وأياما','3408','1332','0');
INSERT INTO [t1446] VALUES (1476,'باب النهي عن الوصال إلى السحر إذ تعجيل الفطر أفضل من تأخيره , إن كان الوصال إلى السحر قد أباحه المصطفى صلى الله عليه وسلم','3410','1332','0');
INSERT INTO [t1446] VALUES (1477,'باب إباحة الوصال إلى السحر وإن كان تعجيل الفطر أفضل','3412','1332','0');
INSERT INTO [t1446] VALUES (1478,'باب ذكر الدليل عن أن لا فرض على المسلمين من الصيام غير رمضان إلا ما يجب عليهم بأفعالهم وأقوالهم قال أبو بكر: خبر طلحة بن عبيد الله في مسألة النبي صلى الله عليه وسلم عن الإسلام , قال: وصيام رمضان , قال: هل علي غيره؟ قال: " لا , إلا أن تطوع "','3414','1332','0');
INSERT INTO [t1446] VALUES (1479,'باب الزجر عن قول المرء صمت رمضان كله','3415','1332','0');
INSERT INTO [t1446] VALUES (1480,'جماع أبواب صوم التطوع','3417','1332','0');
INSERT INTO [t1446] VALUES (1481,'باب فضل الصوم في المحرم إذ هو أفضل الصيام بعد شهر رمضان','3418','1332','0');
INSERT INTO [t1446] VALUES (1482,'باب استحباب صوم شعبان ووصله بشهر رمضان إذ كان أحب الشهور إلى النبي صلى الله عليه وسلم أن يصومه','3420','1332','0');
INSERT INTO [t1446] VALUES (1483,'باب إباحة وصل صوم شعبان بصوم رمضان والدليل على أن معنى خبر أبي هريرة , عن النبي صلى الله عليه وسلم: " إذا انتصف شعبان فلا تصوموا حتى رمضان أي: لا توصلوا شعبان برمضان فتصوموا جميع شعبان , أو أن يوافق ذلك صوما كان يصومه المرء قبل ذاك , فيصوم ذلك الصيام','3422','1332','0');
INSERT INTO [t1446] VALUES (1484,'باب بدء النبي صلى الله عليه وسلم بصيام عاشوراء وصامه','3424','1332','0');
INSERT INTO [t1446] VALUES (1485,'باب الدليل على أن بدء صيام عاشوراء كان قبل فرض صوم شهر رمضان','3426','1332','0');
INSERT INTO [t1446] VALUES (1486,'باب ذكر الدليل على أن ترك النبي صلى الله عليه وسلم صوم عاشوراء بعد نزول فرض صوم رمضان إن شاء تركه , لا أنه كان يتركه على كل حال , بل كان يتركه إن شاء تركه , ويصوم إن شاء صامه','3428','1332','0');
INSERT INTO [t1446] VALUES (1487,'باب ذكر خبر غلط في معناه عالم ممن لم يفهم معنى الخبر , وتوهم أن الأمر لصوم عاشوراء جميعا منسوخ بفرض صوم رمضان قال أبو بكر: خبر عمار بن ياسر: أمرنا بصوم عاشوراء قبل أن ينزل رمضان , فلما نزل رمضان لم نؤمر به , خرجته في كتاب الزكاة','3430','1332','0');
INSERT INTO [t1446] VALUES (1488,'باب علة أمر النبي صلى الله عليه وسلم بصيام عاشوراء بعد مقدمه المدينة والدليل على صحة مذهبنا في معنى " أولى " ضد مذهب من يدعي ما لا يحسنه من العلم , فزعم أنه غير جائز أن يقال: فلان أولى بفلان من فلان , إلا أن يكون لفلان أيضا ولاية , ولو كان على ما زعم ,','3432','1332','0');
INSERT INTO [t1446] VALUES (1489,'باب الدليل على أن أمر النبي صلى الله عليه وسلم بصيام عاشوراء لم يكن بأمر فرض وإيجاب بدءا ولا عددا , وأنه كان أمر فضيلة واستحباب','3434','1332','0');
INSERT INTO [t1446] VALUES (1490,'باب فضيلة صيام عاشوراء وتحري النبي صلى الله عليه وسلم صيامه لفضله من بين الأيام خلا صيام رمضان','3436','1332','0');
INSERT INTO [t1446] VALUES (1491,'باب ذكر تكفير الذنوب بصيام عاشوراء والبيان أن العمل الصالح يتقدم الفعل , الشيء يكون بعده فيكفر العمل الصالح الذنوب تكون بعد العمل الصالح , لا كما يتوهم من خالفنا في تقديم كفارة اليمين قبل الحنث , وزعم أنه غير جائز أن يتقدم المرء عملا صالحا يكفر ذنبا يكون','3438','1332','0');
INSERT INTO [t1446] VALUES (1492,'باب استحباب ترك الأمهات إرضاع الأطفال يوم عاشوراء تعظيما ليوم عاشوراء , إن صح الخبر , فإن في القلب من خالد بن ذكوان','3440','1332','0');
INSERT INTO [t1446] VALUES (1493,'باب الأمر بصيام يوم عاشوراء , إن أصبح المرء غير ناو للصيام غير مجمع على الصيام من الليل , والدليل على أن النبي صلى الله عليه وسلم إنما أراد بقوله: " لا صيام لمن لا يجمع الصيام من الليل " صوم الواجب دون صوم التطوع','3442','1332','0');
INSERT INTO [t1446] VALUES (1494,'باب الأمر بصيام بعض يوم عاشوراء إذا لم يعلم المرء بيوم عاشوراء قبل أن يطعم والفرق في الصوم بين عاشوراء وبين غيره , إذ صوم بعض يوم لا يكون صوما في غير يوم عاشوراء , لما خص النبي صلى الله عليه وسلم به يوم عاشوراء , فأمر بصوم بعض ذلك اليوم , وإن كان المرء','3444','1332','0');
INSERT INTO [t1446] VALUES (1495,'باب ذكر التخيير بين صيام عاشوراء وإفطاره والدليل على أن الأمر بصوم يوم عاشوراء أمر ندب وإرشاد وفضيلة','3446','1332','0');
INSERT INTO [t1446] VALUES (1496,'باب الأمر بأن يصام قبل عاشوراء يوما أو بعده يوما مخالفة لفعل اليهود في صوم عاشوراء','3448','1332','0');
INSERT INTO [t1446] VALUES (1497,'باب استحباب صوم يوم التاسع من المحرم اقتداء بالنبي صلى الله عليه وسلم','3450','1332','0');
INSERT INTO [t1446] VALUES (1498,'باب فضل صوم يوم عرفة , وتكفير الذنوب بلفظ خبر مجمل غير مفسر قال أبو بكر: خبر أبي قتادة عن النبي صلى الله عليه وسلم: " صوم عرفة يكفر السنة الماضية والسنة المقبلة " أمليته في باب صوم عاشوراء','3453','1332','0');
INSERT INTO [t1446] VALUES (1499,'باب ذكر خبر روي عن النبي صلى الله عليه وسلم في النهي عن صوم يوم عرفة مجمل غير مفسر','3454','1332','0');
INSERT INTO [t1446] VALUES (1500,'باب ذكر خبر مفسر للفظتين المجملتين اللتين ذكرتهما والدليل على أن النبي صلى الله عليه وسلم إنما كره صوم يوم عرفة بعرفات لا غيره، وفيه ما دل على أن قوله: صوم يوم عرفة يكفر السنة الماضية، والسنة المستقبلة بغير عرفات "','3456','1332','0');
INSERT INTO [t1446] VALUES (1501,'باب استحباب الإفطار يوم عرفة بعرفات اقتداء بالنبي صلى الله عليه وسلم وتقويا بالفطر على الدعاء، إذ الدعاء يوم عرفة أفضل الدعاء أو من أفضله','3458','1332','0');
INSERT INTO [t1446] VALUES (1502,'باب ذكر إفطار النبي صلى الله عليه وسلم في عشر ذي الحجة','3460','1332','0');
INSERT INTO [t1446] VALUES (1503,'باب ذكر علة قد كان النبي صلى الله عليه وسلم يترك لها بعض أعمال التطوع، وإن كان يحث عليها، وهي خشية أن يفرض عليهم ذلك الفعل مع استحبابه صلى الله عليه وسلم ما خفف على الناس من الفرائض','3462','1332','0');
INSERT INTO [t1446] VALUES (1504,'باب استحباب صوم يوم وإفطار يوم، والإعلام بأنه صوم نبي الله داود صلى الله عليه وسلم','3464','1332','0');
INSERT INTO [t1446] VALUES (1505,'باب الإخبار بأن صوم يوم، وفطر يوم أفضل الصيام، وأحبه إلى الله وأعدله','3467','1332','0');
INSERT INTO [t1446] VALUES (1506,'باب ذكر الدليل على أن النبي صلى الله عليه وسلم إنما خبر أن صيام داود أعدل الصيام وأفضله، وأحبه إلى الله إذ صائم يوم، مفطر يوم يكون مؤديا لحظ نفسه وعينه وأهله أيام فطره، ولا يكون مضيعا لحظ نفسه وعينه وأهله','3469','1332','0');
INSERT INTO [t1446] VALUES (1507,'باب ذكر الدليل على أن داود كان من أعبد الناس إذا كان صومه ما ذكرنا','3471','1332','0');
INSERT INTO [t1446] VALUES (1508,'باب ذكر تمني النبي صلى الله عليه وسلم استطاعة صوم يوم، وإفطار يومين','3473','1332','0');
INSERT INTO [t1446] VALUES (1509,'باب فضل الصوم في سبيل الله، ومباعدة الله المرء يصوم يوما في سبيل الله عن النار سبعين خريفا بذكر خبر مجمل غير مفسر','3475','1332','0');
INSERT INTO [t1446] VALUES (1510,'باب ذكر الخبر المفسر للفظة المجملة التي ذكرتها " والدليل على أن صوم اليوم الذي ذكرناه في سبيل الله إنما باعد الله صائمه به عن النار أنه إذا صامه ابتغاء وجه الله، إذ الله جل وعلا لا يقبل من الأعمال إلا ما كان له خالصا','3477','1332','0');
INSERT INTO [t1446] VALUES (1511,'باب فضل إتباع صيام رمضان بصيام ستة أيام من شوال، فيكون كصيام السنة كلها','3479','1332','0');
INSERT INTO [t1446] VALUES (1512,'باب ذكر الدليل على أن النبي صلى الله عليه وسلم إنما أعلم أن صيام رمضان وستة أيام من شوال يكون كصيام الدهر، إذ الله عز وجل جعل الحسنة بعشر أمثالها، أو يزيد إن شاء الله جل وعز','3481','1332','0');
INSERT INTO [t1446] VALUES (1513,'باب استحباب صوم الاثنين، ويوم الخميس، وتحري صومهما، اقتداء بفعل النبي صلى الله عليه وسلم','3483','1332','0');
INSERT INTO [t1446] VALUES (1514,'باب استحباب صوم يوم الاثنين إذ النبي صلى الله عليه وسلم ولد يوم الاثنين، وفيه أوحي إليه، وفيه مات صلى الله عليه وسلم','3485','1332','0');
INSERT INTO [t1446] VALUES (1515,'باب في استحباب صوم يوم الاثنين والخميس أيضا؛ لأن الأعمال فيهما تعرض على الله عز وجل','3488','1332','0');
INSERT INTO [t1446] VALUES (1516,'باب فضل صوم يوم واحد من كل شهر، وإعطاء الله عز وجل صائم يوم واحد من الشهر " مع الدليل على أن الله لم يرد بقوله: من جاء بالحسنة فله عشر أمثالها، أنه لا يعطي بالحسنة الواحدة أكثر من عشر أمثالها، إذ النبي المصطفى صلى الله عليه وسلم المبين عنه عز وجل قد','3491','1332','0');
INSERT INTO [t1446] VALUES (1517,'باب الأمر بصوم ثلاثة أيام من كل شهر استحبابا لا إيجابا','3493','1332','0');
INSERT INTO [t1446] VALUES (1518,'باب ذكر الدليل على أن الأمر بصوم الثلاث من كل شهر أمر ندب لا أمر فرض','3496','1332','0');
INSERT INTO [t1446] VALUES (1519,'باب ذكر تفضل الله عز وجل على الصائم ثلاثة أيام من كل شهر بإعطائه أجر صيام الدهر بالحسنة الواحدة عشر أمثالها','3500','1332','0');
INSERT INTO [t1446] VALUES (1520,'باب استحباب صيام هذه الأيام الثلاثة من كل شهر أيام البيض منها','3502','1332','0');
INSERT INTO [t1446] VALUES (1521,'باب إباحة صوم هذه الأيام الثلاثة من كل شهر أول الشهر مبادرة بصومهما خوف أن لا يدرك المرء صومها أيام البيض','3505','1332','0');
INSERT INTO [t1446] VALUES (1522,'باب ذكر الدليل على أن صوم ثلاثة أيام من كل شهر يقوم مقام صيام الدهر، كان صوم الثلاثة أيام من أول الشهر، أو من وسطه، أو من آخره قال أبو بكر: " في خبر أبي سلمة، عن عبد الله بن عمرو: " فإن كل حسنة بعشر أمثالها "','3507','1332','0');
INSERT INTO [t1446] VALUES (1523,'باب ذكر إيجاب الله عز وجل الجنة للصائم يوما واحدا إذا جمع مع صومه صدقة، وشهود جنازة، وعيادة مريض','3509','1332','0');
INSERT INTO [t1446] VALUES (1524,'باب في صفة صوم النبي صلى الله عليه وسلم خلا ما تقدم ذكرنا له بذكر خبر مجمل غير مفسر','3511','1332','0');
INSERT INTO [t1446] VALUES (1525,'باب ذكر الخبر المفسر للفظة المجملة التي ذكرتها " والدليل على أن عائشة إنما أرادت بقولها: النبي صلى الله عليه وسلم لم يصم شهرا تاما غير رمضان، شهر شعبان الذي كان يصل صومه بصوم رمضان " قال أبو بكر: " قد أمليت خبر أبي سلمة، وعائشة في مواصلة النبي صلى','3513','1332','0');
INSERT INTO [t1446] VALUES (1526,'باب ذكر صوم أيام متتابعة من الشهر، وإفطار أيام متتابعة بعدها من الشهر','3515','1332','0');
INSERT INTO [t1446] VALUES (1527,'باب ذكر ما أعد الله جل وعلا في الجنة من الغرف لمداوم صيام التطوع " إن صح الخبر؛ فإن في القلب من عبد الرحمن بن إسحاق أبي شيبة الكوفي، وليس هو بعبد الرحمن بن إسحاق الملقب بعباد الذي روى عن سعيد المقبري، والزهري، وغيرهما، هو صالح الحديث مدني، سكن واسط','3518','1332','0');
INSERT INTO [t1446] VALUES (1528,'باب ذكر صلاة الملائكة على الصائم عند أكل المفطرين عنده','3521','1332','0');
INSERT INTO [t1446] VALUES (1529,'باب الرخصة في صوم التطوع، وإن لم يجمع المرء على الصوم من الليل " والدليل على أن النبي صلى الله عليه وسلم إنما أراد بقوله: " لا صيام لمن لم يجمع الصيام من الليل "، وصوم الواجب دون صوم التطوع "','3524','1332','0');
INSERT INTO [t1446] VALUES (1530,'باب إباحة الفطر في صوم التطوع بعد مضي بعض النهار، والمرء ناو للصوم فيما مضى من النهار','3526','1332','0');
INSERT INTO [t1446] VALUES (1531,'باب ذكر الدليل على أن المفطر في صوم التطوع بعد دخوله فيه مجمعا على صوم ذلك اليوم خلاف مذهب من رأى إيجاب إعادة صوم ذلك اليوم عليه','3528','1332','0');
INSERT INTO [t1446] VALUES (1532,'باب تمثيل الصوم في الشتاء بالغنيمة الباردة، والدليل على أن الشيء قد يشبه بما يشبهه في بعض المعاني لا في كلها','3530','1332','0');
INSERT INTO [t1446] VALUES (1533,'جماع أبواب ذكر الأيام " والدليل على أن النبي صلى الله عليه وسلم قد ينهى عن الشيء، ويسكت عن غيره غير مبيح لما سكت عنه، إن النبي صلى الله عليه وسلم قد زجر عن صوم يوم الفطر، ويوم النحر في الأخبار التي رويت عنه في النهي عن صومهما، ولم يكن في نهيه عن','3532','1332','0');
INSERT INTO [t1446] VALUES (1534,'باب النهي عن صوم أيام التشريق بدلالة بتصريح نهي','3534','1332','0');
INSERT INTO [t1446] VALUES (1535,'باب الزجر عن صيام أيام التشريق بتصريح نهي','3536','1332','0');
INSERT INTO [t1446] VALUES (1536,'باب ذكر النهي عن صيام الدهر من غير ذكر العلة التي لها نهي عنه','3539','1332','0');
INSERT INTO [t1446] VALUES (1537,'باب ذكر العلة التي لها زجر النبي صلى الله عليه وسلم عن صوم الدهر','3542','1332','0');
INSERT INTO [t1446] VALUES (1538,'باب الرخصة في صوم الدهر إذا أفطر المرء الأيام التي زجر عن الصيام فيهن','3544','1332','0');
INSERT INTO [t1446] VALUES (1539,'باب فضل صيام الدهر إذا أفطر الأيام التي زجر عن الصيام فيها','3546','1332','0');
INSERT INTO [t1446] VALUES (1540,'باب ذكر أخبار رويت عن النبي صلى الله عليه وسلم في النهي عن صوم يوم الجمعة مجملة غير مفسرة','3550','1332','0');
INSERT INTO [t1446] VALUES (1541,'باب ذكر الخبر المفسر في النهي عن صيام يوم الجمعة " والدليل على أن النهي عنه إذا أفرد يوم الجمعة بالصيام من غير أن يصام قبله أو بعده "','3552','1332','0');
INSERT INTO [t1446] VALUES (1542,'باب الدليل على أن يوم الجمعة يوم عيد " وأن النهي عن صيامه إذ هو عيد، والفرق بين الجمعة، وبين العيدين الفطر والأضحى، إذ جاء بنهي صومهما مفردا، ولا موصولا بصيام قبل ولا بعد "','3554','1332','0');
INSERT INTO [t1446] VALUES (1543,'باب أمر الصائم يوم الجمعة مفردا بالفطر بعد مضي بعض النهار','3556','1332','0');
INSERT INTO [t1446] VALUES (1544,'باب النهي عن صوم يوم السبت تطوعا " إذا أفرد بالصوم بذكر خبر مجمل غير مفسر بلفظ عام مراده خاص، وأحسب أن النهي عن صيامه، إذ اليهود تعظمه، وقد اتخذته عيدا بدل الجمعة "','3558','1332','0');
INSERT INTO [t1446] VALUES (1545,'باب ذكر الدليل على أن النهي عن صوم يوم السبت تطوعا إذا أفرد بصوم لا إذا صام صائم يوما قبله أو يوما بعده قال أبو بكر: " في إخبار النبي صلى الله عليه وسلم في النهي عن صوم يوم الجمعة إلا أن يصام قبله أو بعده يوما دلالة على أنه قد أباح صوم يوم السبت إذا صام','3561','1332','0');
INSERT INTO [t1446] VALUES (1546,'باب الرخصة في يوم السبت إذا صام يوم الأحد بعده','3563','1332','0');
INSERT INTO [t1446] VALUES (1547,'باب النهي عن صوم المرأة تطوعا بغير إذن زوجها إذا كان زوجها حاضرا غير غائب عنها " بذكر خبر لفظه خاص مراده عام، من الجنس الذي نقول: إن الأمر إذا كان لعلة فمتى كانت العلة قائمة كان الأمر واجبا "','3565','1332','0');
INSERT INTO [t1446] VALUES (1548,'باب ذكر أبواب ليلة القدر " والتأليف بين الأخبار المأثورة عن النبي صلى الله عليه وسلم فيها ما يحسب كثير من حملة العلم ممن لا يفهم صناعة العلم أنها متهاترة متنافية، وليس كذلك هي عندنا بحمد الله ونعمته بل هي مختلفة الألفاظ متفقة المعنى على ما سأبينه إن شاء','3567','1332','0');
INSERT INTO [t1446] VALUES (1549,'باب ذكر دوام ليلة القدر في كل رمضان إلى قيام الساعة، ونفي انقطاعها بنفي الأنبياء','3568','1332','0');
INSERT INTO [t1446] VALUES (1550,'باب ذكر الدليل على أن ليلة القدر هي في رمضان من غير شك، ولا ارتياب في غيره " ضد قول من زعم أن الحالف آخر يوم من شعبان أن امرأته طالق، أو عبده حر، أو أمته حرة ليلة القدر، أن الطلاق، والعتق غير واقع إلى مضي السنة من يوم حلف؛ لأنه زعم لا يدري ليلة','3570','1332','0');
INSERT INTO [t1446] VALUES (1551,'باب ذكر الدليل على أن ليلة القدر في العشر الأواخر من رمضان، خلاف قول من ذكرنا مقالتهم في الباب قبل هذا " والدليل على أن الحالف يوم شهر رمضان قبل غروب الشمس بطرفه بأن امرأته طالق، أو عبده حر، فهل هلال شوال كان الطلاق، أو العتق، أو هما لو كان الحلف','3572','1332','0');
INSERT INTO [t1446] VALUES (1552,'باب الأمر بالتماس ليلة القدر وطلبها في العشر الأواخر من رمضان بلفظ مجمل غير مفسر','3574','1332','0');
INSERT INTO [t1446] VALUES (1553,'باب ذكر الخبر المفسر للفظة المجملة التي ذكرتها " والدليل على أن النبي صلى الله عليه وسلم إنما أمر بطلب ليلة القدر في العشر الأواخر من رمضان في الوتر منها لا في الشفع "','3576','1332','0');
INSERT INTO [t1446] VALUES (1554,'باب ذكر الدليل على أن الأمر بطلب ليلة القدر في الوتر مما يبقى من العشر الأواخر لا في الوتر مما يمضي منها','3579','1332','0');
INSERT INTO [t1446] VALUES (1555,'باب ذكر الخبر المفسر للدليل الذي ذكرت في طلب ليلة القدر في الوتر مما يبقى من العشر الأواخر لا مما يمضي منها','3581','1332','0');
INSERT INTO [t1446] VALUES (1556,'باب الدليل على أن الوتر مما يبقى من العشر الأواخر قد يكون أيضا الوتر مما مضى منه، إذ الشهر قد يكون تسعا وعشرين','3583','1332','0');
INSERT INTO [t1446] VALUES (1557,'باب ذكر الخبر المفسر للدليل الذي ذكرت، إذ النبي صلى الله عليه وسلم قد أمر بطلبها ليلة ثلاث وعشرين مما قد مضى من الشهر، وكانت ليلة سابعة مما تبقى','3585','1332','0');
INSERT INTO [t1446] VALUES (1558,'باب ذكر خبر روي عن النبي صلى الله عليه وسلم في الأمر بطلب ليلة القدر في السبع الأواخر من غير ذكر العلة التي لها أمر بالاقتصار على طلبها في السبع دون العشر جميعا','3587','1332','0');
INSERT INTO [t1446] VALUES (1559,'باب ذكر الخبر الدال على صحة المعنى الثاني الذي ذكرت أنه أمر بطلبها في السبع الأواخر إذا ضعف وعجز طالبها عن طلبها في العشر كله','3589','1332','0');
INSERT INTO [t1446] VALUES (1560,'جماع أبواب ذكر الليالي التي كان فيها ليلة القدر في زمن النبي صلى الله عليه وسلم، والدليل على أن ليلة القدر تنتقل في العشر الأواخر من رمضان في الوتر على ما ثبت','3591','1332','0');
INSERT INTO [t1446] VALUES (1561,'باب ذكر الدليل على أن ليلة القدر قد كانت في زمن النبي صلى الله عليه وسلم في بعض الشهر ليلة إحدى وعشرين في رمضان قال أبو بكر: " خبر أبي سعيد الخدري أمليته في غير هذا الموضع "','3592','1332','0');
INSERT INTO [t1446] VALUES (1562,'باب ذكر الأمر بطلب ليلة القدر ليلة ثلاث وعشرين، إذ جائز أن تكون ليلة القدر في بعض السنين ليلة إحدى وعشرين، وفي بعض ليلة ثلاث وعشرين','3593','1332','0');
INSERT INTO [t1446] VALUES (1563,'باب ذكر كون ليلة القدر في بعض السنين، إذ ليلة سبع وعشرين إذ ليلة القدر تنتقل في العشر الأواخر في الوتر على ما ذكرت','3596','1332','0');
INSERT INTO [t1446] VALUES (1564,'باب الأمر بطلب ليلة القدر آخر ليلة من رمضان إذ جائز أن يكون في بعض السنين تلك الليلة','3599','1332','0');
INSERT INTO [t1446] VALUES (1565,'باب صفة ليلة القدر بنفي الحر والبرد فيها، وشدة ضوئها، ومنع خروج شياطينها منها حتى يضيء فجرها','3601','1332','0');
INSERT INTO [t1446] VALUES (1566,'باب صفة الشمس عند طلوعها صبيحة ليلة القدر','3603','1332','0');
INSERT INTO [t1446] VALUES (1567,'باب حمرة الشمس عند طلوعها وضعفها صبيحة ليلة القدر، والاستدلال بصفة الشمس على ليلة القدر إن صح الخبر؛ فإن في القلب من حفظ زمعة','3605','1332','0');
INSERT INTO [t1446] VALUES (1568,'باب الدليل على أن الشمس لا يكون لها شعاع إلى وقت ارتفاعها ذلك اليوم إلى آخر النهار','3607','1332','0');
INSERT INTO [t1446] VALUES (1569,'باب ذكر كثرة الملائكة في الأرض ليلة القدر','3609','1332','0');
INSERT INTO [t1446] VALUES (1570,'باب ذكر البيان أن المدرك لصلاة العشاء في جماعة ليلة القدر يكون مدركا لفضيلة ليلة القدر','3611','1332','0');
INSERT INTO [t1446] VALUES (1571,'باب ذكر إنساء الله عز وجل النبي صلى الله عليه وسلم ليلة القدر بعد رؤيته إياها','3613','1332','0');
INSERT INTO [t1446] VALUES (1572,'باب ذكر الدليل على أن رؤية النبي صلى الله عليه وسلم ليلة القدر كان في نوم، وفي يقظة','3615','1332','0');
INSERT INTO [t1446] VALUES (1573,'باب ذكر رجاء النبي صلى الله عليه وسلم وظنه أن يكون رفع علمه ليلة القدر خيرا لأمته من إطلاعهم على علمها " إذ الاجتهاد في العمل لياليا طمعا في إدراك ليلة القدر أفضل وأكبر عملا من الاجتهاد في ليلة واحدة خاصة "','3617','1332','0');
INSERT INTO [t1446] VALUES (1574,'باب مغفرة ذنوب العبد بقيام ليلة القدر إيمانا واحتسابا','3619','1332','0');
INSERT INTO [t1446] VALUES (1575,'باب استحباب شهود البدوي الصلاة في مسجد المدينة ليلة ثلاث وعشرين من رمضان إذا كان سكنه قرب المدينة تحريا لإدراك ليلة القدر في مسجدها','3621','1332','0');
INSERT INTO [t1446] VALUES (1576,'جماع أبواب ذكر أبواب قيام شهر رمضان','3623','1332','0');
INSERT INTO [t1446] VALUES (1577,'باب ذكر الدليل على أن قيام شهر رمضان سنة النبي صلى الله عليه وسلم خلاف زعم الروافض الذين يزعمون أن قيام شهر رمضان بدعة لا سنة','3624','1332','0');
INSERT INTO [t1446] VALUES (1578,'باب الأمر بقيام رمضان أمر ترغيب، لا أمر عزم وإيجاب','3626','1332','0');
INSERT INTO [t1446] VALUES (1579,'باب ذكر مغفرة سالف ذنوب أخر بقيام رمضان إيمانا واحتسابا','3628','1332','0');
INSERT INTO [t1446] VALUES (1580,'باب الصلاة جماعة في قيام شهر رمضان، ضد قول من يتوهم أن الفاروق هو أول من أمر بالصلاة جماعة في قيام شهر رمضان','3630','1332','0');
INSERT INTO [t1446] VALUES (1581,'باب ذكر الدليل على أن النبي صلى الله عليه وسلم إنما خص القيام بالناس هذه الليالي الثلاث لليلة القدر فيهن','3632','1332','0');
INSERT INTO [t1446] VALUES (1582,'باب ذكر قيام الليل كله للمصلي مع الإمام في قيام رمضان حتى يفرغ','3634','1332','0');
INSERT INTO [t1446] VALUES (1583,'باب الدليل على أن النبي صلى الله عليه وسلم إنما ترك قيام ليالي رمضان كله خشية أن يفترض قيام الليل على أمته فيعجزوا عنه','3636','1332','0');
INSERT INTO [t1446] VALUES (1584,'باب إمامة القارئ الأميين في قيام شهر رمضان مع الدليل على أن صلاة الجماعة في قيام رمضان سنة النبي صلى الله عليه وسلم لا بدعة كما زعمت الروافض','3638','1332','0');
INSERT INTO [t1446] VALUES (1585,'باب استحباب صلاة النساء جماعة مع الإمام في قيام رمضان، مع الدليل على أن قيام رمضان في جماعة أفضل من صلاة المرء منفردا في رمضان، وإن كان المأمومون قراء يقرءون القرآن، لا كمن اختار صلاة المنفرد على صلاة الجماعة في قيام رمضان قال أبو بكر: في خبر أبي','3640','1332','0');
INSERT INTO [t1446] VALUES (1586,'باب في فضل قيام رمضان واستحقاق قائمه اسم الصديقين، والشهداء إذا جمع مع قيامه رمضان صيام نهاره، وكان مقيما للصلوات الخمس، مؤديا للزكاة، شاهدا لله بالوحدانية، مقرا للنبي صلى الله عليه وسلم بالرسالة','3642','1332','0');
INSERT INTO [t1446] VALUES (1587,'باب ذكر عدد صلاة النبي صلى الله عليه وسلم بالليل في رمضان، والدليل على أنه لم يكن يزيد في رمضان على عدد الركعات في الصلاة بالليل ما كان يصلي من غير رمضان','3644','1332','0');
INSERT INTO [t1446] VALUES (1588,'باب استحباب إحياء ليالي العشر الأواخر من شهر رمضان، وترك مجامعة النساء فيهن، والاشتغال بالعبادة، وإيقاظ المرء أهله فيهن','3647','1332','0');
INSERT INTO [t1446] VALUES (1589,'باب استحباب الاجتهاد في العمل في العشر الأواخر من شهر رمضان','3649','1332','0');
INSERT INTO [t1446] VALUES (1590,'باب استحباب ترك المبيت على الفراش في رمضان إذ البائت على الفراش أثقل نوما، وأقل نشاطا للقيام من النائم على غير الفرش الوطيئة الممهدة في شهر رمضان','3651','1332','0');
INSERT INTO [t1446] VALUES (1591,'جماع أبواب الاعتكاف','3653','1332','0');
INSERT INTO [t1446] VALUES (1592,'باب وقت الاعتكاف في العشر الأواخر من شهر رمضان','3654','1332','0');
INSERT INTO [t1446] VALUES (1593,'باب إباحة ضرب القباب في المسجد للاعتكاف فيهن قال أبو بكر: " في خبر عمارة بن غزية حديث أبي سعيد: اعتكف في قبة تركية، خرجته في غير هذا الباب "','3656','1332','0');
INSERT INTO [t1446] VALUES (1594,'باب في اعتكاف شهر رمضان كله','3657','1332','0');
INSERT INTO [t1446] VALUES (1595,'باب الاقتصار في الاعتكاف على العشر الأوسط، والعشر الأواخر من رمضان، إذ الاعتكاف كله فضيلة لا فريضة، والفضيلة لا تضيق على المرء أن يزيد فيها، أو ينقص منها','3659','1332','0');
INSERT INTO [t1446] VALUES (1596,'باب إباحة الاقتصار من الاعتكاف على العشر الأواخر من شهر رمضان دون العشرين الأولين','3661','1332','0');
INSERT INTO [t1446] VALUES (1597,'باب الرخصة في الاقتصار على اعتكاف السبع الوسط من شهر رمضان دون ما قبله، وما بعده من رمضان','3663','1332','0');
INSERT INTO [t1446] VALUES (1598,'باب المداومة على اعتكاف العشر الأواخر من شهر رمضان','3665','1332','0');
INSERT INTO [t1446] VALUES (1599,'باب الاعتكاف في شوال إذا فات الاعتكاف في رمضان، لفضل دوام العمل','3667','1332','0');
INSERT INTO [t1446] VALUES (1600,'باب الاعتكاف في السنة المقبلة إذا فات ذلك لسفر، أو علة تصيب المرء','3669','1332','0');
INSERT INTO [t1446] VALUES (1601,'باب الأمر بوفاء نذر الاعتكاف ينذره المرء في الشرك، ثم يسلم الناذر قبل قضاء النذر، وإباحة اعتكاف ليلة واحدة في عشر رمضان','3673','1332','0');
INSERT INTO [t1446] VALUES (1602,'باب إباحة دخول المعتكف البيت لحاجة الإنسان الغائط، والبول','3677','1332','0');
INSERT INTO [t1446] VALUES (1603,'باب ترك دخول المعتكف البيت إلا لحاجة الإنسان، وإباحة إخراج المعتكف رأسه من المسجد إلى المرأة لتغسله وترجله أخبرني ابن عبد الحكم، أن ابن وهب أخبرهم قال: أخبرني يونس، ومالك، والليث، عن ابن شهاب، عن عروة، وعمرة، بمثل حديث يونس بن عبد الأعلى سواء،','3679','1332','0');
INSERT INTO [t1446] VALUES (1604,'باب الرخصة في ترجيل المرأة الحائض رأس المعتكف، ومسها إياه، وهي خارجة من المسجد','3680','1332','0');
INSERT INTO [t1446] VALUES (1605,'باب الرخصة في زيارة المرأة وزوجها في اعتكافه ومحادثتها إياه عند زيارتها إياه','3682','1332','0');
INSERT INTO [t1446] VALUES (1606,'باب ذكر الدليل على أن النبي صلى الله عليه وسلم إنما بلغ مع صفية حين أراد قلبها إلى منزلها باب المسجد لا أنه خرج من المسجد فردها إلى منزلها','3684','1332','0');
INSERT INTO [t1446] VALUES (1607,'باب الرخصة في السمر للمعتكف مع نسائه في الاعتكاف، خبر صفية من هذا الباب','3686','1332','0');
INSERT INTO [t1446] VALUES (1608,'باب الافتراش في المسجد، ووضع السرر فيه للاعتكاف','3688','1332','0');
INSERT INTO [t1446] VALUES (1609,'باب الرخصة في بناء بيوت السعف في المسجد للاعتكاف فيها','3690','1332','0');
INSERT INTO [t1446] VALUES (1610,'باب الرخصة في وضع الأمتعة التي يحتاج إليها المعتكف في اعتكافه في المسجد','3692','1332','0');
INSERT INTO [t1446] VALUES (1611,'باب الخبر الدال على إجازة الاعتكاف بلا مقارنة للصوم إذ النبي صلى الله عليه وسلم قد أمر باعتكاف ليلة، ولا صوم في الليل','3694','1332','0');
INSERT INTO [t1446] VALUES (1612,'باب الرخصة للنساء في الاعتكاف في مسجد الجماعات مع أزواجهن إذا اعتكفوا " في خبر عائشة: فاستأذنته عائشة لتعتكف معه فأذن لها، ثم استأذنت لحفصة، قد أمليت الحديث بتمامه "','3696','1332','0');
INSERT INTO [t1446] VALUES (1613,'باب ذكر المعتكف ينذر في اعتكافه ما ليس له فيه طاعة، وليس بنذر يتقرب إلى الله عز وجل','3697','1332','0');
INSERT INTO [t1446] VALUES (1614,'باب وقت خروج المعتكف من معتكفه، والدليل على أن المعتكف يخرج من معتكفه مصبحا لا ممسيا','3700','1332','0');
INSERT INTO [t1446] VALUES (1615,'كتاب الزكاة المختصر من المختصر من المسند على الشريطة التي ذكرتها في أول الكتاب','3702','0','0');
INSERT INTO [t1446] VALUES (1616,'باب البيان أن إيتاء الزكاة من الإسلام بحكم الأمينين: أمين السماء جبريل، وأمين الأرض محمد النبي صلى الله عليهما','3703','1615','0');
INSERT INTO [t1446] VALUES (1617,'باب البيان أن إيتاء الزكاة من الإيمان، إذ الإيمان والإسلام اسمان لمعنى واحد','3705','1615','0');
INSERT INTO [t1446] VALUES (1618,'جماع أبواب التغليظ في منع الزكاة','3707','1615','0');
INSERT INTO [t1446] VALUES (1619,'باب الأمر بقتال مانع الزكاة " اتباعا لأمر الله عز وجل بقتال المشركين حتى يتوبوا من الشرك، ويقيموا الصلاة، ويؤتوا الزكاة، وائتمارا لأمره جل وعلا بتخليتهم بعد إقام الصلاة وإيتاء الزكاة. قال الله عز وجل: فاقتلوا المشركين حيث وجدتموهم - إلى قوله -: فإن','3708','1615','0');
INSERT INTO [t1446] VALUES (1620,'باب الدليل على أن دم المرء وماله إنما يحرمان بعد الشهادة بإقام الصلاة وإيتاء الزكاة إذا وجبت " إذ الله عز وجل جعلهم إخوان المسلمين بعد التوبة من الشرك وبعد إقام الصلاة وإيتاء الزكاة إذا وجبتا "','3710','1615','0');
INSERT INTO [t1446] VALUES (1621,'باب ذكر إدخال مانع الزكاة النار مع أوائل من يدخلها، بالله نتعوذ من النار','3712','1615','0');
INSERT INTO [t1446] VALUES (1622,'باب ذكر لعن لاوي الصدقة الممتنع من أدائها','3714','1615','0');
INSERT INTO [t1446] VALUES (1623,'باب صفات ألوان عقاب مانع الزكاة يوم القيامة قبل الفصل بين الخلق، نعوذ بالله من عذابه','3716','1615','0');
INSERT INTO [t1446] VALUES (1624,'باب ذكر بعض ألوان مانع الزكاة " والدليل على ضد قول من جهل معنى قوله تعالى: والذين يكنزون الذهب والفضة الآية، فزعم أن هذه الآية إنما نزلت في الكفار لا في المؤمنين، والنبي المصطفى صلى الله عليه وسلم قد أعلم أن هذه الآية إنما نزلت في المؤمنين لا في','3718','1615','0');
INSERT INTO [t1446] VALUES (1625,'باب ذكر أخبار رويت عن النبي صلى الله عليه وسلم في الكنز مجملة غير مفسرة','3724','1615','0');
INSERT INTO [t1446] VALUES (1626,'باب ذكر الخبر المفسر للكنز " والدليل على أن الكنز هو المال الذي لا يؤدى زكاته، لا المال المدفون الذي يؤدى زكاته "','3727','1615','0');
INSERT INTO [t1446] VALUES (1627,'باب ذكر الدليل على أن لا واجب في المال غير الزكاة " وفيه ما دل على أن الوعيد بالعذاب للمكتنز ولمن لا يؤدي زكاة ماله دون من يؤديها وإن كان المال مدفونا " قال أبو بكر: " في خبر طلحة بن عبيد الله: علي غيرها؟ قال: " لا، إلا أن تطوع "، وفي خبر أبي هريرة','3730','1615','0');
INSERT INTO [t1446] VALUES (1628,'باب ذكر دليل آخر على أن الوعيد للمكتنز هو لمانع الزكاة دون من يؤديها','3731','1615','0');
INSERT INTO [t1446] VALUES (1629,'باب بيعة الإمام الناس على إيتاء الزكاة','3733','1615','0');
INSERT INTO [t1446] VALUES (1630,'باب ذكر البيان أن فرض الزكاة كان قبل الهجرة إلى أرض الحبشة، إذ النبي صلى الله عليه وسلم مقيم بمكة قبل هجرته إلى المدينة','3735','1615','0');
INSERT INTO [t1446] VALUES (1631,'جماع أبواب صدقة المواشي من الإبل والبقر والغنم','3737','1615','0');
INSERT INTO [t1446] VALUES (1632,'باب فرض صدقة الإبل والغنم " والدليل على أن الله عز وجل أراد بقوله: خذ من أموالهم صدقة بعض الأموال لا كلها، إذ اسم المال قد يقع على ما دون خمس من الإبل وعلى ما دون الأربعين من الغنم "','3738','1615','0');
INSERT INTO [t1446] VALUES (1633,'باب ذكر الدليل على أن صغار الإبل والغنم وكبارهما تعد على مالكها عند أخذ الساعي الصدقة من مالكها','3740','1615','0');
INSERT INTO [t1446] VALUES (1634,'باب الدليل على أن الصدقة لا تجب " فيما دون خمس من الإبل ولا فيما دون الأربعين من الغنم، مع الدليل على أن اسم الصدقة واقع على عشر الحبوب والثمار، وعلى زكاة المناض من الورق، وعلى صدقة المواشي، إذ العامة تفرق بين الزكاة والصدقة والعشر، لجهلها بالعلم،','3742','1615','0');
INSERT INTO [t1446] VALUES (1635,'باب ذكر الدليل على أن اسم الزكاة أيضا واقع على صدقة المواشي، إذ الصدقة والزكاة اسمان للواجب في المال','3744','1615','0');
INSERT INTO [t1446] VALUES (1636,'باب ذكر الدليل على أن الصدقة إنما تجب في الإبل والغنم في سوائمها دون غيرهما، ضد قول من زعم أن في الإبل العوامل صدقة','3746','1615','0');
INSERT INTO [t1446] VALUES (1637,'باب صدقة البقر بذكر لفظ مجمل غير مفسر','3750','1615','0');
INSERT INTO [t1446] VALUES (1638,'باب ذكر الخبر المفسر للفظة الجملة التي ذكرتها " والدليل على أن النبي صلى الله عليه وسلم إنما أوجب الصدقة في البقر في سوائمها دون عواملها "','3753','1615','0');
INSERT INTO [t1446] VALUES (1639,'باب النهي عن أخذ اللبون في الصدقة بغير رضى صاحب الماشية','3756','1615','0');
INSERT INTO [t1446] VALUES (1640,'باب الزجر عن إخراج الهرمة والمعيبة والتيس في الصدقة بغير مشيئة المصدق وإباحة أخذهن إذا شاء المصدق وأراد','3758','1615','0');
INSERT INTO [t1446] VALUES (1641,'باب إباحة دعاء الإمام على مخرج مسن ماشيته في الصدقة بأن لا يبارك له في ماشيته ودعائه لمخرج أفضل ماشيته في الصدقة بأن يبارك له في ماله','3760','1615','0');
INSERT INTO [t1446] VALUES (1642,'باب الزجر عن أخذ المصدق خيار المال بذكر خبر مجمل غير مفسر','3762','1615','0');
INSERT INTO [t1446] VALUES (1643,'باب ذكر الخبر المفسر للفظة المجملة التي ذكرتها " والدليل على أن النبي صلى الله عليه وسلم إنما زجر عن أخذ كرائم أموال من تجب عليه الصدقة في ماله إذا أخذ المصدق كرائم أموالهم بغير طيب أنفسهم، إذ النبي صلى الله عليه وسلم قد أباح أخذ خيار أموالهم إذا طابت','3764','1615','0');
INSERT INTO [t1446] VALUES (1644,'باب الزجر عن الجمع بين المتفرق والتفريق بين المجتمع في السوائم خيفة الصدقة وتراجع الخليطين بينهما بالسوية فيما أخذ المصدق ماشيتهما جميعا " والدليل على أن الخليطين في الماشية فيما يجب عليهما من الصدقة كالمالك الواحد، إذ لو كانا خليطين كالمالكين إذا لم','3767','1615','0');
INSERT INTO [t1446] VALUES (1645,'باب النهي عن الجلب عند أخذ الصدقة من المواشي، والأمر بأخذ صدقة المواشي في ديار مالكها من غير أن يؤمروا بجلب المواشي إلى الساعي ليأخذ صدقتهما','3769','1615','0');
INSERT INTO [t1446] VALUES (1646,'باب أخذ الغنم والدراهم فيما بين أسنان الإبل التي يجب في الصدقة إذا لم يوجد السن الواجبة في الإبل، والبيان ضد قول من زعم أن بين السنيين قدر قيمة ما بينهما " وهذا القول إغفال من قائله، أو هو خلاف سنة النبي صلى الله عليه وسلم وكل قول خلاف سنته فمردود غير','3771','1615','0');
INSERT INTO [t1446] VALUES (1647,'باب الأمر بسمة إبل الصدقة إذا قبضت في الصدقة ليعرف الوالي والرعية إبل الصدقة من غيرها ليقسمها على أهل سهمان الصدقة دون غيرها - إن صح الخبر -','3773','1615','0');
INSERT INTO [t1446] VALUES (1648,'باب سمة غنم الصدقة إذا قبضت','3775','1615','0');
INSERT INTO [t1446] VALUES (1649,'باب إسقاط الصدقة - صدقة المال عن الخيل والرقيق - بذكر لفظ مختصر غير مستقصى في الرقيق خاصة','3777','1615','0');
INSERT INTO [t1446] VALUES (1650,'باب ذكر الخبر المستقصي للفظة المختصرة التي ذكرتها في صدقة الرقيق " والدليل على أن النبي صلى الله عليه وسلم إنما عفا عن الصدقة في الرقيق صدقة الأموال دون صدقة الفطر "','3782','1615','0');
INSERT INTO [t1446] VALUES (1651,'باب ذكر السنة الدالة على معنى أخذ عمر بن الخطاب عن الخيل والرقيق الصدقة " والدليل على أنه إنما أخذها منهم إذ جادت أنفسهم وكانت بإعطائها متطوعين بالدفع، لا أن الصدقة كانت واجبة على الخيل والرقيق، إذ الفاروق قد أعلم القوم الذين أخذ منهم صدقة الخيل','3785','1615','0');
INSERT INTO [t1446] VALUES (1652,'باب ذكر إسقاط الصدقة عن الحمر مع الدليل على إسقاطها عن الخيل " والدليل على أن الله عز وجل إنما أمر نبيه صلى الله عليه وسلم بأخذ الصدقة من بعض أموال المسلمين، لا من جميع أموالهم في قوله عز وجل: خذ من أموالهم صدقة تطهرهم وتزكيهم بها، إذ اسم المال واقع','3787','1615','0');
INSERT INTO [t1446] VALUES (1653,'باب الرخصة في تأخير الإمام قسم الصدقة بعد أخذه إياها وإباحة بعثة مواشي الصدقة إلى الرعي إلى أن يرى الإمام قسمها','3791','1615','0');
INSERT INTO [t1446] VALUES (1654,'جماع أبواب صدقة الورق','3793','1615','0');
INSERT INTO [t1446] VALUES (1655,'باب إسقاط فرض الزكاة عما دون خمس أواق من الورق','3794','1615','0');
INSERT INTO [t1446] VALUES (1656,'باب الدليل على أن الخمسة الأواق هي مائتي درهم','3797','1615','0');
INSERT INTO [t1446] VALUES (1657,'باب ذكر مبلغ الزكاة في الورق إذا بلغ خمس أواق','3799','1615','0');
INSERT INTO [t1446] VALUES (1658,'باب ذكر البيان أن الزكاة واجبة على ما زاد على المائتين من الورق ضد قول من زعم أن الزكاة غير واجبة على ما زاد على المائتي درهم حتى تبلغ الزيادة أربعين درهما','3801','1615','0');
INSERT INTO [t1446] VALUES (1659,'باب ذكر الدليل على أن الزكاة غير واجبة على الحلي " إذ اسم الورق في لغة العرب الذين خوطبنا بلغتهم لا يقع على، الحلي الذي: هو متاع ملبوس "','3803','1615','0');
INSERT INTO [t1446] VALUES (1660,'جماع أبواب صدقة الحبوب والثمار','3805','1615','0');
INSERT INTO [t1446] VALUES (1661,'باب ذكر إسقاط الصدقة عما دون خمسة أوسق قال أبو بكر: " خبر أبي سعيد ليس فيما دون خمسة أوسق صدقة "','3806','1615','0');
INSERT INTO [t1446] VALUES (1662,'باب ذكر إيجاب الصدقة في البر والتمر إذا بلغ الصنف الواحد منهما خمسة أوسق','3807','1615','0');
INSERT INTO [t1446] VALUES (1663,'باب ذكر الدليل على أن النبي صلى الله عليه وسلم، إنما أوجب في البر الزكاة إذا بلغ البر خمسة أوساق، وفي التمر إذا بلغ خمسة أوساق، لا إذا بلغ البر والتمر خمسة أوساق إذا ضم أحدهما إلى الآخر','3809','1615','0');
INSERT INTO [t1446] VALUES (1664,'باب إيجاب الصدقة في الزبيب إذا بلغ خمسة أوسق، وفي القلب من هذا الإسناد، ليس هذا الخبر مما سمعه عمرو بن دينار، من جابر علمي','3812','1615','0');
INSERT INTO [t1446] VALUES (1665,'باب ذكر مبلغ الواجب من الصدقة في الحبوب والثمار، والفرق بين الواجب في الصدقة فيما سقته السماء أو الأنهار أو هما، وبين ما سقي بالرشاء، والدوالي','3815','1615','0');
INSERT INTO [t1446] VALUES (1666,'باب ذكر مبلغ الوسق إن صح الخبر، ولا خلاف بين العلماء في مبلغه على ما روي في هذا الخبر إلا أن أبا البختري لا أحسبه سمع من أبي سعيد','3819','1615','0');
INSERT INTO [t1446] VALUES (1667,'باب الزجر عن إخراج الحبوب والتمور الرديئة في الصدقة قال الله عز وجل: ولا تيمموا الخبيث منه تنفقون ولستم بآخذيه إلا أن تغمضوا فيه','3821','1615','0');
INSERT INTO [t1446] VALUES (1668,'باب وقت بعثة الإمام الخارص يخرص الثمار " والدليل على أن الثمار تخرص كي تحصى الزكاة على مالك الثمرة قبل أن تؤكل الثمرة وتفرق، ويخير الخارص صاحب الثمرة بين أن يأخذ جميع الثمرة ويضمن العشر أو نصف العشر للصدقة، وبين أن يدفع جميع الثمر إلى الخارص، ويضمن له','3826','1615','0');
INSERT INTO [t1446] VALUES (1669,'باب السنة في خرص العنب لتؤخذ زكاته زبيبا كما تؤخذ زكاة النخل تمرا','3828','1615','0');
INSERT INTO [t1446] VALUES (1670,'باب السنة في قدر ما يؤمر الخارص بتركه من الثمار فلا يخرصه على صاحب المال ليكون قدر ما يأكله رطبا ويطعمه قبل يبس التمر، غير داخل فيما يخرج منه العشر أو نصف العشر','3831','1615','0');
INSERT INTO [t1446] VALUES (1671,'باب فرض إخراج الصدقة في العسر واليسر، والتغليظ في منع الزكاة في العسر','3834','1615','0');
INSERT INTO [t1446] VALUES (1672,'باب ذكر البيان أن النبي صلى الله عليه وسلم إنما أراد بالنجدة والرسل " - في هذا الموضع - العسر واليسر، وأراد بقوله من نجدتها ورسلها أي: في نجدتها ورسلها "','3836','1615','0');
INSERT INTO [t1446] VALUES (1673,'باب ذكر أخذ الصدقة من المعادن إن صح الخبر، فإن في القلب من اتصال هذا الإسناد','3838','1615','0');
INSERT INTO [t1446] VALUES (1674,'باب ذكر صدقة العسل إن صح الخبر؛ فإن في القلب من هذا الإسناد','3840','1615','0');
INSERT INTO [t1446] VALUES (1675,'باب إيجاب الخمس في الركاز','3842','1615','0');
INSERT INTO [t1446] VALUES (1676,'باب وجوب الخمس فيما يوجد في الخرب العادي من دفن الجاهلية " والدليل على أن الركاز ليس بدفن الجاهلية، إذ النبي صلى الله عليه وسلم، إن ثبت هذا الخبر عنه، قد فرق بين الموجود في الخرب العادي وبين الركاز فأوجب فيهما جميعا الخمس "','3844','1615','0');
INSERT INTO [t1446] VALUES (1677,'باب الرخصة في تقديم الصدقة قبل حلول الحول على المال والفرق بين الفرض الذي يجب في المال وبين الفرض الواجب على البدن','3846','1615','0');
INSERT INTO [t1446] VALUES (1678,'باب احتساب ما قد حبس المؤمن السلاح والعبد في سبيل الله من الصدقة إذا وجبت، فهذه المسألة أيضا من باب تقديم الصدقة قبل وجوبها قال أبو بكر: في خبر أبي هريرة: " فأما خالد فإنكم تظلمون خالدا، قد احتبس أدراعه وأعبده في سبيل الله، والنبي صلى الله عليه وسلم','3851','1615','0');
INSERT INTO [t1446] VALUES (1679,'باب استسلاف الإمام المال لأهل سهمان الصدقة ورده ذلك من الصدقة إذا قبضت بعد الاستسلاف','3852','1615','0');
INSERT INTO [t1446] VALUES (1680,'جماع أبواب ذكر السعاية على الصدقة','3854','1615','0');
INSERT INTO [t1446] VALUES (1681,'باب ذكر التغليظ على السعاية بذكر خبر مجمل غير مفسر','3855','1615','0');
INSERT INTO [t1446] VALUES (1682,'باب ذكر الدليل على أن التغليظ في العمل على السعاية المذكور في خبر عقبة هو في الساعي، إذا لم يعدل في عمله وجار وظلم، وفضل السعاية على الصدقة إذا عدل الساعي فيما يتولى منها وتشبيهه بالغازي في سبيل الله','3857','1615','0');
INSERT INTO [t1446] VALUES (1683,'باب في التغليظ في الاعتداء في الصدقة وتمثيل المعتدي فيها بمانعها','3859','1615','0');
INSERT INTO [t1446] VALUES (1684,'باب التغليظ في غلول الساعي من الصدقة','3862','1615','0');
INSERT INTO [t1446] VALUES (1685,'باب ذكر البيان أن ما كتم الساعي من قليل المال أو كثيره عن الإمام كان ما كتم غلولا قال الله عز وجل: ومن يغلل يأت بما غل يوم القيامة','3864','1615','0');
INSERT INTO [t1446] VALUES (1686,'باب التغليظ في قبول المصدق الهدية ممن يتولى السعاية عليهم','3866','1615','0');
INSERT INTO [t1446] VALUES (1687,'باب صفة إتيان الساعي يوم القيامة بما غل من الصدقة، وأمر الإمام بمحاسبة الساعي إذا قدم من سعايته','3868','1615','0');
INSERT INTO [t1446] VALUES (1688,'باب الأمر بإرضاء المصدق وإصداره راضيا عن أصحاب الأموال','3870','1615','0');
INSERT INTO [t1446] VALUES (1689,'باب الزجر عن استعمال موالي النبي صلى الله عليه وسلم، على الصدقة إذا طلبوا العمالة، إذ هم ممن لا تحل لهم الصدقة المفروضة','3872','1615','0');
INSERT INTO [t1446] VALUES (1690,'باب الزجر عن استعمال موالي النبي صلى الله عليه وسلم على الصدقة إذا طلبوا العمالة على السعاية، إذ الموالي من أنفس القوم، والصدقة تحرم عليهم كتحريمها على النبي صلى الله عليه وسلم، صدقة الفرض دون صدقة التطوع','3874','1615','0');
INSERT INTO [t1446] VALUES (1691,'باب صلاة الإمام على المأخوذ منه الصدقة إتباعا لأمر الله عز وجل بنبيه صلى الله عليه وسلم في قوله: خذ من أموالهم صدقة تطهرهم وتزكيهم بها وصل عليهم إن صلاتك سكن لهم','3876','1615','0');
INSERT INTO [t1446] VALUES (1692,'جماع أبواب قسم المصدقات، وذكر أهل سهمانها','3878','1615','0');
INSERT INTO [t1446] VALUES (1693,'باب الأمر بقسم الصدقة في أهل البلدة التي تؤخذ منهم الصدقة','3879','1615','0');
INSERT INTO [t1446] VALUES (1694,'باب ذكر تحريم الصدقة المفروضة على النبي المصطفى صلى الله عليه وسلم " والدليل على أن الله عز وجل إنما أراد بقوله: إنما الصدقات للفقراء إلى آخر الآية، بعض الفقراء أو بعض المساكين، وبعض العاملين، وبعض الغارمين، وبعض أبناء السبيل، فولى النبي صلى الله','3881','1615','0');
INSERT INTO [t1446] VALUES (1695,'باب ذكر البيان أن على أولياء الأطفال من آل النبي صلى الله عليه وسلم منعهم من أكل ما حرم على البالغين','3884','1615','0');
INSERT INTO [t1446] VALUES (1696,'باب ذكر الدليل على أن الصدقة المحرمة على النبي صلى الله عليه وسلم هي الصدقة المفروضة التي أوجبها الله في أموال الأغنياء لأهل سهمان الصدقة دون صدقة التطوع " والدليل على أن النبي صلى الله عليه وسلم إنما قال: " إنا أهل بيت لا تحل لنا الصدقة "، أي: الصدقة','3886','1615','0');
INSERT INTO [t1446] VALUES (1697,'باب ذكر الدلائل الأخرى على أن النبي صلى الله عليه وسلم، إنما أراد بقوله: " إن الصدقة لا تحل لآل محمد " صدقة الفريضة دون صدقة التطوع','3889','1615','0');
INSERT INTO [t1446] VALUES (1698,'باب ذكر الدليل على أن بني عبد المطلب هم من آل النبي صلى الله عليه وسلم الذين حرموا الصدقة، لا كما قال من زعم أن آل النبي صلى الله عليه وسلم الذين حرموا الصدقة، آل علي، وآل جعفر، وآل العباس','3893','1615','0');
INSERT INTO [t1446] VALUES (1699,'باب إعطاء الفقراء من الصدقة إتباعا لأمر الله في قوله: إنما الصدقات للفقراء','3896','1615','0');
INSERT INTO [t1446] VALUES (1700,'باب صدقة الفقير الذي يجوز له المسألة في الصدقة " والدليل على أن لا وقت فيما يعطى الفقير من الصدقة إلا قدرا يسد خلته وفاقته "','3898','1615','0');
INSERT INTO [t1446] VALUES (1701,'باب الدليل على أن شهادة ذوي الحجا في هذا الموضع هي اليمين، إذ الله عز وجل قد سمى اليمين في اللعان شهادة','3900','1615','0');
INSERT INTO [t1446] VALUES (1702,'باب الرخصة في إعطاء من له ضيعة من الصدقة، إذا أصابت غلته جائحة أذهبت غلته قدر ما يسد فاقته','3902','1615','0');
INSERT INTO [t1446] VALUES (1703,'باب إعطاء اليتامى من الصدقة إذا كانوا فقراء، إن ثبت الخبر فإن في النفس من أشعث بن سوار، وإن لم يثبت هذا الخبر، فالقرآن كاف في نقل الخبر الخاص فيه، قد أعلم الله في محكم تنزيله أن للفقراء قسم في الصدقات، فالفقير - كان يتيما أو غير يتيم - فله في الصدقة','3904','1615','0');
INSERT INTO [t1446] VALUES (1704,'باب ذكر صفة المسلمين الذين أمر الله بإعطائهم من الصدقة','3906','1615','0');
INSERT INTO [t1446] VALUES (1705,'باب إعطاء العامل على الصدقة منها رزقا لعمله قال الله عز وجل: إنما الصدقات للفقراء والمساكين والعاملين عليها الآية','3908','1615','0');
INSERT INTO [t1446] VALUES (1706,'باب ذكر الدليل على أن العامل على الصدقة إن عمل عليها متطوعا بالعمل بغير إرادة ونية لأخذ عمالة على عمله فأعطاه الإمام لعمالته رزقا من غير مسألة ولا إشراف، فجائز له أخذه','3912','1615','0');
INSERT INTO [t1446] VALUES (1707,'باب ذكر إعطاء العامل على الصدقة عمالة من الصدقة وإن كان غنيا','3914','1615','0');
INSERT INTO [t1446] VALUES (1708,'باب فرض الإمام للعامل على الصدقة رزقا معلوما','3916','1615','0');
INSERT INTO [t1446] VALUES (1709,'باب إذن الإمام للعامل بالتزويج واتخاذ الخادم والمسكن من الصدقة','3918','1615','0');
INSERT INTO [t1446] VALUES (1710,'باب ذكر إعطاء المؤلفة قلوبهم من الصدقة ليسلموا للعطية','3920','1615','0');
INSERT INTO [t1446] VALUES (1711,'باب إعطاء رؤساء الناس وقادتهم على الإسلام تألفا بالعطية','3923','1615','0');
INSERT INTO [t1446] VALUES (1712,'باب إعطاء الغارمين من الصدقة وإن كانوا أغنياء بلفظ خبر مجمل غير مفسر','3925','1615','0');
INSERT INTO [t1446] VALUES (1713,'باب الدليل على أن الغارم الذي يجوز إعطاؤه من الصدقة وإن كان غنيا هو الغارم في الحمالة " والدليل على أنه يعطى قدر ما يؤدي الحمالة، لا أكثر "','3927','1615','0');
INSERT INTO [t1446] VALUES (1714,'باب الرخصة في إعطاء من يحج من سهم سبيل الله، إذ الحج من سبيل الله','3929','1615','0');
INSERT INTO [t1446] VALUES (1715,'باب إعطاء الإمام الحاج إبل الصدقة ليحجوا عليها','3931','1615','0');
INSERT INTO [t1446] VALUES (1716,'باب الرخصة في إعطاء الإمام المظاهر من الصدقة ما يكفر به عن ظهاره، إذا لم يكن واجدا للكفارة','3933','1615','0');
INSERT INTO [t1446] VALUES (1717,'باب الإمام المصدق بقسم الصدقة حيث يقبض " إن صح الخبر فإن في القلب من أشعث بن سوار، وإن لم يثبت هذا الخبر، فخبر ابن عباس في أمر النبي صلى الله عليه وسلم معاذا بأخذ الصدقة من أغنياء أهل اليمن وقسمها في فقرائهم، كان من هذا الخبر "','3935','1615','0');
INSERT INTO [t1446] VALUES (1718,'باب حمل صدقات أهل البوادي إلى الإمام؛ ليكون هو المفرق لها','3937','1615','0');
INSERT INTO [t1446] VALUES (1719,'باب حمل الصدقة من المدن إلى الإمام ليتولى تفرقتها على أهل الصدقة','3939','1615','0');
INSERT INTO [t1446] VALUES (1720,'باب الرخصة في قسم المرء صدقته من غير دفعها إلى الوالي قال الله عز وجل: إن تبدوا الصدقات فنعما هي','3941','1615','0');
INSERT INTO [t1446] VALUES (1721,'باب إعطاء الإمام دية من لا يعرف قاتله من الصدقة، وهذا عندي من جنس الحمالة لشبه أن يكون المصطفى صلى الله عليه وسلم، تحمل بهذه الدية فأعطاها من إبل الصدقة','3943','1615','0');
INSERT INTO [t1446] VALUES (1722,'باب استحباب إيثار المرء بصدقته قرابته دون الأباعد، لانتظام الصدقة، وصلة معا بتلك العطية','3945','1615','0');
INSERT INTO [t1446] VALUES (1723,'باب فضل الصدقة على ذي الرحم الكاشح','3947','1615','0');
INSERT INTO [t1446] VALUES (1724,'باب ذكر تحريم الصدقة على الأصحاء الأقوياء على الكسب، والأغنياء بكسبهم عن الصدقات، وإن لم يكونوا أغنياء بمال يملكونه، بذكر خبر مجمل غير مفسر','3949','1615','0');
INSERT INTO [t1446] VALUES (1725,'باب الرخصة في إعطاء الإمام من الصدقة من يذكر حاجة وفاقة لا يعلم الإمام منه خلافه من غير مسألة عن حاله، أهو فقير محتاج أم لا؟ قال أبو بكر: " خبر سلمة بن صخر في ذكره للنبي صلى الله عليه وسلم، أنهم يأتون وحشا ليس لهم عشاء، وبعثة النبي صلى الله عليه','3952','1615','0');
INSERT INTO [t1446] VALUES (1726,'باب استحباب الاستعفاف عن أكل الصدقة لمن يجد عنها إعفاء بمعنى من المعاني، وإن كان من أهلها إذ هي غسالة ذنوب الناس','3953','1615','0');
INSERT INTO [t1446] VALUES (1727,'باب كراهة المسألة من الصدقة إذا كان سائلها واجدا غداء أو عشاء يشبعه يوما وليلة وإن كان أخذه للصدقة - من غير مسألة - جائزا','3955','1615','0');
INSERT INTO [t1446] VALUES (1728,'جماع أبواب صدقة الفطر في رمضان','3957','1615','0');
INSERT INTO [t1446] VALUES (1729,'باب ذكر فرض زكاة الفطر " والبيان على أن زكاة الفطر على من يجب عليه زكاته، ضد قول من زعم أنها سنة غير فريضة، والمبين عن الله عز وجل ما أنزل عليه من وحيه أعلم أمتة أن هذه الصدقة فرض عليهم، كما أعلمهم أن في خمس من الإبل صدقة، وبين لهم جميع الفرض الذي','3958','1615','0');
INSERT INTO [t1446] VALUES (1730,'باب ذكر الدليل على أن الأمر بصدقة الفطر كان قبل فرض لزكاة الأموال','3961','1615','0');
INSERT INTO [t1446] VALUES (1731,'باب الدليل على أن فرض صدقة الفطر على الذكر والأنثى والحر والمملوك مع الدليل على أن النبي صلى الله عليه وسلم، إذا أمرنا لأمر مرة، لم ينسخ أمره السكت بعد ذلك، ولا ينسخ أمره إلا أن يعلم صلى الله عليه وسلم أن ما كان أمرهم به ساقط عنهم','3963','1615','0');
INSERT INTO [t1446] VALUES (1732,'باب الدليل على أن صدقة الفطر عن المملوك واجب على مالكه، لا على المملوك كما توهم بعض الناس','3965','1615','0');
INSERT INTO [t1446] VALUES (1733,'باب ذكر دليل ثان أن صدقة الفطر عن المملوك واجب على مالكه، وأن معنى قوله صلى الله عليه وسلم في خبر ابن عمر على المملوك معناه: عن المملوك، لا أنها واجبة على المملوك كما زعم من قال أن المماليك يملكون','3967','1615','0');
INSERT INTO [t1446] VALUES (1734,'باب الدليل على أن صدقة الفطر يجب أداؤها عن المماليك المسلمين دون المشركين، خلاف قول من زعم أنها واجبة على المسلم في عبيده المشركين','3969','1615','0');
INSERT INTO [t1446] VALUES (1735,'باب الدليل على أن صدقة الفطر فرض على كل من استطاع أداؤها خلاف قول من زعم أن فرضها ساقط عن من لا يجب عليه زكاة الفطر','3971','1615','0');
INSERT INTO [t1446] VALUES (1736,'باب ذكر الدليل على أن زكاة رمضان إنما تجب بصاع النبي صلى الله عليه وسلم، لا بالصاع الذي أحدث بعد، إذ الصاع على عهد النبي صلى الله عليه وسلم بالمدينة كان صاعه','3973','1615','0');
INSERT INTO [t1446] VALUES (1737,'باب الدليل على أن فرض صدقة الفطر على من يستطيع أداءها دون من لم يستطع قال أبو بكر: " خبر أبي هريرة، عن النبي صلى الله عليه وسلم: " وما أمرتكم به من شيء فاتقوا الله ما استطعتم "','3975','1615','0');
INSERT INTO [t1446] VALUES (1738,'باب إيجاب صدقة الفطر على الصغير خلاف قول من زعم أنها ساقطة عن من سقط عنه فرض الصلاة','3976','1615','0');
INSERT INTO [t1446] VALUES (1739,'باب توقيت فرض زكاة الفطر في مبلغه من الكيل','3978','1615','0');
INSERT INTO [t1446] VALUES (1740,'باب الدليل على أن الأمر بصدقة نصف الصاع من حنطة أحدثه الناس بعد النبي المصطفى صلى الله عليه وسلم','3981','1615','0');
INSERT INTO [t1446] VALUES (1741,'باب الدليل على أنهم أمروا نصف صاع حنطة إذا كان ذلك قيمة صاع تمر أو شعير، والواجب على هذا الأصل أن يتصدق بآصع من حنطة في بعض الأزمان وبعض البلدان','3983','1615','0');
INSERT INTO [t1446] VALUES (1742,'باب ذكر أول ما أحدث الأمر بنصف صاع حنطة، وذكر أول من أحدثه','3985','1615','0');
INSERT INTO [t1446] VALUES (1743,'باب إخراج التمر والشعير في صدقة الفطر','3987','1615','0');
INSERT INTO [t1446] VALUES (1744,'باب إخراج الزبيب والأقط في صدقة الفطر','3990','1615','0');
INSERT INTO [t1446] VALUES (1745,'باب إخراج السلت صدقة الفطر إن كان ابن عيينة ومن دونه حفظه أو صح خبر ابن عباس، وإلا فإن في خبر موسى بن عقبة كفاية إن شاء الله','3994','1615','0');
INSERT INTO [t1446] VALUES (1746,'باب إخراج جميع الأطعمة في صدقة الفطر " والدليل على ضد قول من زعم أن الهليلج، والفلوس جائز إخراجها في صدقة الفطر "','3998','1615','0');
INSERT INTO [t1446] VALUES (1747,'باب ذكر ثناء الله عز وجل على مؤدي صدقة الفطر','4002','1615','0');
INSERT INTO [t1446] VALUES (1748,'باب الأمر بأداء صدقة الفطر قبل خروج الناس إلى صلاة العيد','4004','1615','0');
INSERT INTO [t1446] VALUES (1749,'باب الدليل على أن أمر النبي صلى الله عليه وسلم بأدائها في يوم الفطر لا في غيره','4006','1615','0');
INSERT INTO [t1446] VALUES (1750,'باب الدليل على أن الصلاة التي أمر النبي صلى الله عليه وسلم بأداء صدقة الفطر قبل الخروج إليها صلاة العيد لا غيرها','4008','1615','0');
INSERT INTO [t1446] VALUES (1751,'باب الرخصة في تأخير الإمام قسم صدقة الفطر عن يوم الفطر إذا أديت إليه','4010','1615','0');
INSERT INTO [t1446] VALUES (1752,'جماع أبواب صدقة التطوع','4012','1615','0');
INSERT INTO [t1446] VALUES (1753,'باب فضل الصدقة وقبض الرب عز وجل إياها ليربيها لصاحبها والبيان أنه لا يقبل إلا الطيب','4013','1615','0');
INSERT INTO [t1446] VALUES (1754,'باب الأمر باتقاء النار - نعوذ بالله منها - بالصدقة وإن قلت','4016','1615','0');
INSERT INTO [t1446] VALUES (1755,'باب إظلال الصدقة صاحبها يوم القيامة إلى الفراغ من الحكم بين العباد','4020','1615','0');
INSERT INTO [t1446] VALUES (1756,'باب فضل الصدقة على غيرها من الأعمال إن صح الخبر، فإني لا أعرف أبا فروة بعدالة ولا جرح','4023','1615','0');
INSERT INTO [t1446] VALUES (1757,'باب الدليل على أن الصدقة بالمملوك أفضل من عتق المتصدق إياه إن صح الخبر','4025','1615','0');
INSERT INTO [t1446] VALUES (1758,'باب فضل المتصدق على المتصدق عليه','4027','1615','0');
INSERT INTO [t1446] VALUES (1759,'باب ذكر نماء المال بالصدقة منه، وإعطاء الرب عز وجل المتصدق قال الله عز وجل: وما أنفقتم من شيء فهو يخلفه','4030','1615','0');
INSERT INTO [t1446] VALUES (1760,'باب فضل الصدقة عن ظهر غنى يفضل عمن يعول المتصدق','4033','1615','0');
INSERT INTO [t1446] VALUES (1761,'باب الزجر عن صدقة المرء بماله كله " والدليل على أن النبي صلى الله عليه وسلم أراد بقوله: " عن ظهر غنى " عما يغنيه ومن يعول لا عن كثرة الرجل "','4036','1615','0');
INSERT INTO [t1446] VALUES (1762,'باب صدقة المقل إذا أبقى لنفسه قدر حاجته','4039','1615','0');
INSERT INTO [t1446] VALUES (1763,'باب ذكر الدليل على أن النبي صلى الله عليه وسلم إنما فضل صدقة المقل إذا كان فضلا عمن يعول، لا إذا تصدق على الأباعد وترك من يعول جياعا عراة إذ النبي صلى الله عليه وسلم قد أمر ببدء من يعول','4041','1615','0');
INSERT INTO [t1446] VALUES (1764,'باب التغليظ في مسألة الغني من الصدقة','4044','1615','0');
INSERT INTO [t1446] VALUES (1765,'باب ذكر الغني تكون المسألة معه إلحافا','4046','1615','0');
INSERT INTO [t1446] VALUES (1766,'باب تشبيه الملحف بمن سف المسألة','4048','1615','0');
INSERT INTO [t1446] VALUES (1767,'باب الرخصة في الصدقة على من يمونه متطوعا','4050','1615','0');
INSERT INTO [t1446] VALUES (1768,'باب فضل الصدقة على المماليك إذا كانوا عند مليك السوء، إن ثبت الخبر','4052','1615','0');
INSERT INTO [t1446] VALUES (1769,'باب ذكر إعطاء المرء المال ناويا الصدقة، وإلقائه ذلك المال موضع الصدقة من غير نطق منه بأنه صدقة','4054','1615','0');
INSERT INTO [t1446] VALUES (1770,'باب ذكر الدليل على أن النبي صلى الله عليه وسلم إنما فضل صدقة المقل إذا كان فضلا عمن يعول، لا إذا تصدق على الأباعد وترك من يعول جياعا','4055','1615','0');
INSERT INTO [t1446] VALUES (1771,'باب الزجر عن عيب المتصدق المقل بالقليل من الصدقة، ولمزه، والزجر عن رمي المتصدق بالكثير من الصدقة بالرياء والسمعة " إذ الله عز وجل هو العالم بإرادة المراد، ولا إرادة مما تكنه القلوب، ولم يطلع الله العباد على ما في ضمائر غيرهم من الإرادة "','4058','1615','0');
INSERT INTO [t1446] VALUES (1772,'باب فضل صدقة الصحيح الشحيح الخائف من الفقر، المؤمل طول العمر على صدقة المريض الخائف نزول المنية به','4060','1615','0');
INSERT INTO [t1446] VALUES (1773,'باب فضل صدقة المرء بأحب ماله لله " إذ الله عز وجل نفى إدراك البر عمن لا ينفق مما يحب. قال الله عز وجل: لن تنالوا البر حتى تنفقوا مما تحبون "','4062','1615','0');
INSERT INTO [t1446] VALUES (1774,'باب ذكر حب الله عز وجل المخفي بالصدقة " إذ الله عز وجل قد فضلها على صدقة العلانية. قال الله عز وجل: إن تبدوا الصدقات فنعما هي وإن تخفوها وتؤتوها الفقراء فهو خير لكم "','4064','1615','0');
INSERT INTO [t1446] VALUES (1775,'باب ذكر مثل ضربه النبي صلى الله عليه وسلم للمتصدق ومنع الشياطين إياه منها بتخويف الفقير " إن صح الخبر، فإني لا أقف هل سمع الأعمش من ابن بريدة أم لا؟ قال الله عز وجل: الشيطان يعدكم الفقر ويأمركم بالفحشاء " الآية','4066','1615','0');
INSERT INTO [t1446] VALUES (1776,'باب الأمر بإتيان القرابة بما يتقرب به الموالي إلى الله عز وجل من صدقة التطوع " والدليل على أن المراد إذا قال: ما لي ونصفه هو لله، كانت صدقة. مع الدليل على أن الأرض أو الدار أو الحائط أو البستان أو الخان أو الحانوت إذا جعله المرء لله كانت صدقة، وإن لم','4068','1615','0');
INSERT INTO [t1446] VALUES (1777,'باب ذكر الدليل على أن احتمال الشهادة بصدقة العقار جائز للشهود إذا علموا العقار المتصدق به من غير تحديد، إذ العقار مشهور بالمتصدق منسوب إليه مستغن بشهرته ونسبته إلى المتصدق به عن ذكر تحديده " والدليل على إباحة الحاكم احتمال الشهادة إذا شهد عليها "','4070','1615','0');
INSERT INTO [t1446] VALUES (1778,'باب استحباب إتيان المرأة زوجها وولدها بصدقة التطوع على غيرهم من الأباعد إذ هم أحق بأن يتصدق عليهم من الأباعد','4072','1615','0');
INSERT INTO [t1446] VALUES (1779,'باب ذكر تضعيف صدقة المرأة على زوجها وعلى ما في حجرها على الصدقة على غيرهم','4075','1615','0');
INSERT INTO [t1446] VALUES (1780,'باب صدقة المرء على ولده " والدليل على أن الصدقة إذا رجعت إلى المتصدق بها إرثا عن المتصدق عليه جاز له. والفرق بين ما يملكه الرجل من الصدقة إرثا وبين ما يملكه بابتياع أو استيهاب إذ الإرث يملكه الوارث أحب ذلك أم كره، ولا يملك المرء ملكا بغير نية، وأخبر','4078','1615','0');
INSERT INTO [t1446] VALUES (1781,'باب الأمر بالصدقة من الثمار قبل الجذاذ من كل حائط بقنو يوضع في المسجد','4080','1615','0');
INSERT INTO [t1446] VALUES (1782,'باب كراهية الصدقة بالحشف من الثمار، وإن كانت الصدقة تطوعا، إذ الصدقة بخير الثمار وأوساطها أفضل من الصدقة بشرارها','4082','1615','0');
INSERT INTO [t1446] VALUES (1783,'باب إعطاء السائل من الصدقة وإن كان زيه زي الأغنياء في المركب والملبس','4084','1615','0');
INSERT INTO [t1446] VALUES (1784,'باب ذكر مبلغ الثمار الذي يستحب وضع قنو منه للمساكين في المسجد إذا بلغ جذاذ الرجل من الثمار ذلك المبلغ','4086','1615','0');
INSERT INTO [t1446] VALUES (1785,'باب ذكر الدليل على أن أمر النبي صلى الله عليه وسلم بوضع القنو الذي ذكرنا في المسجد للمساكين " أمر ندب، وإرشاد لا أمر فريضة، وإيجاب، خبر طلحة بن عبد الله من هذا الباب "','4088','1615','0');
INSERT INTO [t1446] VALUES (1786,'باب الأمر بإعطاء السائل وإن قلت العطية وصغرت قيمتها، وكراهية رد السائل من غير إعطاء إذا لم يكن للمسئول ما يجزل العطية','4091','1615','0');
INSERT INTO [t1446] VALUES (1787,'باب التغليظ في الرجوع عن صدقة التطوع وتمثيله بالكلب يقيء ثم يعود في قيئه','4094','1615','0');
INSERT INTO [t1446] VALUES (1788,'باب استحباب الإعلان بالصدقة ناويا لاستنان الناس بالمتصدق فيكتب لمبتدئ الصدقة مثل أجر المتصدق استنانا به','4096','1615','0');
INSERT INTO [t1446] VALUES (1789,'باب الرخصة في الخيلاء عند الصدقة قال أبو بكر: " خبر ابن عتيك خرجته في كتاب الجهاد "','4098','1615','0');
INSERT INTO [t1446] VALUES (1790,'باب كراهية منع الصدقة إذ مانعها مانع استقراض ربه " إذ الله عز وجل سمى الصدقة قرضا استقرض الله عباده، ووعد على ذلك بتضعيف الصدقة أضعافا كثيرة قال الله عز وجل: من ذا الذي يقرض الله قرضا حسنا فيضاعفه له أضعافا كثيرة "','4102','1615','0');
INSERT INTO [t1446] VALUES (1791,'باب ذكر البيان أن لأهل الصدقة بابا من أبواب الجنة يخصون بدخولها من ذلك الباب','4104','1615','0');
INSERT INTO [t1446] VALUES (1792,'باب التغليظ في مسألة الغني الصدقة','4106','1615','0');
INSERT INTO [t1446] VALUES (1793,'باب التغليظ في الصدقة مرآة وسمعة " والدليل على أن المرائي بالصدقة من أوائل من تستعر بهم النار يوم القيامة، نعوذ بالله من الرياء والسمعة، والله نسأل أن يعيذنا من النار بعفوه قال الله عز وجل: من كان يريد العاجلة عجلنا له فيها ما نشاء لمن نريد ثم جعلنا','4108','1615','0');
INSERT INTO [t1446] VALUES (1794,'جماع أبواب الصدقات والمحبسات','4110','1615','0');
INSERT INTO [t1446] VALUES (1795,'باب ذكر أول صدقة محبسة تصدق بها في الإسلام " واشتراط المتصدق صدقة المحرمة حبس أصول الصدقة والمنع من بيع رقابها وهبتها وتوريثها، وتسبيل منافعها وغلاتها على الفقراء والقربى والرقاب، وفي سبيل الله، وابن السبيل والضعيف "','4111','1615','0');
INSERT INTO [t1446] VALUES (1796,'باب إباحة الحبس على من لا يحصون لكثرة العدد " والدليل على أن الحبس إذا كان على قوم لا يحصون عددا لكثرتهم جائز أن تعطى منافع تلك الصدقة بعض أهل تلك الصفة، ضد قول من زعم أن الوصية إذا أوصي بها لقوم لا يحصون لكثرة عددهم أن الوصية باطلة غير جائزة على','4114','1615','0');
INSERT INTO [t1446] VALUES (1797,'باب إجازة الحبس على قوم موهومين غير مسمين " وفي سبيل الله، وفي الرقاب، وفي الضيف من غير اشتراط حصة سبيل الله، وحصة الرقاب، وحصة الضيف منها، وإباحة اشتراط المحبس للقيم بها الأكل منها بالمعروف من غير توقيت طعام بكيل معلوم أو وزن معلوم، واشتراطه إطعام','4115','1615','0');
INSERT INTO [t1446] VALUES (1798,'باب ذكر الدليل على أن قوله: " تصدق بها على الفقراء والقربى " " إنما أراد: تصدق بأصلها حبسا، وجعل ثمرها مسبلة على من وصفهم من الفقراء والقربى، ومن ذكر معهم، مع الدليل على أن الحبس إذا لم يخرجه المحبس من يده كان صحيحا جائزا، إذ لو كان الحبس لا يصح','4117','1615','0');
INSERT INTO [t1446] VALUES (1799,'باب إباحة حبس آبار المياه','4119','1615','0');
INSERT INTO [t1446] VALUES (1800,'باب الوصية بالحبس من الضياع والأرضين','4121','1615','0');
INSERT INTO [t1446] VALUES (1801,'باب فضائل بناء السوق لأبناء السابلة، وحفر الأنهار للشارب " مع الدليل على أن قوله في خبر العلاء، عن أبيه، عن أبي هريرة، وخبر أبي قتادة في قوله: " أن صدقة " قد جرت تلك اللفظة بناء المساجد، وبناء البيوت للسابلة، وحفر الأنهار للشاربة أن كل ما ينتفع به','4124','1615','0');
INSERT INTO [t1446] VALUES (1802,'باب حبس آبار المياه على الأغنياء والفقراء وابن السبيل','4126','1615','0');
INSERT INTO [t1446] VALUES (1803,'باب إباحة شرب المحبس من ماء الآبار التي حبسها','4128','1615','0');
INSERT INTO [t1446] VALUES (1804,'باب ذكر الدليل على أن أجر الصدقة المحبسة يكتب للمحبس بعد موته ما دامت الصدقة جارية','4131','1615','0');
INSERT INTO [t1446] VALUES (1805,'باب فضل سقي الماء إن صح الخبر','4134','1615','0');
INSERT INTO [t1446] VALUES (1806,'باب الصدقة عن الميت عن غير وصية من مال الميت، وتكفير ذنوب الميت بها','4137','1615','0');
INSERT INTO [t1446] VALUES (1807,'باب ذكر كتابة الأجر للميت عن غير وصية بالصدقة عنه من ماله','4139','1615','0');
INSERT INTO [t1446] VALUES (1808,'باب الصدقة عن الميت إذا توفي عن غير وصية، وانتفاع الميت في الآخرة بها','4141','1615','0');
INSERT INTO [t1446] VALUES (1809,'باب إيجاب الجنة بسقي الماء من لا يجد الماء إلا غبا " والدليل على أن قوله: " من قال لا إله إلا الله وجبت له الجنة " من الجنس الذي قد بينته في كتاب الإيمان أن هذا من فضائل القول والأعمال، لا أنه جميع الإيمان، إذ العلم محيط أن الاستقاء على بعيره الماء،','4144','1615','0');
INSERT INTO [t1446] VALUES (1810,'كتاب المناسك " المختصر من المختصر من المسند عن النبي صلى الله عليه وسلم على الشرط الذي ذكرنا في أول كتاب الطهارة "','4146','0','0');
INSERT INTO [t1446] VALUES (1811,'باب فرض الحج على من استطاع إليه سبيلا " قال الله عز وجل: ولله على الناس حج البيت من استطاع إليه سبيلا، والبيان أن الحج على من استطاع إليه السبيل من الإسلام "','4147','1810','0');
INSERT INTO [t1446] VALUES (1812,'باب ذكر الدليل على أن اسم الإسلام باسم المعرفة الألف واللام قد يقع على بعض شعب الإسلام " والدليل على أن النبي صلى الله عليه وسلم إنما أجاب جبريل في الخبر الذي ذكرنا عن أصل الإسلام وأساسه، إذ النبي صلى الله عليه وسلم أعلم أن الإسلام بني على هذه الخمس،','4149','1810','0');
INSERT INTO [t1446] VALUES (1813,'باب الأمر بتعجيل الحج خوف فوته برفع الكعبة، إذ النبي صلى الله عليه وسلم أعلم أنها ترفع بعد هدم مرتين','4151','1810','0');
INSERT INTO [t1446] VALUES (1814,'باب ذكر الدليل على أن رفع البيت يكون بعد خروج يأجوج، ومأجوج بعد مدة، لا قبل خروجهما إذ النبي صلى الله عليه وسلم قد أعلم أنه يعتمر ويحج البيت بعد خروج يأجوج، ومأجوج','4153','1810','0');
INSERT INTO [t1446] VALUES (1815,'باب ذكر بيان فرض الحج وأن الفرض حجة واحدة على المرء لا أكثر منها','4155','1810','0');
INSERT INTO [t1446] VALUES (1816,'باب إباحة إعطاء الإمام إبل الصدقة من يحج عليها قال أبو بكر: " خبر أبي لاس الخزاعي قد أمليته في كتاب الزكاة "','4157','1810','0');
INSERT INTO [t1446] VALUES (1817,'باب الرخصة في الحج على الدواب المحبسة في سبيل الله قال أبو بكر: " خبر أم معقل قد أمليته في كتاب الصدقات أيضا "','4158','1810','0');
INSERT INTO [t1446] VALUES (1818,'باب فضل الحج إذ الحاج من وفد الله عز وجل','4159','1810','0');
INSERT INTO [t1446] VALUES (1819,'باب الأمر بالمتابعة بين الحج والعمرة، والبيان أن الفعل قد يضاف إلى الفعل، لا أن الفعل يفعل فعلا كما ادعى بعض أهل الجهل','4161','1810','0');
INSERT INTO [t1446] VALUES (1820,'باب فضل الحج الذي لا رفث فيه، ولا فسوق فيه، وتكفير الذنوب والخطايا به','4164','1810','0');
INSERT INTO [t1446] VALUES (1821,'باب ذكر البيان أن الحج يهدم ما كان قبله من الذنوب والخطايا','4166','1810','0');
INSERT INTO [t1446] VALUES (1822,'باب استحباب دعاء الحاج، إذ النبي صلى الله عليه وسلم قد استغفر لهم ولمن استغفروا له','4168','1810','0');
INSERT INTO [t1446] VALUES (1823,'باب استحباب الخروج إلى الحج يوم الخميس تبركا بفعل النبي صلى الله عليه وسلم، إذ كان صلى الله عليه وسلم قلما يخرج في سفر إلا يوم الخميس','4170','1810','0');
INSERT INTO [t1446] VALUES (1824,'باب استحباب التزود للسفر اقتداء بالنبي صلى الله عليه وسلم ومخالفة لبعض متصوفة أهل زماننا','4172','1810','0');
INSERT INTO [t1446] VALUES (1825,'باب الزجر عن سفر المرأة مع غير ذي محرم وغير زوجها بذكر خبر في التأقيت غير دال توقيته على أن ما كان أقل من ذلك التأقيت من السفر مباح سفر المرأة مع غير محرم وغير زوجها إذا كان سفرها أقل من ثلاث','4174','1810','0');
INSERT INTO [t1446] VALUES (1826,'باب الزجر عن سفر المرأة يومين مع غير زوجها وغير ذي رحمها " والدليل على صحة ما تأولت أن النبي صلى الله عليه وسلم لم يبح بزجره عن سفرها ثلاثا لها أن تسافر أقل من ثلاث مع غير زوجها وغير ذي رحمها، بذكر لفظة في توقيت اليومين لم يرد النبي صلى الله عليه وسلم','4177','1810','0');
INSERT INTO [t1446] VALUES (1827,'باب الزجر عن سفر المرأة يوما وليلة إلا مع ذي محرم " والدليل على أن النبي صلى الله عليه وسلم لم يبح بزجره إياها عن سفر يومين سفر ما هو أقل من يومين، إذ قد زجرها صلى الله عليه وسلم أن تسافر يوما وليلة إلا مع ذي محرم "','4179','1810','0');
INSERT INTO [t1446] VALUES (1828,'باب ذكر الدليل على أن النبي صلى الله عليه وسلم لم يبح بزجره عن سفرها مع غير ذوي محرم يوما وليلة السفر الذي هو أقل منه، إذ قد زجر صلى الله عليه وسلم أيضا أن تسافر ليلة واحدة مع غير ذي محرم اللهم إلا أن يكون هذا من الجنس الذي أعلمت في غير موضع من كتبنا أن','4181','1810','0');
INSERT INTO [t1446] VALUES (1829,'باب الزجر عن سفر المرأة بريدا مع غير ذي محرم " والدليل أن النبي صلى الله عليه وسلم أراد بزجره إياها عن سفر يوم وليلة أنه مباح لها سفر ما هو أقل من يوم وليلة "','4183','1810','0');
INSERT INTO [t1446] VALUES (1830,'باب ذكر الدليل على أن زجر النبي صلى الله عليه وسلم عن سفرها بلا محرم زجر تحريم لا زجر تأديب','4185','1810','0');
INSERT INTO [t1446] VALUES (1831,'باب إباحة سفر المرأة مع عبد زوجها أو مولاه " إذا كان العبد أو المولى يوثق بدينه وأمانته وإن لم يكن العبد أو المولى بمحرم للمرأة إن كان حكم سائر النساء حكم أزواج النبي صلى الله عليه وسلم، ولا أخال؛ لأن الله عز وجل أخبر أنهن أمهات المؤمنين، فجائز أن','4187','1810','0');
INSERT INTO [t1446] VALUES (1832,'باب ذكر خروج المرأة لأداء فرض الحج بغير محرم، وأمر الحاكم زوجها باللحاق بها للحج بها','4189','1810','0');
INSERT INTO [t1446] VALUES (1833,'باب توديع المسلم أخاه عند إرادة السفر','4192','1810','0');
INSERT INTO [t1446] VALUES (1834,'باب دعاء المرء لأخيه المسلم عند إرادة السفر','4194','1810','0');
INSERT INTO [t1446] VALUES (1835,'باب الدعاء عند الخروج إلى السفر','4196','1810','0');
INSERT INTO [t1446] VALUES (1836,'باب الرخصة في الخروج إلى الحج ماشيا لمن قدر على المشي ولم يكن عيالا على رفقائه','4198','1810','0');
INSERT INTO [t1446] VALUES (1837,'باب استحباب ربط الأوساط بالأزر وسرعة المشي إذا كان المرء ماشيا','4200','1810','0');
INSERT INTO [t1446] VALUES (1838,'باب استحباب النسل في المشي عند الإعياء من المشي ليخف الناسل ويذهب بعض الإعياء عنه','4202','1810','0');
INSERT INTO [t1446] VALUES (1839,'باب استحباب مصاحبة الأربعة في السفر','4205','1810','0');
INSERT INTO [t1446] VALUES (1840,'باب حسن الصحابة في السفر، إذ خير الأصحاب خيرهم لصاحبه','4207','1810','0');
INSERT INTO [t1446] VALUES (1841,'باب استحباب تأمير المسافرين أحدهم على أنفسهم، والبيان أن أحقهم بذلك أكثرهم جمعا للقرآن','4209','1810','0');
INSERT INTO [t1446] VALUES (1842,'باب التكبير والتسبيح والدعاء عند ركوب الدواب عند إرادة المرء الخروج مسافرا','4212','1810','0');
INSERT INTO [t1446] VALUES (1843,'باب الأمر بتسمية الله عز وجل عند الركوب وإباحة الحمل على الإبل في المسير قدر طاقتها','4214','1810','0');
INSERT INTO [t1446] VALUES (1844,'باب الزجر عن اتخاذ الدواب كراسي بوقفها والمرء راكبها غير سائر عليها، ولا نازل عنها','4216','1810','0');
INSERT INTO [t1446] VALUES (1845,'باب استحباب الإحسان إلى الدواب المركوبة في العلف والسقي، وكراهية إجاعتها وإعطاشها وركوبها والسير عليها جياعا عطاشا','4218','1810','0');
INSERT INTO [t1446] VALUES (1846,'باب إباحة الحمل على الدواب المركوبة في السير طلبا لقضاء الحوائج إذا ذكر اسم الله عليها عند الركوب، بذكر خبر مختصر غير متقصى','4220','1810','0');
INSERT INTO [t1446] VALUES (1847,'باب الدليل على أن النبي صلى الله عليه وسلم إنما أباح الحمل على الدواب المركوبة، وأن لا تقصر على طلب حاجة، إذ الله عز وجل يراقبه، ورحمته تحمل الراكب بأن يقوي المركوب ليقضي الراكب حاجته','4222','1810','0');
INSERT INTO [t1446] VALUES (1848,'باب ذكر الدليل على أن النبي صلى الله عليه وسلم إنما أباح أن لا يقتصر عن حاجة إذا ركب الدواب من غير أن يجاوز السائر المنازل، إذا كانت الأرض مخصبة، والأمر بإمكان الركاب عن الرعي في الخصب إن صح الخبر؛ فإن في القلب من سماع الحسن من جابر','4224','1810','0');
INSERT INTO [t1446] VALUES (1849,'باب صفة السير في الخصب والجذب " والدليل على أن النبي صلى الله عليه وسلم إنما أمر بسرعة السير في الجذب كي يقطع الدواب المركوبة السفر بنقيها قبل تعجف، فيذهب نقي عظامها من الهزال والعجف "','4227','1810','0');
INSERT INTO [t1446] VALUES (1850,'باب الزجر عن ضرب الدواب على الوجه، وفيه ما دل على أن الضرب على غير الوجه مباح','4229','1810','0');
INSERT INTO [t1446] VALUES (1851,'باب الزجر عن ركوب الجلالة من الدواب المركوبة','4231','1810','0');
INSERT INTO [t1446] VALUES (1852,'باب الزجر عن صحبة الرفقة التي يكون فيها الكلب أو الجرس إذ الملائكة لا تصحبها','4233','1810','0');
INSERT INTO [t1446] VALUES (1853,'باب ذكر الدليل على أن الملائكة لا تصحب رفقة فيها جرس إذ الجرس مزمار الشيطان','4235','1810','0');
INSERT INTO [t1446] VALUES (1854,'باب استحباب الدلجة بالليل إذ الله عز وجل يطوى الأرض بالليل، فيكون السير بالليل أقطع للسفر','4237','1810','0');
INSERT INTO [t1446] VALUES (1855,'باب الزجر عن التعريس على جواد الطريق','4239','1810','0');
INSERT INTO [t1446] VALUES (1856,'باب صفة النوم في العرس','4242','1810','0');
INSERT INTO [t1446] VALUES (1857,'باب كراهية سير أول الليل','4244','1810','0');
INSERT INTO [t1446] VALUES (1858,'باب ذكر توقيت أول الليل الذي كره الانتشار والخروج فيه','4246','1810','0');
INSERT INTO [t1446] VALUES (1859,'باب وصية المسافر بالتكبير عند صعود الشرف والتسبيح عند الهبوط','4248','1810','0');
INSERT INTO [t1446] VALUES (1860,'باب استحباب خفض الصوت بالتكبير عند صعود الشرف في الأسفار','4251','1810','0');
INSERT INTO [t1446] VALUES (1861,'باب فضل الصلاة عند تعريس الناس بالليل','4253','1810','0');
INSERT INTO [t1446] VALUES (1862,'باب الدعاء عند رؤية القرى اللواتي يريد المرء دخولها','4255','1810','0');
INSERT INTO [t1446] VALUES (1863,'باب الاستعاذة عند نزول المنازل','4257','1810','0');
INSERT INTO [t1446] VALUES (1864,'باب توديع المنازل بالصلاة','4259','1810','0');
INSERT INTO [t1446] VALUES (1865,'باب النهي عن سير الوحدة بالليل','4261','1810','0');
INSERT INTO [t1446] VALUES (1866,'باب النهي عن سير الاثنين " والدليل على أن ما دون الثلاث من المسافرين فهم عصاة، إذ النبي صلى الله عليه وسلم قد أعلم أن الواحد شيطان، والاثنان شيطانان، ويشبه أن يكون معنى قوله: شيطان أو عاصي، كقوله شياطين أو عاصي، كقوله شياطين الإنس والجن، ومعناه:','4263','1810','0');
INSERT INTO [t1446] VALUES (1867,'باب دعاء المسافر عند الصباح','4265','1810','0');
INSERT INTO [t1446] VALUES (1868,'باب صفة الدعاء بالليل في الأسفار','4267','1810','0');
INSERT INTO [t1446] VALUES (1869,'باب تقليد البدن وإشعارها عند السوق','4269','1810','0');
INSERT INTO [t1446] VALUES (1870,'باب إشعار البدن في شق السنام الأيمن وسلت الدم عنها، ضد قول من زعم أن إشعار البدن مثلة، فسمى سنة النبي صلى الله عليه وسلم مثلة بجهله','4272','1810','0');
INSERT INTO [t1446] VALUES (1871,'باب الهدي إذا عطب قبل أن يبلغ محله','4275','1810','0');
INSERT INTO [t1446] VALUES (1872,'باب الزجر عن أكل سائق البدن وأهل رفقته من لحمها إذا عطبت ونحرت','4277','1810','0');
INSERT INTO [t1446] VALUES (1873,'باب إيجاب إبدال الهدي الواجب إذا ضلت - إن صح الخبر - ولا أخال؛ فإن في القلب من عبد الله بن عامر الأسلمي','4279','1810','0');
INSERT INTO [t1446] VALUES (1874,'باب التطيب عند الإحرام، ضد قول من كره ذلك، وخالف سنة النبي صلى الله عليه وسلم','4282','1810','0');
INSERT INTO [t1446] VALUES (1875,'باب الرخصة في التطيب عند الإحرام بالمسك " والدليل على أن المسك طاهر غير نجس، لا على ما زعم بعض التابعين أنه ميتة نجس، زعم أنه سقط من حي وهو ميت نجس "','4285','1810','0');
INSERT INTO [t1446] VALUES (1876,'باب الرخصة في التطيب عند الإحرام بطيب يبقى أثره على المتطيب في الإحرام','4288','1810','0');
INSERT INTO [t1446] VALUES (1877,'باب استحباب الاغتسال بعد التطيب عند الإحرام مع استحباب جماع المرء امرأته إذا أراد الإحرام كي يكون أقل شهوة لجماع النساء في الإحرام، إذا كان حديث عهد بجماعهن','4292','1810','0');
INSERT INTO [t1446] VALUES (1878,'باب ذكر مواقيت الإحرام بالحج والعمرة أو بأحدهما لمن منازلهم وراء المواقيت','4294','1810','0');
INSERT INTO [t1446] VALUES (1879,'باب إحرام أهل المناهل التي هي أقرب إلى الحرم من هذه المواقيت التي وقتها النبي صلى الله عليه وسلم لمن منازلهم ورائها " والبيان أن مواقيت من منزله أقرب إلى الحرم من هذه المواقيت منازلهم "','4296','1810','0');
INSERT INTO [t1446] VALUES (1880,'باب ذكر البيان أن هذه المواقيت التي ذكرناها كل ميقات منها لأهله، ولمن مر به من غير أهله، إذا مر المديني على طريق الشام بالجحفة، وحاد عن ذي الحليفة ولم يمر به، كان ميقاته الجحفة إذا هو مار بها، وكذلك اليماني إذا أخذ طريق المدينة فمر بذي الحليفة كان','4298','1810','0');
INSERT INTO [t1446] VALUES (1881,'باب ذكر ميقات أهل العراق إن ثبت الخبر مسندا','4300','1810','0');
INSERT INTO [t1446] VALUES (1882,'باب كراهية الإحرام وراء المواقيت التي وقت النبي صلى الله عليه وسلم لأهل الآفاق الذين منازلهم وراءها، إذ النبي صلى الله عليه وسلم وقت هذه المواقيت لأهلها ولمن أتى عليها من غير أهلها والمصطفى صلى الله عليه وسلم وجميع من خرج من المدينة وقت إرادتهم الحج','4302','1810','0');
INSERT INTO [t1446] VALUES (1883,'باب أمر النفساء بالاغتسال والاستغفار إذا أرادت الإحرام، وإن كان الاغتسال لا يطهر ما يطهر غير النفساء وغير الحيض، إذ النفساء والحيض لا يطهرن بالاغتسال ما لم يطهرن بانقطاع دم النفاس والحيض، والبيان أن ليس في السنة إلا اتباعها، إذ لو كان من جهة العقل','4304','1810','0');
INSERT INTO [t1446] VALUES (1884,'باب استحباب الاغتسال للإحرام','4306','1810','0');
INSERT INTO [t1446] VALUES (1885,'باب النهي عن الإحرام بالحج في غير أشهر الحج، إذ الله وجل وعلا جعل الحج أشهرا معلومات، فغير جائز الدخول في الحج قبل وقته، كما لا يجوز الدخول في الصلوات قبل أوقاتها','4308','1810','0');
INSERT INTO [t1446] VALUES (1886,'باب ذكر الثياب الذي زجر المحرم عن لبسها في الإحرام','4310','1810','0');
INSERT INTO [t1446] VALUES (1887,'باب الزجر عن لبس الأقبية في الإحرام','4312','1810','0');
INSERT INTO [t1446] VALUES (1888,'باب الزجر عن انتقاب المرأة وعن التقفز في الإحرام','4314','1810','0');
INSERT INTO [t1446] VALUES (1889,'باب الإحرام في الأزر، والأردية والنعال','4317','1810','0');
INSERT INTO [t1446] VALUES (1890,'باب اشتراط من به علة عند الإحرام أن محله حيث يحبس، ضد قول من كره ذلك','4319','1810','0');
INSERT INTO [t1446] VALUES (1891,'باب الاكتفاء بالنية عند الإحرام بالحج أو العمرة - أو هما - عند الإهلال عن النطق بذلك','4321','1810','0');
INSERT INTO [t1446] VALUES (1892,'باب إباحة القران بين الحج والعمرة، والإفراد، والتمتع، والبيان أن كل هذا جائز طلق مباح والمرء مخير بين القران والإفراد وبين التمتع، يهل بما شاء من ذلك','4323','1810','0');
INSERT INTO [t1446] VALUES (1893,'باب استحباب التمتع بالعمرة إلى الحج إذ النبي صلى الله عليه وسلم أعلم أصحابه أن لو استقبل من أمره ما استدبر لما ساق الهدى ولحل بعمرة، ولما أمر من لم يسق الهدى بالإهلال بعمرة','4326','1810','0');
INSERT INTO [t1446] VALUES (1894,'باب أمر المهل بالعمرة الذي معه الهدي بالإهلال بالحج مع العمرة ليصير قارنا، إذ سائق الهدي المهل بالعمرة، غير جائز له الإحلال منها قبل مبلغ الهدي محله','4328','1810','0');
INSERT INTO [t1446] VALUES (1895,'باب تقليد الغنم عند الإحرام إذا سيق الهدي، ضد قول من زعم أن الغنم لا تقلد، إذ النبي صلى الله عليه وسلم قد قلد الغنم الذي أهدى وهو مقيم بالمدينة حلال، وسنة الهدي في التقليد لمن كان مقيما ببلدة يريد توجيه الهدي، ومن أراد الحج أو الحج والعمرة وأهدى أو','4330','1810','0');
INSERT INTO [t1446] VALUES (1896,'باب حديث الإحرام خلف الصلاة المكتوبة إذا حضرت','4332','1810','0');
INSERT INTO [t1446] VALUES (1897,'باب إباحة الإحرام من غير صلاة متقدمة من مكتوبة أو تطوع " والدليل أن غير المتطهرة والجنب إن أحرم بالحج والعمرة أو هما كان الإحرام جائزا، إذ النبي صلى الله عليه وسلم قد أمر النفساء والحائض بالإحرام وهما غير طاهرتين، إذ النفساء والحائض لا تجزئهما الصلاة','4334','1810','0');
INSERT INTO [t1446] VALUES (1898,'باب الإهلال عند مسجد ذي الحليفة','4336','1810','0');
INSERT INTO [t1446] VALUES (1899,'باب الإهلال إذا استوت بالراكب ناقته عند مسجد ذي الحليفة ضد قول من زعم أن النبي صلى الله عليه وسلم لم يهل حتى أتى البيداء، وهذا من الجنس الذي أعلمت في غير موضع في كتبنا، أن الخير الواجب قبوله هو خير من يخير بسماع الشيء ورؤيته دون من ينكر الشيء ويدفعه','4338','1810','0');
INSERT INTO [t1446] VALUES (1900,'باب استحباب الاستقبال بالراحلة القبلة إذا أراد الراكب الإهلال','4341','1810','0');
INSERT INTO [t1446] VALUES (1901,'باب استحباب البيتوتة بذي الحليفة والغدو منها استنانا بالنبي صلى الله عليه وسلم','4343','1810','0');
INSERT INTO [t1446] VALUES (1902,'باب استحباب التعرس في بطن الوادي بذي الحليفة','4345','1810','0');
INSERT INTO [t1446] VALUES (1903,'باب استحباب الصلاة في ذلك الوادي','4347','1810','0');
INSERT INTO [t1446] VALUES (1904,'باب استحباب الإهلال بما يحرم به المهل من حج أو عمرة أو هما','4349','1810','0');
INSERT INTO [t1446] VALUES (1905,'باب إباحة الإحرام من غير تسمية حج ولا عمرة، ومن غير قصد نية واحد بعينه عند ابتداء الإحرام','4352','1810','0');
INSERT INTO [t1446] VALUES (1906,'باب صفة تلبية النبي صلى الله عليه وسلم','4354','1810','0');
INSERT INTO [t1446] VALUES (1907,'باب ذكر البيان أن الزيادة في التلبية على ما حفظ ابن عمر، عن النبي صلى الله عليه وسلم جائز " والدليل على أن بعض أصحاب النبي صلى الله عليه وسلم قد يحفظ عنه ما يغرب عن بعضهم؛ لأن أبا هريرة قد حفظ عن النبي صلى الله عليه وسلم في تلبيته ما لم يحك عنه غيره "','4356','1810','0');
INSERT INTO [t1446] VALUES (1908,'باب إباحة الزيادة في التلبية: ذا المعارج، ونحوه، ضد قول من كره هذه الزيادة وذكر أنهم لم يقولوه مع النبي صلى الله عليه وسلم، مع الدليل على أن من تقدمت صحبته للنبي صلى الله عليه وسلم وكان أعلم، قد كان يخفى عليه الشيء من علم الخاصة، فعلمه من هو دونه','4359','1810','0');
INSERT INTO [t1446] VALUES (1909,'باب استحباب رفع الصوت بالتلبية','4362','1810','0');
INSERT INTO [t1446] VALUES (1910,'باب البيان أن رفع الصوت بالإهلال من شعار الحج، وإنما أمر المهل برفع الصوت به إذ هو من شعار الحج','4364','1810','0');
INSERT INTO [t1446] VALUES (1911,'باب ذكر البيان أن رفع الصوت بالإهلال من أفضل الأعمال','4368','1810','0');
INSERT INTO [t1446] VALUES (1912,'باب استحباب وضع الإصبعين في الأذنين عند رفع الصوت والتلبية، إذ وضع الإصبعين في الأذنين عند رفع الصوت يكون أرفع صوتا، وأمده','4370','1810','0');
INSERT INTO [t1446] VALUES (1913,'باب ذكر تلبية الأشجار والأحجار اللواتي عن يمين الملبي وعن شماله عند تلبية الملبي','4373','1810','0');
INSERT INTO [t1446] VALUES (1914,'باب الزجر عن معونة المحرم للحلال على الاصطياد بالإشارة ومناولة السلاح الذي يكون عونا للحلال على الاصطياد','4375','1810','0');
INSERT INTO [t1446] VALUES (1915,'باب ذكر الدليل على أن المحرم إذا أشار للحلال الصيد فاصطاده الحلال، لم يجز أكله للمحرم','4377','1810','0');
INSERT INTO [t1446] VALUES (1916,'باب كراهية قبول المحرم الصيد إذا أهدي له في إحرامه " والدليل على أن المحرم غير جائز له ملك الصيد في إحرامه "','4379','1810','0');
INSERT INTO [t1446] VALUES (1917,'باب ذكر خبر روي عن النبي صلى الله عليه وسلم في إباحة أكل لحم الصيد للمحرم مجمل غير مفسر قد يحسب بعض من لا يميز بين الخبر المجمل والمفسر، أن أكل لحم الصيد للمحرم إذا اصطاده الحلال، طلق حلال بكل حال','4381','1810','0');
INSERT INTO [t1446] VALUES (1918,'باب ذكر خبر روي عن النبي صلى الله عليه وسلم في رده لحم صيد أهدي له في إحرامه مجمل غير مفسر، وقد يحسب بعض لم يتبحر العلم ولا يميز بين المجمل والمفسر من الأخبار، أن لحم الصيد محرم على المحرم بكل حال، وإن اصطاده الحلال','4383','1810','0');
INSERT INTO [t1446] VALUES (1919,'باب ذكر الخبر المفسر للأخبار التي ذكرناها في البابين المتقدمين " والدليل على أن النبي صلى الله عليه وسلم إنما أباح أكل لحم الصيد للمحرم إذا اصطاده الحلال، إذا لم يكن الحلال اصطاده من أجل المحرم، وإنه إنما كره للمحرم أكل لحم الصيد الذي اصطاده الحلال من','4387','1810','0');
INSERT INTO [t1446] VALUES (1920,'باب الزجر عن أكل المحرم بيض الصيد، إذا أخذ البيضة من أجل المحرم','4391','1810','0');
INSERT INTO [t1446] VALUES (1921,'باب الزجر عن قتل الضبع في الإحرام، إذ النبي صلى الله عليه وسلم المولى ببيان ما أنزل الله عليه من الوحي إليه، قد أعلم أن الضبع صيد، والله عز وجل في محكم تنزيله قد نهى المحرم عن قتل الصيد فقال: لا تقتلوا الصيد وأنتم حرم','4393','1810','0');
INSERT INTO [t1446] VALUES (1922,'باب ذكر جزاء الضبع إذا قتله المحرم','4395','1810','0');
INSERT INTO [t1446] VALUES (1923,'باب الدليل على أن الكبش الذي قضي به جزاء للضبع هو المسن منه لا ما دون المسن، مع الدليل على أن الله عز وجل أراد بقوله: فجزاء مثل ما قتل من النعم أقرب الأشياء شبها بالبدن من النعم، لا مثله في القيمة كما قاله بعض العراقيين، إذ العلم محيط أن قيمة الضبع','4398','1810','0');
INSERT INTO [t1446] VALUES (1924,'باب الزجر عن تزويج المحرم وخطبته وإنكاحه','4400','1810','0');
INSERT INTO [t1446] VALUES (1925,'جماع أبواب ذكر أفعال اختلف الناس في إباحته للمحرم، نصت سنة النبي صلى الله عليه وسلم أو دلت على إباحتها','4402','1810','0');
INSERT INTO [t1446] VALUES (1926,'باب الرخصة في غسل المحرم رأسه','4403','1810','0');
INSERT INTO [t1446] VALUES (1927,'باب الرخصة في الحجامة للمحرم من غير قطع شعر ولا حلقه','4405','1810','0');
INSERT INTO [t1446] VALUES (1928,'باب الرخصة في ادهان المحرم بدهن غير مطيب إن جاز الاحتجاج بفرقد السبخي، وصحت هذه اللفظة من روايته، أن النبي صلى الله عليه وسلم ادهن وهو محرم؛ لأن أصحاب حماد بن سلمة قد اختلفوا عنه في هذه اللفظة، أنا خائف أن يكون فرقد السبخي واهما في رفعه هذا الخبر','4407','1810','0');
INSERT INTO [t1446] VALUES (1929,'باب إباحة مداواة المحرم عينه - إذا أصابه رمد - بالصبر','4409','1810','0');
INSERT INTO [t1446] VALUES (1930,'باب الرخصة في السواك للمحرم','4411','1810','0');
INSERT INTO [t1446] VALUES (1931,'باب الرخصة في تلبيد المحرم رأسه كي لا يتأذى بالقمل والصيبان في الإحرام','4413','1810','0');
INSERT INTO [t1446] VALUES (1932,'باب الرخصة في حجامة المحرم على الرأس وإن كان المحجوم ذا جمة أو وفرة، بذكر خبر مختصر غير متقص','4415','1810','0');
INSERT INTO [t1446] VALUES (1933,'باب ذكر الدليل على أن النبي صلى الله عليه وسلم إنما احتجم على رأسه من وجع وجده برأسه','4417','1810','0');
INSERT INTO [t1446] VALUES (1934,'باب إباحة الحجامة للمحرم على ظهر القدم، والدليل على أن النبي صلى الله عليه وسلم قد احتجم محرما غير مرة، مرة على الرأس، ومرة على ظهر القدم','4419','1810','0');
INSERT INTO [t1446] VALUES (1935,'باب ذكر الدليل على أن الوجع الذي وجده النبي صلى الله عليه وسلم في إحرامه فاحتجم بسببه على ظهر القدم، وجده بظهره أو بوركه لا بقدمه','4421','1810','0');
INSERT INTO [t1446] VALUES (1936,'باب إباحة ركوب المحرم البدن إذا ساقه بلفظ مجمل غير مفسر','4423','1810','0');
INSERT INTO [t1446] VALUES (1937,'باب ذكر الخبر المفسر لبعض اللفظة المجملة التي ذكرتها، والدليل على أن النبي صلى الله عليه وسلم إنما أباح ركوب البدن إذا كان راكبها لا يجد ظهرا يركبه، لا إذا وجد ظهرا، مع الدليل على أنه إذا ركب البدنة عند الإعواز من وجود الظهر ثم وجد ظهرا يركبه، لم يجز','4425','1810','0');
INSERT INTO [t1446] VALUES (1938,'باب ذكر الدليل على أن النبي صلى الله عليه وسلم إنما أباح ركوب البدن عند الحاجة إلى ركوبها عند الإعواز من وجود الظهر ركوبا بالمعروف، ومن غير أن يشق الركوب على البدنة','4427','1810','0');
INSERT INTO [t1446] VALUES (1939,'باب ذكر الدواب التي أبيح للمحرم قتلها في الإحرام بذكر لفظة مجملة في ذكر بعضهن بلفظ عام، مراده خاص على أصلنا','4429','1810','0');
INSERT INTO [t1446] VALUES (1940,'باب إباحة قتل المحرم الحية وإن كان قاتلها في الحرم لا في الحل','4433','1810','0');
INSERT INTO [t1446] VALUES (1941,'باب ذكر الخبر المفسر للفظة المجملة التي ذكرتها في بعض ما أبيح قتله للمحرم والدليل على أن النبي صلى الله عليه وسلم إنما أباح للمحرم قتل بعض الغربان لأكلها، وإنه إنما أباح قتل الأبقع منها دون ما سواه من الغربان','4435','1810','0');
INSERT INTO [t1446] VALUES (1942,'باب ذكر طيب المحرم ولبسه في الإحرام ما لا يجوز لبسه جاهلا، بأن ذلك غير جائز في الإحرام وإسقاط الكفارة عن فاعله ضد مذهب من زعم أن الكفارة واجبة عليه وإن كان جاهلا، بأن التطيب ولبس ما لبس من الثياب غير جائز له بذكر خبر لفظة في الطيب، غلط في الاحتجاح بها','4437','1810','0');
INSERT INTO [t1446] VALUES (1943,'باب ذكر اللفظة المفسرة للفظة المجملة التي ذكرتها في الطيب والدليل على أن النبي صلى الله عليه وسلم لما أمر المحرم في الجبة بعد النضخ بالطيب يغسل ذلك الطيب إذا كان ما تطيب به من طيب النساء خلوقا، لا ذاك الطيب التي هي من طيب الرجال التي قد تطيب به النبي','4439','1810','0');
INSERT INTO [t1446] VALUES (1944,'باب ذكر البيان أن النبي صلى الله عليه وسلم إنما أمر هذا المحرم - الذي ذكرناه - بغسل الطيب الذي كان عليه خلوق فيه زعفران والتزعفر غير جائز. . . . أيضا وإن كان المحرم منهيا عنه، لا كما توهم بعض العراقيين، أن النبي صلى الله عليه وسلم أمره بغسل ذلك الطيب','4441','1810','0');
INSERT INTO [t1446] VALUES (1945,'باب ذكر زجر النبي صلى الله عليه وسلم عن تزعفر المحل والمحرم جميعا، والدليل على صحة ما تأولت خبر يعلى بن أمية، أن النبي صلى الله عليه وسلم إنما أمر المحرم الذي ذكرنا صفته بغسل الطيب الذي كان متضمخا به، إذ كان طيبه خلوقا فيه زعفران','4443','1810','0');
INSERT INTO [t1446] VALUES (1946,'باب ذكر دليل ثان يدل على صحة ما تأولت أمر النبي صلى الله عليه وسلم في خبر يعلى بغسل الطيب الذي كان على المحرم، إذ النبي صلى الله عليه وسلم قد أمر المحل أيضا بغسل الخلوق الذي كان قد تخلق به، فسوى في الأمر بغسل الخلوق بين المحرم والمحل','4446','1810','0');
INSERT INTO [t1446] VALUES (1947,'باب البيان ضد قول من زعم أن المحرم في الجبة عليه خرق الجبة، وغير جائز له نزعها فوق رأسه قال أبو بكر: في خبر صفوان بن يعلى، عن أبيه قال: انزع جبتك ثنا محمد بن هشام، ثنا هشيم، عن الحجاج، عن عطاء قال: كنا نقول قبل أن يبلغنا هذا الحديث يخرق عنه جبته','4448','1810','0');
INSERT INTO [t1446] VALUES (1948,'باب الرخصة في حلق المحرم رأسه إذا مرض أو أذاه القمل أو الصيبان أو هما، وإيجاب الفدية على حالق الرأس وإن كان حلقه من مرض أو أذى برأسه','4449','1810','0');
INSERT INTO [t1446] VALUES (1949,'باب ذكر الدليل على أن كعبا أمره النبي صلى الله عليه وسلم بحلق رأسه ويفتدي بصيام أو صدقة أو نسك، قبل أن يبين لهم أنهم يحلقون بالحديبية ويرجعون إلى المدينة من غير وصول إلى مكة','4451','1810','0');
INSERT INTO [t1446] VALUES (1950,'باب ذكر الدليل على أن في قوله تعالى: ولا تحلقوا رؤسكم حتى يبلغ الهدي محله فمن كان منكم مريضا أو به أذى من رأسه ففدية من صيام أو صدقة اختصار كلام معناه: فحلقتم، ففدية من صيام أو صدقة أو نسك، كقوله جل وعلا: اضرب بعصاك البحر فانفلق , أراد: فيهن جميعا','4453','1810','0');
INSERT INTO [t1446] VALUES (1951,'باب الرخصة في أدب المحرم عبده إذا ضيع مال المولى فاستحق الأدب على ذلك','4455','1810','0');
INSERT INTO [t1446] VALUES (1952,'باب الرخصة في إنشاد المحرم الشعر والرجز','4457','1810','0');
INSERT INTO [t1446] VALUES (1953,'باب الرخصة في لبس المحرم السراويل عند الإعواز من الإزار والخفين عند عدم وجود النعلين، بلفظ مجمل غير مفسر في ذكر الخفين عند عدم وجود النعلين','4459','1810','0');
INSERT INTO [t1446] VALUES (1954,'باب ذكر الخبر المفسر للفظة المجملة التي ذكرتها في إباحة لبس الخفين لمن لا يجد النعلين، والدليل على أن النبي صلى الله عليه وسلم إنما أباح للمحرم لبس الخفين المقطوع أسفل الكعبين، لا كل ما وقع عليه اسم خف، وإن كان فوق الكعبين','4461','1810','0');
INSERT INTO [t1446] VALUES (1955,'باب ذكر الدليل على أن النبي صلى الله عليه وسلم إنما أباح للمحرم لبس الخفين اللذين هما أسفل من الكعبين، لا أنه أباح له لبس الخفين اللذين لها ساقان، وإن شق أسفل الكعبين من الخفين شقا، وترك الساقين فلم يبانا مما أسفل من الكعبين على ما توهمه بعض الناس','4464','1810','0');
INSERT INTO [t1446] VALUES (1956,'باب ذكر الدليل على أن النبي صلى الله عليه وسلم إنما رخص بالأمر بقطع الخفين للرجال دون النساء، إذ قد أباح للنساء الخفين وإن وجدن نعالا، فرخص للنساء في لبس الخفاف دون الرجال','4466','1810','0');
INSERT INTO [t1446] VALUES (1957,'باب الرخصة في استظلال المحرم وإن كان نازلا غير سائر ضد قول من كرهه ونهى عنه','4468','1810','0');
INSERT INTO [t1446] VALUES (1958,'باب إباحة استظلال المحرم وإن كان راكبا غير نازل','4470','1810','0');
INSERT INTO [t1446] VALUES (1959,'باب إباحة أبدال المحرم ثيابه في الإحرام والرخصة في لبس الممشق من الثياب وإن كان الممشق مصبوغا غير أنه مصبوغ بالطين','4472','1810','0');
INSERT INTO [t1446] VALUES (1960,'باب إباحة تغطية المحرمة وجهها من الرجال، بذكر خبر مجمل، أحسبه غير مفسر','4475','1810','0');
INSERT INTO [t1446] VALUES (1961,'باب ذكر الخبر المفسر لهذه اللفظة التي حسبتها مجملة، والدليل على أن للمحرمة تغطية وجهها من غير انتقاب ولا إمساس الثوب، إذ الخمار الذي تستر به وجهها بل تسدل الثوب من فوق رأسها على وجهها، أو تستر وجهها بيدها أو بكمها أو ببعض ثيابها، مجافية يدها عن وجهها','4477','1810','0');
INSERT INTO [t1446] VALUES (1962,'باب استحباب دخول مكة نهارا، اقتداء برسول الله صلى الله عليه وسلم، والبيتوتة قرب مكة إذا انتهى المرء بالليل إلى ذي طوى ليكون دخوله مكة نهارا لا ليلا','4479','1810','0');
INSERT INTO [t1446] VALUES (1963,'باب استحباب دخول مكة من الثنية العليا استنانا بالنبي صلى الله عليه وسلم، إذ في الاقتداء الخير الذي لا يعتاض منه أحد ترك الاقتداء به','4481','1810','0');
INSERT INTO [t1446] VALUES (1964,'باب استحباب الاغتسال لدخول مكة، إذ النبي صلى الله عليه وسلم اغتسل عند إرادته دخول مكة','4483','1810','0');
INSERT INTO [t1446] VALUES (1965,'باب قطع التلبية في الحج عند دخول الحرم إلى الفراغ من السعي بين الصفا والمروة','4486','1810','0');
INSERT INTO [t1446] VALUES (1966,'باب استحباب تجديد الوضوء عند إرادة المرء الطواف بالبيت عند مقدمه مكة','4489','1810','0');
INSERT INTO [t1446] VALUES (1967,'باب استحباب دخول المسجد من باب بني شيبة','4491','1810','0');
INSERT INTO [t1446] VALUES (1968,'باب الأمر بالتزين عند إرادة الطواف بالبيت بلبس الثياب، والدليل على أن لبس الثياب زينة للملابسين ولسترة العورة، وإن لم تكن الثياب مزينة بصبغ، ولا كانت ثيابا فاخرة، إذ الله عز وجل قال في محكم تنزيله خذوا زينتكم عند كل مسجد ولم يرد بهذا الأمر لبس الثياب','4493','1810','0');
INSERT INTO [t1446] VALUES (1969,'باب كراهة رفع اليدين عند رؤية البيت بذكر خبر مجمل غير مفسر، قد توهم بعض من لا يميز بين الخبر المجمل والمفسر أنه خلاف خبر عمر بن الخطاب أنه رفع يدية حين رأى البيت، ويحسب أنه خلاف خبر مقسم عن ابن عباس، ونافع عن ابن عمر عن النبي صلى الله عليه وسلم، "','4496','1810','0');
INSERT INTO [t1446] VALUES (1970,'باب ذكر الخبر المفسر للفظة المجملة التي ذكرتها، والدليل على أن جابر بن عبد الله إنما أراد بقوله: لم يكن يفعل هذا، أي: لم نكن نرفع أيدينا عند الخروج من المسجد بعد الفراغ من الطواف والصلاة، ولم نكن نستقبل البيت فنرفع أيدينا بعد ذلك، لا أنا لم نكن','4499','1810','0');
INSERT INTO [t1446] VALUES (1971,'باب الدعاء عند دخول المسجد','4501','1810','0');
INSERT INTO [t1446] VALUES (1972,'باب الاضطباع بالرداء عند طواف الحج والعمرة أو أحدهما','4503','1810','0');
INSERT INTO [t1446] VALUES (1973,'باب ذكر الدليل على أن السنة قد كان يسنها النبي صلى الله عليه وسلم لعلة حادثة فتزول العلة وتبقى السنة قائمة إلى الأبد إذ النبي صلى الله عليه وسلم إنما رمل في الابتداء واضطبع ليري المشركين قوته وقوة أصحابه فبقي الاضطباع والرمل سنتين إلى آخر الأبد','4505','1810','0');
INSERT INTO [t1446] VALUES (1974,'باب استلام الحجر الأسود عند ابتداء الطواف','4507','1810','0');
INSERT INTO [t1446] VALUES (1975,'باب تقبيل الحجر الأسود إذا تم تقبيله من غير إيذاء المسلم','4510','1810','0');
