INSERT INTO [t1446] VALUES (2033,'باب فضل الحج ماشيا من مكة إن صح الخبر فإن في القلب من عيسى بن سوادة هذا','4643','1810','0');
INSERT INTO [t1446] VALUES (2034,'باب عدد حج آدم صلوات الله عليه وصفة حجه إن صح الخبر فإن في القلب من القاسم بن عبد الرحمن هذا','4645','1810','0');
INSERT INTO [t1446] VALUES (2035,'باب خطبة الإمام يوم السابع من ذي الحجة ليعلم الناس مناسكهم','4647','1810','0');
