INSERT INTO [t1446] VALUES (2037,'باب وقت الخروج يوم التروية من مكة إلى منى','4652','1810','0');
INSERT INTO [t1446] VALUES (2038,'باب ذكر عدد الصلوات التي يصلي الإمام والناس بمنى قبل الغدو إلى عرفة','4655','1810','0');
INSERT INTO [t1446] VALUES (2039,'باب وقت الغدو من منى إلى عرفة','4658','1810','0');
INSERT INTO [t1446] VALUES (2040,'باب ذكر البيان أن السنة الغدو من منى إلى عرفات بعد طلوع الشمس لا قبله','4661','1810','0');
INSERT INTO [t1446] VALUES (2041,'باب ذكر البيان أن محمدا النبي صلى الله عليه وسلم إنما اتبع خليل الله في غدوه من منى حين طلعت الشمس، إذ قد أمر باتباعه قال الله عز وجل: أولئك الذين هدى الله فبهداهم اقتده، وابن أبي مليكة قد سمع من عبد الله بن عمرو','4663','1810','0');
INSERT INTO [t1446] VALUES (2042,'باب ذكر العلة التي سميت لها عرفة عرفة مع الدليل على أن جبريل قد أرى النبي صلى الله عليه وسلم محمدا المناسك كما أرى إبراهيم خليل الرحمن','4665','1810','0');
INSERT INTO [t1446] VALUES (2043,'باب ذكر التخيير بين التلبية وبين التكبير في الغدو من منى إلى عرفة','4667','1810','0');
INSERT INTO [t1446] VALUES (2044,'باب التكبير والتهليل والتلبية في الغدو من منى إلى عرفة','4669','1810','0');
INSERT INTO [t1446] VALUES (2045,'باب ذكر خطبة الإمام بعرفة، ووقت الخطبة في ذلك اليوم قال أبو بكر في خبر ابن الزبير: حتى إذا زالت الشمس خطب الناس، ثم صلى الظهر والعصر جميعا','4671','1810','0');
INSERT INTO [t1446] VALUES (2046,'باب صفة الخطبة يوم عرفة','4672','1810','0');
INSERT INTO [t1446] VALUES (2047,'باب ذكر البيان أن النبي صلى الله عليه وسلم إنما خطب بعرفة راكبا لا نازلا بالأرض قال أبو بكر: في خبر زيد بن هارون، عن يحيى بن سعيد، عن القاسم، سمعت ابن الزبير قال: خطب الناس بعرفة ثم نزل فجمع بين الظهر والعصر','4674','1810','0');
INSERT INTO [t1446] VALUES (2048,'باب قصر الخطبة يوم عرفة','4676','1810','0');
