INSERT INTO [t1446] VALUES (2050,'باب ترك التنفل بين الظهر والعصر إذا جمع بينهما بعرفة، ووقت الرواح إلى الموقف','4680','1810','0');
INSERT INTO [t1446] VALUES (2051,'باب التهجير بالصلاة يوم عرفة، وترك تأخير الصلاة بها','4682','1810','0');
INSERT INTO [t1446] VALUES (2052,'باب تعجيل الوقوف بعرفة','4684','1810','0');
INSERT INTO [t1446] VALUES (2053,'باب الوقوف بعرفة، والرخصة للحاج أن يقفوا حيث شاءوا منه، وجميع عرفة موقف','4686','1810','0');
INSERT INTO [t1446] VALUES (2054,'باب الزجر عن الوقوف بعرنة','4688','1810','0');
INSERT INTO [t1446] VALUES (2055,'باب ذكر البيان أن الوقوف بعرفة من سنة إبراهيم خليل الرحمن وأنه إرث عنه، ورثها أمة محمد النبي صلى الله عليه وسلم','4691','1810','0');
INSERT INTO [t1446] VALUES (2056,'باب ذكر وقت الوقوف بعرفة، والدليل على أن المفيض من عرفة بعد زوال الشمس قبل غروب الشمس من ليلة النحر مدرك للحج غير فائت الحج، ضد قول من زعم أن المفيض من عرفة الخارج من حدها قبل غروب الشمس ليلة النحر فائت الحج، إذا لم يرجع فيدخل حد عرفة قبل طلوع الفجر','4694','1810','0');
INSERT INTO [t1446] VALUES (2057,'باب ذكر البيان أن هذه الصلوات التي قال النبي صلى الله عليه وسلم: " من صلى معنا هذه الصلاة " كانت صلاة الصبح لا غيرها','4696','1810','0');
INSERT INTO [t1446] VALUES (2058,'باب ذكر الدليل على أن الحاج إذا لم يدرك عرفة قبل طلوع الفجر من يوم النحر فهو فائت الحج غير مدركه','4698','1810','0');
